-- Đang đổ dữ liệu cho bảng `list_products`
--

INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(4, 'VHL#1', 'Iphone 14 Pro Max 128GB', 25990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'iphone1.jpg', '<h3>&nbsp;</h3>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/dtdd/iphone-14-pro-max\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 14 Pro Max tại Thế Giới Di Động\">iPhone 14 Pro Max</a>&nbsp;một si&ecirc;u phẩm trong giới smartphone được nh&agrave; T&aacute;o tung ra thị trường v&agrave;o th&aacute;ng 09/2022. M&aacute;y trang bị con chip Apple A16 Bionic v&ocirc; c&ugrave;ng mạnh mẽ, đi k&egrave;m theo đ&oacute; l&agrave; thiết kế h&igrave;nh m&agrave;n h&igrave;nh mới, hứa hẹn mang lại những trải nghiệm đầy mới mẻ cho người d&ugrave;ng iPhone.</h3>\r\n\r\n<h3>Thiết kế cao cấp bền bỉ</h3>\r\n\r\n<p>iPhone năm nay sẽ được thừa hưởng n&eacute;t đặc trưng từ người anh&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-13-pro-max-1tb\" target=\"_blank\" title=\"Tham khảo iPhone 13 Pro Max tại Thế Giới Di Động\">iPhone 13 Pro Max</a>, vẫn sẽ l&agrave; khung th&eacute;p kh&ocirc;ng gỉ v&agrave; mặt lưng k&iacute;nh cường lực kết hợp với tạo h&igrave;nh vu&ocirc;ng vức hiện đại th&ocirc;ng qua c&aacute;ch tạo h&igrave;nh phẳng ở c&aacute;c cạnh v&agrave; phần mặt lưng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103221.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế sang trọng - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103221.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103221.jpg\" title=\"Thiết kế sang trọng - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>D&ograve;ng Pro năm nay được ra mắt với nhiều t&ugrave;y chọn m&agrave;u sắc v&agrave; trong đ&oacute; nổi bật nhất c&oacute; lẽ l&agrave; phi&ecirc;n bản Deep Purple (t&iacute;m), bởi v&igrave; đ&acirc;y l&agrave; một m&agrave;u mới n&ecirc;n mang lại độ nhận diện kh&aacute; cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103227.jpg\" onclick=\"return false;\"><img alt=\"Màu sắc mới mẻ - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103227.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103227.jpg\" title=\"Màu sắc mới mẻ - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<h3>Nổi bật với thiết kế m&agrave;n h&igrave;nh mới</h3>\r\n\r\n<p>Điểm ấn tượng nhất tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Xem thêm iPhone chính hãng đang kinh doanh tại Thế Giới Di Động\">điện thoại iPhone</a>&nbsp;năm nay nằm ở thiết kế m&agrave;n h&igrave;nh, c&oacute; thể dễ d&agrave;ng nhận thấy được l&agrave; h&atilde;ng cũng đ&atilde; loại bỏ cụm tai thỏ truyền thống qua bao đời iPhone bằng một h&igrave;nh d&aacute;ng mới v&ocirc; c&ugrave;ng lạ mắt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103229.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế hình notch mới - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103229.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103229.jpg\" title=\"Thiết kế hình notch mới - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>So với cụm tai thỏ h&igrave;nh notch năm nay đ&atilde; c&oacute; phần tiết kiệm diện t&iacute;ch tương đối tốt, nhưng khi so với c&aacute;c kiểu m&agrave;n h&igrave;nh dạng &ldquo;nốt ruồi&rdquo; th&igrave; đ&acirc;y vẫn chưa thực sự l&agrave; một điều qu&aacute; tối ưu cho phần m&agrave;n h&igrave;nh. Thế nhưng Apple lại rất biết c&aacute;ch tận dụng những nhược điểm để biến ch&uacute;ng trở th&agrave;nh ưu điểm một c&aacute;ch ngoạn mục bằng c&aacute;ch ph&aacute;t minh nhiều hiệu ứng th&uacute; vị.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103232.jpg\" onclick=\"return false;\"><img alt=\"Kích thước nhỏ gọn - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103232.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103232.jpg\" title=\"Kích thước nhỏ gọn - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Để l&agrave;m cho ch&uacute;ng trở n&ecirc;n bắt mắt hơn Apple cũng đ&atilde; giới thiệu nhiều hiệu ứng chuyển động nhằm l&agrave;m tăng sự th&iacute;ch th&uacute; cho người d&ugrave;ng, điều n&agrave;y được k&iacute;ch hoạt trong l&uacute;c m&igrave;nh ấn giữ phần h&igrave;nh notch khi đang d&ugrave;ng c&aacute;c phần mềm hỗ trợ như: Nghe nhạc, đồng hồ hẹn giờ, ghi &acirc;m,...</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103235.jpg\" onclick=\"return false;\"><img alt=\"Hiệu ứng sinh động - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103235.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103235.jpg\" title=\"Hiệu ứng sinh động - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<h3>Hiển thị nội dung ch&acirc;n thực hơn th&ocirc;ng qua m&agrave;n h&igrave;nh chất lượng</h3>\r\n\r\n<p>Từ trước đến nay, tấm nền OLED lu&ocirc;n được cộng đồng người d&ugrave;ng đ&aacute;nh gi&aacute; rất cao khi xuất hiện tr&ecirc;n c&aacute;c d&ograve;ng sản phẩm của Apple, với chiếc iPhone 14 Pro Max điều n&agrave;y cũng kh&ocirc;ng l&agrave; ngoại lệ khi h&igrave;nh ảnh m&agrave; m&aacute;y mang lại hết sức ch&acirc;n thực.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103237.jpg\" onclick=\"return false;\"><img alt=\"Màn hình cao cấp - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103237.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103237.jpg\" title=\"Màn hình cao cấp - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Nh&igrave;n v&agrave;o m&agrave;n h&igrave;nh ta sẽ thấy được một độ trong trẻo v&ocirc; c&ugrave;ng th&iacute;ch mắt, nội dung hiển thị c&oacute; chiều s&acirc;u, cảm gi&aacute;c khi xem phim hay chơi game cũng trở n&ecirc;n sống động hơn.</p>\r\n\r\n<p>Điểm vượt trội nhất m&agrave; c&aacute;c loại tấm nền kh&aacute;c kh&oacute; m&agrave; đạt được l&agrave; độ chuẩn c&aacute;c về m&agrave;u sắc cao, m&igrave;nh cũng đ&atilde; sử dụng iPhone 14 Pro Max để thiết kế một v&agrave;i ấn phẩm như poster hay sticker đơn giản v&agrave; th&agrave;nh quả l&uacute;c in ra sau khi đối chiếu lại với ảnh tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Xem thêm Điện thoại chính hãng, giá rẻ đang kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;th&igrave; gần như l&agrave; bằng nhau, độ sai lệch kh&ocirc;ng qu&aacute; đ&aacute;ng kể gi&uacute;p m&igrave;nh an t&acirc;m hơn trong việc thiết kế h&igrave;nh ảnh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103240.jpg\" onclick=\"return false;\"><img alt=\"Độ chuẩn xác về màu cao - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103240.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103240.jpg\" title=\"Độ chuẩn xác về màu cao - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Độ ph&acirc;n giải m&agrave;n h&igrave;nh đạt mức 2796 x 1290 Pixels n&ecirc;n việc t&aacute;i tạo h&igrave;nh ảnh c&oacute; độ chi tiết cao l&agrave; điều hết sức dễ d&agrave;ng, hiện tượng như rỗ m&agrave;n h&igrave;nh l&agrave; điều m&agrave; m&igrave;nh chưa từng thấy xuất hiện tr&ecirc;n điện thoại trong 2 tuần trải nghiệm.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103242.jpg\" onclick=\"return false;\"><img alt=\"Độ chi tiết cực lớn - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103242.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103242.jpg\" title=\"Độ chi tiết cực lớn - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p><strong>Hỗ trợ t&iacute;nh năng Always On Display</strong></p>\r\n\r\n<p>B&ecirc;n cạnh sự đổi mới về h&igrave;nh notch, năm nay Apple c&ograve;n trang bị cho d&ograve;ng Pro v&agrave; Pro Max th&ecirc;m c&ocirc;ng nghệ m&agrave;n h&igrave;nh Always On Display, điều n&agrave;y cho ph&eacute;p thiết bị kh&ocirc;ng tắt m&agrave;n h&igrave;nh hẳn m&agrave; vẫn hiển thị m&agrave;n h&igrave;nh kh&oacute;a với một độ s&aacute;ng kh&aacute; nhỏ, đủ để m&igrave;nh c&oacute; thể đọc th&ocirc;ng b&aacute;o nhanh xem ng&agrave;y giờ kh&aacute; thuận tiện.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/always-on-display-la-gi-co-hao-pin-khong-huong-dan-1169208\" target=\"_blank\">Always On Display l&agrave; g&igrave;? C&oacute; tốn pin kh&ocirc;ng? C&aacute;ch bật, tắt</a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103239.jpg\" onclick=\"return false;\"><img alt=\"TÍnh năng trên màn hình - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103239.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103239.jpg\" title=\"TÍnh năng trên màn hình - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Tuy nhi&ecirc;n m&agrave;n h&igrave;nh l&uacute;c n&agrave;y kh&aacute; tối n&ecirc;n d&ugrave;ng ngo&agrave;i trời sẽ hơi kh&oacute; nh&igrave;n đ&ocirc;i ch&uacute;t. B&ecirc;n cạnh đ&oacute;, t&iacute;nh năng n&agrave;y cũng sẽ giảm tuổi thọ pin hơn so với th&ocirc;ng thường, để điện thoại qua đ&ecirc;m khoảng 7 tiếng ở chế độ Always On Display th&igrave; m&aacute;y mất khoảng 3 - 4% dung lượng pin.</p>\r\n\r\n<p><strong>Tần số qu&eacute;t 120 Hz</strong></p>\r\n\r\n<p>iPhone 14 Pro Max c&oacute; tần số qu&eacute;t rất cao vậy n&ecirc;n mọi thao t&aacute;c hằng ng&agrave;y của m&igrave;nh đều được phản hồi nhanh ch&oacute;ng, cảm gi&aacute;c vuốt chạm cũng trở n&ecirc;n th&iacute;ch hơn, hiện tượng khựng hay độ trễ trong l&uacute;c vuốt đ&atilde; được cải thiện đ&aacute;ng kể so với những chuẩn m&agrave;n h&igrave;nh 60 Hz.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103244.jpg\" onclick=\"return false;\"><img alt=\"Vuốt chạm mượt mà - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103244.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103244.jpg\" title=\"Vuốt chạm mượt mà - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra, Apple c&ograve;n bổ sung t&ugrave;y chọn để gi&uacute;p bạn c&oacute; thể dễ d&agrave;ng điều chỉnh tần số qu&eacute;t l&ecirc;n 120 Hz hay xuống 60 Hz t&ugrave;y v&agrave;o nhu cầu sử dụng, ph&ugrave; hợp để d&ugrave;ng trong những t&igrave;nh huống khẩn cấp như đang muốn tiết kiệm pin nhằm đảm bảo thiết bị c&oacute; thể duy tr&igrave; sử dụng d&agrave;i l&acirc;u.</p>\r\n\r\n<p><strong>Độ s&aacute;ng 2000 nits</strong></p>\r\n\r\n<p>So với những đối thủ kh&aacute;c trong ph&acirc;n kh&uacute;c th&igrave; độ s&aacute;ng 2000 nits được xem l&agrave; ưu điểm vượt trội m&agrave; &iacute;t c&oacute; h&atilde;ng n&agrave;o c&oacute; thể s&aacute;nh kịp, thế liệu n&oacute; gi&uacute;p &iacute;ch được g&igrave; v&agrave; c&oacute; thực sự hiệu quả hay kh&ocirc;ng chắc hẳn l&agrave; điều được rất nhiều người thắc mắc.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103246.jpg\" onclick=\"return false;\"><img alt=\"Độ sáng màn hình cao - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103246.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103246.jpg\" title=\"Độ sáng màn hình cao - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Cụ thể l&agrave; m&agrave;n h&igrave;nh c&oacute; độ s&aacute;ng cao đ&atilde; gi&uacute;p &iacute;ch cho m&igrave;nh rất nhiều trong mọi nhu cầu sử dụng hằng ng&agrave;y của m&igrave;nh, kể từ khi sử dụng iPhone 14 Pro Max việc xem bản đồ khi đang ở ngo&agrave;i đường l&uacute;c trời nắng gắt cũng trở n&ecirc;n dễ d&agrave;ng, m&agrave;n h&igrave;nh l&uacute;c n&agrave;y kh&ocirc;ng qu&aacute; sập tối hay l&oacute;a, c&aacute;c th&ocirc;ng b&aacute;o chỉ dẫn đường đi cũng đều được thể hiện r&otilde; r&agrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103249.jpg\" onclick=\"return false;\"><img alt=\"Độ sáng màn hình cao - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103249.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103249.jpg\" title=\"Độ sáng màn hình cao - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, khi chụp ảnh ở ngo&agrave;i trời nắng độ s&aacute;ng m&agrave;n h&igrave;nh cao cũng sẽ l&agrave; một lợi thế, n&oacute; hữu &iacute;ch trong l&uacute;c m&igrave;nh căn chỉnh g&oacute;c chụp khi mọi đường lưới đều được hiển thị r&otilde;, chủ thể đ&atilde; được lấy n&eacute;t hay chưa th&igrave; m&igrave;nh cũng c&oacute; thể dễ d&agrave;ng quan s&aacute;t trực tiếp ngay tr&ecirc;n m&agrave;n h&igrave;nh m&agrave; kh&ocirc;ng cần d&ugrave;ng tay để che chắn &aacute;nh s&aacute;ng chiếu v&agrave;o như phần lớn c&aacute;c d&ograve;ng điện thoại kh&aacute;c.</p>\r\n\r\n<p><strong>Mặt k&iacute;nh cường lực Ceramic Shield</strong></p>\r\n\r\n<p>Ceramic Shield c&oacute; lẽ kh&ocirc;ng c&ograve;n l&agrave; thuật ngữ qu&aacute; xa lạ đối với phần lớn người d&ugrave;ng khi đ&acirc;y được xem l&agrave; loại c&ocirc;ng nghệ đ&atilde; từng xuất hiện ở thế hệ iPhone 12, chuẩn n&agrave;y nổi tiếng với độ bền bỉ cực cao v&agrave; khả năng kh&aacute;ng lại vết xước rất tốt.</p>\r\n\r\n<p>Thực tế, m&igrave;nh đ&atilde; kiểm tra độ bền của mặt k&iacute;nh n&agrave;y th&ocirc;ng qua việc x&acirc;y x&aacute;t c&aacute;c loại vật dụng kh&aacute;c dễ tiếp x&uacute;c như: M&oacute;c kh&oacute;a, đầu d&acirc;y sạc, b&uacute;t bi v&agrave; cả gọng k&iacute;nh. Kết quả m&agrave; m&igrave;nh nhận được cũng hết sức ấn tượng, m&agrave;n h&igrave;nh gần như kh&ocirc;ng bị tổn hại khi kh&ocirc;ng thấy xuất hiện của bất kỳ một vết xước n&agrave;o cả.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103250.jpg\" onclick=\"return false;\"><img alt=\"Mặt kính kháng xước - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103250.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103250.jpg\" title=\"Mặt kính kháng xước - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Tuy nhi&ecirc;n đ&acirc;y cũng l&agrave; một b&agrave;i kiểm tra n&ecirc;n lực d&ugrave;ng để x&acirc;y x&aacute;t l&agrave; kh&ocirc;ng qu&aacute; lớn, v&igrave; thế người d&ugrave;ng cũng kh&ocirc;ng n&ecirc;n qu&aacute; chủ quan trước những t&igrave;nh huống l&agrave;m rơi. Điều n&agrave;y chỉ gi&uacute;p m&igrave;nh an t&acirc;m hơn trong việc đặt &uacute;p điện thoại l&ecirc;n mặt phẳng hay để c&ugrave;ng với c&aacute;c loại vật dụng kh&aacute;c m&agrave; kh&ocirc;ng lo bị trầy xước.</p>\r\n\r\n<h3>Bắt trọn mọi khoảnh khắc th&ocirc;ng qua camera ch&iacute;nh 48 MP</h3>\r\n\r\n<p><strong>Ảnh chụp chế độ thường</strong></p>\r\n\r\n<p>Trước khi đi v&agrave;o phần đ&aacute;nh gi&aacute; trải nghiệm m&igrave;nh c&ugrave;ng điểm sơ qua th&ocirc;ng số camera của iPhone 14 Pro Max, thiết bị năm nay vẫn sẽ được bố tr&iacute; với cụm 3 camera, trong đ&oacute; cảm biến ch&iacute;nh c&oacute; độ ph&acirc;n giải 48 MP, cảm biến g&oacute;c rộng 12 MP v&agrave; cuối c&ugrave;ng l&agrave; ống k&iacute;nh tele 12 MP. Với th&ocirc;ng số ấn tượng như vậy n&ecirc;n m&aacute;y hứa hẹn sẽ trở th&agrave;nh&nbsp;<a href=\"https://www.thegioididong.com/dtdd-chup-anh-quay-phim\" target=\"_blank\" title=\"Tham khảo điện thoại chụp ảnh, quay phim tại Thế Giới Di Động\">điện thoại chụp ảnh, quay phim</a>&nbsp;h&agrave;ng đầu trong năm 2022.</p>\r\n\r\n<p>Ảnh chụp từ điều kiện thường cho ra kết quả l&agrave;m m&igrave;nh rất ưng &yacute;, h&igrave;nh ảnh được t&aacute;i tạo với độ chi tiết cao, m&agrave;u sắc hiển thị rất ch&acirc;n thực, ảnh khi zoom sẽ kh&ocirc;ng bị bể qu&aacute; nhiều gi&uacute;p cho t&aacute;c vụ ph&oacute;ng to hay thay đổi khung h&igrave;nh kh&ocirc;ng bị thay đổi qu&aacute; nhiều về mặt chất lượng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103252.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103252.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103252.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103256.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103256.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103256.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Điểm ưng &yacute; nhất về phần camera ch&iacute;nh l&agrave; về khả năng c&acirc;n bằng m&agrave;u cực tốt, chất lượng ảnh cho ra c&oacute; gam m&agrave;u trung t&iacute;nh, kh&ocirc;ng xảy ra hiện tượng &aacute;m v&agrave;ng hay &aacute;m xanh như tr&ecirc;n một v&agrave;i chiếc smartphone của những đối thủ kh&aacute;c tr&ecirc;n thị trường. Điều n&agrave;y gi&uacute;p việc chỉnh sửa ảnh trở n&ecirc;n dễ d&agrave;ng hơn bởi m&igrave;nh kh&ocirc;ng cần tinh chỉnh sắc độ để đưa ch&uacute;ng về m&agrave;u ban đầu nữa.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103259.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103259.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103259.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p><strong>Ảnh chụp g&oacute;c si&ecirc;u rộng</strong></p>\r\n\r\n<p>C&ograve;n đối với chế độ chụp g&oacute;c si&ecirc;u rộng th&ocirc;ng qua cảm biến phụ 12 MP kết quả thu được l&agrave;m m&igrave;nh rất ưng &yacute;, bức ảnh chụp vẫn c&oacute; độ chi tiết cao, mọi khung cảnh trước mắt m&igrave;nh gần như được đưa hết v&agrave;o trong bức ảnh, những v&ugrave;ng mở rộng cũng kh&ocirc;ng hề bị b&oacute;p m&eacute;o hay giảm chất lượng ảnh gi&uacute;p cho tổng thể bức h&igrave;nh trở n&ecirc;n rất chất lượng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103301.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103301.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103301.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103303.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103303.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103303.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p><strong>Ảnh chụp ngược s&aacute;ng</strong></p>\r\n\r\n<p>Bị tối mặt hay l&oacute;a s&aacute;ng được xem l&agrave; t&igrave;nh huống phổ biến đối với c&aacute;c smartphone th&ocirc;ng thường, thế nhưng tr&ecirc;n chiếc iPhone 14 Pro Max th&igrave; điều n&agrave;y đ&atilde; được cải thiện đi đ&aacute;ng kể th&ocirc;ng qua t&iacute;nh năng Smart HDR 4.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103306.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103306.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103306.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Khi đối mặt với những nguồn c&oacute; độ s&aacute;ng cao điện thoại sẽ tự động nhận diện chủ thể v&agrave; khung cảnh, từ đ&oacute; tăng s&aacute;ng những v&ugrave;ng cần lấy n&eacute;t tập trung v&agrave; giảm độ s&aacute;ng cho những vị tr&iacute; c&ograve;n lại để gi&uacute;p cho bức ảnh của m&igrave;nh trở n&ecirc;n dễ nh&igrave;n hơn, ngo&agrave;i ra n&oacute; c&ograve;n gi&uacute;p cho bức ảnh của m&igrave;nh được tăng th&ecirc;m phần nghệ thuật.</p>\r\n\r\n<p><strong>Ảnh chụp tele</strong></p>\r\n\r\n<p>Ở chế độ chụp tele điện thoại hỗ trợ cho ch&uacute;ng ta kh&aacute; nhiều mức độ điều chỉnh từ 2X, 3X cho đến 5X v&agrave; 10X,...Tuy nhi&ecirc;n m&aacute;y sẽ chỉ hỗ trợ zoom quang học tối đa l&ecirc;n đến 3X v&agrave; với c&aacute;c mức độ lớn hơn th&igrave; điện thoại sẽ chuyển sang chế độ zoom kỹ thuật số.</p>\r\n\r\n<p>Ảnh chụp từ chế độ zoom quang học m&aacute;y sẽ sử dụng một camera ri&ecirc;ng biệt c&oacute; độ ph&acirc;n giải 12 MP để lấy n&eacute;t, chất lượng ảnh cho ra vẫn sẽ c&oacute; độ chi tiết cao, mọi thứ được lưu giữ gần như nguy&ecirc;n bản v&agrave; kh&ocirc;ng mấy kh&aacute;c biệt so với những bức ảnh chụp từ chế độ mặc định, tuy nhi&ecirc;n để tr&aacute;nh hiểu lầm th&igrave; đ&acirc;y cũng chỉ l&agrave; một v&iacute; dụ so s&aacute;nh nếu như kh&ocirc;ng soi x&eacute;t qu&aacute; kỹ về tỷ lệ độ ph&acirc;n giải của cả hai.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103308.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103308.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103308.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103310.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103310.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103310.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>C&ograve;n ở bức ảnh chụp từ t&iacute;nh năng zoom kỹ thuật số bức ảnh m&agrave; m&igrave;nh c&oacute; chụp được vẫn kh&aacute; ổn, ở đ&acirc;y camera của điện thoại sẽ l&agrave;m việc với nguy&ecirc;n l&yacute; tương tự như bạn đang crop bức ảnh của m&igrave;nh với khung h&igrave;nh v&agrave; tỷ lệ nhỏ hơn, v&igrave; thế chất lượng ảnh sẽ c&oacute; phần k&eacute;m hơn so với chụp mặc định.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103312.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103312.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103312.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Nhưng nhờ camera ch&iacute;nh c&oacute; độ ph&acirc;n giải lớn v&agrave; độ chi tiết thu được kh&aacute; cao, cho n&ecirc;n khi chụp bằng chế độ zoom kỹ thuật số th&igrave; ảnh vẫn c&oacute; độ chi tiết tốt, mọi vật thể v&agrave; khung cảnh đều được thu lại tương đối r&otilde; r&agrave;ng.</p>\r\n\r\n<p><strong>Ảnh chụp khung cảnh nhiều m&agrave;u sắc</strong></p>\r\n\r\n<p>Năm nay th&igrave; Apple truyền th&ocirc;ng kh&aacute; nhiều về c&aacute;c t&iacute;nh năng phụ cũng như loại c&ocirc;ng nghệ tr&ecirc;n camera của iPhone 14 Pro Max, được nhắc đến nhiều c&oacute; lẽ l&agrave; những c&aacute;i t&ecirc;n như: Deep Fusion, Smart HDR 4 v&agrave; Dolby Vision HDR.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103314.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103314.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103314.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Đ&acirc;y được xem l&agrave; những loại c&ocirc;ng nghệ gần như chưa c&oacute; mặt tr&ecirc;n bất kỳ một chiếc điện thoại Android n&agrave;o cả, vậy n&ecirc;n đ&acirc;y ch&iacute;nh l&agrave; lợi thế vượt trội m&agrave; m&igrave;nh đ&aacute;nh gi&aacute; rất cao tr&ecirc;n camera của iPhone 14 Pro Max.</p>\r\n\r\n<p>Thực tế th&igrave; ảnh chụp của điện thoại cho ra c&oacute; m&agrave;u rất giống so với ngo&agrave;i đời thật, độ chi tiết cực cao, những v&ugrave;ng c&oacute; m&agrave;u sắc ri&ecirc;ng biệt kh&ocirc;ng thấy xảy ra t&igrave;nh trạng pha trộn lẫn nhau, tổng thể bức ảnh l&agrave;m cho m&igrave;nh c&oacute; cảm gi&aacute;c nh&igrave;n rất đ&atilde; mắt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103316.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103316.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103316.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103318.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103318.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103318.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Bởi v&igrave; được hỗ trợ t&iacute;nh năng Deep Fusion n&ecirc;n m&aacute;y c&oacute; khả năng chụp tổng cộng l&agrave; 9 tấm trong qu&aacute; tr&igrave;nh m&igrave;nh chụp một bức h&igrave;nh, từ đ&oacute; m&aacute;y sẽ tự động ph&acirc;n t&iacute;ch để đưa ra bức h&igrave;nh c&oacute; độ chi tiết cũng như chất lượng tốt nhất để hiển thị. Vậy n&ecirc;n trong kh&uacute;c chụp m&igrave;nh c&oacute; xảy ra t&igrave;nh trạng run tay nhẹ th&igrave; ảnh cho ra vẫn kh&ocirc;ng hề bị nh&ograve;e hay mất n&eacute;t.</p>\r\n\r\n<h3>Hiệu năng đ&aacute;ng kinh ngạc với chip set Apple A16 Bionic</h3>\r\n\r\n<p>Theo th&ocirc;ng lệ từ trước đến nay của Apple th&igrave; mỗi lần ra mắt d&ograve;ng điện thoại mới l&agrave; nh&agrave; t&aacute;o lại tr&igrave;nh l&agrave;ng th&ecirc;m một con chip với hiệu năng cực khủng v&agrave; ở thế hệ điện iPhone 14 Series sẽ l&agrave; con chip Apple A16 Bionic 6 nh&acirc;n với xung nhịp tối đa l&ecirc;n đến 3.46 GHz.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/chip-a16-bionic-giup-iphone-14-lan-at-hieu-nang-1461587\" target=\"_blank\">Đ&aacute;nh gi&aacute; chip Apple A16 Bionic chi tiết về th&ocirc;ng số v&agrave; hiệu năng</a></p>\r\n\r\n<p>Theo như th&ocirc;ng số m&igrave;nh đo được qua c&aacute;c phần mềm con chip Apple A16 Bionic đạt được mức điểm như sau: 1887 (đơn nh&acirc;n), 5354 (đa nh&acirc;n) tr&ecirc;n Benchmark v&agrave; 15508 điểm đ&aacute;nh gi&aacute; tổng thể. Đ&acirc;y quả thực l&agrave; những con số qu&aacute; khủng khiếp, dường như chip đ&atilde; vượt mặt gần như to&agrave;n bộ c&aacute;c vi xử l&yacute; hiện đang c&oacute; mặt tr&ecirc;n thị trường di động trong năm 2022.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103320.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103320.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103320.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Bởi v&igrave; cầm tr&ecirc;n tay một chiếc m&aacute;y c&oacute; hiệu năng v&ocirc; đối như vậy cho n&ecirc;n m&igrave;nh cũng đ&atilde; tự tin mở ngay Genshin Impact với mức đồ họa cao nhất để trải nghiệm, chất lượng h&igrave;nh ảnh cho ra phải n&oacute;i l&agrave; v&ocirc; c&ugrave;ng đẹp mắt, mọi hiệu ứng đồ họa trong game đều được t&aacute;i hiện r&otilde; n&eacute;t, c&aacute;c pha h&agrave;nh động như đ&aacute;nh nhau, tung chi&ecirc;u đều được thể hiện mượt m&agrave; v&agrave; ổn định.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103323.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103323.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103323.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, đ&acirc;y c&ograve;n l&agrave; một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-6gb\" target=\"_blank\" title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\">điện thoại RAM 6 GB</a>&nbsp;cho n&ecirc;n c&aacute;c thao t&aacute;c về phần đa nhiệm đều được m&aacute;y xử l&yacute; dễ d&agrave;ng, d&ugrave;ng đồng thời nhiều ứng dụng cũng kh&ocirc;ng hề xảy ra t&igrave;nh trạng khựng hay load lại từ đầu mỗi khi m&igrave;nh mở lại.</p>\r\n\r\n<p>Ở phi&ecirc;n bản n&agrave;y điện thoại sẽ được t&iacute;ch hợp sẵn hệ điều h&agrave;nh iOS 16 kể từ khi k&iacute;ch hoạt m&aacute;y, được bổ sung h&agrave;ng loạt c&aacute;c t&iacute;nh năng mới mẻ n&ecirc;n m&igrave;nh cảm thấy rất l&agrave; hứng khởi trong qu&aacute; tr&igrave;nh trải nghiệm m&aacute;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103324.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103324.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103324.jpg\" title=\"Hiệu năng mạnh mẽ - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Những t&iacute;nh năng nổi bật v&agrave; cực kỳ hữu &iacute;ch tr&ecirc;n iOS 16 c&oacute; thể kể đến như: T&ugrave;y chỉnh m&agrave;n h&igrave;nh kh&oacute;a, th&ecirc;m widget mới cho m&agrave;n h&igrave;nh kh&oacute;a, Live Text, t&aacute;ch nền ảnh,... đều được m&igrave;nh sử dụng hằng ng&agrave;y một c&aacute;ch tiện lợi cho c&ocirc;ng việc v&agrave; kể cả những nhu cầu giải tr&iacute;.</p>\r\n\r\n<p>B&ecirc;n trong điện thoại sẽ được t&iacute;ch hợp vi&ecirc;n pin c&oacute; dung lượng l&ecirc;n tới 4323 mAh n&ecirc;n m&igrave;nh cũng đ&atilde; an t&acirc;m hơn trong việc sử dụng m&aacute;y phục vụ c&aacute;c t&aacute;c vụ cơ bản cả ng&agrave;y. Cụ thể l&agrave; trong một ng&agrave;y trải nghiệm m&aacute;y m&igrave;nh đ&atilde; mất khoảng 7 giờ 23 ph&uacute;t sử dụng li&ecirc;n tục để c&oacute; thể d&ugrave;ng hết 100% pin tr&ecirc;n điện thoại.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103327.jpg\" onclick=\"return false;\"><img alt=\"Pin trâu dùng lâu - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103327.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103327.jpg\" title=\"Pin trâu dùng lâu - iPhone 14 Pro Max 128GB\" /></a></p>\r\n\r\n<p>Với những bạn đang mong muốn c&oacute; cho m&igrave;nh một thiết bị c&oacute; ngoại h&igrave;nh đẹp, hiệu năng cao v&agrave; kể cả chụp ảnh quay phim chuy&ecirc;n nghiệm th&igrave; iPhone 14 Pro Max c&oacute; thể coi l&agrave; sự lựa chọn rất ph&ugrave; hợp cho năm 2022 v&agrave; 2023 sắp tới. So với những g&igrave; m&agrave; thiết bị mang lại cho ch&uacute;ng ta th&igrave; mức gi&aacute; b&aacute;n của điện thoại được xem l&agrave; cực kỳ xứng đ&aacute;ng.</p>\r\n', 'Chờ xét duyệt', 1, '11-09-2023', 5),
(5, 'VHL#2', 'Iphone 14 Pro 128GB', 24090000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'iphone2.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/iphone-14-pro\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 14 Pro 128GB tại Thế Giới Di Động\">iPhone 14 Pro 128GB</a>&nbsp;- Mẫu smartphone đến từ nh&agrave; Apple được mong đợi nhất năm 2022, lần n&agrave;y nh&agrave; T&aacute;o mang đến cho ch&uacute;ng ta một phi&ecirc;n bản với kiểu thiết kế h&igrave;nh notch mới, cấu h&igrave;nh mạnh mẽ nhờ con chip Apple A16 Bionic v&agrave; cụm camera c&oacute; độ ph&acirc;n giải được n&acirc;ng cấp l&ecirc;n đến 48 MP.</h3>\r\n\r\n<h3>Thiết kế mang d&aacute;ng vẻ sang trọng v&agrave; đặc trưng</h3>\r\n\r\n<p>Đến với thiết kế của iPhone 14 Pro năm nay, h&atilde;ng vẫn giữ lại n&eacute;t đặc trưng vốn c&oacute; từ c&aacute;c đời trước, vẫn mang trong m&igrave;nh ngoại h&igrave;nh vu&ocirc;ng vức với c&aacute;c cạnh v&agrave; mặt lưng v&aacute;t phẳng. Hiện tại thị hiếu của người d&ugrave;ng về kiểu thiết kế n&agrave;y vẫn đang rất cao, vậy n&ecirc;n theo m&igrave;nh thấy th&igrave; đ&acirc;y vẫn sẽ l</p>\r\n', 'Chờ xét duyệt', 1, '11-09-2023', 15);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(6, 'VHL#3', 'Iphone 11 64GB', 10390000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'iphone3.jpg', '<h3>Apple đ&atilde; ch&iacute;nh thức tr&igrave;nh l&agrave;ng bộ 3 si&ecirc;u phẩm iPhone 11, trong đ&oacute; phi&ecirc;n bản&nbsp;<a href=\"https://www.topzone.vn/iphone/iphone-11\" target=\"_blank\" title=\"Tham khảo thông tin sản phẩm tại TopZone.vn\">iPhone 11 64GB</a>&nbsp;c&oacute; mức gi&aacute; rẻ nhất nhưng vẫn được n&acirc;ng cấp mạnh mẽ như&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-xr-128gb\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone Xr tại Thegioididong.com\">iPhone Xr</a>&nbsp;ra mắt&nbsp;trước đ&oacute;.</h3>\r\n\r\n<h3>N&acirc;ng cấp mạnh mẽ về camera</h3>\r\n\r\n<p>N&oacute;i về n&acirc;ng cấp th&igrave; camera ch&iacute;nh l&agrave; điểm c&oacute; nhiều cải tiến nhất tr&ecirc;n thế hệ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone iPhone chính hãng\">iPhone</a>&nbsp;mới.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd42.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Thiết kế nhiều màu sắc\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd42.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd42.jpg\" title=\"Điện thoại iPhone 11 64GB | Thiết kế nhiều màu sắc\" /></a></p>\r\n\r\n<p>Nếu như trước đ&acirc;y iPhone Xr chỉ c&oacute; một camera th&igrave; nay với&nbsp;<a href=\"https://www.topzone.vn/iphone-11\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 11 tại TopZone thương hiệu của Thế Giới Di Động\">iPhone 11</a>&nbsp;ch&uacute;ng ta sẽ c&oacute; tới hai camera ở mặt sau.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-114-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Camera sau\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-114-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-114-1.jpg\" title=\"Điện thoại iPhone 11 64GB | Camera sau\" /></a></p>\r\n\r\n<p>Ngo&agrave;i camera ch&iacute;nh vẫn c&oacute; độ ph&acirc;n giải 12 MP th&igrave; ch&uacute;ng ta sẽ c&oacute; th&ecirc;m một camera g&oacute;c si&ecirc;u rộng v&agrave; cũng với độ ph&acirc;n giải tương tự.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-116.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Ảnh chụp chế độ chân dung\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-116.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-116.jpg\" title=\"Điện thoại iPhone 11 64GB | Ảnh chụp chế độ chân dung\" /></a></p>\r\n\r\n<p><em>Ảnh chụp ch&acirc;n dung với iPhone 11</em></p>\r\n\r\n<p>Theo Apple th&igrave; việc chuyển đổi qua lại giữa hai ống k&iacute;nh th&igrave; sẽ kh&ocirc;ng l&agrave;m thay đổi m&agrave;u sắc của bức ảnh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-2-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Ảnh chụp chế độ chân dung\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-2-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-2-1.jpg\" title=\"Điện thoại iPhone 11 64GB | Ảnh chụp chế độ chân dung\" /></a></p>\r\n\r\n<p><em>Ảnh chụp chế độ ch&acirc;n dung với iPhone 11</em></p>\r\n\r\n<p>Đ&acirc;y l&agrave; một điều được xem l&agrave; bước ngoặt bởi những chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone chính hãng\">smartphone</a>&nbsp;Android c&oacute; nhiều camera hiện nay sẽ thường bị sai lệch về m&agrave;u sắc khi chuyển đổi qua lại giữa c&aacute;c ống k&iacute;nh g&acirc;y cảm gi&aacute;c kh&aacute; kh&oacute; chịu cho người d&ugrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><i>Ảnh chụp với&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/deep-fusion-tren-dong-iphone-11-pro-la-gi-tinh-nan-1197418\" target=\"_blank\" title=\"Tìm hiểu tính năng Deel Fusion\" type=\"Tìm hiểu tính năng Deel Fusion\">Deep Fusion</a></i></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; với iPhone 11 th&igrave; đ&acirc;y sẽ l&agrave; lần đầu ti&ecirc;n Apple trang bị khả năng chụp đ&ecirc;m l&ecirc;n chiếc iPhone của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-113.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Ảnh chụp với chế độ Night Mode Ảnh chụp với chế độ Night Mode\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-113.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-113.jpg\" title=\"Điện thoại iPhone 11 64GB | Ảnh chụp với chế độ Night Mode Ảnh chụp với chế độ Night Mode\" /></a></p>\r\n\r\n<p><em>Ảnh chụp với&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/night-mode-la-gi-diem-khac-nhau-giu-night-mode-va-1197122\" target=\"_blank\" title=\"Tìm hiểu chế độ Night Mode\" type=\"Tìm hiểu chế độ Night Mode\">chế độ Night Mode</a></em></p>\r\n\r\n<p>Theo trải nghiệm th&igrave; t&iacute;nh năng n&agrave;y hoạt động rất hiệu quả đặc biệt trong những m&ocirc;i trường cực kỳ thiếu s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-112.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Ảnh chụp bằng camera sau\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-112.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-112.jpg\" title=\"Điện thoại iPhone 11 64GB | Ảnh chụp bằng camera sau\" /></a></p>\r\n\r\n<p>K&iacute;ch hoạt chế độ chụp đ&ecirc;m sẽ do iPhone tự quyết định việc của bạn chỉ cần đưa m&aacute;y l&ecirc;n v&agrave; chụp, rất đơn giản.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Ảnh chụp chế độ góc siêu rộng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-.jpg\" title=\"Điện thoại iPhone 11 64GB | Ảnh chụp chế độ góc siêu rộng\" /></a></p>\r\n\r\n<p><em>Ảnh chụp chế độ g&oacute;c si&ecirc;u rộng</em></p>\r\n\r\n<p>Năm nay Apple cũng đ&atilde; n&acirc;ng cấp độ ph&acirc;n giải camera trước n&ecirc;n 12 MP thay v&igrave; 7 MP như thế hệ trước đ&oacute;.</p>\r\n\r\n<p>Camera trước cũng c&oacute; một t&iacute;nh năng th&ocirc;ng minh, khi bạn xoay ngang điện thoại n&oacute; sẽ tự k&iacute;ch hoạt chế độ selfie g&oacute;c rộng để bạn c&oacute; thể chụp với nhiều người hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-111.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Ảnh selfie bằng camera trước\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-111.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-111.jpg\" title=\"Điện thoại iPhone 11 64GB | Ảnh selfie bằng camera trước\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra Apple cũng giới thiệu t&iacute;nh năng quay video slow motion d&agrave;nh cho camera trước, điều m&agrave; Apple chưa từng trang bị cho những chiếc iPhone trước đ&acirc;y.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/danh-gia-chi-tiet-apple-iphone-11-iphone-2019-1191805\" target=\"_blank\" title=\"Đánh giá chi tiết iPhone 11\" type=\"Đánh giá chi tiết iPhone 11\">Đ&aacute;nh gi&aacute; chi tiết iPhone 11: H&oacute;a &#39;b&atilde;o tố&#39; hay th&agrave;nh &#39;bom xịt&#39;?</a></p>\r\n\r\n<h3>Hiệu năng mạnh mẽ h&agrave;ng đầu thế giới</h3>\r\n\r\n<p>Mỗi lần ra&nbsp;<a href=\"https://www.topzone.vn/iphone\" target=\"_blank\" title=\"Tham khảo iPhone kinh doanh tại TopZone thành viên của Thế Giới Di Động\">iPhone</a>&nbsp;mới l&agrave; mỗi lần Apple mang đến cho người d&ugrave;ng một trải nghiệm về hiệu năng &quot;chưa từng c&oacute;&quot;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n iPhone 11 mới Apple n&acirc;ng cấp con chip của m&igrave;nh l&ecirc;n thế hệ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-chip-apple-a13-bionic-tren-iphone-11-n-1197492\" target=\"_blank\" title=\"Tìm hiểu về chip Apple A13 Bionic\" type=\"Tìm hiểu về chip Apple A13 Bionic\">Apple A13 Bionic</a>&nbsp;rất mạnh mẽ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-dmx18.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Điểm hiệu năng Antutu Benchmark\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-dmx18.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-dmx18.jpg\" title=\"Điện thoại iPhone 11 64GB | Điểm hiệu năng Antutu Benchmark\" /></a></p>\r\n\r\n<p>Chiếc iPhone n&agrave;y cũng được n&acirc;ng cấp dung lượng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-4gb\" target=\"_blank\" title=\"Tham khảo điện thoại có RAM 4 GB tại Thegioididong.com\">RAM 4 GB</a>&nbsp;thay v&igrave; 3 GB như thế hệ trước đ&oacute;.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-119.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Trải nghiệm thao tác\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-119.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-119.jpg\" title=\"Điện thoại iPhone 11 64GB | Trải nghiệm thao tác\" /></a></p>\r\n\r\n<p>Ở mức cấu h&igrave;nh tr&ecirc;n gi&uacute;p&nbsp;<a href=\"https://www.thegioididong.com/dtdd-choi-game\" target=\"_blank\" title=\"Tham khảo các mẫu điện thoại chơi game tại Thế Giới Di Động\">điện thoại chơi game</a>&nbsp;tốt với c&aacute;c tựa game phổ biến hiện nay một c&aacute;ch mượt m&agrave;, ổn định. Mọi thao t&aacute;c tr&ecirc;n iPhone mới cũng cho tốc độ phản hồi nhanh m&agrave; bạn gần như sẽ kh&ocirc;ng cảm nhận được sự giật lag cho d&ugrave; c&oacute; sử dụng trong một thời gian d&agrave;i.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd4.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Trải nghiệm chơi game\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd4.jpg\" title=\"Điện thoại iPhone 11 64GB | Trải nghiệm chơi game\" /></a></p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/thu-nghiem-do-ben-iphone-11-cao-xuoc-nung-nong-va-be-cong-1203631\" target=\"_blank\" title=\"Thử nghiệm độ bền iPhone 11\" type=\"Thử nghiệm độ bền iPhone 11\">Thử nghiệm độ bền iPhone 11: C&agrave;o xước, nung n&oacute;ng v&agrave; bẻ cong</a></p>\r\n\r\n<p>Phi&ecirc;n bản iOS 15 (12/2021) đi k&egrave;m với chiếc iPhone n&agrave;y cũng được trang bị nhiều t&iacute;nh năng hơn gi&uacute;p bạn sử dụng chiếc iPhone hiệu quả hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-112-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Thiết kế thời trang\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-112-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-112-1.jpg\" title=\"Điện thoại iPhone 11 64GB | Thiết kế thời trang\" /></a></p>\r\n\r\n<p>Face ID tr&ecirc;n iPhone 11 cũng được cải tiến để c&oacute; thể nhận diện ở nhiều g&oacute;c độ hơn v&agrave; tốc độ phản hồi nhanh hơn.</p>\r\n\r\n<h3>Những thay đổi về thiết kế theo hướng t&iacute;ch cực</h3>\r\n\r\n<p>Ch&uacute;ng ta sẽ c&oacute; một mặt lưng ho&agrave;n thiện dưới dạng k&iacute;nh v&agrave; Apple n&oacute;i rằng họ đ&atilde; sử dụng loại k&iacute;nh bền nhất từ trước tới nay cho chiếc iPhone n&agrave;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-113-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Thiết kế cụm camera kép ở mặt sau\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-113-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-113-1.jpg\" title=\"Điện thoại iPhone 11 64GB | Thiết kế cụm camera kép ở mặt sau\" /></a></p>\r\n\r\n<p>Camera k&eacute;p tr&ecirc;n iPhone mới cũng được thiết kế lại v&agrave; tin vui l&agrave; n&oacute; sẽ bớt lồi hơn so với tr&ecirc;n iPhone Xr trước đ&acirc;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-115-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Thiết kế thời trang\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-115-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-115-1.jpg\" title=\"Điện thoại iPhone 11 64GB | Thiết kế thời trang\" /></a></p>\r\n\r\n<p>Điểm nhấn về cụm camera to bản ở mặt sau sẽ gi&uacute;p người kh&aacute;c dễ d&agrave;ng nhận biết bạn đang sử dụng một chiếc iPhone 11 tr&ecirc;n tay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Logo quả t&aacute;o truyền thống của Apple nay đ&atilde; được di chuyển về phần ch&iacute;nh giữa của mặt lớn thay v&igrave; đặt lệch về ph&iacute;a cạnh tr&ecirc;n như những đời iPhone trước đ&oacute;.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd17.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Khả năng chóng nước chuẩn IP68\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd17.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd17.jpg\" title=\"Điện thoại iPhone 11 64GB | Khả năng chóng nước chuẩn IP68\" /></a></p>\r\n\r\n<p>Apple cho biết họ đ&atilde; ho&agrave;n thiện tr&ecirc;n iPhone mới để n&oacute; cho khả năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd?f=chong-nuoc-bui\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone chống nước chống bụi\">chống nước</a>&nbsp;tốt hơn v&agrave; người d&ugrave;ng c&oacute; thể y&ecirc;n t&acirc;m về điều đ&oacute;.&nbsp;</p>\r\n\r\n<h3>Thời lượng pin tốt nhất từ trước tới nay</h3>\r\n\r\n<p>Khi n&oacute;i đến thời lượng pin iPhone 11, hẳn nhiều người đ&atilde; ước ao rằng m&aacute;y sẽ c&oacute; vi&ecirc;n pin tốt giống như iPhone Xr (c&oacute; thời lượng pin tốt nhất so với bất kỳ iPhone hiện đại n&agrave;o).</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd46.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Thời lượng pin\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd46.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd46.jpg\" title=\"Điện thoại iPhone 11 64GB | Thời lượng pin\" /></a></p>\r\n\r\n<p>Tuy nhi&ecirc;n bạn sẽ c&ograve;n c&oacute; một chiếc m&aacute;y thậm ch&iacute; c&ograve;n tốt hơn nữa.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-116-1.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Thiết kế hiện đại\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-116-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-116-1.jpg\" title=\"Điện thoại iPhone 11 64GB | Thiết kế hiện đại\" /></a></p>\r\n\r\n<p>Theo Apple th&igrave; chiếc iPhone mới sẽ c&oacute; thời lượng pin tr&acirc;u hơn 1 giờ so với chiếc iPhone Xr.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd40.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Giao diện Dark Mode\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd40.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd40.jpg\" title=\"Điện thoại iPhone 11 64GB | Giao diện Dark Mode\" /></a></p>\r\n\r\n<p>Như vậy với iPhone mới bạn ho&agrave;n to&agrave;n c&oacute; thể sử dụng m&aacute;y l&ecirc;n tới 2 ng&agrave;y m&agrave; kh&ocirc;ng cần lo lắng việc thiết bị sẽ hết pin giữa chừng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd39.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Thời lượng pin\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd39.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-11-tgdd39.jpg\" title=\"Điện thoại iPhone 11 64GB | Thời lượng pin\" /></a></p>\r\n\r\n<p>Tất nhi&ecirc;n m&aacute;y cũng sẽ hỗ trợ c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/dtdd?f=sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone sạc pin nhanh\">sạc nhanh</a>&nbsp;nhưng bạn phải mua th&ecirc;m củ sạc b&ecirc;n ngo&agrave;i để c&oacute; thể sử dụng t&iacute;nh năng n&agrave;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-117.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại iPhone 11 64GB | Thời lượng sử dụng dài\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-117.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/153856/iphone-117.jpg\" title=\"Điện thoại iPhone 11 64GB | Thời lượng sử dụng dài\" /></a></p>\r\n\r\n<p>Với chừng đ&oacute; t&iacute;nh năng, chừng đ&oacute; cải tiến th&igrave; chiếc iPhone 11 n&agrave;y tự tin sẽ l&agrave; một đối thủ đ&aacute;ng gờm với những chiếc flagship đến từ c&aacute;c h&atilde;ng Android đang c&oacute; mặt tr&ecirc;n thị trường.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Đã đăng', 1, '09-09-2023', 7),
(7, 'VHL#4', 'Iphone 14 128GB', 18890000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'iphone4.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/iphone-14\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 14 tại Thế Giới Di Động\">iPhone 14 128GB</a>&nbsp;được xem l&agrave; mẫu smartphone b&ugrave;ng nổ của nh&agrave; t&aacute;o trong năm 2022, ấn tượng với ngoại h&igrave;nh trẻ trung, m&agrave;n h&igrave;nh chất lượng đi k&egrave;m với những cải tiến về hệ điều h&agrave;nh v&agrave; thuật to&aacute;n xử l&yacute; h&igrave;nh ảnh, gi&uacute;p m&aacute;y trở th&agrave;nh c&aacute;i t&ecirc;n thu h&uacute;t được đ&ocirc;ng đảo người d&ugrave;ng quan t&acirc;m tại thời điểm ra mắt.</h3>\r\n\r\n<h3>iPhone 14 sở hữu thiết kế cao cấp</h3>\r\n\r\n<p>Với phi&ecirc;n bản&nbsp;ti&ecirc;u chuẩn th&igrave; nh&agrave; Apple vẫn giữ nguy&ecirc;n kiểu d&aacute;ng thiết kế so với thế hệ tiền nhiệm, vẫn l&agrave; mặt lưng phẳng c&ugrave;ng bộ khung vu&ocirc;ng gi&uacute;p m&aacute;y trở n&ecirc;n hiện đại hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-100323-101502.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế hiện đại - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-100323-101502.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-100323-101502.jpg\" title=\"Thiết kế hiện đại - iPhone 14 128GB\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd-apple-iphone-14-series\" target=\"_blank\" title=\"Tham khảo iPhone 14 tại Thế Giới Di Động\">iPhone 14</a>&nbsp;c&oacute; k&iacute;ch thước chiều ngang l&agrave; 71.5 mm n&ecirc;n m&aacute;y c&oacute; thể dễ d&agrave;ng nằm gọn trong l&ograve;ng b&agrave;n tay mỗi khi sử dụng, điều n&agrave;y l&agrave;m cho&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại di động đang kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;trở n&ecirc;n ph&ugrave; hợp hơn với nhiều đối tượng người d&ugrave;ng hơn, kể cả những bạn nữ c&oacute; b&agrave;n tay nhỏ nhắn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-camera.jpg\" onclick=\"return false;\"><img alt=\"Camera - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-camera.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-camera.jpg\" title=\"Camera - iPhone 14 128GB\" /></a></p>\r\n\r\n<p>Mặt lưng của điện thoại được thiết kế từ k&iacute;nh cường lực v&agrave; ho&agrave;n thiện theo kiểu nhẵn b&oacute;ng, theo m&igrave;nh th&igrave; c&aacute;ch l&agrave;m n&agrave;y gi&uacute;p cho iPhone 14 tr&ocirc;ng cuốn h&uacute;t hơn, b&ecirc;n cạnh đ&oacute; n&oacute; m&aacute;y cũng kh&aacute; bền bỉ c&oacute; thể mang lại khả năng chống chịu c&aacute;c vết xước được tốt hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100027.jpg\" onclick=\"return false;\"><img alt=\"Mặt lưng kính cường lực - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100027.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100027.jpg\" title=\"Mặt lưng kính cường lực - iPhone 14 128GB\" /></a></p>\r\n\r\n<p>C&oacute; một lưu &yacute; nhỏ ở phần thiết kế l&agrave; m&aacute;y kh&aacute; dễ b&aacute;m dấu v&acirc;n tay, điều n&agrave;y c&agrave;ng th&ecirc;m lộ r&otilde; ở những phi&ecirc;n bản c&oacute; m&agrave;u đậm như đen v&agrave; đỏ, c&ograve;n ở c&aacute;c phi&ecirc;n bản m&agrave;u s&aacute;ng như xanh dương, trắng v&agrave; t&iacute;m nhạt th&igrave; điều n&agrave;y cũng được cải thiện.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100029.jpg\" onclick=\"return false;\"><img alt=\"Hạn chế vết bám - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100029.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100029.jpg\" title=\"Hạn chế vết bám - iPhone 14 128GB\" /></a></p>\r\n\r\n<p>Tuy nhi&ecirc;n đ&acirc;y cũng l&agrave; điều thường gặp tr&ecirc;n c&aacute;c mẫu điện thoại c&oacute; mặt lưng k&iacute;nh n&ecirc;n m&igrave;nh cũng kh&ocirc;ng xem đ&acirc;y l&agrave; điểm trừ d&agrave;nh cho iPhone 14, bằng c&aacute;ch trang bị th&ecirc;m ốp lưng l&agrave; ta đ&atilde; c&oacute; thể khắc phục ho&agrave;n to&agrave;n t&igrave;nh trạng tr&ecirc;n v&agrave; c&ograve;n tăng th&ecirc;m độ bền cho điện thoại.</p>\r\n\r\n<p>Năm nay Apple cho ra mắt hai phi&ecirc;n bản c&oacute; m&agrave;u mới d&agrave;nh cho iPhone 14 l&agrave; t&iacute;m nhạt v&agrave; xanh dương, theo m&igrave;nh thấy th&igrave; m&agrave;u xanh n&agrave;y c&oacute; m&agrave;u dịu nhẹ hơn so với iPhone 13. Vậy n&ecirc;n nhờ m&agrave;u sắc m&agrave; m&igrave;nh c&oacute; thể dễ d&agrave;ng ph&acirc;n biệt giữa hai d&ograve;ng điện thoại, nếu muốn mọi người xung quanh biết được rằng bạn đang sở hữu iPhone 14 th&igrave; hai m&agrave;u sắc n&agrave;y sẽ l&agrave; sự lựa chọn rất ph&ugrave; hợp.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100030.jpg\" onclick=\"return false;\"><img alt=\"Màu sắc khác biệt - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100030.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100030.jpg\" title=\"Màu sắc khác biệt - iPhone 14 128GB\" /></a></p>\r\n\r\n<h3>Giải tr&iacute; đ&atilde; mắt hơn tr&ecirc;n m&agrave;n h&igrave;nh chất lượng&nbsp;của iPhone 14</h3>\r\n\r\n<p>M&aacute;y sử dụng tấm nền OLED n&ecirc;n m&agrave;u sắc m&agrave; m&aacute;y mang lại rất c&oacute; chiều s&acirc;u, m&agrave;u sắc hiển thị với độ ch&iacute;nh x&aacute;c m&agrave;u cao n&ecirc;n sẽ kh&ocirc;ng bị qu&aacute; ảo như tr&ecirc;n những tấm nền kh&aacute;c m&agrave; ta hay gặp l&agrave; AMOLED v&agrave; Super AMOLED.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-oled-la-gi-905762\" target=\"_blank\">M&agrave;n h&igrave;nh OLED l&agrave; g&igrave;? C&oacute; g&igrave; nổi bật? Thiết bị n&agrave;o c&oacute; m&agrave;n h&igrave;nh OLED?</a></p>\r\n\r\n<p>Tr&ecirc;n iPhone 14 vẫn sẽ sử dụng m&agrave;n h&igrave;nh kiểu tai thỏ, m&agrave;n h&igrave;nh&nbsp;Dynamic Island chỉ xuất hiện tr&ecirc;n d&ograve;ng sản phẩm Pro của h&atilde;ng, như&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-14-pro\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 14 Pro tại Thế Giới Di Động\">iPhone 14 Pro</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-14-pro-max\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 14 Pro Max tại Thế Giới Di Động\">iPhone 14 Pro Max</a>.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100032.jpg\" onclick=\"return false;\"><img alt=\"Màn hình sinh động - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100032.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100032.jpg\" title=\"Màn hình sinh động - iPhone 14 128GB\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh của&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone đang kinh doanh tại Thế Giới Di Động\">điện thoại iPhone</a>&nbsp;c&oacute;&nbsp;k&iacute;ch thước 6.1 inch,&nbsp;độ ph&acirc;n giải 1170 x 2532 Pixels, mật độ điểm ảnh m&agrave; m&aacute;y mang đến kh&aacute; cao, theo như h&atilde;ng c&ocirc;ng bố th&igrave; iPhone 14 c&oacute; mật độ điểm ảnh khoảng 460 ppi.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100034.jpg\" onclick=\"return false;\"><img alt=\"HIển thị hình ảnh sắc - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100034.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100034.jpg\" title=\"HIển thị hình ảnh sắc - iPhone 14 128GB\" /></a></p>\r\n\r\n<p>Điều m&agrave; m&igrave;nh kh&aacute; ấn tượng tr&ecirc;n m&agrave;n h&igrave;nh của iPhone 14 l&agrave; về độ s&aacute;ng, m&aacute;y c&oacute; một kh&aacute; ổn định khi sử dụng điện thoại ở những m&ocirc;i trường c&oacute; độ s&aacute;ng cao như ngo&agrave;i trời nắng cũng trở n&ecirc;n dễ d&agrave;ng hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100036.jpg\" onclick=\"return false;\"><img alt=\"Hiển thị hình ảnh độ sáng cao - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100036.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100036.jpg\" title=\"Hiển thị hình ảnh độ sáng cao - iPhone 14 128GB\" /></a></p>\r\n\r\n<h3>Bắt trọn mọi khoảnh khắc qua cụm camera chất lượng</h3>\r\n\r\n<p>Từ trước đến nay th&igrave; camera của iPhone lu&ocirc;n được đ&aacute;nh gi&aacute; cao về chất lượng v&agrave; đối với iPhone 14 th&igrave; điều n&agrave;y cũng kh&ocirc;ng l&agrave; ngoại lệ. Tuy kh&ocirc;ng c&oacute; sự n&acirc;ng cấp về độ ph&acirc;n giải so với thế hệ trước, nhưng với những g&igrave; m&agrave; m&aacute;y l&agrave;m được th&igrave; m&igrave;nh thấy iPhone 14 cho ra h&igrave;nh ảnh c&oacute; độ ch&acirc;n thực về m&agrave;u sắc bắt mắt hơn iPhone 13.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100038.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp đẹp mắt - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100038.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100038.jpg\" title=\"Ảnh chụp đẹp mắt - iPhone 14 128GB\" /></a></p>\r\n\r\n<p>Vẫn sẽ l&agrave; bộ đ&ocirc;i ống k&iacute;nh 12 MP được trang bị ở mặt lưng v&agrave; một trong số đ&oacute; l&agrave; camera g&oacute;c si&ecirc;u rộng c&oacute; khả năng ghi h&igrave;nh với trường nh&igrave;n 120 độ.</p>\r\n\r\n<p>Ảnh chụp được từ t&iacute;nh năng mặc định cho ra c&oacute; độ chi tiết kh&aacute; cao, ảnh khi zoom l&ecirc;n khoảng 30% cũng &iacute;t khi bị bể, nhờ vậy m&agrave; m&igrave;nh c&oacute; thể thay đổi bức h&igrave;nh bằng c&aacute;ch ph&oacute;ng to m&agrave; kh&ocirc;ng qu&aacute; lo lắng đến việc chất lượng ảnh bị giảm đi nhiều.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100040.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp sắc nét - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100040.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100040.jpg\" title=\"Ảnh chụp sắc nét - iPhone 14 128GB\" /></a></p>\r\n\r\n<p><em>*Ảnh chụp từ t&iacute;nh năng mặc định&nbsp;</em></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100042.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100042.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100042.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 128GB\" /></a></p>\r\n\r\n<p><em>*Ảnh chụp sau khi ph&oacute;ng to</em></p>\r\n\r\n<p>Ở những khung cảnh c&oacute; nhiều chi tiết nhỏ th&igrave; m&aacute;y cũng c&oacute; thể xử l&yacute; v&agrave; thu lại một c&aacute;ch sắc n&eacute;t, như bức ảnh m&agrave; m&igrave;nh c&oacute; chụp b&ecirc;n dưới th&igrave; những t&aacute;n l&aacute; đều t&aacute;i hiện lại một c&aacute;ch r&otilde; r&agrave;ng m&agrave; kh&ocirc;ng phải d&ugrave;ng AI để m&ocirc; phỏng lại như một v&agrave;i mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Tham khảo điện thoại Android đang kinh doanh tại Thế Giới Di Động\">điện thoại Android</a>&nbsp;c&oacute; tr&ecirc;n thị trường.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100044.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100044.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100044.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 128GB\" /></a></p>\r\n\r\n<p>iPhone 14 được hỗ trợ t&iacute;nh năng Smart HDR 4 n&ecirc;n những bức ảnh m&agrave; m&igrave;nh chụp ở điều kiện ngược s&aacute;ng hay ngo&agrave;i trời cũng trở n&ecirc;n chất lượng hơn, m&agrave;u sắc được t&aacute;i hiện lại tươi tắn v&agrave; độ s&aacute;ng cũng sẽ tự động tinh chỉnh gi&uacute;p cho bức ảnh trở n&ecirc;n nịnh mắt v&agrave; dễ nh&igrave;n.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100046.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp ngoài trời - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100046.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100046.jpg\" title=\"Ảnh chụp ngoài trời - iPhone 14 128GB\" /></a></p>\r\n\r\n<p>C&ograve;n ở bức h&igrave;nh chụp từ t&iacute;nh năng g&oacute;c si&ecirc;u rộng th&igrave; kết quả cũng l&agrave;m m&igrave;nh kh&aacute; bất ngờ, ảnh c&oacute; độ chi tiết tốt, c&aacute;c mảng s&aacute;ng tối trong h&igrave;nh được xử l&yacute; t&aacute;ch bạch r&otilde; r&agrave;ng, m&agrave;u sắc cũng được xử l&yacute; th&ocirc;ng minh để cho bức ảnh tr&ocirc;ng đẹp mắt kh&ocirc;ng thua k&eacute;m nhiều so với camera ch&iacute;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100048.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp góc siêu rộng - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100048.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100048.jpg\" title=\"Ảnh chụp góc siêu rộng - iPhone 14 128GB\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100050.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp góc siêu rộng - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100050.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100050.jpg\" title=\"Ảnh chụp góc siêu rộng - iPhone 14 128GB\" /></a></p>\r\n\r\n<p>Đối với những khung cảnh c&oacute; nhiều đường dẫn hay điểm h&uacute;t th&igrave; t&iacute;nh năng chụp ảnh g&oacute;c si&ecirc;u rộng tr&ecirc;n iPhone 14 sẽ l&agrave;m cho bức h&igrave;nh tr&ocirc;ng c&oacute; chiều s&acirc;u hơn, với tay mơ về chụp ảnh như m&igrave;nh th&igrave; cũng dễ d&agrave;ng c&oacute; được những bức ảnh cuốn h&uacute;t mang đậm t&iacute;nh nghệ thuật.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100051.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100051.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100051.jpg\" title=\"Ảnh chụp từ camera - iPhone 14 128GB\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i việc chụp ảnh tốt ra th&igrave; khả năng quay video của iPhone 14 cũng l&agrave;m cho m&igrave;nh cực kỳ th&iacute;ch th&uacute;, m&aacute;y c&oacute; thể quay video đạt chuẩn 4K ở tốc độ khung h&igrave;nh 60 fps.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; th&igrave; m&aacute;y cũng sẽ được hỗ trợ c&aacute;c t&iacute;nh năng xịn s&ograve; như Cinematic v&agrave; chống rung quang học (OIS), điều n&agrave;y gi&uacute;p cho những thước phim quay được trở n&ecirc;n ổn định hơn tr&ocirc;ng rất chuy&ecirc;n nghiệp.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/che-do-quay-phim-cinematic-mode-co-gi-noi-bat-1383619\" target=\"_blank\">Chế độ quay phim điện ảnh Cinematic Mode c&oacute; g&igrave; nổi bật?</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Hiệu năng vượt trội nhờ con chip Apple A15 Bionic</h3>\r\n\r\n<p>Apple A15 Bionic sẽ l&agrave; con chip được trang bị cho điện thoại iPhone 14 năm nay, đ&acirc;y vốn dĩ l&agrave; con chip từng xuất hiện tr&ecirc;n mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-13\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 13 tại Thế Giới Di Động\">iPhone 13</a>&nbsp;v&agrave; đạt được rất nhiều th&agrave;nh tựu về khả năng xử l&yacute; t&aacute;c vụ vượt trội, được đ&aacute;nh gi&aacute; l&agrave; con chip c&oacute; hiệu năng nhất nh&igrave; thị trường điện thoại v&agrave;o thời điểm đ&oacute;.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-chip-apple-a15-bionic-suc-manh-cuc-khung-duoc-he-1339072\" target=\"_blank\">T&igrave;m hiểu về chip Apple A15 Bionic - Hiệu năng c&oacute; khủng như lời đồn?</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thế nhưng đến năm 2022 th&igrave; con chip n&agrave;y vẫn tiếp tục xuất hiện tr&ecirc;n iPhone 14 v&agrave; n&oacute; l&agrave;m cho kh&aacute; nhiều người d&ugrave;ng đặt c&acirc;u hỏi: &ldquo;Liệu n&oacute; c&oacute; thực sự kh&aacute;c biệt với iPhone 13 v&agrave; ta c&oacute; n&ecirc;n bỏ tiền ra mua kh&ocirc;ng?&rdquo;.&nbsp;</p>\r\n\r\n<p>V&agrave; c&acirc;u trả lời của m&igrave;nh về vấn đề n&agrave;y l&agrave; &ldquo;c&oacute;&rdquo;, n&oacute; quả thực đ&atilde; tốt hơn kh&aacute; nhiều về hiệu năng bởi con chip n&agrave;y khi được trang bị tr&ecirc;n iPhone 14 sẽ hoạt động với 5 nh&acirc;n GPU thay v&igrave; 4 nh&acirc;n tr&ecirc;n iPhone 13, v&agrave; điều n&agrave;y đ&atilde; g&oacute;p phần l&agrave;m cho c&aacute;c t&aacute;c vụ chơi game hay xử l&yacute; c&ocirc;ng việc đồ họa trở của m&igrave;nh trở n&ecirc;n mượt m&agrave; v&agrave; nhanh ch&oacute;ng hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Để c&oacute; thể đ&aacute;nh gi&aacute; hiệu năng của m&aacute;y một c&aacute;ch trực quan hơn th&ocirc;ng qua những con số th&igrave; m&igrave;nh cũng đ&atilde; test qua một v&agrave;i phần mềm đ&aacute;nh gi&aacute; hiệu năng, mức điểm m&agrave; iPhone 14 đạt được cũng rất cao v&agrave; cụ thể như sau: 1730 (đơn nh&acirc;n), 4500 (đa nh&acirc;n) tr&ecirc;n Geekbench v&agrave; 833198 điểm tr&ecirc;n AnTuTu Benchmark.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tại thời điểm ra mắt iPhone 14 th&igrave; Apple A15 Bionic vẫn được xếp loại năm trong top 10 chipset c&oacute; hiệu năng mạnh nhất thị trường, nhờ vậy m&agrave; hầu hết mọi tựa game m&agrave; m&igrave;nh chơi qua đều được m&aacute;y xử l&yacute; dễ d&agrave;ng, kể cả Genshin Impact ở mức cấu h&igrave;nh cao th&igrave; điện thoại cũng c&oacute; thể xử l&yacute; được v&agrave; cho ra trải nghiệm khi chơi l&agrave; kh&aacute; ổn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kể từ khi k&iacute;ch hoạt th&igrave; m&aacute;y đ&atilde; được t&iacute;ch hợp sẵn hệ điều h&agrave;nh iOS 16 ti&ecirc;n tiến nhất của Apple trong năm 2022, ở phi&ecirc;n bản n&agrave;y th&igrave; m&aacute;y được bổ sung kh&aacute; nhiều c&aacute;c t&iacute;nh năng từ tối ưu h&oacute;a giao diện cho tới khả năng kết nối v&agrave; tương th&iacute;ch với những m&oacute;n phụ kiện.</p>\r\n\r\n<p>Điều m&agrave; m&igrave;nh th&iacute;ch nhất tr&ecirc;n iOS 16 lần n&agrave;y l&agrave; về giao diện, m&aacute;y cho ph&eacute;p m&igrave;nh t&ugrave;y biến kh&ocirc;ng gian hiển thị v&agrave; m&agrave;n h&igrave;nh kh&oacute;a với nhiều chủ đề kh&aacute;c nhau hết sức mới mẻ. Ngo&agrave;i ra th&igrave; tr&ecirc;n cửa h&agrave;ng ứng dụng của Apple c&ograve;n xuất hiện th&ecirc;m nhiều app c&oacute; widget đầy th&uacute; vị để l&agrave;m cho m&agrave;n h&igrave;nh của m&igrave;nh tr&ocirc;ng sinh động hơn.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/ios-16-co-gi-moi-huong-dan-cach-cap-nhat-ios-16-beta-1439395\" target=\"_blank\">iOS 16 ch&iacute;nh thức c&oacute; g&igrave; mới? Hỗ trợ m&aacute;y n&agrave;o? C&oacute; n&ecirc;n cập nhật?</a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100105.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ hệ điều hành mới - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100105.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100105.jpg\" title=\"Hỗ trợ hệ điều hành mới - iPhone 14 128GB\" /></a></p>\r\n\r\n<h3>Duy tr&igrave; thời gian sử dụng d&agrave;i l&acirc;u</h3>\r\n\r\n<p>iPhone 14 trang bị vi&ecirc;n pin c&oacute; dung lượng&nbsp;3279 mAh, sau một thời gian sử dụng th&igrave; m&igrave;nh thấy m&aacute;y c&oacute; thể đ&aacute;p ứng nhu cầu sử dụng l&acirc;u hơn m&igrave;nh tưởng tượng, cụ thể l&agrave; m&aacute;y c&oacute; thể đạt 8 giờ 5 ph&uacute;t khi sử dụng đa t&aacute;c vụ trong v&ograve;ng một ng&agrave;y li&ecirc;n tục.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100106.jpg\" onclick=\"return false;\"><img alt=\"Sử dụng dài lâu - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100106.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100106.jpg\" title=\"Sử dụng dài lâu - iPhone 14 128GB\" /></a></p>\r\n\r\n<p>C&ograve;n về thời gian lấp đầy vi&ecirc;n pin của m&aacute;y cũng rất nhanh bởi thiết bị được hỗ trợ sạc nhanh 20 W, b&ecirc;n cạnh đ&oacute; th&igrave; m&aacute;y cũng được trang bị c&ocirc;ng nghệ sạc kh&ocirc;ng d&acirc;y MagSafe tiện lợi. Kh&aacute; tiếc l&agrave; m&aacute;y kh&ocirc;ng trang bị k&egrave;m theo củ sạc, v&igrave; thế m&agrave; m&igrave;nh c&oacute; trang bị th&ecirc;m phụ kiện từ b&ecirc;n ngo&agrave;i để test, sau khoảng 1 tiếng hơn l&agrave; m&aacute;y đ&atilde; c&oacute; thể lấp đầy từ 0% đến 100% pin.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100108.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ sạc nhanh - iPhone 14 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100108.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/240259/iphone-14-261222-100108.jpg\" title=\"Hỗ trợ sạc nhanh - iPhone 14 128GB\" /></a></p>\r\n\r\n<p>Sau những g&igrave; m&agrave; m&igrave;nh trải nghiệm được tr&ecirc;n iPhone 14 th&igrave; đ&acirc;y quả thực l&agrave; một sự lựa chọn rất đ&aacute;ng để đầu tư, m&aacute;y c&oacute; gần như những loại c&ocirc;ng nghệ h&agrave;ng đầu tr&ecirc;n thị trường, c&oacute; thể đ&aacute;p ứng tốt cho m&igrave;nh mọi t&aacute;c vụ sử dụng, v&agrave; hơn hết l&agrave; điện thoại được hỗ trợ hệ điều h&agrave;nh mới nhất, vậy n&ecirc;n cũng sẽ an t&acirc;m hơn v&igrave; Apple vẫn sẽ tiếp tục hỗ trợ v&agrave; n&acirc;ng cấp cho iPhone 14 nhiều phi&ecirc;n bản hệ điều h&agrave;nh mới trong tương lai.</p>\r\n', 'Chờ xét duyệt', 1, '11-09-2023', 0),
(8, 'VHL#5', 'Iphone 14 Plus 128GB', 21690000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'iphone5.jpg', '<h3>Sau nhiều thế hệ điện thoại của Apple th&igrave; c&aacute;i t&ecirc;n &ldquo;Plus&rdquo; cũng đ&atilde; ch&iacute;nh thức trở lại v&agrave;o năm 2022 v&agrave; xuất hiện tr&ecirc;n chiếc iPhone 14 Plus 128GB, nổi trội với ngoại h&igrave;nh bắt trend c&ugrave;ng m&agrave;n h&igrave;nh k&iacute;ch thước lớn để đem đến kh&ocirc;ng gian hiển thị tốt hơn c&ugrave;ng cấu h&igrave;nh mạnh mẽ kh&ocirc;ng đổi so với bản ti&ecirc;u chuẩn.</h3>\r\n\r\n<h3>Th&acirc;n h&igrave;nh thanh mảnh c&ugrave;ng ngoại h&igrave;nh g&oacute;c cạnh</h3>\r\n\r\n<p>Giống như những thế hệ &ldquo;Plus&rdquo; trước đ&acirc;y, iPhone 14 Plus vẫn sẽ l&agrave; phi&ecirc;n bản ph&oacute;ng to từ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone-14-series\" target=\"_blank\" title=\"Tham khảo iPhone 14 tại Thế Giới Di Động\">iPhone 14</a>&nbsp;với ng&ocirc;n ngữ thiết kế kh&ocirc;ng đổi, vẫn sẽ l&agrave; cạnh viền vu&ocirc;ng vức đi k&egrave;m với mặt lưng phẳng để tạo n&ecirc;n c&aacute;i nh&igrave;n bắt trend v&amp;agra</p>\r\n', 'Đã đăng', 1, '11-09-2023', 2);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(9, 'VHL#6', 'Iphone 13 128GB', 16490000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'iphone6.jpg', '<h3>Trong khi sức h&uacute;t đến từ bộ 4 phi&ecirc;n bản&nbsp;iPhone 12&nbsp;vẫn chưa nguội đi, th&igrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại iPhone đang kinh doanh tại Thế Giới Di Động \">h&atilde;ng điện thoại&nbsp;Apple</a>&nbsp;đ&atilde; mang đến cho người d&ugrave;ng một si&ecirc;u phẩm mới iPhone 13 với&nbsp;nhiều cải tiến th&uacute; vị sẽ mang lại những trải nghiệm hấp dẫn nhất cho người d&ugrave;ng.</h3>\r\n\r\n<h3>Hiệu năng vượt trội nhờ chip Apple A15 Bionic</h3>\r\n\r\n<p>Con chip&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-chip-apple-a15-bionic-suc-manh-cuc-khung-duoc-he-1339072\" target=\"_blank\" title=\"Tìm hiểu về con chip Apple A15 Bionic\">Apple A15 Bionic</a>&nbsp;si&ecirc;u mạnh được sản xuất tr&ecirc;n quy tr&igrave;nh 5 nm gi&uacute;p&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-13\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 13 chính hãng tại thegioididong.com\">iPhone 13</a>&nbsp;đạt hiệu năng ấn tượng, với CPU nhanh hơn 50%,&nbsp;GPU nhanh hơn 30% so với c&aacute;c đối thủ trong c&ugrave;ng ph&acirc;n kh&uacute;c.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-1-1.jpg\" onclick=\"return false;\"><img alt=\"Chip Apple A15 Bionic mạnh mẽ - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-1-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-1-1.jpg\" title=\"Chip Apple A15 Bionic mạnh mẽ - iPhone 13 128GB\" /></a></p>\r\n\r\n<p>Nhờ hiệu năng được cải tiến, người d&ugrave;ng c&oacute; được những trải nghiệm tốt hơn tr&ecirc;n điện thoại khi d&ugrave;ng c&aacute;c ứng dụng chỉnh sửa ảnh hay chiến c&aacute;c tựa game đồ họa cao mượt m&agrave;.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-2.jpg\" onclick=\"return false;\"><img alt=\"Đồ họa mượt mà - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-2.jpg\" title=\"Đồ họa mượt mà - iPhone 13 128GB\" /></a></p>\r\n\r\n<p>iPhone 13 trang bị&nbsp;bộ nhớ trong 128 GB&nbsp;dung lượng l&yacute; tưởng&nbsp;cho ph&eacute;p bạn thỏa th&iacute;ch lưu trữ mọi nội dung theo &yacute; muốn m&agrave; kh&ocirc;ng lo nhanh đầy bộ nhớ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-19.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng bộ nhớ - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-19.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-19.jpg\" title=\"Dung lượng bộ nhớ - iPhone 13 128GB\" /></a></p>\r\n\r\n<h3>Tốc độ 5G tốt hơn&nbsp;</h3>\r\n\r\n<p>Mạng 5G được cải thiện chất lượng với nhiều băng tần hơn, với 5G gi&uacute;p điện thoại xem trực tuyến hay tải xuống c&aacute;c ứng dụng v&agrave; t&agrave;i liệu đều đạt tốc độ nhanh ch&oacute;ng. Kh&ocirc;ng chỉ vậy, si&ecirc;u phẩm mới n&agrave;y c&ograve;n c&oacute; chế độ dữ liệu th&ocirc;ng minh, tự động ph&aacute;t hiện v&agrave; giảm tải tốc độ mạng để tiết kiệm năng lượng khi kh&ocirc;ng cần d&ugrave;ng tốc độ cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-4.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ kết nối 5G hiện đại - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-4.jpg\" title=\"Hỗ trợ kết nối 5G hiện đại - iPhone 13 128GB\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh Super Retina XDR độ s&aacute;ng cao, tiết kiệm pin</h3>\r\n\r\n<p>iPhone 13 sử dụng tấm nền OLED với k&iacute;ch thước m&agrave;n h&igrave;nh 6.1 inch&nbsp;cho chất lượng m&agrave;u sắc v&agrave; chi tiết h&igrave;nh ảnh sắc n&eacute;t, sống động, độ ph&acirc;n giải đạt 1170 x 2532 Pixels.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-6.jpg\" onclick=\"return false;\"><img alt=\"Màn hình OLED 6.1 inch - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-6.jpg\" title=\"Màn hình OLED 6.1 inch - iPhone 13 128GB\" /></a></p>\r\n\r\n<p>Nhờ c&oacute; c&ocirc;ng nghệ Super Retina XDR gi&uacute;p cho m&aacute;y đạt&nbsp;độ s&aacute;ng 800 nits, tối đa l&ecirc;n đến 1200 nits c&ugrave;ng dải m&agrave;u rộng P3, tỷ lệ tương phản lớn gi&uacute;p ổn định tốt m&agrave;u sắc v&agrave; chất lượng h&igrave;nh ảnh hiển thị trong nhiều điều kiện s&aacute;ng kh&aacute;c nhau, kể cả m&ocirc;i trường nắng gắt hay &aacute;nh s&aacute;ng ch&oacute;i.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-5.jpg\" onclick=\"return false;\"><img alt=\"Độ sáng đạt 1200 nits - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-5.jpg\" title=\"Độ sáng đạt 1200 nits - iPhone 13 128GB\" /></a></p>\r\n\r\n<p>Ph&iacute;a ngo&agrave;i m&agrave;n h&igrave;nh c&ograve;n được phủ lớp oleophobic hạn chế t&igrave;nh trạng b&aacute;m bụi bẩn v&agrave; mồ h&ocirc;i do tay người d&ugrave;ng, giữ m&agrave;n h&igrave;nh lu&ocirc;n sạch v&agrave; tinh tế hơn. V&agrave; trang bị&nbsp;k&iacute;nh cường lực Ceramic Shield gi&uacute;p m&agrave;n h&igrave;nh của m&aacute;y được an to&agrave;n hơn trước những vết xước, va đập trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-7.jpg\" onclick=\"return false;\"><img alt=\"Trang bị kính cường lực Ceramic Shield - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-7.jpg\" title=\"Trang bị kính cường lực Ceramic Shield - iPhone 13 128GB\" /></a></p>\r\n\r\n<h3>Cụm camera k&eacute;p nổi bật với nhiều cải tiến</h3>\r\n\r\n<p>Cụm camera k&eacute;p ph&iacute;a sau tr&ecirc;n iPhone 13 đều sở hữu độ ph&acirc;n giải 12 MP, camera ch&iacute;nh&nbsp;gi&uacute;p thu được nhiều &aacute;nh s&aacute;ng hơn, tăng khả năng thu s&aacute;ng l&ecirc;n đến 47% n&ecirc;n chất lượng ảnh chụp cũng cải thiện hơn so với bản tiền nhiệm.&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại có camera góc siêu rộng đang kinh doanh tại Thế Giới Di Động \">Điện thoại c&oacute;&nbsp;camera g&oacute;c si&ecirc;u rộng</a>&nbsp;cho g&oacute;c nh&igrave;n&nbsp;120 độ gi&uacute;p thu được nhiều chi tiết hơn, dễ d&agrave;ng ghi lại những khung cảnh n&uacute;i non h&ugrave;ng vĩ, ảnh chụp nh&oacute;m đ&ocirc;ng người.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-8.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera sau sắc nét - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-8.jpg\" title=\"Cụm camera sau sắc nét - iPhone 13 128GB\" /></a></p>\r\n\r\n<p>Cụm camera cũng được trang bị t&iacute;nh năng chống rung quang học Sensor Shift gi&uacute;p lấy n&eacute;t nhanh ch&oacute;ng, khả năng chụp đ&ecirc;m cũng trở n&ecirc;n r&otilde; n&eacute;t m&agrave; &iacute;t bị nhiễu hạt, hay quay video cũng rất ổn định v&agrave; mượt m&agrave; hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-9.jpg\" onclick=\"return false;\"><img alt=\"Chụp đêm siêu rõ nét - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-9.jpg\" title=\"Chụp đêm siêu rõ nét - iPhone 13 128GB\" /></a></p>\r\n\r\n<p>&ldquo;Chế độ điện ảnh&rdquo; cho ph&eacute;p camera tr&ecirc;n iPhone 13 tự động l&agrave;m mờ hậu cảnh trong video để đối tượng lu&ocirc;n được lấy n&eacute;t nổi bật. Đặc biệt người d&ugrave;ng c&oacute; thể thay đổi ti&ecirc;u điểm để chọn chủ thể mong muốn trong khung h&igrave;nh khi sử dụng quay video, nhờ đ&oacute; tạo n&ecirc;n những video chuy&ecirc;n nghiệp, như &yacute; hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-10.jpg\" onclick=\"return false;\"><img alt=\"Chế độ điện ảnh độc đáo - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-10.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-10.jpg\" title=\"Chế độ điện ảnh độc đáo - iPhone 13 128GB\" /></a></p>\r\n\r\n<p>Chế độ chụp Smart HDR 4 nhận diện tối đa 4 người trong một khung h&igrave;nh, tiến h&agrave;nh tối ưu h&oacute;a &aacute;nh s&aacute;ng v&agrave; tương phản, tone m&agrave;u da cho từng người để ho&agrave;n thiện bức ảnh đẹp m&agrave; kh&ocirc;ng cần qua chỉnh sửa.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-11.jpg\" onclick=\"return false;\"><img alt=\"Chế độ Smart HDR 4 - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-11.jpg\" title=\"Chế độ Smart HDR 4 - iPhone 13 128GB\" /></a></p>\r\n\r\n<p>Camera trước vẫn nằm trong tai thỏ, độ ph&acirc;n giải cũng đạt 12 MP với c&aacute;c c&ocirc;ng nghệ chụp ảnh chuy&ecirc;n nghiệp như hiệu ứng bokeh, chế độ điện ảnh, Animoji v&agrave; Memoji để bạn tự tin thể hiện m&igrave;nh trước ống k&iacute;nh, kh&ocirc;ng lo lắng che giấu khuyết điểm.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-12.jpg\" onclick=\"return false;\"><img alt=\"Camera trước 12 MP - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-12.jpg\" title=\"Camera trước 12 MP - iPhone 13 128GB\" /></a></p>\r\n\r\n<h3>Sang trọng, đẳng cấp hơn về ngoại h&igrave;nh</h3>\r\n\r\n<p>iPhone 13&nbsp;c&oacute; thiết kế nguy&ecirc;n khối vu&ocirc;ng vắn như tiền nhiệm, khung viền được l&agrave;m từ nh&ocirc;m c&ugrave;ng mặt lưng k&iacute;nh sang trọng, cao cấp.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-13.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế nguyên khối sang trọng, đẳng cấp - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-13.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-13.jpg\" title=\"Thiết kế nguyên khối sang trọng, đẳng cấp - iPhone 13 128GB\" /></a></p>\r\n\r\n<p>Ngoại h&igrave;nh kh&ocirc;ng qu&aacute; kh&aacute;c biệt so với tiền nhiệm, nhưng tỷ lệ m&agrave;n h&igrave;nh so với khung m&aacute;y tốt hơn, phần tai thỏ nhỏ hơn 20% gia tăng diện t&iacute;ch trải nghiệm khi lướt web, xem phim, chơi game.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-14.jpg\" onclick=\"return false;\"><img alt=\"Màn hình cải tiến - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-14.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-14.jpg\" title=\"Màn hình cải tiến - iPhone 13 128GB\" /></a></p>\r\n\r\n<p>Điểm kh&aacute;c biệt lớn nhất trong ngoại h&igrave;nh c&oacute; lẽ ch&iacute;nh l&agrave; c&aacute;c cảm biến camera được đặt ch&eacute;o thay v&igrave; xếp thẳng h&agrave;ng như&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-12-128gb\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 12 đang kinh doanh tại Thế Giới Di Động  \">iPhone 12</a>, tạo điểm nhận diện đặc trưng, mới mẻ v&agrave; đẹp mắt, sang chảnh hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-15.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera sau với diện mạo mới - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-15.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-15.jpg\" title=\"Cụm camera sau với diện mạo mới - iPhone 13 128GB\" /></a></p>\r\n\r\n<p>M&agrave;u sắc tr&ecirc;n mẫu iPhone mới n&agrave;y cũng đa dạng hơn gồm Trắng, Đen, Đỏ, Xanh dương v&agrave; Hồng mới mẻ dễ t&ugrave;y chọn cho mọi người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-16.jpg\" onclick=\"return false;\"><img alt=\"Có nhiều màu sắc để lựa chọn - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-16.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-16.jpg\" title=\"Có nhiều màu sắc để lựa chọn - iPhone 13 128GB\" /></a></p>\r\n\r\n<p>iPhone 13 hỗ trợ chuẩn kh&aacute;ng nước v&agrave; bụi IP68, bạn sẽ y&ecirc;n t&acirc;m sử dụng hơn khi đem theo thiết bị để chụp ảnh khi bơi hay những lần v&ocirc; t&igrave;nh l&agrave;m đổ nước, c&oacute; cơn mưa đột ngột.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-17.jpg\" onclick=\"return false;\"><img alt=\"Khả năng kháng nước, bụi - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-17.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-17.jpg\" title=\"Khả năng kháng nước, bụi - iPhone 13 128GB\" /></a></p>\r\n\r\n<h3>Thời lượng pin cũng được cải thiện</h3>\r\n\r\n<p>Theo c&ocirc;ng bố của h&atilde;ng, iPhone 13 c&oacute; thời lượng pin tăng th&ecirc;m 2.5 tiếng so với thế hệ trước, cho thời gian sử dụng l&acirc;u hơn, phục vụ hiệu quả với người d&ugrave;ng c&oacute; nhu cầu sử dụng tần suất cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-18.jpg\" onclick=\"return false;\"><img alt=\"Chip A15 tối ưu hóa năng lượng - iPhone 13 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-18.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/223602/iphone-13-18.jpg\" title=\"Chip A15 tối ưu hóa năng lượng - iPhone 13 128GB\" /></a></p>\r\n\r\n<p>Hỗ trợ&nbsp;sạc nhanh&nbsp;20 W gi&uacute;p nạp đầy 50% pin chỉ trong khoảng 30 ph&uacute;t. Đi k&egrave;m theo c&aacute;c c&ocirc;ng nghệ sạc kh&ocirc;ng d&acirc;y&nbsp;ti&ecirc;n tiến như chuẩn Qi hay MagSafe.</p>\r\n\r\n<p>Lưu &yacute;: Củ sạc nhanh 20 W sẽ kh&ocirc;ng đi k&egrave;m trong hộp khi mua.</p>\r\n\r\n<p>T&oacute;m lại, với những n&acirc;ng cấp v&agrave; cải tiến mạnh mẽ về hiệu năng, camera đa chức năng, m&igrave;nh nghĩ rằng iPhone 13 128GB sẽ l&agrave; một chiếc điện thoại cao cấp rất đ&aacute;ng được đầu tư v&agrave; sử dụng.</p>\r\n', 'Đã đăng', 1, '11-09-2023', 1),
(10, 'VHL#7', 'Iphone 12 64GB', 13790000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'iphone7.jpg', '<h3>Trong những th&aacute;ng cuối năm 2020,&nbsp;<a href=\"https://www.thegioididong.com/apple\" target=\"_blank\" title=\"Tham khảo sản phẩm chính hãng của Apple tại Thế Giới Di Động\">Apple</a>&nbsp;đ&atilde; ch&iacute;nh thức giới thiệu đến người d&ugrave;ng cũng như iFan thế hệ iPhone&nbsp;12&nbsp;series&nbsp;mới với h&agrave;ng loạt t&iacute;nh năng bứt ph&aacute;, thiết kế được lột x&aacute;c ho&agrave;n to&agrave;n, hiệu năng đầy mạnh mẽ v&agrave; một trong số đ&oacute; ch&iacute;nh l&agrave;&nbsp;<a href=\"https://www.topzone.vn/iphone/iphone-12\" target=\"_blank\" title=\"Tham khảo thông tin sản phẩm tại TopZone.vn\">iPhone 12 64GB</a>.</h3>\r\n\r\n<h3>Hiệu năng vượt xa mọi giới hạn</h3>\r\n\r\n<p>Apple đ&atilde; trang bị con chip mới nhất của h&atilde;ng (t&iacute;nh đến 11/2020) cho iPhone 12 đ&oacute; l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-chip-apple-a14-bionic-tren-iphone-12-va-ipad-1290695\" target=\"_blank\" title=\"Tìm hiểu chip A14 Bionic là gì?\">A14 Bionic</a>, được sản xuất tr&ecirc;n tiến tr&igrave;nh 5 nm với hiệu suất ổn định hơn so với chip A13 được trang bị tr&ecirc;n phi&ecirc;n bản tiền nhiệm&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-11\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone 11 chính hãng tại Thế Giới Di Động\">iPhone 11</a>.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-144220-044259.jpg\" onclick=\"return false;\"><img alt=\"Chip A14 Bionic mạnh mẽ | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-144220-044259.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-144220-044259.jpg\" title=\"Chip A14 Bionic mạnh mẽ | iPhone 12\" /></a></p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-chip-apple-a14-bionic-tren-iphone-12-va-ipad-1290695\" target=\"_blank\" title=\"Tìm hiểu về chip Apple A14 Bionic trên iPhone 12 và iPad Air 2020\">T&igrave;m hiểu về chip Apple A14 Bionic tr&ecirc;n iPhone 12 v&agrave; iPad Air 2020</a></p>\r\n\r\n<p>Với CPU Apple A14 Bionic, bạn c&oacute; thể dễ d&agrave;ng trải nghiệm mọi tựa game với những pha chuyển cảnh mượt m&agrave; hay h&agrave;ng loạt hiệu ứng đồ họa tuyệt đẹp ở mức đồ họa cao m&agrave; kh&ocirc;ng lo t&igrave;nh trạng giật lag.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-18.jpg\" onclick=\"return false;\"><img alt=\"Chiến game siêu mượt, đồ họa tuyệt đẹp | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-18.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-18.jpg\" title=\"Chiến game siêu mượt, đồ họa tuyệt đẹp | iPhone 12\" /></a></p>\r\n\r\n<p>Chưa hết, Apple c&ograve;n g&acirc;y bất ngờ đến người d&ugrave;ng với hệ thống 5G lần đầu ti&ecirc;n được trang bị tr&ecirc;n những chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo iPhone kinh doanh tại thegioididong.com\">iPhone</a>, cho tốc độ truyền tải dữ liệu nhanh hơn, ổn định hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-20.jpg\" onclick=\"return false;\"><img alt=\"Kết nối 5G truyền tải dữ liệu nhanh chóng | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-20.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-20.jpg\" title=\"Kết nối 5G truyền tải dữ liệu nhanh chóng | iPhone 12\" /></a></p>\r\n\r\n<p>iPhone 12 sẽ chạy tr&ecirc;n hệ điều h&agrave;nh iOS 15 (12/2021)&nbsp;với nhiều t&iacute;nh năng hấp dẫn như hỗ trợ Widget cũng như những n&acirc;ng cấp tối ưu phần mềm đ&aacute;ng kể mang lại những trải nghiệm th&uacute; vị mới lạ đến người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-13.jpg\" onclick=\"return false;\"><img alt=\"Khám phá những tính năng mới hấp dẫn trên iOS 14 | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-13.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-13.jpg\" title=\"Khám phá những tính năng mới hấp dẫn trên iOS 14 | iPhone 12\" /></a></p>\r\n\r\n<h3>Cụm camera kh&ocirc;ng ngừng cải tiến</h3>\r\n\r\n<p>iPhone 12 được trang bị hệ thống camera k&eacute;p bao gồm&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\" title=\"Tham khảo điện thoại có camera góc rộng tại Thế Giới Di Động\">camera g&oacute;c rộng</a>&nbsp;v&agrave; camera si&ecirc;u rộng c&oacute; c&ugrave;ng độ ph&acirc;n giải l&agrave; 12 MP, chế độ ban đ&ecirc;m (<a href=\"https://www.thegioididong.com/hoi-dap/che-do-chup-dem-night-mode-la-gi-907873\" target=\"_blank\" title=\"Tìm hiểu chế độ chụp đêm Night Mode là gì?\">Night Mode</a>) tr&ecirc;n bộ đ&ocirc;i camera n&agrave;y cũng đ&atilde; được n&acirc;ng cấp về phần cứng lẫn thuật to&aacute;n xử l&yacute;, khi chụp những bức ảnh thiếu s&aacute;ng bạn sẽ nhận được kết quả ấn tượng với m&agrave;u sắc, độ chi tiết r&otilde; n&eacute;t đ&aacute;ng kinh ngạc.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030344.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera sau| iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030344.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030344.jpg\" title=\"Cụm camera sau| iPhone 12\" /></a></p>\r\n\r\n<p>Bạn c&oacute; thể kh&aacute;m ph&aacute; th&ecirc;m những t&iacute;nh năng của camera tr&ecirc;n iPhone 12 như chế độ smart HDR 3 gi&uacute;p c&acirc;n bằng yếu tố &aacute;nh s&aacute;ng trong ảnh, l&agrave;m nổi bật chi tiết đối tượng v&agrave; c&acirc;y cối trong khi vẫn giữ được m&agrave;u sắc phong ph&uacute; của bầu trời ngay cả v&agrave;o buổi trưa nắng gắt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-9.jpg\" onclick=\"return false;\"><img alt=\"Chụp ảnh với tính năng smart HDR 3 sắc nét, chất lượng | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-9.jpg\" title=\"Chụp ảnh với tính năng smart HDR 3 sắc nét, chất lượng | iPhone 12\" /></a></p>\r\n\r\n<p>Chế độ chụp ch&acirc;n dung đ&atilde; tốt nay c&ograve;n tốt hơn trong việc l&agrave;m mờ hậu cảnh một c&aacute;ch nghệ thuật để dồn hết sự tập trung v&agrave;o đối tượng m&agrave; bạn muốn chụp.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-10.jpg\" onclick=\"return false;\"><img alt=\"Nổi bật chủ thể với tính năng chụp chân dung độc đáo | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-10.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-10.jpg\" title=\"Nổi bật chủ thể với tính năng chụp chân dung độc đáo | iPhone 12\" /></a></p>\r\n\r\n<p>Bạn sẽ dễ d&agrave;ng quay video 4K HDR với chuẩn điện ảnh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/dolby-vision-la-gi-cac-ung-dung-noi-bat-va-nhung-loai-1226284\" target=\"_blank\" title=\"Tìm hiểu Dolby Vision là gì?\">Dolby Vision</a>&nbsp;v&agrave; chỉnh sửa ngay tr&ecirc;n chiếc điện thoại của m&igrave;nh, bạn c&oacute; thể đưa video l&ecirc;n TV của bạn để thưởng thức thước phim sắc n&eacute;t chất lượng cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-11.jpg\" onclick=\"return false;\"><img alt=\"Quay video chất lượng ngay cả trong điều kiện ánh sáng yếu | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-11.jpg\" title=\"Quay video chất lượng ngay cả trong điều kiện ánh sáng yếu | iPhone 12\" /></a></p>\r\n\r\n<p>Sự kết hợp của 2 cảm biến chất lượng n&agrave;y đ&atilde; tạo n&ecirc;n một hệ thống camera chuy&ecirc;n nghiệp kh&ocirc;ng kh&aacute;c g&igrave; những chiếc m&aacute;y ảnh thực thụ, dễ d&agrave;ng đem lại những bức h&igrave;nh sắc n&eacute;t tuyệt đối, độ chi tiết cao v&agrave; đa dạng chế độ chụp cho người d&ugrave;ng linh hoạt sử dụng.</p>\r\n\r\n<h3>Ghi dấu ấn về mặt thiết kế</h3>\r\n\r\n<p>Về ngoại h&igrave;nh iPhone 12 c&oacute; thiết kế ho&agrave;i niệm với phần cạnh được l&agrave;m vu&ocirc;ng vức tương tự tr&ecirc;n mẫu iPhone 4 thay v&igrave; bo cong như iPhone 11.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030352.jpg\" onclick=\"return false;\"><img alt=\" iPhone 12 có thiết kế hoài niệm tương tự iPhone 4 series\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030352.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030352.jpg\" title=\" iPhone 12 có thiết kế hoài niệm tương tự iPhone 4 series\" /></a></p>\r\n\r\n<p>Ở mặt trước iPhone 12 phần tai thỏ được l&agrave;m nhỏ gọn hơn v&agrave; cũng l&agrave; nơi chứa cảm biến Face ID c&oacute; thể nhận diện khu&ocirc;n mặt một c&aacute;ch nhanh ch&oacute;ng v&agrave; ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p>Apple c&ograve;n mang đến cho người một loạt gam m&agrave;u c&aacute; t&iacute;nh, độc đ&aacute;o tr&ecirc;n những chiếc iPhone của m&igrave;nh để người d&ugrave;ng c&oacute; sự lựa chọn ph&ugrave; hợp với những phong c&aacute;ch kh&aacute;c nhau.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-17.jpg\" onclick=\"return false;\"><img alt=\"Sở hữu loạt gam màu cá tính | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-17.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-17.jpg\" title=\"Sở hữu loạt gam màu cá tính | iPhone 12\" /></a></p>\r\n\r\n<p>iPhone 12 được trang bị m&agrave;n h&igrave;nh OLED tr&agrave;n viền c&oacute; k&iacute;ch thước 6.1 inch, cho bạn kh&ocirc;ng gian trải nghiệm lớn cũng như những gi&acirc;y ph&uacute;t giải tr&iacute; hấp dẫn tr&ecirc;n một m&agrave;n ảnh v&ocirc; c&ugrave;ng chất lượng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040421-030410.jpg\" onclick=\"return false;\"><img alt=\"Màn hình Super Retina XDR OLED 6.1 inch | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040421-030410.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040421-030410.jpg\" title=\"Màn hình Super Retina XDR OLED 6.1 inch | iPhone 12\" /></a></p>\r\n\r\n<p>M&aacute;y được chế t&aacute;c c&oacute; độ ho&agrave;n thiện cực cao với thiết kế nguy&ecirc;n khối, khung nh&ocirc;m v&agrave; mặt sau l&agrave; k&iacute;nh cường lực cao cấp to&aacute;t l&ecirc;n vẻ ngo&agrave;i sang chảnh cũng như mang lại độ hiệu quả an to&agrave;n cao mỗi khi sử dụng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-041521-031513.jpg\" onclick=\"return false;\"><img alt=\"Độ hoàn thiện cao, chắc chắn cho thiết bị | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-041521-031513.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-041521-031513.jpg\" title=\"Độ hoàn thiện cao, chắc chắn cho thiết bị | iPhone 12\" /></a></p>\r\n\r\n<p>Mặt trước của iPhone 12 được phủ ho&agrave;n to&agrave;n bởi lớp k&iacute;nh cường lực Ceramic Shield cứng c&aacute;p, được đ&aacute;nh gi&aacute; l&agrave; c&oacute; độ bền cao v&agrave; cứng c&aacute;p hơn hầu hết c&aacute;c loại mặt k&iacute;nh c&oacute; tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại chính hãng tại Thế Giới Di Động\">điện thoại th&ocirc;ng minh</a>&nbsp;kh&aacute;c c&oacute; mặt tr&ecirc;n thị trường.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030328.jpg\" onclick=\"return false;\"><img alt=\"Lớp kính cường lực Ceramic Shield | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030328.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030328.jpg\" title=\"Lớp kính cường lực Ceramic Shield | iPhone 12\" /></a></p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/mat-kinh-ceramic-shield-tren-iphone-12-la-gi-co-xin-so-nhu-1298900\" target=\"_blank\" title=\"Mặt kính Ceramic Shield trên iPhone 12 là gì? Có xịn sò như quảng cáo?\">Mặt k&iacute;nh Ceramic Shield tr&ecirc;n iPhone 12 l&agrave; g&igrave;? C&oacute; xịn s&ograve; như quảng c&aacute;o?</a></p>\r\n\r\n<p>V&agrave; để cho thiết bị trở n&ecirc;n ho&agrave;n hảo hơn n&ecirc;n kh&ocirc;ng thể thiếu khả năng kh&aacute;ng nước, bụi chuẩn IP68 gi&uacute;p người d&ugrave;ng y&ecirc;n t&acirc;m sử dụng với những buổi đi chơi biển m&agrave; kh&ocirc;ng hề lo chiếc m&aacute;y bị v&ocirc; nước.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-2.jpg\" onclick=\"return false;\"><img alt=\"Kháng nước bụi chuẩn IP68 | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-2.jpg\" title=\"Kháng nước bụi chuẩn IP68 | iPhone 12\" /></a></p>\r\n\r\n<h3>Trải nghiệm xuy&ecirc;n suốt, liền mạch cả ng&agrave;y d&agrave;i</h3>\r\n\r\n<p>Để bạn c&oacute; những gi&acirc;y ph&uacute;t trải nghiệm liền mạch, Apple đ&atilde; trang bị một vi&ecirc;n pin c&oacute; dung lượng 2815 mAh, t&iacute;ch hợp cho khả năng tiết kiệm pin gi&uacute;p người d&ugrave;ng c&oacute; thể giải tr&iacute; đa phương tiện l&ecirc;n đến 17 giờ v&agrave; nghe nhạc li&ecirc;n tục l&ecirc;n đến 65 giờ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030358.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng pin sử dụng đáp ứng đủ một ngày | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030358.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-040321-030358.jpg\" title=\"Dung lượng pin sử dụng đáp ứng đủ một ngày | iPhone 12\" /></a></p>\r\n\r\n<p>V&agrave; kh&ocirc;ng thể thiếu đ&oacute; ch&iacute;nh l&agrave; t&iacute;nh năng sạc nhanh, iPhone 12 c&oacute; khả năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo điện thoại có sạc pin nhanh tại Thế Giới Di Động\">sạc pin nhanh</a>&nbsp;qua c&aacute;p c&ocirc;ng suất 20 W, chỉ trong v&ograve;ng 30 ph&uacute;t th&igrave; chiếc m&aacute;y đ&atilde; c&oacute; thế sạc được 50% pin. Th&ecirc;m v&agrave;o đ&oacute; l&agrave; khả năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-khong-day\" target=\"_blank\" title=\"Tham khảo điện thoại có sạc không dây tại Thế Giới Di Động\">sạc kh&ocirc;ng d&acirc;y</a>&nbsp;MagSafe v&ocirc; c&ugrave;ng tiện dụng.</p>\r\n\r\n<p>Lưu &yacute;: Củ sạc kh&ocirc;ng k&egrave;m theo m&aacute;y m&agrave; phải mua ri&ecirc;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-16.jpg\" onclick=\"return false;\"><img alt=\"Sạc nhanh và sạc không dây tiện lợi hữu ích | iPhone 12\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-16.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/213031/iphone-12-16.jpg\" title=\"Sạc nhanh và sạc không dây tiện lợi hữu ích | iPhone 12\" /></a></p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-magsafe-magsafe-2-la-gi-co-tren-thiet-bi-nao-cua-1241888\" target=\"_blank\" title=\"MagSafe trên iPhone 12 là gì? Dùng để làm gì trên các thiết bị Apple?\">MagSafe tr&ecirc;n iPhone 12 l&agrave; g&igrave;? D&ugrave;ng để l&agrave;m g&igrave; tr&ecirc;n c&aacute;c thiết bị Apple?</a></p>\r\n\r\n<p>Sự lột x&aacute;c đầy mạnh mẽ lần n&agrave;y của Apple kh&ocirc;ng chỉ g&acirc;y bất ngờ đến người d&ugrave;ng m&agrave; c&ograve;n đ&aacute;nh dấu một kỷ nguy&ecirc;n mới trong nền ph&aacute;t triển smartphone Apple. V&agrave; đ&acirc;y cũng được xem l&agrave; một trong những bộ series iPhone m&agrave; Apple đặt nhiều t&acirc;m huyết, mục đ&iacute;ch v&agrave; đầy t&iacute;nh năng mạnh mẽ chưa từng thấy.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 4);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(11, 'VHL#8', 'Samsung Galaxy Z Fold5 5G 256GB', 36990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung1.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-fold5\" target=\"_blank\" title=\"Tham khảo Samsung Galaxy Z Fold5 tại Thế Giới Di Động\">Samsung Galaxy Z Fold5</a>&nbsp;l&agrave; mẫu điện thoại cao cấp được ra mắt v&agrave;o th&aacute;ng 07/2023 với nhiều điểm đ&aacute;ng ch&uacute; &yacute; như thiết kế gập độc đ&aacute;o, hiệu năng mạnh mẽ c&ugrave;ng camera quay chụp tốt, điều n&agrave;y gi&uacute;p cho m&aacute;y thu h&uacute;t được nhiều sự quan t&acirc;m của đ&ocirc;ng đảo người d&ugrave;ng y&ecirc;u c&ocirc;ng nghệ hiện nay.</h3>\r\n\r\n<p><strong>ĐẶC ĐIỂM NỔI BẬT</strong><br />\r\n● Nổi bật với thiết kế cơ chế gập độc đ&aacute;o, chắc chắn v&agrave; bền bỉ<br />\r\n● Cấu h&igrave;nh khủng với chip Snapdragon 8 Gen 2 for Galaxy<br />\r\n● Đa nhiệm linh hoạt, tối ưu hiệu suất l&agrave;m việc<br />\r\n● Ho&agrave;n thiện với c&aacute;c chất liệu cao cấp</p>\r\n\r\n<h3>Bảng so s&aacute;nh th&ocirc;ng số&nbsp;phi&ecirc;n bản Galaxy Z Fold5 v&agrave; Galaxy Z Fold4</h3>\r\n\r\n<p>Dưới đ&acirc;y sẽ l&agrave; bảng tổng hợp th&ocirc;ng số kỹ thuật giữa hai thế hệ điện thoại&nbsp;Galaxy Z Fold:</p>\r\n\r\n<table border=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><font color=\"#ffcc00\"><b>Thuộc t&iacute;nh</b></font></p>\r\n			</td>\r\n			<td>\r\n			<p><font color=\"#ffcc00\"><b>Samsung Galaxy Z Fold5</b></font></p>\r\n			</td>\r\n			<td>\r\n			<p><b>Samsung Galaxy Z Fold4</b></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>M&agrave;n h&igrave;nh ngo&agrave;i</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>Dynamic AMOLED 2X 6.2 inch</p>\r\n\r\n			<p>Độ ph&acirc;n giải HD+</p>\r\n			</td>\r\n			<td>\r\n			<p>Dynamic AMOLED 2X 6.2 inch</p>\r\n\r\n			<p>Độ ph&acirc;n giải HD+</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>M&agrave;n h&igrave;nh trong</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>Dynamic AMOLED 2X 7.6 inch</p>\r\n\r\n			<p>Độ ph&acirc;n giải QXGA+ (2176 x 1812 Pixels)</p>\r\n			</td>\r\n			<td>\r\n			<p>Dynamic AMOLED 2X 7.6 inch</p>\r\n\r\n			<p>Độ ph&acirc;n giải QXGA+ (2176 x 1812 Pixels)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Chip xử l&yacute;</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>Snapdragon 8 Gen 2<br />\r\n			(For Galaxy)</p>\r\n			</td>\r\n			<td>\r\n			<p>Snapdragon 8+ Gen 1</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Camera selfie</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>10 MP &amp; 4 MP</p>\r\n			</td>\r\n			<td>\r\n			<p>10 MP &amp; 4 MP</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Camera ch&iacute;nh</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>50 MP</p>\r\n			</td>\r\n			<td>\r\n			<p>50 MP</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><b>RAM</b></p>\r\n			</td>\r\n			<td>\r\n			<p>12 GB</p>\r\n			</td>\r\n			<td>\r\n			<p>12 GB</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><b>Bộ nhớ trong</b></p>\r\n			</td>\r\n			<td>\r\n			<p>256 GB</p>\r\n			</td>\r\n			<td>\r\n			<p>256 GB</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><b>Dung lượng pin</b></p>\r\n			</td>\r\n			<td>4400 mAh</td>\r\n			<td>4400 mAh</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>C&ocirc;ng suất sạc</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>25 W</p>\r\n			</td>\r\n			<td>\r\n			<p>25 W</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h3>Thiết kế gập mở linh hoạt</h3>\r\n\r\n<p>Samsung Galaxy Z Fold5 vẫn tiếp tục giữ nguy&ecirc;n thiết kế gập độc đ&aacute;o dạng ngang của d&ograve;ng Galaxy Z Fold trước đ&oacute;. Với k&iacute;ch thước nhỏ gọn khi gập lại,&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;n&agrave;y trở n&ecirc;n dễ d&agrave;ng mang theo v&agrave; cất giữ trong t&uacute;i &aacute;o hay t&uacute;i x&aacute;ch của bạn. Khi mở ra, Galaxy Z Fold5 trở th&agrave;nh một chiếc điện thoại th&ocirc;ng thường với m&agrave;n h&igrave;nh lớn hơn, mang đến trải nghiệm sử dụng rộng lớn v&agrave; ấn tượng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095313.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại - Samsung Galaxy Z Fold5\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095313.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095313.jpg\" title=\"Thiết kế điện thoại - Samsung Galaxy Z Fold5\" /></a></p>\r\n\r\n<p>Samsung lu&ocirc;n hướng đến chất lượng v&agrave; đẳng cấp trong việc chọn chất liệu cho sản phẩm của m&igrave;nh. Với Galaxy Z Fold5, khung b&ecirc;n ngo&agrave;i của m&aacute;y được l&agrave;m từ hợp kim nh&ocirc;m Armor Aluminum, mang đến cảm gi&aacute;c cứng c&aacute;p v&agrave; đẳng cấp trong việc cầm nắm.</p>\r\n\r\n<p>Bản lề của Samsung Galaxy Z Fold5 được thiết kế đặc biệt để đảm bảo t&iacute;nh ổn định v&agrave; sự mở đ&oacute;ng mượt m&agrave;. Đ&acirc;y l&agrave; một phần quan trọng trong thiết kế của điện thoại gập, v&igrave; thế Samsung đ&atilde; đầu tư nghi&ecirc;n cứu v&agrave; ph&aacute;t triển để cải thiện, tối ưu bản lề v&agrave; cho ra c&ocirc;ng nghệ r&atilde;nh k&eacute;p, n&oacute; gi&uacute;p gập mở dễ d&agrave;ng v&agrave; tối ưu được độ mờ nếp gấp.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095317.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại - Samsung Galaxy Z Fold5\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095317.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095317.jpg\" title=\"Thiết kế điện thoại - Samsung Galaxy Z Fold5\" /></a></p>\r\n\r\n<p>Với sự ph&aacute;t triển của c&ocirc;ng nghệ linh hoạt v&agrave; tiến bộ trong vật liệu, Samsung đ&atilde; thể hiện sự ch&uacute; trọng đ&aacute;ng kể v&agrave;o việc l&agrave;m cho Galaxy Z Fold5 trở n&ecirc;n cực kỳ mỏng. Với độ mỏng chỉ 13.4 mm khi gập lại v&agrave; 6.1 mm khi mở rộng (&iacute;t hơn 2.4 mm so với đời trước), điện thoại mang lại một cảm gi&aacute;c tinh tế v&agrave; thanh lịch cho người d&ugrave;ng.</p>\r\n\r\n<p>Tuy d&aacute;ng mỏng nhưng Galaxy Z Fold5 kh&ocirc;ng gặp phải vấn đề về độ bền v&agrave; ổn định nhờ v&agrave;o sự t&iacute;ch hợp cẩn thận của bản lề c&ugrave;ng chất liệu chắc chắn gi&uacute;p người d&ugrave;ng c&oacute; thể tự tin v&agrave; y&ecirc;n t&acirc;m khi sử dụng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095321.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại - Samsung Galaxy Z Fold5\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095321.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095321.jpg\" title=\"Thiết kế điện thoại - Samsung Galaxy Z Fold5\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra, Samsung cũng đ&atilde; t&iacute;ch hợp c&ocirc;ng nghệ kh&aacute;ng nước IPX8 ti&ecirc;n tiến v&agrave;o thiết kế của Galaxy Z Fold5. Bằng c&aacute;ch sử dụng c&aacute;c kỹ thuật chống thấm nước ti&ecirc;n tiến v&agrave; độ bền cao, chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-z\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung dòng gập tại Thế Giới Di Động\">điện thoại Samsung d&ograve;ng gập</a>&nbsp;n&agrave;y c&oacute; thể chịu được tiếp x&uacute;c với nước khi đi mưa trong một thời gian ngắn m&agrave; kh&ocirc;ng ảnh hưởng đến hoạt động b&ecirc;n trong.</p>\r\n\r\n<h3>N&acirc;ng cao trải nghiệm với m&agrave;n h&igrave;nh chất lượng</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh của Samsung Galaxy Z Fold5 l&agrave; một trong những yếu tố quan trọng l&agrave;m n&ecirc;n sự th&agrave;nh c&ocirc;ng v&agrave; đẳng cấp của chiếc điện thoại n&agrave;y. Với sự ph&aacute;t triển của c&ocirc;ng nghệ m&agrave;n h&igrave;nh, Galaxy Z Fold5 mang đến những trải nghiệm hiển thị đ&aacute;ng kinh ngạc v&agrave; ti&ecirc;n tiến cho người d&ugrave;ng.</p>\r\n\r\n<p>Samsung Galaxy Z Fold5 sử dụng tấm nền Dynamic AMOLED 2X đa điểm với khả năng hiển thị m&agrave;u sắc v&ocirc; c&ugrave;ng sắc n&eacute;t, ch&acirc;n thực v&agrave; rực rỡ. Tấm nền n&agrave;y cho ph&eacute;p t&aacute;i tạo độ ph&acirc;n giải cao v&agrave; độ tương phản đ&aacute;ng kể, gi&uacute;p h&igrave;nh ảnh, video v&agrave; nội dung tr&ecirc;n m&agrave;n h&igrave;nh trở n&ecirc;n sống động v&agrave; ch&acirc;n thực.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095632.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại - Samsung Galaxy Z Fold5\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095632.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095632.jpg\" title=\"Màn hình điện thoại - Samsung Galaxy Z Fold5\" /></a></p>\r\n\r\n<p>Những m&agrave;u sắc sặc sỡ v&agrave; sắc n&eacute;t l&agrave; điểm đ&aacute;ng khen ngợi của m&agrave;n h&igrave;nh n&agrave;y, gi&uacute;p tạo n&ecirc;n trải nghiệm hấp dẫn cho người d&ugrave;ng khi sử dụng&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại di động tại Thế Giới Di Động\">điện thoại</a>.</p>\r\n\r\n<p>Với k&iacute;ch thước m&agrave;n h&igrave;nh 7.6 inch v&agrave; độ ph&acirc;n giải QXGA+, chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung tại Thế Giới Di Động\">điện thoại Samsung</a>&nbsp;n&agrave;y cung cấp một loạt chi tiết sắc n&eacute;t v&agrave; r&otilde; r&agrave;ng tr&ecirc;n m&agrave;n h&igrave;nh lớn khi mở ra, mang đến trải nghiệm xem nội dung đắm ch&igrave;m v&agrave; hấp dẫn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095638.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại - Samsung Galaxy Z Fold5\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095638.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095638.jpg\" title=\"Màn hình điện thoại - Samsung Galaxy Z Fold5\" /></a></p>\r\n\r\n<p>Samsung Galaxy Z Fold5 c&oacute; tần số qu&eacute;t m&agrave;n h&igrave;nh cao 120 Hz mang đến trải nghiệm mượt m&agrave;, kh&ocirc;ng nhấp nh&aacute;y v&agrave; đ&aacute;p ứng nhanh nhạy đặc biệt khi vuốt v&agrave; chạm v&agrave;o m&agrave;n h&igrave;nh. Tần số qu&eacute;t cao l&agrave;m cho việc chuyển đổi giữa c&aacute;c ứng dụng v&agrave; trang web trở n&ecirc;n trơn tru v&agrave; dễ d&agrave;ng, c&ugrave;ng với trải nghiệm chơi game mượt m&agrave; v&agrave; ch&acirc;n thực.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh phụ của Samsung Galaxy Z Fold5 được thiết kế với k&iacute;ch thước 6.2 inch v&agrave; t&iacute;ch hợp tr&ecirc;n bề mặt b&ecirc;n ngo&agrave;i của m&aacute;y khi n&oacute; gập lại. Đ&acirc;y l&agrave; một t&iacute;nh năng độc đ&aacute;o v&agrave; tiện &iacute;ch, cho ph&eacute;p người d&ugrave;ng dễ d&agrave;ng truy cập c&aacute;c chức năng tương tự như trong m&agrave;n h&igrave;nh ch&iacute;nh m&agrave; kh&ocirc;ng cần mở điện thoại ho&agrave;n to&agrave;n.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095700.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại - Samsung Galaxy Z Fold5\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095700.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095700.jpg\" title=\"Màn hình điện thoại - Samsung Galaxy Z Fold5\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh phụ của Galaxy Z Fold5 cũng được x&acirc;y dựng với c&ocirc;ng nghệ Dynamic AMOLED 2X đem đến h&igrave;nh ảnh sắc n&eacute;t v&agrave; tương phản cao, gi&uacute;p người d&ugrave;ng c&oacute; trải nghiệm xem nội dung đ&aacute;ng kinh ngạc tr&ecirc;n m&agrave;n h&igrave;nh phụ n&agrave;y. C&ugrave;ng với đ&oacute; l&agrave; độ ph&acirc;n giải HD+ cho ph&eacute;p hiển thị h&igrave;nh ảnh, video v&agrave; nội dung đa phương tiện với chất lượng r&otilde; r&agrave;ng, đủ tốt để ta c&oacute; thể xem phim v&agrave; chơi game được thoải m&aacute;i.</p>\r\n\r\n<h3>Hiệu năng bức ph&aacute; mọi giới hạn</h3>\r\n\r\n<p>Một trong những điểm nổi bật của chiếc m&aacute;y n&agrave;y l&agrave; sự t&iacute;ch hợp của vi xử l&yacute; Snapdragon 8 Gen 2 for Galaxy gi&uacute;p cho điện thoại sở hữu hiệu năng mạnh mẽ v&agrave; đa nhiệm tuyệt vời. Snapdragon 8 Gen 2&nbsp;for Galaxy&nbsp;l&agrave; một vi xử l&yacute; di động h&agrave;ng đầu của Qualcomm, được x&acirc;y dựng tr&ecirc;n tiến tr&igrave;nh c&ocirc;ng nghệ ti&ecirc;n tiến, l&agrave; sự kết hợp giữa c&aacute;c nh&acirc;n xử l&yacute; mạnh mẽ v&agrave; đồ họa cấp cao, hứa hẹn mang đến khả năng xử l&yacute; nhanh ch&oacute;ng v&agrave; hiệu suất tối đa.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095950.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng điện thoại - Samsung Galaxy Z Fold5\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095950.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-095950.jpg\" title=\"Hiệu năng điện thoại - Samsung Galaxy Z Fold5\" /></a></p>\r\n\r\n<p>Nhờ sự mạnh mẽ của Snapdragon 8 Gen 2 for Galaxy, Galaxy Z Fold5 c&oacute; thể thực hiện nhiều nhiệm vụ phức tạp v&agrave; đ&ograve;i hỏi t&agrave;i nguy&ecirc;n cao cấp một c&aacute;ch mượt m&agrave; v&agrave; nhanh ch&oacute;ng. D&ugrave; đ&oacute; l&agrave; chơi c&aacute;c tựa game 3D đỉnh cao, xem video 4K hoặc l&agrave;m việc đa nhiệm với nhiều ứng dụng c&ugrave;ng l&uacute;c, chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Tham khảo điện thoại Android tại Thế Giới Di Động\">điện thoại Android</a>&nbsp;n&agrave;y đều đ&aacute;p ứng mượt m&agrave; m&agrave; kh&ocirc;ng c&oacute; t&igrave;nh trạng giật lag.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-100115.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng điện thoại - Samsung Galaxy Z Fold5\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-100115.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-100115.jpg\" title=\"Hiệu năng điện thoại - Samsung Galaxy Z Fold5\" /></a></p>\r\n\r\n<p>Galaxy Z Fold5 trang bị dung lượng RAM 12 GB gi&uacute;p hỗ trợ tốt cho việc chạy nhiều ứng dụng c&ugrave;ng một l&uacute;c v&agrave; l&agrave;m việc đa nhiệm mượt m&agrave;. Với RAM dung lượng cao, người d&ugrave;ng c&oacute; thể mở đồng thời nhiều tab tr&igrave;nh duyệt, chơi game nặng v&agrave; thậm ch&iacute; l&agrave; chỉnh sửa video m&agrave; vẫn giữ được sự ổn định, hiệu suất cao.</p>\r\n\r\n<h3>N&acirc;ng tầm chất lượng ảnh chụp</h3>\r\n\r\n<p>Samsung Galaxy Z Fold5 được trang bị hệ thống camera đa dạng v&agrave; mạnh mẽ. Điện thoại c&oacute; tổng cộng ba camera bao gồm một camera ch&iacute;nh 50 MP chất lượng cao, một camera si&ecirc;u rộng 12 MP v&agrave; một camera tele 10 MP.&nbsp;</p>\r\n\r\n<p>Trong khi đ&oacute;, mặt trước của điện thoại cũng được trang bị camera selfie 10 MP v&agrave; 4 MP cho m&agrave;n h&igrave;nh phụ để bạn c&oacute; thể thỏa sức chụp ảnh tự sướng v&agrave; tham gia video call một c&aacute;ch thoải m&aacute;i.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-100606.jpg\" onclick=\"return false;\"><img alt=\"Camera của điện thoại - Samsung Galaxy Z Fold5\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-100606.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-100606.jpg\" title=\"Camera của điện thoại - Samsung Galaxy Z Fold5\" /></a></p>\r\n\r\n<p>Với c&ocirc;ng nghệ ti&ecirc;n tiến v&agrave; cảm biến cao cấp, camera tr&ecirc;n Samsung Galaxy Z Fold5 đảm bảo cho việc chụp ảnh sắc n&eacute;t v&agrave; chi tiết. Khả năng lấy n&eacute;t nhanh v&agrave; ch&iacute;nh x&aacute;c gi&uacute;p bạn bắt trọn khoảnh khắc đẹp ngay cả trong điều kiện &aacute;nh s&aacute;ng kh&oacute; khăn.</p>\r\n\r\n<p>Hệ thống camera tr&ecirc;n Galaxy Z Fold5 được trang bị c&aacute;c t&iacute;nh năng chụp ảnh trong điều kiện &aacute;nh s&aacute;ng yếu như chế độ Night Mode, cho ph&eacute;p bạn chụp ảnh chất lượng cao ngay cả trong điều kiện thiếu s&aacute;ng. Bạn c&oacute; thể ghi lại những khoảnh khắc đ&aacute;ng nhớ v&agrave;o ban đ&ecirc;m m&agrave; kh&ocirc;ng cần lo lắng về chất lượng ảnh bị mờ hay nhiễu.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-100611.jpg\" onclick=\"return false;\"><img alt=\"Camera của điện thoại - Samsung Galaxy Z Fold5\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-100611.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-100611.jpg\" title=\"Camera của điện thoại - Samsung Galaxy Z Fold5\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra hệ thống chống rung quang học OIS cũng đ&atilde; xuất hiện tr&ecirc;n Galaxy Z Fold5, n&oacute; gi&uacute;p giảm thiểu tối đa hiện tượng rung h&igrave;nh ảnh khi chụp ảnh hoặc quay video, đảm bảo c&aacute;c bức ảnh v&agrave; video của bạn lu&ocirc;n mượt m&agrave; v&agrave; ổn định, kể cả trong điều kiện chụp chuyển động hoặc bất ổn.</p>\r\n\r\n<h3>Pin lớn đ&aacute;p ứng đủ cho cả ng&agrave;y d&agrave;i</h3>\r\n\r\n<p>Galaxy Z Fold5 được trang bị vi&ecirc;n pin c&oacute; dung lượng lớn 4400 mAh. Pin n&agrave;y được sản xuất với c&ocirc;ng nghệ ti&ecirc;n tiến, mang lại khả năng chịu tải tốt v&agrave; tuổi thọ d&agrave;i hơn so với c&aacute;c phi&ecirc;n bản trước đ&oacute;. Nhờ v&agrave;o pin dung lượng lớn v&agrave; tối ưu h&oacute;a hiệu suất, Galaxy Z Fold5 c&oacute; thể sử dụng trong một khoảng thời gian d&agrave;i trước khi cần sạc lại.&nbsp;</p>\r\n\r\n<p>Th&ocirc;ng thường, người d&ugrave;ng c&oacute; thể sử dụng điện thoại trong khoảng 7 - 10 tiếng với mức sử dụng th&ocirc;ng thường như lướt web, xem video v&agrave; sử dụng c&aacute;c ứng dụng hằng ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-100742.jpg\" onclick=\"return false;\"><img alt=\"Pin trên điện thoại - Samsung Galaxy Z Fold5\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-100742.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/301608/samsung-galaxy-z-fold5-260723-100742.jpg\" title=\"Pin trên điện thoại - Samsung Galaxy Z Fold5\" /></a></p>\r\n\r\n<p>Galaxy Z Fold5 hỗ trợ sạc nhanh th&ocirc;ng qua cổng Type-C với c&ocirc;ng suất tối đa 25 W. Với c&ocirc;ng nghệ sạc nhanh ti&ecirc;n tiến của Samsung, người d&ugrave;ng c&oacute; thể nạp đầy pin nhanh ch&oacute;ng v&agrave; tiếp tục sử dụng điện thoại m&agrave; kh&ocirc;ng mất nhiều thời gian chờ đợi. Ngo&agrave;i ra, điện thoại cũng hỗ trợ sạc kh&ocirc;ng d&acirc;y, cho ph&eacute;p người d&ugrave;ng tiện lợi sạc pin m&agrave; kh&ocirc;ng cần kết nối d&acirc;y c&aacute;p.</p>\r\n\r\n<h3>V&igrave; sao bạn n&ecirc;n chọn mua Samsung Galaxy Z Fold5 tại Thế Giới Di Động?</h3>\r\n\r\n<p>Thế Giới Di Động l&agrave; một trong những doanh nghiệp h&agrave;ng đầu trong lĩnh vực b&aacute;n lẻ điện thoại di động tại Việt Nam. Với uy t&iacute;n v&agrave; kinh nghiệm l&acirc;u năm, ch&uacute;ng t&ocirc;i đ&atilde; x&acirc;y dựng một mạng lưới cửa h&agrave;ng rộng khắp tr&ecirc;n to&agrave;n quốc, mang đến sự tiện lợi cho kh&aacute;ch h&agrave;ng trong việc sử dụng dịch vụ sau b&aacute;n h&agrave;ng.</p>\r\n\r\n<p>Nếu bạn c&oacute; bất kỳ c&acirc;u hỏi hoặc vấn đề n&agrave;o li&ecirc;n quan đến sản phẩm, Thế Giới Di Động sẽ cung cấp sự hỗ trợ nhanh ch&oacute;ng để giải đ&aacute;p mọi thắc mắc.</p>\r\n\r\n<p>Thế Giới Di Động thường xuy&ecirc;n tổ chức c&aacute;c chương tr&igrave;nh khuyến m&atilde;i hấp dẫn d&agrave;nh cho kh&aacute;ch h&agrave;ng. Điều n&agrave;y bao gồm c&aacute;c ưu đ&atilde;i như giảm gi&aacute;, tặng qu&agrave; k&egrave;m theo hoặc cung cấp dịch vụ trả g&oacute;p với l&atilde;i suất ưu đ&atilde;i. Điều n&agrave;y gi&uacute;p bạn tiết kiệm một khoản tiền đ&aacute;ng kể khi mua một chiếc Samsung Galaxy Z Fold5.</p>\r\n\r\n<h3>Tổng hợp c&aacute;c c&acirc;u hỏi xoay quanh sản phẩm</h3>\r\n\r\n<p><strong>Samsung Galaxy Z Fold5 c&oacute; những điểm n&acirc;ng cấp mạnh về g&igrave;?</strong></p>\r\n\r\n<p>Chiếc điện thoại gập n&agrave;y của Samsung được trang bị con chip Snapdragon 8 Gen 2 mạnh mẽ nhất của Qualcomm ở thời điểm hiện tại, hơn hết n&oacute; c&ograve;n l&agrave; phi&ecirc;n bản đặc biệt v&agrave; được tối ưu ri&ecirc;ng cho d&ograve;ng Galaxy, v&igrave; thế hiệu suất m&agrave;&nbsp;Z Fold5 mang lại hứa hẹn sẽ rất mạnh mẽ để c&oacute; thể xử l&yacute; được mọi t&aacute;c vụ nặng.</p>\r\n\r\n<p>Đối với hệ thống camera th&igrave;&nbsp;Z Fold5 nổi bật với cảm biến ch&iacute;nh 50 MP, c&ugrave;ng với đ&oacute; l&agrave; khả năng zoom quang học v&agrave; quay video với chất lượng độ ph&acirc;n giải l&ecirc;n đến 8K.</p>\r\n\r\n<p>Ngo&agrave;i ra, thiết kế của m&aacute;y cũng được quan t&acirc;m nhiều,&nbsp;Z Fold5 sẽ n&acirc;ng cấp phần bản lề tốt hơn c&aacute;c thế hệ trước đ&oacute;, c&ugrave;ng với độ d&agrave;y m&aacute;y tối ưu nhằm mang đến vẻ ngo&agrave;i thanh tho&aacute;t hơn, tinh tế v&agrave; cầm nắm nhẹ nh&agrave;ng thoải m&aacute;i.</p>\r\n\r\n<p><strong>Galaxy Z Fold5 sẽ c&oacute; mấy m&agrave;u?</strong></p>\r\n\r\n<p>Z Fold5 sẽ được kinh doanh với 3 m&agrave;u sắc, thường tập trung ở những m&agrave;u sắc trầm t&iacute;nh, sang trọng như: Xanh Icy, Đen Phantom v&agrave; Kem Ivory.</p>\r\n\r\n<p><strong>Gi&aacute; b&aacute;n Samsung Galaxy Z Fold5 bao nhi&ecirc;u?</strong></p>\r\n\r\n<p>Th&ocirc;ng tin về chương tr&igrave;nh khuyến m&atilde;i từ ng&agrave;y 26/07 - 11/08:</p>\r\n\r\n<p>- Săn Galaxy Z Flip5 nhập hội GEN Z: 5 suất Samsung Galaxy Z Flip5 với gi&aacute; 5K, 5 suất tai nghe BUDS 2 với gi&aacute; 0đ.</p>\r\n\r\n<p>- Tặng g&oacute;i dịch vụ cao cấp Z-Elite trị gi&aacute; 8 triệu bao gồm Samsung Care + 1 năm.</p>\r\n\r\n<p>- Thu cũ đổi mới trợ gi&aacute; đến 2 triệu.</p>\r\n\r\n<p>- Hỗ trợ trả g&oacute;p 0%</p>\r\n\r\n<p>- Đặc biệt giảm ngay 4 triệu khi mua Galaxy Z Fold5 phi&ecirc;n bản bộ nhớ 512 GB v&agrave; 1 TB.</p>\r\n\r\n<p>Gi&aacute; b&aacute;n Samsung Z Fold 5 tại Thế Giới Di Động:</p>\r\n\r\n<p>Samsung Galaxy Z Fold 5 256 GB:&nbsp;<strong>40.990.000₫</strong></p>\r\n\r\n<p>Samsung Galaxy Z Fold 5 512 GB:&nbsp;<strong>44.990.000₫</strong></p>\r\n\r\n<p>Samsung Galaxy Z Fold 5 1 TB:&nbsp;<strong>51.990.000₫</strong></p>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh lớn, camera chụp ảnh sắc n&eacute;t, hiệu năng mạnh mẽ v&agrave; cơ chế gập mở độc đ&aacute;o gi&uacute;p cho Galaxy Z Fold5 trở th&agrave;nh một lựa chọn hấp dẫn cho những người y&ecirc;u c&ocirc;ng nghệ v&agrave; mong muốn trải nghiệm một thiết bị di động đỉnh cao.</p>\r\n', 'Đã đăng', 1, '11-09-2023', 3),
(12, 'VHL#9', 'Samsung Galaxy A24 6GB', 5890000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung2.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a24\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy A24 6GB đang kinh doanh tại Thế Giới Di Động\">Samsung Galaxy A24 6GB</a>&nbsp;tiếp tục l&agrave; mẫu điện thoại tầm trung được nh&agrave; Samsung giới thiệu đến thị trường Việt Nam v&agrave;o th&aacute;ng 04/2023, m&aacute;y nổi bật với gi&aacute; th&agrave;nh rẻ, m&agrave;n h&igrave;nh Super AMOLED c&ugrave;ng camera 50 MP chụp ảnh sắc n&eacute;t.</h3>\r\n\r\n<h3>Hiển thị nội dung tốt hơn với m&agrave;n h&igrave;nh Super AMOLED</h3>\r\n\r\n<p>Ấn tượng đầu ti&ecirc;n của m&igrave;nh sau khi cầm tr&ecirc;n tay chiếc Galaxy A24 l&agrave; về phần m&agrave;n h&igrave;nh của m&aacute;y, m&igrave;nh thực sự rất ấn tượng khi đ&acirc;y chỉ l&agrave; một mẫu điện thoại thuộc ph&acirc;n kh&uacute;c tầm trung - cận gi&aacute; rẻ, nhưng đ&atilde; được Samsung trang bị tấm nền Super AMOLED cao cấp.</p>\r\n\r\n<p>Ở thời điểm hiện tại, việc bắt gặp một chiếc m&aacute;y trong tầm gi&aacute; n&agrave;y được trang bị tấm nền Super AMOLED thực sự l&agrave; rất hiếm tại thị trường Việt Nam, trong khi nhiều đối thủ cũng chỉ trang bị&nbsp;cho sản phẩm của m&igrave;nh tấm nền IPS LCD, hay thậm ch&iacute; tốt hơn cũng chỉ l&agrave; AMOLED.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-amoled-la-gi-905766\">M&agrave;n h&igrave;nh AMOLED l&agrave; g&igrave;? C&oacute; g&igrave; nổi bật? Thiết bị n&agrave;o c&oacute; m&agrave;n h&igrave;nh AMOLED?</a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043233.jpg\" onclick=\"return false;\"><img alt=\"Màn hình AMOLED - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043233.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043233.jpg\" title=\"Màn hình AMOLED - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>Điều n&agrave;y gi&uacute;p Galaxy A24 c&oacute; được kh&aacute; nhiều lợi thế để cạnh tranh với nhiều mẫu m&aacute;y kh&aacute;c khi mang đến trải nghiệm h&igrave;nh ảnh thật sự ấn tượng.</p>\r\n\r\n<p>Nội dung khi được hiển thị tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung tại Thế Giới Di Động\">điện thoại Samsung</a>&nbsp;c&oacute; m&agrave;u sắc rực rỡ, sống động, ở những gam m&agrave;u nổi th&igrave; m&agrave;n h&igrave;nh cũng sẽ tự động l&agrave;m rực l&ecirc;n đ&ocirc;i ch&uacute;t, gi&uacute;p trải nghiệm của m&igrave;nh khi xem trở n&ecirc;n m&atilde;n nh&atilde;n hơn rất nhiều.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043235.jpg\" onclick=\"return false;\"><img alt=\"Khả năng tái hiện hình ảnh có chiều sâu - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043235.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043235.jpg\" title=\"Khả năng tái hiện hình ảnh có chiều sâu - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>Đối với những v&ugrave;ng tối hay cụ thể l&agrave; c&aacute;c điểm đen tr&ecirc;n m&agrave;n h&igrave;nh, Galaxy A24 t&aacute;i hiện lại nội dung c&oacute; chiều s&acirc;u hơn khi c&aacute;c điểm ảnh l&uacute;c n&agrave;y được tắt ho&agrave;n to&agrave;n để thể hiện m&agrave;u đen tuyệt đối.</p>\r\n\r\n<p>Được hỗ trợ độ ph&acirc;n giải Full HD+ (1080 x 2340 Pixels) vậy n&ecirc;n mọi chi tiết đều được t&aacute;i hiện lại một c&aacute;ch sắc n&eacute;t, kh&ocirc;ng xuất hiện hiện tượng răng cưa hay lộ điểm ảnh, cảm gi&aacute;c khi chơi game hay xem phim cũng trở n&ecirc;n ch&acirc;n thực v&agrave; sống động.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043237.jpg\" onclick=\"return false;\"><img alt=\"Hiển thị nội dung sắc nét - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043237.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043237.jpg\" title=\"Hiển thị nội dung sắc nét - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>Galaxy A24 được hỗ trợ tần số qu&eacute;t 90 Hz, v&igrave; thế mọi thao t&aacute;c cuộn/lướt tr&ecirc;n m&agrave;n h&igrave;nh đều được phản hồi nhanh, tạo cho m&igrave;nh cảm gi&aacute;c mượt m&agrave; hơn.</p>\r\n\r\n<p>Theo m&igrave;nh thấy th&igrave; đ&acirc;y l&agrave; một mức l&yacute; tưởng đối với một người d&ugrave;ng cơ bản, khi m&agrave; việc trang bị tần số qu&eacute;t cao như 120 Hz kh&ocirc;ng được tận dụng hết bởi nhu cầu chơi game hay xem phim, v&agrave; với 90 Hz sẽ gi&uacute;p tối ưu được trải nghiệm vuốt chạm song cũng mang lại hiệu quả cho việc l&agrave;m giảm năng lượng ti&ecirc;u hao tr&ecirc;n m&agrave;n h&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043239.jpg\" onclick=\"return false;\"><img alt=\"Trang bị màn hình tần số quét cao - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043239.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043239.jpg\" title=\"Trang bị màn hình tần số quét cao - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>Về phần độ s&aacute;ng th&igrave; m&agrave;n h&igrave;nh đạt được mức tối đa 1000 nits, n&oacute; vừa đủ để hiển thị r&otilde; nội dung khi sử dụng dưới trời nắng gắt, m&igrave;nh c&oacute; thể vừa xem chỉ dẫn đường v&agrave; vừa di chuyển một c&aacute;ch dễ d&agrave;ng m&agrave; kh&ocirc;ng cần phải d&ugrave;ng tay che chắn bớt &aacute;nh nắng.</p>\r\n\r\n<p>Tuy nhi&ecirc;n th&igrave; m&igrave;nh cũng c&oacute; một v&agrave;i lưu &yacute; l&agrave; m&aacute;y chỉ hiển thị thực sự tốt ở những nội dung c&oacute; nhiều &aacute;nh s&aacute;ng trắng, bởi sau khi chuyển sang giao diện ban đ&ecirc;m hay xem những nội dung c&oacute; nhiều gam m&agrave;u tối, l&uacute;c n&agrave;y m&aacute;y mang đến cho m&igrave;nh cảm gi&aacute;c hơi kh&oacute; nh&igrave;n một t&iacute;.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043240.jpg\" onclick=\"return false;\"><img alt=\"Độ sáng màn hình cao - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043240.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043240.jpg\" title=\"Độ sáng màn hình cao - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>V&agrave; theo như trải nghiệm thực tế, số trường hợp m&agrave; m&igrave;nh sử dụng điện thoại trực tiếp dưới trời nắng gắt l&agrave; rất &iacute;t, chủ yếu l&agrave; d&ugrave;ng để xem đường hay trả lời tin nhắn gấp, v&igrave; thế những nội dung m&igrave;nh xem cũng kh&ocirc;ng thuộc dạng qu&aacute; tối n&ecirc;n cảm gi&aacute;c khi nh&igrave;n vẫn rất thoải m&aacute;i.&nbsp;</p>\r\n\r\n<p>Galaxy A24 sở hữu m&agrave;n h&igrave;nh c&oacute; k&iacute;ch thước lớn 6.5 inch, được Samsung thiết kế dạng giọt nước với viền m&agrave;n h&igrave;nh được tối ưu h&oacute;a mỏng đều ở cả ba cạnh. Tuy nhi&ecirc;n, phần cằm m&agrave;n h&igrave;nh vẫn c&ograve;n đ&ocirc;i ch&uacute;t d&agrave;y, g&acirc;y ra sự thiếu c&acirc;n đối trong tổng thể.</p>\r\n\r\n<p>Tuy vậy, m&igrave;nh cũng đ&atilde; tận dụng phần n&agrave;y như một điểm tựa khi cầm m&aacute;y để chơi game hoặc xem nội dung m&agrave; kh&ocirc;ng cần lo lắng về việc ng&oacute;n tay g&acirc;y cản trở trong v&ugrave;ng hiển thị.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Thiết kế tối giản đầy sang trọng</h3>\r\n\r\n<p>Năm nay nh&agrave; Samsung đ&atilde; mang đến một sự thay đổi đ&aacute;ng ch&uacute; &yacute; trong thiết kế với d&ograve;ng sản phẩm mới. Kh&ocirc;ng c&ograve;n sử dụng kiểu bo cong mềm mại như phi&ecirc;n bản tiền nhiệm m&agrave; thay v&agrave;o đ&oacute; sẽ l&agrave; một diện mạo thời thượng hơn. Khung viền vu&ocirc;ng đậm chất xu hướng, mặt lưng phẳng kết hợp bề mặt nhẵn b&oacute;ng đ&atilde; khiến tổng thể m&aacute;y trở n&ecirc;n nổi bật hơn.&nbsp;</p>\r\n\r\n<p>Mặt lưng c&ugrave;ng bộ khung của&nbsp;chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-a\" target=\"_blank\" title=\"Tham khảo điện thoại Galaxy dòng A tại Thế Giới Di Động\">điện thoại Galaxy d&ograve;ng A</a>&nbsp;n&agrave;y&nbsp;được cấu tạo chủ yếu từ chất liệu nhựa n&ecirc;n m&aacute;y c&oacute; khối lượng chỉ 195 g. Mặc d&ugrave; ho&agrave;n thiện từ nhựa nhưng m&aacute;y vẫn c&oacute; được một sự cứng c&aacute;p nhất định, khi chạm v&agrave;o mặt lưng kh&ocirc;ng c&oacute; hiện tượng l&otilde;m hay tiếng ọp ẹp, tạo cảm gi&aacute;c b&ecirc;n trong m&aacute;y được thiết kế một c&aacute;ch chắc chắn v&agrave; đầy đặn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n, c&oacute; một điểm trừ nhỏ l&agrave; phần mặt lưng của m&aacute;y cũng kh&aacute; dễ b&aacute;m dấu v&acirc;n tay, việc kh&aacute;ng xước cũng chưa thực sự hiệu quả cho lắm. Do vậy người d&ugrave;ng n&ecirc;n trang bị th&ecirc;m cho điện thoại một ốp lưng để bảo vệ m&aacute;y tốt hơn trước những t&igrave;nh huống va quẹt ngo&agrave;i &yacute; muốn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Samsung cũng đ&atilde; c&oacute; sự thay đổi trong thiết kế camera với thế hệ sản phẩm mới n&agrave;y. Thay v&igrave; gộp tất cả ống k&iacute;nh v&agrave;o chung một cụm như trước đ&acirc;y, h&atilde;ng đ&atilde; bố tr&iacute; ri&ecirc;ng lẻ từng ống k&iacute;nh cho Galaxy A24. C&aacute;ch l&agrave;m n&agrave;y giống với những mẫu điện thoại cao cấp của h&atilde;ng như Galaxy S23 hay Galaxy S23 Plus. Nhờ vậy m&agrave; m&aacute;y mang một vẻ đẹp hiện đại v&agrave; đồng bộ hơn với hai mẫu flagship tr&ecirc;n.</p>\r\n\r\n<p>M&igrave;nh nhận thấy rằng cụm camera được thiết kế hơi nh&ocirc; cao so với mặt lưng của m&aacute;y. Tuy nhi&ecirc;n, điều đ&aacute;ng ch&uacute; &yacute; l&agrave; mặt k&iacute;nh bảo vệ cảm biến được ho&agrave;n thiện l&otilde;m v&agrave;o b&ecirc;n trong, gi&uacute;p hạn chế bị trầy xước khi đặt m&aacute;y l&ecirc;n c&aacute;c mặt phẳng th&ocirc;ng thường. Điều n&agrave;y hỗ trợ bảo vệ camera tốt hơn, đồng thời tr&aacute;nh được t&igrave;nh trạng giảm chất lượng ảnh chụp do c&aacute;c vết xước g&acirc;y ra.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>K&eacute;o d&agrave;i thời gian sử dụng với pin 5000 mAh</h3>\r\n\r\n<p>Galaxy A24 được trang bị một vi&ecirc;n pin lớn c&oacute; dung lượng l&ecirc;n đến 5000 mAh v&igrave; thế m&aacute;y mang lại thời gian sử dụng rất d&agrave;i. Với c&aacute;c nhu cầu đa t&aacute;c vụ từ chơi game cho đến lướt web hay xem phim, điện thoại c&oacute; khả năng duy tr&igrave; thời lượng pin trong khoảng 9 tiếng 13 ph&uacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&agrave;i thử nghiệm n&agrave;y được m&igrave;nh thực hiện khi m&aacute;y đang ở trạng th&aacute;i 100% dung lượng pin, &acirc;m lượng điện thoại ở mức 50%, độ s&aacute;ng m&agrave;n h&igrave;nh đạt mức tối đa v&agrave; m&aacute;y ho&agrave;n to&agrave;n sử dụng kết nối wifi, kh&ocirc;ng sử dụng mạng di động.</p>\r\n\r\n<p>C&oacute; những h&ocirc;m m&igrave;nh kh&ocirc;ng sử dụng điện thoại để chơi game m&agrave; chỉ d&ugrave;ng để lướt web, xem phim cũng như nhắn tin trả lời c&ocirc;ng việc, nhờ vậy m&agrave; m&aacute;y kh&ocirc;ng phải tốn qu&aacute; nhiều năng lượng để cung cấp cho chip hoạt động ở mức xung nhịp cao, điều n&agrave;y gi&uacute;p k&eacute;o d&agrave;i thời gian sử dụng l&ecirc;n đến hơn 10 giờ li&ecirc;n tục.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trung b&igrave;nh th&igrave; Galaxy A24 ti&ecirc;u hao khoảng 8 cho đến 12% pin cho khoảng 1 giờ sử dụng, nếu c&agrave;i đặt độ s&aacute;ng m&agrave;n h&igrave;nh ở mức thấp hơn th&igrave; m&igrave;nh tin rằng con số n&agrave;y c&ograve;n c&oacute; thể k&eacute;o d&agrave;i hơn nữa.</p>\r\n\r\n<p>Ngo&agrave;i việc trang bị vi&ecirc;n pin lớn ra th&igrave; đ&acirc;y c&ograve;n l&agrave; mẫu điện thoại sạc nhanh với c&ocirc;ng suất hỗ trợ tối đa l&agrave; 25 W. Kh&aacute; tiếc khi Samsung kh&ocirc;ng trang bị th&ecirc;m củ sạc d&agrave;nh cho Galaxy A24, v&igrave; thế n&ecirc;n m&igrave;nh phải sử dụng củ sạc ri&ecirc;ng từ h&atilde;ng Baseus c&oacute; c&ocirc;ng suất 65 W để thực hiện b&agrave;i test về thời gian sạc tr&ecirc;n điện thoại.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043254.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ tính năng sạc pin nhanh - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043254.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043254.jpg\" title=\"Hỗ trợ tính năng sạc pin nhanh - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>Mục đ&iacute;ch m&igrave;nh sử dụng củ sạc c&oacute; c&ocirc;ng suất lớn hơn để kiểm tra xem m&aacute;y c&oacute; tương th&iacute;ch tốt hay kh&ocirc;ng, cũng như theo d&otilde;i xem m&aacute;y c&oacute; thực sự sạc được nhanh hơn?&nbsp;</p>\r\n\r\n<p>Kết quả thực tế, m&igrave;nh đ&atilde; mất khoảng 2 giờ 43 ph&uacute;t để c&oacute; thể nạp đầy được vi&ecirc;n pin 5000 mAh v&agrave; m&aacute;y chỉ nhận được c&ocirc;ng suất tối đa l&agrave; 25 W, vậy n&ecirc;n những bạn c&oacute; &yacute; tưởng d&ugrave;ng củ sạc c&oacute; c&ocirc;ng suất lớn hơn để r&uacute;t ngắn thời gian th&igrave; cũng n&ecirc;n lưu &yacute; vấn đề n&agrave;y trước khi chọn mua củ sạc.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043255.jpg\" onclick=\"return false;\"><img alt=\"Thời gian sử dụng điện thoại - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043255.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043255.jpg\" title=\"Thời gian sử dụng điện thoại - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>Để tận dụng tối đa hiệu suất sạc đồng thời tiết kiệm tiền, bạn n&ecirc;n lựa chọn một củ sạc c&ocirc;ng suất 25 W l&agrave; ph&ugrave; hợp. Điều n&agrave;y gi&uacute;p bạn kh&ocirc;ng phải đầu tư qu&aacute; nhiều cho một củ sạc kh&ocirc;ng cần thiết, trong khi vẫn đảm bảo khả năng sạc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại di động tại Thế Giới Di Động\">điện thoại</a>&nbsp;một c&aacute;ch hiệu quả.</p>\r\n\r\n<h3>T&iacute;ch hợp chipset gaming từ nh&agrave; MediaTek</h3>\r\n\r\n<p>B&ecirc;n trong Galaxy A24 được trang bị con chip Helio G99 đến từ nh&agrave; MediaTek, hoạt động với mức xung nhịp tối đa c&oacute; thể đạt được l&agrave; 2.2 GHz, kết hợp c&ugrave;ng GPU Mali-G57 cung cấp hiệu suất đồ họa mạnh mẽ. Điều n&agrave;y gi&uacute;p cho trải nghiệm chơi game ổn định v&agrave; mượt m&agrave; hơn, đồng thời cũng hỗ trợ xử l&yacute; h&igrave;nh ảnh v&agrave; video chất lượng cao.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/chip-mediatek-helio-g99-hieu-nang-the-nao-co-dang-1465468\" target=\"_blank\">Đ&aacute;nh gi&aacute; CPU MediaTek Helio G99 chi tiết về th&ocirc;ng số v&agrave; hiệu năng</a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043257.jpg\" onclick=\"return false;\"><img alt=\"Trang bị chipset nhà MediaTek - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043257.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043257.jpg\" title=\"Trang bị chipset nhà MediaTek - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Tham khảo điện thoại Android tại Thế Giới Di Động\">điện thoại Android</a>&nbsp;n&agrave;y&nbsp;g&acirc;y ấn tượng mạnh với hiệu năng vượt trội th&ocirc;ng qua c&aacute;c ứng dụng kiểm tra khả năng xử l&yacute; chuy&ecirc;n d&ugrave;ng. Tr&ecirc;n Geekbench 6, điểm đơn nh&acirc;n đạt 717 v&agrave; điểm đa nh&acirc;n đạt 1852, c&ograve;n tr&ecirc;n PCMark, tổng điểm đạt 9034. Những con số n&agrave;y kh&ocirc;ng chỉ cho thấy khả năng đa nhiệm mạnh mẽ m&agrave; c&ograve;n khẳng định sức mạnh xử l&yacute; đa luồng của m&aacute;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043259.jpg\" onclick=\"return false;\"><img alt=\"Điểm đánh giá hiệu năng - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043259.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043259.jpg\" title=\"Điểm đánh giá hiệu năng - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>Đối với c&aacute;c t&aacute;c vụ sử dụng cơ bản, Galaxy A24 mang đến cho m&igrave;nh một trải nghiệm ổn định khi kh&ocirc;ng hề c&oacute; bất kỳ hiện tượng giật lag n&agrave;o xảy ra, kể cả việc lướt web th&ocirc;ng thường cho đến xem phim hay chụp ảnh.</p>\r\n\r\n<p>C&ograve;n về khả năng xử l&yacute; đồ họa trong l&uacute;c chơi game th&igrave; Galaxy A24 vẫn mang đến trải nghiệm ổn. Mặc d&ugrave; c&agrave;i đặt đồ họa ở mức si&ecirc;u cao nhưng m&aacute;y vẫn xử l&yacute; dễ d&agrave;ng khi cho ra qu&aacute; tr&igrave;nh chơi game tương đối mượt v&agrave; ổn định, tốc độ khung h&igrave;nh cũng dao động loanh quanh mức 60 FPS.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043300.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game trên điện thoại - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043300.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043300.jpg\" title=\"Trải nghiệm chơi game trên điện thoại - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043302.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game trên điện thoại - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043302.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043302.jpg\" title=\"Trải nghiệm chơi game trên điện thoại - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>Đến một tựa game nặng hơn một ch&uacute;t l&agrave; PUBG Mobile, m&igrave;nh c&agrave;i đặt mức đồ họa cho tựa game n&agrave;y l&agrave; ở mức c&acirc;n bằng v&agrave; tốc độ khung h&igrave;nh cực cao. L&uacute;c n&agrave;y m&aacute;y c&oacute; xảy ra một v&agrave;i hiện tượng giật lag ở đầu game khi phải load map kh&aacute; nhiều, thế nhưng dần về sau th&igrave; mọi thứ bắt đầu ổn hơn v&agrave; FPS cũng nằm ở trạng th&aacute;i ổn khi dao động trong ngưỡng 40.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043304.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game trên điện thoại - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043304.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043304.jpg\" title=\"Trải nghiệm chơi game trên điện thoại - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043305.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game trên điện thoại - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043305.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043305.jpg\" title=\"Trải nghiệm chơi game trên điện thoại - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>Ngo&agrave;i việc trang bị chipset tốt ra th&igrave; đ&acirc;y c&ograve;n l&agrave; mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-6gb\" target=\"_blank\" title=\"Tham khảo điện thoại RAM 6GB tại Thế Giới Di Động\">điện thoại RAM 6 GB</a>, v&igrave; thế m&aacute;y mang đến khả năng đa nhiệm tốt hơn, bộ nhớ đệm lớn n&ecirc;n việc hỗ trợ chơi game đồ họa cao cũng trở n&ecirc;n mượt m&agrave; hẳn.&nbsp;</p>\r\n\r\n<h3>Thu lại c&aacute;c bức ảnh sắc n&eacute;t hơn nhờ camera 50 MP</h3>\r\n\r\n<p>Về hệ thống ống k&iacute;nh của m&aacute;y, Galaxy A24 được trang bị 3 camera trong đ&oacute; camera ch&iacute;nh c&oacute; độ ph&acirc;n giải 50 MP, k&egrave;m với đ&oacute; l&agrave; 2 camera phụ c&oacute; độ ph&acirc;n giải lần lượt l&agrave; 5 MP v&agrave; 2 MP để hỗ trợ cho việc chụp ảnh g&oacute;c si&ecirc;u rộng v&agrave; macro (si&ecirc;u cận).</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043307.jpg\" onclick=\"return false;\"><img alt=\"Trang bị hệ thống 3 camera - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043307.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043307.jpg\" title=\"Trang bị hệ thống 3 camera - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>Ở m&ocirc;i trường c&oacute; đầy đủ &aacute;nh s&aacute;ng, ảnh chụp cho ra c&oacute; độ chi tiết cao, m&agrave;u sắc được t&aacute;i tạo h&agrave;i h&ograve;a v&agrave; sinh động, tổng thể tạo n&ecirc;n một c&aacute;i nh&igrave;n bắt mắt m&agrave; kh&ocirc;ng cần qua bước hậu kỳ trước khi đăng tải l&ecirc;n mạng x&atilde; hội.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043309.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043309.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043309.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>Một trong những t&iacute;nh năng hữu &iacute;ch m&agrave; Galaxy A24 c&oacute; được l&agrave; khả năng zoom quang học, ảnh chụp từ t&iacute;nh năng n&agrave;y sẽ ph&oacute;ng to được chủ thể hoặc quang cảnh nhưng vẫn đảm bảo được độ chi tiết cao trong một khoảng nhất định. Cụ thể l&agrave; bức ảnh dưới đ&acirc;y được m&igrave;nh zoom 3X nhưng độ n&eacute;t gần như kh&ocirc;ng c&oacute; qu&aacute; nhiều kh&aacute;c biệt so với ảnh chụp từ chế độ mặc định.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043311.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A24\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043311.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274018/samsung-galaxy-a24-010623-043311.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy A24\" /></a></p>\r\n\r\n<p>Tổng kết lại, Galaxy A24 thực sự l&agrave; một sự lựa chọn đ&aacute;ng c&acirc;n nhắc trong ph&acirc;n kh&uacute;c điện thoại tầm trung - cận gi&aacute; rẻ hiện nay. M&aacute;y kh&ocirc;ng chỉ c&oacute; mức gi&aacute; hấp dẫn m&agrave; c&ograve;n sở hữu cấu h&igrave;nh mạnh mẽ, đ&aacute;p ứng tốt c&aacute;c nhu cầu cơ bản v&agrave; đảm bảo cho người d&ugrave;ng trải nghiệm sử dụng l&acirc;u d&agrave;i.</p>\r\n', 'Chờ xét duyệt', 1, '11-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(13, 'VHL#10', 'Samsung Galaxy S21 FE 5G', 9790000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung3.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s21-fe-6gb\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S21 FE 5G (6GB/128GB) đang kinh doanh tại Thế Giới Di Động \">Samsung Galaxy S21 FE 5G (6GB/128GB)</a>&nbsp;được Samsung ra mắt với d&aacute;ng dấp thời thượng, cấu h&igrave;nh khỏe, chụp ảnh đẹp với bộ 3 camera chất lượng, thời lượng pin đủ d&ugrave;ng hằng ng&agrave;y v&agrave; c&ograve;n g&igrave; nữa? Mời bạn kh&aacute;m ph&aacute; qua nội dung sau ngay.</h3>\r\n\r\n<h3>Vẻ ngo&agrave;i sang trọng, m&agrave;u sắc thời trang</h3>\r\n\r\n<p>Galaxy S21 FE 5G&nbsp;thiết kế mỏng nhẹ với độ d&agrave;y&nbsp;<strong>7.9 mm</strong>, khối lượng chỉ&nbsp;<strong>177 gram</strong>, c&aacute;c g&oacute;c cạnh bo tr&ograve;n cho cảm gi&aacute;c h&agrave;i h&ograve;a, mềm mại, kết hợp c&aacute;c t&ocirc;ng m&agrave;u thời thượng gi&uacute;p bạn dễ d&agrave;ng tạo n&ecirc;n phong c&aacute;ch ri&ecirc;ng đầy c&aacute; t&iacute;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040738.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Vẻ ngoài sang trọng, màu sắc thời trang\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040738.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040738.jpg\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Vẻ ngoài sang trọng, màu sắc thời trang\" /></a></p>\r\n\r\n<p>C&aacute;c chi tiết được ho&agrave;n thiện tinh tế, khung m&aacute;y bằng nh&ocirc;m kh&aacute; nhẹ v&agrave; bền chắc, mặt lưng l&agrave;m từ nhựa, chế t&aacute;c c&oacute; độ nh&aacute;m nhẹ, cầm b&aacute;m tay, khi sử dụng c&oacute; hơi d&iacute;nh mồ h&ocirc;i nhưng rất dễ l&agrave;m sạch.&nbsp;</p>\r\n\r\n<p>M&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại chính hãng tại Thế Giới Di Động\">điện thoại th&ocirc;ng minh</a>&nbsp;phủ&nbsp;k&iacute;nh cường lực&nbsp;<strong>Corning Gorilla Glass Victus</strong>&nbsp;s&aacute;ng&nbsp;b&oacute;ng, gần như an to&agrave;n khi rơi ở độ cao&nbsp;<strong>2m&nbsp;</strong>xuống (th&ocirc;ng tin từ h&atilde;ng sản xuất c&ocirc;ng bố), hạn chế trầy xước hiệu quả.</p>\r\n\r\n<p>Ở cạnh h&ocirc;ng m&aacute;y, c&oacute; c&aacute;c n&uacute;t nguồn, &acirc;m lượng bấm &ecirc;m &aacute;i, khe gắn sim ở cạnh dưới, nằm gần micro n&ecirc;n khi lắp sim bạn cần ch&uacute; &yacute; để tr&aacute;nh nhầm lẫn thao t&aacute;c.&nbsp;</p>\r\n\r\n<p>Chuyển qua mặt sau của m&aacute;y th&igrave; c&aacute;ch sắp xếp camera sau theo h&agrave;ng dọc giống với phi&ecirc;n bản&nbsp;S21, cụm camera l&agrave;m gồ l&ecirc;n, c&oacute; thể bị xước nếu đặt nằm tr&ecirc;n mặt b&agrave;n nhiều lần, n&ecirc;n để bảo vệ ống k&iacute;nh tốt hơn, bạn c&oacute; thể d&ugrave;ng th&ecirc;m ốp lưng, miếng d&aacute;n.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040740.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Mặt sau của máy thì cách sắp xếp camera sau theo hàng dọc\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040740.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040740.jpg\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Mặt sau của máy thì cách sắp xếp camera sau theo hàng dọc\" /></a></p>\r\n\r\n<p>Sản phẩm được thiết kế&nbsp;<a href=\"https://www.thegioididong.com/dtdd-chong-nuoc-bui\" target=\"_blank\" title=\"Tham khảo điện thoại có kháng nước, chống bụi tại Thế Giới Di Động\">kh&aacute;ng nước, kh&aacute;ng bụi</a>&nbsp;ti&ecirc;u chuẩn&nbsp;<strong>IP68</strong>, giảm khả năng bị nước, bụi bẩn x&acirc;m nhập g&acirc;y ảnh hưởng đến tuổi thọ v&agrave; hoạt động của m&aacute;y, cho bạn tự tin mang theo điện thoại trong c&aacute;c h&agrave;nh tr&igrave;nh thường nhật cũng như khi đi phượt, c&ocirc;ng t&aacute;c xa nh&agrave;.&nbsp;</p>\r\n\r\n<h3>Trải nghiệm xem ch&acirc;n thật</h3>\r\n\r\n<p>Với k&iacute;ch thước&nbsp;<strong>6.4 inch</strong>, đường viền kh&aacute; mỏng, camera trước dạng đục lỗ cho kh&ocirc;ng gian hiển thị rộng r&atilde;i, bạn c&oacute; thể xem được nhiều chi tiết tr&ecirc;n m&agrave;n h&igrave;nh hơn.</p>\r\n\r\n<p>Hỗ trợ c&ocirc;ng nghệ m&agrave;n h&igrave;nh hiện đại&nbsp;<strong>Dynamic AMOLED 2X</strong>,&nbsp;độ ph&acirc;n giả<strong>i Full HD+</strong>,&nbsp;tần số qu&eacute;t&nbsp;<strong>120 Hz</strong>, cho h&igrave;nh ảnh t&aacute;i hiện chi tiết, mượt m&agrave;, m&agrave;u sắc tươi tắn, độ tương phản cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040742.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Trải nghiệm xem chân thật\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040742.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040742.jpg\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Trải nghiệm xem chân thật\" /></a></p>\r\n\r\n<p>C&oacute; độ s&aacute;ng tối đa&nbsp;<strong>1200 nits&nbsp;</strong>xem trong nh&agrave; v&agrave; ngo&agrave;i trời đều cho chất lượng h&igrave;nh ảnh tốt. Điện thoại t&iacute;ch hợp loa k&eacute;p stereo trong trẻo cho &acirc;m lượng ở mức kh&aacute;.&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, nằm b&ecirc;n dưới của m&agrave;n h&igrave;nh c&oacute; cảm biến v&acirc;n tay quang học cho ph&eacute;p mở kh&oacute;a bằng v&acirc;n tay nhanh ch&oacute;ng, bạn cũng c&oacute; thể chọn mở kh&oacute;a bằng khu&ocirc;n mặt với camera trước t&ugrave;y theo từng ho&agrave;n cảnh sử dụng.</p>\r\n\r\n<h3>Hiệu năng ổn định, chơi game mượt m&agrave;</h3>\r\n\r\n<p>Cung cấp sức mạnh cho S21 FE l&agrave; CPU&nbsp;<strong>Exynos 2100</strong>&nbsp;c&oacute; sức mạnh được đ&aacute;nh gi&aacute; l&agrave; tương đương Snapdragon 888 n&ecirc;n chắc chắn sẽ kh&ocirc;ng l&agrave;m thất vọng bạn khi n&oacute; đ&aacute;p ứng được từ c&aacute;c t&aacute;c vụ hằng ng&agrave;y như đọc b&aacute;o, xem phim đến g&otilde; chữ trong Word, Excel mượt m&agrave;. Kiểm tra hiệu năng với phần mềm&nbsp;PCMark (b&ecirc;n tr&aacute;i) cho 14.234 điểm,&nbsp;GeekBench 5 (b&ecirc;n phải) cho 745 điểm đơn nh&acirc;n v&agrave; 2.183 điểm đa nh&acirc;n.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040753.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Hiệu năng ổn định, chơi game mượt mà\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040753.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040753.jpg\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Hiệu năng ổn định, chơi game mượt mà\" /></a></p>\r\n\r\n<p>C&ograve;n&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-6gb\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại RAM 6 GB đang kinh doanh tại Thế Giới Di Động \">RAM 6 GB</a>&nbsp;cho c&aacute;c hoạt động đa nhiệm kh&aacute; tốt, phối hợp GPU Mali-G78 xử l&yacute; đồ họa tối ưu, chơi game Li&ecirc;n Qu&acirc;n với độ họa tối đa 60 FPS, tần số qu&eacute;t 120 Hz, combat kh&ocirc;ng lag, kh&ocirc;ng drop FPS khi chơi.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040747.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Game Liên Quân\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040747.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040747.jpg\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Game Liên Quân\" /></a></p>\r\n\r\n<p>Với game PUBG Mobile th&igrave; cho đồ họa sắc n&eacute;t, c&oacute; t&igrave;nh trạng drop FPS nhẹ khi bắn s&uacute;ng ở nơi đ&ocirc;ng người hoặc khi chết, tuy nhi&ecirc;n kh&ocirc;ng đ&aacute;ng kể.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040749.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Game PUBG Mobile\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040749.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040749.jpg\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Game PUBG Mobile\" /></a></p>\r\n\r\n<p>V&igrave; đều&nbsp;<strong>chơi ở mức đồ họa tối đa, tốc độ l&agrave;m mới 120 Hz</strong>&nbsp;n&ecirc;n m&aacute;y kh&aacute; n&oacute;ng khi chơi, n&ecirc;n game thủ cần hạ mức độ họa xuống ch&uacute;t để m&aacute;y m&aacute;t v&agrave; d&ugrave;ng tiết kiệm pin hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd-rom-128gb\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại ROM 128 GB đang kinh doanh tại Thế Giới Di Động \">Bộ nhớ trong&nbsp;128 GB</a>, kh&ocirc;ng gian lưu trữ tương đối rộng để bạn lưu lại được nhiều h&igrave;nh ảnh, t&agrave;i liệu hoặc ứng dụng m&agrave; bạn đang quan t&acirc;m tiện lợi. Hỗ trợ kết nối&nbsp;<strong>5G</strong>&nbsp;cho t&iacute;n hiệu ổn định, ph&aacute;t trực tuyến hoặc tải xuống c&aacute;c tệp tin dung lượng lớn nhanh ch&oacute;ng, độ trễ thấp.</p>\r\n\r\n<h3>Ghi lại h&igrave;nh ảnh sống động</h3>\r\n\r\n<p>Galaxy S21 FE 5G thiết kế cụm camera sau c&oacute; 3 ống k&iacute;nh, trong đ&oacute; c&oacute; camera ch&iacute;nh v&agrave; camera g&oacute;c si&ecirc;u rộng cho độ ph&acirc;n giải&nbsp;<strong>12 MP</strong>&nbsp;v&agrave; 1 camera tele&nbsp;<strong>8 MP</strong>&nbsp;chụp ảnh cho chất lượng rất &quot;ổn &aacute;p&quot; với c&aacute;c chi tiết r&otilde; r&agrave;ng, m&agrave;u sắc của bầu trời, c&acirc;y cối nịnh mắt.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040751.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Ghi lại hình ảnh sống động\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040751.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040751.jpg\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Ghi lại hình ảnh sống động\" /></a></p>\r\n\r\n<p>Chụp phong cảnh c&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại camera gốc rộng đang kinh doanh tại Thế Giới Di Động \">điện thoại c&oacute; camera g&oacute;c rộng</a>&nbsp;với trường nh&igrave;n l&ecirc;n đến&nbsp;<strong>123 độ,&nbsp;</strong>nhờ c&oacute; tầm bao qu&aacute;t rộng lớn gi&uacute;p cho tấm ảnh thu được nhiều chi tiết hơn, ảnh chụp kh&ocirc;ng bị m&eacute;o ở c&aacute;c g&oacute;c l&agrave;m cho tổng quan bức h&igrave;nh th&ecirc;m phần chất lượng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040744.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Chụp phong cảnh với camera góc siêu rộng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040744.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040744.jpg\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Chụp phong cảnh với camera góc siêu rộng\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\">Samsung</a>&nbsp;hỗ trợ cho m&aacute;y chế độ Night Mode cải thiện chất lượng chụp ảnh trong điều kiện thiếu s&aacute;ng. M&aacute;y bắt n&eacute;t kh&aacute; nhanh, chi tiết c&oacute; độ n&eacute;t ở mức chấp nhận được, m&agrave;u sắc t&aacute;i tạo ở t&ocirc;ng ấm, nếu c&oacute; vật thể di chuyển nhanh th&igrave; ch&uacute;ng c&oacute; thể hơi nh&ograve;e khi xuất hiện trong h&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040757.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Hỗ trợ chế độ Night Mode\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040757.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040757.jpg\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Hỗ trợ chế độ Night Mode\" /></a></p>\r\n\r\n<p>Camera trước c&oacute; độ ph&acirc;n giải cao&nbsp;<strong>32 MP</strong>, trang bị đa dạng chế độ chụp, l&agrave;m nổi bật chủ thể trong h&igrave;nh với chế độ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-xoa-phong\" target=\"_blank\" title=\"Tham khảo điện thoại có camera xóa phông tại Thế Giới Di Động\">chụp x&oacute;a ph&ocirc;ng</a>, cho bạn tỏa s&aacute;ng trong bức ảnh ch&acirc;n dung thu lại được.</p>\r\n\r\n<h3>Vi&ecirc;n pin&nbsp;4500&nbsp;mAh</h3>\r\n\r\n<p>Đ&acirc;y l&agrave; mức dung lượng tương đối ổn, khi Thế Giới Di Động kiểm tra với t&aacute;c vụ hỗn hợp c&oacute; chơi&nbsp;Li&ecirc;n Qu&acirc;n, xem YouTube, d&ugrave;ng&nbsp;Facebook, Messenger,&nbsp;Chrome,&nbsp;PUBG Mobile th&igrave; thời gian trải nghiệm rơi v&agrave;o tầm&nbsp;<strong>8 giờ</strong>&nbsp;trong điều kiện &acirc;m lượng, độ s&aacute;ng tối đa, d&ugrave;ng WIFI trong m&ocirc;i trường c&oacute; m&aacute;y lạnh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040755.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Viên pin 4500 mAh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040755.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/267211/samsung-galaxy-s21-fe-6gb-200122-040755.jpg\" title=\"Samsung Galaxy S21 FE 5G (6GB/128GB) - Viên pin 4500 mAh\" /></a></p>\r\n\r\n<p>Được hỗ trợ c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo điện thoại có sạc pin nhanh tại Thế Giới Di Động\">sạc pin nhanh</a><strong>&nbsp;25 W</strong>, nạp đầy từ 0 - 100% chỉ khoảng 1 tiếng 30 ph&uacute;t, thời gian sạc ph&ugrave; hợp, cho bạn kh&ocirc;ng cần phải chờ đợi qu&aacute; l&acirc;u. B&ecirc;n cạnh sạc nhanh, điện thoại c&ograve;n c&oacute; thể sạc kh&ocirc;ng d&acirc;y,&nbsp;sạc ngược kh&ocirc;ng d&acirc;y, cho bạn sạc linh hoạt hơn.&nbsp;</p>\r\n\r\n<p>Mức gi&aacute; hấp dẫn, t&iacute;nh năng xịn s&ograve; trong ph&acirc;n kh&uacute;c, mẫu Samsung Galaxy S21 FE 5G (6GB/128GB) của d&ograve;ng Galaxy S hứa hẹn sẽ mang đến những ph&uacute;t gi&acirc;y trải nghiệm đ&aacute;ng nhớ cho bạn.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(14, 'VHL#11', 'Samsung Galaxy S23 Ultra 5G 256GB', 23790000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung4.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s23-ultra\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S23 Ultra 5G 256GB tại Thế Giới Di Động\">Samsung Galaxy S23 Ultra 5G 256GB</a>&nbsp;l&agrave; chiếc smartphone cao cấp nhất của nh&agrave; Samsung, sở hữu cấu h&igrave;nh kh&ocirc;ng tưởng với con chip khủng được&nbsp;Qualcomm tối ưu ri&ecirc;ng cho d&ograve;ng Galaxy&nbsp;v&agrave; camera l&ecirc;n đến 200 MP, xứng danh l&agrave; chiếc flagship Android được mong đợi nhất trong năm 2023.</h3>\r\n\r\n<h3>Tạo h&igrave;nh sang trọng đầy tinh tế</h3>\r\n\r\n<p>Về thiết kế th&igrave; Samsung Galaxy S23 Ultra sẽ tiếp tục thừa hưởng kiểu d&aacute;ng sang trọng đến từ thế hệ trước, vẫn l&agrave; bộ khung kim loại, mặt lưng k&iacute;nh c&ugrave;ng kiểu tạo h&igrave;nh bo cong nhẹ ở cạnh b&ecirc;n v&agrave; m&agrave;n h&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020326.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế sang trọng - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020326.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020326.jpg\" title=\"Thiết kế sang trọng - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Tuy nhi&ecirc;n kiểu bo cong n&agrave;y sẽ hơi thi&ecirc;n hướng phẳng một ch&uacute;t so với S22 Ultra, điều n&agrave;y mang đến cho m&igrave;nh trải nghiệm cầm nắm chắc tay hơn, song cũng mang lại cảm gi&aacute;c dễ chịu cho những l&uacute;c sử dụng li&ecirc;n tục trong thời gian d&agrave;i.</p>\r\n\r\n<p>Về m&agrave;u sắc, năm nay Samsung cũng đ&atilde; cho ra c&aacute;c phi&ecirc;n bản m&agrave;u như: T&iacute;m, kem, xanh v&agrave; đen. Nh&igrave;n chung th&igrave; đ&acirc;y l&agrave; những m&agrave;u sắc cực kỳ sang trọng v&agrave; lịch l&atilde;m, ph&ugrave; hợp cho c&aacute;c bạn trẻ năng động, mạnh mẽ v&agrave; đặc biệt l&agrave; những kh&aacute;ch h&agrave;ng đang l&agrave; doanh nh&acirc;n bởi ngoại h&igrave;nh đẳng cấp v&agrave; thanh lịch.</p>\r\n\r\n<p>Hiện tr&ecirc;n tay m&igrave;nh l&agrave; bản m&agrave;u xanh đặc trưng của Samsung, m&agrave;u n&agrave;y vừa đem đến sự trẻ trung tươi mới v&agrave; cũng vừa mang tr&ecirc;n m&igrave;nh một gam m&agrave;u tối để c&oacute; thể giữ được vẻ huyền b&iacute; đầy m&ecirc; hoặc.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020336.jpg\" onclick=\"return false;\"><img alt=\"Màu sắc mê hoặc - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020336.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020336.jpg\" title=\"Màu sắc mê hoặc - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Ở mặt lưng ta c&oacute; thể thấy Samsung trang bị cho m&aacute;y một lớp k&iacute;nh cường lực c&oacute; khả năng chống va đập tốt, được l&agrave;m theo kiểu nh&aacute;m nhẹ gi&uacute;p cho việc b&aacute;m dấu v&acirc;n tay gần như kh&ocirc;ng thấy xuất hiện trong qu&aacute; tr&igrave;nh m&igrave;nh trải nghiệm.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020340.jpg\" onclick=\"return false;\"><img alt=\"Mặt lưng kính nhám - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020340.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020340.jpg\" title=\"Mặt lưng kính nhám - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Về phần thiết kế camera th&igrave; năm nay h&atilde;ng gần như kh&ocirc;ng c&oacute; sự thay đổi g&igrave; so với Galaxy S22 Ultra, vẫn l&agrave; cụm camera được bố tr&iacute; ri&ecirc;ng lẻ v&agrave; được l&agrave;m nh&ocirc; l&ecirc;n kh&aacute; cao.</p>\r\n\r\n<p>Tuy nhi&ecirc;n xung quanh c&aacute;c ống k&iacute;nh sẽ được th&ecirc;m một lớp viền cao hơn so với bề mặt camera, điều n&agrave;y gi&uacute;p hạn chế việc trầy xước bề mặt ống k&iacute;nh rất tốt n&ecirc;n m&igrave;nh cũng y&ecirc;n t&acirc;m hơn trong l&uacute;c d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020347.jpg\" onclick=\"return false;\"><img alt=\"Vị trí camera - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020347.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020347.jpg\" title=\"Vị trí camera - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>C&ograve;n về viền xung quanh m&aacute;y, Galaxy S23 Ultra được bao bọc bởi bộ khung l&agrave;m từ nh&ocirc;m với đặc t&iacute;nh bền bỉ, chắc chắn c&ugrave;ng trọng lượng được tối ưu cực kỳ hiệu quả.</p>\r\n\r\n<p>Bộ khung n&agrave;y được l&agrave;m theo kiểu b&oacute;ng lo&aacute;ng gi&uacute;p m&aacute;y c&oacute; th&ecirc;m một điểm nhấn đầy nổi bật về thiết kế. Cảm gi&aacute;c khi sờ v&agrave;o bộ khung như được phủ một lớp mạ b&oacute;ng xung quanh, v&igrave; thế đ&acirc;y sẽ kh&ocirc;ng phải l&agrave; vị tr&iacute; dễ xước như một bộ phận người người d&ugrave;ng ho&agrave;i nghi.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020353.jpg\" onclick=\"return false;\"><img alt=\"Khung viền bo cong - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020353.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020353.jpg\" title=\"Khung viền bo cong - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<h3>Hiệu năng bức ph&aacute; mọi giới hạn</h3>\r\n\r\n<p>Tại thời điểm ra mắt sản phẩm, Galaxy S23 Ultra c&oacute; thể xem l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Tham khảo điện thoại Android đang kinh doanh tại Thế Giới Di Động\">điện thoại Android</a>&nbsp;sở hữu hiệu năng mạnh mẽ bậc nhất thị trường. Điều n&agrave;y l&agrave; do Samsung đ&atilde; trang bị con chip Snapdragon 8 Gen 2 t&acirc;n tiến được nh&agrave; Qualcomm tối ưu&nbsp;ri&ecirc;ng biệt cho d&ograve;ng sản phẩm Galaxy S series, mang lại hiệu năng vượt trội&nbsp;so với Snapdragon 8 Gen 2 th&ocirc;ng thường.</p>\r\n\r\n<p>Trước ti&ecirc;n l&agrave; về phần trải nghiệm cơ bản với những t&aacute;c vụ như: Xem phim, lướt web, chụp ảnh hay nghe gọi nhắn tin th&igrave; dường như m&aacute;y kh&ocirc;ng gặp phải bất kỳ kh&oacute; khăn n&agrave;o cả, mọi thứ đều được diễn ra su&ocirc;n sẻ v&agrave; hết sức mượt m&agrave;.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020358.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mượt mà - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020358.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020358.jpg\" title=\"Hiệu năng mượt mà - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Để c&oacute; thể đ&aacute;nh gi&aacute; cấu h&igrave;nh của điện thoại một c&aacute;ch trực quan hơn bằng những con số, vậy n&ecirc;n m&igrave;nh cũng đ&atilde; test qua một v&agrave;i phần mềm kiểm tra hiệu năng với kết quả đạt được như sau: 1767 (đơn nh&acirc;n), 4314 (đa nh&acirc;n) tr&ecirc;n Geekbench 6 v&agrave; 15635 điểm tr&ecirc;n PCMark.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020403.jpg\" onclick=\"return false;\"><img alt=\"Điểm đánh giá hiệu năng - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020403.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020403.jpg\" title=\"Điểm đánh giá hiệu năng - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Ở thời điểm hiện tại (04/2023), đ&acirc;y quả thực l&agrave; những con số rất cao v&agrave; c&oacute; &iacute;t d&ograve;ng m&aacute;y n&agrave;o sở hữu được mức điểm lớn như vậy. Nhờ v&agrave;o đ&oacute; m&agrave; Galaxy S23 Ultra tự tin c&oacute; thể xử l&yacute; tốt bất kỳ t&aacute;c vụ n&agrave;o v&agrave;o thời điểm n&agrave;y, hay thậm ch&iacute; l&agrave; mang đến trải nghiệm sử dụng mượt m&agrave; cho tới 2 - 3 năm hoặc l&acirc;u hơn t&ugrave;y v&agrave;o mục đ&iacute;ch sử dụng.</p>\r\n\r\n<p>Đến với phần trải nghiệm chơi game th&igrave; Snapdragon 8 Gen 2 for Galaxy dường như đ&atilde; c&oacute; cơ hội bộc lộ gần hết sức mạnh của chip, điều n&agrave;y được minh chứng trong l&uacute;c m&igrave;nh chơi qua c&aacute;c tựa game như Li&ecirc;n Qu&acirc;n Mobile v&agrave; Genshin Impact ở mức đồ họa cao nhất.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020407.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020407.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020407.jpg\" title=\"Trải nghiệm chơi game - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Ở Li&ecirc;n Qu&acirc;n Mobile, m&aacute;y cho ph&eacute;p c&agrave;i đặt mức đồ họa tối đa th&ocirc;ng qua c&aacute;c mức độ như sau: Độ ph&acirc;n giải si&ecirc;u cao, chất lượng h&igrave;nh ảnh HD v&agrave; chất lượng chi tiết cao. Điều n&agrave;y g&oacute;p phần l&agrave;m tăng trải nghiệm chơi game của m&igrave;nh khi mọi nội dung đều được hiển thị tốt nhất.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020412.jpg\" onclick=\"return false;\"><img alt=\"Bảng cài dặt cấu hình - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020412.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020412.jpg\" title=\"Bảng cài dặt cấu hình - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Sau khoảng 2 v&aacute;n game li&ecirc;n tục tr&ecirc;n Galaxy S23 Ultra th&igrave; m&igrave;nh thấy dường như m&aacute;y kh&ocirc;ng gặp phải bất kỳ hiện tượng giật lag hay chậm đơ, kể cả những l&uacute;c giao tranh tổng, khi m&agrave; mọi hiệu ứng h&igrave;nh ảnh từ mọi tướng đều được hiển thị gần như c&ugrave;ng l&uacute;c.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020646.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020646.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020646.jpg\" title=\"Trải nghiệm chơi game - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Biểu đồ ghi nhận tốc độ khung h&igrave;nh khi chơi Li&ecirc;n Qu&acirc;n ở l&uacute;c n&agrave;y thể hiện mọi thứ diễn ra rất ổn, kh&ocirc;ng c&oacute; sự biến động nhiều hay bị drop qu&aacute; s&acirc;u để g&acirc;y ra giật lag, FPS lu&ocirc;n được dao động trong khoảng 59 - 64.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020642.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020642.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020642.jpg\" title=\"Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Tiếp đến l&agrave; tựa game Genshin Impact c&oacute; hơi kh&oacute; khăn hơn đ&ocirc;i ch&uacute;t, mức cấu h&igrave;nh l&uacute;c n&agrave;y m&igrave;nh c&agrave;i đặt cho m&aacute;y l&agrave; gần như cao nhất. Đ&acirc;y c&oacute; thể xem l&agrave; mức đồ họa kh&ocirc;ng tưởng, chỉ c&oacute; thể c&agrave;i đặt v&agrave; hoạt động tốt tr&ecirc;n những d&ograve;ng m&aacute;y cao cấp như Galaxy S23 Ultra.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020653.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020653.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020653.jpg\" title=\"Trải nghiệm chơi game - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Th&ocirc;ng qua trải nghiệm thực tế th&igrave; m&igrave;nh thấy Galaxy S23 Ultra xử l&yacute; tựa game n&agrave;y rất tốt, tuy chỉ c&oacute; xuất hiện một ch&uacute;t khựng nhẹ trong l&uacute;c load bản đồ ở l&uacute;c đầu, nhưng n&oacute; kh&ocirc;ng phải l&agrave; điều g&igrave; qu&aacute; to t&aacute;c ảnh hưởng trải nghiệm của m&igrave;nh.</p>\r\n\r\n<p>Mọi thứ l&uacute;c n&agrave;y được Galaxy S23 Ultra xử l&yacute; ổn, tốc độ khung h&igrave;nh lu&ocirc;n được dao động ổn định trong tầm 27 - 31 FPS, m&igrave;nh cảm thấy h&igrave;nh ảnh hiển thị rất mượt mắt, mọi thao t&aacute;c điều hướng nh&acirc;n vật cũng được phản hồi nhanh ch&oacute;ng v&agrave; gần như kh&ocirc;ng thấy xuất hiện độ trễ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020659.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020659.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020659.jpg\" title=\"Trải nghiệm chơi game - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Về vấn đề sinh nhiệt khi sử dụng&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại di động đang kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;ở c&aacute;c t&aacute;c vụ n&acirc;ng cao th&igrave; Galaxy S23 Ultra dường như đ&atilde; tối ưu phần n&agrave;y rất tốt, trong l&uacute;c m&igrave;nh chơi game ở cấu h&igrave;nh cao li&ecirc;n tục trong 2 tiếng, c&oacute; kết nối wifi, độ s&aacute;ng m&agrave;n h&igrave;nh trung b&igrave;nh v&agrave; thực hiện trong m&ocirc;i trường c&oacute; nhiệt độ 26 - 27 độ th&igrave; m&aacute;y vẫn cho ra mức nhiệt thấp, dường như chỉ dừng lại ở mức ấm tay nhẹ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020704.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020704.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020704.jpg\" title=\"Trải nghiệm chơi game - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>L&agrave; một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\" title=\"Tham khảo điện thoại RAM 8GB đang kinh doanh tại Thế Giới Di Động\">điện thoại RAM 8 GB</a>, Galaxy S23 Ultra ho&agrave;n to&agrave;n c&oacute; thể đủ sức xử l&yacute; t&aacute;c vụ đa nhiệm một c&aacute;ch hiệu quả v&agrave; nhanh ch&oacute;ng, với khoảng 15 - 18 ứng dụng (ngoại trừ game) được mở c&ugrave;ng l&uacute;c th&igrave; sau tầm 20 ph&uacute;t mở lại, m&aacute;y vẫn kh&ocirc;ng hề xuất hiện t&igrave;nh trạng tải lại ứng dụng từ đầu v&agrave; đ&acirc;y cũng l&agrave; điều m&igrave;nh hết sức ngạc nhi&ecirc;n khi sử dụng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020709.jpg\" onclick=\"return false;\"><img alt=\"Đa nhiệm mượt mà - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020709.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020709.jpg\" title=\"Đa nhiệm mượt mà - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<h3>Trang bị camera 200 MP v&agrave; khả năng quay video 8K</h3>\r\n\r\n<p>Về hệ thống ống k&iacute;nh, Galaxy S23 Ultra được trang bị 3 camera ở mặt sau với th&ocirc;ng số lần lượt l&agrave;: 200 MP cho camera ch&iacute;nh, một camera 12 MP g&oacute;c si&ecirc;u rộng c&ugrave;ng hai camera tele c&oacute; chung độ ph&acirc;n giải 10 MP để hỗ trợ chụp ảnh zoom quang học v&agrave; x&oacute;a ph&ocirc;ng.</p>\r\n\r\n<p>Đầu ti&ecirc;n th&igrave; sẽ l&agrave; chế độ chụp mặc định ở điều kiện ngoại cảnh, với m&ocirc;i trường c&oacute; đầy đủ &aacute;nh s&aacute;ng th&igrave; kết quả thu lại l&agrave;m cho m&igrave;nh rất ưng, mọi thứ đều thể hiện chi tiết, m&agrave;u sắc được n&acirc;ng tone nhẹ gi&uacute;p bức h&igrave;nh tr&ocirc;ng tươi mới v&agrave; đầy sức sống hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020721.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020721.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020721.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Với kết quả cho ra th&igrave; m&igrave;nh ho&agrave;n to&agrave;n c&oacute; thể sử dụng ngay để đăng tải l&ecirc;n mạng x&atilde; hội m&agrave; kh&ocirc;ng cần th&ocirc;ng qua nhiều bước hậu kỳ, ngo&agrave;i ra bạn cũng c&oacute; thể sử dụng th&ecirc;m nhiều t&ugrave;y chọn bộ lọc m&agrave;u kh&aacute;c để t&igrave;m ra gam m&agrave;u ưng &yacute; nhất cho khung cảnh hay chủ đề bạn hướng tới.</p>\r\n\r\n<p>Tiếp đến sẽ l&agrave; chế độ chụp g&oacute;c si&ecirc;u rộng, ở phần t&iacute;nh năng n&agrave;y th&igrave; m&aacute;y cho ph&eacute;p m&igrave;nh c&oacute; thể thu lại c&aacute;c bức ảnh c&oacute; độ bao qu&aacute;t lớn, kết quả chụp được gần như thu lại to&agrave;n bộ những g&igrave; xuất hiện trước mắt m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020727.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020727.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020727.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Ở những vị tr&iacute; xung quanh viền mở rộng th&igrave; sẽ kh&ocirc;ng bị b&oacute;p m&eacute;o, độ chi tiết cũng được đảm bảo gi&uacute;p cho bức ảnh tr&ocirc;ng ch&acirc;n thực hơn, nghệ thuật hơn n&ecirc;n m&igrave;nh cảm thấy rất ưng &yacute;.</p>\r\n\r\n<p>Từ trước đến nay th&igrave; c&aacute;c mẫu điện thoại flagship của Samsung lu&ocirc;n được đ&aacute;nh gi&aacute; l&agrave; những thiết bị c&oacute; khả năng zoom quang học trong chụp ảnh cực kỳ tốt, v&agrave; điều n&agrave;y cũng sẽ lặp lại một lần nữa tr&ecirc;n Galaxy S23 Ultra khi m&aacute;y hỗ trợ tới 2 cảm biến tele để hỗ trợ zoom quang học 10X v&agrave; 3X.</p>\r\n\r\n<p>Ở những bức ảnh chụp zoom, m&igrave;nh thực sự kh&ocirc;ng cảm thấy c&oacute; sự thay đổi qu&aacute; nhiều về độ chi tiết giữa bức ảnh chụp bằng camera tele so với camera ch&iacute;nh, mọi thứ đều hết sức ch&acirc;n thực v&agrave; r&otilde; n&eacute;t, tổng thể bức ảnh tr&ocirc;ng h&agrave;i h&ograve;a từ m&agrave;u sắc cho tới &aacute;nh s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020735.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020735.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-020735.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p><em>*Ảnh chụp từ chế độ g&oacute;c si&ecirc;u rộng</em></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021023.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021023.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021023.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p><em>*Ảnh chụp từ chế độ mặc định</em></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021030.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021030.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021030.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p><em>*Ảnh chụp từ chế độ zoom 3X</em></p>\r\n\r\n<p>C&oacute; một điểm m&agrave; m&igrave;nh cảm thấy rất ấn tượng trong l&uacute;c chụp zoom tr&ecirc;n Galaxy S23 Ultra l&agrave; về khả năng lấy n&eacute;t tốc độ chụp. C&oacute; thể c&aacute;c bạn cũng đ&atilde; biết khi ch&uacute;ng ta chụp zoom th&igrave; camera của m&aacute;y rất dễ bị lấy n&eacute;t chậm, rung lắc hay chụp nh&ograve;e, thế nhưng tr&ecirc;n chiếc điện thoại S23 Ultra n&agrave;y th&igrave; điều n&agrave;y cũng đ&atilde; được khắc phục gần như ho&agrave;n to&agrave;n.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021035.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021035.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021035.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p><em>*Ảnh chụp từ chế độ zoom 10X</em></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021042.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021042.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021042.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p><em>*Ảnh chụp từ chế độ zoom 10X</em></p>\r\n\r\n<p>Với một người thường xuy&ecirc;n chụp ảnh ngoại cảnh như m&igrave;nh th&igrave; m&igrave;nh đ&aacute;nh gi&aacute; khả năng xử l&yacute; &aacute;nh s&aacute;ng tr&ecirc;n S23 Ultra hoạt động cực kỳ hiệu quả, kể cả khi chụp đối diện với mặt trời l&uacute;c nắng to th&igrave; m&aacute;y vẫn c&oacute; thể c&acirc;n bằng tốt, sự tương quan giữa chủ thể v&agrave; v&ugrave;ng s&aacute;ng ph&iacute;a sau đều được c&acirc;n bằng h&agrave;i h&ograve;a để cho bức h&igrave;nh trở n&ecirc;n v&ocirc; c&ugrave;ng đẹp mắt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021048.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021048.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021048.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Một điểm th&uacute; vị tiếp theo m&agrave; m&igrave;nh muốn chia sẻ về camera của S23 Ultra l&agrave; nằm ở phần xử l&yacute; m&agrave;u sắc, như th&ocirc;ng lệ tr&ecirc;n c&aacute;c mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung đang kinh doanh tại Thế Giới Di Động\">điện thoại Samsung</a>&nbsp;th&igrave; những bức ảnh chụp được sẽ c&oacute; xu hướng n&acirc;ng tone m&agrave;u l&ecirc;n rực rỡ hơn đ&ocirc;i ch&uacute;t để bức ảnh trở n&ecirc;n sinh động hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021053.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021053.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021053.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021059.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021059.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021059.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Tuy nhi&ecirc;n th&igrave; đ&acirc;y cũng c&oacute; thể xem l&agrave; một điểm hạn chế cho một v&agrave;i t&igrave;nh huống cụ thể. Thực thế th&igrave; S23 Ultra xử l&yacute; tốt ở phần lớn c&aacute;c m&agrave;u, tuy nhi&ecirc;n th&igrave; ở những gam m&agrave;u n&oacute;ng th&igrave; m&aacute;y xử l&yacute; hơi qu&aacute; đ&agrave; một t&yacute;, n&ecirc;n thường m&agrave;u sắc l&uacute;c n&agrave;y sẽ kh&ocirc;ng c&ograve;n được ch&acirc;n thực lắm.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021107.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021107.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021107.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Chuyển đến điều kiện buổi đ&ecirc;m, Galaxy S23 Ultra đ&atilde; dường như thể hiện được sức mạnh của camera v&agrave; chứng minh được l&yacute; do tại sao gọi đ&acirc;y l&agrave; &ldquo;Mắt thần b&oacute;ng đ&ecirc;m&rdquo;.</p>\r\n\r\n<p>Thực sự m&agrave; n&oacute;i th&igrave; khả năng thu s&aacute;ng v&agrave; xử l&yacute; h&igrave;nh ảnh tr&ecirc;n chiếc m&aacute;y n&agrave;y l&agrave; cực kỳ tốt, c&aacute;c nguồn s&aacute;ng trong khi chụp đ&ecirc;m sẽ kh&ocirc;ng bị nh&ograve;e hay ch&oacute;i, hiện tượng nhiễu hạt th&igrave; cũng được cải thiện so với thế hệ trước đ&oacute; gi&uacute;p cho bức ảnh th&ecirc;m phần h&agrave;i h&ograve;a v&agrave; nịnh mắt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021113.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021113.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021113.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Một điểm ấn tượng kh&ocirc;ng k&eacute;m l&agrave; ở phần t&iacute;nh năng quay video tr&ecirc;n m&aacute;y với chuẩn quay tối đa l&ecirc;n đến 8K ở tốc độ khung h&igrave;nh 30 fps, điều n&agrave;y sẽ mang đến cho ch&uacute;ng ta những thước phim vươn tầm điện ảnh khi độ ph&acirc;n giải thu được l&uacute;c n&agrave;y l&agrave; cực kỳ lớn.</p>\r\n\r\n<h3>T&iacute;ch hợp b&uacute;t S-Pen tiện dụng</h3>\r\n\r\n<p>Tương tự như mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-s\" target=\"_blank\" title=\"Tham khảo điện thoại Galaxy dòng S đang kinh doanh tại Thế Giới Di Động\">điện thoại Galaxy d&ograve;ng S</a>&nbsp;trước đ&oacute;, S23 Ultra cũng sẽ được tiếp tục trang bị b&uacute;t S-Pen huyền thoại ngay trong th&acirc;n m&aacute;y, đ&acirc;y c&oacute; thể coi l&agrave; một điều m&agrave; những kh&aacute;ch h&agrave;ng thuộc đối tượng người d&ugrave;ng l&agrave; doanh nh&acirc;n hay người l&agrave;m s&aacute;ng tạo cực kỳ y&ecirc;u th&iacute;ch bởi độ tiện dụng.</p>\r\n\r\n<p>Về cảm nhận nhấn để lấy b&uacute;t th&igrave; m&igrave;nh thấy kh&aacute; dễ d&agrave;ng, chỉ cần chạm nhẹ v&agrave;o v&ugrave;ng chứa b&uacute;t th&igrave; phụ kiện n&agrave;y sẽ ngay lập tức đẩy ra để m&igrave;nh c&oacute; thể r&uacute;t một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021118.jpg\" onclick=\"return false;\"><img alt=\"Tích hợp bút S-Pen - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021118.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021118.jpg\" title=\"Tích hợp bút S-Pen - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Với một người l&agrave;m c&aacute;c c&ocirc;ng việc s&aacute;ng tạo nội dung như m&igrave;nh, b&uacute;t S-Pen sẽ rất ph&ugrave; hợp để m&igrave;nh thực hiện c&aacute;c c&ocirc;ng việc như ph&aacute;c thảo nội dung bản vẽ, tạo chữ k&yacute;, ghi ch&uacute; nhanh hay thậm ch&iacute; l&agrave; c&oacute; thể d&ugrave;ng n&oacute; thực hiện t&aacute;c vụ chỉnh sửa video, với đầu ng&ograve;i nhỏ sẽ gi&uacute;p m&igrave;nh c&oacute; thể thao t&aacute;c chỉnh sửa v&agrave; cắt gh&eacute;p video ch&iacute;nh x&aacute;c hơn.</p>\r\n\r\n<p>Về độ trễ của b&uacute;t ra sao th&igrave; m&igrave;nh dường như kh&ocirc;ng cảm nhận được trong qu&aacute; tr&igrave;nh sử dụng, mọi thứ đều được phản hồi nhanh ch&oacute;ng v&agrave; gần như khớp tuyệt đối với c&aacute;c thao t&aacute;c của m&igrave;nh (khi ở tần số qu&eacute;t m&agrave;n h&igrave;nh 120 Hz), v&igrave; thế m&igrave;nh cũng cảm thấy hết sức thoải m&aacute;i khi sử dụng b&uacute;t S-Pen v&agrave; n&oacute; dường như trở th&agrave;nh một phần kh&ocirc;ng thể thiếu trong l&uacute;c m&igrave;nh l&agrave;m việc.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021445.jpg\" onclick=\"return false;\"><img alt=\"Tích hợp bút S-Pen - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021445.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021445.jpg\" title=\"Tích hợp bút S-Pen - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh lớn mang đến kh&ocirc;ng gian bất tận</h3>\r\n\r\n<p>Với chiếc Galaxy S23 Ultra th&igrave; nh&agrave; Samsung t&iacute;ch hợp cho chiếc m&aacute;y n&agrave;y kh&aacute; nhiều th&ocirc;ng số nổi bật, cụ thể l&agrave; c&ocirc;ng nghệ m&agrave;n h&igrave;nh Dynamic AMOLED 2X, độ ph&acirc;n giải 2K+ (1440 x 3088 Pixels) c&ugrave;ng độ s&aacute;ng tối đa 1750 nits.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-man-hinh-dynamic-amoled-2x-tren-smartphone-1245213\" target=\"_blank\">M&agrave;n h&igrave;nh Dynamic AMOLED 2X l&agrave; g&igrave;? Điểm nổi bật? C&oacute; tr&ecirc;n điện thoại n&agrave;o</a></p>\r\n\r\n<p>Cả ba điều n&agrave;y đ&ecirc;m đến chất lượng hiển thị với m&agrave;u sắc rực rỡ, nội dung cũng được t&aacute;i hiện sắc n&eacute;t hơn khi m&igrave;nh chơi game hay xem phim. Hơn hết, độ s&aacute;ng cao cũng sẽ gi&uacute;p &iacute;ch cho m&igrave;nh trong việc xem bản đồ ở ngo&agrave;i đường hay căn g&oacute;c chụp ảnh khi đang sử dụng ở những m&ocirc;i trường c&oacute; &aacute;nh s&aacute;ng cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021459.jpg\" onclick=\"return false;\"><img alt=\"Độ sáng màn hình cao - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021459.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021459.jpg\" title=\"Độ sáng màn hình cao - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>Th&uacute; vị hơn l&agrave; độ ph&acirc;n giải cao tr&ecirc;n S23 Ultra cho ph&eacute;p m&igrave;nh được c&agrave;i đặt được chuẩn độ ph&acirc;n giải lớn hơn khi xem những nội dung, gi&uacute;p tận hưởng được trọn vẹn vẻ đẹp từ khung h&igrave;nh một c&aacute;ch sinh động nhất.</p>\r\n\r\n<p>Nhờ được sử dụng m&agrave;n h&igrave;nh lớn 6.8 inch thế n&ecirc;n mọi thước phim m&igrave;nh xem đều được tr&igrave;nh chiếu với k&iacute;ch thước lớn hơn, kh&ocirc;ng gian l&uacute;c n&agrave;y cũng đ&atilde; trở n&ecirc;n rộng r&atilde;i để mọi cử chỉ, thao t&aacute;c bằng b&uacute;t S-Pen của m&igrave;nh trở n&ecirc;n thoải m&aacute;i.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021504.jpg\" onclick=\"return false;\"><img alt=\"Không gian hiển thị rõ ràng - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021504.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021504.jpg\" title=\"Không gian hiển thị rõ ràng - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<h3>K&eacute;o d&agrave;i thời lượng d&ugrave;ng với vi&ecirc;n pin lớn</h3>\r\n\r\n<p>Với một vi&ecirc;n pin c&oacute; dung lượng 5000 mAh mang lại cho m&igrave;nh thời gian d&ugrave;ng khoảng 9 tiếng 28 ph&uacute;t* cho c&aacute;c t&aacute;c vụ như: Chơi game, lướt Facebook, xem YouTube,... nhưng nếu sử dụng cho c&aacute;c t&aacute;c vụ cơ bản h&agrave;ng ng&agrave;y như: Nhắn tin, nghe gọi hay lướt web cơ bản th&igrave; m&aacute;y c&oacute; thể đ&aacute;p ứng cho m&igrave;nh cả một ng&agrave;y d&agrave;i sử dụng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021509.jpg\" onclick=\"return false;\"><img alt=\"Pin lớn dùng lâu - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021509.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021509.jpg\" title=\"Pin lớn dùng lâu - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, h&atilde;ng c&ograve;n hỗ trợ t&iacute;ch hợp th&ecirc;m c&ocirc;ng nghệ sạc nhanh với c&ocirc;ng suất tối đa l&ecirc;n đến 45 W. Nhờ đ&oacute;, m&igrave;nh chỉ mất khoảng 1 tiếng 27 ph&uacute;t* để sạc đầy pin m&agrave; kh&ocirc;ng cần phải chờ đợi qu&aacute; l&acirc;u.</p>\r\n\r\n<p>Điều n&agrave;y rất tiện lợi, n&oacute; gi&uacute;p m&igrave;nh c&oacute; thể sạc đầy pin trong những l&uacute;c d&agrave;nh thời gian ăn uống hoặc nghỉ ngơi một ch&uacute;t, thay v&igrave; phải chờ đợi cho tới v&agrave;i giờ đồng hồ rất mất thời gian.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021513.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ sạc siêu nhanh - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021513.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-150423-021513.jpg\" title=\"Hỗ trợ sạc siêu nhanh - Samsung Galaxy S23 Ultra 5G 256GB\" /></a></p>\r\n\r\n<p><i>*Thời gian sử dụng/sạc c&oacute; thể thay đổi t&ugrave;y v&agrave;o t&aacute;c vụ v&agrave; c&ocirc;ng suất của thiết bị sạc</i></p>\r\n\r\n<p>Samsung Galaxy S23 Ultra l&agrave; một mẫu smartphone được thiết kế tập trung chủ yếu d&agrave;nh cho đối tượng kh&aacute;ch h&agrave;ng l&agrave; doanh nh&acirc;n, những ai y&ecirc;u th&iacute;ch thiết kế sang trọng hay t&igrave;m mua một thiết bị c&oacute; hiệu năng mạnh. Với những g&igrave; m&agrave; m&aacute;y mang lại th&igrave; đ&acirc;y ho&agrave;n to&agrave;n l&agrave; thiết bị xứng đ&aacute;ng với gi&aacute; tiền để bạn đầu tư trong năm 2023.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 0),
(15, 'VHL#12', 'Samsung Galaxy A34 5G 128GB', 7390000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung5.jpg', '<h3>Được giới thiệu v&agrave;o th&aacute;ng 03/2023,&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a34-5g-128gb\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy A34 5G 128GB đang kinh doanh tại Thế Giới Di Động\">Samsung Galaxy A34 5G 128GB</a>&nbsp;l&agrave; một lựa chọn đ&aacute;ng ch&uacute; &yacute; trong thị trường&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Tham khảo điện thoại Android tại Thế Giới Di Động\">điện thoại Android</a>&nbsp;tầm trung. Với kết nối 5G ti&ecirc;n tiến v&agrave; hiệu suất đ&aacute;ng tin cậy, sản phẩm hứa hẹn mang lại trải nghiệm mượt m&agrave; v&agrave; th&uacute; vị cho người d&ugrave;ng.</h3>\r\n\r\n<h3>Tạo h&igrave;nh sang trọng đầy hiện đại</h3>\r\n\r\n<p>Với thiết kế đẹp v&agrave; hiện đại, Galaxy A34 5G c&oacute; mặt lưng nhẵn l&agrave;m từ nhựa cao cấp v&agrave; cụm camera được bố tr&iacute; đối xứng theo một h&agrave;ng dọc đầy bắt mắt. Nhờ sử dụng vật liệu nhựa gi&uacute;p m&aacute;y trở n&ecirc;n nhẹ hơn v&agrave; cảm thấy thoải m&aacute;i khi cầm nắm.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-thiet-ke-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế trẻ trung năng động - Samsung Galaxy A34 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-thiet-ke-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-thiet-ke-1.jpg\" title=\"Thiết kế trẻ trung năng động - Samsung Galaxy A34 5G\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh của Galaxy A34 5G c&oacute; thiết kế giọt nước tinh tế v&agrave; viền m&agrave;n h&igrave;nh được tối ưu h&oacute;a, tạo ra cảm gi&aacute;c thoải m&aacute;i khi nh&igrave;n v&agrave; dễ d&agrave;ng sử dụng.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh hiển thị tươi mới, rực rỡ</h3>\r\n\r\n<p>Galaxy A34 5G trang bị m&agrave;n h&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-super-amoled-la-gi-905770\" target=\"_blank\" title=\"Tham khảo màn hình Super AMOLED tại Thegioididong.com\">Super AMOLED</a>&nbsp;k&iacute;ch thước 6.6 inch với thiết kế notch dạng chữ U, đi k&egrave;m với độ ph&acirc;n giải Full HD+ gi&uacute;p hiển thị h&igrave;nh ảnh sắc n&eacute;t, ch&acirc;n thực v&agrave; m&agrave;u sắc trung thực.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-5g-2.jpg\" onclick=\"return false;\"><img alt=\"Màn hình kích thước lớn - Samsung Galaxy A34 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-5g-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-5g-2.jpg\" title=\"Màn hình kích thước lớn - Samsung Galaxy A34 5G\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, tần số qu&eacute;t 120 Hz gi&uacute;p c&aacute;c thao t&aacute;c chạm vuốt tr&ecirc;n m&agrave;n h&igrave;nh được thực hiện một c&aacute;ch mượt m&agrave; v&agrave; phản hồi nhanh hơn, mang đến trải nghiệm thị gi&aacute;c tuyệt vời cho người d&ugrave;ng.</p>\r\n\r\n<h3>Hiệu năng đ&aacute;ng gờm trong ph&acirc;n kh&uacute;c</h3>\r\n\r\n<p>Galaxy A34 5G được trang bị bộ vi xử l&yacute; MediaTek Dimensity 1080, chip c&oacute; thể hoạt động l&ecirc;n đến tốc độ tối đa 2.6 GHz, điều n&agrave;y tạo n&ecirc;n một hiệu năng mạnh mẽ gi&uacute;p thực hiện c&aacute;c t&aacute;c vụ hằng ng&agrave;y một c&aacute;ch mượt m&agrave; v&agrave; nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-5g-3.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ - Samsung Galaxy A34 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-5g-3.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-5g-3.jpg\" title=\"Hiệu năng mạnh mẽ - Samsung Galaxy A34 5G\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng chỉ thế, thiết bị n&agrave;y c&ograve;n l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\" title=\"Tham khảo điện thoại RAM 8GB tại Thegioididong.com\">điện thoại RAM 8 GB</a>&nbsp;với khả năng đa nhiệm tối ưu, cho ph&eacute;p bạn mở đồng thời nhiều ứng dụng m&agrave; kh&ocirc;ng gặp t&igrave;nh trạng chậm lại hay phải tải lại từ đầu sau khi đa nhiệm ứng dụng một thời gian d&agrave;i.</p>\r\n\r\n<h3>Chụp lại mọi khung cảnh được sắc n&eacute;t</h3>\r\n\r\n<p>Ở Galaxy A34 5G, bạn sẽ t&igrave;m thấy ba camera ở mặt sau, bao gồm camera ch&iacute;nh 48 MP, camera si&ecirc;u rộng 8 MP v&agrave; camera macro 5 MP. Nhờ sự kết hợp của ch&uacute;ng, chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung kinh doanh tại Thegioididong.com\">điện thoại Samsung</a>&nbsp;n&agrave;y mang đến nhiều chế độ chụp ảnh đầy th&uacute; vị, mỗi bức ảnh chụp từ m&aacute;y đều sắc n&eacute;t v&agrave; tươi s&aacute;ng với độ chi tiết tốt.</p>\r\n\r\n<p>Ngo&agrave;i ra, ở mặt trước của Galaxy A34 5G sẽ c&oacute; một camera selfie 13 MP c&ugrave;ng g&oacute;c chụp rộng, cho ph&eacute;p bạn chụp ảnh chất lượng cao v&agrave; đầy m&agrave;u sắc. Hơn nữa, t&iacute;nh năng chụp ảnh ban đ&ecirc;m v&agrave; quay chuyển động si&ecirc;u chậm c&ograve;n l&agrave;m tăng th&ecirc;m sự th&uacute; vị khi sử dụng chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại kinh doanh tại Thegioididong.com\">điện thoại</a>&nbsp;n&agrave;y để chụp ảnh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-5g-4.jpg\" onclick=\"return false;\"><img alt=\"Camera chụp ảnh sắc nét - Samsung Galaxy A34 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-5g-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-5g-4.jpg\" title=\"Camera chụp ảnh sắc nét - Samsung Galaxy A34 5G\" /></a></p>\r\n\r\n<h3>Thoải m&aacute;i d&ugrave;ng cho cả ng&agrave;y d&agrave;i</h3>\r\n\r\n<p>B&ecirc;n trong của chiếc điện thoại Galaxy A34 5G n&agrave;y, bạn sẽ t&igrave;m thấy vi&ecirc;n pin c&oacute; dung lượng lớn l&ecirc;n tới 5000 mAh, n&oacute; gi&uacute;p cho thời gian sử dụng của m&aacute;y c&oacute; thể k&eacute;o d&agrave;i suốt cả ng&agrave;y để bạn kh&ocirc;ng cần qu&aacute; lo lắng đến việc sạc pin.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-pin-1.jpg\" onclick=\"return false;\"><img alt=\"Sử dụng dài lâu - Samsung Galaxy A34 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-pin-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/298377/samsung-galaxy-a34-pin-1.jpg\" title=\"Sử dụng dài lâu - Samsung Galaxy A34 5G\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, điện thoại c&ograve;n được trang bị t&iacute;nh năng sạc nhanh với c&ocirc;ng suất tối đa l&ecirc;n tới 25 W gi&uacute;p bạn nhanh ch&oacute;ng sạc đầy pin lại mỗi khi cần thiết.</p>\r\n\r\n<p>Được thiết kế để cung cấp kết nối 5G v&agrave; t&iacute;nh năng nổi bật với gi&aacute; cả phải chăng. Với vẻ bề ngo&agrave;i sang trọng, khả năng chụp ảnh đ&aacute;ng kinh ngạc v&agrave; hiệu suất ấn tượng, Galaxy A34 5G c&oacute; thể l&agrave; sự lựa chọn l&yacute; tưởng cho những người muốn sở hữu một thiết bị 5G với c&aacute;c t&iacute;nh năng chất lượng cao.</p>\r\n', 'Đã đăng', 1, '11-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(16, 'VHL#13', 'Samsung Galaxy S23+ 5G 256GB', 18990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung6.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s23-plus\" target=\"_blank\" title=\"Tham khảo mẫu điện thoại Samsung Galaxy S23 Plus 5G 256GB đang kinh doanh tại Thế Giới Di Động \">Samsung Galaxy S23+ 5G 256GB</a>&nbsp;l&agrave; chiếc điện thoại thuộc d&ograve;ng cao cấp nhất của Samsung được giới thiệu v&agrave;o th&aacute;ng 02/2023. M&aacute;y sở hữu một v&agrave;i điểm ấn tượng như camera c&oacute; khả năng quay video 8K, c&ugrave;ng với đ&oacute; l&agrave; con chip Snapdragon 8 Gen 2 mạnh mẽ h&agrave;ng đầu giới điện thoại Android.</h3>\r\n\r\n<h3>Vẻ ngo&agrave;i đẳng cấp xứng danh flagship</h3>\r\n\r\n<p>Ấn tượng đầu ti&ecirc;n của m&igrave;nh khi tr&ecirc;n tay chiếc Galaxy S23+ l&agrave; c&aacute;i nh&igrave;n vu&ocirc;ng vắn sang trọng, tổng thể m&aacute;y được ho&agrave;n thiện từ c&aacute;c loại chất liệu cao cấp gi&uacute;p mang đến trải nghiệm cầm nắm đầm tay hơn.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111218.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế vuông vắn - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111218.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111218.jpg\" title=\"Thiết kế vuông vắn - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Hơn thế, bộ khung nh&ocirc;m v&agrave; mặt lưng k&iacute;nh cường lực cũng mang đến cho m&aacute;y khả năng kh&aacute;ng xước tốt hay hạn chế được rủi ro nứt, vỡ mỗi khi m&aacute;y c&oacute; gặp va chạm ngo&agrave;i &yacute; muốn.&nbsp;</p>\r\n\r\n<p>Về kiểu ho&agrave;n thiện th&igrave; khung m&aacute;y được l&agrave;m theo kiểu nhẵn b&oacute;ng gi&uacute;p mang đến c&aacute;i nh&igrave;n bắt mắt v&agrave; sang trọng hơn, c&ugrave;ng với đ&oacute; l&agrave; mặt lưng l&agrave;m nh&aacute;m nhằm giảm thiểu hiện tượng b&aacute;m dấu v&acirc;n tay v&agrave; mồ h&ocirc;i mỗi khi sử dụng, ngo&agrave;i ra c&aacute;c vết xước nhẹ cũng được giảm thiểu đối với mặt lưng của chiếc m&aacute;y n&agrave;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111223.jpg\" onclick=\"return false;\"><img alt=\"Khung viền nhôm chắc chắn - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111223.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111223.jpg\" title=\"Khung viền nhôm chắc chắn - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Thoạt nh&igrave;n th&igrave; ở phi&ecirc;n bản Galaxy S23+ c&oacute; lẽ sẽ kh&ocirc;ng c&oacute; qu&aacute; nhiều sự đổi mới so với thế hệ tiền nhiệm, tuy nhi&ecirc;n điểm m&agrave; ta c&oacute; thể dễ d&agrave;ng nhận thấy v&agrave; ph&acirc;n biệt được hai mẫu m&aacute;y giữa đời trước v&agrave; đời sau l&agrave; nằm ở phần cụm camera.</p>\r\n\r\n<p>Sẽ kh&ocirc;ng c&ograve;n đặt chung cụm nữa, m&agrave; thay v&agrave;o đ&oacute; camera tr&ecirc;n Galaxy S23+ đều được bố tr&iacute; theo c&aacute;ch ri&ecirc;ng lẻ, vừa mang đến điểm nhấn mới lạ xong cũng đem lại điểm nhận diện đặc trưng cho d&ograve;ng m&aacute;y n&agrave;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111355.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera riêng biệt - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111355.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111355.jpg\" title=\"Cụm camera riêng biệt - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>C&ograve;n về nguy&ecirc;n l&yacute; thiết kế phần m&agrave;n h&igrave;nh th&igrave; Galaxy S23+ cũng kh&ocirc;ng c&oacute; qu&aacute; nhiều điều kh&aacute;c biệt, vẫn sẽ l&agrave; m&agrave;n h&igrave;nh phẳng với c&aacute;c viền si&ecirc;u mỏng bao bọc xung quanh, c&ugrave;ng với đ&oacute; l&agrave; h&igrave;nh notch dạng đục lỗ chứa camera được l&agrave;m nhỏ để tối ưu diện t&iacute;ch hiển thị.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111402.jpg\" onclick=\"return false;\"><img alt=\"Viền màn hình mỏng - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111402.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111402.jpg\" title=\"Viền màn hình mỏng - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<h3>Bứt ph&aacute; mọi giới hạn với chip Snapdragon 8 Gen 2 for Galaxy</h3>\r\n\r\n<p>V&agrave;o mỗi dịp ra mắt, h&atilde;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung đang kinh doanh tại Thế Giới Di Động\">điện thoại Samsung</a>&nbsp;sẽ trang bị những con chip mạnh mẽ nhất được ra mắt c&ugrave;ng thời gian đ&oacute;, v&agrave; tới thời điểm hiện tại th&igrave; vi xử l&yacute; Snapdragon 8 Gen 2 for Galaxy l&agrave; c&aacute;i t&ecirc;n đ&atilde; xuất hiện tr&ecirc;n Galaxy S23+ với hiệu năng mang lại l&agrave; v&ocirc; c&ugrave;ng ấn tượng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111409.jpg\" onclick=\"return false;\"><img alt=\"Trang bị Snapdragon 8 Gen 2 - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111409.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111409.jpg\" title=\"Trang bị Snapdragon 8 Gen 2 - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Theo th&ocirc;ng số của chip được cung cấp bởi h&atilde;ng th&igrave; Snapdragon 8 Gen 2 for Galaxy c&oacute; thể hoạt động với hiệu suất tối đa đạt được l&agrave; 3.36 GHz, đ&acirc;y l&agrave; con số rất lớn tr&ecirc;n những mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Tham khảo điện thoại Android đang kinh doanh tại Thế Giới Di Động\">điện thoại Android</a>&nbsp;khi kh&aacute; &iacute;t thiết bị đạt được ngưỡng hiệu năng tr&ecirc;n.</p>\r\n\r\n<p>Ngo&agrave;i ra th&igrave; m&igrave;nh cũng đ&atilde; kiểm chứng sức mạnh của m&aacute;y th&ecirc;m một lần nữa, thao t&aacute;c n&agrave;y được thực hiện th&ocirc;ng qua hai phần mềm đo hiệu năng thường d&ugrave;ng v&agrave; kết quả c&oacute; được như sau: 1449 (đơn nh&acirc;n), 4565 (đa nh&acirc;n) tr&ecirc;n Benchmark v&agrave; 15211 tr&ecirc;n PCMark.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111414.jpg\" onclick=\"return false;\"><img alt=\"Điểm đánh giá hiệu năng - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111414.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111414.jpg\" title=\"Điểm đánh giá hiệu năng - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Về trải nghiệm thực tế ra sao th&igrave; m&igrave;nh cũng đ&atilde; d&agrave;nh ra hai tuần trải nghiệm Galaxy S23+, v&agrave; những g&igrave; m&igrave;nh cảm nhận được l&agrave; một độ mượt rất ổn định m&agrave; m&aacute;y đ&atilde; mang lại. Mọi nhu cầu sử dụng cơ bản h&agrave;ng ng&agrave;y đều được xử l&yacute; nhanh ch&oacute;ng v&agrave; m&igrave;nh cũng kh&ocirc;ng hề cảm thấy giật lag hay chậm đơ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111418.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111418.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111418.jpg\" title=\"Hiệu năng mạnh mẽ - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Với những t&iacute;n đồ về game th&igrave; Snapdragon 8 Gen 2 for Galaxy l&agrave; c&aacute;i t&ecirc;n thu h&uacute;t được nhiều sự ch&uacute; &yacute; đến từ người d&ugrave;ng, khi đ&acirc;y được xem l&agrave; mẫu chip mạnh mẽ bậc nhất d&ograve;ng điện thoại Android hiện nay.</p>\r\n\r\n<p>Thực tế th&igrave; m&igrave;nh đ&atilde; d&ugrave;ng Galaxy S23+ để chơi game Li&ecirc;n Qu&acirc;n Mobile trong l&uacute;c trải nghiệm m&aacute;y, cấu h&igrave;nh cụ thể m&agrave; m&igrave;nh c&agrave;i đặt ở đ&acirc;y l&agrave; gần như tối đa cho mọi th&ocirc;ng số, điều n&agrave;y nhằm khai th&aacute;c được tối đa hiệu năng tr&ecirc;n chip để thử xem m&aacute;y mạnh mẽ tới đ&acirc;u.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111423.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình game - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111423.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111423.jpg\" title=\"Cấu hình game - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Kết quả th&igrave; Galaxy S23+ vẫn thừa sức xử l&yacute; được tựa game n&agrave;y, m&aacute;y gần như kh&ocirc;ng gặp phải kh&oacute; khăn n&agrave;o trong l&uacute;c m&igrave;nh sử dụng, mọi thứ đều được diễn ra mượt m&agrave; với tốc độ khung h&igrave;nh duy tr&igrave; ổn định ở mức loanh toanh 60 FPS.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111427.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình game - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111427.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111427.jpg\" title=\"Cấu hình game - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Chuyển đến tựa game kh&oacute; hơn l&agrave; Genshin Impact, đ&acirc;y được đ&aacute;nh gi&aacute; l&agrave; tr&ograve; chơi đ&ograve;i hỏi hiệu năng của thiết bị phải thật mạnh mẽ, v&agrave; l&agrave; c&aacute;i t&ecirc;n xứng tầm để test thử hiệu năng của Galaxy S23+.</p>\r\n\r\n<p>Cấu h&igrave;nh ở tựa game n&agrave;y vẫn tiếp tục l&agrave; mức c&agrave;i đặt cao nhất m&agrave; m&aacute;y c&oacute; thể đạt được, bởi v&igrave; m&igrave;nh muốn trải nghiệm được h&igrave;nh ảnh với chất lượng nhất m&agrave; tựa game n&agrave;y hiển thị ra sao, v&agrave; n&oacute; c&oacute; thực sự l&agrave;m kh&oacute; được Galaxy S23+.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111435.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình game - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111435.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111435.jpg\" title=\"Cấu hình game - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111440.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình game - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111440.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111440.jpg\" title=\"Cấu hình game - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>M&igrave;nh kh&aacute; bất ngờ khi trải nghiệm thực tế qua tựa game n&agrave;y ở mức cấu h&igrave;nh cao m&agrave; m&aacute;y vẫn c&oacute; thể xử l&yacute; tốt, mức FPS lu&ocirc;n được dao động trong tầm từ 35 - 45 v&agrave; &iacute;t khi xảy ra hiện tượng drop qu&aacute; s&acirc;u, n&oacute; gi&uacute;p cho t&igrave;nh trạng giật hay đơ m&aacute;y &iacute;t khi xảy ra.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111445.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình game - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111445.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111445.jpg\" title=\"Cấu hình game - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Nhiệt độ do chip tỏa ra lu&ocirc;n l&agrave; c&acirc;u hỏi được người d&ugrave;ng quan t&acirc;m tr&ecirc;n những mẫu chip đời cao, v&agrave; nếu như ở Gen 1 c&oacute; xuất hiện t&igrave;nh trạng qu&aacute; nhiệt trong l&uacute;c chơi game ở một v&agrave;i d&ograve;ng m&aacute;y đến từ c&aacute;c h&atilde;ng kh&aacute;c. Th&igrave; đối với Gen 2, đ&acirc;y l&agrave; một bước tiến lớn từ nh&agrave; Samsung khi hợp t&aacute;c với Qualcomm để sản xuất ra mẫu chip chuy&ecirc;n dụng độc quyền cho Galaxy.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111450.jpg\" onclick=\"return false;\"><img alt=\"Khả năng tản nhiệt - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111450.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111450.jpg\" title=\"Khả năng tản nhiệt - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Nhờ điều đ&oacute; m&agrave; Snapdragon 8 Gen 2 tương th&iacute;ch với điện thoại tốt hơn, hoạt động với hiệu suất cao v&agrave; cũng cải thiện được t&igrave;nh trạng qu&aacute; nhiệt. Với khoảng 2 giờ chơi game li&ecirc;n tục ở m&ocirc;i trường nhiệt độ ph&ograve;ng, m&igrave;nh gần như kh&ocirc;ng cảm thấy được sự n&oacute;ng l&ecirc;n qu&aacute; đ&aacute;ng kể cả tr&ecirc;n th&acirc;n m&aacute;y, chỉ hơi ấm nhẹ v&agrave; vẫn c&oacute; thể thoải m&aacute;i để tiếp tục sử dụng.</p>\r\n\r\n<p>B&ecirc;n cạnh&nbsp;một con chip mang hiệu năng mạnh mẽ&nbsp;th&igrave; đ&acirc;y c&ograve;n l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\" title=\"Tham khảo điện thoại có RAM 8 GB kinh doanh tại Thegioididong.com\">điện thoại RAM 8 GB</a>, điều n&agrave;y kh&ocirc;ng chỉ gi&uacute;p m&igrave;nh sử dụng c&aacute;c t&aacute;c vụ đa nhiệm mượt m&agrave; hơn m&agrave; c&ograve;n cải thiện được hiện tượng tải lại ứng dụng từ đầu.</p>\r\n\r\n<h3>Tạo n&ecirc;n kiệt t&aacute;c chỉ qua một c&uacute; chạm</h3>\r\n\r\n<p>Với c&aacute;c mẫu điện thoại thuộc bộ sản phẩm Samsung Galaxy d&ograve;ng S trong một v&agrave;i năm gần đ&acirc;y, h&atilde;ng đều mệnh danh c&aacute;c thiết bị n&agrave;y l&agrave; sở hữu cụm camera chuẩn danh &ldquo;mắt thần b&oacute;ng đ&ecirc;m&rdquo; v&agrave; với Galaxy S23+ cũng kh&ocirc;ng nằm trong ngoại lệ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111500.jpg\" onclick=\"return false;\"><img alt=\"Camera chát lượng cao - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111500.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111500.jpg\" title=\"Camera chát lượng cao - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Về th&ocirc;ng số cho phần m&aacute;y ảnh th&igrave; Galaxy S23+ được trang bị cụm gồm 3 ống k&iacute;nh, đầu ti&ecirc;n sẽ l&agrave; camera ch&iacute;nh c&oacute; độ ph&acirc;n giải tới 50 MP v&agrave; c&oacute; hỗ trợ g&oacute;c rộng, sau đ&oacute; sẽ l&agrave; hai cảm biến phụ c&oacute; độ ph&acirc;n giải lần lượt l&agrave; 10 MP v&agrave; 12 MP để bổ sung th&ecirc;m t&iacute;nh năng chụp ảnh zoom v&agrave; g&oacute;c si&ecirc;u rộng.</p>\r\n\r\n<p>Đối với chế độ chụp ảnh mặc định th&igrave; bức h&igrave;nh m&agrave; m&aacute;y thu được c&oacute; độ chi tiết tốt, c&aacute;c v&ugrave;ng s&aacute;ng tối được t&aacute;i hiện lại v&agrave; t&aacute;ch bạch r&otilde; r&agrave;ng. M&agrave;u sắc của ảnh cho ra sẽ c&oacute; một độ rực rỡ đẹp mắt hơn so với thực tế, điều n&agrave;y gi&uacute;p bức ảnh chụp được c&oacute; thể sử dụng ng&agrave;y m&agrave; kh&ocirc;ng cần hậu kỳ chỉnh m&agrave;u qu&aacute; nhiều.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111508.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111508.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111508.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Đến với chế độ chụp ảnh zoom, Galaxy S23+ được hỗ trợ khả năng zoom quang học n&ecirc;n khi sử dụng ở mức điều chỉnh khoảng 3X th&igrave; chất lượng ảnh gần như kh&ocirc;ng c&oacute; qu&aacute; nhiều sự kh&aacute;c biệt đối với chế độ mặc định.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111515.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111515.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111515.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>C&aacute;c v&ugrave;ng trong bức ảnh đều được t&aacute;i hiện r&otilde;, m&agrave;u sắc tươi mới c&ugrave;ng độ ti&ecirc;u cự lớn gi&uacute;p cho bức ảnh tr&ocirc;ng nghệ thuật v&agrave; đẹp mắt hơn rất nhiều. Tuy nhi&ecirc;n nếu x&eacute;t về độ chi tiết khi zoom ảnh th&igrave; chế độ n&agrave;y sẽ kh&ocirc;ng bằng so với chế độ mặc định.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111521.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111521.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111521.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Với t&iacute;nh năng g&oacute;c si&ecirc;u rộng, ở phần n&agrave;y m&aacute;y sẽ cho ph&eacute;p ta chụp được c&aacute;c bức ảnh c&oacute; tầm bao qu&aacute;t lớn hơn, gi&uacute;p thu được nhiều nội dung v&agrave; khung cảnh hơn trong một lần chụp, ph&ugrave; hợp để lưu lại c&aacute;c bức ảnh nh&oacute;m đ&ocirc;ng người hay thu lại vẻ đẹp của c&aacute;c khung cảnh rộng lớn.</p>\r\n\r\n<p>Như bức ảnh b&ecirc;n dưới, những v&ugrave;ng mở rộng ở xung quanh r&igrave;a ảnh sẽ kh&ocirc;ng bị b&oacute;p m&eacute;o hay giảm chất lượng ảnh, v&igrave; thế mỗi bức ảnh chụp được từ t&iacute;nh năng n&agrave;y vẫn sẽ mang đến kết quả tốt d&agrave;nh cho bạn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111529.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111529.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111529.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Để l&agrave;m r&otilde; hơn về t&ecirc;n gọi &ldquo;Mắt thần b&oacute;ng đ&ecirc;m&rdquo; th&igrave; m&igrave;nh cũng đ&atilde; thử qua việc chụp ảnh ở những m&ocirc;i trường c&oacute; &aacute;nh s&aacute;ng thấp bằng con Galaxy S23+. Kết quả sau khi m&igrave;nh chụp được l&agrave;m m&igrave;nh rất ưng &yacute;, từ độ chi tiết cho đến khả năng t&aacute;i hiện m&agrave;u th&igrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>&nbsp;vẫn c&oacute; thể l&agrave;m rất tốt v&agrave; gần như kh&ocirc;ng c&oacute; vấn đề g&igrave;.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111536.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111536.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111536.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Hiện tượng nhiễu hạt hay bị nh&ograve;e ở c&aacute;c v&ugrave;ng c&oacute; &aacute;nh s&aacute;ng cao gần như được cải thiện rất tốt, n&oacute; l&agrave;m cho tổng thể bức ảnh trở n&ecirc;n h&agrave;i h&ograve;a hơn, nịnh mắt hơn gi&uacute;p m&igrave;nh c&oacute; thể tự tin đăng tải l&ecirc;n mạng x&atilde; hội m&agrave; kh&ocirc;ng cần hậu kỳ.</p>\r\n\r\n<h3>Sử dụng tấm nền Dynamic AMOLED 2X</h3>\r\n\r\n<p>Trang bị ở phần mặt trước sẽ l&agrave; m&agrave;n h&igrave;nh với k&iacute;ch thước lớn l&ecirc;n đến 6.6 inch v&agrave; đi k&egrave;m với đ&oacute; độ ph&acirc;n giải Full HD+ (1080 x 2340 Pixels), gi&uacute;p cho ra chất lượng hiển thị tốt hơn với độ chi tiết cao, cảm nhận khi xem phim hay chơi game tr&ecirc;n m&agrave;n h&igrave;nh của m&aacute;y l&agrave; v&ocirc; c&ugrave;ng ch&acirc;n thực.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111543.jpg\" onclick=\"return false;\"><img alt=\"Màn hình chất lượng - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111543.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111543.jpg\" title=\"Màn hình chất lượng - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Về c&ocirc;ng nghệ m&agrave;n h&igrave;nh, Galaxy S23+ được trang bị tấm nền&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-man-hinh-dynamic-amoled-2x-tren-smartphone-1245213\" target=\"_blank\" title=\"Tìm hiểu màn hình Dynamic AMOLED 2X tại Thegioididong.com\">Dynamic AMOLED 2X</a>&nbsp;cao cấp được sản xuất bởi nh&agrave; Samsung, với khả năng hiển thị với gam m&agrave;u sống động đầy bắt mắt, độ tương phản cao c&ugrave;ng khả năng tối ưu &aacute;nh s&aacute;ng xanh tốt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111550.jpg\" onclick=\"return false;\"><img alt=\"Công nghệ màn hinhc cao cấp - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111550.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111550.jpg\" title=\"Công nghệ màn hinhc cao cấp - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Những điều n&agrave;y gi&uacute;p mang lại cho m&igrave;nh trải nghiệm khi xem nội dung được th&iacute;ch mắt hơn, hiện tượng mỏi mắt khi sử dụng v&agrave;o ban đ&ecirc;m cũng được cải thiện.</p>\r\n\r\n<p>Điểm ấn tượng tiếp theo đ&oacute; ch&iacute;nh l&agrave; tần số qu&eacute;t m&agrave;n h&igrave;nh 120 Hz, phải n&oacute;i những thao t&aacute;c vuốt chạm cơ bản tr&ecirc;n m&agrave;n h&igrave;nh của Galaxy S23+ đều được diễn ra hết sức nhanh ch&oacute;ng, hoạt ảnh chuyển m&agrave;n h&igrave;nh khi thao t&aacute;c cũng được t&aacute;i hiện lại mượt m&agrave; gi&uacute;p m&igrave;nh cảm thấy đ&atilde; mắt hơn khi sử dụng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111559.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ tần số quét cao - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111559.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111559.jpg\" title=\"Hỗ trợ tần số quét cao - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>C&ograve;n về độ s&aacute;ng m&agrave;n h&igrave;nh th&igrave; đ&acirc;y l&agrave; một chiếc m&aacute;y m&igrave;nh đ&aacute;nh gi&aacute; kh&aacute; cao, Galaxy S23+ c&oacute; thể hiển thị nội dung với độ s&aacute;ng tối đa l&ecirc;n đến 1750 nits, mức n&agrave;y đủ để m&igrave;nh thấy r&otilde; được nội dung tr&ecirc;n m&agrave;n h&igrave;nh khi vừa xem bản đồ v&agrave; vừa di chuyển khi trời đang nắng to.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111606.jpg\" onclick=\"return false;\"><img alt=\"Độ sáng màn hình cao - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111606.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111606.jpg\" title=\"Độ sáng màn hình cao - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<h3>Năng lượng đủ d&ugrave;ng cho cả ng&agrave;y d&agrave;i</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-s\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại Samsung Galaxy S đang kinh doanh tại Thế Giới Di Động \">điện thoại Samsung Galaxy d&ograve;ng S</a>&nbsp;n&agrave;y được trang bị vi&ecirc;n pin với dung lượng 4700 mAh k&egrave;m sạc nhanh&nbsp;c&ocirc;ng suất&nbsp;45 W. Trải nghiệm thực tế sau khi m&igrave;nh thử d&ugrave;ng qua th&igrave; thấy m&aacute;y c&oacute; thể đ&aacute;p ứng cho m&igrave;nh khoảng 8 tiếng 7 ph&uacute;t sử dụng li&ecirc;n tục, đủ để m&igrave;nh c&oacute; thể thực hiện nhu cầu sử dụng cho c&ocirc;ng việc lẫn giải tr&iacute; m&agrave; kh&ocirc;ng cần lo lắng về vấn đề sạc.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111610.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng pin - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111610.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111610.jpg\" title=\"Thời lượng pin - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>Đối với thời gian sạc th&igrave; m&igrave;nh mất đ&acirc;u đ&oacute; tầm 1 tiếng 18 ph&uacute;t l&agrave; c&oacute; thể nạp đầy được năng lượng cho vi&ecirc;n pin. Ấn tượng hơn l&agrave; m&igrave;nh chỉ cần 30 ph&uacute;t l&agrave; m&aacute;y c&oacute; ngay 62% pin, bấy nhi&ecirc;u l&agrave; đủ d&ugrave;ng cho m&igrave;nh c&oacute; thể sử dụng thoải m&aacute;i cả ng&agrave;y (kh&ocirc;ng li&ecirc;n tục).</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111629.jpg\" onclick=\"return false;\"><img alt=\"Thời gian sạc - Samsung Galaxy S23+ 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111629.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/290829/samsung-galaxy-s23-plus-080423-111629.jpg\" title=\"Thời gian sạc - Samsung Galaxy S23+ 5G 256GB\" /></a></p>\r\n\r\n<p>C&oacute; thể thấy Samsung Galaxy S23+ l&agrave; chiếc điện thoại to&agrave;n diện khi đ&aacute;p ứng tốt hầu hết c&aacute;c t&aacute;c vụ bạn cần, từ giải tr&iacute; đến c&ocirc;ng việc bởi m&aacute;y gần như hội tụ đầy đủ c&aacute;c c&ocirc;ng nghệ h&agrave;ng đầu giới smartphone như chipset hiệu năng cao v&agrave; m&agrave;n h&igrave;nh chất lượng.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 0),
(17, 'VHL#14', 'Samsung Galaxy Z Fold4 5G 256GB', 25990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung7.jpg', '<h3>Tại sự kiện Samsung Unpacked thường ni&ecirc;n,&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-fold4\" target=\"_blank\" title=\"Tham khảo thông tin sản phẩm tại Thế Giới Di Động\">Samsung Galaxy Z Fold4 256GB</a>&nbsp;ch&iacute;nh thức được tr&igrave;nh l&agrave;ng thị trường c&ocirc;ng nghệ, mang tr&ecirc;n m&igrave;nh nhiều cải tiến đ&aacute;ng gi&aacute; gi&uacute;p Galaxy Z Fold trở th&agrave;nh d&ograve;ng điện thoại gập đ&atilde; tốt nay c&agrave;ng tốt hơn.</h3>\r\n\r\n<h3>Kiểu d&aacute;ng gập mở tinh tế tạo n&ecirc;n sự kh&aacute;c biệt</h3>\r\n\r\n<p>Galaxy Z Fold4 (2022) ra mắt với ngoại h&igrave;nh gần như l&agrave; kh&ocirc;ng đổi khi so với&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-fold-3\" target=\"_blank\" title=\"Tham khảo thông tin sản phẩm tại Thế Giới Di Động\">Galaxy Z Fold3</a>, nếu bạn chỉ nh&igrave;n bề ngo&agrave;i th&igrave; bạn sẽ kh&oacute; l&ograve;ng ph&acirc;n biệt được 2 sản phẩm n&agrave;y. M&aacute;y vẫn sử dụng khung viền Armor Aluminum bền bỉ, mặt k&iacute;nh m&agrave;n h&igrave;nh phụ l&agrave; Corning Gorilla Glass Victus+, mặt k&iacute;nh m&agrave;n h&igrave;nh ch&iacute;nh l&agrave; Ultra Thin Glass.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-100822-064950.jpg\" onclick=\"return false;\"><img alt=\"Tạo khác biệt với màn hình gập, mở - Samsung Galaxy Z Fold4 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-100822-064950.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-100822-064950.jpg\" title=\"Tạo khác biệt với màn hình gập, mở - Samsung Galaxy Z Fold4 256GB\" /></a></p>\r\n\r\n<p>V&agrave; đ&acirc;y cũng l&agrave; chiếc điện thoại gập bền bỉ nhất giới smartphone khi kết hợp với khả năng kh&aacute;ng nước chuẩn IPX8, c&oacute; khả năng ng&acirc;m trong nước ngọt ở độ s&acirc;u tới 1.5 m&eacute;t trong thời gian tối đa 30 ph&uacute;t* (m&aacute;y kh&ocirc;ng c&oacute; khả năng kh&aacute;ng bụi).</p>\r\n\r\n<p><em>*Th&ocirc;ng tin h&atilde;ng Samsung cung cấp</em></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-62.jpg\" onclick=\"return false;\"><img alt=\"Điện thoại kháng nước - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-62.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-62.jpg\" title=\"Điện thoại kháng nước - Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p>Năm nay Z Fold4 c&oacute; thiết kế c&acirc;n bằng hơn, bản lề v&agrave; viền m&agrave;n h&igrave;nh được thiết kế mỏng hơn gi&uacute;p tổng thể ở m&agrave;n h&igrave;nh phụ của m&aacute;y rộng th&ecirc;m 2.7 mm v&agrave; m&agrave;n h&igrave;nh ch&iacute;nh rộng hơn 3 mm khi so với thế hệ tiền nhiệm. Tuy con số kh&ocirc;ng qu&aacute; lớn nhưng với trải nghiệm thực tế th&igrave; đ&acirc;y l&agrave; một thay đổi cực kỳ đ&aacute;ng gi&aacute; v&agrave; mang lại hiệu quả sử dụng cao.</p>\r\n\r\n<p>Với k&iacute;ch thước m&agrave;n h&igrave;nh được tăng l&ecirc;n th&igrave; m&agrave;n h&igrave;nh phụ của Fold4 đ&atilde; dễ d&agrave;ng thao t&aacute;c v&agrave; g&otilde; văn bản hơn, kh&ocirc;ng c&ograve;n gặp t&igrave;nh trạng d&iacute;nh chữ như tr&ecirc;n thế hệ trước nữa. Với tỉ lệ m&agrave;n h&igrave;nh n&agrave;y th&igrave; n&oacute; vẫn cao hơn smartphone ngo&agrave;i thị thường một x&iacute;u.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-100822-070512.jpg\" onclick=\"return false;\"><img alt=\"Màn hình phụ lớn hơn - Samsung Galaxy Z Fold4 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-100822-070512.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-100822-070512.jpg\" title=\"Màn hình phụ lớn hơn - Samsung Galaxy Z Fold4 256GB\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung tại Thế Giới Di Động\">H&atilde;ng điện thoại Samsung</a>&nbsp;đ&atilde; thay đổi cấu tr&uacute;c phần bản lề tr&ecirc;n Fold4, điều n&agrave;y mang lại một cảm gi&aacute;c đ&oacute;ng - mở cứng c&aacute;p hơn. M&aacute;y c&oacute; thể linh hoạt gập - mở ở c&aacute;c g&oacute;c từ 75 độ đến 115 độ, cho bạn nhiều g&oacute;c độ sử dụng điện thoại hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115021.jpg\" onclick=\"return false;\"><img alt=\"Thay đổi cấu trúc bản lề - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115021.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115021.jpg\" title=\"Thay đổi cấu trúc bản lề - Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p>Với khả năng gập mở linh hoạt n&agrave;y th&igrave; bạn c&oacute; thể đặt m&aacute;y tr&ecirc;n mặt phẳng để chụp ảnh với c&aacute;c g&oacute;c chụp s&aacute;ng tạo để quay TikTok hay l&agrave;m việc như một chiếc &quot;laptop mini&quot;.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/flex-mode-dien-thoai-man-hinh-gap-samsung-moi-1467788\" target=\"_blank\" title=\"Flex Mode trên Galaxy Z Fold4 và Z Flip4: Một xu hướng và lối sống linh hoạt mới!\">Flex Mode tr&ecirc;n Galaxy Z Fold4 v&agrave; Z Flip4: Một xu hướng v&agrave; lối sống linh hoạt mới!</a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-69.jpg\" onclick=\"return false;\"><img alt=\"Flex Mode trên Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-69.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-69.jpg\" title=\"Flex Mode trên Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh cực đại, trải nghiệm cực đ&atilde;</h3>\r\n\r\n<p>Samsung Galaxy Z Fold4 c&oacute; m&agrave;n h&igrave;nh phụ b&ecirc;n ngo&agrave;i k&iacute;ch thước 6.2 inch độ ph&acirc;n giải HD+. M&agrave;n h&igrave;nh ch&iacute;nh 7.6 inch hỗ trợ cơ chế gập, sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh Dynamic AMOLED 2X rực rỡ c&ugrave;ng một số c&ocirc;ng nghệ như hỗ trợ độ s&aacute;ng tối đa 1200 nits, tần số qu&eacute;t 120 Hz c&oacute; khả năng th&iacute;ch ứng t&ugrave;y thuộc v&agrave;o ứng dụng v&agrave; c&agrave;i đặt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115019.jpg\" onclick=\"return false;\"><img alt=\"Công nghệ màn hình hàng đầu trong giới smartphone - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115019.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115019.jpg\" title=\"Công nghệ màn hình hàng đầu trong giới smartphone - Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p>Tất cả th&ocirc;ng số tr&ecirc;n gi&uacute;p Fold4 trở th&agrave;nh một thiết bị c&oacute; khả năng hiển thị bật nhất thị trường, m&agrave;u sắc rực rỡ, nịnh mắt, m&agrave;u đen s&acirc;u v&agrave; m&agrave;n h&igrave;nh cũng được chứng nhận khả năng bảo vệ mắt gi&uacute;p bạn y&ecirc;n t&acirc;m hơn khi sử dụng sản phẩm trong thời gian d&agrave;i.</p>\r\n\r\n<p>K&iacute;ch thước m&agrave;n h&igrave;nh ch&iacute;nh của Fold4 gần tương đương với một chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo máy tính bảng tại Thế Giới Di Động\">m&aacute;y t&iacute;nh bảng</a>, cho bạn một kh&ocirc;ng gian hiển thị rộng hơn, kết hợp với phần mềm được tối ưu độc quyền, gi&uacute;p Galaxy Z Fold4 trở th&agrave;nh thiết bị 2 trong 1 cực kỳ tiện dụng.</p>\r\n\r\n<p>Năm nay Galaxy Z Fold4 tập trung n&acirc;ng cấp về phần mềm v&agrave; trải nghiệm người d&ugrave;ng l&agrave; ch&iacute;nh, với phi&ecirc;n bản Fold mới n&agrave;y khi bạn v&agrave;o một ứng dụng bất kỳ th&igrave; ph&iacute;a dưới m&agrave;n h&igrave;nh sẽ xuất hiện thanh Taskbar tương tự tr&ecirc;n m&aacute;y t&iacute;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115017.jpg\" onclick=\"return false;\"><img alt=\"Thanh taskbar trên Galaxy Z Fold thế hệ mới - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115017.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115017.jpg\" title=\"Thanh taskbar trên Galaxy Z Fold thế hệ mới - Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p>Bạn c&oacute; thể t&ugrave;y chỉnh như th&ecirc;m, x&oacute;a ứng dụng tr&ecirc;n thanh c&ocirc;ng cụ n&agrave;y, c&aacute;c ứng dụng vừa được mở cũng sẽ xuất hiện ở đ&acirc;y. N&oacute; sẽ gi&uacute;p bạn đa nhiệm nhanh hơn, chuyển đổi qua lại c&aacute;c ứng dụng trực quan hơn. Bạn c&oacute; thể k&eacute;o thả ứng dụng từ thanh taskbar l&ecirc;n m&agrave;n h&igrave;nh để đa nhiệm đa cửa sổ, chia m&agrave;n h&igrave;nh.</p>\r\n\r\n<p>Thanh taskbar n&agrave;y chỉ xuất hiện ở m&agrave;n h&igrave;nh ch&iacute;nh, m&agrave;n h&igrave;nh phụ thanh taskbar sẽ kh&ocirc;ng xuất hiện, bạn vẫn sẽ sử dụng như một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại tại Thế Giới Di Động\">smartphone</a>&nbsp;th&ocirc;ng thường.</p>\r\n\r\n<p>Ở m&agrave;n h&igrave;nh ch&iacute;nh, Galaxy Z Fold4 hỗ trợ đa nhiệm chia m&agrave;n h&igrave;nh tối đa 3 ứng dụng (m&agrave;n h&igrave;nh phụ hỗ trợ chia tối đa 2 ứng dụng), nếu bạn sử dụng cửa sổ nổi th&igrave; số lượng cửa sổ sẽ được tăng l&ecirc;n.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115016.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ chia màn hình, giúp đa nhiệm hiệu quả hơn - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115016.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115016.jpg\" title=\"Hỗ trợ chia màn hình, giúp đa nhiệm hiệu quả hơn - Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p>Với những bạn c&oacute; sở th&iacute;ch viết, vẽ hoặc c&ocirc;ng việc c&oacute; t&iacute;nh chất đặc th&ugrave; l&agrave; ghi ch&eacute;p nhanh hay ph&aacute;c họa th&igrave; Galaxy Z Fold4 c&oacute; lẽ sẽ l&agrave; một lựa chọn tối ưu d&agrave;nh cho c&aacute;c bạn bởi m&aacute;y sẽ c&oacute; chức năng tương tự như một cuốn sổ truyền thống khi sử dụng k&egrave;m với b&uacute;t S Pen*. V&agrave; S Pen vẫn sẽ được tối ưu để sử dụng với c&aacute;c thiết bị gập, vừa tương th&iacute;ch vừa kh&ocirc;ng l&agrave;m ảnh hưởng đến m&agrave;n h&igrave;nh.</p>\r\n\r\n<p><em>*S Pen l&agrave; phụ kiện b&aacute;n rời, kh&ocirc;ng k&egrave;m trong hộp khi mua m&aacute;y</em></p>\r\n\r\n<h3>D&aacute;m n&acirc;ng cấp, tạo kh&aacute;c biệt</h3>\r\n\r\n<p>Cụm camera tr&ecirc;n Fold4 c&oacute; th&ocirc;ng số v&agrave; thiết kế kh&aacute; giống với Galaxy S22+, camera tr&ecirc;n Fold4 được n&acirc;ng cấp rất nhiều so với c&aacute;c thế hệ trước đ&acirc;y, m&aacute;y sở hữu camera ch&iacute;nh g&oacute;c rộng độ ph&acirc;n giải 50 MP, camera g&oacute;c si&ecirc;u rộng 12 MP v&agrave; một camera tele 10 MP.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-54.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera chất lượng - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-54.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-54.jpg\" title=\"Cụm camera chất lượng - Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p>Cụm camera n&agrave;y hỗ trợ zoom quang học 3x cho chất lượng h&igrave;nh ảnh rất tốt, m&igrave;nh kh&aacute; ấn tượng về khả năng chụp của Galaxy Z Fold năm nay, h&igrave;nh ảnh cho ra c&oacute; chiều s&acirc;u, hiệu ứng x&oacute;a ph&ocirc;ng rất mịn, nh&igrave;n tổng quan chất lượng ảnh tương đương chụp bằng chiếc m&aacute;y ảnh DSLR.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-57.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera zoom - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-57.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-57.jpg\" title=\"Ảnh chụp từ camera zoom - Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-58.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera trên Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-58.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-58.jpg\" title=\"Ảnh chụp từ camera trên Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-55.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-55.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-55.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p>Galaxy Z Fold 4 l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-chong-rung-quang-hoc\" target=\"_blank\" title=\"Tham khảo điện thoại có chống rung quang học tại Thế Giới Di Động\">điện thoại c&oacute; chống rung quang học</a>&nbsp;gi&uacute;p bạn chụp ảnh, quay video mượt m&agrave; hơn, kết hợp c&ugrave;ng chế độ chụp đ&ecirc;m gi&uacute;p hạn chế được nhiễu hạt, tất cả những điều tr&ecirc;n l&agrave; tiền đề để tạo n&ecirc;n một bức ảnh đẹp trong mọi điều kiện &aacute;nh s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-56.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp trong điều kiện đủ sáng - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-56.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-56.jpg\" title=\"Ảnh chụp trong điều kiện đủ sáng - Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-61.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp trong điều kiện thiếu sáng - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-61.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-61.jpg\" title=\"Ảnh chụp trong điều kiện thiếu sáng - Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p>Khả năng zoom kỹ thuật số tr&ecirc;n Fold4 gấp 10 lần so với zoom quang học, khi kết hợp với camera tele Fold4 c&oacute; khả năng zoom tối đa đến 30x, cho bạn thoải m&aacute;i s&aacute;ng tạo để tạo n&ecirc;n t&aacute;c phẩm cho ri&ecirc;ng m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-65.jpg\" onclick=\"return false;\"><img alt=\"Camera zoom đến 30 lần - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-65.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-65.jpg\" title=\"Camera zoom đến 30 lần - Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p>Số lượng camera trước tr&ecirc;n Fold4 l&agrave; 2 camera, với một cảm biến thiết kế dạng nốt ruồi ở m&agrave;n h&igrave;nh phụ c&oacute; độ ph&acirc;n giải 10 MP, cảm biến c&ograve;n lại thiết kế ẩn dưới m&agrave;n h&igrave;nh độ ph&acirc;n giải 4 MP.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-63.jpg\" onclick=\"return false;\"><img alt=\"Camera ẩn dưới màn hình - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-63.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-63.jpg\" title=\"Camera ẩn dưới màn hình - Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p>Camera trước dưới m&agrave;n h&igrave;nh đ&atilde; được n&acirc;ng cao về chất lượng so với thế hệ Fold3, thuật to&aacute;n n&acirc;ng cao gi&uacute;p xử l&yacute; bức ảnh trong trẻo hơn, m&agrave;u sắc tươi tắn, cũng như khả năng chụp ảnh ngược s&aacute;ng được tốt hơn.</p>\r\n\r\n<h3>Trải nghiệm mượt m&agrave;, xử l&yacute; đa t&aacute;c vụ</h3>\r\n\r\n<p>Galaxy Z Fold4 l&agrave; chiếc điện thoại thuộc d&ograve;ng cao cấp n&ecirc;n hiệu năng của m&aacute;y l&agrave; điều kh&ocirc;ng cần bận t&acirc;m qu&aacute; nhiều, bởi n&oacute; lu&ocirc;n được trang bị con chip mạnh mẽ nhất tại thời điểm m&agrave; n&oacute; ra mắt.<br />\r\nCPU Snapdragon 8+ Gen 1 l&agrave; nguồn cung cấp sức mạnh cho Galaxy Z Fold năm nay, GPU t&iacute;ch hợp l&agrave; Adreno 670 mang lại một hiệu năng tổng thể cực kỳ ấn tượng. Vấn đề nhiệt độ khi sử dụng rất được quan t&acirc;m tr&ecirc;n c&aacute;c sản phẩm sử dụng Snapdragon 8 Gen 1 series, năm nay Samsung đ&atilde; l&agrave;m rất tốt, nhiệt độ thiết bị khi sử dụng giảm đ&aacute;ng kể, kh&ocirc;ng c&ograve;n n&oacute;ng ran như c&aacute;c sản phẩm sử dụng c&ugrave;ng con chip.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-64.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng cực mạnh mẽ với CPU mới nhất đến từ nhà Qualcomm - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-64.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-64.jpg\" title=\"Hiệu năng cực mạnh mẽ với CPU mới nhất đến từ nhà Qualcomm - Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/danh-gia-hieu-nang-galaxy-z-fold4-1469282\" target=\"_blank\" title=\"Đánh giá hiệu năng Galaxy Z Fold4: Snapdragon 8+ Gen 1 rất ngon, pin khỏe nhưng cần cải thiện một vài điểm\">Đ&aacute;nh gi&aacute; hiệu năng Galaxy Z Fold4: Snapdragon 8+ Gen 1 rất ngon, pin khỏe nhưng cần cải thiện một v&agrave;i điểm.</a></p>\r\n\r\n<p>Năm nay Samsung cũng đ&atilde; hợp t&aacute;c với c&aacute;c &ocirc;ng lớn như Microsoft, Google v&agrave; Meta nhằm tối ưu c&aacute;c ứng dụng từ l&otilde;i để ph&ugrave; hợp hơn với điện thoại m&agrave;n h&igrave;nh gập. C&aacute;c ứng dụng như Outlook, Google Sheets, YouTube, Telegram, Facebook,... v&agrave; c&aacute;c ứng dụng thuộc Google cũng đ&atilde; hỗ trợ tỉ lệ m&agrave;n h&igrave;nh của Samsung hơn, gi&uacute;p người d&ugrave;ng sử dụng một c&aacute;ch dễ d&agrave;ng v&agrave; tối ưu hơn.</p>\r\n\r\n<h3>Pin sử dụng cả ng&agrave;y d&agrave;i</h3>\r\n\r\n<p>Về pin th&igrave; sản phẩm năm nay dường như kh&ocirc;ng c&oacute; thay đổi về th&ocirc;ng số,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo điện thoại sạc nhanh tại Thế Giới Di Động\">điện thoại c&oacute; sạc nhanh</a>&nbsp;25 W v&agrave; dung lượng pin vẫn l&agrave; 4400 mAh, tuy nhi&ecirc;n với sự thay đổi về phần cứng cũng như tối ưu phần mềm th&igrave; thời gian sử dụng của Fold4 sẽ tăng 15-20% so với Fold3.</p>\r\n\r\n<p>Với c&aacute;c t&aacute;c vụ như duyệt web bằng Chrome, xem YouTube, chơi Li&ecirc;n Qu&acirc;n Mobile, lướt Facebook bằng m&agrave;n h&igrave;nh ch&iacute;nh th&igrave; m&aacute;y cho thời gian sử dụng khoảng 8 tiếng. Đ&acirc;y l&agrave; một con số kh&aacute; ổn với một thiết bị c&oacute; m&agrave;n h&igrave;nh lớn như vậy. Nếu bạn sử dụng m&agrave;n h&igrave;nh phụ th&igrave; con số n&agrave;y c&ograve;n tăng hơn nữa.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-67.jpg\" onclick=\"return false;\"><img alt=\"Thời gian sử dụng pin trên Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-67.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-67.jpg\" title=\"Thời gian sử dụng pin trên Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p>Về thời gian nạp năng lượng, trong 30 ph&uacute;t sạc đầu ti&ecirc;n th&igrave; m&aacute;y c&oacute; thể sạc từ 0 - 49% pin v&agrave; mất khoảng 85 ph&uacute;t để sạc đầy 100% pin. Hơn nữa m&aacute;y c&ograve;n hỗ trợ sạc kh&ocirc;ng d&acirc;y 15 W v&agrave; sạc ngược kh&ocirc;ng d&acirc;y cho thiết bị kh&aacute;c.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-69-1.jpg\" onclick=\"return false;\"><img alt=\"Thời gian sạc pin trên Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-69-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-69-1.jpg\" title=\"Thời gian sạc pin trên Samsung Galaxy Z Fold4\" /></a></p>\r\n\r\n<p>Năm nay Samsung đ&atilde; tập trung v&agrave;o n&acirc;ng cấp phần mềm, camera cũng đ&atilde; được n&acirc;ng cấp mạnh mẽ v&agrave; c&oacute; một tỉ lệ m&agrave;n h&igrave;nh mới n&acirc;ng cao trải nghiệm người d&ugrave;ng. H&atilde;y đến Thế Giới Di Động gần nhất để c&ugrave;ng trải nghiệm si&ecirc;u phẩm mới đến từ Samsung.</p>\r\n', 'Đã đăng', 1, '11-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(18, 'VHL#15', 'Samsung Galaxy A04s', 3290000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung8.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a04s\" target=\"_blank\" title=\"Samsung Galaxy A04s đang kinh doanh chính hãng tại Thegioididong.com\">Samsung Galaxy A04s</a>&nbsp;l&agrave; mẫu điện thoại gi&aacute; rẻ đ&aacute;ng ch&uacute; &yacute; được Samsung ra mắt v&agrave;o th&aacute;ng&nbsp;09/2022, m&aacute;y nổi bật với c&aacute;c th&ocirc;ng số như m&agrave;n h&igrave;nh tần số qu&eacute;t 90 Hz, camera 50 MP c&ugrave;ng hiệu năng ổn định với chip Exynos 850.</h3>\r\n\r\n<h3>Vẻ ngo&agrave;i tối giản, m&agrave;u sắc thời trang</h3>\r\n\r\n<p>Galaxy A04s được lấy cảm hứng thiết kế đến từ những chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-a\" target=\"_blank\" title=\"Điện thoại Samsung Galaxy A giá rẻ, trả góp 0% đang kinh doanh chính hãng tại Thegioididong.com\">điện thoại Galaxy d&ograve;ng A</a>&nbsp;trong năm 2023, với mặt lưng đơn sắc c&ugrave;ng kiểu bố tr&iacute; cụm camera xếp dọc ri&ecirc;ng lẻ l&agrave;m điểm nhấn. Những điều n&agrave;y tạo n&ecirc;n một sự tối giản trong thiết kế, c&ugrave;ng độ nhận diện cao cho chiếc smartphone gi&aacute; rẻ nh&agrave; Samsung.</p>\r\n\r\n<p>Đối với chiếc m&aacute;y n&agrave;y th&igrave; h&atilde;ng cho ra mắt 3 phi&ecirc;n bản m&agrave;u đ&oacute; l&agrave; đen, n&acirc;u v&agrave; xanh l&aacute; đậm, ph&ugrave; hợp với nhiều đối tượng người d&ugrave;ng kh&aacute;c nhau ở mọi độ tuổi.</p>\r\n\r\n<p><img src=\"https://lh4.googleusercontent.com/jonQbVJ4TU2hWQbsqelGRJnwe_YeIPWZVxnjbMa7agg2RjE-T-75NikBs9QvW9F5bxdYKdeOEx4YNG4evFWIYZ05m4qrC0yjSuASWo6P2UpJt7WU5jr4LsoIcDeIzNiryrmD1vQxesZgGH4NCznTeg\" /></p>\r\n\r\n<p>Mặt lưng của chiếc m&aacute;y n&agrave;y được l&agrave;m theo kiểu nhẵn b&oacute;ng, khi nh&igrave;n v&agrave;o sẽ c&oacute; cảm gi&aacute;c như đ&acirc;y l&agrave; mặt lưng l&agrave;m từ k&iacute;nh, tr&ocirc;ng cũng rất th&uacute; vị. Tuy nhi&ecirc;n chất liệu n&agrave;y cũng sẽ khiến m&aacute;y dễ bị b&aacute;m v&acirc;n tay, mồ h&ocirc;i hơn, do đ&oacute; bạn cần lau ch&ugrave;i m&aacute;y thường xuy&ecirc;n hơn để giữ cho bề mặt được sạch sẽ.</p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/9MZ0OINCB28tBn4jxktofE_8BCwk8azIYd8wTpZusTXjFEu_7x0liGaEJV7dpaPQQ5QLGZ28w_GKPX2f_ZZuyEN213NvK5HPeJRTZaVW23nKjqZdzSajynrTkdtvHzFM_xSEQ7w5qknXDlr37VSKXA\" /></p>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Điện thoại smartphone Samsung chính hãng, giá rẻ, trả góp 0% đang kinh doanh chính hãng tại Thegioididong.com\">điện thoại Samsung</a>&nbsp;n&agrave;y được cấu tạo bộ khung v&agrave; mặt lưng liền mạch với nhau, ở những vị tr&iacute; tiếp gi&aacute;p th&igrave; h&atilde;ng ho&agrave;n thiện theo kiểu bo cong để dễ cầm nắm hơn. Trong qu&aacute; tr&igrave;nh trải nghiệm điện thoại khi phải sử dụng li&ecirc;n tục để test pin, l&uacute;c n&agrave;y m&igrave;nh kh&ocirc;ng hề thấy cấn tay, cảm gi&aacute;c cầm nắm m&agrave; m&aacute;y mang lại l&agrave; cực kỳ dễ chịu.</p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/Rg4taEbfJiVLUixMAnSVnpGo0LDZI_wuB1J16yyzu4XIibBGpFKSNZEvzwzHfrMjvtFK-AIgkv931CX_diRpzBs-Ucrs4WoMW71-YS1qZcroILiOo2sthc7Oog3Ro-Eoxa1IYT-tkF6i1lfrepO-Uw\" /></p>\r\n\r\n<h3>Trải nghiệm tốt hơn với m&agrave;n h&igrave;nh lớn</h3>\r\n\r\n<p>Ở phần m&agrave;n h&igrave;nh của&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại tại Thế Giới Di Động\">điện thoại</a>&nbsp;th&igrave; h&atilde;ng trang bị cho Galaxy A04s c&aacute;c th&ocirc;ng số cũng kh&aacute; cơ bản, vẫn sẽ l&agrave; tấm nền IPS LCD, độ ph&acirc;n giải HD+ (720 x 1600 Pixels) c&ugrave;ng k&iacute;ch thước m&agrave;n h&igrave;nh lớn 6.5 inch.</p>\r\n\r\n<p>Nội dung khi hiển thị tr&ecirc;n m&agrave;n h&igrave;nh của Galaxy A04s c&oacute; độ chi tiết ở mức kh&aacute;, mọi thứ vẫn r&otilde; r&agrave;ng, &iacute;t bị răng cửa hay lộ r&otilde; điểm ảnh. Tuy nhi&ecirc;n th&igrave; độ ph&acirc;n giải HD+ cũng sẽ giới hạn chất lượng khi xem những khung h&igrave;nh, thước phim c&oacute; mức đồ họa cao.</p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/3IRiUVm4a5sfE1Svt_KECNgg6smJRwjUuJm1CFMY088JIqoDkUjhThh7DkDX1Qqh4T9WqMmwd3RXps277vstLcNXSnJtUwVB5RuQm7bv8uLZbdwVc0eebcPUYKEk0BXP5E26EbEDigUx4WW3rFbg1w\" /></p>\r\n\r\n<p>Trải nghiệm thực tế khi xem những nội dung như phim h&agrave;nh động 4K, Full HD, th&igrave; m&igrave;nh cảm nhận n&oacute; chưa được trọn vẹn cho lắm khi một v&agrave;i hạn chế như răng cưa thỉnh thoảng&nbsp;vẫn&nbsp;xuất hiện.&nbsp;V&igrave; thế m&igrave;nh đ&aacute;nh gi&aacute; m&agrave;n h&igrave;nh n&agrave;y ph&ugrave; hợp để xem những nội dung cơ bản như tin tức, thời sự, c&aacute;c vlog giải tr&iacute; hơn.&nbsp;</p>\r\n\r\n<p>Nội dung khi hiển thị tr&ecirc;n m&agrave;n h&igrave;nh vẫn c&oacute; được m&agrave;u sắc tươi mới, h&igrave;nh ảnh vẫn trong trẻo m&agrave; kh&ocirc;ng hề bị bệt, khi xem những nội dung c&oacute; nhiều sắc m&agrave;u th&igrave; cũng kh&aacute; bắt mắt.</p>\r\n\r\n<p>Ngo&agrave;i ra m&agrave;n h&igrave;nh n&agrave;y c&ograve;n được hỗ trợ tần số qu&eacute;t 90 Hz, gi&uacute;p cho mọi trải nghiệm vuốt chạm đều được diễn ra mượt m&agrave; v&agrave; nhanh ch&oacute;ng, khi xem những tựa phim c&oacute; tốc độ khung h&igrave;nh cao cũng &iacute;t gặp t&igrave;nh trạng x&eacute; h&igrave;nh, nh&ograve;e h&igrave;nh hơn.</p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/jm4EgF7XFubz_sWW37H1ZRe-MjIxd05oMNKtifGI-jUzQV9C0lJuDYMqRTc6fx29xn60W6sq1gt1V-ooGjm4fT4O8lSNW4q1-AA27941ytUc_DMwxRNeQk8hHJozmFgKOmGEJ6q0T5piMCgl77z0Xw\" /></p>\r\n\r\n<p>C&oacute; thể n&oacute;i th&igrave; đ&acirc;y l&agrave; một trong số &iacute;t những chiếc m&aacute;y gi&aacute; rẻ c&oacute; trang bị k&iacute;nh cường lực, v&agrave; Corning Gorilla Glass 3 l&agrave; chuẩn k&iacute;nh được Samsung trang bị cho A04s, về mặt l&yacute; thuyết th&igrave; m&agrave;n h&igrave;nh n&agrave;y c&oacute; khả năng kh&aacute;ng xước nhẹ khi v&ocirc; t&igrave;nh xảy ra va quẹt trong qu&aacute; tr&igrave;nh sử dụng hằng ng&agrave;y.</p>\r\n\r\n<h3>Chụp ảnh n&eacute;t hơn với camera 50 MP</h3>\r\n\r\n<p>Khả năng chụp ảnh của điện thoại c&oacute; thể xem l&agrave; một trong những điểm s&aacute;ng gi&aacute; nhất tr&ecirc;n Galaxy A04s khi h&atilde;ng trang bị cho m&aacute;y cụm 3 camera, trong đ&oacute; camera ch&iacute;nh c&oacute; độ ph&acirc;n giải tới 50 MP v&agrave; hai cảm biến phụ c&oacute; c&ugrave;ng độ ph&acirc;n giải 2 MP.</p>\r\n\r\n<p>Về t&iacute;nh năng hỗ trợ th&igrave; chiếc điện thoại cũng được Samsung trang bị kh&aacute; nhiều, trong đ&oacute; những t&iacute;nh năng nổi bật c&oacute; thể kể đến như: Chuy&ecirc;n nghiệp (Pro), HDR, Si&ecirc;u độ ph&acirc;n giải, L&agrave;m đẹp, Si&ecirc;u cận (Macro), X&oacute;a ph&ocirc;ng,...</p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/6YD8xCvMds43FTOpFDTrVD2reY9JetPWFrMGJDGZ7QfBYcjvftW5AE_FL3j0dR0vfS6Tu-oFbSeSEYW3PoXzefUAYL2LkNj5r1CHSsl824XCsFOj4A_X_0Jcj7i4Ue5tNK6dkHHJwkNvQKwVLr6i5Q\" /></p>\r\n\r\n<p>Ảnh khi chụp bằng t&iacute;nh năng mặc định từ Galaxy A04s cho ra độ chi tiết cao, m&agrave;u sắc ch&acirc;n thực v&agrave; độ s&aacute;ng h&agrave;i h&ograve;a, kể cả khi chụp ảnh v&agrave;o thời điểm quang cảnh u tối th&igrave; m&aacute;y vẫn c&oacute; thể xử l&yacute; v&agrave; điều chỉnh &aacute;nh s&aacute;ng th&ocirc;ng qua t&iacute;nh năng HDR, gi&uacute;p cho tổng quan trở n&ecirc;n dễ nh&igrave;n v&agrave; đẹp mắt hơn.</p>\r\n\r\n<p><img src=\"https://lh4.googleusercontent.com/e9OkQy21Dz381CT1dgyIhDFTdpWJdUjoE8hryX2ShfZKbhTNMhUtalxmQUwSQVtWpUgkKTknAt_0MBYcjvSKX6Aw5OqXIbSUPHD5d677io2MJ15y2iclU0belNIio9mHQBKLomcwTQpoLFA12EZbEw\" /></p>\r\n\r\n<p>Tuy nhi&ecirc;n th&igrave; trong m&ocirc;i trường hạn chế về độ s&aacute;ng th&igrave; tốc độ lấy n&eacute;t của điện thoại giảm đi kh&aacute; nhiều, những pha chụp d&ograve;ng xe qua lại th&igrave; thường xuy&ecirc;n xuất hiện hiện tượng nh&ograve;e, mất n&eacute;t.</p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/uIs7elkDVqjubhQMCjm-nLSbVO2ChliR1CeHKcob7QdDVG77oZovp15affzEG3YpxNTDJ3yKNd1GJowgFjXNTNTfUbN593wsI8yinDmx2r1VHwWkOmMDJR7WgA7kOb_Ppa5nLeI5HF-IiF2Wa-Donw\" /></p>\r\n\r\n<h3>Cung cấp thời lượng d&ugrave;ng d&agrave;i l&acirc;u với vi&ecirc;n pin lớn&nbsp;</h3>\r\n\r\n<p>Trang bị b&ecirc;n trong m&aacute;y l&agrave; vi&ecirc;n pin c&oacute; dung lượng 5000 mAh n&ecirc;n Galaxy A04s ho&agrave;n to&agrave;n c&oacute; thể được sử dụng cả ng&agrave;y m&agrave; kh&ocirc;ng cần bận t&acirc;m đến việc cắm sạc, trải nghiệm thực tế khi m&igrave;nh sử dụng th&igrave; điện thoại duy tr&igrave; được trong 6 tiếng 54 ph&uacute;t li&ecirc;n tục đối với t&aacute;c vụ chơi game.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/orGEz61nr4PtsXjCxq2Vyy_BcmBaw9OD3AYOOAo0n1jKdBnxs11_oltkeIVJxTeLFILx_CRdPLxXpC6x3BioA0tA_KZ-Y54W79QF7ruiyG4slCOweZwRoXWmpQABDFjnJUwJotZOCJr4IftpZ41kqw\" /></p>\r\n\r\n<p>Tuy nhi&ecirc;n th&igrave; ở những t&aacute;c vụ nhẹ nh&agrave;ng hơn như lướt web, xem phim hay nghe nhạc th&igrave; m&aacute;y ho&agrave;n to&agrave;n c&oacute; thể n&acirc;ng mức n&agrave;y l&ecirc;n đến hơn 8 tiếng, đủ để c&oacute; thể thoải m&aacute;i sử dụng cả ng&agrave;y.</p>\r\n\r\n<p>V&igrave; đ&acirc;y l&agrave; mẫu m&aacute;y gi&aacute; rẻ v&igrave; thế Galaxy A04s sẽ kh&ocirc;ng được trang bị c&ocirc;ng nghệ sạc nhanh, m&agrave; chỉ giới hạn ở mức c&ocirc;ng suất 15 W n&ecirc;n việc sạc đầy được vi&ecirc;n pin 5000 mAh cũng mất kh&aacute; nhiều thời gian. Cụ thể trong l&uacute;c sạc điện thoại từ 0% th&igrave; m&igrave;nh mất đ&acirc;u đ&oacute; khoảng 1 tiếng 50 ph&uacute;t để c&oacute; thể nạp đầy được 100% dung lượng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/1KeoJASh2vr2mWy-c5ICqTuyOrwQuUCX_2oIOStuF4Gog4DjByOwzMupP5y4sRtbTQtuwsTIMIQCtMtzwNlbORmmMranxql8kxa6kq9xPpmawBvA-m5OCCW7oWxPDyCtoP3HCv66Byc2eUkJZcToFw\" /></p>\r\n\r\n<h3>Hiệu năng ổn trong tầm gi&aacute;</h3>\r\n\r\n<p>X&eacute;t về mặt cấu h&igrave;nh th&igrave; đ&acirc;y c&oacute; thể l&agrave; chiếc m&aacute;y đ&aacute;ng ch&uacute; &yacute; trong ph&acirc;n kh&uacute;c gi&aacute; rẻ, trong khi c&aacute;c h&atilde;ng kh&aacute;c vẫn c&ograve;n sử dụng những mẫu chip như Unisoc hoặc l&agrave; Helio đời thấp th&igrave; Galaxy A04s được trang bị Exynos 850.</p>\r\n\r\n<p>Exynos 850 l&agrave; vi xử l&yacute; kh&aacute; mới khi được ra mắt v&agrave;o cuối th&aacute;ng 5/2020 v&agrave; c&ograve;n được ch&iacute;nh Samsung sản xuất, v&igrave; thế con chip n&agrave;y cho ra một hiệu năng ổn định, n&oacute; đem đến sự tin cậy tốt hơn so với một v&agrave;i d&ograve;ng chip đời cũ đến từ Unisoc hay MediaTek.</p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/WTl3p_FBCu9N23N46JyofT3PNIWfNg-bxCEjVqUV60mUZh6QuZLJDfpXUK3_5FAUTMJr-K41M2V-N-PI3TT1mvj66xNlKcpZViO6EC_jMISxRqCscDx3_RsY7eX9di8RnCkscHApRoTEb7AQMr0sgA\" /></p>\r\n\r\n<p>Ở c&aacute;c t&aacute;c vụ th&ocirc;ng thường như lướt web, xem phim hay nhắn tin th&igrave; điện thoại xử l&yacute; ổn, mọi thứ diễn mượt m&agrave; v&agrave; kh&ocirc;ng hề xảy ra hiện tượng giật lag g&acirc;y kh&oacute; chịu.&nbsp;</p>\r\n\r\n<p>Đối với c&aacute;c t&aacute;c vụ chơi game th&igrave; m&igrave;nh thấy Galaxy A04s xử l&yacute; vẫn c&ograve;n hơi đuối, mặc d&ugrave; ở mức cấu h&igrave;nh trung b&igrave;nh tr&ecirc;n Li&ecirc;n Qu&acirc;n Mobile nhưng m&aacute;y vẫn xảy ra hiện tượng giật lag thường xuy&ecirc;n, FPS cũng bị drop s&acirc;u n&ecirc;n đ&ocirc;i l&uacute;c sẽ bị khựng l&acirc;u l&agrave;m cho trải nghiệm chơi game của m&igrave;nh kh&ocirc;ng được thoải m&aacute;i cho lắm.</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/D_jYzRRG5tUHI8e0Yfj1091XW5N5t4yyMDRsyGCni5pALn4vumOIGPViAPeXa5G4UjSq7J1ZwgdtYujv5B03pI4sx0I7Yek8ps3priMre1zfDuHrPMknlpEUVcvYOglah77eErIyEH-lgkizlHvV6g\" /></p>\r\n\r\n<p>Vậy n&ecirc;n Galaxy A04s c&oacute; thể sẽ ph&ugrave; hợp hơn khi d&ugrave;ng để giải tr&iacute; với c&aacute;c t&aacute;c vụ xem phim, lướt web..., V&agrave; nếu như bạn đang t&igrave;m cho m&igrave;nh một chiếc m&aacute;y để phục vụ cho những nhu cầu đ&ograve;i hỏi cấu h&igrave;nh cao hơn th&igrave; c&oacute; thể tham khảo qua c&aacute;c d&ograve;ng m&aacute;y tại đ&acirc;y:&nbsp;<a href=\"https://www.thegioididong.com/dtdd-choi-game\" target=\"_blank\" title=\"Xem thêm điện thoại chơi game kinh doanh tại thegioididong.com\">Điện thoại chơi game/cấu h&igrave;nh cao</a>.</p>\r\n\r\n<p>L&agrave; một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-4gb\" target=\"_blank\" title=\"Tham khảo điện thoại RAM 4 GB tại Thế Giới Di Động\">điện thoại RAM 4 GB</a>&nbsp;n&ecirc;n về phần đa nhiệm m&aacute;y xử l&yacute; cũng kh&aacute; ổn, đ&ocirc;i l&uacute;c vẫn c&oacute; hiện tượng khựng nhẹ nếu như mở c&ugrave;ng l&uacute;c nhiều ứng dụng. Để c&oacute; được trải nghiệm tốt nhất tr&ecirc;n Galaxy A04s, bạn n&ecirc;n thường xuy&ecirc;n dọn dẹp bộ nhớ v&agrave; tắt c&aacute;c dụng kh&ocirc;ng cần thiết nhằm tiết kiệm RAM để m&aacute;y vận h&agrave;nh mượt hơn.</p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/5rbrmLcbd05Z47Jryvmcpm0iDbqS9i6hz3n0-1yg0QKjLHN5NA8DwDXWKuPRnwc41RsQlVb4W97Ul9cGNbgP4QL1jMx6jRPUY-fN1PanZa7I24GsrRg07b40aWyupgi6Mi0ISlo4bsaaDoTIJCiHsw\" /></p>\r\n\r\n<p>Samsung Galaxy A04s l&agrave; một chiếc điện thoại gi&aacute; rẻ với thiết kế trẻ trung, đẹp mắt, m&agrave;n h&igrave;nh lớn v&agrave; r&otilde; n&eacute;t, hiệu năng ổn định v&agrave; thời lượng pin l&acirc;u d&agrave;i. Nếu bạn đang t&igrave;m kiếm một chiếc điện thoại để sử dụng c&aacute;c t&aacute;c vụ cơ bản h&agrave;ng ng&agrave;y, th&igrave; Samsung Galaxy A04s sẽ l&agrave; một lựa chọn s&aacute;ng gi&aacute; trong ph&acirc;n kh&uacute;c.</p>\r\n', 'Đã đăng', 1, '11-09-2023', 0),
(19, 'VHL#16', 'Samsung Galaxy Z Flip5 5G 256GB', 21990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung9.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-flip5\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy Z Flip5 5G 256GB tại Thế Giới Di Động\">Samsung Galaxy Z Flip5</a>&nbsp;đ&atilde; ch&iacute;nh thức ra mắt v&agrave;o ng&agrave;y 26 th&aacute;ng 7. Đ&acirc;y l&agrave; một chiếc điện thoại th&ocirc;ng minh c&oacute; thiết kế độc đ&aacute;o với m&agrave;n h&igrave;nh gập, được trang bị bộ vi xử l&yacute; cao cấp Snapdragon 8 Gen 2 for Galaxy, RAM 8 GB, bộ nhớ trong 256 GB, camera k&eacute;p 12 MP v&agrave; pin 3700 mAh.</h3>\r\n\r\n<p><strong>ĐẶC ĐIỂM NỔI BẬT</strong><br />\r\n● Sở hữu lối thiết kế gập độc đ&aacute;o - mang t&iacute;nh ph&aacute; c&aacute;ch v&agrave; thời thượng<br />\r\n● Hiệu năng mạnh mẽ từ chip&nbsp;Snapdragon 8 Gen 2 for Galaxy<br />\r\n● M&agrave;n h&igrave;nh phụ Flex Window 3.4 inch<br />\r\n● C&ocirc;ng nghệ Flex mới - gập kh&ocirc;ng kẽ hở</p>\r\n\r\n<h3>Bảng so s&aacute;nh th&ocirc;ng số&nbsp;phi&ecirc;n bản Galaxy Z Flip5 v&agrave; Galaxy Z Flip4</h3>\r\n\r\n<p>Dưới đ&acirc;y sẽ l&agrave; bảng tổng hợp th&ocirc;ng số kỹ thuật giữa hai thế hệ điện thoại&nbsp;Galaxy Z Flip:</p>\r\n\r\n<table border=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><font color=\"#ffcc00\"><b>Thuộc t&iacute;nh</b></font></p>\r\n			</td>\r\n			<td>\r\n			<p><font color=\"#ffcc00\"><b>Samsung Galaxy Z Flip5</b></font></p>\r\n			</td>\r\n			<td>\r\n			<p><b>Samsung Galaxy Z Flip4</b></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>M&agrave;n h&igrave;nh ch&iacute;nh</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>6.7 inch Full HD+</p>\r\n\r\n			<p>Dynamic AMOLED 2X</p>\r\n\r\n			<p>Infinity Flex</p>\r\n			</td>\r\n			<td>\r\n			<p>6.7 inch Full HD+</p>\r\n\r\n			<p>Dynamic AMOLED 2X</p>\r\n\r\n			<p>Infinity Flex</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>M&agrave;n h&igrave;nh phụ</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>3.4 inch Super AMOLED HD+ 60 Hz</p>\r\n			</td>\r\n			<td>\r\n			<p>1.9 inch Super AMOLED</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Chip xử l&yacute;</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>Snapdragon 8 Gen 2<br />\r\n			(For Galaxy)</p>\r\n			</td>\r\n			<td>\r\n			<p>Snapdragon 8+ Gen 1</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Camera trước</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>10 MP</p>\r\n			</td>\r\n			<td>\r\n			<p>10 MP</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Camera sau</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>2 camera 12 MP</p>\r\n			</td>\r\n			<td>\r\n			<p>2 camera 12 MP</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><b>RAM</b></p>\r\n			</td>\r\n			<td>\r\n			<p>8 GB</p>\r\n			</td>\r\n			<td>\r\n			<p>8 GB</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><b>Bộ nhớ trong</b></p>\r\n			</td>\r\n			<td>\r\n			<p>256 GB</p>\r\n			</td>\r\n			<td>\r\n			<p>256 GB</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><b>Dung lượng pin</b></p>\r\n			</td>\r\n			<td>\r\n			<p>3700 mAh</p>\r\n			</td>\r\n			<td>\r\n			<p>3700 mAh</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>C&ocirc;ng suất sạc</strong></p>\r\n			</td>\r\n			<td>\r\n			<p>25 W</p>\r\n			</td>\r\n			<td>\r\n			<p>25 W</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h3>Ấn tượng với cơ chế gấp gọn</h3>\r\n\r\n<p>Samsung Galaxy Z Flip5 tiếp tục theo đuổi phong c&aacute;ch gập độc đ&aacute;o đ&atilde; trở th&agrave;nh thương hiệu của d&ograve;ng sản phẩm &quot;Z Flip&quot;. Với thiết kế nhỏ gọn v&agrave; h&igrave;nh d&aacute;ng hộp phấn khi gập lại, chiếc điện thoại n&agrave;y rất dễ d&agrave;ng cất đi trong t&uacute;i &aacute;o hoặc t&uacute;i x&aacute;ch của bạn.</p>\r\n\r\n<p>Samsung Galaxy Z Flip5 được chế tạo từ c&aacute;c chất liệu cao cấp như kim loại v&agrave; k&iacute;nh. Khung b&ecirc;n ngo&agrave;i được l&agrave;m bằng Armor Aluminum chắc chắn, mang đến sự cứng c&aacute;p v&agrave; sang trọng cho thiết bị. Mặt sau của điện thoại được bao phủ bởi lớp k&iacute;nh cường lực, gi&uacute;p bảo vệ m&aacute;y khỏi vết xước kh&ocirc;ng mong muốn v&agrave; mang đến vẻ b&oacute;ng bẩy, tinh tế.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092317.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại - Samsung Galaxy Z Flip5 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092317.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092317.jpg\" title=\"Thiết kế điện thoại - Samsung Galaxy Z Flip5 5G\" /></a></p>\r\n\r\n<p>Samsung đ&atilde; li&ecirc;n tục n&acirc;ng cấp c&ocirc;ng nghệ bản lề qua c&aacute;c phi&ecirc;n bản trước đ&oacute; v&agrave; Galaxy Z Flip5 kh&ocirc;ng phải l&agrave; ngoại lệ. Bản lề của chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-z\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung dòng gập tại Thế Giới Di Động\">điện thoại Samsung d&ograve;ng gập</a>&nbsp;n&agrave;y được thiết kế theo cơ chế r&atilde;nh k&eacute;p gi&uacute;p mang đến sự chắc chắn v&agrave; chịu lực tốt, đảm bảo cho việc đ&oacute;ng/mở mượt m&agrave; v&agrave; ổn định.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092319.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế điện thoại - Samsung Galaxy Z Flip5 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092319.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092319.jpg\" title=\"Thiết kế điện thoại - Samsung Galaxy Z Flip5 5G\" /></a></p>\r\n\r\n<p>Điểm nổi bật kh&aacute;c của Galaxy Z Flip5 l&agrave; m&aacute;y được trang bị chuẩn kh&aacute;ng nước IPX8, gi&uacute;p người d&ugrave;ng y&ecirc;n t&acirc;m hơn khi sử dụng&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại di động tại Thế Giới Di Động\">điện thoại</a>&nbsp;trong c&aacute;c ho&agrave;n cảnh c&oacute; li&ecirc;n quan đến nước như đi mưa. N&oacute; c&ograve;n gi&uacute;p tăng cường khả năng ứng dụng v&agrave; linh hoạt của điện thoại, đ&aacute;p ứng nhu cầu ng&agrave;y c&agrave;ng cao của người d&ugrave;ng trong việc sử dụng thiết bị di động trong m&ocirc;i trường ẩm ướt v&agrave; khắc nghiệt.</p>\r\n\r\n<h3>Trang bị m&agrave;n h&igrave;nh cao cấp</h3>\r\n\r\n<p><strong>Trang bị m&agrave;n h&igrave;nh phụ 3.4 inch 60 Hz</strong></p>\r\n\r\n<p>Ngo&agrave;i ra, chiếc điện thoại n&agrave;y c&ograve;n c&oacute; m&agrave;n h&igrave;nh phụ Flex Window với k&iacute;ch thước 3.4 inch, lớn hơn phi&ecirc;n bản tiền nhiệm khi chỉ đạt 1.9 inch.&nbsp;Mặc d&ugrave; kh&ocirc;ng lớn bằng m&agrave;n h&igrave;nh ch&iacute;nh, nhưng n&oacute; ho&agrave;n to&agrave;n đủ để xem th&ocirc;ng b&aacute;o, trả lời cuộc gọi, hay nhắn tin nhanh ch&oacute;ng m&agrave; kh&ocirc;ng cần mở to&agrave;n bộ điện thoại.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh phụ sử dụng tấm nền&nbsp;Super AMOLED c&ugrave;ng độ ph&acirc;n giải 720 x 748 Pixels, đủ sắc n&eacute;t để hiển thị r&otilde; c&aacute;c nội dung cơ bản v&agrave; c&aacute;c ứng dụng th&ocirc;ng dụng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092322.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại - Samsung Galaxy Z Flip5 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092322.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092322.jpg\" title=\"Màn hình điện thoại - Samsung Galaxy Z Flip5 5G\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh phụ của Z Flip5 c&oacute; t&iacute;nh năng Always-on Display, cho ph&eacute;p hiển thị th&ocirc;ng tin cơ bản như giờ, ng&agrave;y th&aacute;ng v&agrave; th&ocirc;ng b&aacute;o m&agrave; kh&ocirc;ng cần bật m&agrave;n h&igrave;nh ch&iacute;nh, đồng thời cũng gi&uacute;p người d&ugrave;ng lu&ocirc;n nắm bắt được th&ocirc;ng tin cần thiết một c&aacute;ch thuận tiện.</p>\r\n\r\n<p><strong>Sử dụng tấm nền&nbsp;Dynamic AMOLED 2X</strong></p>\r\n\r\n<p>Samsung Galaxy Z Flip5 được trang bị m&agrave;n h&igrave;nh Dynamic AMOLED 2X c&ocirc;ng nghệ cao với khả năng hiển thị m&agrave;u sắc đẹp. Tấm nền n&agrave;y cho ph&eacute;p hiển thị c&aacute;c m&agrave;u sắc sống động, tự nhi&ecirc;n v&agrave; đậm m&agrave;u, đem lại cho người d&ugrave;ng những h&igrave;nh ảnh, video ch&acirc;n thực v&agrave; sắc n&eacute;t. M&agrave;u đen s&acirc;u cũng tạo n&ecirc;n độ tương phản tốt, gi&uacute;p hiển thị nội dung m&agrave;u sắc với độ r&otilde; n&eacute;t v&agrave; sắc sảo tuyệt vời.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092321.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại - Samsung Galaxy Z Flip5 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092321.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092321.jpg\" title=\"Màn hình điện thoại - Samsung Galaxy Z Flip5 5G\" /></a></p>\r\n\r\n<p><strong>Hỗ trợ độ ph&acirc;n giải cao v&agrave; tần số qu&eacute;t 120 Hz</strong></p>\r\n\r\n<p>M&agrave;n h&igrave;nh ch&iacute;nh của Samsung Galaxy Z Flip5 c&oacute; độ ph&acirc;n giải cao Full HD+ (1080 x 2640 Pixels) đảm bảo mọi nội dung hiển thị tr&ecirc;n m&agrave;n h&igrave;nh đều r&otilde; n&eacute;t v&agrave; chi tiết. Cho d&ugrave; bạn đang xem h&igrave;nh ảnh, video, trang web hoặc chơi c&aacute;c tr&ograve; chơi di động phức tạp, m&agrave;n h&igrave;nh với độ ph&acirc;n giải cao sẽ gi&uacute;p bạn tận hưởng trải nghiệm sống động v&agrave; ch&acirc;n thực hơn nhiều.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh ch&iacute;nh tr&ecirc;n Samsung Galaxy Z Flip5 sẽ c&oacute; c&oacute; tần số qu&eacute;t m&agrave;n h&igrave;nh 120 Hz cho khả năng cập nhật h&igrave;nh ảnh l&ecirc;n tới 120 lần mỗi gi&acirc;y, mang lại cho người d&ugrave;ng cảm gi&aacute;c mượt m&agrave; v&agrave; phản hồi nhanh hơn khi vuốt, cuộn hoặc chơi c&aacute;c tr&ograve; chơi di động.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092947.jpg\" onclick=\"return false;\"><img alt=\"Màn hình điện thoại - Samsung Galaxy Z Flip5 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092947.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092947.jpg\" title=\"Màn hình điện thoại - Samsung Galaxy Z Flip5 5G\" /></a></p>\r\n\r\n<p><strong>Giảm thiểu nếp gấp tr&ecirc;n m&agrave;n h&igrave;nh tốt hơn thế hệ cũ</strong></p>\r\n\r\n<p>Samsung đ&atilde; li&ecirc;n tục cải tiến c&ocirc;ng nghệ m&agrave;n h&igrave;nh gập để giảm thiểu độ mờ nếp gấp. Ở Galaxy Z Flip5, họ đ&atilde; sử dụng c&aacute;c lớp bảo vệ m&agrave;n h&igrave;nh mỏng v&agrave; ti&ecirc;n tiến hơn để giảm thiểu hiện tượng n&agrave;y. Mặc d&ugrave; kh&ocirc;ng thể ho&agrave;n to&agrave;n loại bỏ độ mờ nếp gấp do t&iacute;nh chất của c&ocirc;ng nghệ n&agrave;y, nhưng đ&acirc;y l&agrave; một bước đ&aacute;ng kể để cải thiện trải nghiệm sử dụng của người d&ugrave;ng tốt hơn so với thế hệ tiền nhiệm.</p>\r\n\r\n<h3>Cấu h&igrave;nh mạnh mẽ h&agrave;ng đầu</h3>\r\n\r\n<p>Samsung Galaxy Z Flip5 được trang bị bộ vi xử l&yacute; Snapdragon 8 Gen 2 for Galaxy - l&agrave; d&ograve;ng chip được tối ưu ri&ecirc;ng cho d&ograve;ng Galaxy chắc chắn sẽ đảm bảo hiệu năng mạnh mẽ v&agrave; hiệu quả về mặt năng lượng. Chip n&agrave;y cũng sẽ hỗ trợ 5G, cho ph&eacute;p người d&ugrave;ng tận hưởng tốc độ internet si&ecirc;u nhanh v&agrave; truyền tải dữ liệu mượt m&agrave;.</p>\r\n\r\n<p>Nhờ v&agrave;o bộ vi xử l&yacute; mạnh mẽ v&agrave; c&aacute;c c&ocirc;ng nghệ ti&ecirc;n tiến t&iacute;ch hợp, Samsung Galaxy Z Flip5 c&oacute; thể dễ d&agrave;ng xử l&yacute; c&aacute;c t&aacute;c vụ đa nhiệm phức tạp, chạy mượt c&aacute;c ứng dụng nặng v&agrave; thậm ch&iacute; đ&aacute;p ứng tốt cho c&aacute;c tr&ograve; chơi 3D đ&ograve;i hỏi đồ họa cao.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092324.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình điện thoại - Samsung Galaxy Z Flip5 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092324.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092324.jpg\" title=\"Cấu hình điện thoại - Samsung Galaxy Z Flip5 5G\" /></a></p>\r\n\r\n<p>Đ&acirc;y được coi l&agrave; một bước đột ph&aacute; đ&aacute;ng ch&uacute; &yacute; về hiệu năng so với phi&ecirc;n bản trước đ&oacute;, khi Z Flip4 chỉ trang bị Snapdragon 8+ Gen 1. Đến thời điểm hiện tại, cả hai vi xử l&yacute; n&agrave;y tiếp tục duy tr&igrave; vị tr&iacute; dẫn đầu trong hạng mục hiệu năng trong thế giới&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Tham khảo điện thoại Android tại Thế Giới Di Động\">điện thoại Android</a>.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092326.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình điện thoại - Samsung Galaxy Z Flip5 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092326.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092326.jpg\" title=\"Cấu hình điện thoại - Samsung Galaxy Z Flip5 5G\" /></a></p>\r\n\r\n<p>Samsung Galaxy Z Flip5 sẽ được trang bị bộ nhớ RAM 8 GB. Việc sở hữu lượng RAM lớn n&agrave;y sẽ cải thiện hiệu suất của điện thoại, gi&uacute;p cho việc chuyển đổi giữa c&aacute;c ứng dụng v&agrave; nhiệm vụ đa nhiệm trở n&ecirc;n trơn tru hơn. Người d&ugrave;ng c&oacute; thể thoải m&aacute;i mở nhiều ứng dụng c&ugrave;ng l&uacute;c, xem video HD, chơi game mượt m&agrave; v&agrave; duyệt web nhanh ch&oacute;ng m&agrave; kh&ocirc;ng gặp trở ngại.</p>\r\n\r\n<h3>T&iacute;ch hợp hệ thống 2 camera</h3>\r\n\r\n<p>Samsung Galaxy Z Flip5 được trang bị một hệ thống camera k&eacute;p ở mặt sau, bao gồm một camera ch&iacute;nh 12 MP v&agrave; một camera g&oacute;c si&ecirc;u rộng 12 MP. Hệ thống n&agrave;y mang đến sự đa dạng trong việc chụp ảnh, gi&uacute;p người d&ugrave;ng dễ d&agrave;ng bắt gọn cảnh quan rộng lớn hoặc chụp những bức ảnh ch&acirc;n dung độc đ&aacute;o.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092328.jpg\" onclick=\"return false;\"><img alt=\"Camera điện thoại - Samsung Galaxy Z Flip5 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092328.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092328.jpg\" title=\"Camera điện thoại - Samsung Galaxy Z Flip5 5G\" /></a></p>\r\n\r\n<p>Hệ thống camera của Galaxy Z Flip5 được tối ưu h&oacute;a để chụp ảnh trong điều kiện &aacute;nh s&aacute;ng yếu v&agrave; đ&ecirc;m tối. C&ocirc;ng nghệ xử l&yacute; h&igrave;nh ảnh ti&ecirc;n tiến v&agrave; cảm biến lớn cho ph&eacute;p nhiều &aacute;nh s&aacute;ng hơn được thu v&agrave;o trong từng khung h&igrave;nh, từ đ&oacute; giảm thiểu nhiễu v&agrave; tăng cường chi tiết trong ảnh. Nhờ đ&oacute;, bạn c&oacute; thể chụp ảnh đẹp v&agrave; sắc n&eacute;t ngay cả khi điều kiện &aacute;nh s&aacute;ng kh&ocirc;ng thuận lợi.</p>\r\n\r\n<p>Samsung Galaxy Z Flip5 hỗ trợ quay video 4K với chất lượng xuất sắc. Khả năng quay video 4K cho ph&eacute;p bạn ghi lại những khoảnh khắc đ&aacute;ng nhớ với độ n&eacute;t cao v&agrave; m&agrave;u sắc ch&acirc;n thực. Hơn nữa, hệ thống ổn định h&igrave;nh ảnh gi&uacute;p giảm thiểu rung lắc khi quay video, đảm bảo những đoạn phim mượt m&agrave; v&agrave; chất lượng.</p>\r\n\r\n<p>Với m&agrave;n h&igrave;nh phụ k&iacute;ch thước lớn ở ph&iacute;a sau, giờ đ&acirc;y người d&ugrave;ng c&oacute; thể ho&agrave;n to&agrave;n trải nghiệm t&iacute;nh năng selfie bằng camera một c&aacute;ch thuận tiện, vừa mang đến trải nghiệm ho&agrave;n to&agrave;n mới song cũng mang lại chất lượng ảnh tốt hơn nhiều so với camera trước th&ocirc;ng thường.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092329.jpg\" onclick=\"return false;\"><img alt=\"Camera điện thoại - Samsung Galaxy Z Flip5 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092329.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092329.jpg\" title=\"Camera điện thoại - Samsung Galaxy Z Flip5 5G\" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra, m&agrave;n h&igrave;nh phụ 3.4 inch c&ugrave;ng c&ocirc;ng nghệ Flexcam cũng sẽ gi&uacute;p bạn c&oacute; thể selfie bằng camera sau một c&aacute;ch dễ d&agrave;ng, bạn c&oacute; thể t&ugrave;y biến thay đổi g&oacute;c m&aacute;y linh hoạt m&agrave; kh&ocirc;ng lo m&agrave;n h&igrave;nh của điện thoại bị gập ho&agrave;n to&agrave;n.</p>\r\n\r\n<h3>Hỗ trợ khả năng sạc 25 W</h3>\r\n\r\n<p>Được trang bị vi&ecirc;n pin c&oacute; dung lượng 3700 mAh, cho ph&eacute;p&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung tại Thế Giới Di Động\">điện thoại Samsung</a>&nbsp;n&agrave;y hoạt động mạnh mẽ trong thời gian d&agrave;i m&agrave; kh&ocirc;ng cần phải sạc lại thường xuy&ecirc;n, thời gian c&oacute; thể dao động trong khoảng 5 - 8 tiếng li&ecirc;n tục t&ugrave;y v&agrave;o t&aacute;c vụ.</p>\r\n\r\n<p>Samsung Galaxy Z Flip5 hỗ trợ c&ocirc;ng nghệ sạc nhanh 25 W, cho ph&eacute;p bạn nạp lại pin nhanh ch&oacute;ng để tiếp tục sử dụng điện thoại m&agrave; kh&ocirc;ng phải chờ đợi qu&aacute; l&acirc;u.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092331.jpg\" onclick=\"return false;\"><img alt=\"Pin và công suất sạc điện thoại - Samsung Galaxy Z Flip5 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092331.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309745/samsung-galaxy-z-flip5-5g-512gb-250723-092331.jpg\" title=\"Pin và công suất sạc điện thoại - Samsung Galaxy Z Flip5 5G\" /></a></p>\r\n\r\n<p>Thời gian để pin đầy thường sẽ phụ thuộc v&agrave;o c&ocirc;ng nghệ sạc l&agrave; chủ yếu v&agrave; theo th&ocirc;ng thường, điện thoại c&oacute; thể được sạc đầy từ 0-100% trong khoảng thời gian từ 1.5 đến 2 giờ khi sạc bằng bộ sạc c&oacute; c&ocirc;ng suất 25 W.</p>\r\n\r\n<h3>V&igrave; sao bạn n&ecirc;n chọn mua Samsung Galaxy Z Flip5 tại Thế Giới Di Động?</h3>\r\n\r\n<p>Thế Giới Di Động l&agrave; một trong những nh&agrave; b&aacute;n lẻ di động h&agrave;ng đầu tại Việt Nam, c&oacute; uy t&iacute;n v&agrave; kinh nghiệm l&acirc;u năm trong ng&agrave;nh, c&oacute; mạng lưới rộng khắp c&aacute;c cửa h&agrave;ng tr&ecirc;n to&agrave;n quốc, gi&uacute;p bạn dễ d&agrave;ng tiếp cận với dịch vụ sau b&aacute;n h&agrave;ng.</p>\r\n\r\n<p>Nếu bạn c&oacute; bất kỳ c&acirc;u hỏi hoặc vấn đề n&agrave;o li&ecirc;n quan đến sản phẩm, bạn c&oacute; thể t&igrave;m được sự hỗ trợ nhanh ch&oacute;ng.</p>\r\n\r\n<p>Thế Giới Di Động thường c&oacute; nhiều chương tr&igrave;nh khuyến m&atilde;i hấp dẫn cho kh&aacute;ch h&agrave;ng, bao gồm giảm gi&aacute;, tặng qu&agrave; k&egrave;m, hoặc trả g&oacute;p với l&atilde;i suất ưu đ&atilde;i. Điều n&agrave;y c&oacute; thể gi&uacute;p bạn tiết kiệm được một số tiền khi mua Samsung Galaxy Z Flip5.</p>\r\n\r\n<h3>Tổng hợp c&aacute;c c&acirc;u hỏi xoay quanh sản phẩm</h3>\r\n\r\n<p><strong>Samsung Galaxy Z Flip5 c&oacute; những điểm n&acirc;ng cấp mạnh về g&igrave;?</strong></p>\r\n\r\n<p>Chiếc m&aacute;y n&agrave;y được n&acirc;ng cấp mạnh về phần bản lề với nguy&ecirc;n l&yacute; gập mới, điều n&agrave;y gi&uacute;p cho Samsung c&oacute; thể tối ưu được nếp gấp nhằm mang đến một m&agrave;n h&igrave;nh tốt hơn.</p>\r\n\r\n<p>Tương tự như c&aacute;c thế hệ trước, chiếc điện thoại Samsung n&agrave;y vẫn sẽ tiếp tục sử dụng chipset mạnh mẽ h&agrave;ng đầu từ Qualcomm. V&agrave; ở thời điểm hiện tại th&igrave;&nbsp;Snapdragon 8 Gen 2 for Galaxy được xuất hiện tr&ecirc;n Galaxy Z Flip5, hứa hẹn mang lại hiệu suất đ&aacute;ng kinh ngạc tr&ecirc;n một chiếc điện thoại gập.</p>\r\n\r\n<p><strong>Galaxy Z Flip5 sẽ c&oacute; mấy m&agrave;u?</strong></p>\r\n\r\n<p>Ở phi&ecirc;n bản n&agrave;y th&igrave; h&atilde;ng cho ra mắt 4 phi&ecirc;n bản m&agrave;u sắc với X&aacute;m Indie, Xanh Mint, T&iacute;m Fancy v&agrave; Kem Latte, đ&acirc;y được xem l&agrave; những m&agrave;u trẻ trung rất ph&ugrave; hợp với c&aacute;c bạn trẻ năng động, đồng thời cũng gi&uacute;p t&ocirc;n l&ecirc;n được vẻ sang trọng v&agrave; thời thượng cho điện thoại.</p>\r\n\r\n<p><strong>Gi&aacute; b&aacute;n Samsung Galaxy Z Flip5 bao nhi&ecirc;u?</strong></p>\r\n\r\n<p>Th&ocirc;ng tin về chương tr&igrave;nh khuyến m&atilde;i từ ng&agrave;y 26/07 - 11/08:</p>\r\n\r\n<p>- Săn Galaxy Z Flip5 nhập hội GEN Z: 5 suất Samsung Galaxy Z Flip5 với gi&aacute; 5K, 5 suất tai nghe BUDS 2 với gi&aacute; 0đ.</p>\r\n\r\n<p>- Tặng g&oacute;i dịch vụ cao cấp Z-Elite trị gi&aacute; 8 triệu bao gồm Samsung Care+ 1 năm.</p>\r\n\r\n<p>- Thu cũ đổi mới trợ gi&aacute; đến 2 triệu.</p>\r\n\r\n<p>- Hỗ trợ trả g&oacute;p 0%</p>\r\n\r\n<p>- Đặc biệt giảm ngay 4 triệu khi mua phi&ecirc;n bản bộ nhớ 512 GB.</p>\r\n\r\n<p>Gi&aacute; b&aacute;n Samsung Z Flip 5 tại Thế Giới Di Động:</p>\r\n\r\n<p>Samsung Galaxy Z Flip 5 256 GB:&nbsp;<strong>25.990.000₫</strong></p>\r\n\r\n<p>Samsung Galaxy Z Flip 5 512 GB:&nbsp;<strong>29.990.000₫</strong></p>\r\n\r\n<p>Nếu bạn l&agrave; một người trẻ trung, năng động, v&agrave; lu&ocirc;n t&igrave;m kiếm những xu hướng c&ocirc;ng nghệ mới, th&igrave; kh&ocirc;ng thể kh&ocirc;ng nhắc đến Samsung Galaxy Z Flip5 - một si&ecirc;u phẩm điện thoại kh&ocirc;ng thể bỏ qua. Với cơ chế gập độc đ&aacute;o, thiết kế của chiếc điện thoại n&agrave;y tạo n&ecirc;n sự ph&aacute; c&aacute;ch v&agrave; ấn tượng ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(20, 'VHL#17', 'Samsung Galaxy S22 Ultra 5G 128GB', 17790000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung10.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s22-ultra\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S22 Ultra 5G kinh doanh tại Thegioididong.com\">Galaxy S22 Ultra 5G</a>&nbsp;chiếc smartphone cao cấp nhất trong bộ 3 Galaxy S22 series m&agrave;&nbsp;<a href=\"https://www.thegioididong.com/samsung\" target=\"_blank\" title=\"Tham khảo sản phẩm Samsung kinh doanh tại Thegioididong.com\">Samsung</a>&nbsp;đ&atilde; cho ra mắt v&agrave; lần nữa S22 Ultra 5G tiếp tục&nbsp;trở lại đầy ngoạn mục mang đến 1 diện mạo mới khi t&iacute;ch hợp b&uacute;t S Pen ho&agrave;n hảo trong th&acirc;n m&aacute;y, trang bị vi xử l&yacute; mạnh mẽ cho c&aacute;c t&aacute;c vụ sử dụng v&ocirc; c&ugrave;ng mượt m&agrave; v&agrave; nổi bật hơn với cụm camera kh&ocirc;ng viền độc đ&aacute;o mang đậm dấu ấn ri&ecirc;ng v&agrave; c&oacute; mức gi&aacute; b&aacute;n si&ecirc;u hấp dẫn.</h3>\r\n\r\n<h3>Sở hữu một diện mạo đầy nổi bật</h3>\r\n\r\n<p>Galaxy S22 Ultra 5G được kế thừa form thiết kế từ những d&ograve;ng Note trước đ&acirc;y của h&atilde;ng đem đến cho bạn c&oacute; cảm gi&aacute;c vừa mới lạ vừa ho&agrave;i niệm. Khối lượng của m&aacute;y 228 g cho cảm gi&aacute;c cầm nắm đầm tay, khi cầm m&aacute;y trần th&igrave; hơi c&oacute; cảm gi&aacute;c dễ trượt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-10-2.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S22 Ultra 5G - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-10-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-10-2.jpg\" title=\"Galaxy S22 Ultra 5G - Thiết kế\" /></a></p>\r\n\r\n<p>Phần mặt lưng&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>&nbsp;được l&agrave;m nh&aacute;m n&ecirc;n hạn chế tốt việc b&aacute;m dấu v&acirc;n tay, c&oacute; thiết kế cũng kh&aacute; đơn giản nhưng vẫn to&aacute;t l&ecirc;n vẻ sang trọng v&agrave; cao cấp của chiếc m&aacute;y. Cụm camera sau tr&ecirc;n Galaxy S22 Ultra 5G được thiết kế trần tạo cảm gi&aacute;c gọn g&agrave;ng v&agrave; cũng tạo n&ecirc;n một điểm nhấn độc đ&aacute;o cho chiếc m&aacute;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-11-2.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S22 Ultra 5G - Mặt lưng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-11-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-11-2.jpg\" title=\"Galaxy S22 Ultra 5G - Mặt lưng\" /></a></p>\r\n\r\n<p>C&oacute; thể n&oacute;i Galaxy S22 Ultra 5G ch&iacute;nh l&agrave; chiếc m&aacute;y đầu ti&ecirc;n được t&iacute;ch hợp b&uacute;t S Pen ho&agrave;n hảo trong một th&acirc;n m&aacute;y của&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-s\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại Samsung Galaxy S đang kinh doanh tại Thế Giới Di Động \">d&ograve;ng Galaxy S</a>. Giờ đ&acirc;y, bạn c&oacute; thể dễ d&agrave;ng ph&aacute;c thảo, ghi ch&uacute; lại những &yacute; tưởng v&ocirc; c&ugrave;ng nhanh ch&oacute;ng với độ trễ đ&atilde; được cải thiện cho cảm gi&aacute;c viết vẽ v&ocirc; c&ugrave;ng ch&acirc;n thật.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-7-2.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S22 Ultra 5G - Thao tác viết bằng bút S Pen\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-7-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-7-2.jpg\" title=\"Galaxy S22 Ultra 5G - Thao tác viết bằng bút S Pen\" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh mạnh mẽ với Snapdragon 8 Gen 1</h3>\r\n\r\n<p>Hiệu năng tr&ecirc;n Galaxy S22 Ultra 5G l&agrave; điều khỏi b&agrave;n c&atilde;i khi m&aacute;y sử dụng chip Snapdragon 8 Gen 1 v&ocirc; c&ugrave;ng mạnh mẽ với tiến tr&igrave;nh sản xuất 4 nm. Khi m&aacute;y chơi game cũng chỉ ấm l&ecirc;n một ch&uacute;t ở vị tr&iacute; cụm camera chứ kh&ocirc;ng ho&agrave;n to&agrave;n qu&aacute; n&oacute;ng như những sản phẩm kh&aacute;c khi d&ugrave;ng c&ugrave;ng con chip n&agrave;y.</p>\r\n\r\n<p>M&aacute;y dễ d&agrave;ng vượt qua được c&aacute;c tựa game Li&ecirc;n Qu&acirc;n, PUBG Mobile, c&agrave;i đặt cấu h&igrave;nh tối đa th&igrave; biểu đồ FPS tr&ecirc;n Galaxy S22 Ultra 5G vẫn kh&aacute; ổn định, kh&ocirc;ng bị drop qu&aacute; nhiều. Kể cả tựa game Genshin Impact ở mức đồ họa mặc định th&igrave; m&aacute;y chơi vẫn mượt m&agrave;, đồ họa hiển thị đẹp mắt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-1-2.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S22 Ultra 5G - Trải nghiệm chơi game\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-1-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-1-2.jpg\" title=\"Galaxy S22 Ultra 5G - Trải nghiệm chơi game\" /></a></p>\r\n\r\n<p>C&ograve;n n&oacute;i về c&aacute;c t&aacute;c vụ cơ bản như lướt web, đọc b&aacute;o, xem YouTube,... th&igrave; m&aacute;y ho&agrave;n th&agrave;nh một c&aacute;ch dễ d&agrave;ng, sử dụng mượt m&agrave;, khả năng đa nhiệm thoải m&aacute;i với&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\" title=\"Tham khảo điện thoại có RAM 8 GB kinh doanh tại Thegioididong.com\">RAM 8 GB</a>.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-3-2.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S22 Ultra 5G - Trải nghiệm lướt web\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-3-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-3-2.jpg\" title=\"Galaxy S22 Ultra 5G - Trải nghiệm lướt web\" /></a></p>\r\n\r\n<h3>Mang đến những bức ảnh đầy chất lượng</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung đang kinh doanh tại Thế Giới Di Động\">điện thoại Samsung</a>&nbsp;n&agrave;y được trang bị 4 camera với camera ch&iacute;nh l&ecirc;n đến 108 MP,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\" title=\"Tham khảo điện thoại có camera góc rộng tại Thegioididong.com\">camera g&oacute;c si&ecirc;u rộng</a>&nbsp;12 MP, 2&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-zoom\" target=\"_blank\" title=\"Tham khảo điện thoại có camera tele kinh doanh tại Thegioididong.com\">camera tele</a>&nbsp;10 MP hỗ trợ thu ph&oacute;ng quang học 10x. Nổi bật nhất l&agrave; camera ch&iacute;nh với khả năng chụp đ&ecirc;m v&ocirc; c&ugrave;ng ấn tượng như c&acirc;u m&agrave; h&atilde;ng đ&atilde; đưa ra &quot;Nightography Camera - Mắt thần b&oacute;ng đ&ecirc;m&quot;. Sở hữu cảm biến điểm ảnh lớn n&ecirc;n thu được nhiều &aacute;nh s&aacute;ng hơn, khi chụp đ&ecirc;m những bức ảnh cực kỳ s&aacute;ng, hiện tượng nhiễu hạt được cải thiện đ&aacute;ng kể.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-8-2.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S22 Ultra 5G - Ảnh chụp trên camera\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-8-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-8-2.jpg\" title=\"Galaxy S22 Ultra 5G - Ảnh chụp trên camera\" /></a></p>\r\n\r\n<p>Chế độ zoom 100x hoạt động kh&aacute; l&agrave; ổn định, khả năng zoom xa v&agrave; bắt n&eacute;t ảnh nhanh. Tất nhi&ecirc;n khi bạn zoom tới 100x th&igrave; ảnh kh&aacute; mờ, khoảng c&aacute;ch zoom để cho ra chất lượng ảnh tốt l&agrave; khoảng 30-40x.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-6-2.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S22 Ultra 5G - Chế độ zoom 30x trên máy\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-6-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-6-2.jpg\" title=\"Galaxy S22 Ultra 5G - Chế độ zoom 30x trên máy\" /></a></p>\r\n\r\n<p>Chế độ chụp ch&acirc;n dung cũng đ&atilde; được h&atilde;ng cải thiện kh&aacute; nhiều so với thế hệ trước.&nbsp;Ảnh chụp x&oacute;a ph&ocirc;ng s&acirc;u, &iacute;t bị lẹm kể cả phần kh&oacute; như t&oacute;c, độ chi tiết cao, ảnh tươi s&aacute;ng v&agrave; m&agrave;u sắc kh&aacute; ch&acirc;n thật. Bạn muốn chụp ảnh phong cảnh, khu phố hay đơn giản l&agrave; chụp nh&oacute;m th&igrave; tr&ecirc;n Galaxy S22 Ultra 5G ho&agrave;n to&agrave;n l&agrave;m được với camera g&oacute;c si&ecirc;u rộng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-9-2.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S22 Ultra 5G - Ảnh chụp trên camera\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-9-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-9-2.jpg\" title=\"Galaxy S22 Ultra 5G - Ảnh chụp trên camera\" /></a></p>\r\n\r\n<p>Galaxy S22 Ultra 5G hỗ trợ quay video 8K tuy nhi&ecirc;n kh&ocirc;ng đi k&egrave;m chống rung, nếu như bật chế độ Si&ecirc;u ổn định l&ecirc;n th&igrave; độ ph&acirc;n giải sẽ bị giới hạn xuống Full HD 60 FPS. Bao nhi&ecirc;u đ&oacute; cũng kh&aacute; đủ rồi, khung h&igrave;nh ổn định, &iacute;t bị giật, độ chi tiết cao v&agrave; m&agrave;u sắc cũng kh&aacute; ch&acirc;n thật để ch&uacute;ng ta dễ d&agrave;ng chỉnh sửa.</p>\r\n\r\n<p>Camera trước l&ecirc;n đến 40 MP cho n&ecirc;n h&igrave;nh sống ảo của ch&uacute;ng ta sẽ ch&acirc;n thật hơn bao giờ hết, da vẫn được l&agrave;m mịn nhẹ nhưng c&aacute;c chi tiết tr&ecirc;n da đều sẽ được t&aacute;i tạo rất r&otilde; n&eacute;t. M&agrave;u sắc kh&aacute; rực rỡ n&ecirc;n kh&ocirc;ng cần &aacute;p dụng th&ecirc;m bộ lọc m&agrave;u nữa.</p>\r\n\r\n<h3>Trải nghiệm xem nh&igrave;n ho&agrave;n hảo</h3>\r\n\r\n<p>Galaxy S22 Ultra 5G&nbsp;trang bị một m&agrave;n h&igrave;nh kh&aacute; xuất sắc với độ ph&acirc;n giải 2K+, rộng 6.8 inch c&ugrave;ng với đ&oacute; l&agrave; tấm nền&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-man-hinh-dynamic-amoled-2x-tren-smartphone-1245213\" target=\"_blank\" title=\"Tìm hiểu màn hình Dynamic AMOLED 2X tại Thegioididong.com\">Dynamic AMOLED 2X</a>&nbsp;đem đến một m&agrave;u sắc hiển thị rực rỡ, m&agrave;u đen s&acirc;u cho cảm gi&aacute;c xem nh&igrave;n ch&acirc;n thật. Hỗ trợ tần số qu&eacute;t 120 Hz n&ecirc;n khả năng vuốt chạm mượt m&agrave;, nhanh ch&oacute;ng. M&agrave;n h&igrave;nh c&oacute; độ s&aacute;ng cao 1750 nits n&ecirc;n việc khi d&ugrave;ng ngo&agrave;i trời chất lượng hiển thị vẫn rất r&otilde; r&agrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-5-2.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S22 Ultra 5G - Màn hình cong tràn sang 2 cạnh\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-5-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-5-2.jpg\" title=\"Galaxy S22 Ultra 5G - Màn hình cong tràn sang 2 cạnh\" /></a></p>\r\n\r\n<p>Mặt k&iacute;nh cong tr&agrave;n sang 2 cạnh kh&aacute; sang trọng v&agrave; cũng l&agrave; đặc trưng của d&ograve;ng Note, được trang bị k&iacute;nh cường lực Corning Gorilla Glass Victus+ gia tăng khả năng chống trầy xước rất tốt, cho bạn y&ecirc;n t&acirc;m hơn khi sử dụng m&aacute;y.</p>\r\n\r\n<h3>Thời lượng sử dụng cả ng&agrave;y với vi&ecirc;n pin 5000 mAh</h3>\r\n\r\n<p>Vi&ecirc;n pin tr&ecirc;n Galaxy S22 Ultra 5G được hỗ trợ l&agrave; 5000 mAh tương tự như&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s21-ultra\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S21 Ultra tại Thegioididong.com\">Galaxy S21 Ultra 5G</a>&nbsp;nhưng c&ocirc;ng suất sạc th&igrave; đ&atilde; được n&acirc;ng l&ecirc;n th&agrave;nh 45 W thay v&igrave; 25 W. Khả năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo điện thoại có sạc nhanh kinh doanh tại Thegioididong.com\">sạc nhanh</a>&nbsp;ấn tượng với 30 ph&uacute;t được hơn 50% dung lượng v&agrave; mất khoảng 1 giờ 10 ph&uacute;t l&agrave; đầy.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-12-2.jpg\" onclick=\"return false;\"><img alt=\"Galaxy S22 Ultra 5G - Thời gian sạc\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-12-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/235838/samsung-galaxy-s22-ultra-12-2.jpg\" title=\"Galaxy S22 Ultra 5G - Thời gian sạc\" /></a></p>\r\n\r\n<p>V&igrave; thế với dung lượng pin cao c&ugrave;ng khả năng sạc nhanh, th&igrave; Galaxy S22 Ultra 5G ho&agrave;n to&agrave;n c&oacute; thể c&ugrave;ng bạn chinh phục c&aacute;c tựa game, những bộ phim y&ecirc;u th&iacute;ch hoặc đ&aacute;p ứng tốt nhu cầu l&agrave;m việc cả một ng&agrave;y d&agrave;i.</p>\r\n\r\n<h3>Chuyển đổi dữ liệu nhanh ch&oacute;ng v&agrave; tiện lợi</h3>\r\n\r\n<p>Ngo&agrave;i ra, để trải nghiệm sử dụng của kh&aacute;ch h&agrave;ng được liền mạch hơn, Samsung c&oacute; cung cấp một c&aacute;ch chuyển đổi dữ liệu an to&agrave;n, bảo mật với t&iacute;nh năng&nbsp;<strong>Smart Switch.&nbsp;</strong>Hỗ trợ người d&ugrave;ng truyền dữ liệu như ảnh, video, danh bạ, sự kiện lịch, ghi ch&uacute;,... từ nhiều thiết bị&nbsp;<strong>kể cả kh&aacute;c hệ điều h&agrave;nh</strong>&nbsp;đến Samsung Galaxy S22 Ultra.</p>\r\n\r\n<p>Bạn c&oacute; thể tham khảo chi tiết b&agrave;i hướng dẫn tại:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/huong-dan-chuyen-du-lieu-tu-iphone-sang-dien-thoai-927670\" target=\"_blank\" title=\"Chuyển dữ liệu sang điện thoại Samsung bằng Smart Switch\">Chuyển dữ liệu sang điện thoại Samsung bằng Smart Switch.</a></p>\r\n\r\n<p>T&oacute;m lại, với những ưu điểm như thiết kế sang trọng, camera chụp đ&ecirc;m ấn tượng, hiệu năng vượt trội th&igrave; Galaxy S22 Ultra 5G ho&agrave;n to&agrave;n xứng đ&aacute;ng để bạn bỏ tiền ra sở hữu em n&oacute; để phục vụ nhiều cầu sử dụng của bản th&acirc;n.</p>\r\n\r\n<p>Kh&aacute;m ph&aacute; bộ sưu tập&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-flip5\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy Z Flip5 tại Thegioididong.com\">Galaxy Z Flip 5</a>,&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-fold5\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy Z Fold5 tại Thegioididong.com\">Galaxy Z Fold 5</a>&nbsp;với h&agrave;ng loạt m&agrave;u sắc hot trend c&ugrave;ng c&ocirc;ng nghệ đột ph&aacute; năm 2023 như m&agrave;n h&igrave;nh gập kh&ocirc;ng kẽ hở, nếp gấp v&ocirc; h&igrave;nh, thế hệ vi xử l&yacute; mạnh mẽ nhất. Đến ngay Thế Giới Di Động để trải nghiệm sự kết hợp ho&agrave;n hảo giữa c&ocirc;ng nghệ v&agrave; phong c&aacute;ch.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 1),
(21, 'VHL#18', 'Samsung Galaxy S23 5G 128GB', 16190000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung11.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s23-5g\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy S23 5G 128GB đang kinh doanh tại Thế Giới Di Động\">Samsung Galaxy S23 5G 128GB</a>&nbsp;chắc hẳn kh&ocirc;ng c&ograve;n l&agrave; c&aacute;i t&ecirc;n qu&aacute; xa lạ đối với c&aacute;c t&iacute;n độ c&ocirc;ng nghệ hiện nay, được xem l&agrave; một trong những gương mặt chủ chốt đến từ nh&agrave; Samsung với cấu h&igrave;nh mạnh mẽ bậc nhất, camera trứ danh h&agrave;ng đầu c&ugrave;ng lối ho&agrave;n thiện v&ocirc; c&ugrave;ng sang trọng v&agrave; hiện đại.</h3>\r\n\r\n<h3>Sở hữu lối thiết kế sang trọng</h3>\r\n\r\n<p>Ở phi&ecirc;n bản năm nay, m&igrave;nh rất vui khi biết được rằng Galaxy S23 vẫn giữ nguy&ecirc;n kiểu d&aacute;ng quen thuộc từ thế hệ trước, n&oacute; được xem l&agrave; một biểu tượng của d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-s\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy dòng S đang kinh doanh tại Thế Giới Di Động\">điện thoại Samsung Galaxy d&ograve;ng S</a>&nbsp;khi mang trong m&igrave;nh một đặc trưng ri&ecirc;ng biệt v&agrave; đầy đẳng cấp.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031926.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế vuông vắn - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031926.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031926.jpg\" title=\"Thiết kế vuông vắn - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>Mặt lưng k&iacute;nh của m&aacute;y vẫn được bao phủ bởi bộ khung kim loại v&agrave; bo tr&ograve;n ở c&aacute;c g&oacute;c, c&ugrave;ng với c&aacute;ch tạo h&igrave;nh phẳng tr&ecirc;n c&aacute;c mặt. Điều n&agrave;y l&agrave;m cho&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại di động đang kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;trở n&ecirc;n sang trọng v&agrave; hiện đại, cực kỳ ph&ugrave; hợp cho những bạn đang muốn c&oacute; một chiếc m&aacute;y bắt trend về ngoại h&igrave;nh ở thời điểm hiện tại.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031929.jpg\" onclick=\"return false;\"><img alt=\"Mặt lưng kính cường lực - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031929.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031929.jpg\" title=\"Mặt lưng kính cường lực - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>Ở lần n&agrave;y Samsung đ&atilde; thực hiện một số thay đổi nhỏ ở phần camera để tạo sự kh&aacute;c biệt so với thế hệ trước. Kh&ocirc;ng c&ograve;n c&aacute;c ống k&iacute;nh nằm chung cụm như trước đ&acirc;y nữa, thay v&agrave;o đ&oacute; l&agrave; một c&aacute;ch sắp xếp ri&ecirc;ng lẻ độc đ&aacute;o, nhờ đ&oacute; m&agrave; m&igrave;nh c&oacute; thể dễ d&agrave;ng nhận biết được đ&acirc;u l&agrave; sản phẩm thuộc thế hệ tiền nhiệm v&agrave; đ&acirc;u mới l&agrave; mẫu Galaxy S23.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031939.jpg\" onclick=\"return false;\"><img alt=\"Cụm camera riêng biệt - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031939.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031939.jpg\" title=\"Cụm camera riêng biệt - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>To&agrave;n bộ bề ngo&agrave;i của m&aacute;y được cấu tạo phần lớn l&agrave; những loại vật liệu cao cấp, ở bộ khung sẽ được l&agrave;m từ nh&ocirc;m v&agrave; ho&agrave;n thiện theo kiểu nhẵn, kh&ocirc;ng chỉ gi&uacute;p tăng độ bền cho m&aacute;y m&agrave; c&ograve;n th&ecirc;m phần nổi bật bởi độ b&oacute;ng bẩy đầy mới mẻ.</p>\r\n\r\n<p>C&ograve;n ở cả hai phần mặt trước v&agrave; mặt sau th&igrave; m&aacute;y đều được l&agrave;m từ k&iacute;nh cường lực (kh&aacute;c cấu tạo v&agrave; c&aacute;ch ho&agrave;n thiện), nhờ đ&oacute; m&agrave; m&igrave;nh cũng đ&atilde; an t&acirc;m hơn trong qu&aacute; tr&igrave;nh sử dụng khi kh&ocirc;ng phải qu&aacute; lo lắng đến c&aacute;c vấn đề về trầy xước.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031934.jpg\" onclick=\"return false;\"><img alt=\"Mặt lưng làm từ kính cường lực - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031934.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031934.jpg\" title=\"Mặt lưng làm từ kính cường lực - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>Điểm ấn tượng tiếp theo về phần thiết kế đ&oacute; ch&iacute;nh l&agrave; c&aacute;ch tối ưu viền m&agrave;n h&igrave;nh, phải n&oacute;i đ&acirc;y được xem l&agrave; một trong những chiếc điện thoại Android hiếm hoi đ&atilde; l&agrave;m rất tốt điều n&agrave;y. Bốn viền tr&ecirc;n m&agrave;n h&igrave;nh được l&agrave;m si&ecirc;u mỏng v&agrave; gần như bằng nhau, cho n&ecirc;n trải nghiệm khi xem phim l&agrave; cực kỳ thoải m&aacute;i, cảm gi&aacute;c như đang sử dụng một chiếc m&aacute;y kh&ocirc;ng viền.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031943.jpg\" onclick=\"return false;\"><img alt=\"Viền màn hình siêu mỏng - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031943.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031943.jpg\" title=\"Viền màn hình siêu mỏng - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<h3>Hiệu năng vượt trội với con chip h&agrave;ng đầu Qualcomm</h3>\r\n\r\n<p>Theo th&ocirc;ng lệ h&agrave;ng năm, Samsung sẽ trang bị l&ecirc;n d&ograve;ng sản phẩm cao cấp của h&atilde;ng những con chip h&agrave;ng đầu thế giới v&agrave;o mỗi dịp ra mắt v&agrave; vi xử l&yacute; Snapdragon 8 Gen 2&nbsp; for Galaxy l&agrave; c&aacute;i t&ecirc;n xuất hiện tr&ecirc;n chiếc m&aacute;y n&agrave;y.</p>\r\n\r\n<p>Đ&acirc;y được xem l&agrave; mẫu chip mới nhất v&agrave; mạnh mẽ nhất của Qualcomm với hiệu năng cực khủng gi&uacute;p bạn c&oacute; thể c&acirc;n mọi loại tựa game. Đ&acirc;y được xem l&agrave; một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-choi-game\" target=\"_blank\" title=\"Tham khảo điện thoại chơi game đang kinh doanh tại Thế Giới Di Động\">điện thoại chơi game</a>&nbsp;gi&uacute;p người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể tự tin chơi tốt mọi tựa game với mức cấu h&igrave;nh tối đa.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031947.jpg\" onclick=\"return false;\"><img alt=\"Trang bị chipset mạnh - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031947.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031947.jpg\" title=\"Trang bị chipset mạnh - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>V&agrave; với cấu h&igrave;nh mạnh mẽ như tr&ecirc;n sẽ ph&ugrave; hợp với những đối tượng n&agrave;o? Theo m&igrave;nh th&igrave; hiệu suất cao như vậy sẽ th&iacute;ch hợp cho những bạn thường xuy&ecirc;n l&agrave;m việc tr&ecirc;n điện thoại như chỉnh sửa nội dung, video v&agrave; h&igrave;nh ảnh m&agrave; kh&ocirc;ng bị giật lag, hay những bạn game thủ với mong muốn t&igrave;m mua một thiết bị c&oacute; hiệu năng cực cao để leo rank c&ugrave;ng bạn b&egrave;.</p>\r\n\r\n<p>Một điểm được kh&aacute; nhiều bạn quan t&acirc;m tr&ecirc;n những chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung đang kinh doanh tại Thế Giới Di Động\">điện thoại Samsung</a>&nbsp;cao cấp c&oacute; trang bị chipset mạnh mẽ từ nh&agrave; Qualcomm l&agrave; về t&igrave;nh trạng sinh nhiệt, thực tế m&igrave;nh cũng d&ugrave;ng m&aacute;y xuy&ecirc;n suốt trong khoảng 2 tiếng để chơi game (ở m&ocirc;i trường nhiệt độ ph&ograve;ng) th&igrave; thấy được m&aacute;y ấm l&ecirc;n tương đối nhanh, tuy nhi&ecirc;n m&aacute;y sẽ duy tr&igrave; nhiệt độ ở mức vừa phải chứ kh&ocirc;ng hề xảy hiện tượng n&oacute;ng r&aacute;t tay như bao lời đồn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031952.jpg\" onclick=\"return false;\"><img alt=\"Tản nhiệt tốt trên thân máy - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031952.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031952.jpg\" title=\"Tản nhiệt tốt trên thân máy - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>V&agrave; đ&oacute; l&agrave; l&uacute;c m&igrave;nh sử dụng m&aacute;y trong t&igrave;nh trạng gần như hết c&ocirc;ng suất của chip, thế cho n&ecirc;n đối với c&aacute;c t&aacute;c vụ cơ bản m&aacute;y ho&agrave;n to&agrave;n b&igrave;nh thường v&agrave; gần như kh&ocirc;ng cảm thấy nhiệt độ từ m&aacute;y sinh ra trong l&uacute;c m&igrave;nh cầm nắm.</p>\r\n\r\n<p>V&igrave; đ&acirc;y l&agrave; mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\" title=\"Tham khảo điện thoại RAM 8GB đang kinh doanh tại Thế Giới Di Động\">điện thoại RAM 8 GB</a>&nbsp;cho n&ecirc;n gần như c&aacute;c t&aacute;c thao t&aacute;c đa nhiệm tr&ecirc;n m&aacute;y hay những l&uacute;c chơi những tựa game đồ họa cao đi chăng nữa, Galaxy S23 cũng ho&agrave;n to&agrave;n c&acirc;n tốt v&agrave; m&igrave;nh cũng chưa hề thấy xảy ra hiện tượng chậm/đơ tr&ecirc;n m&aacute;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031957.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng RAM 8 GB - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031957.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-031957.jpg\" title=\"Dung lượng RAM 8 GB - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<h3>Nhiếp ảnh chuy&ecirc;n nghiệp với bộ ống k&iacute;nh chất lượng</h3>\r\n\r\n<p>Về th&ocirc;ng số camera của Galaxy S23, m&aacute;y được t&iacute;ch hợp bộ 3 ống k&iacute;nh với camera ch&iacute;nh 50 MP, k&egrave;m theo đ&oacute; sẽ l&agrave; hai ống k&iacute;nh phụ với độ ph&acirc;n giải lần lượt l&agrave; 12 MP, 10 MP để bổ sung th&ecirc;m t&iacute;nh năng chụp ảnh g&oacute;c si&ecirc;u rộng v&agrave; tele.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032002.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ 3 camera - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032002.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032002.jpg\" title=\"Hỗ trợ 3 camera - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>Sau một v&agrave;i lần chụp ảnh bằng con Galaxy S23, m&igrave;nh thấy kết quả m&agrave; m&aacute;y mang lại l&agrave;m m&igrave;nh rất ưng, từ m&agrave;u sắc cho đến độ chi tiết đều được thể hiện tốt. Như bức ảnh dưới đ&acirc;y m&igrave;nh c&oacute; chụp từ chế độ mặc định, c&aacute;c chi tiết nhỏ như chữ viết, &ocirc; van tr&ecirc;n gạch cũng được m&aacute;y ghi lại một c&aacute;ch r&otilde; r&agrave;ng v&agrave; ch&acirc;n thực.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032447.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032447.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032447.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032505.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032505.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032505.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>C&ograve;n về phần khả năng xử l&yacute; m&agrave;u tr&ecirc;n Galaxy S23 phải n&oacute;i l&agrave; cực kỳ xuất sắc, ở những tone m&agrave;u n&oacute;ng v&agrave; rực rỡ cũng sẽ được m&aacute;y t&aacute;i tạo lại một c&aacute;ch chuẩn x&aacute;c v&agrave; kh&ocirc;ng bị lẹm m&agrave;u lại với nhau, điều n&agrave;y gi&uacute;p cho bức ảnh trở n&ecirc;n h&agrave;i h&ograve;a v&agrave; tr&ocirc;ng bắt mắt hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032451.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032451.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032451.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032518.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032518.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032518.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>Với chế độ chụp ảnh g&oacute;c si&ecirc;u rộng, theo m&igrave;nh đ&acirc;y vẫn được xem l&agrave; một trong những t&iacute;nh năng m&agrave; Samsung l&agrave;m rất tốt so với một v&agrave;i đối thủ kh&aacute;c tr&ecirc;n thị trường&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Tham khảo điện thoại Android đang kinh doanh tại Thế Giới Di Động\">điện thoại Android</a>&nbsp;hiện nay, g&oacute;c mở của camera mang đến l&agrave; rất lớn, với những trường hợp chụp ảnh cần độ bao qu&aacute;t lớn Galaxy S23 ho&agrave;n to&agrave;n c&oacute; thể đ&aacute;p ứng tốt nhu cầu n&agrave;y d&agrave;nh cho bạn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032456.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032456.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032456.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh những ưu điểm tr&ecirc;n, Galaxy S23 c&ograve;n sở hữu một t&iacute;nh năng cực kỳ hữu dụng đ&oacute; ch&iacute;nh l&agrave; chế độ chụp ảnh tele, được bổ sung th&ecirc;m khả năng zoom quang học n&ecirc;n c&aacute;c bức ảnh chụp ở ti&ecirc;u cự lớn vẫn sẽ mang lại chất lượng ảnh tốt, với độ chi tiết cao gần như tương đồng với chụp bằng camera ch&iacute;nh (so s&aacute;nh về cảm quan g&oacute;c nh&igrave;n, ảnh kh&ocirc;ng zoom).</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032514.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032514.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032514.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<h3>N&acirc;ng cấp trải nghiệm với m&agrave;n h&igrave;nh Dynamic AMOLED 2X</h3>\r\n\r\n<p>Ở mẫu điện thoại cao cấp lần n&agrave;y nh&agrave; Samsung vẫn sẽ tiếp tục sử dụng m&agrave;n h&igrave;nh Dynamic AMOLED 2X, nhờ ưu điểm vượt trội l&agrave; t&aacute;i hiện h&igrave;nh ảnh c&oacute; chiều s&acirc;u v&agrave; m&agrave;u sắc bắt mắt, thế n&ecirc;n m&aacute;y rất ph&ugrave; hợp d&ugrave;ng để xem phim hay chơi c&aacute;c tựa game c&oacute; nội dung h&igrave;nh ảnh đồ họa cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032523.jpg\" onclick=\"return false;\"><img alt=\"Màn hình chất lượng cao - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032523.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032523.jpg\" title=\"Màn hình chất lượng cao - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>C&ograve;n về độ ph&acirc;n giải tr&ecirc;n m&agrave;n h&igrave;nh, h&atilde;ng cũng sẽ kh&ocirc;ng thay đổi g&igrave; so với thế hệ tiền nhiệm, vẫn l&agrave; Full HD+ (1080 x 2340 Pixels) tr&ecirc;n một tấm nền c&oacute; k&iacute;ch thước 6.1 inch. Nhờ vậy m&agrave; mật độ điểm ảnh được tăng cao, gi&uacute;p cho m&aacute;y c&oacute; thể t&aacute;i hiện sắc n&eacute;t mọi h&igrave;nh ảnh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032529.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ độ phân giải cao - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032529.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032529.jpg\" title=\"Hỗ trợ độ phân giải cao - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>C&ograve;n về tần số qu&eacute;t 120 Hz tr&ecirc;n Galaxy S23, đ&acirc;y được xem l&agrave; con số cực kỳ lớn tr&ecirc;n những chiếc điện thoại hiện nay, bởi vậy m&agrave; bất kỳ thao t&aacute;c vuốt chạm n&agrave;o tr&ecirc;n m&aacute;y cũng sẽ được phản hồi nhanh ch&oacute;ng, hoạt ảnh chuyển cảnh cũng tr&ocirc;ng mượt m&agrave; khi chơi những tựa game h&agrave;nh động hay xem phim chất lượng cao.</p>\r\n\r\n<h3>Sử dụng d&agrave;i l&acirc;u với vi&ecirc;n pin lớn lơn</h3>\r\n\r\n<p>Về thời lượng pin, Galaxy S23 cũng đ&atilde; được n&acirc;ng cấp từ 3700 mAh l&ecirc;n 3900 mAh so với mẫu Galaxy S22 v&agrave;o năm ngo&aacute;i, tuy đ&acirc;y l&agrave; một sự n&acirc;ng cấp nhẹ nhưng với vi&ecirc;n pin lớn hơn sẽ gi&uacute;p k&eacute;o d&agrave;i thời gian d&ugrave;ng tốt hơn.</p>\r\n\r\n<p>Đối với c&aacute;c nhu cầu cơ bản như: trả lời th&ocirc;ng b&aacute;o tin nhắn, cuộc gọi, lướt web hay giải tr&iacute; qua những tựa game Li&ecirc;n Qu&acirc;n Mobile, PUBG Mobile một v&agrave;i trận th&igrave; Galaxy S23 mang đến cho m&igrave;nh thời lượng d&ugrave;ng đ&acirc;u đ&oacute; khoảng 7 - 8 giờ sử dụng liền mạch (ở mức độ s&aacute;ng m&agrave;n h&igrave;nh trung b&igrave;nh).</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032535.jpg\" onclick=\"return false;\"><img alt=\"Tích hợp viên pin lớn - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032535.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032535.jpg\" title=\"Tích hợp viên pin lớn - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>Về thời gian sạc của Galaxy S23 th&igrave; m&aacute;y c&oacute; khả năng nạp pin tương đối nhanh, bởi m&aacute;y hỗ trợ sạc pin nhanh với c&ocirc;ng suất tối đa l&agrave; 25 W, v&igrave; vậy m&igrave;nh chỉ mất đ&acirc;u đ&oacute; loanh quanh 1 giờ đồng hồ l&agrave; đ&atilde; c&oacute; ngay một chiếc điện thoại với full dung lượng để sử dụng cho cả ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032541.jpg\" onclick=\"return false;\"><img alt=\"Sử dụng dài lâu - Samsung Galaxy S23 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032541.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/264060/samsung-galaxy-s23-5g-060423-032541.jpg\" title=\"Sử dụng dài lâu - Samsung Galaxy S23 5G 128GB\" /></a></p>\r\n\r\n<p>Với những cải tiến vượt trội c&ugrave;ng diện mạo đổi mới tr&ecirc;n chiếc Galaxy S23 đ&acirc;y c&oacute; thể coi l&agrave; mẫu flagship rất đ&aacute;ng sắm trong năm 2023, giờ đ&acirc;y người d&ugrave;ng c&oacute; thể sở hữu được những bức ảnh chụp chất lượng nhờ bộ ống k&iacute;nh xịn s&ograve; hay những ph&uacute;t gi&acirc;y trải nghiệm mượt m&agrave; tr&ecirc;n c&aacute;c tựa game đồ họa cao.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(22, 'VHL#19', 'Samsung Galaxy Z Flip4 5G 128GB', 14990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung12.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-flip4\" target=\"_blank\" title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\">Samsung Galaxy Z Flip4 128GB</a>&nbsp;đ&atilde; ch&iacute;nh thức ra mắt thị trường c&ocirc;ng nghệ, đ&aacute;nh dấu sự trở lại của Samsung tr&ecirc;n con đường định hướng người d&ugrave;ng về sự tiện lợi tr&ecirc;n những chiếc điện thoại gập. Với độ bền được gia tăng c&ugrave;ng kiểu thiết kế đẹp mắt gi&uacute;p Flip4 trở th&agrave;nh một trong những t&acirc;m điểm s&aacute;ng gi&aacute; cho nửa cuối năm 2022.</h3>\r\n\r\n<h3>Dẫn đầu xu hướng thiết kế mới&nbsp;</h3>\r\n\r\n<p>C&oacute; lẽ điện thoại gập đ&atilde; kh&ocirc;ng c&ograve;n l&agrave; c&aacute;i t&ecirc;n qu&aacute; xa lạ bởi nhiều &ocirc;ng lớn trong ng&agrave;nh sản xuất thiết bị di động đ&atilde; cho ra mắt kh&aacute; nhiều sản phẩm c&oacute; thiết kế tương tự, gần đ&acirc;y nhất th&igrave; c&oacute; sự g&oacute;p mặt của chiếc flagship đến từ nh&agrave; Samsung mang t&ecirc;n Galaxy Z Flip4.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100334.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế cao cấp - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100334.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100334.jpg\" title=\"Thiết kế cao cấp - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Ngay từ những gi&acirc;y ph&uacute;t đầu ti&ecirc;n sử dụng chiếc Galaxy Z Flip4 m&igrave;nh đ&atilde; cảm nhận được sự kh&aacute;c biệt của n&oacute; so với thế hệ trước, m&aacute;y b&acirc;y giờ đ&atilde; vu&ocirc;ng vắn hơn nhờ tạo h&igrave;nh v&aacute;t phẳng ở hai mặt v&agrave; c&aacute;c cạnh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100336.jpg\" onclick=\"return false;\"><img alt=\"Ngoại hình vuông vức - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100336.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100336.jpg\" title=\"Ngoại hình vuông vức - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Phần mặt lưng của m&aacute;y được phủ một lớp nh&aacute;m nhẹ gi&uacute;p cho m&igrave;nh c&oacute; thể cầm nắm chắc tay hơn, hạn chế được t&igrave;nh trạng b&aacute;m dấu v&acirc;n tay trong qu&aacute; tr&igrave;nh sử dụng.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100343.jpg\" onclick=\"return false;\"><img alt=\"Hoàn thiện mặt lưng nhám - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100343.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100343.jpg\" title=\"Hoàn thiện mặt lưng nhám - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Theo như h&atilde;ng c&ocirc;ng bố th&igrave; phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-z\" target=\"_blank\" title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\">điện thoại Galaxy Z</a>&nbsp;n&agrave;y c&oacute; thể gập l&ecirc;n đến 200.000 lần li&ecirc;n tục trong ph&ograve;ng th&iacute; nghiệm. Nếu trung b&igrave;nh một ng&agrave;y bạn gập, mở m&aacute;y khoảng 50 lần th&igrave; mất khoảng 10 năm th&igrave; mới c&oacute; thể đạt đến số lần gập n&agrave;y.</p>\r\n\r\n<p>&nbsp;<a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100345.jpg\" onclick=\"return false;\"><img alt=\"Gập mở chắc chắn - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100345.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100345.jpg\" title=\"Gập mở chắc chắn - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Bộ khung v&agrave; bản lề của sản phẩm được ho&agrave;n thiện từ vật liệu Armor Aluminum cứng c&aacute;p. Li&ecirc;n kết giữa c&aacute;c chi tiết được l&agrave;m kh&iacute;t lại để gi&uacute;p m&aacute;y c&oacute; thể kh&aacute;ng nước tốt hơn với chuẩn IPX8, từ đ&oacute; gi&uacute;p Galaxy Z Flip4 trở th&agrave;nh chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;gập bền bỉ nhất.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100346.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ kháng nước - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100346.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100346.jpg\" title=\"Hỗ trợ kháng nước - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Với những th&ocirc;ng tin tr&ecirc;n cho thấy Samsung Galaxy Z Flip4 l&agrave; một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-chong-nuoc-bui\" target=\"_blank\" title=\"Tham khảo điện thoại kháng nước, bụi đang kianh doanh tại Thế Giới Di Động\">điện thoại&nbsp;kh&aacute;ng nước, bụi</a>&nbsp;c&oacute; độ bền rất cao, v&igrave; thế bạn ho&agrave;n to&agrave;n c&oacute; thể an t&acirc;m trong việc sử dụng thiết bị một khoảng thời gian d&agrave;i.</p>\r\n\r\n<h3>N&acirc;ng tầm trải nghiệm smartphone&nbsp;</h3>\r\n\r\n<p>Với một người th&iacute;ch sử dụng những chiếc điện thoại m&agrave;n h&igrave;nh lớn như m&igrave;nh th&igrave; trở ngại thường gặp đ&oacute; ch&iacute;nh l&agrave; k&iacute;ch thước m&aacute;y kh&aacute; to, g&acirc;y kh&oacute; khăn cho m&igrave;nh trong những l&uacute;c bỏ v&agrave;o t&uacute;i. Thế nhưng điều n&agrave;y lại được khắc phục ho&agrave;n to&agrave;n tr&ecirc;n chiếc Galaxy Z Flip4 bởi sau khi gập th&igrave; chiều d&agrave;i của m&aacute;y chỉ c&ograve;n khoảng 84.9 mm.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100350.jpg\" onclick=\"return false;\"><img alt=\"Gấp gọn tiện lợi - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100350.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100350.jpg\" title=\"Gấp gọn tiện lợi - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Với k&iacute;ch thước nhỏ gọn như vậy th&igrave; m&aacute;y dễ d&agrave;ng nằm gọn trong l&ograve;ng b&agrave;n tay, đ&acirc;y được xem l&agrave; một sản phẩm rất th&iacute;ch hợp d&agrave;nh cho những bạn nữ mong muốn c&oacute; cho m&igrave;nh một thiết bị c&oacute; m&agrave;n h&igrave;nh lớn nhưng vẫn đ&aacute;p ứng được ti&ecirc;u ch&iacute; nhỏ gọn v&agrave; thời trang.</p>\r\n\r\n<p>Một điểm hay tr&ecirc;n phi&ecirc;n bản n&agrave;y l&agrave; về giao diện chụp ảnh chia đ&ocirc;i m&agrave;n h&igrave;nh đ&atilde; hỗ trợ tr&ecirc;n ứng dụng camera b&ecirc;n thứ 3, giờ đ&acirc;y khung h&igrave;nh hiển thị m&agrave;n chụp sẽ được thu nhỏ v&agrave; nằm vừa vặn b&ecirc;n trong nửa m&agrave;n h&igrave;nh c&ograve;n lại để m&igrave;nh dễ d&agrave;ng theo d&otilde;i chất lượng ảnh, từ đ&oacute; c&oacute; thể chủ động hơn trong việc điều chỉnh g&oacute;c chụp.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-f2.jpg\" onclick=\"return false;\"><img alt=\"Tùy biến giao diện chụp ảnh - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-f2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-f2.jpg\" title=\"Tùy biến giao diện chụp ảnh - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Hơn nữa, bạn c&oacute; thể selfie bằng cụm camera sau để n&acirc;ng cao chất lượng ảnh một c&aacute;ch dễ d&agrave;ng nhờ c&oacute; m&agrave;n h&igrave;nh phụ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100353.jpg\" onclick=\"return false;\"><img alt=\"Selfie dễ dàng - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100353.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100353.jpg\" title=\"Selfie dễ dàng - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Một điểm hay nữa ở phần m&agrave;n h&igrave;nh phụ m&agrave; m&igrave;nh muốn chia sẻ với c&aacute;c bạn l&agrave; ở phần t&iacute;nh năng hiển thị, Galaxy Z Flip4 c&oacute; thể đem đến nhiều th&ocirc;ng tin v&agrave; tiện &iacute;ch để bạn c&oacute; thể sử dụng nhanh ch&oacute;ng m&agrave; kh&ocirc;ng cần mở thiết bị, nổi bật trong số đ&oacute; c&oacute; thể kể đến như: Nhạc, lịch, thời tiết, gọi trực tiếp,...</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-f3.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ nhiều tiện ích - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-f3.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-f3.jpg\" title=\"Hỗ trợ nhiều tiện ích - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Điều n&acirc;ng cấp ở phi&ecirc;n bản n&agrave;y so với thế hệ trước l&agrave; giờ đ&acirc;y Galaxy Z Flip4 c&oacute; thể gi&uacute;p bạn nhận trực tiếp cuộc gọi từ m&agrave;n h&igrave;nh phụ (hỗ trợ nghe loa ngo&agrave;i), trả lời tin nhắn bằng văn bản soạn sẵn.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100357.jpg\" onclick=\"return false;\"><img alt=\"Trang bị nhiều tiện ích - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100357.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100357.jpg\" title=\"Trang bị nhiều tiện ích - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Để m&agrave;n h&igrave;nh phụ trở n&ecirc;n th&uacute; vị hơn th&igrave; h&atilde;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung đang kinh doanh tại Thế Giới Di Động\">điện thoại Samsung</a>&nbsp;đ&atilde; bổ sung rất nhiều kiểu m&agrave;n h&igrave;nh để bạn c&oacute; thể t&ugrave;y biến giao diện theo &yacute; th&iacute;ch c&aacute; nh&acirc;n. Kh&ocirc;ng những thế, việc c&agrave;i đặt m&agrave;n h&igrave;nh mang dấu ấn c&aacute; nh&acirc;n cũng đ&atilde; tạo th&ecirc;m điểm nhấn cho chiếc m&aacute;y ở phần mặt lưng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100358.jpg\" onclick=\"return false;\"><img alt=\"Tùy biến màn hình theo ý thích - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100358.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100358.jpg\" title=\"Tùy biến màn hình theo ý thích - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<h3>Sở hữu m&agrave;n h&igrave;nh cao cấp</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh ch&iacute;nh được trang bị một tấm nền Dynamic AMOLED 2X c&oacute; độ ph&acirc;n giải Full HD+ (2640 x 1080 Pixels), ảnh cho ra sẽ c&oacute; m&agrave;u sắc rực rỡ v&agrave; nh&igrave;n rất trong trẻo, mọi nội dung đều được t&aacute;i hiện sắc n&eacute;t.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-man-hinh-dynamic-amoled-2x-tren-smartphone-1245213\" target=\"_blank\">M&agrave;n h&igrave;nh Dynamic AMOLED 2X l&agrave; g&igrave;? Điểm nổi bật? C&oacute; tr&ecirc;n điện thoại n&agrave;o</a></p>\r\n\r\n<p>Nếu như chỉ sử dụng điện thoại cho những t&aacute;c vụ cơ bản th&igrave; dường như m&igrave;nh &iacute;t cảm nhận được sự kh&aacute;c biệt r&otilde; rệt, đặc biệt l&agrave; khi so s&aacute;nh với những tấm nền Super AMOLED của c&aacute;c thiết bị đến từ nh&agrave; Samsung trước đ&acirc;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100400.jpg\" onclick=\"return false;\"><img alt=\"Màn hình chất lượng - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100400.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100400.jpg\" title=\"Màn hình chất lượng - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Tuy nhi&ecirc;n sau khi m&igrave;nh chơi những tựa game c&oacute; đồ họa nhiều m&agrave;u sắc v&agrave; hiệu ứng h&igrave;nh ảnh như Li&ecirc;n Qu&acirc;n Mobile, xem những bộ phim bom tấn đến từ Marvel th&igrave; mọi thứ dường như đ&atilde; l&agrave;m m&igrave;nh cho&aacute;ng ngợp. M&agrave;u sắc phải n&oacute;i l&agrave; rất đ&atilde; mắt v&agrave; ch&acirc;n thực.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100402.jpg\" onclick=\"return false;\"><img alt=\"Màu sắc rực rỡ - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100402.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100402.jpg\" title=\"Màu sắc rực rỡ - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh ch&iacute;nh c&oacute; k&iacute;ch thước đến 6.7 inch n&ecirc;n ta ho&agrave;n to&agrave;n c&oacute; thể sử dụng đồng thời hai app bằng t&iacute;nh năng chia đ&ocirc;i m&agrave;n h&igrave;nh. Ngo&agrave;i ra khi chơi game m&igrave;nh cũng cảm thấy thoải m&aacute;i hơn bởi c&aacute;c ph&iacute;m chức năng được bố tr&iacute; c&aacute;ch xa với nhau m&agrave; kh&ocirc;ng l&agrave;m che đi qu&aacute; nhiều phần hiển thị, l&uacute;c thao t&aacute;c cũng hạn chế được t&igrave;nh trạng ấn nhầm.</p>\r\n\r\n<p>Nhờ c&oacute; độ s&aacute;ng tối đa l&ecirc;n tới 1200 nits n&ecirc;n khi sử dụng ngo&agrave;i trời m&igrave;nh cũng cảm thấy dễ d&agrave;ng hơn, tuy nhi&ecirc;n nếu d&ugrave;ng m&aacute;y ở mức s&aacute;ng n&agrave;y li&ecirc;n tục trong khoảng thời gian d&agrave;i sẽ l&agrave;m giảm thời lượng d&ugrave;ng của vi&ecirc;n pin.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhờ c&oacute; tần số qu&eacute;t 120 Hz tr&ecirc;n tấm nền Dynamic AMOLED 2X n&ecirc;n mọi thao t&aacute;c vuốt chạm ở phần m&agrave;n h&igrave;nh được phản hồi nhanh ch&oacute;ng v&agrave; hết sức mượt m&agrave;. Galaxy Z Flip4 c&oacute; khả năng tự động điều chỉnh tần số qu&eacute;t từ 1 - 120 Hz phụ thuộc v&agrave;o t&aacute;c vụ sử dụng, nhằm tiết kiệm điện năng ti&ecirc;u thụ.</p>\r\n\r\n<h3>Ảnh chụp bắt trọn vẻ đẹp từ mọi khung cảnh</h3>\r\n\r\n<p>Điện thoại được trang bị hai camera sau c&oacute; c&ugrave;ng độ ph&acirc;n giải 12 MP với rất nhiều t&iacute;nh năng chụp ảnh chuy&ecirc;n nghiệp, gi&uacute;p m&igrave;nh c&oacute; thể thỏa sức kh&aacute;m ph&aacute; vẻ đẹp của khung cảnh qua nhiều kiểu ảnh kh&aacute;c nhau.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đầu ti&ecirc;n l&agrave; đến với phần chụp ảnh bằng chế độ mặc định, ảnh cho ra c&oacute; độ chi tiết cao n&ecirc;n d&ugrave; m&igrave;nh c&oacute; ph&oacute;ng to l&ecirc;n 30% th&igrave; ảnh vẫn chưa xuất hiện t&igrave;nh trạng vỡ ảnh.</p>\r\n\r\n<p><i>*Ảnh chụp nguy&ecirc;n bản</i></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100409.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100409.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100409.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p><i>*Ảnh chụp sau khi ph&oacute;ng to</i></p>\r\n\r\n<p>Với những t&igrave;nh huống chụp ảnh ngo&agrave;i trời khi cường độ &aacute;nh s&aacute;ng cao l&agrave;m cho bức ảnh c&oacute; khả năng bị ch&aacute;y s&aacute;ng th&igrave; Galaxy Z Flip4 sẽ tự động điều chỉnh v&agrave; c&acirc;n bằng &aacute;nh s&aacute;ng, ảnh sau khi chụp sẽ được load trong 1 đến 3 gi&acirc;y để m&aacute;y c&oacute; thể sử dụng thuật to&aacute;n xử l&yacute; ảnh th&ocirc;ng minh gi&uacute;p bức ảnh trở n&ecirc;n h&agrave;i h&ograve;a hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100410.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100410.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100410.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Ở những khung cảnh c&oacute; v&ugrave;ng tối - s&aacute;ng ch&ecirc;nh lệch nhau qu&aacute; nhiều th&igrave; Galaxy Z Flip4 cũng c&oacute; thể đem đến cho bạn một bức ảnh đẹp mắt, những v&ugrave;ng tối sẽ được n&acirc;ng s&aacute;ng l&ecirc;n đ&ocirc;i ch&uacute;t v&agrave; tương tự v&ugrave;ng s&aacute;ng cũng sẽ được giảm lại để c&acirc;n bằng với nhau.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100412.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100412.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100412.jpg\" title=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>T&iacute;nh năng m&agrave; m&igrave;nh ấn tượng nhất ch&iacute;nh l&agrave; chụp ảnh x&oacute;a ph&ocirc;ng tr&ecirc;n camera sau, chủ thể được t&aacute;ch nền cực kỳ mịn ở m&ocirc;i trường đủ s&aacute;ng, với cảnh chụp c&oacute; nhiều chi tiết nhỏ th&igrave; m&aacute;y vẫn c&oacute; thể xử l&yacute; v&agrave; t&aacute;ch nền hiệu quả.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chuyển qua phần camera g&oacute;c si&ecirc;u rộng th&igrave; ảnh thu nhiều kh&ocirc;ng gian hơn, ph&ugrave; hợp để chụp những khung ảnh rộng lớn. Chất lượng ảnh ở c&aacute;c g&oacute;c kh&ocirc;ng bị b&oacute;p m&eacute;o qu&aacute; nhiều.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhờ c&oacute; chế độ chụp ảnh zoom n&ecirc;n khi m&igrave;nh chụp c&aacute;c vật thể ở khoảng c&aacute;ch xa bằng Samsung Galaxy Z Flip4 cũng trở n&ecirc;n dễ d&agrave;ng hơn, tiết kiệm thời gian khi kh&ocirc;ng phải thường xuy&ecirc;n di chuyển để chọn g&oacute;c chụp, nhưng chất lượng ảnh cho ra vẫn c&oacute; độ chi tiết cao.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&oacute;i đến phần chụp ảnh đ&ecirc;m tr&ecirc;n Galaxy Z Flip4 phải n&oacute;i l&agrave; rất tốt khi hiện tượng nhiễu hạt dường như kh&ocirc;ng xuất hiện tr&ecirc;n bức ảnh (ảnh gốc), t&igrave;nh trạng ch&aacute;y s&aacute;ng ở những nơi c&oacute; độ s&aacute;ng cao cũng được cải thiện so với thế hệ trước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n khả năng thu s&aacute;ng của m&aacute;y theo m&igrave;nh cảm nhận l&agrave; chưa được tốt cho lắm, nh&igrave;n tổng quan vẫn c&ograve;n kh&aacute; tối ở những v&ugrave;ng thiếu s&aacute;ng.</p>\r\n\r\n<h3>Sức mạnh đ&aacute;ng kinh ngạc đến từ Qualcomm</h3>\r\n\r\n<p>Trang bị tr&ecirc;n chiếc flagship n&agrave;y l&agrave; một bộ vi xử l&yacute; cao cấp mới nhất đến từ nh&agrave; Qualcomm với t&ecirc;n gọi Snapdragon 8+ Gen 1 v&agrave; xung nhịp tối đa đạt v&agrave;o khoảng 3.18 GHz. M&igrave;nh c&oacute; test qua một v&agrave;i phần mềm chấm điểm hiệu năng với những con số sau khi đo được như sau: 1283 (đơn nh&acirc;n), 3748 (đa nh&acirc;n) tr&ecirc;n Benchmark v&agrave; 14223 tr&ecirc;n PCMark, quả l&agrave; một con số qu&aacute; kinh khủng tr&ecirc;n giới smartphone.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đi k&egrave;m với CPU mạnh mẽ th&igrave; đ&acirc;y cũng l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\" title=\"Tham khảo điện thoại có RAM 8 GB đang kinh doanh tại Thế Giới Di Động\">điện thoại RAM 8 GB</a>*&nbsp;chạy tr&ecirc;n hệ điều h&agrave;nh Android 12 v&agrave; được t&ugrave;y biến ri&ecirc;ng theo nh&agrave; Samsung, gi&uacute;p mang lại một giao diện đẹp mắt cũng như n&acirc;ng cao trải nghiệm tr&ecirc;n một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-choi-game\" target=\"_blank\" title=\"Tham khảo điện thoại chơi game đang kinh doanh tại Thế Giới Di Động\">điện thoại chơi game</a>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><i>*C&oacute; hỗ trợ t&iacute;nh năng mở rộng RAM l&ecirc;n tới 8 GB</i></p>\r\n\r\n<p>Đến với phần trải nghiệm chơi game th&igrave; Snapdragon 8+ Gen 1 đ&atilde; bộc lộ gần như hết sức mạnh của chip, điều n&agrave;y được minh chứng trong l&uacute;c m&igrave;nh chơi tựa game Li&ecirc;n Qu&acirc;n Mobile ở mức đồ họa cao nhất. H&igrave;nh ảnh cho ra phải n&oacute;i l&agrave; cực kỳ đ&atilde; mắt, m&agrave;u sắc rực rỡ n&ecirc;n m&igrave;nh c&oacute; thể cảm nhận được vẻ đẹp tr&ecirc;n từng hiệu ứng kỹ năng của nh&acirc;n vật.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100425.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình game - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100425.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100425.jpg\" title=\"Cấu hình game - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Về phần trải nghiệm th&igrave; Galaxy Z Flip4 mang đến cho m&igrave;nh một sự ổn định xuy&ecirc;n suốt qu&aacute; tr&igrave;nh chơi, tốc độ khung h&igrave;nh sau khi đo bằng phần mềm th&igrave; kết quả được ghi nhận v&agrave;o khoảng 59.8 FPS.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100427.jpg\" onclick=\"return false;\"><img alt=\"Tốc độ khung hình - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100427.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100427.jpg\" title=\"Tốc độ khung hình - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>C&ograve;n đối với tựa game PUBG Mobile ở mức đồ họa HDR c&ugrave;ng tốc độ khung h&igrave;nh cực độ, h&igrave;nh ảnh nh&acirc;n vật v&agrave; quang cảnh c&oacute; đổ b&oacute;ng l&agrave;m cho m&igrave;nh cảm nhận được sự ch&acirc;n thật tr&ecirc;n từng chuyển động. K&egrave;m với đ&oacute; l&agrave; sự hỗ trợ đến từ m&agrave;n h&igrave;nh tần số qu&eacute;t cao n&ecirc;n mọi hiệu ứng di chuyển, giao tranh được xử l&yacute; mượt m&agrave; hơn rất nhiều.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100428.jpg\" onclick=\"return false;\"><img alt=\"Tốc độ khung hình - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100428.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100428.jpg\" title=\"Tốc độ khung hình - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>Cảm nhận về phần nhiệt độ của m&aacute;y th&igrave; m&igrave;nh thấy Galaxy Z Flip4 kh&ocirc;ng thực sự qu&aacute; n&oacute;ng khi được trang bị một con chip c&oacute; hiệu suất khủng, mọi thứ chỉ dừng ở mức ấm tay sau khi m&igrave;nh chơi game tầm 40 - 50 ph&uacute;t li&ecirc;n tục. C&ograve;n đối với c&aacute;c t&aacute;c vụ phổ th&ocirc;ng kh&aacute;c th&igrave; nhiệt độ của m&aacute;y gần như kh&ocirc;ng c&oacute; sự thay đổi so với b&igrave;nh thường.</p>\r\n\r\n<h3>Hỗ trợ sạc pin nhanh ch&oacute;ng</h3>\r\n\r\n<p>M&igrave;nh đ&atilde; từng cho rằng vi&ecirc;n pin c&oacute; dung lượng 3700 mAh sẽ kh&ocirc;ng đủ đ&aacute;p ứng một ng&agrave;y sử dụng của m&igrave;nh, nhưng sau khi kiểm chứng bằng c&aacute;ch d&ugrave;ng m&aacute;y li&ecirc;n tục cho mọi t&aacute;c vụ từ chụp ảnh, xem phim, lướt web v&agrave; chơi game th&igrave; Galaxy Z Flip4 lại c&oacute; thể đ&aacute;p ứng thời lượng sử dụng cho tới 6 tiếng 47 ph&uacute;t* rất ấn tượng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100430.jpg\" onclick=\"return false;\"><img alt=\"Sử dụng dài lâu - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100430.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100430.jpg\" title=\"Sử dụng dài lâu - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p><i>*Thời gian c&oacute; thể thay đổi t&ugrave;y v&agrave;o t&aacute;c vụ sử dụng</i></p>\r\n\r\n<p>Nhờ c&oacute; c&ocirc;ng nghệ sạc nhanh 25 W n&ecirc;n m&igrave;nh chỉ mất khoảng 1 giờ 30 ph&uacute;t* l&agrave; c&oacute; thể sạc đầy được vi&ecirc;n pin tr&ecirc;n Galaxy Z Flip4. Với một v&agrave;i trường hợp gấp cần sử dụng th&igrave; chỉ cần đợi 30 ph&uacute;t l&agrave; m&igrave;nh đ&atilde; c&oacute; ngay 37% pin để sử dụng (tất cả đều sạc khi m&aacute;y ở trạng th&aacute;i 0% pin). Thực sự l&agrave; một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo điện thoại sạc pin nhanh đang kinh doanh tại Thế Giới Di Động\">điện thoại sạc pin nhanh</a>&nbsp;đ&aacute;ng sắm để tối ưu c&ocirc;ng việc nhanh ch&oacute;ng.</p>\r\n\r\n<p>Chia sẻ pin kh&ocirc;ng d&acirc;y l&agrave; t&iacute;nh năng kh&aacute; tiện lợi đối với một người đang sử dụng một chiếc smartwatch như m&igrave;nh (c&oacute; hỗ trợ sạc kh&ocirc;ng d&acirc;y), khi đồng hồ hết pin th&igrave; m&igrave;nh chỉ việc đặt thiết bị l&ecirc;n tr&ecirc;n mặt lưng của Galaxy Z Flip4 l&agrave; đ&atilde; c&oacute; thể k&iacute;ch hoạt t&iacute;nh năng tr&ecirc;n (cần k&iacute;ch hoạt t&iacute;nh năng trước ở trong phần c&agrave;i đặt).</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100432.jpg\" onclick=\"return false;\"><img alt=\"Chia sẻ pin không dây - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100432.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100432.jpg\" title=\"Chia sẻ pin không dây - Samsung Galaxy Z Flip4\" /></a></p>\r\n\r\n<p>L&agrave; một người d&ugrave;ng trẻ trung năng động hay y&ecirc;u th&iacute;ch c&aacute;c loại điện thoại dẫn đầu xu hướng th&igrave; chiếc Samsung Galaxy Z Flip4 sẽ l&agrave; c&aacute;i t&ecirc;n m&agrave; bạn kh&ocirc;ng n&ecirc;n bỏ qua, nhờ c&oacute; cơ chế gập v&ocirc; c&ugrave;ng độc đ&aacute;o k&egrave;m với bộ th&ocirc;ng số kỹ thuật hết sức mạnh mẽ n&ecirc;n m&aacute;y hứa hẹn sẽ mang đến cho bạn v&ocirc; v&agrave;n trải nghiệm th&uacute; vị.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(23, 'VHL#20', 'Samsung Galaxy A54 5G 128GB', 9090000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung13.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a54-5g-128gb\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy A54 5G 128 GB đang kinh doanh tại Thế Giới Di Động\">Samsung Galaxy A54 5G 128GB</a>&nbsp;l&agrave; mẫu điện thoại tầm trung hiện đang l&agrave;m mưa l&agrave;m gi&oacute; tại thị trường Việt Nam khi sở hữu những th&ocirc;ng số ấn tượng với chip hiệu năng cao, m&agrave;n h&igrave;nh chất lượng đi c&ugrave;ng hệ thống camera xịn s&ograve; trong ph&acirc;n kh&uacute;c.</h3>\r\n\r\n<h3>Sở hữu khả năng chụp đ&ecirc;m ấn tượng</h3>\r\n\r\n<p>Sau hơn 2 tuần trải nghiệm chiếc Galaxy A54, m&igrave;nh thực sự đ&atilde; rất ngạc nhi&ecirc;n về khả năng chụp ảnh của m&aacute;y khi đ&acirc;y chỉ l&agrave; một chiếc điện thoại tầm trung nhưng lại cho ra chất lượng ảnh c&oacute; thể xem như tiệm cận một v&agrave;i sản phẩm thuộc d&ograve;ng flagship.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125643.jpg\" onclick=\"return false;\"><img alt=\"Trang bị 3 camera - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125643.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125643.jpg\" title=\"Trang bị 3 camera - Sở hữu khả năng chụp đêm ấn tượng\" /></a></p>\r\n\r\n<p>Điện thoại được trang bị hệ thống camera gồm 3 ống k&iacute;nh chất lượng nhằm mang đến trải nghiệm chụp ảnh v&agrave; quay video ấn tượng. Camera ch&iacute;nh được n&acirc;ng cấp l&ecirc;n độ ph&acirc;n giải 50 MP cho ra những h&igrave;nh ảnh sắc n&eacute;t, chi tiết đến từng điểm ảnh.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, camera g&oacute;c si&ecirc;u rộng 12 MP cho ph&eacute;p bạn thu được g&oacute;c nh&igrave;n rộng, bao qu&aacute;t khung cảnh. Đặc biệt, camera macro 2 MP sẽ khiến những chủ thể nhỏ nhất cũng trở n&ecirc;n sống động.</p>\r\n\r\n<p>Galaxy A54 c&ograve;n được trang bị c&ocirc;ng nghệ chống rung OIS ti&ecirc;n tiến cho camera ch&iacute;nh. Điều n&agrave;y gi&uacute;p m&igrave;nh c&oacute; thể chụp ảnh v&agrave; quay video m&agrave; kh&ocirc;ng phải lo lắng về hiện tượng nh&ograve;e do rung hoặc lắc trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125647.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ nhiều tính năng phụ - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125647.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125647.jpg\" title=\"Hỗ trợ nhiều tính năng phụ - Sở hữu khả năng chụp đêm ấn tượng\" /></a></p>\r\n\r\n<p>Trong điều kiện đầy đủ &aacute;nh s&aacute;ng tự nhi&ecirc;n, ảnh chụp từ điện thoại cho ra c&oacute; độ chi tiết cao, m&agrave;u sắc được t&aacute;i tạo lại một c&aacute;ch tươi mới v&agrave; v&ocirc; c&ugrave;ng sống động, những v&ugrave;ng s&aacute;ng tối tr&ecirc;n bức ảnh được c&acirc;n bằng một c&aacute;ch h&agrave;i h&ograve;a gi&uacute;p cho tổng thể trở n&ecirc;n đẹp mắt v&agrave; chuy&ecirc;n nghiệp.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125645.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125645.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125645.jpg\" title=\"Ảnh chụp từ điện thoại - Sở hữu khả năng chụp đêm ấn tượng\" /></a></p>\r\n\r\n<p><em>Ảnh chụp mặc định</em></p>\r\n\r\n<p>Nhờ c&oacute; độ chi tiết cao n&ecirc;n ảnh chụp sau khi ph&oacute;ng to l&ecirc;n cũng kh&ocirc;ng bị bể qu&aacute; nhiều, rất ph&ugrave; hợp cho những t&igrave;nh huống m&igrave;nh cần thay đổi k&iacute;ch thước khung h&igrave;nh hay muốn ph&oacute;ng lớn chủ thể để l&agrave;m cho bức ảnh trở n&ecirc;n nổi bật hơn. Cụ thể th&igrave; m&igrave;nh cũng đ&atilde; zoom bức ảnh b&ecirc;n tr&ecirc;n lớn hơn khoảng 30% nhưng chất lượng ảnh vẫn kh&ocirc;ng hề bị giảm đi đ&aacute;ng kể so với bức ảnh gốc khi nh&igrave;n bằng mắt thường.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125649.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125649.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125649.jpg\" title=\"Ảnh chụp từ điện thoại - Sở hữu khả năng chụp đêm ấn tượng\" /></a></p>\r\n\r\n<p><em>Ảnh chụp sau khi ph&oacute;ng to</em></p>\r\n\r\n<p>D&ugrave; trong m&ocirc;i trường chỉ c&oacute; &aacute;nh s&aacute;ng từ đ&egrave;n, Galaxy A54 vẫn mang đến cho m&igrave;nh những bức ảnh rất ấn tượng. Độ chi tiết của c&aacute;c bức ảnh gần như kh&ocirc;ng thay đổi so với khi chụp ở nơi c&oacute; &aacute;nh s&aacute;ng tự nhi&ecirc;n. M&agrave;u sắc v&agrave; độ tương phản được điều chỉnh một c&aacute;ch kh&eacute;o l&eacute;o, tạo n&ecirc;n sự c&acirc;n đối v&agrave; h&agrave;i h&ograve;a, khiến tổng thể của c&aacute;c bức ảnh trở n&ecirc;n v&ocirc; c&ugrave;ng đẹp mắt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125651.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125651.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125651.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p>Galaxy A54 thực sự l&agrave; một chiến thần chụp đ&ecirc;m h&agrave;ng đầu trong ph&acirc;n kh&uacute;c điện thoại tầm trung, khi c&aacute;c bức ảnh chụp trong điều kiện thiếu s&aacute;ng vẫn giữ được độ chi tiết nhờ khả năng t&aacute;i tạo &aacute;nh s&aacute;ng v&agrave; giảm nhiễu được Samsung tối ưu rất tốt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125656.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125656.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125656.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p><em>Ảnh chụp ở chế độ mặc định</em></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125657.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125657.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125657.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p><em>Ảnh chụp ở chế độ ban đ&ecirc;m</em></p>\r\n\r\n<p>Cụ thể, m&igrave;nh đ&atilde; thực hiện một buổi chụp qua nhiều khung giờ kh&aacute;c nhau, nhằm theo d&otilde;i khả năng chụp đ&ecirc;m thực tế v&agrave; t&igrave;m ra xem đ&acirc;u l&agrave; thời điểm th&iacute;ch hợp để chụp được những bức ảnh ấn tượng nhất tr&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-a\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy dòng A tại Thế Giới Di Động\">điện thoại Samsung d&ograve;ng A</a>&nbsp;n&agrave;y.</p>\r\n\r\n<p>Đầu ti&ecirc;n l&agrave; v&agrave;o khoảng 06:00 PM, khi bầu trời chuyển dần sang m&agrave;u tối v&agrave; hiện ra những vệt s&aacute;ng hồng - cam. Chiếc điện thoại Samsung kh&ocirc;ng chỉ thu được m&agrave;u sắc ch&acirc;n thực m&agrave; c&ograve;n giữ được độ chi tiết cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125652.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125652.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125652.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125654.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125654.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125654.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p>Tiếp đến l&agrave; v&agrave;o 07:00 PM, khung cảnh l&uacute;c n&agrave;y cũng đ&atilde; bắt đầu tối hẳn v&agrave; gần như kh&ocirc;ng c&ograve;n &aacute;nh s&aacute;ng tự nhi&ecirc;n, tổng quan bức ảnh vẫn rất s&aacute;ng v&agrave; dễ nh&igrave;n, hiện tượng nhiễu hạt cũng &iacute;t xuất hiện, những vị tr&iacute; c&oacute; nguồn s&aacute;ng lớn như b&oacute;ng đ&egrave;n cũng kh&ocirc;ng bị l&oacute;a qu&aacute; nhiều, m&igrave;nh ho&agrave;n to&agrave;n c&oacute; thể d&ugrave;ng bức ảnh n&agrave;y để đăng tải trực tiếp l&ecirc;n mạng x&atilde; hội m&agrave; kh&ocirc;ng cần qua th&ecirc;m bước hậu kỳ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125659.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125659.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125659.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p>V&agrave;o khoảng 08:00 PM, l&uacute;c n&agrave;y m&igrave;nh chọn một vị tr&iacute; c&oacute; &aacute;nh s&aacute;ng phức tạp để thực hiện b&agrave;i test, m&igrave;nh đ&atilde; sử dụng điện thoại để chụp ở 3 chế độ kh&aacute;c nhau rồi so s&aacute;nh kết quả, thứ tự lần lượt l&agrave; chụp bằng camera g&oacute;c si&ecirc;u rộng, camera thường v&agrave; cuối c&ugrave;ng l&agrave; zoom 3X.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125701.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125701.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125701.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p><em>Ảnh chụp từ chế độ mặc định</em></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125703.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125703.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125703.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p><em>Ảnh chụp từ chế độ g&oacute;c si&ecirc;u rộng</em></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125705.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125705.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125705.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p><em>Ảnh chụp từ chế độ zoom 3X</em></p>\r\n\r\n<p>Kết quả của những bức ảnh chụp ở 3 chế độ n&agrave;y đều ấn tượng, kh&ocirc;ng bị vỡ điểm ảnh hay nhiễu hạt qu&aacute; nhiều. V&agrave; d&ugrave; trong điều kiện thiếu s&aacute;ng, Galaxy A54 vẫn cho thấy tốc độ lấy n&eacute;t đ&aacute;ng kinh ngạc khi bức ảnh được chụp gần như ngay lập tức m&agrave; kh&ocirc;ng c&oacute; độ trễ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125706.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125706.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125706.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125708.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125708.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125708.jpg\" title=\"Ảnh chụp từ điện thoại - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p>Điều n&agrave;y khiến điện thoại trở th&agrave;nh một lựa chọn l&yacute; tưởng để chụp ảnh đường phố v&agrave;o ban đ&ecirc;m, với khả năng bắt những khoảnh khắc s&ocirc;i động v&agrave; tạo n&ecirc;n những bức ảnh độc đ&aacute;o trong m&ocirc;i trường &aacute;nh s&aacute;ng yếu.</p>\r\n\r\n<h3>Ấn tượng với thiết kế đột ph&aacute;</h3>\r\n\r\n<p>Galaxy A54 mang đậm cảm hứng thiết kế từ những phi&ecirc;n bản flagship như Galaxy S23 hay Galaxy S23 Plus với diện mạo vu&ocirc;ng vắn v&agrave; tinh tế. Cụm camera cũng được đặt c&ugrave;ng kiểu với c&aacute;c phi&ecirc;n bản cao cấp tạo n&ecirc;n phong c&aacute;ch đặc trưng v&agrave;&nbsp;thống nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Được ho&agrave;n thiện từ bộ khung nhựa chắc chắn v&agrave; mặt lưng k&iacute;nh cường lực Gorilla Glass 5 cao cấp,&nbsp;Galaxy A54 mang một vẻ ngo&agrave;i thời thượng nhưng vẫn đảm bảo trọng lượng kh&ocirc;ng qu&aacute; nặng.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ti&ecirc;u chuẩn kh&aacute;ng nước v&agrave; bụi IP67 của Galaxy A54 mang đến cho m&igrave;nh sự y&ecirc;n t&acirc;m hơn trong việc sử dụng hằng ng&agrave;y. Theo như nh&agrave; sản xuất, chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;n&agrave;y c&oacute; khả năng kh&aacute;ng nước ở độ s&acirc;u tối đa 1 m&eacute;t trong thời gian l&ecirc;n đến 30 ph&uacute;t. Điều n&agrave;y c&oacute; nghĩa l&agrave; nếu v&ocirc; t&igrave;nh rơi xuống bể bơi hoặc bồn rửa, m&igrave;nh kh&ocirc;ng cần phải lo lắng qu&aacute; nhiều về việc m&aacute;y sẽ bị hỏng do v&agrave;o nước.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>H&atilde;ng đ&atilde; tạo ra một thiết kế cụm camera tối giản tr&ecirc;n Galaxy A54 với việc bố tr&iacute; 3 ống k&iacute;nh theo kiểu ri&ecirc;ng lẻ. Với mỗi ống k&iacute;nh, h&atilde;ng đ&atilde; sử dụng viền kim loại nh&ocirc; cao để bảo vệ tốt hơn cho mặt k&iacute;nh camera, hạn chế t&igrave;nh trạng trầy xước khi đặt m&aacute;y xuống c&aacute;c mặt phẳng.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ở phần mặt trước, Samsung đ&atilde; &aacute;p dụng thiết kế m&agrave;n h&igrave;nh dạng nốt ruồi cho Galaxy A54, với 3 cạnh viền được tối ưu mỏng v&agrave; c&acirc;n đối. Một điểm trừ nhỏ l&agrave; phần viền dưới c&ograve;n hơi d&agrave;y nhưng cũng kh&ocirc;ng g&acirc;y ảnh hưởng nhiều đến trải nghiệm xem của m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>N&acirc;ng cao chất lượng hiển thị với m&agrave;n h&igrave;nh Super AMOLED</h3>\r\n\r\n<p>Galaxy A54 được trang bị một m&agrave;n h&igrave;nh Super AMOLED cao cấp đạt độ ph&acirc;n giải Full HD+ (1080 x 2340 Pixels), mang đến chất lượng h&igrave;nh ảnh tuyệt vời. Mọi nội dung hiển thị tr&ecirc;n m&agrave;n h&igrave;nh đều được t&aacute;i hiện một c&aacute;ch r&otilde; n&eacute;t, m&agrave;u sắc rực rỡ v&agrave; m&agrave;u đen s&acirc;u.&nbsp;Điều n&agrave;y gi&uacute;p trải nghiệm của m&igrave;nh khi xem phim, video v&agrave; h&igrave;nh ảnh trở n&ecirc;n thư gi&atilde;n v&agrave; sống động hơn bao giờ hết.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hơn nữa, tần số qu&eacute;t 120 Hz cũng gi&uacute;p h&igrave;nh ảnh chuyển động trở n&ecirc;n cực kỳ mượt m&agrave;. Hiện tượng b&oacute;ng mờ hay giật lag với c&aacute;c khung h&igrave;nh chuyển động nhanh cũng được giảm thiểu tối đa.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Galaxy A54 đạt mức độ s&aacute;ng tối đa l&ecirc;n đến 1000 nits, do đ&oacute; m&agrave;n h&igrave;nh c&oacute; khả năng hiển thị nội dung một c&aacute;ch r&otilde; n&eacute;t ngay cả khi m&igrave;nh sử dụng điện thoại ngo&agrave;i trời dưới &aacute;nh s&aacute;ng mạnh, gi&uacute;p m&igrave;nh c&oacute; thể đọc bản đồ, xem th&ocirc;ng b&aacute;o một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n, khi chuyển sang giao diện tối, độ s&aacute;ng của m&agrave;n h&igrave;nh sẽ giảm xuống khiến cho việc sử dụng điện thoại trở n&ecirc;n kh&oacute; khăn hơn một ch&uacute;t. Theo m&igrave;nh chế độ n&agrave;y chỉ n&ecirc;n sử dụng v&agrave;o ban đ&ecirc;m khi kh&ocirc;ng cần độ s&aacute;ng cao.&nbsp;</p>\r\n\r\n<h3>Tối ưu hiệu suất tốt với con chip Exynos thế hệ mới</h3>\r\n\r\n<p>Galaxy A54 được trang bị con chip xử l&yacute; Exynos 1380, đ&acirc;y ch&iacute;nh l&agrave; mẫu chip được ch&iacute;nh Samsung nghi&ecirc;n cứu v&agrave; ph&aacute;t triển trong đầu năm 2023, được định h&igrave;nh l&agrave; sản phẩm chuy&ecirc;n d&agrave;nh cho c&aacute;c m&aacute;y tầm trung khi c&oacute; mức xung nhịp kh&aacute; cao v&agrave;o khoảng 2.4 GHz.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ hoạt động mượt m&agrave; trong c&aacute;c c&ocirc;ng việc h&agrave;ng ng&agrave;y như nhắn tin, lướt web v&agrave; xem phim, chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Tham khảo điện thoại Android đang kinh doanh tại Thế Giới Di Động\">điện thoại Android</a>&nbsp;n&agrave;y&nbsp;c&ograve;n đạt hiệu suất ổn định m&agrave; kh&ocirc;ng gặp t&igrave;nh trạng đơ m&aacute;y,&nbsp;giật lag trong qu&aacute; tr&igrave;nh đa nhiệm hay chơi một v&agrave;i tựa game cơ bản như Temple Run, Subway Surfers.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&igrave;nh đ&atilde; tiến h&agrave;nh thử nghiệm với một số tựa game phổ biến để thu thập th&ocirc;ng tin chi tiết v&agrave; cung cấp cho bạn c&aacute;i nh&igrave;n s&acirc;u hơn về hiệu năng của m&aacute;y.</p>\r\n\r\n<p>Thực tế th&igrave; tr&ecirc;n tựa game Li&ecirc;n Qu&acirc;n Mobile, m&igrave;nh đ&atilde; tối đa h&oacute;a cấu h&igrave;nh đồ họa, gi&uacute;p h&igrave;nh ảnh hiển thị tr&ecirc;n m&agrave;n h&igrave;nh sống động hơn. Tốc độ khung h&igrave;nh được duy tr&igrave; ổn định xung quanh con số 60 đến 61 FPS, v&agrave; m&igrave;nh kh&ocirc;ng gặp bất kỳ hiện tượng giật lag hay drop FPS qu&aacute; s&acirc;u n&agrave;o trong qu&aacute; tr&igrave;nh tham gia trận chiến.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125728.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125728.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125728.jpg\" title=\"Trải nghiệm chơi game - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125729.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125729.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125729.jpg\" title=\"Trải nghiệm chơi game - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p>Với tựa game PUBG Mobile, m&igrave;nh đ&atilde; tinh chỉnh đồ họa l&ecirc;n chế độ HD v&agrave; tăng tốc độ khung h&igrave;nh ở mức cao để đảm bảo chất lượng h&igrave;nh ảnh tối ưu. Tuy nhi&ecirc;n, trải nghiệm vẫn chưa thực sự thỏa m&atilde;n, cảm gi&aacute;c khi thao t&aacute;c điều hướng nh&acirc;n vật vẫn chưa ho&agrave;n to&agrave;n mượt m&agrave;, v&agrave; tốc độ khung h&igrave;nh chỉ dao động khoảng 30 FPS. V&igrave; vậy, để c&oacute; trải nghiệm tốt hơn, c&oacute; lẽ ta n&ecirc;n giảm đồ họa xuống mức thấp hơn trong tựa game n&agrave;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125731.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125731.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125731.jpg\" title=\"Trải nghiệm chơi game - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125733.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm chơi game - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125733.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125733.jpg\" title=\"Trải nghiệm chơi game - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\" title=\"Tham khảo điện thoại RAM 8 GB đang kinh doanh tại Thế Giới Di Động\">điện thoại RAM 8 GB</a>&nbsp;cũng sở hữu khả năng mở rộng th&ecirc;m 8 GB RAM (tổng 16 GB), nhờ vậy những t&aacute;c vụ đa nhiệm tr&ecirc;n điện thoại diễn ra hết sức mượt m&agrave;, m&igrave;nh c&oacute; thử mở nhiều ứng dụng c&ugrave;ng một l&uacute;c nhưng t&igrave;nh trạng tải lại từ đầu cũng &iacute;t khi xảy ra.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125735.jpg\" onclick=\"return false;\"><img alt=\"Trang bị RAM lớn - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125735.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125735.jpg\" title=\"Trang bị RAM lớn - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<h3>Trang bị t&iacute;nh năng sạc nhanh v&agrave; vi&ecirc;n pin lớn</h3>\r\n\r\n<p>Với vi&ecirc;n pin dung lượng 5000 mAh, m&igrave;nh ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m khi sử dụng Galaxy A54 l&agrave;m thiết bị ch&iacute;nh cho mọi nhu cầu h&agrave;ng ng&agrave;y. Trong qu&aacute; tr&igrave;nh trải nghiệm của m&igrave;nh th&igrave; thời lượng pin của m&aacute;y k&eacute;o d&agrave;i khoảng 8 giờ 24 ph&uacute;t cho c&aacute;c t&aacute;c vụ như xem phim, nghe nhạc, chơi game v&agrave; lướt web.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125736.jpg\" onclick=\"return false;\"><img alt=\"Thời gian sử dụng dài lâu - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125736.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125736.jpg\" title=\"Thời gian sử dụng dài lâu - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p>Galaxy A54 cũng được trang bị t&iacute;nh năng sạc pin nhanh, cho ph&eacute;p sạc với c&ocirc;ng suất tối đa 25 W. Điều n&agrave;y c&oacute; nghĩa l&agrave; để ho&agrave;n to&agrave;n nạp đầy vi&ecirc;n pin dung lượng lớn th&igrave; m&aacute;y phải mất khoảng 2 giờ 27 ph&uacute;t.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125738.jpg\" onclick=\"return false;\"><img alt=\"Trang bị tính năng sạc siêu nhanh - Samsung Galaxy A54 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125738.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/303585/samsung-galaxy-a54-5g-128gb-040623-125738.jpg\" title=\"Trang bị tính năng sạc siêu nhanh - Samsung Galaxy A54 5G 128GB\" /></a></p>\r\n\r\n<p>Nếu bạn l&agrave; một người bận rộn, bạn c&oacute; thể sắp xếp thời gian sạc pin khi kh&ocirc;ng cần sử dụng điện thoại nhiều, nhằm tr&aacute;nh mất qu&aacute; nhiều thời gian chờ đợi. Thực tế, thời gian sạc pin khoảng 2 giờ 27 ph&uacute;t l&agrave; một con số kh&aacute; l&acirc;u với một chiếc điện thoại tầm trung.</p>\r\n\r\n<p>Với những g&igrave; đ&atilde; c&oacute; được, Galaxy A54 xứng đ&aacute;ng l&agrave; một lựa chọn đ&aacute;ng c&acirc;n nhắc cho người d&ugrave;ng trong ph&acirc;n kh&uacute;c tầm trung - cận cao cấp nhờ thiết kế thời thượng, đẹp mắt đi c&ugrave;ng hiệu năng tốt v&agrave; hệ thống camera chất lượng, đ&aacute;p ứng mọi nhu cầu sử dụng hằng ng&agrave;y.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 0),
(24, 'VHL#21', 'Samsung Galaxy A53 5G 128GB', 6990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung14.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a53\" target=\"_blank\" title=\"Samsung Galaxy A53 5G 128GB\">Samsung Galaxy A53 5G 128GB</a>&nbsp;tr&igrave;nh l&agrave;ng với một thiết kế hiện đại, hiệu năng ổn định c&ugrave;ng một m&agrave;n h&igrave;nh hiển thị sắc n&eacute;t, hỗ trợ tần số qu&eacute;t cao gi&uacute;p bạn c&oacute; được những ph&uacute;t gi&acirc;y giải tr&iacute; cực kỳ đ&atilde; mắt, hay thỏa m&atilde;n đam m&ecirc; nhiếp ảnh trong bạn nhờ trang bị camera c&oacute; độ ph&acirc;n giải cao.</h3>\r\n\r\n<h3>M&agrave;u sắc hiển thị rực rỡ</h3>\r\n\r\n<p>M&aacute;y trang bị tấm nền Super AMOLED với k&iacute;ch thước m&agrave;n h&igrave;nh l&ecirc;n đến 6.5 inch&nbsp;mang đến h&igrave;nh ảnh đầy m&agrave;u sắc, độ tương phản cao, tối ưu năng lượng ti&ecirc;u hao&nbsp;v&agrave; kh&ocirc;ng gian hiển thị đầy đủ, bao qu&aacute;t.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-super-amoled-hd-la-gi-905777\" target=\"_blank\">M&agrave;n h&igrave;nh Super AMOLED l&agrave; g&igrave;? Thiết bị n&agrave;o c&oacute; m&agrave;n h&igrave;nh SUPER AMOLED?</a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054930.jpg\" onclick=\"return false;\"><img alt=\"Màn hình hiển thị đẹp mắt - Samsung Galaxy A53 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054930.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054930.jpg\" title=\"Màn hình hiển thị đẹp mắt - Samsung Galaxy A53 5G 128GB\" /></a></p>\r\n\r\n<p>Sở hữu độ ph&acirc;n giải Full HD+ (1080 x 2400 Pixels) gi&uacute;p h&igrave;nh ảnh được&nbsp;hiển thị sắc n&eacute;t, độ chi tiết cao, đem đến cho m&igrave;nh cảm gi&aacute;c ch&acirc;n thực tr&ecirc;n từng t&aacute;c vụ giải tr&iacute; khi trải nghiệm qua con m&aacute;y n&agrave;y.</p>\r\n\r\n<p>C&aacute;c cử chỉ vuốt chạm khi m&igrave;nh thao t&aacute;c cho thấy m&aacute;y mang đến trải nghiệm cực kỳ mượt m&agrave; nhờ hỗ trợ tần số qu&eacute;t 120 Hz, ngo&agrave;i ra người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể t&ugrave;y chỉnh tần số qu&eacute;t ở hai mức độ 60 Hz v&agrave; 120 Hz cho ph&ugrave; hợp với t&aacute;c vụ sử dụng nhằm tiết kiệm điện năng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054934.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ tần số quét 120 Hz - Samsung Galaxy A53 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054934.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054934.jpg\" title=\"Hỗ trợ tần số quét 120 Hz - Samsung Galaxy A53 5G 128GB\" /></a></p>\r\n\r\n<h3>Đa dạng m&agrave;u sắc trẻ trung</h3>\r\n\r\n<p>Ra mắt 4 phi&ecirc;n bản m&agrave;u sắc nhằm đa dạng t&ugrave;y chọn đến với kh&aacute;ch h&agrave;ng, c&ugrave;ng với đ&oacute; l&agrave; tone m&agrave;u pastel mang đến vẻ trẻ trung v&agrave; tươi mới, ph&ugrave; hợp với c&aacute;c bạn trẻ năng động, c&aacute; t&iacute;nh hay những ai y&ecirc;u th&iacute;ch c&aacute;c tone m&agrave;u dịu &ecirc;m.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054938.jpg\" onclick=\"return false;\"><img alt=\"Ra mắt với 4 phiên bản màu - Samsung Galaxy A53 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054938.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054938.jpg\" title=\"Ra mắt với 4 phiên bản màu - Samsung Galaxy A53 5G 128GB\" /></a></p>\r\n\r\n<p>Galaxy A53 5G sử dụng lối thiết kế phẳng, đem lại c&aacute;i nh&igrave;n rất hợp xu hướng v&agrave; thời thượng. C&aacute;c cạnh được bo cong mềm mại mang đến cho m&igrave;nh cảm gi&aacute;c cầm nắm rất thoải m&aacute;i, sử dụng l&acirc;u d&agrave;i m&agrave; kh&ocirc;ng cảm thấy qu&aacute; cấn tay.</p>\r\n\r\n<p>M&aacute;y được thiết kế nguy&ecirc;n khối từ nhựa cao cấp nhằm tối ưu khối lượng v&agrave; mặt lưng gia c&ocirc;ng nh&aacute;m gi&uacute;p hạn chế c&aacute;c vết b&aacute;m v&acirc;n tay v&agrave; mồ h&ocirc;i kh&aacute; tốt, điều n&agrave;y thực sự rất hữu &iacute;ch đối với những ai thường ra mồ h&ocirc;i tay như c&aacute; nh&acirc;n m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054939.jpg\" onclick=\"return false;\"><img alt=\"Mặt lưng hoàn thiện nhám - Samsung Galaxy A53 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054939.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054939.jpg\" title=\"Mặt lưng hoàn thiện nhám - Samsung Galaxy A53 5G 128GB\" /></a></p>\r\n\r\n<h3>Ảnh chụp c&oacute; chi tiết cao, nhiều t&iacute;nh năng mới mẻ</h3>\r\n\r\n<p>Trang bị ở phần mặt lưng l&agrave; cụm camera được thiết kế &quot;hiệu ứng nổi 3D&quot; rất nổi bật với 4 camera b&ecirc;n trong, gi&uacute;p bạn c&oacute; được những bức ảnh sắc n&eacute;t hơn nhờ t&iacute;ch hợp nhiều t&iacute;nh năng mới mẻ v&agrave; bộ th&ocirc;ng số ấn tượng như: Camera ch&iacute;nh 64 MP,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\">camera g&oacute;c si&ecirc;u rộng</a>&nbsp;12 MP v&agrave; hai camera phụ 5 MP.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054941.jpg\" onclick=\"return false;\"><img alt=\"Trang bị 4 camera - Samsung Galaxy A53 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054941.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054941.jpg\" title=\"Trang bị 4 camera - Samsung Galaxy A53 5G 128GB\" /></a></p>\r\n\r\n<p>C&aacute;c bức ảnh thu được c&oacute; độ chi tiết tốt, m&agrave;u sắc được t&aacute;i tạo ch&acirc;n thực nhờ trang bị camera ch&iacute;nh c&oacute; độ ph&acirc;n giải cao, với khả năng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-zoom\" target=\"_blank\">zoom</a>&nbsp;tối đa 10x gi&uacute;p m&igrave;nh ph&oacute;ng to vật thể một c&aacute;ch nhanh ch&oacute;ng hay bắt lại những khoảnh khắc xung quanh được bao qu&aacute;t v&agrave; đầy đủ khi k&iacute;ch hoạt t&iacute;nh năng chụp ảnh g&oacute;c si&ecirc;u rộng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-290322-043029.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp ở chế độ thường - Samsung Galaxy A53 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-290322-043029.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-290322-043029.jpg\" title=\"Ảnh chụp ở chế độ thường - Samsung Galaxy A53 5G 128GB\" /></a></p>\r\n\r\n<p><em>*Ảnh chụp từ chế độ thường</em></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-290322-043027.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp ở chế độ góc rộng - Samsung Galaxy A53 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-290322-043027.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-290322-043027.jpg\" title=\"Ảnh chụp ở chế độ góc rộng - Samsung Galaxy A53 5G 128GB\" /></a></p>\r\n\r\n<p><em>*Ảnh chụp ở chế độ g&oacute;c si&ecirc;u rộng</em></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-290322-043025.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp ở chế độ macro - Samsung Galaxy A53 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-290322-043025.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-290322-043025.jpg\" title=\"Ảnh chụp ở chế độ macro - Samsung Galaxy A53 5G 128GB\" /></a></p>\r\n\r\n<p><em>*Ảnh chụp từ chế độ macro</em></p>\r\n\r\n<p>Với khả năng quay video 4K 30fps c&ugrave;ng c&ocirc;ng nghệ chống rung quang học (OIS) gi&uacute;p ổn định khung h&igrave;nh rất tốt, giữ cho chuyển động lu&ocirc;n mượt m&agrave; v&agrave; sắc n&eacute;t ngay cả khi thiếu s&aacute;ng, mang lại những thước phim chất lượng.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/che-do-chong-rung-quang-hoc-ois-chup-anh-tren-sm-906416\" target=\"_blank\">Chống rung quang học (OIS) l&agrave; g&igrave;? C&oacute; t&aacute;c dụng g&igrave;? Thiết bị n&agrave;o hỗ trợ?</a></p>\r\n\r\n<p>Mặt trước m&aacute;y sở hữu camera selfie với độ ph&acirc;n giải l&ecirc;n đến 32 MP c&ugrave;ng nhiều t&iacute;nh năng mới mẻ, m&igrave;nh kh&aacute; th&iacute;ch th&uacute; với &ldquo;Chế độ vui nhộn&rdquo; khi mang đến c&aacute;c bức ảnh hay đoạn phim th&uacute; vị.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054945.jpg\" onclick=\"return false;\"><img alt=\"Tính năng chụp ảnh vui nhộn - Samsung Galaxy A53 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054945.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054945.jpg\" title=\"Tính năng chụp ảnh vui nhộn - Samsung Galaxy A53 5G 128GB\" /></a></p>\r\n\r\n<h3>Trải nghiệm mượt m&agrave; tr&ecirc;n mọi t&aacute;c vụ</h3>\r\n\r\n<p>M&aacute;y trang bị chip được ch&iacute;nh&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\">Samsung</a>&nbsp;ph&aacute;t triển c&oacute; t&ecirc;n Exynos 1280 8 nh&acirc;n với xung nhịp tối đa đạt khoảng 2.4 GHz mang đến sự mượt m&agrave; tr&ecirc;n hầu hết c&aacute;c t&aacute;c vụ giải tr&iacute;, c&ocirc;ng việc h&agrave;ng ng&agrave;y của m&igrave;nh.</p>\r\n\r\n<p>Để kiểm tra xem m&aacute;y c&oacute; thực sự khai th&aacute;c tối đa sức mạnh tr&ecirc;n chip hay chưa, m&igrave;nh c&oacute; test qua hai phần mềm đo hiệu năng với số điểm như sau: 743 (đơn nh&acirc;n), 1806 (đa nh&acirc;n) tr&ecirc;n Benchmark v&agrave; 11745 điểm cho ứng dụng PCMark.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-300322-103645.jpg\" onclick=\"return false;\"><img alt=\"Trang bị chipset Exynos -Samsung Galaxy A53 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-300322-103645.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-300322-103645.jpg\" title=\"Trang bị chipset Exynos -Samsung Galaxy A53 5G 128GB\" /></a></p>\r\n\r\n<p>Tr&ecirc;n c&aacute;c t&aacute;c vụ chơi game, m&aacute;y vẫn đủ sức đ&aacute;p ứng tốt nhu cầu xử l&yacute; mượt m&agrave; với mức đồ họa cao nhất. Như m&igrave;nh c&oacute; trải nghiệm qua hai tựa game Li&ecirc;n Qu&acirc;n Mobile v&agrave; PUBG Mobile cho thấy h&igrave;nh ảnh hiển thị đẹp mắt, nh&acirc;n vật di chuyển mượt m&agrave; c&ugrave;ng khả năng duy tr&igrave; mức khung h&igrave;nh ổn định.</p>\r\n\r\n<p>Tốc độ khung h&igrave;nh tr&ecirc;n Li&ecirc;n Qu&acirc;n Mobile dao động trung b&igrave;nh khoảng 56 FPS v&agrave; xấp xỉ 30 FPS với tựa game PUBG Mobile khi chơi mức đồ họa cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054949.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ chơi game tốt - Samsung Galaxy A53 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054949.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054949.jpg\" title=\"Hỗ trợ chơi game tốt - Samsung Galaxy A53 5G 128GB\" /></a></p>\r\n\r\n<p>M&aacute;y sở hữu&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\">RAM 8 GB</a>&nbsp;c&ugrave;ng khả năng mở rộng qua chế độ &ldquo;RAM Plus&rdquo; n&acirc;ng tổng số dung lượng RAM l&ecirc;n đến 14 GB (8 GB mặc định + 6 GB mở rộng) gi&uacute;p m&igrave;nh thao t&aacute;c đa nhiệm một c&aacute;ch mượt m&agrave;, t&igrave;nh trạng load lại app gần như kh&ocirc;ng xảy ra cho d&ugrave; mở nhiều ứng dụng c&ugrave;ng l&uacute;c.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054952.jpg\" onclick=\"return false;\"><img alt=\"Bộ nhớ trong 128 GB - Samsung Galaxy A53 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054952.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054952.jpg\" title=\"Bộ nhớ trong 128 GB - Samsung Galaxy A53 5G 128GB\" /></a></p>\r\n\r\n<h3>Sử dụng cả ng&agrave;y với pin 5000 mAh c&ugrave;ng sạc nhanh 25 W</h3>\r\n\r\n<p>Trang bị b&ecirc;n trong m&aacute;y l&agrave; vi&ecirc;n pin c&oacute; dung lượng l&ecirc;n đến 5000 mAh mang đến thời lượng sử dụng l&acirc;u d&agrave;i, đ&aacute;p ứng nhu cầu giải tr&iacute; li&ecirc;n tục cả ng&agrave;y m&agrave; kh&ocirc;ng qu&aacute; lo lắng đến vấn đề hết pin giữa chừng.</p>\r\n\r\n<p>Với c&ocirc;ng nghệ sạc nhanh 25 W mang đến khả năng r&uacute;t ngắn thời gian lấp đầy vi&ecirc;n pin khủng, gi&uacute;p bạn quay lại c&ocirc;ng việc hay c&aacute;c t&aacute;c vụ giải tr&iacute; một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054954.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng sử dụng lâu dài - Samsung Galaxy A53 5G 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054954.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246196/samsung-galaxy-a53-280322-054954.jpg\" title=\"Thời lượng sử dụng lâu dài - Samsung Galaxy A53 5G 128GB\" /></a></p>\r\n\r\n<p>Qua những đ&aacute;nh gi&aacute; tr&ecirc;n cho thấy m&aacute;y đ&aacute;p ứng tốt hầu hết c&aacute;c y&ecirc;u cầu cơ bản tr&ecirc;n một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>, với ưu điểm s&aacute;ng gi&aacute; về phần m&agrave;n h&igrave;nh gi&uacute;p Galaxy A53 5G mang đến cho bạn những trải nghiệm giải tr&iacute; v&ocirc; c&ugrave;ng đ&atilde; mắt, hay đ&aacute;p ứng khả năng chụp ảnh sắc n&eacute;t gi&uacute;p thỏa m&atilde;n đam m&ecirc; nhiếp ảnh trong bạn.</p>\r\n', 'Đã đăng', 1, '03-10-2023', 1);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(25, 'VHL#22', 'Samsung Galaxy A33 5G 6GB', 6290000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung15.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a33\" target=\"_blank\">Samsung Galaxy A33 5G 6GB</a>&nbsp;ra mắt v&agrave;o ng&agrave;y 04/2022, được xem l&agrave; bản cập nhật kh&aacute; lớn so với thế hệ tiền nhiệm&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a32-4g\" target=\"_blank\">Galaxy A32</a>&nbsp;về thiết kế đến th&ocirc;ng số kỹ thuật b&ecirc;n trong, nhằm mang đến vẻ ngo&agrave;i đẹp mắt cũng như cạnh tranh trực tiếp ở phần hiệu năng đối với c&aacute;c đối thủ c&ugrave;ng ph&acirc;n kh&uacute;c gi&aacute;.</h3>\r\n\r\n<h3>Diện mạo trẻ trung v&agrave; năng động</h3>\r\n\r\n<p>Galaxy A33 c&oacute; cạnh viền được chế tạo từ nhựa nhằm tối ưu khối lượng, mang lại cảm gi&aacute;c cầm nắm nhẹ nh&agrave;ng, c&aacute;c cạnh được bo cong mềm mại gi&uacute;p m&igrave;nh sử dụng l&acirc;u d&agrave;i m&agrave; kh&ocirc;ng thấy xuất hiện t&igrave;nh trạng cấn tay.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072150.jpg\" onclick=\"return false;\"><img alt=\"Cầm nắm thoải mái - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072150.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072150.jpg\" title=\"Cầm nắm thoải mái - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p>Mặt sau l&agrave;m từ nhựa nh&aacute;m cho ra cảm gi&aacute;c sờ rất th&iacute;ch hay hạn chế b&aacute;m dấu v&acirc;n tay v&agrave; mồ h&ocirc;i, b&ecirc;n cạnh đ&oacute; n&oacute; c&ograve;n gi&uacute;p m&igrave;nh cầm m&aacute;y một c&aacute;ch chắc chắn hơn nhờ gia tăng độ ma s&aacute;t.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072154.jpg\" onclick=\"return false;\"><img alt=\"Camera nổi bật - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072154.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072154.jpg\" title=\"Camera nổi bật - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p>Viền m&agrave;n h&igrave;nh cũng được tối ưu tốt, tạo cho m&igrave;nh c&aacute;i nh&igrave;n rất thoải m&aacute;i v&agrave; dễ chịu, được xem l&agrave; ưu điểm nổi bật so với c&aacute;c đối thủ trong c&ugrave;ng ph&acirc;n kh&uacute;c gi&aacute;.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072155.jpg\" onclick=\"return false;\"><img alt=\"Viền màn hình mỏng - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072155.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072155.jpg\" title=\"Viền màn hình mỏng - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<h3>Chụp ảnh sắc n&eacute;t với camera 48 MP</h3>\r\n\r\n<p>Điều l&agrave;m m&igrave;nh ấn tượng tiếp theo l&agrave; Galaxy A33 được trang bị 4 camera sau c&ugrave;ng nhiều t&iacute;nh năng v&ocirc; c&ugrave;ng th&uacute; vị như: To&agrave;n cảnh (Panorama), Tr&ocirc;i nhanh thời gian (Time Lapse), X&oacute;a ph&ocirc;ng,... giờ h&atilde;y c&ugrave;ng m&igrave;nh xem những đ&aacute;nh gi&aacute; chi tiết về từng chiếc camera tr&ecirc;n m&aacute;y để xem n&oacute; mang lại trải nghiệm như thế n&agrave;o nh&eacute;!</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072157.jpg\" onclick=\"return false;\"><img alt=\"Trang bị 3 camera - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072157.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072157.jpg\" title=\"Trang bị 3 camera - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p>Với chế độ chụp ảnh thường tr&ecirc;n camera ch&iacute;nh 48 MP ở điều kiện đầy đủ &aacute;nh s&aacute;ng, ảnh cho ra c&oacute; độ tương phản kh&aacute; cao, c&aacute;c chi tiết kh&ocirc;ng bị vỡ qu&aacute; nhiều khi zoom ảnh, hỗ trợ tốt cho những c&ocirc;ng việc chỉnh sửa hậu kỳ hay in ấn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072159.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp chế độ thường - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072159.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072159.jpg\" title=\"Ảnh chụp chế độ thường - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p>Về phần chụp ảnh thiếu s&aacute;ng th&igrave; m&aacute;y cho ra chất lượng ở mức ổn, m&agrave;u sắc thể hiện kh&aacute; tươi, khả năng thu s&aacute;ng c&ograve;n hạn chế n&ecirc;n c&aacute;c v&ugrave;ng tối chưa được t&aacute;i hiện r&otilde;, hiện tượng nhiễu ảnh vẫn c&ograve;n xuất hiện nhưng kh&ocirc;ng đ&aacute;ng kể.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072201.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp vào ban đêm - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072201.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072201.jpg\" title=\"Ảnh chụp vào ban đêm - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p>Tiếp đến l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-goc-rong\" target=\"_blank\">camera g&oacute;c si&ecirc;u rộng</a>&nbsp;8 MP đem lại cho m&igrave;nh bức ảnh to&agrave;n cảnh c&oacute; độ bao qu&aacute;t lớn, những v&ugrave;ng mở rộng của bức h&igrave;nh được m&aacute;y xử l&yacute; tốt khi c&aacute;c g&oacute;c của ảnh kh&ocirc;ng bị b&oacute;p m&eacute;o nhiều.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072203.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp góc siêu rộng - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072203.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072203.jpg\" title=\"Ảnh chụp góc siêu rộng - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p>Ở chế độ chụp ảnh cận với&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-macro\" target=\"_blank\">camera macro</a>&nbsp;5 MP, ảnh cho ra c&oacute; độ chi tiết ổn, tốc độ bắt n&eacute;t tr&ecirc;n cảm biến kh&aacute; nhanh gi&uacute;p m&igrave;nh c&oacute; được những bức ảnh ưng &yacute; một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072205.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp bằng tính năng macro - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072205.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072205.jpg\" title=\"Ảnh chụp bằng tính năng macro - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p>Vị tr&iacute; cuối c&ugrave;ng b&ecirc;n trong cụm l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-camera-xoa-phong\" target=\"_blank\">camera x&oacute;a ph&ocirc;ng</a>&nbsp;2 MP c&oacute; khả năng xử l&yacute; kh&aacute; tốt, c&aacute;c bức h&igrave;nh thu được tr&ocirc;ng rất nịnh mắt, ảnh được t&aacute;ch biệt khỏi nền v&agrave; t&igrave;nh trạng lẹm ảnh khi chụp ở m&ocirc;i trường đủ s&aacute;ng gần như kh&ocirc;ng xuất hiện.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072207.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp xóa phông - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072207.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072207.jpg\" title=\"Ảnh chụp xóa phông - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p>Ở phần mặt trước, Galaxy A33 được trang bị camera selfie 13 MP c&ugrave;ng t&iacute;nh năng l&agrave;m đẹp gi&uacute;p m&igrave;nh c&oacute; được những bức ảnh rất ưng &yacute;, c&aacute;c khuyết điểm được m&aacute;y xử l&yacute; một c&aacute;ch th&ocirc;ng minh để kh&ocirc;ng l&agrave;m mất đi t&iacute;nh ch&acirc;n thực ban đầu.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072209.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp selfie - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072209.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072209.jpg\" title=\"Ảnh chụp selfie - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<h3>Hiệu năng ổn định đến từ con chip Exynos</h3>\r\n\r\n<p>Về phần xử l&yacute; c&aacute;c t&aacute;c vụ h&agrave;ng ng&agrave;y tr&ecirc;n thiết bị được Exynos 1280 đảm nhận một c&aacute;ch hiệu quả khi c&aacute;c nhu cầu sử dụng như: Nghe gọi, xem phim, lướt web đều hoạt động hết sức ổn định.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072211.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng ổn định - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072211.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072211.jpg\" title=\"Hiệu năng ổn định - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p>M&igrave;nh c&oacute; sử dụng 2 phần mềm đ&aacute;nh gi&aacute; điểm hiệu năng chuy&ecirc;n dụng để c&oacute; c&aacute;i nh&igrave;n tổng quan hơn tr&ecirc;n m&aacute;y v&agrave; lấy kết quả so s&aacute;nh với những d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>&nbsp;kh&aacute;c th&igrave; kết quả thu được như sau: 544 (đơn nh&acirc;n), 1794 (đa nh&acirc;n) ở Benchmark v&agrave; 10095 tr&ecirc;n PCMark.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072212.jpg\" onclick=\"return false;\"><img alt=\"Điểm đánh giá hiệu năng - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072212.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072212.jpg\" title=\"Điểm đánh giá hiệu năng - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p><em>*Benchmark b&ecirc;n tr&aacute;i v&agrave; PCMark b&ecirc;n phải</em></p>\r\n\r\n<p>Nhằm kiểm chứng sức mạnh th&igrave; m&igrave;nh c&oacute; sử dụng con Galaxy A33 cho t&aacute;c vụ xử l&yacute; đồ họa ở 2 tr&ograve; chơi thịnh h&agrave;nh tr&ecirc;n di động, ở tựa game đầu ti&ecirc;n l&agrave; Li&ecirc;n Qu&acirc;n Mobile với mức cấu h&igrave;nh cao nhất cho ra khả năng xử l&yacute; tương đối ổn, t&igrave;nh trạng giật lag vẫn c&ograve;n xuất hiện, tốc độ khung h&igrave;nh dao động loanh quanh 57 - 58 FPS.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072214.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình Liên Quân Mobile - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072214.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072214.jpg\" title=\"Cấu hình Liên Quân Mobile - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p><em>*Cấu h&igrave;nh cao nhất m&agrave; m&aacute;y bật được tr&ecirc;n Li&ecirc;n Qu&acirc;n Mobile</em></p>\r\n\r\n<p>Tiếp đến l&agrave; PUBG Mobile th&igrave; m&aacute;y hỗ trợ bật được mức đồ họa l&ecirc;n đến HD c&ugrave;ng tốc độ khung h&igrave;nh cực cao, cho ra h&igrave;nh ảnh hiển thị ch&acirc;n thực v&agrave; sống động, tuy nhi&ecirc;n hiện tượng khựng khung h&igrave;nh xảy ra kh&aacute; thường xuy&ecirc;n, tốc độ khung h&igrave;nh đo được dao động tầm 25 - 30 FPS.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072216.jpg\" onclick=\"return false;\"><img alt=\"Cấu hình PUBG Mobile - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072216.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072216.jpg\" title=\"Cấu hình PUBG Mobile - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p><em>*Cấu h&igrave;nh cao nhất m&agrave; m&aacute;y bật được tr&ecirc;n PUBG Mobile</em></p>\r\n\r\n<p>Sở hữu trong m&igrave;nh&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-6gb\" target=\"_blank\">6 GB RAM</a>&nbsp;c&ugrave;ng khả năng mở rộng l&ecirc;n đến 12 GB (6 GB mặc định + 6 GB mở rộng) qua t&iacute;nh năng &ldquo;RAM Plus&rdquo; gi&uacute;p m&aacute;y đa nhiệm hết sức mượt m&agrave;, t&igrave;nh trạng tải lại ứng dụng gần như kh&ocirc;ng xảy ra cho d&ugrave; m&igrave;nh c&oacute; đa nhiệm nhiều ứng dụng c&ugrave;ng một l&uacute;c.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072218.jpg\" onclick=\"return false;\"><img alt=\"Tính năng mở rộng RAM - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072218.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072218.jpg\" title=\"Tính năng mở rộng RAM - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p><em>*T&iacute;nh năng mở rộng RAM tr&ecirc;n&nbsp;Samsung Galaxy A33 5G 6GB</em></p>\r\n\r\n<h3>Xem phim, chơi game tốt hơn với m&agrave;n h&igrave;nh Super AMOLED</h3>\r\n\r\n<p>Galaxy A33 sử dụng lối thiết kế m&agrave;n h&igrave;nh Infinity-U được&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\">Samsung</a>&nbsp;trang bị tấm nền Super AMOLED c&oacute; k&iacute;ch thước 6.4 inch, cho ra khả năng hiển thị m&agrave;u sắc tươi mới, t&aacute;i tạo m&agrave;u đen s&acirc;u ch&acirc;n thực c&ugrave;ng một kh&ocirc;ng gian tr&igrave;nh chiếu lớn hỗ trợ cho m&igrave;nh c&aacute;c c&ocirc;ng việc đọc tin tức, t&agrave;i liệu rất hữu &iacute;ch khi nhiều nội dung được thể hiện l&ecirc;n c&ugrave;ng một khung h&igrave;nh.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-amoled-la-gi-905766\" target=\"_blank\">M&agrave;n h&igrave;nh Super AMOLED l&agrave; g&igrave;? C&oacute; g&igrave; nổi bật?&nbsp;</a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072222.jpg\" onclick=\"return false;\"><img alt=\"Trang bị màn hình AMOLED - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072222.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072222.jpg\" title=\"Trang bị màn hình AMOLED - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p>Độ ph&acirc;n giải đạt được ở mức Full HD+ (1080 x 2400 Pixels) cho ra h&igrave;nh ảnh c&oacute; độ chi tiết cực cao, c&aacute;c vật thể nhỏ tr&ecirc;n m&agrave;n h&igrave;nh được t&aacute;i hiện một c&aacute;ch r&otilde; n&eacute;t, hỗ trợ m&igrave;nh chơi tựa game sinh tồn như PUBG Mobile một c&aacute;ch dễ d&agrave;ng hơn.</p>\r\n\r\n<h3>Thời lượng sử dụng l&acirc;u d&agrave;i với pin 5000 mAh</h3>\r\n\r\n<p>Galaxy A33 mang lại thời gian sử dụng cho đến gần 9 giờ* cho c&aacute;c t&aacute;c vụ xem phim, lướt web v&agrave; chơi game, gi&uacute;p m&igrave;nh sử dụng một c&aacute;ch thoải m&aacute;i hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072226.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng sử dụng - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072226.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072226.jpg\" title=\"Thời lượng sử dụng - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p>Mất 1 giờ 30 ph&uacute;t* để lấp đầy vi&ecirc;n pin từ 0 - 100% nhờ c&ocirc;ng nghệ sạc nhanh 25 W, so với gi&aacute; th&agrave;nh m&agrave; n&oacute; trang bị th&igrave; thời gian sạc tr&ecirc;n con m&aacute;y n&agrave;y theo m&igrave;nh cảm nhận l&agrave; kh&aacute; nhanh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072227.jpg\" onclick=\"return false;\"><img alt=\"Thời gian sạc đầy - Samsung Galaxy A33 5G 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072227.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/246199/samsung-galaxy-a33-050422-072227.jpg\" title=\"Thời gian sạc đầy - Samsung Galaxy A33 5G 6GB\" /></a></p>\r\n\r\n<p><em>*Thời gian sử dụng/sạc c&oacute; thể thay đổi t&ugrave;y v&agrave;o t&aacute;c vụ v&agrave; nhiệt độ của m&aacute;y.</em></p>\r\n\r\n<p>Samsung Galaxy A33 5G 6GB l&agrave; một sự lựa chọn ph&ugrave; hợp đối với c&aacute;c bạn trẻ như học sinh, sinh vi&ecirc;n nhằm phục vụ c&aacute;c t&aacute;c vụ xem tin tức, học tập trực tuyến, giải tr&iacute; nhẹ nh&agrave;ng một c&aacute;ch ổn định hay d&agrave;nh cho những ai muốn c&oacute; cho m&igrave;nh một chiếc điện thoại c&oacute; hiệu năng ổn định, chụp ảnh chất lượng c&ugrave;ng mức gi&aacute; phải chăng.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 0),
(26, 'VHL#23', 'Samsung Galaxy A23 5G 4GB', 4890000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung16.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a23-5g\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy A23 5G 4GB đang kinh doanh tại Thế Giới Di Động \">Samsung Galaxy A23 5G 4GB</a>&nbsp;l&agrave; thiết bị di động được cho ra mắt v&agrave;o th&aacute;ng 11/2022 tại thị trường Việt Nam. Điện thoại mang đến sự ch&uacute; &yacute; khi sở hữu mức gi&aacute; b&aacute;n rẻ, cấu h&igrave;nh ổn trong ph&acirc;n kh&uacute;c c&ugrave;ng vẻ ngo&agrave;i đầy bắt mắt.</h3>\r\n\r\n<h3>Nổi bật với vẻ ngo&agrave;i trẻ trung năng động</h3>\r\n\r\n<p>Về thiết kế của Galaxy A23, m&aacute;y sở hữu một vẻ ngo&agrave;i kh&aacute; đặc trưng của c&aacute;c mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-a\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại Samsung Galaxy A đang kinh doanh tại Thế Giới Di Động \">điện thoại Samsung Galaxy d&ograve;ng&nbsp; A</a>, với khung viền v&agrave; mặt lưng nối liền gi&uacute;p tạo n&ecirc;n một sự kết hợp đầy chắc chắn cho m&aacute;y, c&ugrave;ng với đ&oacute; l&agrave; c&aacute;c cạnh bo cong đem đến trải nghiệm cầm nắm chắc tay v&agrave; thoải m&aacute;i.</p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/d8RxyVTIpbH_nV_Y1aCoJ31bBb_h3L0muFMkMTrVy892ayhXDXKAhebOgLvZ85bwQhI2clE8iAmIWs4Ahnwm7G6uri993GhniQ8FbH15qJYpF6kpzCNA2oixnp_5RWxfbbp2CAQZs4Y90nef5rW2rQ\" /></p>\r\n\r\n<p>Mặt lưng của điện thoại th&igrave; được ho&agrave;n thiện theo kiểu nh&aacute;m nhẹ, đ&acirc;y c&oacute; thể coi l&agrave; một điểm nhấn gi&uacute;p m&aacute;y trở n&ecirc;n sang hơn. Khi sử dụng cũng hạn chế b&aacute;m dấu v&acirc;n tay v&agrave; c&aacute;c vết xước dăm cũng &iacute;t xảy ra, điều n&agrave;y gi&uacute;p m&aacute;y lu&ocirc;n trong t&igrave;nh trạng mới mẻ hơn so với c&aacute;c mẫu điện thoại c&oacute; mặt lưng nhẵn b&oacute;ng.</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/QdfUunRkpjNgtpAN5RM-6wQ6nVrrnaDKpeaSJC_ZCey4NRDrcWvfjl82aYePekM5pk_9A-tncgHSIVTajbVkQMtwhHYo-FnJ49usoKtK_pcmyCB2aXfDw8JGlm-fE08o0NrUIb1fG58bdRaVjQdmWw\" /></p>\r\n\r\n<p>Cả khung v&agrave; mặt lưng của m&aacute;y đều được l&agrave;m từ nhựa n&ecirc;n khi cầm th&igrave; m&igrave;nh c&oacute; cảm gi&aacute;c kh&aacute; nhẹ nh&agrave;ng, d&ugrave;ng l&acirc;u cũng &iacute;t khi bị mỏi tay. Nh&igrave;n tổng quan th&igrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Xem thêm Điện thoại Samsung chính hãng đang kinh doanh tại Thế Giới Di Động\">điện thoại Samsung</a>&nbsp;nh&igrave;n cũng tương đối gọn g&agrave;ng v&agrave; nhỏ nhắn khi k&iacute;ch thước chỉ 165.4 mm cho chiều d&agrave;i v&agrave; 76.9 mm của bề ngang.</p>\r\n\r\n<p>Galaxy A23 c&oacute; kiểu thiết kế cụm camera kh&aacute; th&uacute; vị khi n&oacute; được l&agrave;m liền với mặt lưng c&ugrave;ng kiểu tạo h&igrave;nh &ldquo;nổi&rdquo;, điều n&agrave;y gi&uacute;p cho m&igrave;nh c&oacute; cảm gi&aacute;c như cụm n&agrave;y được trồi l&ecirc;n tr&ocirc;ng kh&aacute; th&uacute; vị v&agrave; th&iacute;ch mắt.</p>\r\n\r\n<p><img src=\"https://lh4.googleusercontent.com/OPYDcbhCQyqhHggui4HhSq9hE_zyUTC-j4AEzNli4iUlql0f5yGnBV_qSrmemRptyiV4bX81T1ERc_8TmIx_ekHhCT8AEZJ6DvnwoXI07-m-PJMq-XsuZcGocfRHq86F76OY-edVpnKflLXfkJQ2GA\" /></p>\r\n\r\n<p>Ở phần mặt trước th&igrave; sẽ l&agrave; m&agrave;n h&igrave;nh theo kiểu giọt nước, một lối thiết kế dễ bắt gặp tr&ecirc;n c&aacute;c mẫu m&aacute;y gi&aacute; rẻ tr&ecirc;n thị trường. Nhằm mang đến sự cứng c&aacute;p hơn th&igrave; Samsung cũng đ&atilde; trang bị th&ecirc;m k&iacute;nh cường lực Corning Gorilla Glass 3, n&oacute; mang đến khả năng kh&aacute;ng lại được c&aacute;c vết xước nhẹ tr&ecirc;n m&agrave;n h&igrave;nh.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/kinh-cuong-luc-gorilla-glass-3-la-gi-co-ben-khong-1209036\" target=\"_blank\">K&iacute;nh cường lực Gorilla Glass 3 l&agrave; g&igrave;? C&oacute; bền kh&ocirc;ng?</a></p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/b4LS1NGk-X-y13IMiGp47Q2Ano_GSwWEaAedJ_MtJ8smSex2TC-MJTRRrf2LZX_TCkJkicMXrfjqYr0IagZd-0s2HrmoSfeIwz4-bUhLbpbHwkYVXeSI7_Mnu68l37xxhkAVNwS1HgFiDKMFoG4V1g\" /></p>\r\n\r\n<h3>Chụp ảnh sắc n&eacute;t hơn với camera 50 MP</h3>\r\n\r\n<p>Galaxy A23 kh&ocirc;ng chỉ mang lại ấn tượng với camera ch&iacute;nh c&oacute; độ ph&acirc;n giải 50 MP m&agrave; m&aacute;y c&ograve;n c&oacute; th&ecirc;m điểm nhấn khi sở hữu 4 camera ở phần mặt lưng, trong đ&oacute; c&oacute;: Camera g&oacute;c si&ecirc;u rộng 5 MP, camera đo độ s&acirc;u 2 MP v&agrave; cuối c&ugrave;ng l&agrave; camera macro 2 MP.</p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/gOJ-WqFJi0drmAoAiDPf58LqrIsquczBCeKVnmGKmxAPUZ27hslPd3nLQULnZGsP1SF-ebtyBMk7kmwfNAW1gspDBQv9m8gBU8hGJmwU77ofkCdAerptjvaVwLpTmAoj1_990F5F6D1_US0ZAKT5vQ\" /></p>\r\n\r\n<p>Trong m&ocirc;i trường c&oacute; &aacute;nh s&aacute;ng đầy đủ, camera ch&iacute;nh của Samsung Galaxy A23 5G ghi lại những bức ảnh sắc n&eacute;t, m&agrave;u sắc được tinh chỉnh một c&aacute;ch h&agrave;i h&ograve;a v&agrave; sống động, đồng thời cung cấp độ chi tiết cao v&agrave; thể hiện được chiều s&acirc;u của khung h&igrave;nh. Ngay cả khi zoom, độ chi tiết v&agrave; sắc n&eacute;t vẫn được duy tr&igrave; m&agrave; kh&ocirc;ng bị giảm s&uacute;t qu&aacute; nhiều.</p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/-uw86YbqO3BpCjcXHK8TtfJUauAb6pUaeGTYAn_Zer3GPPf65d9tbhQyU3cIaRRayNYRhr-wsJ0jdynn7KucdzOvi0XX8mY9cJfTooGJ8BnZ-s9A_C4zfwzm_k_-4pCWXofahKOWZQHPV-ajiU9gjQ\" /></p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/x4QEJTRgkm1gSnMlm2TEcgb-VPYzKRs_15wy3j1jKbUjZLDtA8RBOJ4DGKpLDTl7H5DpPv94f2QWVp19w0FbWeTl1FIIQRyLlu7EWEww5owu92sM_Y4EScgveNrEmo7f7HYQVlYr7aPKF2dTUzqR4g\" /></p>\r\n\r\n<p>V&agrave;o c&aacute;c khung giờ l&yacute; tưởng như 4 - 5 giờ chiều, ảnh chụp từ Galaxy A23 c&ograve;n cho ra chất lượng ấn tượng hơn khi t&aacute;i tạo lại m&agrave;u sắc một c&aacute;ch rực rỡ v&agrave; đầy tươi mới, chất lượng ảnh phải gọi l&agrave; cực kỳ sắc n&eacute;t, sinh động v&agrave; gi&agrave;u chi tiết. Mặc d&ugrave; quang cảnh cũng đ&atilde; chạng vạng tối nhưng m&aacute;y vẫn c&oacute; thể xử l&yacute; v&agrave; c&acirc;n bằng lại gi&uacute;p cho tổng thể trở n&ecirc;n dễ nh&igrave;n v&agrave; đẹp mắt.</p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/lpq6xhme6iB-RypNECHflVh4ePNXKk3lkPDuxdUZyV5Mkx7jqsbylCwgBcppfs_LLcDT5jyY1trBJP7h5FNmSbojzEFdqk5O03ShSpFDS28fq8-V60N-bLtredUS9TiVAvFox92X9n8Mt7VHgLKRhg\" /></p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/xcPf2a6ryRcH-yorLqLv6bDsDnPDaMebg8Z-EDNqZL-s6sqxRK7HQSkJ_W8og4NeX6zW_1sukK8tqsGGVavscarAjXHLuUVAKHuY3bOtPt9Tj1KPXdjnSITfSnBkwvz4XKUU9wrpWp9JDGQ59f_cDQ\" /></p>\r\n\r\n<p>Trong điều kiện thiếu s&aacute;ng, camera tr&ecirc;n Samsung Galaxy A23 5G tiếp tục g&acirc;y ấn tượng bởi c&ocirc;ng nghệ xử l&yacute; ảnh vượt trội. H&igrave;nh ảnh chụp mang đến chất lượng tốt với độ chi tiết cao, đủ s&aacute;ng để c&oacute; thể nh&igrave;n r&otilde; được quang cảnh xung quanh, d&ugrave; c&oacute; một ch&uacute;t hiện tượng nhiễu nhưng kh&ocirc;ng ảnh hưởng đ&aacute;ng kể.</p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/D_J24aFrLf721GTBPLYFQB_fXZ2PcYLUcMGAYJjqWxqyoo_Z1PmXggVlhEseT_u9gvivRDXD6Jh_BLYXwOjkwdNrq94epLg5Dq4EwnK7ofd3t7BkjJ53DmJ95hbRlCBVvGbfplG6pHL05GzkYltKSA\" /></p>\r\n\r\n<h3>Hiệu năng xử l&yacute; tốt c&aacute;c t&aacute;c vụ cơ bản</h3>\r\n\r\n<p>Đối với Galaxy A23 5G th&igrave; h&atilde;ng sử dụng vi xử l&yacute; Snapdragon 695 được sản xuất bởi nh&agrave; Qualcomm. T&iacute;nh tại thời điểm ra mắt điện thoại th&igrave; đ&acirc;y được xem l&agrave; con chip kh&aacute; nổi bật trong ph&acirc;n kh&uacute;c cận gi&aacute; rẻ, vừa mang lại mức hiệu năng ổn song cũng mang lại khả năng tiết kiệm năng lượng.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-chip-snapdragon-695-5g-hieu-nang-manh-me-1456599\">T&igrave;m hiểu chip Snapdragon 695 5G</a></p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/LzQTBGhP3Fde6TpsrulCx7WRU7fYQV5LCoLajMXoYi2yjX6ZpSkYrTMuS4uPYuaFoDC8gUt1A_V1UhJzAVfcLK0VzD_lgGcfW9jwR0568Ugv586If-EiaRysYMde841GFTfgTS8BQqRfXHgCuGXr5g\" /></p>\r\n\r\n<p>Galaxy A23 khi được test qua một v&agrave;i phần mềm kiểm tra th&ocirc;ng số hiệu năng th&igrave; kết quả cho ra cũng kh&aacute; ấn tượng, m&aacute;y đạt 680 điểm đơn nh&acirc;n v&agrave; 1977 điểm đa nh&acirc;n tr&ecirc;n Geekbench 6, đồng thời đạt 10185 điểm tổng tr&ecirc;n PCMark.</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/cSjHrcu5WE39Xa8Am00-ozY1y9CbmKc53lXKQRsKIEB2yIz5UFv8KPj6n_zQH6FReFjvxVtqgDm1KF0Kc0tH3-Dm26UVemzQz8T4kOAUuuLhFy2avDP5MFPA32KafwwuqPgQ6S7UMzI4W1D8ypKGiA\" /></p>\r\n\r\n<p>Ngo&agrave;i ra Snapdragon 695 c&ograve;n l&agrave; mẫu chip c&oacute; hỗ trợ 5G, v&igrave; thế n&oacute; gi&uacute;p cho Galaxy A23 5G trở th&agrave;nh một trong những chiếc điện thoại ở ph&acirc;n kh&uacute;c gi&aacute; rẻ c&oacute; hỗ trợ chuẩn mạng di động ti&ecirc;n tiến, chỉ cần đầu tư một số tiền tương đối thấp l&agrave; ta đ&atilde; c&oacute; thể trải nghiệm được tốc độ kết nối mạng nhanh ch&oacute;ng đầy ấn tượng.</p>\r\n\r\n<p>Về c&aacute;c t&aacute;c vụ cơ bản sử dụng h&agrave;ng ng&agrave;y như lướt web đọc b&aacute;o, xem phim hay nhắn tin nghe gọi th&igrave; điện thoại xử l&yacute; dễ d&agrave;ng, mọi thứ diễn ra nhanh ch&oacute;ng v&agrave; mượt m&agrave;, kh&ocirc;ng c&oacute; bất kỳ hiện tượng giật lag n&agrave;o l&agrave;m ảnh hưởng đến qu&aacute; tr&igrave;nh trải nghiệm của m&igrave;nh cả.</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/OBtq4FFbblwH6mTq4axTNbQLvF96jriVCjPwclOhj9spV9wcV6tnmp7TZS8IETGJM1vAIHJ2hpggvGbakhbOvFmfb6yHOsD14jWpocXXBzC5Y5DfDLj_wEl2n39nq_iN7WtbVp_f8N1rhjJ4SCbVWg\" /></p>\r\n\r\n<p>C&ograve;n với c&aacute;c t&aacute;c vụ chơi game, Galaxy A23 vẫn được xem l&agrave; một chiếc m&aacute;y mang đến trải nghiệm ổn tr&ecirc;n c&aacute;c tựa game MOBA như Li&ecirc;n Qu&acirc;n Mobile hay Li&ecirc;n Minh Huyền Thoại Tốc Chiến. Kể cả khi c&agrave;i đặt cấu h&igrave;nh tr&ograve; chơi ở mức cao nhất th&igrave; mọi thứ đều su&ocirc;n mượt v&agrave; &iacute;t khi bị giật lag g&acirc;y cản trở.</p>\r\n\r\n<p><img src=\"https://lh4.googleusercontent.com/R3E74-DSIxq6qwXXdFSwCcPtMMAkyL5xEv1NT1jBWStd3UL5tz0HVvS6xQwK7rz49qMy6zmvgOM6upUC_WL_3dBqBL65squEyWOjniAhxFgfZrls_WxlwLvQdU015YtzDN0NvAktVYGppJ-wEkY9yA\" /></p>\r\n\r\n<p>Chuyển đến tựa game nặng hơn đ&oacute; ch&iacute;nh l&agrave; PUBG Mobile, mức đồ họa tối đa m&agrave; ta c&oacute; thể bật được tr&ecirc;n tựa game n&agrave;y l&agrave; HD c&ugrave;ng tốc độ khung h&igrave;nh cao. V&igrave; l&agrave; điện thoại thuộc ph&acirc;n kh&uacute;c thấp n&ecirc;n khi xử l&yacute; tựa game n&agrave;y ở cấu h&igrave;nh tối đa th&igrave; m&aacute;y kh&aacute; đuối, c&oacute; xảy ra giật lag trong l&uacute;c bắn nhau với nhiều kẻ địch.</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/WQCLxfiqmhXoBryM60ViW2Riy_3Hd1wmyofFWcW6Yo2NCZMlfhiZrK-Opif3oKRyKzESI-67-6VxDdV0U0L_5DRbLtt4SB7Ry0-HSO1YU4V3FwmOe1jhGbEWxn7bOqaqLVsFmjX5KS_vV2y8NJkDvA\" /></p>\r\n\r\n<p>Vậy n&ecirc;n để c&oacute; được trải nghiệm mượt m&agrave; một c&aacute;ch tối ưu nhất th&igrave; ta n&ecirc;n c&agrave;i đặt đồ họa cho tựa game n&agrave;y ở mức mượt v&agrave; tốc độ khung h&igrave;nh cao, điều n&agrave;y sẽ l&agrave;m tăng sự ổn định gi&uacute;p cho qu&aacute; tr&igrave;nh bắn nhau diễn ra mượt m&agrave;.&nbsp;</p>\r\n\r\n<p>Galaxy A23 l&agrave; một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-4gb\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại RAM 4GB đang kinh doanh tại Thế Giới Di Động \">điện thoại RAM 4 GB</a>&nbsp;v&agrave; hỗ trợ mở rộng th&ecirc;m 4 GB từ bộ nhớ trong, điều n&agrave;y gi&uacute;p tăng cường khả năng xử l&yacute; đa nhiệm n&ecirc;n khi chuyển đổi qua lại giữa c&aacute;c ứng dụng m&igrave;nh lu&ocirc;n cảm thấy sự mượt m&agrave; v&agrave; nhanh ch&oacute;ng.&nbsp;</p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/tgUv3nBoHMqMJZK34VFgI-AOFHZDksFefz_k9MLI8jQ2chqDd0GSBh-JDXZSvqXAucBfM-XH6wfubz7t5WTWALxft7T6-V1OfINGNJOh8quxI33fKkLEC-ci4JT8huJH5lwxciYBVZX89qObcPcOfw\" /></p>\r\n\r\n<p>Ngo&agrave;i ra th&igrave; h&atilde;ng c&ograve;n ra mắt th&ecirc;m phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a23-5g-6gb\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy A23 5G 6 GB tại Thế Giới Di Động\">Samsung Galaxy A23 5G 6 GB</a>, ph&ugrave; hợp với người d&ugrave;ng thường xuy&ecirc;n sử dụng đa t&aacute;c vụ, cần một mức RAM lớn v&agrave; ổn định để trải nghiệm sử dụng được mượt m&agrave; hơn.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh sắc n&eacute;t v&agrave; tần số qu&eacute;t 120 Hz</h3>\r\n\r\n<p>Galaxy A23 sở hữu một m&agrave;n h&igrave;nh c&oacute; độ chi tiết cao đạt chuẩn Full HD+ (1080 x 2408 Pixels), nhờ vậy m&agrave; mọi thứ khi được hiển thị tr&ecirc;n điện thoại đều rất sắc n&eacute;t, hiện tượng như răng cưa hay lộ r&otilde; điểm ảnh l&agrave; điều m&agrave; m&igrave;nh kh&ocirc;ng thấy xuất hiện tr&ecirc;n m&agrave;n h&igrave;nh của điện thoại.</p>\r\n\r\n<p><img src=\"https://lh4.googleusercontent.com/p75X_qb4Q6kUAjH64a1-CZFyiDxlDbvGnnsL3Wx5uNJSwVbdY_9nGtf2colXiUXuZmW0L2U3uca5fNahW-1rEF5m89Gp281GpVjWQZumVENyhRfS4fw8f2YaIqoJgVFCjeC0XMG1a9TOiXkrPAbEAQ\" /></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; th&igrave; m&agrave;n h&igrave;nh n&agrave;y c&ograve;n nổi bật hơn khi được trang bị tần số qu&eacute;t l&ecirc;n đến 120 Hz, n&oacute; gi&uacute;p cho c&aacute;c cử chỉ vuốt chạm trở n&ecirc;n trơn tru hơn khi nội dung tr&ecirc;n m&agrave;n h&igrave;nh được t&aacute;i tạo lại một c&aacute;ch nhanh ch&oacute;ng.&nbsp;</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/7MuRLEM1P2axM0t9PYI2c7xKCZ7takWs5782ZEbRo-MOED0O9iL4jRZrAFbzkv3chjzJAn2DFZMzJhdes1p7FwgRsb7UO4yCabYsS1Edl6OHlAcIWcZ7zeiY_jiM7eNukfEWLnaa4zWtFflqwKMNxA\" /></p>\r\n\r\n<p>Hiệu ứng khi vuốt qua - lại cũng kh&ocirc;ng bị t&igrave;nh trạng b&oacute;ng mờ hay lưu lại điểm ảnh, nhờ đ&oacute; m&agrave; trải nghiệm khi xem phim hay chơi game cũng đều trở n&ecirc;n tốt hơn.</p>\r\n\r\n<p>Galaxy A23 được trang bị tấm nền IPS LCD với k&iacute;ch thước lớn 6.6 inch, h&igrave;nh ảnh cho ra c&oacute; m&agrave;u sắc kh&aacute; tươi tắn, bắt mắt, g&oacute;c nh&igrave;n rộng v&agrave; kh&ocirc;ng gian rộng gi&uacute;p hiển thị mọi thứ một c&aacute;ch r&otilde; r&agrave;ng.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" target=\"_blank\">M&agrave;n h&igrave;nh IPS LCD l&agrave; g&igrave;? C&oacute; g&igrave; nổi bật? Thiết bị n&agrave;o c&oacute; m&agrave;n h&igrave;nh IPS?</a></p>\r\n\r\n<p><img src=\"https://lh4.googleusercontent.com/SrnBAI0ofgLJNX3sy7ASNSLp4nqiVLl09D56WjVXQFo0oI3tBzSZKUUoNkUdA7BkytsYG3cbCZwx9PmcRj5cHn0wV0gnqghH0Jq6hfMZwr0EkPXu8d7e8dlsg0ZXu__c0NfATUAgjo04kOiXFDg-xQ\" /></p>\r\n\r\n<p>Theo m&igrave;nh thấy th&igrave; m&agrave;n h&igrave;nh của&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Xem thêm Điện thoại chính hãng, giá rẻ đang kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;c&oacute; hơi &aacute;m xanh nhẹ một ch&uacute;t, đ&acirc;y c&oacute; thể xem l&agrave; một hạn chế điển h&igrave;nh của tấm nền n&agrave;y v&agrave; n&oacute; l&agrave;m cho m&agrave;u đen t&aacute;i hiện chưa được s&acirc;u cho lắm. Gi&aacute; m&agrave; Samsung trang bị tấm nền AMOLED d&agrave;nh cho Galaxy A23 th&igrave; đ&acirc;y quả l&agrave; một điều tuyệt vời, n&oacute; c&oacute; thể khuấy đảo cả ph&acirc;n kh&uacute;c khi kh&ocirc;ng c&oacute; bất kỳ hạn chế n&agrave;o.</p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/1L64RdCbz-xHe6D42tkXctvRl-Y0Dg2owQ1weSupPoH3ZVVpM9qxUCBJaue0nBjFWMyGfuYI_9YWaa6l00N2mKymuWE3K0m79fumnJM7RGD6yL5k1sqCofMbKXbBalU3fsg6BFk1Dm2w9wb3Bjxq0A\" /></p>\r\n\r\n<h3>Trang bị pin 5000 mAh v&agrave; hỗ trợ sạc nhanh</h3>\r\n\r\n<p>Galaxy A23 được trang bị một vi&ecirc;n pin lớn với mức dung lượng l&ecirc;n đến 5000 mAh, v&igrave; thế m&aacute;y ho&agrave;n to&agrave;n c&oacute; thể đồng h&agrave;nh c&ugrave;ng với ta trong một ng&agrave;y sử dụng d&agrave;i l&acirc;u m&agrave; kh&ocirc;ng cần quan t&acirc;m đến việc cắm sạc.</p>\r\n\r\n<p>Thực tế th&igrave; m&igrave;nh đ&atilde; d&ugrave;ng Galaxy A23 như một chiếc điện thoại ch&iacute;nh để phục vụ mọi nhu cầu giải tr&iacute;, m&igrave;nh sử dụng điện thoại từ l&uacute;c 100% dung lượng th&igrave; mất khoảng 8 tiếng 27 ph&uacute;t mới c&oacute; thể d&ugrave;ng hết.&nbsp;</p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/RvFA-mfssLTtHbYcoJY6queQnT60BoPdTbLq_AlwHAaSpDPUY7uLuyva452cq8doJ17h-6qzmOpO6Iryl5eFAFUmutSAPbucNdC5yCG8vWyN9P2QhiAh34NZwXEf4ztUfqCl5RiAoXWOhx7PSC9QKw\" /></p>\r\n\r\n<p>C&ograve;n về khả năng sạc pin cho điện thoại th&igrave; m&aacute;y c&oacute; khả năng tiếp nhận c&ocirc;ng suất sạc tối đa l&ecirc;n đến 25 W. Thế nhưng phụ kiện k&egrave;m theo m&aacute;y th&igrave; Samsung chỉ cấp cho ch&uacute;ng ta một củ sạc 15 W v&agrave; khi d&ugrave;ng n&oacute; để sạc th&igrave; tốn đ&acirc;u đ&oacute; khoảng 1 tiếng 50 ph&uacute;t.</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/05niA7Fa7X64AutbAc4ppiXShH744FUmpkWAueg42KzS0Xef0g0Ut2Hbyz1Q4h-INK8W5LtsUBC5eL3Fcd4WrsYFP8ih3Ryci0RHXBnuHZs9kGsrvH4Ty6wt-JwKO59X1qziFGxu-I8H4Qyyw4Ep4w\" /></p>\r\n\r\n<p>Tuy nhi&ecirc;n th&igrave; người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể r&uacute;t ngắn thời gian sạc bằng c&aacute;ch trang bị th&ecirc;m một củ sạc kh&aacute;c c&oacute; c&ocirc;ng suất tương ứng 25 W, n&oacute; gi&uacute;p m&aacute;y c&oacute; thể tận dụng được tối đa khả năng sạc để gi&uacute;p m&aacute;y nạp đầy pin trong thời gian ngắn hơn.</p>\r\n\r\n<p>Samsung Galaxy A23 5G sở hữu cho m&igrave;nh một m&agrave;n h&igrave;nh đẹp mắt, camera chất lượng, hiệu năng ổn định, vi&ecirc;n pin lớn c&ugrave;ng khả năng kết nối 5G hiện đại, đ&acirc;y sẽ l&agrave; một sự lựa chọn l&yacute; tưởng d&agrave;nh cho người d&ugrave;ng đang t&igrave;m kiếm một chiếc m&aacute;y tốt với một mức gi&aacute; kh&ocirc;ng qu&aacute; kh&oacute; để tiếp cận.</p>\r\n', 'Đã đăng', 1, '11-09-2023', 0),
(27, 'VHL#26', 'Samsung Galaxy A13 6GB', 4190000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung19.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a13-6gb\" target=\"_blank\">Samsung Galaxy A13 6GB</a>&nbsp;đem đến những trải nghiệm tuyệt vời c&ugrave;ng mức gi&aacute; cực kỳ phải chăng, nhằm hướng tới đối tượng học sinh, sinh vi&ecirc;n. M&aacute;y c&oacute; thiết kế trẻ trung, thanh tho&aacute;t, cụm camera được chăm ch&uacute;t chỉnh chu c&ugrave;ng thời gian sử dụng đ&aacute;p ứng cho cả ng&agrave;y d&agrave;i.</h3>\r\n\r\n<h3>Thiết kế trẻ trung, b&oacute;ng bẩy</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại kinh doanh tại Thế Giới Di Động\">Điện thoại</a>&nbsp;Galaxy A13 được chế tạo từ vật liệu nhựa nguy&ecirc;n khối gi&uacute;p thiết bị trở n&ecirc;n bền bỉ, độ ho&agrave;n thiện của c&aacute;c chi tiết kh&aacute; tốt. Khi cầm nắm sử dụng m&igrave;nh thấy m&aacute;y kh&ocirc;ng xuất hiện t&igrave;nh trạng &ldquo;ọp ẹp&rdquo; như tr&ecirc;n một v&agrave;i sản phẩm được l&agrave;m từ nhựa kh&aacute;c.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025943.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế nguyên khối - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025943.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025943.jpg\" title=\"Thiết kế nguyên khối - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<p>Mặt lưng thiết kế nhựa b&oacute;ng mang đến vẻ ngo&agrave;i &oacute;ng &aacute;nh, tuy nhi&ecirc;n t&igrave;nh trạng b&aacute;m dấu v&acirc;n tay thường xuy&ecirc;n xuất hiện khi m&igrave;nh sử dụng, điều n&agrave;y được cải thiện đối với phi&ecirc;n bản m&agrave;u xanh dương v&agrave; cam.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025945.jpg\" onclick=\"return false;\"><img alt=\"Mặt lưng nhựa bóng - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025945.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025945.jpg\" title=\"Mặt lưng nhựa bóng - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<p>Cụm camera ch&iacute;nh được đặt thẳng tr&ecirc;n mặt lưng, kh&ocirc;ng c&ograve;n bố tr&iacute; trong một cụm nữa, mang đến một vẻ ngo&agrave;i cực kỳ &ldquo;ph&aacute; c&aacute;ch&rdquo;, gi&uacute;p bạn g&acirc;y ấn tượng đối với mọi người xung quanh bởi thiết kế độc đ&aacute;o chưa từng c&oacute; tr&ecirc;n d&ograve;ng sản phẩm&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-a\" target=\"_blank\">A series</a>.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025946.jpg\" onclick=\"return false;\"><img alt=\"Bố trí camera lạ mắt - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025946.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025946.jpg\" title=\"Bố trí camera lạ mắt - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<h3>Trải nghiệm đ&atilde; mắt với m&agrave;n h&igrave;nh lớn&nbsp;</h3>\r\n\r\n<p>Galaxy A23 sở hữu m&agrave;n h&igrave;nh tr&agrave;n viền c&oacute; k&iacute;ch thước l&ecirc;n đến 6.6 inch sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh PLS TFT LCD, mang lại chất lượng h&igrave;nh ảnh theo m&igrave;nh đ&aacute;nh gi&aacute; c&oacute; độ tương phản kh&aacute; tốt, t&aacute;i tạo m&agrave;u sắc ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025948.jpg\" onclick=\"return false;\"><img alt=\"Màn hình kích thước lớn - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025948.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025948.jpg\" title=\"Màn hình kích thước lớn - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<p>Với độ ph&acirc;n giải Full HD+ (1080 x 2408 Pixels) cho ra h&igrave;nh ảnh hiển thị sắc n&eacute;t v&agrave; chi tiết, gi&uacute;p cho những trải nghiệm giải tr&iacute; th&ecirc;m phần sống động.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025950.jpg\" onclick=\"return false;\"><img alt=\"Màu sắc hiển thị đẹp mắt - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025950.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025950.jpg\" title=\"Màu sắc hiển thị đẹp mắt - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<h3>Chụp ảnh sắc n&eacute;t c&ugrave;ng nhiều t&iacute;nh năng</h3>\r\n\r\n<p>Trang bị ở phần mặt lưng l&agrave; 4 camera với th&ocirc;ng số lần lượt: Camera ch&iacute;nh 50 MP, phụ 5 MP v&agrave; 2 cảm biến c&oacute; c&ugrave;ng độ ph&acirc;n giải 2 MP, gi&uacute;p bạn dễ d&agrave;ng ghi lại những khoảnh khắc th&uacute; vị xung quanh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025952.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ nhiều tính năng - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025952.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025952.jpg\" title=\"Hỗ trợ nhiều tính năng - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<p>Ở điều kiện đầy đủ &aacute;nh s&aacute;ng, ảnh cho ra c&oacute; độ chi tiết tốt, m&agrave;u sắc h&agrave;i h&ograve;a, ảnh zoom l&ecirc;n kh&ocirc;ng bị bể qu&aacute; nhiều, gi&uacute;p việc hậu kỳ - chỉnh sửa ảnh trở n&ecirc;n dễ d&agrave;ng hơn. Tuy nhi&ecirc;n ảnh chụp đ&ecirc;m chưa thực sự l&agrave;m m&igrave;nh ưng &yacute; cho lắm bởi t&igrave;nh trạng nhiễu ảnh vẫn c&ograve;n xuất hiện.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025953.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp ở môi trường đủ sáng - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025953.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025953.jpg\" title=\"Ảnh chụp ở môi trường đủ sáng - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<p>Với t&iacute;nh năng chụp ảnh x&oacute;a ph&ocirc;ng, Galaxy A13 cho ra c&aacute;c bức ảnh kh&aacute; tốt, m&agrave;u sắc t&aacute;i tạo sắc n&eacute;t, chủ thể được t&aacute;ch biệt với nền v&agrave; t&igrave;nh trạng lẹm ảnh gần như kh&ocirc;ng xuất hiện khi chụp ở m&ocirc;i trường đầy đủ &aacute;nh s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025955.jpg\" onclick=\"return false;\"><img alt=\"Ảnh chụp chân dung - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025955.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025955.jpg\" title=\"Ảnh chụp chân dung - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<p>C&ograve;n ở chế độ chụp ảnh g&oacute;c si&ecirc;u rộng, h&igrave;nh ảnh được thể hiện một c&aacute;ch chi tiết, c&aacute;c g&oacute;c mở rộng kh&ocirc;ng bị b&oacute;p m&eacute;o qu&aacute; nhiều, mang đến h&igrave;nh ảnh c&oacute; độ ch&acirc;n thực cao.</p>\r\n\r\n<h3>Xử l&yacute; mượt m&agrave; cho mọi t&aacute;c vụ cơ bản</h3>\r\n\r\n<p>Trang bị b&ecirc;n trong l&agrave; bộ vi xử l&yacute; được ch&iacute;nh&nbsp;<a href=\"https://www.thegioididong.com/samsung\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung kinh doanh tại Thế Giới Di Động\">Samsung</a>&nbsp;nghi&ecirc;n cứu v&agrave; ph&aacute;t triển mang t&ecirc;n Exynos 850 8 nh&acirc;n với hiệu suất tối đa 2.0 GHz, theo m&igrave;nh đ&aacute;nh gi&aacute; th&igrave; đ&acirc;y l&agrave; con chip c&oacute; hiệu năng tương đối ổn trong ph&acirc;n kh&uacute;c, c&oacute; thể đ&aacute;p ứng ổn định trong c&aacute;c c&ocirc;ng việc h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; bảng đ&aacute;nh gi&aacute; hiệu năng qua hai ứng dụng: Benchmark (tr&aacute;i) v&agrave; 3D Mark (phải) m&agrave; m&igrave;nh c&oacute; test qua:</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025958.jpg\" onclick=\"return false;\"><img alt=\"Điểm đánh giá hiệu năng - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025958.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-025958.jpg\" title=\"Điểm đánh giá hiệu năng - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<p>Đối với tựa game Li&ecirc;n Qu&acirc;n Mobile ở mức max setting, m&aacute;y mang lại cho m&igrave;nh trải nghiệm chưa được tốt cho lắm khi t&igrave;nh trạng drop FPS vẫn c&ograve;n xuất hiện, người d&ugrave;ng n&ecirc;n c&agrave;i đặt mức cấu h&igrave;nh trung b&igrave;nh để đảm bảo trải nghiệm chơi được ổn định nhất.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030000.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm Liên Quân Mobile - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030000.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030000.jpg\" title=\"Trải nghiệm Liên Quân Mobile - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<p>PUBG Mobile - tựa game n&agrave;y bạn chơi ở mức đồ họa c&acirc;n bằng v&agrave; tốc độ khung h&igrave;nh cực cao, m&aacute;y đ&aacute;p ứng với mức FPS dao động tầm 20 - 28, t&igrave;nh trạng khựng m&agrave;n h&igrave;nh khi gặp kẻ địch hay di chuyển bằng phương tiện vẫn c&ograve;n xuất hiện, bạn c&oacute; thể hạ đồ họa xuống để c&oacute; trải nghiệm chơi tốt nhất.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030002.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm PUBG Mobile - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030002.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030002.jpg\" title=\"Trải nghiệm PUBG Mobile - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<p>Trang bị&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-6gb\" target=\"_blank\">RAM 6 GB</a>&nbsp;mang đến sự mượt m&agrave; cho t&aacute;c vụ đa nhiệm t&igrave;nh trạng load lại app gần như kh&ocirc;ng xảy ra cho d&ugrave; mở nhiều ứng dụng c&ugrave;ng l&uacute;c.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030004.jpg\" onclick=\"return false;\"><img alt=\"Trang bị 6 BG RAM - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030004.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030004.jpg\" title=\"Trang bị 6 BG RAM - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<h3>Sử dụng l&acirc;u d&agrave;i với vi&ecirc;n pin lớn</h3>\r\n\r\n<p>M&aacute;y cung cấp một thời gian sử dụng l&acirc;u d&agrave;i nhờ trang bị vi&ecirc;n pin c&oacute; dung lượng l&ecirc;n đến 5000 mAh, khi sử dụng li&ecirc;n tục thời gian onscreen l&ecirc;n đến hơn 8 giờ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030006.jpg\" onclick=\"return false;\"><img alt=\"Thời lượng sử dụng - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030006.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030006.jpg\" title=\"Thời lượng sử dụng - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<p>Samsung trang bị cho m&aacute;y c&ocirc;ng nghệ sạc 15 W gi&uacute;p tối ưu thời gian lấp đầy vi&ecirc;n pin xuống c&ograve;n 2 giờ 40 ph&uacute;t, tuy nhi&ecirc;n mức thời gian n&agrave;y vẫn chưa thực sự nhanh, n&ecirc;n bạn c&oacute; thể sạc thiết bị v&agrave;o những l&uacute;c nghỉ ngơi để kh&ocirc;ng mất nhiều thời gian chờ đợi.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030007.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ sạc nhanh 15 W - Samsung Galaxy A13 6GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030007.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/274360/samsung-galaxy-a13-6gb-310322-030007.jpg\" title=\"Hỗ trợ sạc nhanh 15 W - Samsung Galaxy A13 6GB\" /></a></p>\r\n\r\n<p>Qua những trải nghiệm tr&ecirc;n cho thấy Samsung Galaxy A13 đ&aacute;p ứng kh&aacute; tốt c&aacute;c nhu cầu sử dụng cơ bản h&agrave;ng ng&agrave;y, m&aacute;y c&oacute; thiết kế trẻ trung, camera chụp ảnh ổn, hứa hẹn sẽ mang đến những trải nghiệm tuyệt vời so với mức gi&aacute; m&agrave; n&oacute; trang bị.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(31, 'VHL#25', 'Samsung Galaxy A14 5G', 4490000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung18.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a14-5g\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy A14 5G đang kinh doanh tại Thế Giới Di Động\">Samsung Galaxy A14 5G</a>&nbsp;c&oacute; thể&nbsp;l&agrave; chiếc smartphone đầu ti&ecirc;n của năm 2023 được nh&agrave; sản xuất đến từ H&agrave;n Quốc ph&aacute;t h&agrave;nh ch&iacute;nh thức ở Việt Nam. M&aacute;y nổi bật với lối thiết kế trẻ trung, m&agrave;n h&igrave;nh k&iacute;ch thước lớn v&agrave; camera ch&iacute;nh c&oacute; độ ph&acirc;n giải l&ecirc;n tới 50 MP.</h3>\r\n\r\n<h3>D&aacute;ng vẻ quen thuộc đến từ d&ograve;ng A</h3>\r\n\r\n<p>Galaxy A14 được lấy cảm hứng thiết kế từ những mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-a\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy dòng A đang kinh doanh tại Thế Giới Di Động\">điện thoại Samsung d&ograve;ng A</a>&nbsp;trước đ&acirc;y với mặt lưng phẳng, m&agrave;n h&igrave;nh giọt nước c&ugrave;ng cụm camera xếp dọc. M&aacute;y trở n&ecirc;n g&oacute;c cạnh hơn khi c&aacute;c viền xung quanh bộ khung đều được gia c&ocirc;ng phẳng một c&aacute;ch tinh tế.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103347.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế trẻ trung - Samsung Galaxy A14 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103347.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103347.jpg\" title=\"Thiết kế trẻ trung - Samsung Galaxy A14 5G\" /></a></p>\r\n\r\n<p>Th&acirc;n m&aacute;y th&igrave; sẽ được l&agrave;m chủ yếu từ nhựa v&agrave; ho&agrave;n thiện theo kiểu v&acirc;n nổi bao phủ l&ecirc;n to&agrave;n bộ phần mặt lưng, điều n&agrave;y vừa gi&uacute;p m&aacute;y tăng th&ecirc;m độ bền bỉ nhưng cũng vừa gi&uacute;p bạn c&oacute; thể cầm nắm chắc hơn.</p>\r\n\r\n<h3>Trải nghiệm h&igrave;nh ảnh tốt hơn với m&agrave;n h&igrave;nh lớn</h3>\r\n\r\n<p>Tấm nền m&agrave; Samsung sử dụng tr&ecirc;n Galaxy A14 l&agrave; PLS LCD với ưu điểm l&agrave; t&aacute;i hiện h&igrave;nh ảnh c&oacute; độ s&aacute;ng cao, m&agrave;u sắc trung thực v&agrave; g&oacute;c nh&igrave;n rộng. Về độ chi tiết th&igrave; h&igrave;nh ảnh hiển thị tr&ecirc;n điện thoại l&agrave; cực kỳ sắc n&eacute;t nhờ c&oacute; độ ph&acirc;n giải Full HD+, điều n&agrave;y gi&uacute;p bạn hạn chế t&igrave;nh trạng răng cưa hay hỗ trợ c&agrave;i đặt nhiều mức đồ họa cao hơn khi chơi game.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103345.jpg\" onclick=\"return false;\"><img alt=\"Màn hình kích thước lớn - Samsung Galaxy A14 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103345.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103345.jpg\" title=\"Màn hình kích thước lớn - Samsung Galaxy A14 5G\" /></a></p>\r\n\r\n<p>Samsung Galaxy A14 vẫn c&ograve;n &aacute;p dụng kiểu m&agrave;n h&igrave;nh giọt nước quen thuộc, k&iacute;ch thước m&agrave;n h&igrave;nh th&igrave; sẽ l&agrave; 6.6 inch n&ecirc;n bạn c&oacute; thể thoải m&aacute;i hơn trong việc xem phim tr&ecirc;n một kh&ocirc;ng gian lớn. B&ecirc;n cạnh đ&oacute; th&igrave; h&atilde;ng c&ograve;n hỗ trợ th&ecirc;m cho m&aacute;y tần số qu&eacute;t 90 Hz, n&oacute; gi&uacute;p bạn c&oacute; thể thao t&aacute;c mượt m&agrave; hơn với những cử chỉ cuộn/lướt quen thuộc hằng ng&agrave;y.</p>\r\n\r\n<h3>Hiệu năng đ&aacute;p ứng tốt mọi t&aacute;c vụ hằng ng&agrave;y</h3>\r\n\r\n<p>Trang bị tr&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Tham khảo điện thoại Android đang kinh doanh tại Thế Giới Di Động\">điện thoại Android</a>&nbsp;n&agrave;y l&agrave; con chip&nbsp;MediaTek Dimensity 700&nbsp;với t&aacute;m nh&acirc;n, điều n&agrave;y gi&uacute;p m&aacute;y c&oacute; thể duy tr&igrave; sự ổn định khi sử dụng c&aacute;c t&aacute;c vụ từ lướt web, xem phim v&agrave; cho đến chơi game.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103340.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng ổn định - Samsung Galaxy A14 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103340.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103340.jpg\" title=\"Hiệu năng ổn định - Samsung Galaxy A14 5G\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh ưu điểm về hiệu năng ra th&igrave; Galaxy A14 c&ograve;n nổi trội với thế mạnh xử l&yacute; c&aacute;c t&aacute;c vụ đa nhiệm mượt m&agrave;, nhanh ch&oacute;ng, bởi đ&acirc;y l&agrave; mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-4gb\" target=\"_blank\" title=\"Tham khảo điện thoại RAM 4GB đang kinh doanh tại Thế Giới Di Động\">điện thoại RAM 4 GB</a>&nbsp;v&agrave; c&ograve;n được hỗ trợ t&iacute;nh năng mở rộng từ bộ nhớ trong.</p>\r\n\r\n<h3>Lưu trọn mọi khoảnh khắc một c&aacute;ch sắc n&eacute;t</h3>\r\n\r\n<p>Ở phi&ecirc;n bản th&igrave; m&aacute;y được trang bị bộ 3 camera trong đ&oacute; cảm biến ch&iacute;nh sẽ c&oacute; độ ph&acirc;n giải tới 50 MP, giờ đ&acirc;y th&igrave; người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể lưu lại những bức ảnh c&oacute; độ chi tiết cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103342.jpg\" onclick=\"return false;\"><img alt=\"Chụp ảnh sắc nét - Samsung Galaxy A14 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103342.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103342.jpg\" title=\"Chụp ảnh sắc nét - Samsung Galaxy A14 5G\" /></a></p>\r\n\r\n<p>K&egrave;m theo đ&oacute; sẽ l&agrave; c&aacute;c t&iacute;nh năng chụp ảnh hữu dụng như si&ecirc;u độ ph&acirc;n giải, x&oacute;a ph&ocirc;ng hay macro để bạn c&oacute; thể thỏa sức s&aacute;ng tạo trước những khung cảnh tuyệt đẹp với đa dạng phong c&aacute;ch chụp hơn.</p>\r\n\r\n<h3>K&eacute;o d&agrave;i thời gian sử dụng với vi&ecirc;n pin lớn</h3>\r\n\r\n<p>Cung cấp năng lượng cho điện thoại sẽ l&agrave; vi&ecirc;n pin dung lượng 5000 mAh kh&aacute; ấn tượng, bấy nhi&ecirc;u đ&acirc;y l&agrave; qu&aacute; đủ cho một ng&agrave;y d&agrave;i sử dụng trong việc lướt web hay xem phim. C&ograve;n đối với những bạn chỉ sử dụng để nghe gọi hay trả lời tin nhắn th&igrave; Galaxy A14 c&ograve;n c&oacute; thể đ&aacute;p ứng khả năng duy tr&igrave; l&ecirc;n tới hai ng&agrave;y li&ecirc;n tục.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103343.jpg\" onclick=\"return false;\"><img alt=\"Sử dụng dài lâu - Samsung Galaxy A14 5G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103343.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/271721/samsung-galaxy-a14-5g-090123-103343.jpg\" title=\"Sử dụng dài lâu - Samsung Galaxy A14 5G\" /></a></p>\r\n\r\n<p>Samsung c&oacute; lẽ l&agrave; một thương hiệu rất được ưa chuộng tr&ecirc;n thị trường ở thời điểm hiện tại, bởi h&atilde;ng c&acirc;n bằng kh&aacute; tốt về mọi mặt tr&ecirc;n những chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại di động đang kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;để gi&uacute;p cho người d&ugrave;ng c&oacute; thể tối ưu được gi&aacute; th&agrave;nh nhưng vẫn đảm bảo trải nghiệm được tốt, v&agrave; một trong những c&aacute;i t&ecirc;n s&aacute;ng gi&aacute; c&oacute; thể kể đến ch&iacute;nh l&agrave; Samsung Galaxy A14 5G.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 0),
(33, 'VHL#27', 'Samsung Galaxy A14 6GB', 3940000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung20.jpg', '<h3>Samsung tiếp tục chứng tỏ sự nỗ lực của m&igrave;nh trong việc cải thiện d&ograve;ng sản phẩm ph&acirc;n kh&uacute;c cấp thấp trong năm 2023 bằng việc ra mắt mẫu smartphone&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a14\" target=\"_blank\" title=\"Samsung Galaxy A14 4G\" type=\"Samsung Galaxy A14 4G\">Samsung Galaxy A14 4G</a>. Với thiết kế độc đ&aacute;o v&agrave; hiện đại, sản phẩm n&agrave;y đ&aacute;p ứng đầy đủ ti&ecirc;u ch&iacute; &quot;ngon - bổ - rẻ&quot; với cấu h&igrave;nh ổn định v&agrave; gi&aacute; cả cực kỳ hợp l&yacute;.</h3>\r\n\r\n<h3>Diện mạo thời thượng v&agrave; trẻ trung</h3>\r\n\r\n<p>Samsung Galaxy A14 4G được thiết kế với sự thừa hưởng vẻ đẹp tinh tế đến từ d&ograve;ng sản phẩm cao cấp Galaxy S23 series. Với vẻ đẹp hiện đại, m&agrave;u sắc thanh lịch v&agrave; g&oacute;c cạnh bo tr&ograve;n tinh tế, những điều n&agrave;y đem đến cho m&aacute;y một c&aacute;i nh&igrave;n cao cấp hơn về thiết kế để gi&uacute;p bất kỳ ai khi cầm nắm đều trở n&ecirc;n sang trọng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-112128.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A14 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-112128.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-112128.jpg\" title=\"Samsung Galaxy A14 4G\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, mặt lưng của chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung đang kinh doanh tại Thế Giới Di Động\">điện thoại Samsung</a>&nbsp;n&agrave;y c&ograve;n được ho&agrave;n thiện bằng nhựa nh&aacute;m với c&aacute;c đường v&acirc;n nổi bật, tạo th&ecirc;m một khối thẩm mỹ độc đ&aacute;o, giảm thiểu t&igrave;nh trạng b&aacute;m v&acirc;n tay v&agrave; mồ h&ocirc;i khi sử dụng trong thời gian d&agrave;i.</p>\r\n\r\n<h3>Giải tr&iacute; sống động với m&agrave;n h&igrave;nh lớn</h3>\r\n\r\n<p>Một điểm đắt gi&aacute; kh&aacute;c l&agrave;&nbsp;Samsung Galaxy A14 4G c&ograve;n được trang bị m&agrave;n h&igrave;nh k&iacute;ch thước khủng l&ecirc;n đến&nbsp;6.6 inch tỷ&nbsp;lệ khung h&igrave;nh 20:9 tương tự c&aacute;c smartphone d&ograve;ng S, vậy n&ecirc;n kh&ocirc;ng gian hiển thị sẽ đủ để đ&aacute;p ứng tốt c&aacute;c t&aacute;c vụ lướt web hay xem phim nhằm mang đến những ph&uacute;t gi&acirc;y trải nghiệm thoải m&aacute;i d&agrave;nh cho bạn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-112635.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A14 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-112635.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-112635.jpg\" title=\"Samsung Galaxy A14 4G\" /></a></p>\r\n\r\n<p>Bởi v&igrave; m&aacute;y thuộc ph&acirc;n kh&uacute;c gi&aacute; rẻ n&ecirc;n tấm nền m&agrave; h&atilde;ng sử dụng cho Galaxy A14 4G cũng chỉ nằm ở mức PLS LCD, nhưng đổi lại th&igrave; h&atilde;ng cũng sẽ hỗ trợ cho&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại di động đang kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;độ ph&acirc;n giải đạt mức Full HD+ thay v&igrave; HD+ như tr&ecirc;n một v&agrave;i đối thủ trong ph&acirc;n kh&uacute;c, nhờ đ&oacute; m&agrave; h&igrave;nh ảnh được t&aacute;i hiện sắc n&eacute;t hơn gi&uacute;p mọi nội dung đều trở n&ecirc;n ch&acirc;n thực.</p>\r\n\r\n<h3>Camera bắt trọn từng khung h&igrave;nh</h3>\r\n\r\n<p>Samsung Galaxy A14 4G kh&ocirc;ng chỉ g&acirc;y ấn tượng bởi hiệu năng ổn định, thiết kế mới lạ m&agrave; c&ograve;n nằm ở hệ thống camera sắc n&eacute;t, bao gồm cảm biến&nbsp;ch&iacute;nh 50 MP đi k&egrave;m ống k&iacute;nh si&ecirc;u rộng 5 MP v&agrave; camera macro 2 MP, mặt trước th&igrave; sẽ l&agrave; camera selfie độ ph&acirc;n giải 13 MP.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-112407.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A14 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-112407.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-112407.jpg\" title=\"Samsung Galaxy A14 4G\" /></a></p>\r\n\r\n<p>Ảnh chụp m&agrave; Samsung Galaxy A14 4G&nbsp;cho ra ở điều kiện đủ s&aacute;ng c&oacute; chất lượng h&igrave;nh ảnh r&otilde; r&agrave;ng, khả năng t&aacute;i tạo m&agrave;u sắc ổn, v&igrave; vậy người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể tự tin lưu giữ lại trọn vẹn vẻ đẹp của từng khoảnh khắc. Với mức gi&aacute; kh&ocirc;ng qu&aacute; cao nhưng lại sở hữu bộ camera tốt như vậy th&igrave; đ&acirc;y quả thực l&agrave; sản phẩm gi&aacute; rẻ đ&aacute;ng c&acirc;n nhắc d&agrave;nh cho những bạn đam m&ecirc; chụp ảnh.</p>\r\n\r\n<h3>Hiệu năng ổn định tr&ecirc;n mọi t&aacute;c vụ</h3>\r\n\r\n<p>Chuyển sang về mặt hiệu năng, Samsung Galaxy A14 4G được trang bị chip Exynos 850, đ&acirc;y&nbsp;l&agrave; một gương mặt m&agrave; rất nhiều nh&agrave; sản xuất&nbsp;<a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" title=\"Tham khảo điện thoại Android đang kinh doanh tại Thế Giới Di Động\">điện thoại Android</a>&nbsp;lựa chọn cho c&aacute;c sản phẩm gi&aacute; rẻ - tầm trung của m&igrave;nh bởi sức mạnh m&agrave; vi xử l&yacute; mang lại c&ugrave;ng mức gi&aacute; phải chăng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-113505.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A14 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-113505.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-113505.jpg\" title=\"Samsung Galaxy A14 4G\" /></a></p>\r\n\r\n<p>Với cấu h&igrave;nh đạt mức xung nhịp tối đa khoảng 2.0 GHz,&nbsp;Samsung Galaxy A14 4G&nbsp;ho&agrave;n to&agrave;n c&oacute; thể chiến&nbsp;được nhiều tựa game từ nhẹ đến trung b&igrave;nh ở thời điểm hiện tại, ph&ugrave; hợp với những người d&ugrave;ng cơ bản kh&ocirc;ng y&ecirc;u cầu qu&aacute; cao về hiệu suất nhưng vẫn đảm bảo&nbsp;hạn chế t&igrave;nh trạng giật lag khi sử dụng thường ng&agrave;y.&nbsp;</p>\r\n\r\n<h3>Pin &quot;tr&acirc;u&quot; thoả sức trải nghiệm</h3>\r\n\r\n<p>Samsung Galaxy A14 4G c&oacute; dung lượng pin khủng đến&nbsp;5000 mAh v&agrave; hỗ trợ sạc c&oacute; c&ocirc;ng suất 15 W. Đặc biệt khi bật chế độ si&ecirc;u tiết kiệm pin, thiết bị vẫn c&oacute; thể đ&aacute;p ứng đầy đủ nhu cầu li&ecirc;n lạc cơ bản ngay cả khi chỉ c&ograve;n 10% cho nhiều giờ liền.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-112232.jpg\" onclick=\"return false;\"><img alt=\"Samsung Galaxy A14 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-112232.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/292770/samsung-galaxy-a14-280223-112232.jpg\" title=\"Samsung Galaxy A14 4G\" /></a></p>\r\n\r\n<p>Với sự n&acirc;ng cấp mạnh mẽ v&agrave; to&agrave;n diện về mọi mặt,&nbsp;Samsung Galaxy A14 4G&nbsp;hứa hẹn sẽ l&agrave; một sản phẩm đ&aacute;ng c&acirc;n nhắc&nbsp;đối với những ai đang muốn t&igrave;m cho m&igrave;nh một sự lựa chọn tối ưu về kinh tế,&nbsp;nhất l&agrave; c&aacute;c bạn học sinh - sinh vi&ecirc;n.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 0),
(35, 'VHL#24', 'Samsung Galaxy A23 5G 4GB', 4890000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'samsung16.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a23-5g\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy A23 5G 4GB đang kinh doanh tại Thế Giới Di Động \">Samsung Galaxy A23 5G 4GB</a>&nbsp;l&agrave; thiết bị di động được cho ra mắt v&agrave;o th&aacute;ng 11/2022 tại thị trường Việt Nam. Điện thoại mang đến sự ch&uacute; &yacute; khi sở hữu mức gi&aacute; b&aacute;n rẻ, cấu h&igrave;nh ổn trong ph&acirc;n kh&uacute;c c&ugrave;ng vẻ ngo&agrave;i đầy bắt mắt.</h3>\r\n\r\n<h3>Nổi bật với vẻ ngo&agrave;i trẻ trung năng động</h3>\r\n\r\n<p>Về thiết kế của Galaxy A23, m&aacute;y sở hữu một vẻ ngo&agrave;i kh&aacute; đặc trưng của c&aacute;c mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-a\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại Samsung Galaxy A đang kinh doanh tại Thế Giới Di Động \">điện thoại Samsung Galaxy d&ograve;ng&nbsp; A</a>, với khung viền v&agrave; mặt lưng nối liền gi&uacute;p tạo n&ecirc;n một sự kết hợp đầy chắc chắn cho m&aacute;y, c&ugrave;ng với đ&oacute; l&agrave; c&aacute;c cạnh bo cong đem đến trải nghiệm cầm nắm chắc tay v&agrave; thoải m&aacute;i.</p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/d8RxyVTIpbH_nV_Y1aCoJ31bBb_h3L0muFMkMTrVy892ayhXDXKAhebOgLvZ85bwQhI2clE8iAmIWs4Ahnwm7G6uri993GhniQ8FbH15qJYpF6kpzCNA2oixnp_5RWxfbbp2CAQZs4Y90nef5rW2rQ\" /></p>\r\n\r\n<p>Mặt lưng của điện thoại th&igrave; được ho&agrave;n thiện theo kiểu nh&aacute;m nhẹ, đ&acirc;y c&oacute; thể coi l&agrave; một điểm nhấn gi&uacute;p m&aacute;y trở n&ecirc;n sang hơn. Khi sử dụng cũng hạn chế b&aacute;m dấu v&acirc;n tay v&agrave; c&aacute;c vết xước dăm cũng &iacute;t xảy ra, điều n&agrave;y gi&uacute;p m&aacute;y lu&ocirc;n trong t&igrave;nh trạng mới mẻ hơn so với c&aacute;c mẫu điện thoại c&oacute; mặt lưng nhẵn b&oacute;ng.</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/QdfUunRkpjNgtpAN5RM-6wQ6nVrrnaDKpeaSJC_ZCey4NRDrcWvfjl82aYePekM5pk_9A-tncgHSIVTajbVkQMtwhHYo-FnJ49usoKtK_pcmyCB2aXfDw8JGlm-fE08o0NrUIb1fG58bdRaVjQdmWw\" /></p>\r\n\r\n<p>Cả khung v&agrave; mặt lưng của m&aacute;y đều được l&agrave;m từ nhựa n&ecirc;n khi cầm th&igrave; m&igrave;nh c&oacute; cảm gi&aacute;c kh&aacute; nhẹ nh&agrave;ng, d&ugrave;ng l&acirc;u cũng &iacute;t khi bị mỏi tay. Nh&igrave;n tổng quan th&igrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" title=\"Xem thêm Điện thoại Samsung chính hãng đang kinh doanh tại Thế Giới Di Động\">điện thoại Samsung</a>&nbsp;nh&igrave;n cũng tương đối gọn g&agrave;ng v&agrave; nhỏ nhắn khi k&iacute;ch thước chỉ 165.4 mm cho chiều d&agrave;i v&agrave; 76.9 mm của bề ngang.</p>\r\n\r\n<p>Galaxy A23 c&oacute; kiểu thiết kế cụm camera kh&aacute; th&uacute; vị khi n&oacute; được l&agrave;m liền với mặt lưng c&ugrave;ng kiểu tạo h&igrave;nh &ldquo;nổi&rdquo;, điều n&agrave;y gi&uacute;p cho m&igrave;nh c&oacute; cảm gi&aacute;c như cụm n&agrave;y được trồi l&ecirc;n tr&ocirc;ng kh&aacute; th&uacute; vị v&agrave; th&iacute;ch mắt.</p>\r\n\r\n<p><img src=\"https://lh4.googleusercontent.com/OPYDcbhCQyqhHggui4HhSq9hE_zyUTC-j4AEzNli4iUlql0f5yGnBV_qSrmemRptyiV4bX81T1ERc_8TmIx_ekHhCT8AEZJ6DvnwoXI07-m-PJMq-XsuZcGocfRHq86F76OY-edVpnKflLXfkJQ2GA\" /></p>\r\n\r\n<p>Ở phần mặt trước th&igrave; sẽ l&agrave; m&agrave;n h&igrave;nh theo kiểu giọt nước, một lối thiết kế dễ bắt gặp tr&ecirc;n c&aacute;c mẫu m&aacute;y gi&aacute; rẻ tr&ecirc;n thị trường. Nhằm mang đến sự cứng c&aacute;p hơn th&igrave; Samsung cũng đ&atilde; trang bị th&ecirc;m k&iacute;nh cường lực Corning Gorilla Glass 3, n&oacute; mang đến khả năng kh&aacute;ng lại được c&aacute;c vết xước nhẹ tr&ecirc;n m&agrave;n h&igrave;nh.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/kinh-cuong-luc-gorilla-glass-3-la-gi-co-ben-khong-1209036\" target=\"_blank\">K&iacute;nh cường lực Gorilla Glass 3 l&agrave; g&igrave;? C&oacute; bền kh&ocirc;ng?</a></p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/b4LS1NGk-X-y13IMiGp47Q2Ano_GSwWEaAedJ_MtJ8smSex2TC-MJTRRrf2LZX_TCkJkicMXrfjqYr0IagZd-0s2HrmoSfeIwz4-bUhLbpbHwkYVXeSI7_Mnu68l37xxhkAVNwS1HgFiDKMFoG4V1g\" /></p>\r\n\r\n<h3>Chụp ảnh sắc n&eacute;t hơn với camera 50 MP</h3>\r\n\r\n<p>Galaxy A23 kh&ocirc;ng chỉ mang lại ấn tượng với camera ch&iacute;nh c&oacute; độ ph&acirc;n giải 50 MP m&agrave; m&aacute;y c&ograve;n c&oacute; th&ecirc;m điểm nhấn khi sở hữu 4 camera ở phần mặt lưng, trong đ&oacute; c&oacute;: Camera g&oacute;c si&ecirc;u rộng 5 MP, camera đo độ s&acirc;u 2 MP v&agrave; cuối c&ugrave;ng l&agrave; camera macro 2 MP.</p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/gOJ-WqFJi0drmAoAiDPf58LqrIsquczBCeKVnmGKmxAPUZ27hslPd3nLQULnZGsP1SF-ebtyBMk7kmwfNAW1gspDBQv9m8gBU8hGJmwU77ofkCdAerptjvaVwLpTmAoj1_990F5F6D1_US0ZAKT5vQ\" /></p>\r\n\r\n<p>Trong m&ocirc;i trường c&oacute; &aacute;nh s&aacute;ng đầy đủ, camera ch&iacute;nh của Samsung Galaxy A23 5G ghi lại những bức ảnh sắc n&eacute;t, m&agrave;u sắc được tinh chỉnh một c&aacute;ch h&agrave;i h&ograve;a v&agrave; sống động, đồng thời cung cấp độ chi tiết cao v&agrave; thể hiện được chiều s&acirc;u của khung h&igrave;nh. Ngay cả khi zoom, độ chi tiết v&agrave; sắc n&eacute;t vẫn được duy tr&igrave; m&agrave; kh&ocirc;ng bị giảm s&uacute;t qu&aacute; nhiều.</p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/-uw86YbqO3BpCjcXHK8TtfJUauAb6pUaeGTYAn_Zer3GPPf65d9tbhQyU3cIaRRayNYRhr-wsJ0jdynn7KucdzOvi0XX8mY9cJfTooGJ8BnZ-s9A_C4zfwzm_k_-4pCWXofahKOWZQHPV-ajiU9gjQ\" /></p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/x4QEJTRgkm1gSnMlm2TEcgb-VPYzKRs_15wy3j1jKbUjZLDtA8RBOJ4DGKpLDTl7H5DpPv94f2QWVp19w0FbWeTl1FIIQRyLlu7EWEww5owu92sM_Y4EScgveNrEmo7f7HYQVlYr7aPKF2dTUzqR4g\" /></p>\r\n\r\n<p>V&agrave;o c&aacute;c khung giờ l&yacute; tưởng như 4 - 5 giờ chiều, ảnh chụp từ Galaxy A23 c&ograve;n cho ra chất lượng ấn tượng hơn khi t&aacute;i tạo lại m&agrave;u sắc một c&aacute;ch rực rỡ v&agrave; đầy tươi mới, chất lượng ảnh phải gọi l&agrave; cực kỳ sắc n&eacute;t, sinh động v&agrave; gi&agrave;u chi tiết. Mặc d&ugrave; quang cảnh cũng đ&atilde; chạng vạng tối nhưng m&aacute;y vẫn c&oacute; thể xử l&yacute; v&agrave; c&acirc;n bằng lại gi&uacute;p cho tổng thể trở n&ecirc;n dễ nh&igrave;n v&agrave; đẹp mắt.</p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/lpq6xhme6iB-RypNECHflVh4ePNXKk3lkPDuxdUZyV5Mkx7jqsbylCwgBcppfs_LLcDT5jyY1trBJP7h5FNmSbojzEFdqk5O03ShSpFDS28fq8-V60N-bLtredUS9TiVAvFox92X9n8Mt7VHgLKRhg\" /></p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/xcPf2a6ryRcH-yorLqLv6bDsDnPDaMebg8Z-EDNqZL-s6sqxRK7HQSkJ_W8og4NeX6zW_1sukK8tqsGGVavscarAjXHLuUVAKHuY3bOtPt9Tj1KPXdjnSITfSnBkwvz4XKUU9wrpWp9JDGQ59f_cDQ\" /></p>\r\n\r\n<p>Trong điều kiện thiếu s&aacute;ng, camera tr&ecirc;n Samsung Galaxy A23 5G tiếp tục g&acirc;y ấn tượng bởi c&ocirc;ng nghệ xử l&yacute; ảnh vượt trội. H&igrave;nh ảnh chụp mang đến chất lượng tốt với độ chi tiết cao, đủ s&aacute;ng để c&oacute; thể nh&igrave;n r&otilde; được quang cảnh xung quanh, d&ugrave; c&oacute; một ch&uacute;t hiện tượng nhiễu nhưng kh&ocirc;ng ảnh hưởng đ&aacute;ng kể.</p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/D_J24aFrLf721GTBPLYFQB_fXZ2PcYLUcMGAYJjqWxqyoo_Z1PmXggVlhEseT_u9gvivRDXD6Jh_BLYXwOjkwdNrq94epLg5Dq4EwnK7ofd3t7BkjJ53DmJ95hbRlCBVvGbfplG6pHL05GzkYltKSA\" /></p>\r\n\r\n<h3>Hiệu năng xử l&yacute; tốt c&aacute;c t&aacute;c vụ cơ bản</h3>\r\n\r\n<p>Đối với Galaxy A23 5G th&igrave; h&atilde;ng sử dụng vi xử l&yacute; Snapdragon 695 được sản xuất bởi nh&agrave; Qualcomm. T&iacute;nh tại thời điểm ra mắt điện thoại th&igrave; đ&acirc;y được xem l&agrave; con chip kh&aacute; nổi bật trong ph&acirc;n kh&uacute;c cận gi&aacute; rẻ, vừa mang lại mức hiệu năng ổn song cũng mang lại khả năng tiết kiệm năng lượng.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-chip-snapdragon-695-5g-hieu-nang-manh-me-1456599\">T&igrave;m hiểu chip Snapdragon 695 5G</a></p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/LzQTBGhP3Fde6TpsrulCx7WRU7fYQV5LCoLajMXoYi2yjX6ZpSkYrTMuS4uPYuaFoDC8gUt1A_V1UhJzAVfcLK0VzD_lgGcfW9jwR0568Ugv586If-EiaRysYMde841GFTfgTS8BQqRfXHgCuGXr5g\" /></p>\r\n\r\n<p>Galaxy A23 khi được test qua một v&agrave;i phần mềm kiểm tra th&ocirc;ng số hiệu năng th&igrave; kết quả cho ra cũng kh&aacute; ấn tượng, m&aacute;y đạt 680 điểm đơn nh&acirc;n v&agrave; 1977 điểm đa nh&acirc;n tr&ecirc;n Geekbench 6, đồng thời đạt 10185 điểm tổng tr&ecirc;n PCMark.</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/cSjHrcu5WE39Xa8Am00-ozY1y9CbmKc53lXKQRsKIEB2yIz5UFv8KPj6n_zQH6FReFjvxVtqgDm1KF0Kc0tH3-Dm26UVemzQz8T4kOAUuuLhFy2avDP5MFPA32KafwwuqPgQ6S7UMzI4W1D8ypKGiA\" /></p>\r\n\r\n<p>Ngo&agrave;i ra Snapdragon 695 c&ograve;n l&agrave; mẫu chip c&oacute; hỗ trợ 5G, v&igrave; thế n&oacute; gi&uacute;p cho Galaxy A23 5G trở th&agrave;nh một trong những chiếc điện thoại ở ph&acirc;n kh&uacute;c gi&aacute; rẻ c&oacute; hỗ trợ chuẩn mạng di động ti&ecirc;n tiến, chỉ cần đầu tư một số tiền tương đối thấp l&agrave; ta đ&atilde; c&oacute; thể trải nghiệm được tốc độ kết nối mạng nhanh ch&oacute;ng đầy ấn tượng.</p>\r\n\r\n<p>Về c&aacute;c t&aacute;c vụ cơ bản sử dụng h&agrave;ng ng&agrave;y như lướt web đọc b&aacute;o, xem phim hay nhắn tin nghe gọi th&igrave; điện thoại xử l&yacute; dễ d&agrave;ng, mọi thứ diễn ra nhanh ch&oacute;ng v&agrave; mượt m&agrave;, kh&ocirc;ng c&oacute; bất kỳ hiện tượng giật lag n&agrave;o l&agrave;m ảnh hưởng đến qu&aacute; tr&igrave;nh trải nghiệm của m&igrave;nh cả.</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/OBtq4FFbblwH6mTq4axTNbQLvF96jriVCjPwclOhj9spV9wcV6tnmp7TZS8IETGJM1vAIHJ2hpggvGbakhbOvFmfb6yHOsD14jWpocXXBzC5Y5DfDLj_wEl2n39nq_iN7WtbVp_f8N1rhjJ4SCbVWg\" /></p>\r\n\r\n<p>C&ograve;n với c&aacute;c t&aacute;c vụ chơi game, Galaxy A23 vẫn được xem l&agrave; một chiếc m&aacute;y mang đến trải nghiệm ổn tr&ecirc;n c&aacute;c tựa game MOBA như Li&ecirc;n Qu&acirc;n Mobile hay Li&ecirc;n Minh Huyền Thoại Tốc Chiến. Kể cả khi c&agrave;i đặt cấu h&igrave;nh tr&ograve; chơi ở mức cao nhất th&igrave; mọi thứ đều su&ocirc;n mượt v&agrave; &iacute;t khi bị giật lag g&acirc;y cản trở.</p>\r\n\r\n<p><img src=\"https://lh4.googleusercontent.com/R3E74-DSIxq6qwXXdFSwCcPtMMAkyL5xEv1NT1jBWStd3UL5tz0HVvS6xQwK7rz49qMy6zmvgOM6upUC_WL_3dBqBL65squEyWOjniAhxFgfZrls_WxlwLvQdU015YtzDN0NvAktVYGppJ-wEkY9yA\" /></p>\r\n\r\n<p>Chuyển đến tựa game nặng hơn đ&oacute; ch&iacute;nh l&agrave; PUBG Mobile, mức đồ họa tối đa m&agrave; ta c&oacute; thể bật được tr&ecirc;n tựa game n&agrave;y l&agrave; HD c&ugrave;ng tốc độ khung h&igrave;nh cao. V&igrave; l&agrave; điện thoại thuộc ph&acirc;n kh&uacute;c thấp n&ecirc;n khi xử l&yacute; tựa game n&agrave;y ở cấu h&igrave;nh tối đa th&igrave; m&aacute;y kh&aacute; đuối, c&oacute; xảy ra giật lag trong l&uacute;c bắn nhau với nhiều kẻ địch.</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/WQCLxfiqmhXoBryM60ViW2Riy_3Hd1wmyofFWcW6Yo2NCZMlfhiZrK-Opif3oKRyKzESI-67-6VxDdV0U0L_5DRbLtt4SB7Ry0-HSO1YU4V3FwmOe1jhGbEWxn7bOqaqLVsFmjX5KS_vV2y8NJkDvA\" /></p>\r\n\r\n<p>Vậy n&ecirc;n để c&oacute; được trải nghiệm mượt m&agrave; một c&aacute;ch tối ưu nhất th&igrave; ta n&ecirc;n c&agrave;i đặt đồ họa cho tựa game n&agrave;y ở mức mượt v&agrave; tốc độ khung h&igrave;nh cao, điều n&agrave;y sẽ l&agrave;m tăng sự ổn định gi&uacute;p cho qu&aacute; tr&igrave;nh bắn nhau diễn ra mượt m&agrave;.&nbsp;</p>\r\n\r\n<p>Galaxy A23 l&agrave; một chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-ram-4gb\" target=\"_blank\" title=\"Tham khảo một số mẫu điện thoại RAM 4GB đang kinh doanh tại Thế Giới Di Động \">điện thoại RAM 4 GB</a>&nbsp;v&agrave; hỗ trợ mở rộng th&ecirc;m 4 GB từ bộ nhớ trong, điều n&agrave;y gi&uacute;p tăng cường khả năng xử l&yacute; đa nhiệm n&ecirc;n khi chuyển đổi qua lại giữa c&aacute;c ứng dụng m&igrave;nh lu&ocirc;n cảm thấy sự mượt m&agrave; v&agrave; nhanh ch&oacute;ng.&nbsp;</p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/tgUv3nBoHMqMJZK34VFgI-AOFHZDksFefz_k9MLI8jQ2chqDd0GSBh-JDXZSvqXAucBfM-XH6wfubz7t5WTWALxft7T6-V1OfINGNJOh8quxI33fKkLEC-ci4JT8huJH5lwxciYBVZX89qObcPcOfw\" /></p>\r\n\r\n<p>Ngo&agrave;i ra th&igrave; h&atilde;ng c&ograve;n ra mắt th&ecirc;m phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-a23-5g-6gb\" target=\"_blank\" title=\"Tham khảo điện thoại Samsung Galaxy A23 5G 6 GB tại Thế Giới Di Động\">Samsung Galaxy A23 5G 6 GB</a>, ph&ugrave; hợp với người d&ugrave;ng thường xuy&ecirc;n sử dụng đa t&aacute;c vụ, cần một mức RAM lớn v&agrave; ổn định để trải nghiệm sử dụng được mượt m&agrave; hơn.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh sắc n&eacute;t v&agrave; tần số qu&eacute;t 120 Hz</h3>\r\n\r\n<p>Galaxy A23 sở hữu một m&agrave;n h&igrave;nh c&oacute; độ chi tiết cao đạt chuẩn Full HD+ (1080 x 2408 Pixels), nhờ vậy m&agrave; mọi thứ khi được hiển thị tr&ecirc;n điện thoại đều rất sắc n&eacute;t, hiện tượng như răng cưa hay lộ r&otilde; điểm ảnh l&agrave; điều m&agrave; m&igrave;nh kh&ocirc;ng thấy xuất hiện tr&ecirc;n m&agrave;n h&igrave;nh của điện thoại.</p>\r\n\r\n<p><img src=\"https://lh4.googleusercontent.com/p75X_qb4Q6kUAjH64a1-CZFyiDxlDbvGnnsL3Wx5uNJSwVbdY_9nGtf2colXiUXuZmW0L2U3uca5fNahW-1rEF5m89Gp281GpVjWQZumVENyhRfS4fw8f2YaIqoJgVFCjeC0XMG1a9TOiXkrPAbEAQ\" /></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; th&igrave; m&agrave;n h&igrave;nh n&agrave;y c&ograve;n nổi bật hơn khi được trang bị tần số qu&eacute;t l&ecirc;n đến 120 Hz, n&oacute; gi&uacute;p cho c&aacute;c cử chỉ vuốt chạm trở n&ecirc;n trơn tru hơn khi nội dung tr&ecirc;n m&agrave;n h&igrave;nh được t&aacute;i tạo lại một c&aacute;ch nhanh ch&oacute;ng.&nbsp;</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/7MuRLEM1P2axM0t9PYI2c7xKCZ7takWs5782ZEbRo-MOED0O9iL4jRZrAFbzkv3chjzJAn2DFZMzJhdes1p7FwgRsb7UO4yCabYsS1Edl6OHlAcIWcZ7zeiY_jiM7eNukfEWLnaa4zWtFflqwKMNxA\" /></p>\r\n\r\n<p>Hiệu ứng khi vuốt qua - lại cũng kh&ocirc;ng bị t&igrave;nh trạng b&oacute;ng mờ hay lưu lại điểm ảnh, nhờ đ&oacute; m&agrave; trải nghiệm khi xem phim hay chơi game cũng đều trở n&ecirc;n tốt hơn.</p>\r\n\r\n<p>Galaxy A23 được trang bị tấm nền IPS LCD với k&iacute;ch thước lớn 6.6 inch, h&igrave;nh ảnh cho ra c&oacute; m&agrave;u sắc kh&aacute; tươi tắn, bắt mắt, g&oacute;c nh&igrave;n rộng v&agrave; kh&ocirc;ng gian rộng gi&uacute;p hiển thị mọi thứ một c&aacute;ch r&otilde; r&agrave;ng.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" target=\"_blank\">M&agrave;n h&igrave;nh IPS LCD l&agrave; g&igrave;? C&oacute; g&igrave; nổi bật? Thiết bị n&agrave;o c&oacute; m&agrave;n h&igrave;nh IPS?</a></p>\r\n\r\n<p><img src=\"https://lh4.googleusercontent.com/SrnBAI0ofgLJNX3sy7ASNSLp4nqiVLl09D56WjVXQFo0oI3tBzSZKUUoNkUdA7BkytsYG3cbCZwx9PmcRj5cHn0wV0gnqghH0Jq6hfMZwr0EkPXu8d7e8dlsg0ZXu__c0NfATUAgjo04kOiXFDg-xQ\" /></p>\r\n\r\n<p>Theo m&igrave;nh thấy th&igrave; m&agrave;n h&igrave;nh của&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Xem thêm Điện thoại chính hãng, giá rẻ đang kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;c&oacute; hơi &aacute;m xanh nhẹ một ch&uacute;t, đ&acirc;y c&oacute; thể xem l&agrave; một hạn chế điển h&igrave;nh của tấm nền n&agrave;y v&agrave; n&oacute; l&agrave;m cho m&agrave;u đen t&aacute;i hiện chưa được s&acirc;u cho lắm. Gi&aacute; m&agrave; Samsung trang bị tấm nền AMOLED d&agrave;nh cho Galaxy A23 th&igrave; đ&acirc;y quả l&agrave; một điều tuyệt vời, n&oacute; c&oacute; thể khuấy đảo cả ph&acirc;n kh&uacute;c khi kh&ocirc;ng c&oacute; bất kỳ hạn chế n&agrave;o.</p>\r\n\r\n<p><img src=\"https://lh3.googleusercontent.com/1L64RdCbz-xHe6D42tkXctvRl-Y0Dg2owQ1weSupPoH3ZVVpM9qxUCBJaue0nBjFWMyGfuYI_9YWaa6l00N2mKymuWE3K0m79fumnJM7RGD6yL5k1sqCofMbKXbBalU3fsg6BFk1Dm2w9wb3Bjxq0A\" /></p>\r\n\r\n<h3>Trang bị pin 5000 mAh v&agrave; hỗ trợ sạc nhanh</h3>\r\n\r\n<p>Galaxy A23 được trang bị một vi&ecirc;n pin lớn với mức dung lượng l&ecirc;n đến 5000 mAh, v&igrave; thế m&aacute;y ho&agrave;n to&agrave;n c&oacute; thể đồng h&agrave;nh c&ugrave;ng với ta trong một ng&agrave;y sử dụng d&agrave;i l&acirc;u m&agrave; kh&ocirc;ng cần quan t&acirc;m đến việc cắm sạc.</p>\r\n\r\n<p>Thực tế th&igrave; m&igrave;nh đ&atilde; d&ugrave;ng Galaxy A23 như một chiếc điện thoại ch&iacute;nh để phục vụ mọi nhu cầu giải tr&iacute;, m&igrave;nh sử dụng điện thoại từ l&uacute;c 100% dung lượng th&igrave; mất khoảng 8 tiếng 27 ph&uacute;t mới c&oacute; thể d&ugrave;ng hết.&nbsp;</p>\r\n\r\n<p><img src=\"https://lh6.googleusercontent.com/RvFA-mfssLTtHbYcoJY6queQnT60BoPdTbLq_AlwHAaSpDPUY7uLuyva452cq8doJ17h-6qzmOpO6Iryl5eFAFUmutSAPbucNdC5yCG8vWyN9P2QhiAh34NZwXEf4ztUfqCl5RiAoXWOhx7PSC9QKw\" /></p>\r\n\r\n<p>C&ograve;n về khả năng sạc pin cho điện thoại th&igrave; m&aacute;y c&oacute; khả năng tiếp nhận c&ocirc;ng suất sạc tối đa l&ecirc;n đến 25 W. Thế nhưng phụ kiện k&egrave;m theo m&aacute;y th&igrave; Samsung chỉ cấp cho ch&uacute;ng ta một củ sạc 15 W v&agrave; khi d&ugrave;ng n&oacute; để sạc th&igrave; tốn đ&acirc;u đ&oacute; khoảng 1 tiếng 50 ph&uacute;t.</p>\r\n\r\n<p><img src=\"https://lh5.googleusercontent.com/05niA7Fa7X64AutbAc4ppiXShH744FUmpkWAueg42KzS0Xef0g0Ut2Hbyz1Q4h-INK8W5LtsUBC5eL3Fcd4WrsYFP8ih3Ryci0RHXBnuHZs9kGsrvH4Ty6wt-JwKO59X1qziFGxu-I8H4Qyyw4Ep4w\" /></p>\r\n\r\n<p>Tuy nhi&ecirc;n th&igrave; người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể r&uacute;t ngắn thời gian sạc bằng c&aacute;ch trang bị th&ecirc;m một củ sạc kh&aacute;c c&oacute; c&ocirc;ng suất tương ứng 25 W, n&oacute; gi&uacute;p m&aacute;y c&oacute; thể tận dụng được tối đa khả năng sạc để gi&uacute;p m&aacute;y nạp đầy pin trong thời gian ngắn hơn.</p>\r\n\r\n<p>Samsung Galaxy A23 5G sở hữu cho m&igrave;nh một m&agrave;n h&igrave;nh đẹp mắt, camera chất lượng, hiệu năng ổn định, vi&ecirc;n pin lớn c&ugrave;ng khả năng kết nối 5G hiện đại, đ&acirc;y sẽ l&agrave; một sự lựa chọn l&yacute; tưởng d&agrave;nh cho người d&ugrave;ng đang t&igrave;m kiếm một chiếc m&aacute;y tốt với một mức gi&aacute; kh&ocirc;ng qu&aacute; kh&oacute; để tiếp cận.</p>\r\n', 'Đã đăng', 1, '08-09-2023', 0),
(36, 'VHL#100', 'iPad 9 WiFi 64GB', 6990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'table1.jpg', '<h3>Sau th&agrave;nh c&ocirc;ng của&nbsp;iPad 8,&nbsp;<a href=\"https://www.thegioididong.com/apple\" target=\"_blank\" title=\"Tham khảo sản phẩm Apple chính hãng tại Thegioididong.com\">Apple</a>&nbsp;cho đ&atilde; cho ra mắt&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-gen-9\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng IPad 9 WiFi 64GB đang kinh doanh tại Thế Giới Di Động \">m&aacute;y t&iacute;nh bảng&nbsp;iPad 9 WiFi 64GB</a>&nbsp;- phi&ecirc;n bản tiếp theo của d&ograve;ng iPad 10.2 inch, về cơ bản n&oacute; kế thừa những điểm mạnh từ c&aacute;c phi&ecirc;n bản trước đ&oacute; v&agrave; được cải tiến th&ecirc;m hiệu suất, trải nghiệm người d&ugrave;ng nhằm gi&uacute;p nhu cầu sử dụng giải tr&iacute; v&agrave; l&agrave;m việc tiện lợi, linh hoạt hơn.</h3>\r\n\r\n<h3>Thiết kế quen thuộc của d&ograve;ng iPad</h3>\r\n\r\n<p>iPad 9 sở hữu&nbsp;h&igrave;nh dạng chữ nhật k&egrave;m viền tr&ecirc;n dưới quen thuộc, c&oacute; 2 m&agrave;u sắc ch&iacute;nh để bạn lựa chọn l&agrave; x&aacute;m thanh lịch v&agrave; m&agrave;u bạc thời thượng cho bạn t&ugrave;y chọn theo sở th&iacute;ch của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-1.jpg\" onclick=\"return false;\"><img alt=\"Kiểu dáng thanh lịch, sang trọng - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-1.jpg\" title=\"Kiểu dáng thanh lịch, sang trọng - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<p>Vỏ ngo&agrave;i bằng aluminum s&aacute;ng b&oacute;ng, bền bỉ, chịu lực v&agrave; tản nhiệt tốt, bảo vệ to&agrave;n diện c&aacute;c linh kiện b&ecirc;n trong v&agrave; tăng t&iacute;nh thẩm mỹ cho sản phẩm.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-2.jpg\" onclick=\"return false;\"><img alt=\"Vỏ ngoài tối giản bền bỉ - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-2.jpg\" title=\"Vỏ ngoài tối giản bền bỉ - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<p>Thiết kế n&uacute;t Home ở giữa viền dưới của m&agrave;n h&igrave;nh cho bạn thao t&aacute;c nhanh khi cần. Cảm biến v&acirc;n tay Touch ID được t&iacute;ch hợp ở trong ph&iacute;m Home, cho bạn mở kh&oacute;a một c&aacute;ch nhanh ch&oacute;ng v&agrave; an to&agrave;n.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-3.jpg\" onclick=\"return false;\"><img alt=\"Cảm biến vân tay - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-3.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-3.jpg\" title=\"Cảm biến vân tay - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh hiển thị r&otilde; r&agrave;ng</h3>\r\n\r\n<p>Nổi bật với k&iacute;ch thước m&agrave;n h&igrave;nh l&ecirc;n đến 10.2 inch cho khu vực hiển thị c&aacute;c nội dung rộng r&atilde;i hơn, người d&ugrave;ng c&oacute; thể theo d&otilde;i c&aacute;c chương tr&igrave;nh giải tr&iacute;, xử l&yacute; c&aacute;c văn bản tiện lợi hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-4.jpg\" onclick=\"return false;\"><img alt=\"Màn hình 10.2 inch sắc nét - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-4.jpg\" title=\"Màn hình 10.2 inch sắc nét - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<p>Sử dụng m&agrave;n h&igrave;nh Retina c&oacute; độ tương phản cao, độ ph&acirc;n giải 1620 x 2160 Pixels t&aacute;i hiện h&igrave;nh ảnh một c&aacute;ch sinh động. Hơn nữa, bề mặt m&agrave;n h&igrave;nh c&oacute; lớp phủ oleophobic hạn chế b&aacute;m v&acirc;n tay, giữ cho m&agrave;n h&igrave;nh lu&ocirc;n sạch sẽ, nh&igrave;n r&otilde; hơn.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-5.jpg\" onclick=\"return false;\"><img alt=\"Màn hình Retina với độ phân giải cao - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-5.jpg\" title=\"Màn hình Retina với độ phân giải cao - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<p>Ngo&agrave;i ra&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng đang kinh doanh tại Thế Giới Di Động \">m&aacute;y t&iacute;nh bảng&nbsp;iPad&nbsp;</a>n&agrave;y c&ograve;n hỗ trợ c&ocirc;ng nghệ True Tone, c&aacute;c cảm biến xung quanh sẽ nhận biết &aacute;nh s&aacute;ng, m&agrave;u sắc xung quanh gi&uacute;p hệ thống tinh chỉnh độ s&aacute;ng theo nhiệt độ m&agrave;u ph&ugrave; hợp với m&ocirc;i trường đang sử dụng gi&uacute;p h&igrave;nh ảnh hiển thị tự nhi&ecirc;n, xem thoải m&aacute;i trong mọi điều kiện.</p>\r\n\r\n<p>C&ugrave;ng với trải nghiệm h&igrave;nh ảnh tuyệt vời th&igrave; iPad 9 c&ograve;n được trang bị loa stereo cực hay, khiến cho mọi trải nghiệm của bạn tr&ecirc;n chiếc m&aacute;y t&iacute;nh bảng n&agrave;y trở n&ecirc;n trọn vẹn hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-6.jpg\" onclick=\"return false;\"><img alt=\"Được trang bị loa Stereo - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-6.jpg\" title=\"Được trang bị loa Stereo - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<h3>FaceTime chất lượng cao&nbsp;</h3>\r\n\r\n<p>Camera Ultra Wide ph&iacute;a trước c&oacute; độ ph&acirc;n giải 12 MP c&oacute; g&oacute;c nh&igrave;n rộng đến 122&deg;, cho bạn chụp ảnh HDR, quay video Full HD với tốc độ khung h&igrave;nh tới 60 fps, hỗ quay video tua nhanh thời gian (Time‑lapse).</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-7.jpg\" onclick=\"return false;\"><img alt=\"Camera Ultra Wide 12 MP - Camera Ultra Wide\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-7.jpg\" title=\"Camera Ultra Wide 12 MP - Camera Ultra Wide\" /></a></p>\r\n\r\n<p>C&ocirc;ng nghệ&nbsp;Center Stage ở camera trước sẽ đảm bảo bạn lu&ocirc;n hiện diện đầy nổi bật trong cuộc gọi video, khi bạn thay đổi vị tr&iacute; th&igrave; khung h&igrave;nh sẽ tự động di chuyển, zoom theo để giữ bạn trong tầm nh&igrave;n, nếu c&oacute; người kh&aacute;c tham gia, ống k&iacute;nh ph&aacute;t hiện v&agrave; tự thu nhỏ để đưa họ v&agrave;o m&agrave;n h&igrave;nh, gi&uacute;p trải nghiệm gọi video được tối ưu hơn.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-8.jpg\" onclick=\"return false;\"><img alt=\"Công nghệ Center Stage - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-8.jpg\" title=\"Công nghệ Center Stage - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<p>Camera sau độ ph&acirc;n giải&nbsp;8 MP, c&oacute; khả năng&nbsp;zoom kỹ thuật số 5x, hỗ trợ chụp to&agrave;n cảnh Panorama v&agrave; chụp ảnh HDR. C&ocirc;ng nghệ ổn định h&igrave;nh ảnh tự động gi&uacute;p bạn tạo ra những bức h&igrave;nh từ phong cảnh đến h&igrave;nh ch&acirc;n dung đều trở n&ecirc;n đẹp mắt hơn.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-9.jpg\" onclick=\"return false;\"><img alt=\"Camera sau 8 MP - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-9.jpg\" title=\"Camera sau 8 MP - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng đang kinh doanh tại Thế Giới Di Động \">M&aacute;y t&iacute;nh bảng</a>&nbsp;hỗ trợ quay video chất lượng Full HD v&agrave; c&oacute; thể zoom 3x, ngo&agrave;i ra c&ograve;n c&oacute; thể quay video chuyển động chậm, video tua nhanh thời gian.&nbsp;B&ecirc;n cạnh đ&oacute;, hệ thống camera sau đa năng cũng cho ph&eacute;p bạn qu&eacute;t t&agrave;i liệu v&agrave; trải nghiệm c&aacute;c ứng dụng AR linh hoạt.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-10.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ nhiều tính năng - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-10.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-10.jpg\" title=\"Hỗ trợ nhiều tính năng - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<h3>Trang bị cấu h&igrave;nh mạnh mẽ</h3>\r\n\r\n<p>M&aacute;y được t&iacute;ch hợp con chip Apple A13 Bionic 6 nh&acirc;n cho hiệu suất cao đến 20% so với thế hệ trước bao gồm&nbsp;CPU, GPU đến Neural Engine. Kết hợp c&ugrave;ng RAM 3 GB th&igrave; với hiệu năng n&agrave;y iPad Gen 9 64GB sẽ mang đến những trải nghiệm tr&ograve; chơi sống động, mượt m&agrave; v&agrave; c&oacute; độ chi tiết cao, c&aacute;c t&aacute;c vụ 3D cũng sẽ được xử l&yacute; một c&aacute;ch nhanh ch&oacute;ng nhất.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-11.jpg\" onclick=\"return false;\"><img alt=\"Chip Apple A13 Bionic 6 nhân - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-11.jpg\" title=\"Chip Apple A13 Bionic 6 nhân - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<p>Về khả năng kết nối iPad 9 hỗ trợ Wi-Fi 802.11 a/b/g/n/ac v&agrave; Bluetooth v4.2 cung cấp đường truyền kết nối kh&ocirc;ng d&acirc;y ổn định cho bạn gh&eacute;p nối v&agrave; sử dụng c&ugrave;ng c&aacute;c thiết bị kh&aacute;c một c&aacute;ch dễ d&agrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-12.jpg\" onclick=\"return false;\"><img alt=\"Đường truyền ổn định - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-12.jpg\" title=\"Đường truyền ổn định - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<p>M&aacute;y c&oacute; bộ nhớ trong 64 GB&nbsp;cho người d&ugrave;ng c&oacute; nhu cầu ghi lại nhiều video, h&igrave;nh ảnh, tr&ograve; chơi hơn. Hỗ trợ&nbsp;iCloud - dịch vụ lưu trữ đ&aacute;m m&acirc;y cho trường hợp bạn cần th&ecirc;m kh&ocirc;ng gian lưu trữ dữ liệu.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-13.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng bộ nhớ - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-13.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-13.jpg\" title=\"Dung lượng bộ nhớ - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<p>Thiết lập iPadOS 15 với khả năng đa nhiệm ấn tượng,&nbsp;bố cục widget trực quan cho m&agrave;n h&igrave;nh ch&iacute;nh v&agrave; ghi ch&uacute; Quick Note tr&ecirc;n to&agrave;n hệ thống, thư viện ứng dụng phong ph&uacute; cho bạn dễ d&agrave;ng c&aacute; nh&acirc;n h&oacute;a trải nghiệm ph&ugrave; hợp với nhu cầu của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-14.jpg\" onclick=\"return false;\"><img alt=\"iPadOS đa nhiệm ổn định - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-14.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-14.jpg\" title=\"iPadOS đa nhiệm ổn định - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<p>Hệ điều h&agrave;nh n&agrave;y c&ograve;n cung cấp th&ecirc;m nhiều t&iacute;nh năng kh&aacute;c như ứng dụng dịch với khả năng tự động, chế độ xem trực diện, t&iacute;nh năng Live Text nhận dạng văn bản b&ecirc;n trong ảnh v&agrave; Focus lọc th&ocirc;ng b&aacute;o dựa v&agrave;o hoạt động bạn đang l&agrave;m,...&nbsp;</p>\r\n\r\n<p>Tương th&iacute;ch với&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/but-cam-ung-apple-pencil-1-mk0c2\" target=\"_blank\" title=\"Bút cảm ứng Apple Pencil 1 MK0C2\">Apple Pencil 1</a>&nbsp;v&agrave;&nbsp;b&agrave;n ph&iacute;m th&ocirc;ng minh của h&atilde;ng, cho bạn biến m&aacute;y t&iacute;nh bảng th&agrave;nh tập vẽ, hoặc sử dụng như một chiếc laptop thực thụ, bạn c&oacute; thể l&agrave;m việc giải tr&iacute; với iPad 9 mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-15.jpg\" onclick=\"return false;\"><img alt=\"Tương thích với Apple Pencil 1 - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-15.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-15.jpg\" title=\"Tương thích với Apple Pencil 1 - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<h3>Thời lượng pin tốt</h3>\r\n\r\n<p>Thiết bị cho dung lượng pin 32.4 Wh (~ 8600 mAh) bạn c&oacute; thể chơi game, xem phim, đọc tin tức, kiểm tra email, xử l&yacute; c&ocirc;ng việc ngay tr&ecirc;n m&aacute;y t&iacute;nh bảng&nbsp;dễ d&agrave;ng cả ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-16.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng pin 32.4 Wh - iPad 9 WiFi 64GB \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-16.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/247517/ipad-gen-9-16.jpg\" title=\"Dung lượng pin 32.4 Wh - iPad 9 WiFi 64GB \" /></a></p>\r\n\r\n<p>Người d&ugrave;ng dễ d&agrave;ng sạc lại qua cổng&nbsp;Lightning,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng có sạc nhanh đang kinh doanh tại Thế Giới Di Động \">m&aacute;y t&iacute;nh bảng c&oacute;&nbsp;sạc nhanh</a>&nbsp;với d&ograve;ng sạc l&ecirc;n đến 20 W, gi&uacute;p tiết kiệm thời gian sạc v&agrave; cho bạn tận dụng tối đa c&ocirc;ng năng của thiết bị.&nbsp;</p>\r\n\r\n<p>M&aacute;y t&iacute;nh bảng iPad 9 WiFi 64GB sang trọng về thiết kế, ổn định về cấu h&igrave;nh, tiện lợi về loạt t&iacute;nh năng th&ocirc;ng minh, camera chất lượng, dung lượng pin ấn tượng, lựa chọn ph&ugrave; hợp cho mọi người d&ugrave;ng.</p>\r\n', 'Đã đăng', 2, '08-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(37, 'VHL#101', 'iPad Air 5 M1 Wifi 64GB', 14090000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'table2.jpg', '<h3><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-air-5\" target=\"_blank\" title=\"iPad Air 5 M1 Wifi 64 GB\" type=\"iPad Air 5 M1 Wifi 64 GB\">iPad Air 5 M1 Wifi 64 GB</a>&nbsp;đ&atilde; được c&ocirc;ng bố tại sự kiện Peek Performance diễn ra h&ocirc;m 9/3 (theo giờ Việt Nam). Năm nay&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Apple\" type=\"Apple\">Apple</a>&nbsp;đ&atilde; c&oacute; những thay đổi lớn về cả hiệu năng v&agrave; bổ sung m&agrave;u sắc mới cho thiết bị.</h3>\r\n\r\n<h3>Sức mạnh từ con chip M1</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/248096/ipad-air-5-150322-044254.jpeg\" onclick=\"return false;\"><img alt=\"Sức mạnh từ con chip M1 - iPad Air 5 M1 Wifi 64 GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/248096/ipad-air-5-150322-044254.jpeg\" src=\"https://cdn.tgdd.vn/Products/Images/522/248096/ipad-air-5-150322-044254.jpeg\" title=\"Sức mạnh từ con chip M1 - iPad Air 5 M1 Wifi 64 GB\" /></a></p>\r\n\r\n<p>Apple M1 8 nh&acirc;n l&agrave; vi xử l&yacute; do ch&iacute;nh Apple nghi&ecirc;n cứu v&agrave; sản xuất. Con chip n&agrave;y đ&atilde; được chứng minh sức mạnh qua nhiều d&ograve;ng sản phẩm v&agrave; b&acirc;y giờ đ&atilde; xuất hiện tr&ecirc;n iPad Air 5 M1 Wifi 64 GB. Với 8 nh&acirc;n CPU, vi xử l&yacute; n&agrave;y sẽ gi&uacute;p thiết bị c&oacute; thể hoạt động ổn định c&ugrave;ng với&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-ram-8gb\" target=\"_blank\">RAM 8 GB</a>.&nbsp;</p>\r\n\r\n<h3>Thiết kế sang trọng</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/248096/ipad-air-5-150322-044250.jpeg\" onclick=\"return false;\"><img alt=\"Thiết kế sang trọng - iPad Air 5 M1 Wifi 64 GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/248096/ipad-air-5-150322-044250.jpeg\" src=\"https://cdn.tgdd.vn/Products/Images/522/248096/ipad-air-5-150322-044250.jpeg\" title=\"Sức mạnh từ con chip M1 - iPad Air 5 M1 Wifi 64 GB\" /></a></p>\r\n\r\n<p>iPad Air 5 M1 Wifi 64 GB c&oacute; thiết kế phẳng ở 4 cạnh, mặt sau được l&agrave;m từ nh&ocirc;m với nhiều m&agrave;u sắc tươi trẻ. Đặc biệt, năm nay Apple đ&atilde; bổ sung m&agrave;u t&iacute;m cho d&ograve;ng iPad Air 5 M1 Wifi 64 GB, m&agrave;u sắc n&agrave;y sẽ g&acirc;y ấn tượng mạnh khi ch&uacute;ng ta cầm m&aacute;y sử dụng. M&agrave;n h&igrave;nh của m&aacute;y cũng được l&agrave;m phẳng với k&iacute;ch thước 10.9 inch.</p>\r\n\r\n<h3>T&iacute;nh năng đặc biệt</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/248096/ipad-air-5-150322-044252.jpeg\" onclick=\"return false;\"><img alt=\"Nhiều tính năng - iPad Air 5 M1 Wifi 64 GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/248096/ipad-air-5-150322-044252.jpeg\" src=\"https://cdn.tgdd.vn/Products/Images/522/248096/ipad-air-5-150322-044252.jpeg\" title=\"Nhiều tính năng - iPad Air 5 M1 Wifi 64 GB\" /></a></p>\r\n\r\n<p>iPad Air 5 M1 Wifi 64 GB c&oacute; nhiều t&iacute;nh năng đặc biệt: Kết nối Apple Pencil 2, Kết nối b&agrave;n ph&iacute;m rời, Micro k&eacute;p, Mở kh&oacute;a bằng v&acirc;n tay, Nam ch&acirc;m v&agrave; sạc cho Apple Pencil.</p>\r\n\r\n<p>Tổng kết lại với những người th&iacute;ch xem phim nghe nhạc, lướt mạng x&atilde; hội giải tr&iacute; tr&ecirc;n một thiết bị m&agrave;n h&igrave;nh to th&igrave; đ&acirc;y l&agrave; 1 trong những&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\">m&aacute;y t&iacute;nh bảng</a>&nbsp;đ&aacute;ng ch&uacute; &yacute;.</p>\r\n', 'Đã đăng', 2, '08-09-2023', 0),
(38, 'VHL#102', 'iPad 10 WiFi 64GB', 11190000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'table3.jpg', '<h3>Sau khi tr&igrave;nh l&agrave;ng h&agrave;ng loạt mẫu iPhone v&agrave;o th&aacute;ng 09/2022 th&igrave; Apple cũng đ&atilde; tiếp tục giới thiệu series iPad mới cho năm 2022. Trong đ&oacute;&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-gen-10\" target=\"_blank\" title=\"Tham khảo máy tính bảng iPad 10 WiFi đang kinh doanh tại Thế Giới Di Động\">iPad 10 WiFi 64GB</a>&nbsp;l&agrave; c&aacute;i t&ecirc;n được h&atilde;ng ch&uacute; trọng kh&aacute; nhiều về việc tối ưu gi&aacute; th&agrave;nh nhằm gi&uacute;p người d&ugrave;ng c&oacute; thể dễ d&agrave;ng tiếp cận. M&aacute;y hỗ trợ hệ điều h&agrave;nh iPadOS 16 c&ugrave;ng con chip Apple A14 Bionic gi&uacute;p mang lại hiệu năng vượt trội.</h3>\r\n\r\n<h3>Thiết kế trẻ trung</h3>\r\n\r\n<p>iPad 10 sẽ c&oacute; một thiết kế kh&aacute; quen thuộc khi được ho&agrave;n thiện với phần vật liệu chủ yếu l&agrave; kim loại ở phần mặt lưng, c&aacute;c cạnh v&agrave; hai mặt đều được v&aacute;t phẳng để mang lại c&aacute;i nh&igrave;n sang trọng. Đi k&egrave;m với đ&oacute; l&agrave; c&aacute;c g&oacute;c được bo tr&ograve;n gi&uacute;p cho tổng thể m&aacute;y t&iacute;nh bảng trở n&ecirc;n thanh tho&aacute;t v&agrave; hiện đại hơn thay v&igrave; vu&ocirc;ng vức tr&ocirc;ng như một cuốn tập.</p>\r\n\r\n<p>Với iPad thế hệ thứ 10 th&igrave; nh&agrave; Apple mang đến cho ch&uacute;ng ta 4 phi&ecirc;n bản với c&aacute;c m&agrave;u sắc như: Bạc, hồng, v&agrave;ng v&agrave; xanh. Trong đ&oacute; c&aacute;c tone m&agrave;u của năm nay được l&agrave;m đậm hơn đ&ocirc;i ch&uacute;t so với những thế hệ trước đ&oacute;, đ&acirc;y c&oacute; thể xem l&agrave; đặc điểm nhận dạng gi&uacute;p người d&ugrave;ng ph&acirc;n biệt iPad 10 c&aacute;c đ&agrave;n anh của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031416.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế trẻ trung - iPad 10 WiFi 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031416.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031416.jpg\" title=\"Thiết kế trẻ trung - iPad 10 WiFi 64GB\" /></a></p>\r\n\r\n<p>Mẫu&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Tham khảo iPad đang kinh doanh tại Thế Giới Di Động\">iPad</a>&nbsp;n&agrave;y chỉ c&oacute; độ mỏng khoảng 7 mm n&ecirc;n khi cầm tr&ecirc;n tay th&igrave; m&aacute;y mang lại cho ch&uacute;ng ta một c&aacute;i nh&igrave;n sang trọng v&agrave; mang t&iacute;nh thời thượng, điều n&agrave;y l&agrave; dễ hiểu bởi hiện tại xu hướng thiết kế mỏng nhẹ v&agrave; thanh lịch đang l&agrave; mục ti&ecirc;u m&agrave; hầu hết c&aacute;c nh&agrave; sản xuất c&ocirc;ng nghệ đang hướng đến.</p>\r\n\r\n<p>Điểm vượt trội hơn so với phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-gen-9\" target=\"_blank\" title=\"Tham khảo iPad 9 WiFi 64GB đang kinh doanh tại Thế Giới Di Động\">iPad 9 WiFi 64GB</a>&nbsp;v&agrave;o năm ngo&aacute;i l&agrave; phần viền m&agrave;n h&igrave;nh giờ đ&acirc;y đ&atilde; được tối ưu v&agrave; l&agrave;m đều ở 4 cạnh, n&oacute; mang lại cho m&aacute;y một c&aacute;i nh&igrave;n c&acirc;n đối v&agrave; h&agrave;i h&ograve;a hơn, cảm gi&aacute;c sử dụng cũng trở n&ecirc;n cực kỳ dễ chịu khi m&agrave; ch&uacute;ng ta nh&igrave;n v&agrave;o m&agrave;n h&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031418.jpg\" onclick=\"return false;\"><img alt=\"Viền màn hình mỏng - iPad 10 WiFi 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031418.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031418.jpg\" title=\"Màn hình chất lượng - iPad 10 WiFi 64GB\" /></a></p>\r\n\r\n<p>Ph&iacute;m Home tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-10-2-inch\" target=\"_blank\" title=\"Xem thêm iPad 9 10.2 inch đang kinh doanh tại Thế Giới Di Động\">iPad 9</a>&nbsp;giờ đ&acirc;y cũng đ&atilde; kh&ocirc;ng c&ograve;n xuất hiện ở phi&ecirc;n bản mới nữa. Tuy nhi&ecirc;n với sự ph&aacute;t triển kh&ocirc;ng ngừng về c&ocirc;ng nghệ th&igrave; việc loại bỏ cũng chỉ l&agrave; chuyện sớm muộn, b&acirc;y giờ c&oacute; thể coi l&agrave; thời điểm th&iacute;ch hợp để ch&uacute;ng ta l&agrave;m quen với những thao t&aacute;c sử dụng mới. Đa nhiệm hay tho&aacute;t app đ&atilde; c&oacute; thể thao t&aacute;c bằng cử chỉ ở m&agrave;n h&igrave;nh, n&oacute; mang lại một cảm gi&aacute;c sử dụng hiện đại v&agrave; cao cấp hơn tr&ecirc;n một thiết bị hướng tới tương lai.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031420.jpg\" onclick=\"return false;\"><img alt=\"Loại bỏ phím Home - iPad 10 WiFi 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031420.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031420.jpg\" title=\"Loại bỏ phím Home - iPad 10 WiFi 64GB\" /></a></p>\r\n\r\n<h3>L&agrave;m việc hiệu quả với m&agrave;n h&igrave;nh chất lượng</h3>\r\n\r\n<p>iPad 10 sẽ c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh l&agrave; 10.9 inch, đ&acirc;y c&oacute; thể xem l&agrave; con số kh&aacute; ph&ugrave; hợp với hầu hết mọi người d&ugrave;ng, n&oacute; kh&ocirc;ng qu&aacute; to v&agrave; cồng kềnh hay qu&aacute; nhỏ để ta nheo mắt mỗi khi nh&igrave;n.&nbsp;</p>\r\n\r\n<p>M&agrave;n h&igrave;nh 10.9 inch được xem l&agrave; chuẩn m&agrave;n h&igrave;nh l&yacute; tưởng cho kh&aacute;ch h&agrave;ng đang l&agrave; học sinh, sinh vi&ecirc;n, d&acirc;n văn ph&ograve;ng hay những bạn đam m&ecirc; vẽ vời bởi ta c&oacute; thể dễ d&agrave;ng mang theo đến bất cứ nơi đ&acirc;u m&agrave; kh&ocirc;ng sợ bị vướng hay cảm thấy chật chội.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031423.jpg\" onclick=\"return false;\"><img alt=\"Màn hình kích thước lớn - iPad 10 WiFi 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031423.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031423.jpg\" title=\"Màn hình kích thước lớn - iPad 10 WiFi 64GB\" /></a></p>\r\n\r\n<p>M&aacute;y t&iacute;nh bảng lần n&agrave;y sẽ được sử dụng tấm nền Retina IPS LCD, loại c&ocirc;ng nghệ n&agrave;y được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển độc quyền bởi Apple cho n&ecirc;n m&aacute;y sẽ mang lại cho ch&uacute;ng ta một cảm nhận về m&agrave;u sắc, độ tương phản rất đ&atilde; mắt m&agrave; &iacute;t c&oacute; h&atilde;ng n&agrave;o c&oacute; thể l&agrave;m được điều tương tự.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-retina-la-gi-905780\" target=\"_blank\">M&agrave;n h&igrave;nh Retina l&agrave; g&igrave;? Thiết bị Apple n&agrave;o sử dụng m&agrave;n h&igrave;nh Retina?</a></p>\r\n\r\n<p>iPad 10 c&oacute; độ ph&acirc;n giải 1640 x 2360 Pixels v&agrave; mật độ điểm ảnh kh&aacute; cao, tuy h&atilde;ng kh&ocirc;ng c&ocirc;ng bố tablet thuộc v&agrave;o chuẩn độ ph&acirc;n giải n&agrave;o, nhưng ta c&oacute; thể so s&aacute;nh dựa theo con số rằng m&agrave;n h&igrave;nh n&agrave;y c&oacute; thể nằm trong khoảng chuẩn 2K. V&igrave; thế m&agrave; nội dung được phản &aacute;nh chi tiết, t&aacute;i tạo quang cảnh trong phim hay tr&ograve; chơi được ch&acirc;n thực hơn bao giờ hết.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031424.jpg\" onclick=\"return false;\"><img alt=\"Hiển thị hình ảnh chi tiết - iPad 10 WiFi 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031424.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031424.jpg\" title=\"Hiển thị hình ảnh chi tiết - iPad 10 WiFi 64GB\" /></a></p>\r\n\r\n<h3>Sở hữu hiệu năng ổn định</h3>\r\n\r\n<p>Theo như truyền thống của những mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo điện thoại iPhone đang kinh doanh tại Thế Giới Di Động\">iPhone</a>&nbsp;v&agrave; iPad từ trước đến nay, chiếc m&aacute;y t&iacute;nh bảng lần n&agrave;y cũng sẽ được trang bị con chip được sản xuất bởi ch&iacute;nh nh&agrave; Apple v&agrave; đ&oacute; l&agrave; bộ vi xử l&yacute; A14 Bionic.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-chip-xu-ly-apple-a14-bionic-hieu-nang-manh-den-1302095\" target=\"_blank\">T&igrave;m hiểu chip xử l&yacute; Apple A14 Bionic. Hiệu năng mạnh đến mức n&agrave;o?</a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031426.jpg\" onclick=\"return false;\"><img alt=\"Chipset mạnh mẽ - iPad 10 WiFi 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031426.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031426.jpg\" title=\"Chipset mạnh mẽ - iPad 10 WiFi 64GB\" /></a></p>\r\n\r\n<p>Apple A14 Bionic đ&atilde; kh&ocirc;ng c&ograve;n qu&aacute; xa lạ bởi mức độ nổi tiếng về hiệu năng cực kỳ mạnh mẽ tr&ecirc;n nhiều d&ograve;ng sản phẩm trước đ&acirc;y. Với hiệu suất tối đa v&agrave;o khoảng 3.1 GHz n&ecirc;n bạn ho&agrave;n to&agrave;n c&oacute; thể an t&acirc;m, kh&ocirc;ng lo đến sự ổn định của m&aacute;y hay thậm ch&iacute; c&oacute; thể d&ugrave;ng để chơi những tựa game nặng v&agrave; thiết kế đồ họa.</p>\r\n\r\n<p>iPad 10 l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-ram-4gb\" target=\"_blank\" title=\"Tham khảo máy tính bảng RAM 4 GB đang kinh doanh tại Thế Giới Di Động\">m&aacute;y t&iacute;nh bảng RAM 4 GB</a>&nbsp;n&ecirc;n đ&acirc;y c&oacute; thể l&agrave; điểm m&agrave; h&atilde;ng đ&atilde; tối ưu để c&oacute; thể giảm mức gi&aacute; th&agrave;nh sản phẩm đến tay người d&ugrave;ng. Nhưng thực sự m&agrave; n&oacute;i th&igrave; con số chưa phải l&agrave; tất cả để chứng minh rằng m&aacute;y c&oacute; xử l&yacute; đa nhiệm tốt hay kh&ocirc;ng, điều n&agrave;y c&ograve;n phụ thuộc một phần v&agrave;o hệ điều h&agrave;nh m&agrave; n&oacute; được hỗ trợ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031421.jpg\" onclick=\"return false;\"><img alt=\"Đa nhiệm mượt mà  - iPad 10 WiFi 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031421.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031421.jpg\" title=\"Đa nhiệm mượt mà  - iPad 10 WiFi 64GB\" /></a></p>\r\n\r\n<p>V&agrave; tr&ecirc;n iPad 10 sẽ được c&agrave;i đặt sẵn hệ điều h&agrave;nh iPadOS 16 mới nhất (10/2022), gi&uacute;p&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Xem thêm Tablet chính hãng, giá tốt đang kinh doanh tại Thế Giới Di Động\">tablet</a>&nbsp;c&oacute; thể c&acirc;n bằng hiệu suất v&agrave; tối ưu phần mềm để bạn c&oacute; thể sử dụng mượt m&agrave; v&agrave; ổn định. Ngo&agrave;i ra, hệ điều h&agrave;nh n&agrave;y c&ograve;n c&oacute; một giao diện đẹp mắt, độ bảo mật an to&agrave;n hơn nhằm hạn chế t&igrave;nh trạng r&ograve; rỉ th&ocirc;ng tin c&aacute; nh&acirc;n trước những đối tượng nguy hiểm.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031431.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ hệ điều hành mới - iPad 10 WiFi 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031431.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031431.jpg\" title=\"Hỗ trợ hệ điều hành mới - iPad 10 WiFi 64GB\" /></a></p>\r\n\r\n<h3>T&iacute;ch hợp camera chất lượng</h3>\r\n\r\n<p>Từ trước cho đến nay th&igrave; việc quay phim, chụp ảnh được xem l&agrave; một điểm kh&ocirc;ng thuộc v&agrave;o thế mạnh của tablet, bởi nhu cầu sử dụng của người d&ugrave;ng thường thi&ecirc;n về c&aacute;c t&aacute;c vụ văn ph&ograve;ng v&agrave; học tập.&nbsp;</p>\r\n\r\n<p>Thế nhưng tr&ecirc;n iPad 10 th&igrave; lại kh&aacute;c, m&aacute;y ho&agrave;n to&agrave;n c&oacute; thể quay phim với chất lượng 4K v&agrave; chụp được những bức ảnh g&oacute;c rộng, k&egrave;m với đ&oacute; l&agrave; c&ocirc;ng nghệ Smart HDR 3 tr&ecirc;n camera 12 MP gi&uacute;p chụp đ&ecirc;m hay ngược s&aacute;ng trở n&ecirc;n tuyệt vời hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031428.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ quay video 4K - iPad 10 WiFi 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031428.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031428.jpg\" title=\"Hỗ trợ quay video 4K - iPad 10 WiFi 64GB\" /></a></p>\r\n\r\n<p>Mặt trước của tablet th&igrave; được trang bị camera selfie c&oacute; độ ph&acirc;n giải 12 MP đi c&ugrave;ng t&iacute;nh năng HDR th&ocirc;ng minh, vậy n&ecirc;n m&aacute;y c&oacute; thể chụp được những bức ảnh selfie ngược s&aacute;ng tốt hơn, n&acirc;ng cao chất lượng tr&ecirc;n những cuộc gọi video hay hội họp online qua những phần mềm chuy&ecirc;n dụng như: Google Meet, Zoom,...</p>\r\n\r\n<p>Ở những trường hợp quay video bằng camera trước th&igrave; m&aacute;y ho&agrave;n to&agrave;n c&oacute; thể thu ph&oacute;ng khung h&igrave;nh ở một mức độ giới hạn nhằm gi&uacute;p chủ thể l&agrave; người được nằm gần trung t&acirc;m nhất. Ta c&oacute; thể thấy đ&acirc;y l&agrave; t&iacute;nh năng rất hay v&agrave; cực kỳ ph&ugrave; hợp đối với những bạn đang l&agrave;m nội dung video c&aacute; nh&acirc;n thi&ecirc;n về học thuật hay review tr&ecirc;n nhiều nền tảng mạng x&atilde; hội.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031430.jpg\" onclick=\"return false;\"><img alt=\"Nâng cao chất lượng video call - iPad 10 WiFi 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031430.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031430.jpg\" title=\"Nâng cao chất lượng video call - iPad 10 WiFi 64GB\" /></a></p>\r\n\r\n<h3>Thời lượng sử dụng d&agrave;i l&acirc;u</h3>\r\n\r\n<p>iPad 10 được trang bị một vi&ecirc;n pin c&oacute; dung lượng 28.6 Wh hay c&ograve;n c&oacute; thể đổi th&agrave;nh con số xấp xỉ 7587 mAh, với một mức pin tương đối cao n&ecirc;n người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể an t&acirc;m sử dụng thiết bị cho cả ng&agrave;y d&agrave;i m&agrave; kh&ocirc;ng cần qu&aacute; lo lắng đến những vấn đề như cạn pin giữa chừng chẳng hạn.</p>\r\n\r\n<p>Ngo&agrave;i ra h&atilde;ng c&ograve;n c&ocirc;ng bố th&ecirc;m iPad 10 c&oacute; thể duy tr&igrave; việc sử dụng cho lướt web, xem video l&ecirc;n đến 10 tiếng đồng hồ ở chế độ Wifi.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031433.jpg\" onclick=\"return false;\"><img alt=\"Sử dụng dài lâu - iPad 10 WiFi 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031433.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294103/ipad-gen-10-231022-031433.jpg\" title=\"Sử dụng dài lâu - iPad 10 WiFi 64GB\" /></a></p>\r\n\r\n<p>Ở phi&ecirc;n bản tablet năm nay ta c&oacute; thể dễ d&agrave;ng nhận thấy sự đổi mới m&agrave; Apple mang đến cho người d&ugrave;ng,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-10-9-inch\" target=\"_blank\" title=\"Xem thêm iPad 10 10.9 inch đang kinh doanh tại Thế Giới Di Động\">iPad 10</a>&nbsp;kh&ocirc;ng chỉ n&acirc;ng cấp về phần thiết kế b&ecirc;n ngo&agrave;i m&agrave; gần như mọi linh kiện b&ecirc;n trong cũng đ&atilde; được cải tiến nhằm mang lại cho người d&ugrave;ng những trải nghiệm tuyệt vời.</p>\r\n', 'Đã đăng', 2, '08-09-2023', 0),
(39, 'VHL#103', 'iPad Pro M1 12.9 inch WiFi Cellular 2TB', 58990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'table4.jpg', '<h3>Thể hiện sức mạnh dẫn đầu nhờ chip M1</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-m1-129-inch-wifi-cellular-2tb-2021\" target=\"_blank\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi Cellular 2TB (2021)\">M&aacute;y t&iacute;nh bảng iPad Pro M1 12.9 inch WiFi Cellular 2TB (2021)</a>&nbsp;sở hữu sức mạnh vượt trội nhờ trang bị&nbsp;<strong>chip Apple M1</strong>&nbsp;với 8 nh&acirc;n CPU v&agrave; 8 nh&acirc;n GPU, c&oacute; sự hỗ trợ của&nbsp;<strong>RAM 16 GB</strong>&nbsp;mang đến khả năng đa nhiệm ấn tượng, kể cả tr&ecirc;n c&aacute;c t&aacute;c vụ nặng như xử l&yacute; thiết kế AR hay chiến game đồ họa cao. Dung lượng bộ nhớ trong l&ecirc;n đến&nbsp;<strong>2 TB&nbsp;</strong>thoải m&aacute;i cho vấn đề lưu trữ, g&oacute;p phần ổn định khả năng truy xuất dữ liệu v&agrave; tốc độ hoạt động của m&aacute;y.</p>\r\n\r\n<p>Ngo&agrave;i ra, m&aacute;y c&ograve;n c&oacute; thể kết hợp với<strong>&nbsp;Apple Pencil 2</strong>&nbsp;để thỏa sức ghi ch&uacute;, s&aacute;ng tạo tr&ecirc;n m&agrave;n h&igrave;nh, hoặc khi cần xử l&yacute; c&ocirc;ng việc th&ecirc;m tiện lợi c&oacute; thể d&ugrave;ng c&ugrave;ng&nbsp;<strong>Magic Keyboard</strong>&nbsp;để biến iPad Pro&nbsp;th&agrave;nh Macbook mini, rất đa năng v&agrave; hữu &iacute;ch.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/269334/ipad-pro-m1-129-inch-wifi-cellular-2tb-2021-160122-025817.jpg\" onclick=\"return false;\"><img alt=\"Mạnh mẽ, tiện lợi - iPad Pro M1 12.9 inch WiFi Cellular 2TB (2021)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/269334/ipad-pro-m1-129-inch-wifi-cellular-2tb-2021-160122-025817.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/269334/ipad-pro-m1-129-inch-wifi-cellular-2tb-2021-160122-025817.jpg\" title=\"Mạnh mẽ, tiện lợi - iPad Pro M1 12.9 inch WiFi Cellular 2TB (2021)\" /></a></p>\r\n\r\n<h3>Khung h&igrave;nh rực rỡ, sống động đầy l&ocirc;i cuốn</h3>\r\n\r\n<p>Phi&ecirc;n bản iPad Pro M1 n&agrave;y c&oacute; m&agrave;n h&igrave;nh&nbsp;<strong>Liquid Retina XDR</strong>&nbsp;cao cấp với 10.000 b&oacute;ng đ&egrave;n mini-LED, thể hiện rực rỡ tr&ecirc;n dải m&agrave;u rộng v&agrave; tỷ lệ tương phản cao, độ ph&acirc;n giải sắc n&eacute;t&nbsp;<strong>2048 x 2732 Pixels</strong>, biến khung h&igrave;nh&nbsp;<strong>12.9 inch</strong>&nbsp;trở n&ecirc;n sống động v&agrave; cuốn h&uacute;t đến bất ngờ.</p>\r\n\r\n<p>C&ocirc;ng nghệ TrueTone, dải m&agrave;u rộng&nbsp;<strong>P3&nbsp;</strong>chuẩn điện ảnh v&agrave; c&ocirc;ng nghệ&nbsp;<strong>ProMotion&nbsp;</strong>hỗ trợ tần số qu&eacute;t&nbsp;<strong>120 Hz&nbsp;</strong>gi&uacute;p cho mọi thao t&aacute;c chạm vuốt v&agrave; h&igrave;nh ảnh chuyển cảnh tr&ecirc;n m&agrave;n h&igrave;nh mượt, nhạy hơn bao giờ hết, hấp dẫn người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/269334/ipad-pro-m1-129-inch-wifi-cellular-2tb-2021-160122-025819.jpg\" onclick=\"return false;\"><img alt=\"Màn hình rực rỡ - iPad Pro M1 12.9 inch WiFi Cellular 2TB (2021)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/269334/ipad-pro-m1-129-inch-wifi-cellular-2tb-2021-160122-025819.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/269334/ipad-pro-m1-129-inch-wifi-cellular-2tb-2021-160122-025819.jpg\" title=\"Màn hình rực rỡ - iPad Pro M1 12.9 inch WiFi Cellular 2TB (2021)\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng đang kinh doanh tại Thế Giới Di Động \">M&aacute;y t&iacute;nh bảng</a>&nbsp;thiết kế sang trọng v&agrave; chắc chắn, lớp vỏ kim loại nguy&ecirc;n khối ho&agrave;n thiện tỉ mỉ, v&aacute;t cạnh vu&ocirc;ng vức, m&agrave;n h&igrave;nh tr&agrave;n viền tinh tế, tạo n&ecirc;n tổng thể đẹp nịnh mắt. Nhờ được hỗ trợ mạng&nbsp;<strong>5G&nbsp;</strong>si&ecirc;u nhanh nhạy, bạn c&oacute; thể tận hưởng tốc độ mạng si&ecirc;u tốc khi cần tải xuống tệp, ph&aacute;t trực tuyến phim hay trải nghiệm c&aacute;c nội dung online tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng iPad đang kinh doanh tại Thế Giới Di Động \">m&aacute;y t&iacute;nh bảng&nbsp;iPad</a>&nbsp;mọi l&uacute;c, mọi nơi.</p>\r\n\r\n<p>Thiết bị c&oacute; pin dung lượng l&ecirc;n đến<strong>&nbsp;40.88 Wh&nbsp;</strong>tương đương với khoảng&nbsp;<strong>10.835 mAh</strong>, c&oacute; thể thoải m&aacute;i sử dụng ở tần suất cao gần như suốt ng&agrave;y d&agrave;i. Khi cạn nguồn,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng có sạc nhanh đang kinh doanh tại Thế Giới Di Động \">m&aacute;y t&iacute;nh bảng c&oacute; sạc nhanh</a>&nbsp;20 W sẽ nhanh ch&oacute;ng phục hồi hoạt động cho iPad của bạn.</p>\r\n\r\n<h3>Quay phim, chụp ảnh kh&ocirc;ng giới hạn</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/269334/ipad-pro-m1-129-inch-wifi-cellular-2tb-2021-160122-025821.jpg\" onclick=\"return false;\"><img alt=\"Chụp ảnh ấn tượng - iPad Pro M1 12.9 inch WiFi Cellular 2TB (2021)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/269334/ipad-pro-m1-129-inch-wifi-cellular-2tb-2021-160122-025821.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/269334/ipad-pro-m1-129-inch-wifi-cellular-2tb-2021-160122-025821.jpg\" title=\"Chụp ảnh ấn tượng - iPad Pro M1 12.9 inch WiFi Cellular 2TB (2021)\" /></a></p>\r\n\r\n<p>D&ugrave; l&agrave; m&aacute;y t&iacute;nh bảng, nhưng Apple lại trang bị cho iPad Pro M1 khả năng chụp ảnh v&agrave; quay phim kh&aacute; tốt với cụm camera gồm camera g&oacute;c rộng 12 MP, camera si&ecirc;u rộng 10 MP&nbsp;v&agrave;&nbsp;cảm biến&nbsp;TOF 3D LiDAR c&oacute; t&iacute;ch hợp AI, mang đến c&aacute;c th&agrave;nh phẩm quay chụp sắc n&eacute;t kh&ocirc;ng thua k&eacute;m điện thoại.</p>\r\n\r\n<p><strong>Camera TrueDepth 12 MP</strong>&nbsp;c&oacute; hỗ trợ t&iacute;nh năng&nbsp;<strong>Center Stage</strong>&nbsp;giữ chủ thể lu&ocirc;n ở trung t&acirc;m m&agrave;n h&igrave;nh khi FaceTime, tối ưu nội dung muốn chia sẻ. Đồng thời c&ograve;n c&oacute; t&iacute;nh năng&nbsp;<strong>mở kh&oacute;a khu&ocirc;n mặt</strong>&nbsp;sẽ hữu &iacute;ch cho việc truy cập nhanh v&agrave; bảo mật tr&ecirc;n iPad.</p>\r\n', 'Đã đăng', 2, '08-09-2023', 0),
(40, 'VHL#104', 'iPad Pro M1 11 inch WiFi Cellular 2TB', 50990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'table5.jpg', '<h3>Ấn tượng tr&ecirc;n m&agrave;n h&igrave;nh giải tr&iacute; rực rỡ, sống động</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-m1-11-inch-wifi-cellular-2tb-2021\" target=\"_blank\" title=\"Máy tính bảng iPad Pro M1 11 inch WiFi Cellular 2TB (2021)\">M&aacute;y t&iacute;nh bảng iPad Pro M1 11 inch WiFi Cellular 2TB (2021)</a>&nbsp;mang vẻ ngo&agrave;i sang trọng, chắc chắn của lớp vỏ kim loại nguy&ecirc;n khối ho&agrave;n thiện tinh tế, thiết kế vu&ocirc;ng vức hiện đại, k&iacute;ch thước&nbsp;m&agrave;n h&igrave;nh 11 inch&nbsp;sử dụng thuận tiện như 1 chiếc laptop mini d&ugrave;ng cho c&aacute; nh&acirc;n.</p>\r\n\r\n<p>iPad Pro M1&nbsp;thể hiện khung h&igrave;nh rực rỡ, sống động v&agrave; ch&acirc;n thực nhờ tấm nền&nbsp;<strong>Liquid Retina</strong>&nbsp;c&oacute; dải m&agrave;u rộng, chất lượng tương phản đạt&nbsp;<strong>1668 x 2388 Pixels&nbsp;</strong>cho c&aacute;c chi tiết sắc n&eacute;t, l&ocirc;i cuốn, c&ugrave;ng với tần số qu&eacute;t l&ecirc;n đến&nbsp;<strong>120 Hz</strong>&nbsp;nhờ c&ocirc;ng nghệ&nbsp;<strong>ProMotion&nbsp;</strong>để&nbsp;mọi trải nghiệm xem thật mượt m&agrave;, thao t&aacute;c chạm vuốt nhanh nhạy.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/269330/ipad-pro-m1-11-inch-wifi-cellular-2tb-2021-160122-085555.jpg\" onclick=\"return false;\"><img alt=\"Khung hình sống động - iPad Pro M1 11 inch WiFi Cellular 2TB (2021)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/269330/ipad-pro-m1-11-inch-wifi-cellular-2tb-2021-160122-085555.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/269330/ipad-pro-m1-11-inch-wifi-cellular-2tb-2021-160122-085555.jpg\" title=\"Khung hình sống động - iPad Pro M1 11 inch WiFi Cellular 2TB (2021)\" /></a></p>\r\n\r\n<p>X&eacute;t về dung lượng pin, iPad Pro M1 11 inch WiFi Cellular 2TB (2021) cho ph&eacute;p sử dụng li&ecirc;n tục đến&nbsp;<strong>10 giờ&nbsp;</strong>khi d&ugrave;ng lướt web, xem video nhờ pin&nbsp;<strong>28.65 Wh</strong>&nbsp;tương đương khoảng&nbsp;<strong>7538 mAh</strong>, thỏa m&atilde;n cho 1 ng&agrave;y d&agrave;i l&agrave;m việc hay giải tr&iacute;. Khi cạn nguồn, chế độ&nbsp;<strong><a href=\"https://www.thegioididong.com/may-tinh-bang-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo máy tính bảng có sạc nhanh tại Thegioididong.com\">sạc pin nhanh</a></strong>&nbsp;<strong>20 W</strong>&nbsp;sẽ nhanh ch&oacute;ng phục hồi năng lượng pin cho thiết bị của bạn.</p>\r\n\r\n<h3>Trải nghiệm kh&ocirc;ng giới hạn với sức mạnh hiệu năng từ chip M1</h3>\r\n\r\n<p>Sử dụng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Tham khảo các sản phẩm máy tính bảng iPad tại Thế Giới Di Động\">iPad</a>&nbsp;đa nhiệm ổn định tr&ecirc;n c&aacute;c t&aacute;c vụ lướt web, nghe nhạc, xem phim, chơi game giải tr&iacute; hay thiết kế đồ họa cơ bản,&hellip; Sức mạnh hiệu năng đến từ&nbsp;<strong>chip Apple M1 8 nh&acirc;n</strong>&nbsp;mạnh mẽ v&agrave;&nbsp;<strong>chip đồ họa Apple GPU</strong>, cộng th&ecirc;m sự hỗ trợ của&nbsp;<strong>RAM 16 GB</strong>&nbsp;đảm bảo m&agrave;n h&igrave;nh trải nghiệm lu&ocirc;n ổn định, kh&ocirc;ng bị giật lag hay đơ m&aacute;y.</p>\r\n\r\n<p>Hỗ trợ bộ nhớ trong l&ecirc;n đến&nbsp;<strong>2 TB&nbsp;</strong>tạo kho lưu trữ ấn tượng cho nhu cầu c&aacute; nh&acirc;n, bạn c&oacute; thể giữ lại mọi ảnh chụp, video, c&aacute;c album nhạc y&ecirc;u th&iacute;ch,&hellip; m&agrave; kh&ocirc;ng lo qu&aacute; tải bộ nhớ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/269330/ipad-pro-m1-11-inch-wifi-cellular-2tb-2021-160122-085553.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ - iPad Pro M1 11 inch WiFi Cellular 2TB (2021)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/269330/ipad-pro-m1-11-inch-wifi-cellular-2tb-2021-160122-085553.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/269330/ipad-pro-m1-11-inch-wifi-cellular-2tb-2021-160122-085553.jpg\" title=\"Hiệu năng mạnh mẽ - iPad Pro M1 11 inch WiFi Cellular 2TB (2021)\" /></a></p>\r\n\r\n<p>Khả năng kết hợp với&nbsp;<strong>Apple Pen 2</strong>&nbsp;cho ph&eacute;p bạn thể hiện sự s&aacute;ng tạo tr&ecirc;n m&agrave;n h&igrave;nh iPad c&aacute;ch độc đ&aacute;o, linh hoạt v&agrave; tiện lợi hơn.</p>\r\n\r\n<p>C&ocirc;ng nghệ 5G kết nối si&ecirc;u nhạy, đường truyền ổn định, để bạn c&oacute; thể xem video, tải, chia sẻ dữ liệu một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n\r\n<h3>Quay phim, chụp ảnh cực n&eacute;t ngay tr&ecirc;n iPad</h3>\r\n\r\n<p>D&ugrave; kh&ocirc;ng phải l&agrave; t&iacute;nh năng ch&iacute;nh, nhưng Apple cũng rộng r&atilde;i trang bị cụm camera chất lượng cho phi&ecirc;n bản iPad n&agrave;y, gồm bộ đ&ocirc;i&nbsp;<strong>camera g&oacute;c rộng 12 MP</strong>,&nbsp;<strong>camera si&ecirc;u rộng 10 MP</strong>&nbsp;v&agrave;&nbsp;<strong>cảm biến&nbsp;TOF 3D LiDAR</strong>&nbsp;ở mặt sau, t&iacute;ch hợp c&aacute;c t&iacute;nh năng AI để cho ra c&aacute;c ảnh chụp v&agrave; video sắc n&eacute;t, sống động.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/269330/ipad-pro-m1-11-inch-wifi-cellular-2tb-2021-160122-085557.jpg\" onclick=\"return false;\"><img alt=\"Camera sắc nét - iPad Pro M1 11 inch WiFi Cellular 2TB (2021)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/269330/ipad-pro-m1-11-inch-wifi-cellular-2tb-2021-160122-085557.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/269330/ipad-pro-m1-11-inch-wifi-cellular-2tb-2021-160122-085557.jpg\" title=\"Camera sắc nét - iPad Pro M1 11 inch WiFi Cellular 2TB (2021)\" /></a></p>\r\n\r\n<p>Mặt trước l&agrave;&nbsp;<strong>Camera TrueDepth 12 MP</strong>&nbsp;c&oacute; hỗ trợ t&iacute;nh năng&nbsp;<strong>Center Stage</strong>&nbsp;gi&uacute;p tự động nhận diện v&agrave; căn g&oacute;c để giữ chủ thể ở trung t&acirc;m m&agrave;n h&igrave;nh khi di chuyển, hữu &iacute;ch trong l&uacute;c bạn FaceTime.</p>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\">m&aacute;y t&iacute;nh bảng</a>&nbsp;c&ograve;n c&oacute; t&iacute;nh năng mở kh&oacute;a bằng&nbsp;khu&ocirc;n mặt Face ID vừa gi&uacute;p truy cập nhanh v&agrave;o m&agrave;n h&igrave;nh ch&iacute;nh, vừa bảo mật tốt tr&ecirc;n thiết bị c&aacute; nh&acirc;n.</p>\r\n', 'Đã đăng', 2, '08-09-2023', 0),
(41, 'VHL#105', 'iPad Pro M1 11 inch WiFi 2TBB', 44990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'table6.jpg', '<h3>Trải nghiệm đầy th&iacute;ch th&uacute; tr&ecirc;n m&agrave;n h&igrave;nh chất lượng</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-m1-11-inch-wifi-2tb-2021\" target=\"_blank\" title=\"Máy tính bảng iPad Pro M1 11 inch WiFi 2TB (2021)\">M&aacute;y t&iacute;nh bảng iPad Pro M1 11 inch WiFi 2TB (2021)</a>&nbsp;được giới thiệu với 2 m&agrave;u x&aacute;m v&agrave; bạc hiện đại, chất liệu nh&ocirc;m nguy&ecirc;n khối sang trọng c&ugrave;ng thiết kế thanh lịch, tinh tế, ph&ugrave; hợp trong mọi kh&ocirc;ng gian sử dụng.</p>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh&nbsp;<strong>Liquid Retina&nbsp;</strong>với k&iacute;ch thước m&agrave;n h&igrave;nh&nbsp;<strong>11 inch</strong>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo máy tính bảng kinh doanh tại Thegioididong.com\">m&aacute;y t&iacute;nh bảng</a>&nbsp;thể hiện khung h&igrave;nh m&agrave;u sắc rực rỡ, sống động v&agrave; ch&acirc;n thật, chi tiết sắc n&eacute;t tr&ecirc;n độ ph&acirc;n giải&nbsp;<strong>1668 x 2388 Pixels</strong>.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/269328/ipad-pro-m1-11-inch-wifi-2tb-2021-1.jpg\" onclick=\"return false;\"><img alt=\"Màn hình chất lượng - iPad Pro M1 11 inch WiFi 2TB (2021)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/269328/ipad-pro-m1-11-inch-wifi-2tb-2021-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/269328/ipad-pro-m1-11-inch-wifi-2tb-2021-1.jpg\" title=\"Màn hình chất lượng - iPad Pro M1 11 inch WiFi 2TB (2021)\" /></a></p>\r\n\r\n<p>Nhờ tần số qu&eacute;t l&ecirc;n đến&nbsp;<strong>120 Hz</strong>, thao t&aacute;c chạm vuốt m&agrave;n h&igrave;nh cực mượt, kh&ocirc;ng c&oacute; độ trễ trong c&aacute;c khung h&igrave;nh chuyển động, nhờ đ&oacute; sử dụng m&aacute;y cho nhu cầu chơi game online, xem phim h&agrave;nh động sẽ thật tuyệt.</p>\r\n\r\n<h3>L&agrave;m việc hay giải tr&iacute; đều ho&agrave;n hảo</h3>\r\n\r\n<p>Được gia cố sức mạnh hiệu năng nhờ&nbsp;<strong>chip Apple M1 8 nh&acirc;n</strong>, chip đồ họa Apple GPU 8 nh&acirc;n v&agrave; RAM 16 GB, iPad&nbsp;dễ d&agrave;ng chinh phục cả c&aacute;c t&aacute;c vụ đồ họa, hay chiến c&aacute;c tựa game nặng mượt m&agrave;, đa nhiệm với hiệu suất thật ổn định. M&aacute;y trang bị<strong>&nbsp;bộ nhớ trong 2 TB</strong>&nbsp;tạo kho lưu trữ c&aacute; nh&acirc;n khổng lồ cho tất cả h&igrave;nh ảnh, video y&ecirc;u th&iacute;ch v&agrave; t&agrave;i liệu c&ocirc;ng việc của bạn.</p>\r\n\r\n<p>Đồng thời, để tạo được sự tiện &iacute;ch v&agrave; hiệu suất giải tr&iacute;, l&agrave;m việc tốt hơn, c&oacute; thể kết hợp iPad của bạn với<strong>&nbsp;Apple Pencil thế hệ thứ 2</strong>&nbsp;v&agrave;<strong>&nbsp;Magic Keyboard&nbsp;</strong>tr&ecirc;n b&agrave;n l&agrave;m việc.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/269328/ipad-pro-m1-11-inch-wifi-2tb-2021-2.jpg\" onclick=\"return false;\"><img alt=\"Mạnh mẽ, đa năng - iPad Pro M1 11 inch WiFi 2TB (2021)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/269328/ipad-pro-m1-11-inch-wifi-2tb-2021-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/269328/ipad-pro-m1-11-inch-wifi-2tb-2021-2.jpg\" title=\"Mạnh mẽ, đa năng - iPad Pro M1 11 inch WiFi 2TB (2021)\" /></a></p>\r\n\r\n<h3>Kết hợp th&ecirc;m cụm camera xịn s&ograve;</h3>\r\n\r\n<p>Sử dụng m&aacute;y t&iacute;nh bảng để quay phim, chụp ảnh v&agrave; selfie cũng thật ấn tượng. Cụm camera sau lấy n&eacute;t nhanh, chụp h&igrave;nh sắc n&eacute;t với&nbsp;camera ch&iacute;nh&nbsp;<strong>12 MP</strong>, camera g&oacute;c si&ecirc;u rộng&nbsp;<strong>10 MP</strong>&nbsp;v&agrave; cảm biến&nbsp;<strong>TOF 3D LiDAR</strong>.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/269328/ipad-pro-m1-11-inch-wifi-2tb-2021-3.jpg\" onclick=\"return false;\"><img alt=\"Camera xịn sò - iPad Pro M1 11 inch WiFi 2TB (2021)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/269328/ipad-pro-m1-11-inch-wifi-2tb-2021-3.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/269328/ipad-pro-m1-11-inch-wifi-2tb-2021-3.jpg\" title=\"Camera xịn sò - iPad Pro M1 11 inch WiFi 2TB (2021)\" /></a></p>\r\n\r\n<p><strong>Camera TrueDepth 12 MP</strong>&nbsp;hỗ trợ FaceTime tiện lợi c&ugrave;ng t&iacute;nh năng&nbsp;<strong>Center Stage</strong>, tự động theo d&otilde;i chuyển động để giữ bạn lu&ocirc;n ở trung t&acirc;m cuộc đối thoại.&nbsp;iPad Pro M1 c&ograve;n được&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\">Apple</a>&nbsp;hỗ trợ<strong>&nbsp;mở kh&oacute;a khu&ocirc;n mặt</strong>, th&ecirc;m lựa chọn bảo mật v&agrave; truy cập nhanh tr&ecirc;n thiết bị c&aacute; nh&acirc;n.</p>\r\n\r\n<p>Ngo&agrave;i ra, việc sử dụng m&aacute;y với tần suất cao sẽ kh&ocirc;ng qu&aacute; &aacute;i ngại vấn đề pin sạc, nhờ trang bị pin Li-Po dung lượng lớn khoảng&nbsp;<strong>7538 mAh&nbsp;</strong>k&egrave;m th&ecirc;m&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo máy tính bảng có sạc nhanh tại Thegioididong.com\">sạc pin nhanh</a>&nbsp;<strong>20 W&nbsp;</strong>an to&agrave;n qua cổng&nbsp;<strong>Type-C</strong>, nhanh ch&oacute;ng nạp năng lượng cho thiết bị khi cạn nguồn.</p>\r\n', 'Đã đăng', 2, '08-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(42, 'VHL#106', 'iPad Pro M1 12.9 inch WiFi 512GB', 32690000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'table7.jpg', '<h3 id=\"docs-internal-guid-f29158b9-7fff-3fb8-6d2b-1c81f7bdf223\"><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-m1-129-inch-wifi-512gb-2021\" target=\"_blank\" title=\"Tham khảo máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) chính hãng giá tốt tại TGDĐ\">iPad Pro M1 12.9 inch WiFi 512GB (2021)</a>&nbsp;chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo các dòng máy tính bảng tablet chính hãng giá tốt tại TGDĐ\">m&aacute;y t&iacute;nh bảng</a>&nbsp;với thiết kế sang trọng đẳng cấp, m&agrave;n h&igrave;nh mini-LED tuyệt hảo 12.9 inch, sức mạnh tương đương m&aacute;y t&iacute;nh để b&agrave;n nhờ bộ vi xử l&yacute; Apple M1 v&agrave; hệ thống camera chất lượng rất đ&aacute;ng để đầu tư v&agrave; n&acirc;ng cấp.</h3>\r\n\r\n<h3>Thiết kế sang trọng, đẳng cấp h&agrave;ng đầu</h3>\r\n\r\n<p>Giống với những d&ograve;ng iPad tiền nhiệm, iPad Pro 12.9 phi&ecirc;n bản 2021 tiếp tục được&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Tham khảo giá điện thoại smartphone iPhone chính hãng\">Apple</a>&nbsp;thiết kế với vẻ ngoại h&igrave;nh đơn giản nhưng v&ocirc; c&ugrave;ng nổi bật với những đường n&eacute;t thiết kế v&aacute;t cạnh vu&ocirc;ng vức tạo cảm gi&aacute;c mạnh mẽ, nam t&iacute;nh v&agrave; cứng c&aacute;p.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074101.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thiết kế sang trọng, đẳng cấp hàng đầu\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074101.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074101.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thiết kế sang trọng, đẳng cấp hàng đầu\" /></a></p>\r\n\r\n<p>To&agrave;n bộ th&acirc;n m&aacute;y được ho&agrave;n thiện bằng chất liệu nh&ocirc;m nguy&ecirc;n khối kết hợp c&ugrave;ng khung viền kim loại cao cấp với k&iacute;ch thước 280.6 x 214.9 x 6.4 mm, d&agrave;y hơn một ch&uacute;t so với thế hệ trước nhưng vẫn đảm bảo sự liền mạch thoải m&aacute;i khi cầm nắm sử dụng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074057.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Trải nghiệm cầm nắm thao tác\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074057.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074057.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Trải nghiệm cầm nắm thao tác\" /></a></p>\r\n\r\n<p>Tr&ecirc;n iPad Pro 12.9 M1 2021 được trang bị c&oacute; tới 5 micro gi&uacute;p ghi lại &acirc;m thanh r&otilde; n&eacute;t v&agrave; hệ thống 4 loa ngo&agrave;i hỗ trợ Dolby Atmos cao cấp cho chất lượng &acirc;m thanh sống động ch&acirc;n thực như ở rạp chiếu phim chuy&ecirc;n nghiệp.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074027.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Hệ thông âm thanh chất lượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074027.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074027.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Hệ thông âm thanh chất lượng\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh Mini-LED 12.9 inch si&ecirc;u n&eacute;t, si&ecirc;u mượt</h3>\r\n\r\n<p>Với iPad Pro 11 M1 2021, Apple vẫn mang đến cho bạn trải nghiệm h&igrave;nh ảnh cao cấp với c&ocirc;ng nghệ Liquid Retina XDR mini-LED LCD c&oacute; k&iacute;ch thước 12.9 inch, sử dụng đến tận 10.000 đ&egrave;n LED si&ecirc;u nhỏ kh&ocirc;ng chỉ hiển thị tuyệt đẹp, sắc n&eacute;t đ&aacute;ng kinh ngạc m&agrave; c&ograve;n tiết kiệm điện năng một c&aacute;ch ti&ecirc;n tiến.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074044.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Màn hình Mini-LED siêu nét, siêu mượt\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074044.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074044.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Màn hình Mini-LED siêu nét, siêu mượt\" /></a></p>\r\n\r\n<p>Độ tương phản 1.000.000:1 v&agrave; độ s&aacute;ng to&agrave;n m&agrave;n h&igrave;nh cũng được n&acirc;ng l&ecirc;n để bạn xem, chỉnh sửa ảnh chất lượng cao hay thưởng thức những thước phim giải tr&iacute; chất lượng cao ngay tr&ecirc;n chiếc m&aacute;y t&iacute;nh bảng n&agrave;y.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074046.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Công nghệ Mini-LED\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074046.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074046.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Công nghệ Mini-LED\" /></a></p>\r\n\r\n<p>Hơn nữa, m&agrave;n h&igrave;nh n&agrave;y c&ograve;n hỗ trợ tần số qu&eacute;t 120 Hz c&ugrave;ng c&ocirc;ng nghệ ProMotion kết hợp với hệ điều h&agrave;nh iPadOS 15 (12/2021) cho mọi thao t&aacute;c, chuyển cảnh tr&ecirc;n chiếc m&aacute;y t&iacute;nh bảng cao cấp n&agrave;y đều diễn ra mượt m&agrave; v&agrave; hấp dẫn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074028.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Màn hình với độ sáng vượt trội\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074028.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074028.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Màn hình với độ sáng vượt trội\" /></a></p>\r\n\r\n<h3>Vi xử l&yacute; Apple M1, bứt ph&aacute; hiệu năng vượt trội</h3>\r\n\r\n<p>Mang tr&ecirc;n m&igrave;nh sức mạnh của bộ vi xử l&yacute; Apple M1, c&oacute; thể n&oacute;i iPad Pro 12.9 2021 ch&iacute;nh l&agrave; chiếc m&aacute;y t&iacute;nh bảng nhanh nhất từ trước đến nay với sức mạnh nhanh hơn tới 50% v&agrave; khả năng xử l&yacute; đồ họa tốt hơn 40% so với thế hệ trước.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-081717.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Hiệu năng bứt phá vượt trội\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-081717.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-081717.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Hiệu năng bứt phá vượt trội\" /></a></p>\r\n\r\n<p>Với cấu h&igrave;nh v&agrave; sức mạnh gần như tương tự với một chiếc laptop, giờ đ&acirc;y bạn ho&agrave;n to&agrave;n c&oacute; thể thoải m&aacute;i l&agrave;m việc, chơi game đồ họa cao hay dựng c&aacute;c m&ocirc; h&igrave;nh AR phức tạp ngay tr&ecirc;n m&aacute;y t&iacute;nh bảng iPad Pro 12.9 M1 2021.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074104.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thoải mái dựng phim \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074104.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074104.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thoải mái dựng phim \" /></a></p>\r\n\r\n<p>Dung lượng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-ram-8gb\" target=\"_blank\" title=\"Tham khảo máy tính bảng có dung lượng RAM 8 GB đang bán tại TGDĐ\">RAM 8 GB</a>&nbsp;gi&uacute;p mọi thao t&aacute;c đa nhiệm hay sử dụng nhiều ứng dụng c&ugrave;ng l&uacute;c lu&ocirc;n được mượt m&agrave; v&agrave; dung lượng bộ nhớ trong 512 GB, iPad Pro 12.9 M1 2021 cho ph&eacute;p bạn thoải m&aacute;i c&agrave;i đặt ứng dụng, lưu trữ những nội dung bộ nhớ lớn như ảnh RAW để tha hồ chỉnh sửa ảnh, video, &acirc;m nhạc, bản thu,... m&agrave; kh&ocirc;ng lo đầy bộ nhớ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074025.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thoải mái đa nhiệm lưu trữ\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074025.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074025.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thoải mái đa nhiệm lưu trữ\" /></a></p>\r\n\r\n<p>Kết nối kh&ocirc;ng d&acirc;y tr&ecirc;n iPad Pro 12.9 M1 2021 cũng được n&acirc;ng cấp với chuẩn Wi-Fi 6, tăng cường sự mượt m&agrave;, ổn định khi lướt web chơi game kể cả ở những nơi c&ocirc;ng cộng với nguồn ph&aacute;t Wi-Fi yếu v&agrave; đ&ocirc;ng người truy cập, để bạn thoải m&aacute;i trải nghiệm internet tốc độ cao kh&ocirc;ng giới hạn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074113.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Trải nghiệm chơi game chất lượng cao\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074113.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074113.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Trải nghiệm chơi game chất lượng cao\" /></a></p>\r\n\r\n<h3>Cụm camera chụp ảnh, quay video sắc n&eacute;t v&agrave; hơn thế nữa</h3>\r\n\r\n<p>Ph&iacute;a sau iPad Pro 12.9 2021 l&agrave; hệ thống camera chuy&ecirc;n nghiệp với camera ch&iacute;nh 12 MP, camera g&oacute;c si&ecirc;u rộng 10 MP v&agrave; m&aacute;y qu&eacute;t LiDAR tăng cường c&ocirc;ng nghệ thực tế ảo AR, t&iacute;ch hợp bộ xử l&yacute; h&igrave;nh ảnh ISP v&agrave; hỗ trợ t&iacute;nh năng Smart HDR 3 cho ph&eacute;p người d&ugrave;ng lưu lại ho&agrave;n hảo những khoảnh khắc đ&aacute;ng nhớ kể cả trong điều kiện &aacute;nh s&aacute;ng phức tạp.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074103.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Cụm camera sau\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074103.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074103.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Cụm camera sau\" /></a></p>\r\n\r\n<p>Ph&iacute;a trước, iPad Pro 12.9 M1 2021 được trang bị camera trước độ ph&acirc;n giải cao 12 MP hỗ trợ t&iacute;nh năng TrueDepth, g&oacute;c nh&igrave;n si&ecirc;u rộng l&ecirc;n tới 122 độ cho chất lượng h&igrave;nh ảnh sắc n&eacute;t ho&agrave;n hảo m&agrave; kh&ocirc;ng bỏ lỡ bất kỳ chi tiết n&agrave;o. M&aacute;y c&ograve;n đ&aacute;p ứng nhu cầu&nbsp;mở kh&oacute;a bằng&nbsp;nhận diện khu&ocirc;n mặt&nbsp;nhanh ch&oacute;ng v&agrave; an to&agrave;n.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074050.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Camera trước\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074050.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074050.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Camera trước\" /></a></p>\r\n\r\n<p>Apple cũng kh&ocirc;ng qu&ecirc;n trang bị t&iacute;nh năng Center Stage cho iPad Pro 12.9 M1, t&iacute;nh năng n&agrave;y gi&uacute;p bạn lu&ocirc;n nằm ở trung t&acirc;m của khung h&igrave;nh, kể cả khi c&oacute; nhiều hơn m&aacute;y cũng sẽ tự động c&acirc;n chỉnh kh&eacute;o l&eacute;o để người xem c&oacute; được g&oacute;c nh&igrave;n tốt nhất.&nbsp;</p>\r\n\r\n<h3>Kết nối Thunderbolt tương th&iacute;ch những phụ kiện h&agrave;ng đầu</h3>\r\n\r\n<p>iPad Pro 12.9 2021 hỗ trợ cổng kết nối Thunderbolt dưới giao thức USB-C mạnh mẽ cho tốc độ truyền dữ liệu l&ecirc;n tới 40 Gbps, dễ d&agrave;ng kết nối với nhiều phụ kiện kh&aacute;c nhau, kể cả xuất ra m&agrave;n h&igrave;nh lớn độ ph&acirc;n giải l&ecirc;n tới 6K, để bạn l&agrave;m việc trực quan v&agrave; chuy&ecirc;n nghiệp hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074032.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Kết nối Thunderbolt cao cấp \" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074032.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074032.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Kết nối Thunderbolt cao cấp \" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, iPad Pro 11 M1 cũng ho&agrave;n to&agrave;n tương th&iacute;ch với b&uacute;t cảm ứng Apple Pencil biến iPad Pro th&agrave;nh một bảng vẽ v&agrave; l&agrave; thiết bị ghi ch&uacute; v&agrave; b&agrave;n ph&iacute;m Magic Keyboard sẽ gi&uacute;p trải nghiệm iPad Pro kh&ocirc;ng k&eacute;m g&igrave; những chiếc MacBook hiện tại với tốc độ đ&aacute;nh m&aacute;y cực nhanh v&agrave; t&iacute;ch hợp b&agrave;n di chuột TouchPad.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074042.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Bàn phím rời\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074042.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074042.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Bàn phím rời\" /></a></p>\r\n\r\n<p>Lưu &yacute;: B&agrave;n ph&iacute;m Magic Keyboard v&agrave; b&uacute;t Apple Pencil kh&ocirc;ng đi k&egrave;m trong hộp m&aacute;y.</p>\r\n\r\n<h3>Thời lượng pin ấn tượng</h3>\r\n\r\n<p>iPad Pro 12.9 inch 2021 được trang bị vi&ecirc;n pin dung lượng lớn 40.88 Wh tương đương khoảng 10.835 mAh cho thời lượng sử dụng l&acirc;u d&agrave;i để thực hiện nhiều t&aacute;c vụ kh&aacute;c nhau.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074038.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thời lượng pin ấn tượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074038.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074038.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thời lượng pin ấn tượng\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; m&aacute;y t&iacute;nh bảng n&agrave;y cũng hỗ trợ&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-sac-pin-nhanh\" target=\"_blank\" title=\"Tham khảo máy tính bảng có khả năng sạc nhanh\">sạc pin nhanh</a>&nbsp;c&ocirc;ng suất cao 20 W r&uacute;t ngắn đ&aacute;ng kể thời gian để bạn nhanh ch&oacute;ng tiếp tục lại c&ocirc;ng việc hay giải tr&iacute; c&ograve;n đang dang dở.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074049.jpg\" onclick=\"return false;\"><img alt=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thời lượng pin ấn tượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074049.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/259650/ipad-pro-m1-129-inch-wifi-512gb-2021-261121-074049.jpg\" title=\"Máy tính bảng iPad Pro M1 12.9 inch WiFi 512GB (2021) | Thời lượng pin ấn tượng\" /></a></p>\r\n\r\n<p>T&oacute;m lại, iPad Pro 12.9 M1 Wifi 512GB 2021 l&agrave; một chiếc m&aacute;y t&iacute;nh bảng rất đ&aacute;ng để đầu tư v&agrave; sở hữu với thiết kế sang trọng, nhỏ gọn, t&iacute;nh di động cao, thời lượng pin ổn định v&agrave; đặc biệt mạnh mẽ c&oacute; thể xử l&yacute; tốt c&aacute;c t&aacute;c vụ. Sản phẩm cũng hướng tới nh&oacute;m người d&ugrave;ng đang t&igrave;m kiếm một sản phẩm c&oacute; thể thay thế cho laptop để l&agrave;m việc v&agrave; giải tr&iacute;.</p>\r\n', 'Đã đăng', 2, '08-09-2023', 0),
(43, 'VHL#107', 'iPad Pro M2 11 inch WiFi Cellular 128GB', 23990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'table8.jpg', '<h3><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-m2-11-wifi-cellular-128gb\" target=\"_blank\">iPad Pro M2 11 inch WiFi Cellular 128GB</a>&nbsp;l&agrave; mẫu tablet mới nhất được nh&agrave; t&aacute;o ph&aacute;t h&agrave;nh cho năm 2022 với điểm nổi bật l&agrave; sự xuất hiện của Apple M2. Đ&acirc;y vốn dĩ l&agrave; vi xử l&yacute; mạnh mẽ được trang bị tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\">Macbook</a>&nbsp;nhưng nay lại t&iacute;ch hợp cho tablet, điều n&agrave;y gi&uacute;p cho thiết bị c&oacute; thể xử l&yacute; tốt hầu hết mọi t&aacute;c vụ từ việc kết xuất video cho đến thiết kế - dựng h&igrave;nh ảnh 2D v&agrave; 3D.</h3>\r\n\r\n<h3>Thiết kế đẳng cấp sang trọng</h3>\r\n\r\n<p>Năm nay nh&agrave; Apple vẫn giữ lại kiểu thiết kế quen thuộc tr&ecirc;n thế hệ iPad Pro M1 v&agrave;o năm ngo&aacute;i, với mặt lưng kim loại chắc chắn đi c&ugrave;ng hai mặt v&agrave; c&aacute;c cạnh v&aacute;t phẳng gi&uacute;p cho tổng thể thiết bị c&oacute; được c&aacute;i nh&igrave;n tinh tế v&agrave; kh&ocirc;ng k&eacute;m phần hiện đại.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033149.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế sang trọng - iPad Pro M2 11 inch WiFi Cellular 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033149.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033149.jpg\" title=\"Thiết kế sang trọng - iPad Pro M2 11 inch WiFi Cellular 128GB\" /></a></p>\r\n\r\n<p>G&oacute;c phải m&agrave;n h&igrave;nh sẽ l&agrave; n&uacute;t nguồn v&agrave; tăng/giảm &acirc;m lượng được h&atilde;ng bố tr&iacute; để người d&ugrave;ng c&oacute; thể thuận tiện hơn trong việc điều chỉnh thiết bị bằng ph&iacute;m cứng.&nbsp;</p>\r\n\r\n<p>Điểm nhấn ch&iacute;nh ở mặt lưng sẽ l&agrave; logo quả t&aacute;o được l&agrave;m s&aacute;ng b&oacute;ng v&agrave; đặt ở vị tr&iacute; trung t&acirc;m, đ&acirc;y c&oacute; thể coi l&agrave; đặc điểm nhận diện của iPad Pro M2 gi&uacute;p cho người d&ugrave;ng c&oacute; thể to&aacute;t l&ecirc;n vẻ ngo&agrave;i đẳng cấp hơn khi tr&ecirc;n tay một sản phẩm mới nhất đến từ nh&agrave; Apple (10/2022).</p>\r\n\r\n<p>iPad Pro M2 được xem l&agrave; chiếc tablet si&ecirc;u mỏng khi bề d&agrave;y của thiết bị chỉ rơi v&agrave;o khoảng 5.9 mm. Vậy n&ecirc;n tổng quan thiết bị c&oacute; được một c&aacute;i nh&igrave;n đẳng cấp v&agrave; sang trọng hơn bởi n&oacute; đ&aacute;p ứng được ti&ecirc;u ch&iacute; si&ecirc;u mỏng, điều m&agrave; hầu hết c&aacute;c nh&agrave; sản xuất thiết bị di động đều đang hướng tới trong tương lai.</p>\r\n\r\n<h3>Hiệu năng mạnh mẽ vượt trội</h3>\r\n\r\n<p>Lần ra mắt n&agrave;y, nh&agrave; t&aacute;o trang bị cho iPad con chip Apple M2 thuộc thế hệ mới nhất của h&atilde;ng, kh&ocirc;ng chỉ tăng cường khả năng xử l&yacute; m&agrave; chip c&ograve;n gi&uacute;p tối ưu được điện năng ti&ecirc;u thụ để k&eacute;o d&agrave;i thời gian sử dụng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033151.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ - iPad Pro M2 11 inch WiFi Cellular 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033151.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033151.jpg\" title=\"Hiệu năng mạnh mẽ - iPad Pro M2 11 inch WiFi Cellular 128GB\" /></a></p>\r\n\r\n<p>Khi trang bị tr&ecirc;n tablet th&igrave; Apple M2 sẽ c&oacute; 8 l&otilde;i CPU v&agrave; 10 l&otilde;i GPU đều hoạt động với hiệu suất cao, hiệu năng xử l&yacute; được tăng cường th&ecirc;m 15% v&agrave; 35% về khoản xử l&yacute; đồ họa so với Apple M1. Nhờ vậy m&agrave; tablet ho&agrave;n to&agrave;n c&oacute; thể đ&aacute;p ứng tốt cho c&aacute;c t&aacute;c vụ xử l&yacute; đồ họa n&acirc;ng cao như: thiết kế đồ họa, kiến tr&uacute;c hay kết xuất v&agrave; dựng video.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033152.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ - iPad Pro M2 11 inch WiFi Cellular 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033152.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033152.jpg\" title=\"Hiệu năng mạnh mẽ - iPad Pro M2 11 inch WiFi Cellular 128GB\" /></a></p>\r\n\r\n<p>Đối với người d&ugrave;ng thường xuy&ecirc;n sử dụng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\">m&aacute;y t&iacute;nh bảng iPad</a>&nbsp;để kết nối với m&agrave;n h&igrave;nh rời th&igrave; hiệu năng tr&ecirc;n Apple M2 sẽ gi&uacute;p &iacute;ch được kh&aacute; nhiều, h&atilde;ng cho biết iPad Pro M2 c&oacute; thể xuất ra m&agrave;n h&igrave;nh phụ với chất lượng tối đa đạt được l&agrave; 6K c&ugrave;ng tốc độ khung h&igrave;nh ở mức 60.</p>\r\n\r\n<p>Đối với iPad Pro M2 th&igrave; m&aacute;y sẽ hỗ trợ hệ điều h&agrave;nh iPadOS 16 được tối ưu ri&ecirc;ng d&agrave;nh cho c&aacute;c thế hệ m&aacute;y t&iacute;nh bảng, điều n&agrave;y gi&uacute;p thiết bị c&oacute; thể vận h&agrave;nh tốt hơn khi hiệu suất v&agrave; năng lượng đ&atilde; được c&acirc;n bằng một c&aacute;ch tối ưu.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033155.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ - iPad Pro M2 11 inch WiFi Cellular 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033155.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033155.jpg\" title=\"Hiệu năng mạnh mẽ - iPad Pro M2 11 inch WiFi Cellular 128GB\" /></a></p>\r\n\r\n<h3>N&acirc;ng cao trải nghiệm nghe nh&igrave;n</h3>\r\n\r\n<p>Về phần m&agrave;n h&igrave;nh th&igrave; iPad Pro M2 đ&atilde; được trang bị m&agrave;n h&igrave;nh Liquid Retina với độ ph&acirc;n giải 1668 x 2388 Pixels, điều n&agrave;y gi&uacute;p mang lại khả năng t&aacute;i hiện h&igrave;nh ảnh với m&agrave;u sắc sinh động, độ ch&iacute;nh x&aacute;c về m&agrave;u cao gi&uacute;p cho nội dung m&agrave; bạn đang xem trở n&ecirc;n ch&acirc;n thực hơn bao giờ hết.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033159.jpg\" onclick=\"return false;\"><img alt=\"Màn hình chất lượng - iPad Pro M2 11 inch WiFi Cellular 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033159.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033159.jpg\" title=\"Màn hình chất lượng - iPad Pro M2 11 inch WiFi Cellular 128GB\" /></a></p>\r\n\r\n<p>V&igrave; được hỗ trợ dải m&agrave;u rộng P3, c&ocirc;ng nghệ m&agrave;n h&igrave;nh True Tone c&ugrave;ng độ tương phản cao n&ecirc;n iPad Pro M2 c&oacute; thể d&ugrave;ng l&agrave;m thiết bị để tham chiếu m&agrave;u sắc cho c&aacute;c t&aacute;c vụ thiết kế - in ấn, hạn chế được tối đa t&igrave;nh trạng sai lệch m&agrave;u qu&aacute; nhiều sau khi in ra th&agrave;nh phẩm.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh c&oacute; k&iacute;ch thước 11 inch gi&uacute;p cho người d&ugrave;ng c&oacute; thể quan s&aacute;t được nhiều nội dung hơn tr&ecirc;n c&ugrave;ng một khung h&igrave;nh, ngo&agrave;i ra khi sử dụng t&iacute;nh năng Stage Manager cũng được tốt hơn bởi c&aacute;c tab sẽ được hiển thị với k&iacute;ch thước lớn gi&uacute;p người d&ugrave;ng c&oacute; thể thao t&aacute;c dễ d&agrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033201.jpg\" onclick=\"return false;\"><img alt=\"Sử dụng nhiều ứng dụng cùng lúc - iPad Pro M2 11 inch WiFi Cellular 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033201.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033201.jpg\" title=\"Sử dụng nhiều ứng dụng cùng lúc - iPad Pro M2 11 inch WiFi Cellular 128GB\" /></a></p>\r\n\r\n<p>Đến với phần &acirc;m thanh th&igrave; phi&ecirc;n bản lần n&agrave;y sẽ được t&iacute;ch hợp 4 loa c&oacute; đi k&egrave;m c&ocirc;ng nghệ Dolby Atmos, vậy n&ecirc;n &acirc;m thanh cho ra sẽ c&oacute; độ trong trẻo v&agrave; ch&acirc;n thực hơn gi&uacute;p người d&ugrave;ng c&oacute; thể cảm nhận như đang tận hưởng giai điệu trực tiếp nơi ph&ograve;ng thu.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-dolby-atmos-tren-smartphone-772074\" target=\"_blank\">C&ocirc;ng nghệ Dolby Atmos l&agrave; g&igrave;? Kh&aacute;m ph&aacute; c&ocirc;ng nghệ &acirc;m thanh đỉnh cao</a></p>\r\n\r\n<h3>Hỗ trợ quay - chụp sắc n&eacute;t</h3>\r\n\r\n<p>Mặt sau của m&aacute;y sẽ được t&iacute;ch hợp 2 camera trong đ&oacute; camera ch&iacute;nh c&oacute; độ ph&acirc;n giải 12 MP đi c&ugrave;ng với cảm biến g&oacute;c si&ecirc;u rộng 10 MP. B&ecirc;n cạnh đ&oacute; th&igrave; m&aacute;y được hỗ trợ c&aacute;c t&iacute;nh năng nổi bật như Smart HDR 4, chống rung OIS, chế độ điện ảnh v&agrave; bộ lọc m&agrave;u để n&acirc;ng cao chất lượng tr&ecirc;n từng thước phim hay bức h&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033205.jpg\" onclick=\"return false;\"><img alt=\"Camera chất lượng - iPad Pro M2 11 inch WiFi Cellular 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033205.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033205.jpg\" title=\"Camera chất lượng - iPad Pro M2 11 inch WiFi Cellular 128GB\" /></a></p>\r\n\r\n<p>C&oacute; thể thấy iPad Pro M2 l&agrave; một chiếc tablet kh&aacute; l&yacute; tưởng d&agrave;nh cho việc quay phim khi m&aacute;y ho&agrave;n to&agrave;n c&oacute; thể thu được những video c&oacute; chất lượng l&ecirc;n tới 4K 2160p@60fps. Điều n&agrave;y gi&uacute;p người d&ugrave;ng c&oacute; thể tối ưu được cả hai t&aacute;c vụ quay - dựng cho c&ugrave;ng một thiết bị, ph&ugrave; hợp cho những bạn l&agrave;m c&aacute;c c&ocirc;ng việc thi&ecirc;n về sản xuất nội dung h&igrave;nh ảnh tr&ecirc;n mạng x&atilde; hội.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033206.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ quay - chụp chuyên nghiệp - iPad Pro M2 11 inch WiFi Cellular 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033206.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033206.jpg\" title=\"Hỗ trợ quay - chụp chuyên nghiệp - iPad Pro M2 11 inch WiFi Cellular 128GB\" /></a></p>\r\n\r\n<h3>Sử dụng d&agrave;i l&acirc;u&nbsp;</h3>\r\n\r\n<p>iPad Pro M2 l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-pin-khung\" target=\"_blank\">m&aacute;y t&iacute;nh bảng pin khủng tr&ecirc;n 7000 mAh</a>&nbsp;sẽ được trang bị vi&ecirc;n pin lớn c&oacute; dung lượng 28.65 Wh v&agrave; c&oacute; thể quy đổi th&agrave;nh con số xấp xỉ 7538 mAh. V&igrave; thế m&aacute;y ho&agrave;n to&agrave;n c&oacute; thể đ&aacute;p ứng cho bạn thời lượng d&ugrave;ng l&ecirc;n tới cả ng&agrave;y, từ đ&oacute; c&oacute; thể phục vụ tốt trong việc học tập hay l&agrave;m những c&ocirc;ng việc thiết kế m&agrave; kh&ocirc;ng cần bận t&acirc;m qu&aacute; nhiều đến vấn đề hết pin.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033154.jpg\" onclick=\"return false;\"><img alt=\"Thoải mái dùng lâu - iPad Pro M2 11 inch WiFi Cellular 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033154.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295458/ipad-pro-m2-11-wifi-cellular-128gb-261022-033154.jpg\" title=\"Thoải mái dùng lâu - iPad Pro M2 11 inch WiFi Cellular 128GB\" /></a></p>\r\n\r\n<p>Với những điều tuyệt vời b&ecirc;n tr&ecirc;n đ&atilde; đủ để minh chứng iPad Pro M2 trở th&agrave;nh chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\">m&aacute;y t&iacute;nh bảng</a>&nbsp;đ&aacute;ng mua nhất t&iacute;nh đến thời điểm hiện tại, với những bạn thường xuy&ecirc;n l&agrave;m c&aacute;c c&ocirc;ng việc về thiết kế hay chỉnh sửa nội dung video th&igrave; đ&acirc;y chắc hẳn l&agrave; một thiết bị l&yacute; tưởng để tối ưu c&ocirc;ng việc của bạn c&oacute; thể diễn ra nhanh ch&oacute;ng.</p>\r\n', 'Đã đăng', 2, '08-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(44, 'VHL#108', 'iPad Pro M2 11 inch WiFi 128GB', 20490000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'table9.jpg', '<h3>Cơn sốt của&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone-14-series\" target=\"_blank\" title=\"Tham khảo thêm iPhone 14 series đang kinh doanh tại Thế Giới Di Động\">iPhone 14 series</a>&nbsp;chưa kịp lắng xuống th&igrave; mới đ&acirc;y nh&agrave; Apple lại vừa tung ra bộ sản phẩm tablet cho năm 2022 với nhiều điểm thu h&uacute;t. Con chip Apple M2 từng khuấy đảo thị trường laptop b&acirc;y giờ đ&atilde; được xuất hiện tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-m2-11-inch\" target=\"_blank\" title=\"iPad Pro M2 11 inch WiFi 128GB\">iPad Pro M2 11 inch WiFi 128GB</a>, n&oacute; quả thực l&agrave; một tin chấn động trong giới c&ocirc;ng nghệ bởi đ&acirc;y c&oacute; thể l&agrave; chiếc m&aacute;y t&iacute;nh bảng c&oacute; hiệu năng v&ocirc; đối tr&ecirc;n thị trường (10/2022).</h3>\r\n\r\n<h3>Thiết kế tinh tế - dẫn đầu xu thế</h3>\r\n\r\n<p>iPad Pro M2 c&oacute; một diện mạo cao cấp với mặt lưng l&agrave;m từ kim loại chắc chắn, đi k&egrave;m với độ mỏng chỉ 5.9 mm gi&uacute;p cho tổng thể thiết bị th&ecirc;m phần sang trọng. Dường như Apple đang rất nỗ lực để biến thiết bị của h&atilde;ng trở n&ecirc;n ng&agrave;y c&agrave;ng thanh mảnh, chắc hẳn tr&ecirc;n thị trường tablet hiện tại chưa c&oacute; c&aacute;i t&ecirc;n n&agrave;o c&oacute; thể vượt qua độ mỏng của iPad Pro M2.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053252.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế sang trọng - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053252.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053252.jpg\" title=\"Thiết kế sang trọng - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>Nh&igrave;n chung th&igrave; iPad Pro M2 kh&ocirc;ng c&oacute; qu&aacute; nhiều sự đổi mới về thiết kế so với thế hệ tiền nhiệm, vẫn sẽ l&agrave; kiểu ho&agrave;n thiện vu&ocirc;ng vức ở c&aacute;c cạnh v&agrave; hai mặt, nổi bật nhờ tone m&agrave;u chủ đạo l&agrave; x&aacute;m v&agrave; bạc để ph&ugrave; hợp cho cả nam v&agrave; nữ hay bất kỳ lứa tuổi n&agrave;o.</p>\r\n\r\n<p>B&ecirc;n cạnh hai sự lựa chọn về m&agrave;u sắc th&igrave; năm nay h&atilde;ng cũng sẽ ph&aacute;t h&agrave;nh hai phi&ecirc;n bản với chuẩn kết nối mạng c&oacute; đ&ocirc;i ch&uacute;t kh&aacute;c nhau l&agrave; WiFi v&agrave; WiFi Cellular. Điểm kh&aacute;c biệt m&agrave; ta c&oacute; thể ph&acirc;n biệt hai loại n&agrave;y nằm ở phần anten, tr&ecirc;n phi&ecirc;n bản WiFi th&igrave; d&atilde;y n&agrave;y chỉ dừng lại ở phần cụm camera chứ kh&ocirc;ng chạy d&agrave;i đến phần cạnh b&ecirc;n như WiFi Cellular.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-241022-014908.jpg\" onclick=\"return false;\"><img alt=\"Đặc điểm nhận dạng - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-241022-014908.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-241022-014908.jpg\" title=\"Đặc điểm nhận dạng - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<h3>Hiệu năng bứt ph&aacute; mọi giới hạn</h3>\r\n\r\n<p>Sau rất nhiều ho&agrave;i nghi về sự xuất hiện của Apple M2 tr&ecirc;n thế hệ iPad 2022 th&igrave; giờ đ&acirc;y n&oacute; cũng đ&atilde; thực sự c&oacute; mặt tr&ecirc;n m&aacute;y t&iacute;nh bảng iPad Pro mới nhất. Qua lần ra mắt n&agrave;y cho thấy Apple lu&ocirc;n cố gắng để biến những điều tưởng chừng như kh&ocirc;ng thể th&agrave;nh hiện thực tr&ecirc;n d&ograve;ng sản phẩm của m&igrave;nh, điều n&agrave;y c&oacute; thể minh chứng qua sự xuất hiện của Apple M2 l&ecirc;n tr&ecirc;n một chiếc m&aacute;y t&iacute;nh bảng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053255.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053255.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053255.jpg\" title=\"Hiệu năng mạnh mẽ - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>Bộ vi xử l&yacute; n&agrave;y c&oacute; tận 8 l&otilde;i CPU v&agrave; 10 l&otilde;i GPU gi&uacute;p mang lại hiệu suất vượt trội hơn so với thế hệ Apple M1 trước đ&oacute;, như h&atilde;ng c&oacute; đề cập đến th&igrave; Apple M2 c&oacute; hiệu suất nhanh hơn 15% v&agrave; khả năng xử l&yacute; đồ họa tốt hơn 35% đối với M1. Vậy n&ecirc;n người d&ugrave;ng c&oacute; thể an t&acirc;m sử dụng&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Xem thêm Máy tính bảng chính hãng, giá tốt đang kinh doanh tại Thế Giới Di Động\">m&aacute;y t&iacute;nh bảng</a>&nbsp;để d&ugrave;ng cho hầu hết mọi t&aacute;c vụ kể cả đồ họa n&acirc;ng cao 3D.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053257.jpg\" onclick=\"return false;\"><img alt=\"Xử lý công việc nhanh chóng - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053257.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053257.jpg\" title=\"Xử lý công việc nhanh chóng - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>M&aacute;y t&iacute;nh bảng n&agrave;y c&oacute; tới 8 GB RAM n&ecirc;n phần lớn những t&aacute;c vụ đa nhiệm th&igrave; iPad Pro M2 đều c&oacute; thể xử l&yacute; dễ d&agrave;ng. Ngo&agrave;i ra bộ nhớ RAM lớn c&ograve;n hỗ trợ tốt cho việc chơi những tựa game c&oacute; đồ họa cao, điều n&agrave;y đảm bảo mỗi trận đấu rank hay chơi game c&ugrave;ng bạn b&egrave; kh&ocirc;ng bị gi&aacute;n đoạn bởi những hiện tượng như giật lag.</p>\r\n\r\n<p>iPad Pro M2 được hỗ trợ hệ điều h&agrave;nh iPadOS 16 gi&uacute;p mang lại giao diện đẹp mắt hơn, c&oacute; th&ecirc;m nhiều t&ugrave;y chọn c&agrave;i đặt về giao hiện hay n&acirc;ng cao khả năng bảo mật. Điều n&agrave;y sẽ trở n&ecirc;n cực kỳ hữu &iacute;ch bởi n&oacute; mang lại cho người d&ugrave;ng sự an t&acirc;m về việc bảo mật th&ocirc;ng tin cũng như tối ưu được c&ocirc;ng việc th&ocirc;ng qua nhiều t&iacute;nh năng hay ho.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053259.jpg\" onclick=\"return false;\"><img alt=\"Xử lý công việc hiệu quả - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053259.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053259.jpg\" title=\"Xử lý công việc hiệu quả - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng chỉ đem tới kết nối mạng ổn định hơn m&agrave; lần n&agrave;y Apple c&ograve;n hướng tới việc n&acirc;ng cao tốc độ truyền tải internet vượt trội, bởi iPad Pro M2 l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-5g\" target=\"_blank\" title=\"Tham khảo máy tính bảng hỗ trợ 5G đang kinh doanh tại Thế Giới Di Động\">m&aacute;y t&iacute;nh bảng 5G</a>&nbsp;v&agrave; được hỗ trợ chuẩn WiFi 6E n&ecirc;n việc tải l&ecirc;n hay tải xuống dữ liệu th&ocirc;ng qua chuẩn kết nối n&agrave;y l&agrave; cực kỳ nhanh ch&oacute;ng.</p>\r\n\r\n<h3>N&acirc;ng cao trải nghiệm nghe - nh&igrave;n</h3>\r\n\r\n<p>iPad Pro M2 ở phi&ecirc;n bản n&agrave;y sẽ c&oacute; m&agrave;n h&igrave;nh k&iacute;ch thước 11 inch, đủ để bạn c&oacute; thể thỏa th&iacute;ch vẽ vời hay xem phim được to r&otilde; hơn. Ngo&agrave;i ra m&agrave;n h&igrave;nh lớn c&ograve;n đ&aacute;p ứng tốt cho việc sử dụng đồng thời nhiều app c&ugrave;ng l&uacute;c th&ocirc;ng qua t&iacute;nh năng Stage Manager c&oacute; tr&ecirc;n iPadOS 16.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053307.jpg\" onclick=\"return false;\"><img alt=\"Màn hình kích thước lớn - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053307.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053307.jpg\" title=\"Màn hình kích thước lớn - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>Nếu cảm thấy đ&acirc;y vẫn l&agrave; k&iacute;ch thước m&agrave;n h&igrave;nh chưa l&agrave;m bạn thỏa m&atilde;n th&igrave; người d&ugrave;ng c&oacute; thể quan t&acirc;m đến phi&ecirc;n bản&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-m2-12-9-inch\" target=\"_blank\" title=\"iPad Pro M2 12.9 inch WiFi 128GB\">iPad Pro M2 12.9 inch WiFi 128GB</a>, đ&acirc;y c&oacute; thể coi như một m&agrave;n h&igrave;nh gần như tương đồng với một chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm Laptop đang kinh doanh tại Thế Giới Di Động\">laptop</a>&nbsp;tr&ecirc;n thị trường.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053300.jpg\" onclick=\"return false;\"><img alt=\"Màn hình kích thước lớn - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053300.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053300.jpg\" title=\"Màn hình kích thước lớn - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>Ở phi&ecirc;n bản iPad Pro M2 11 inch th&igrave; m&agrave;n h&igrave;nh sẽ được sử dụng tấm nền Liquid Retina, v&igrave; được ch&iacute;nh Apple nghi&ecirc;n cứu v&agrave; ph&aacute;t triển n&ecirc;n m&agrave;n h&igrave;nh sẽ tương th&iacute;ch với thiết bị tốt hơn, cho ra độ tương phản cao, h&igrave;nh ảnh phản &aacute;nh gần đ&uacute;ng với thực tế th&ocirc;ng qua độ ch&iacute;nh x&aacute;c về m&agrave;u cao, mọi nội dung từ việc xem phim hay chơi game đều mang lại cho người d&ugrave;ng trải nghiệm ch&acirc;n thực.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cung-tim-hieu-ve-man-hinh-liquid-retina-tren-iphon-1125106\">M&agrave;n h&igrave;nh Liquid Retina l&agrave; g&igrave;? C&oacute; g&igrave; đặc biệt? C&oacute; tr&ecirc;n thiết bị n&agrave;o?</a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053303.jpg\" onclick=\"return false;\"><img alt=\"Tấm nền cao cấp - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053303.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053303.jpg\" title=\"Tấm nền cao cấp - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; th&igrave;&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-pro-m2\" target=\"_blank\" title=\"Xem thêm iPad Pro M2 đang kinh doanh tại Thế Giới Di Động\">m&aacute;y t&iacute;nh bảng iPad Pro M2</a>&nbsp;c&ograve;n được hỗ trợ đầy đủ c&aacute;c c&ocirc;ng nghệ m&agrave;n h&igrave;nh như: Dải m&agrave;u rộng P3, True Tone v&agrave; ProMotion, đ&acirc;y chắc hẳn l&agrave; những g&igrave; m&agrave; mọi nh&agrave; thiết kế cực kỳ quan t&acirc;m v&agrave; ưa chuộng, bởi n&oacute; gi&uacute;p người d&ugrave;ng c&oacute; thể sử dụng m&aacute;y t&iacute;nh bảng như một thiết bị để tham chiếu m&agrave;u sắc sau khi thiết kế, l&agrave;m việc tr&ecirc;n m&aacute;y t&iacute;nh bảng cũng sẽ cảm thấy an t&acirc;m hơn m&agrave; kh&ocirc;ng sợ bị sai lệch về m&agrave;u sắc qu&aacute; nhiều.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/do-bao-phu-mau-dci-p3-la-gi-1380493\">Độ bao phủ m&agrave;u DCI-P3 l&agrave; g&igrave;?</a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053309.jpg\" onclick=\"return false;\"><img alt=\"Tái hiện màu sắc chính xác - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053309.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053309.jpg\" title=\"Tái hiện màu sắc chính xác - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>C&aacute;c thao t&aacute;c tr&ecirc;n m&agrave;n h&igrave;nh giờ đ&acirc;y cũng sẽ được phản hồi nhanh ch&oacute;ng hơn, giảm thiểu được độ trễ nhờ trang bị tần số qu&eacute;t 120 Hz ti&ecirc;n tiến, hỗ trợ tốt hơn trong việc sử dụng chung với phụ kiện như Apple Pencil 2 hay chơi những tựa game c&oacute; FPS cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053305.jpg\" onclick=\"return false;\"><img alt=\"Tái hiện màu sắc chính xác - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053305.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053305.jpg\" title=\"Tái hiện màu sắc chính xác - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>Hai cạnh tr&ecirc;n v&agrave; dưới của m&aacute;y t&iacute;nh bảng được bố tr&iacute; 4 loa v&agrave; được hỗ trợ c&ocirc;ng nghệ &acirc;m thanh Dolby Atmos, kh&ocirc;ng chỉ mang lại &acirc;m thanh to r&otilde; m&agrave; tablet c&ograve;n tạo hiệu ứng để nội dung đến tai người d&ugrave;ng c&oacute; chiều s&acirc;u hơn, cảm gi&aacute;c như đang h&ograve;a m&igrave;nh v&agrave;o b&ecirc;n trong khung cảnh của m&agrave;n h&igrave;nh.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/cong-nghe-dolby-atmos-tren-smartphone-772074\">C&ocirc;ng nghệ Dolby Atmos l&agrave; g&igrave;? Kh&aacute;m ph&aacute; c&ocirc;ng nghệ &acirc;m thanh đỉnh cao</a></p>\r\n\r\n<h3>Đ&aacute;p ứng tốt nhu cầu chụp ảnh - quay phim</h3>\r\n\r\n<p>Để hỗ trợ cho việc sản xuất nội dung tốt hơn nhờ gộp cả hai qu&aacute; tr&igrave;nh quay v&agrave; dựng tr&ecirc;n c&ugrave;ng một thiết bị cho n&ecirc;n Apple cũng kh&aacute; ch&uacute; trọng đến camera của iPad Pro M2, minh chứng cho điều n&agrave;y l&agrave; h&atilde;ng c&oacute; trang bị bộ đ&ocirc;i camera trong đ&oacute; cảm biến ch&iacute;nh sẽ c&oacute; độ ph&acirc;n giải 12 MP v&agrave; phụ 10 MP.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053313.jpg\" onclick=\"return false;\"><img alt=\"Camera chất lượng - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053313.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053313.jpg\" title=\"Camera chất lượng - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>Năm nay h&atilde;ng chưa thực sự ph&aacute;t triển mạnh về khoản th&ocirc;ng số camera m&agrave; Apple n&acirc;ng cấp nhiều đến thuật to&aacute;n dựa tr&ecirc;n bộ vi xử l&yacute; trung t&acirc;m, ISP giờ đ&acirc;y cũng đ&atilde; mạnh mẽ hơn trong chip M2 v&agrave; c&oacute; hỗ trợ Smart HDR 4 n&ecirc;n nội dung thu được sẽ c&oacute; chất lượng gần như tương đồng với đời thực, độ chi tiết cao cũng l&agrave; ưu điểm gi&uacute;p việc hậu kỳ trở n&ecirc;n dễ d&agrave;ng hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053311.jpg\" onclick=\"return false;\"><img alt=\"Nâng cao thuật toán xử lý - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053311.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053311.jpg\" title=\"Nâng cao thuật toán xử lý - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>iPad Pro M2 c&oacute; thể gi&uacute;p bạn lưu lại những thước phim c&oacute; chuẩn độ ph&acirc;n giải l&ecirc;n tới 4K, hỗ trợ quay video ProRes để n&eacute;n chất lượng video của bạn được tốt nhất.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053315.jpg\" onclick=\"return false;\"><img alt=\"Nén tệp với chất lượng cao hơn - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053315.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053315.jpg\" title=\"Nén tệp với chất lượng cao hơn - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>Ở phần m&agrave;n h&igrave;nh tablet sẽ được trang bị một camera TrueDepth 12 MP để người d&ugrave;ng c&oacute; thể sử dụng Face ID một c&aacute;ch an to&agrave;n nhờ khả năng nhận diện c&oacute; độ ch&iacute;nh x&aacute;c cao, ảnh tự chụp của bạn cũng sẽ trở n&ecirc;n chất lượng hơn nhờ số chi tiết cực lớn m&agrave; thiết bị c&oacute; thể thu lại.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Xem thêm iPad chính hãng, giá tốt đang kinh doanh tại Thế Giới Di Động\">M&aacute;y t&iacute;nh bảng iPad</a>&nbsp;n&agrave;y c&oacute; thể tự động điều chỉnh khung h&igrave;nh sao cho chủ thể l&agrave; con người được nằm ở vị tr&iacute; gần trung t&acirc;m nhất, điều n&agrave;y gi&uacute;p &iacute;ch cho việc gọi FaceTime với bạn b&egrave; hay tự quay cho m&igrave;nh những thước phim để phục vụ cho việc sản xuất nội dung.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053317.jpg\" onclick=\"return false;\"><img alt=\"Camera trước chất lượng - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053317.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053317.jpg\" title=\"Camera trước chất lượng - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>Camera trước cũng c&oacute; khả năng quay video với chất lượng 4K n&ecirc;n người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể mong đợi thước phim của bản th&acirc;n thu lại sẽ c&oacute; chất lượng tốt hơn, đi k&egrave;m với đ&oacute; l&agrave; c&aacute;c t&iacute;nh năng như: X&oacute;a ph&ocirc;ng, HDR, bộ lọc m&agrave;u,...</p>\r\n\r\n<h3>Tương th&iacute;ch với phụ kiện tốt hơn</h3>\r\n\r\n<p>Năm nay nh&agrave; Apple tập trung kh&aacute; nhiều đến việc tối ưu sự tương th&iacute;ch giữa iPad v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/but-cam-ung-apple-pencil-2-mu8f2\" target=\"_blank\" title=\"Tham khảo Apple Pencil 2 đang kinh doanh trên Thế Giới Di Động\">Apple Pencil 2</a>, bởi m&agrave;n h&igrave;nh của m&aacute;y t&iacute;nh bảng c&oacute; thể nhận diện được thiết bị n&agrave;y ở khoảng c&aacute;ch 12 mm n&ecirc;n những thao t&aacute;c của bạn sẽ được phản hồi nhanh hơn. Vẽ vời hay vuốt/chạm cũng trở n&ecirc;n ch&iacute;nh x&aacute;c hơn nhờ việc hiển thị b&oacute;ng mờ vị tr&iacute; m&agrave; Apple Pencil 2 sẽ chạm tr&ecirc;n m&agrave;n h&igrave;nh để gi&uacute;p người d&ugrave;ng c&oacute; thể dễ d&agrave;ng theo d&otilde;i.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053321.jpg\" onclick=\"return false;\"><img alt=\"Giảm thiểu độ trễ - iPad Pro M2 11 inch WiFi 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053321.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/294104/ipad-pro-m2-11-inch-231022-053321.jpg\" title=\"Giảm thiểu độ trễ - iPad Pro M2 11 inch WiFi 128GB\" /></a></p>\r\n\r\n<p>Với những ưu điểm vượt trội m&agrave; iPad Pro M2 mang lại như ở b&ecirc;n tr&ecirc;n b&agrave;i viết, điều n&agrave;y gi&uacute;p m&aacute;y trở th&agrave;nh một thiết bị cực kỳ đ&aacute;ng sắm nhằm n&acirc;ng cấp trải nghiệm hay tối ưu h&oacute;a c&ocirc;ng việc của bạn tốt hơn. Kh&ocirc;ng những ch&uacute; trọng về hiệu năng m&agrave; h&atilde;ng c&ograve;n n&acirc;ng cấp cả về phần camera v&agrave; m&agrave;n h&igrave;nh để gi&uacute;p người d&ugrave;ng c&oacute; được những trải nghiệm tuyệt vời hơn.</p>\r\n', 'Đã đăng', 2, '08-09-2023', 0),
(45, 'VHL#109', 'iPad Air 5 M1 Wifi Cellular 64GB', 17490000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'table10.jpg', '<h3>So với chiếc iPad Air 5 M1 Wifi 64GB th&igrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-air-5-m1-wifi-cellular-64gb\" target=\"_blank\" title=\"iPad Air 5 M1 Wifi Cellular 64\" type=\"iPad Air 5 M1 Wifi Cellular 64\">iPad Air 5 M1 Wifi Cellular 64GB</a>&nbsp;đ&atilde; c&oacute; một điểm kh&aacute;c biệt đ&aacute;ng kể đ&oacute; l&agrave; phương thức kết nối khi bạn vừa c&oacute; thể sử dụng Wifi v&agrave; mạng di động một c&aacute;ch b&igrave;nh thường nhưng vẫn cho một trải nghiệm rất tuyệt vời.</h3>\r\n\r\n<h3>M&agrave;n h&igrave;nh to, xem phim, l&agrave;m việc đ&atilde; hơn</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\">M&aacute;y t&iacute;nh bảng</a>&nbsp;được trang bị một m&agrave;n h&igrave;nh lớn l&ecirc;n đến 10.9 inch vừa đủ kh&ocirc;ng qu&aacute; to nhưng cũng kh&ocirc;ng nhỏ cho bạn dễ d&agrave;ng trải nghiệm, sử dụng. Đi k&egrave;m với đ&oacute; chiếc m&agrave;n h&igrave;nh&nbsp;Retina IPS LCD&nbsp;n&agrave;y sẽ mang lại cho bạn những trải nghiệm về m&agrave;u sắc tốt hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/274155/ipad-air-5-m1-wifi-cellular-64gb-2.jpg\" onclick=\"return false;\"><img alt=\"Trải nghiệm siêu đã với màn hình lớn - iPad Air 5 M1 Wifi Cellular 64\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/274155/ipad-air-5-m1-wifi-cellular-64gb-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/274155/ipad-air-5-m1-wifi-cellular-64gb-2.jpg\" title=\"Trải nghiệm siêu đã với màn hình lớn - iPad Air 5 M1 Wifi Cellular 64\" /></a></p>\r\n\r\n<h3>Thiết kế thời thượng</h3>\r\n\r\n<p>Thiết kế gọn nhẹ v&agrave; được&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\">Apple</a>&nbsp;l&agrave;m vu&ocirc;ng vức ở c&aacute;c cạnh mang lại cảm gi&aacute;c sang trọng, đẳng cấp, nhiều người d&ugrave;ng rất th&iacute;ch điều n&agrave;y. M&agrave;u sắc đa dạng cũng sẽ gi&uacute;p bạn c&oacute; nhiều sự lựa chọn ph&ugrave; hợp để sử dụng hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/274155/ipad-air-5-m1-wifi-cellular-64gb-5.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế thời thượng - iPad Air 5 M1 Wifi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/274155/ipad-air-5-m1-wifi-cellular-64gb-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/274155/ipad-air-5-m1-wifi-cellular-64gb-5.jpg\" title=\"Thiết kế thời thượng - iPad Air 5 M1 Wifi Cellular 64GB\" /></a></p>\r\n\r\n<h3>Hiệu năng mạnh mẽ</h3>\r\n\r\n<p>Được cung cấp sức mạnh từ con chip Apple M1 v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-ram-8gb\" target=\"_blank\">RAM 8 GB</a>&nbsp;n&ecirc;n chiếc&nbsp;iPad Air 5 M1 Wifi Cellular n&agrave;y sẽ c&oacute; một sức mạnh v&ocirc; c&ugrave;ng kinh khủng. N&oacute; mạnh hơn thế hệ tiền nhiệm l&ecirc;n đến 60% hứa hẹn sẽ cho bạn một khả năng xử l&yacute;, l&agrave;m việc si&ecirc;u tốc.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/274155/ipad-air-5-m1-wifi-cellular-64gb-1.jpg\" onclick=\"return false;\"><img alt=\"Tính năng thông minh - iPad Air 5 M1 Wifi Cellular 64\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/274155/ipad-air-5-m1-wifi-cellular-64gb-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/274155/ipad-air-5-m1-wifi-cellular-64gb-1.jpg\" title=\"Tính năng thông minh - iPad Air 5 M1 Wifi Cellular 64\" /></a></p>\r\n\r\n<p>Nhờ được chạy tr&ecirc;n phi&ecirc;n bản phần mềm iPadOS 15 sẽ c&oacute; rất nhiều t&iacute;nh năng hay cho bạn cũng như tối ưu hiệu suất m&aacute;y. C&oacute; cả khả năng kết nối với b&uacute;t Apple Pencil, Magic Keyboard,... với độ trễ cực thấp.</p>\r\n\r\n<p>Qu&aacute; nhiều thứ hay ho cho một chiếc iPad Air 5 sẽ mang lại bạn những trải nghiệm mới nhất, mạnh mẽ v&agrave; thoải m&aacute;i nhất kể cả khi l&agrave;m việc hay giải tr&iacute;.</p>\r\n', 'Đã đăng', 2, '08-09-2023', 0),
(46, 'VHL#110', 'iPad 10 WiFi + Cellular 64GB', 14390000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'table11.jpg', '<h3>Nh&agrave; Apple đ&atilde; ch&iacute;nh thức cho ra mắt những mẫu iPad series mới v&agrave;o th&aacute;ng 10/2022. Trong đ&oacute;&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-10-wifi-cellular-64gb\" target=\"_blank\" title=\"Tham khảo iPad 10 WiFi Cellular 64GB đang kinh doanh tại Thế Giới Di Động\">iPad 10 WiFi Cellular 64GB</a>&nbsp;được xem l&agrave; c&aacute;i t&ecirc;n kh&aacute; nổi trội khi sở hữu mức gi&aacute; b&aacute;n dễ tiếp cận nhưng lại được trang bị cấu h&igrave;nh kh&aacute; tốt, c&oacute; những điểm nổi bật c&oacute; thể kể đến như: Apple A14 Bionic, hệ điều h&agrave;nh iPadOS 16, thay đổi về mặt thiết kế v&agrave; k&iacute;ch thước m&agrave;n h&igrave;nh lớn 10.9 inch.</h3>\r\n\r\n<h3>Thiết kế đem đến sự trẻ trung hiện đại</h3>\r\n\r\n<p>iPad 10 mang trong m&igrave;nh n&eacute;t đặc trưng vốn c&oacute; của nhiều thế hệ&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Tham khảo máy tính bảng iPad đang kinh doanh tại Thế Giới Di Động\">m&aacute;y t&iacute;nh bảng iPad</a>&nbsp;trước đ&acirc;y. Vẫn l&agrave; mặt lưng v&agrave; bộ khung được l&agrave;m liền mạch với nhau đi c&ugrave;ng vật liệu ho&agrave;n thiện l&agrave; kim loại, điều n&agrave;y mang lại cho m&aacute;y một diện mạo sang trọng v&agrave; đẳng cấp cũng như l&agrave;m tăng th&ecirc;m độ bền bỉ cho thiết bị.</p>\r\n\r\n<p>Năm nay nh&agrave; Apple mang đến cho ch&uacute;ng ta 4 phi&ecirc;n bản m&agrave;u sắc trẻ trung đầy vẻ c&aacute; t&iacute;nh. Kh&aacute;c với những tone m&agrave;u trước đ&acirc;y thường thấy tr&ecirc;n những d&ograve;ng iPad Air, m&agrave;u sắc tr&ecirc;n iPad thế hệ thứ 10 sẽ được l&agrave;m đậm v&agrave; c&oacute; phần nổi trội hơn rất nhiều.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111632.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế trẻ trung - iPad 10 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111632.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111632.jpg\" title=\"Thiết kế trẻ trung - iPad 10 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>Một sự cải tiến đ&aacute;ng kể về phần thiết kế so với mẫu&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-10-2-inch\" target=\"_blank\" title=\"Xem thêm iPad thế hệ thứ 9 chính hãng Apple đang bán tại Thế Giới Di Động\">iPad 9</a>&nbsp;v&agrave;o năm ngo&aacute;i l&agrave; phần viền m&agrave;n h&igrave;nh giờ đ&acirc;y đ&atilde; được l&agrave;m đều ở 4 cạnh, mang lại kh&ocirc;ng gian hiển thị rộng r&atilde;i cũng như cảm gi&aacute;c khi sử dụng trở n&ecirc;n dễ chịu.</p>\r\n\r\n<h3>Kh&ocirc;ng gian hiển thị rộng r&atilde;i</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-10-9-inch\" target=\"_blank\" title=\"Xem thêm dòng iPad thế hệ thứ 10 chính hãng Apple đang kinh doanh tại Thế Giới Di Động\">iPad gen 10</a>&nbsp;được trang bị m&agrave;n h&igrave;nh k&iacute;ch thước lớn với 10.9 inch n&ecirc;n phần lớn nội dung sẽ được hiển thị nhiều hơn tr&ecirc;n c&ugrave;ng một khung h&igrave;nh, ngo&agrave;i ra m&aacute;y c&ograve;n c&oacute; thể đ&aacute;p ứng tốt cho những t&aacute;c vụ vẽ vời hay hỗ trợ d&ugrave;ng nhiều ứng dụng tr&ecirc;n c&ugrave;ng một m&agrave;n h&igrave;nh được dễ d&agrave;ng hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111637.jpg\" onclick=\"return false;\"><img alt=\"Màn hình kích thước lớn - iPad 10 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111637.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111637.jpg\" title=\"Màn hình kích thước lớn - iPad 10 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo máy tính bảng đang kinh doanh tại Thế Giới Di Động\">M&aacute;y t&iacute;nh bảng</a>&nbsp;được t&iacute;ch hợp c&ocirc;ng nghệ m&agrave;n h&igrave;nh Retina IPS LCD độc quyền đến từ nh&agrave; Apple n&ecirc;n h&igrave;nh ảnh mang lại sẽ c&oacute; độ ch&acirc;n thực cao, nội dung t&aacute;i hiện trong trẻo c&ugrave;ng khả năng phản &aacute;nh m&agrave;u sắc một c&aacute;ch chuẩn x&aacute;c.</p>\r\n\r\n<p>Độ ph&acirc;n giải m&agrave;n h&igrave;nh cũng l&agrave; một lợi thế lớn của iPad 10 so với những đối thủ trong ph&acirc;n kh&uacute;c khi m&aacute;y hỗ trợ hiển thị h&igrave;nh ảnh chuẩn 1640 x 2360 Pixels. Tuy Apple kh&ocirc;ng c&ocirc;ng bố ch&iacute;nh x&aacute;c chuẩn m&agrave;n h&igrave;nh n&agrave;y l&agrave; g&igrave; nhưng th&ocirc;ng qua con số th&igrave; ta c&oacute; thể ước chừng m&aacute;y c&oacute; thể nằm trong khoảng 2K.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111647.jpg\" onclick=\"return false;\"><img alt=\"Màn hình kích thước lớn - iPad 10 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111647.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111647.jpg\" title=\"Màn hình kích thước lớn - iPad 10 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>Với những bạn đang l&agrave;m những c&ocirc;ng việc về s&aacute;ng tạo h&igrave;nh ảnh hay thời trang th&igrave; đ&acirc;y chắc hẳn l&agrave; một sự lựa chọn hết sức ph&ugrave; hợp, sẽ tuyệt vời hơn nếu d&ugrave;ng chung iPad 10 với&nbsp;<a href=\"https://www.thegioididong.com/phu-kien-thong-minh/but-cam-ung-apple-pencil-1-mk0c2\" target=\"_blank\" title=\"Tham khảo Apple Pencil đang kinh doanh tại Thế Giới Di Động\">Apple Pencil</a>&nbsp;để c&oacute; thể thực hiện c&aacute;c t&aacute;c vụ viết ghi ch&uacute; hay ph&aacute;c thảo nội dung một c&aacute;ch nhanh ch&oacute;ng v&agrave; tiện lợi.</p>\r\n\r\n<h3>Hiệu năng xử l&yacute; tốt nhiều t&aacute;c vụ</h3>\r\n\r\n<p>Với con chip Apple A14 Bionic được trang bị b&ecirc;n trong th&igrave; phần lớn c&aacute;c nhu cầu sử dụng h&agrave;ng ng&agrave;y m&aacute;y ho&agrave;n to&agrave;n c&oacute; thể xử l&yacute; dễ d&agrave;ng, chip hoạt động với 6 nh&acirc;n hiệu suất v&agrave; mức xung nhịp tối đa c&oacute; thể đạt được l&agrave; 3.1 GHz n&ecirc;n iPad 10 c&ograve;n c&oacute; thể thực hiện c&aacute;c c&ocirc;ng việc kh&oacute; khăn hơn như chỉnh sửa video, thiết kế h&igrave;nh ảnh hay chơi game đồ họa cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111640.jpg\" onclick=\"return false;\"><img alt=\"Hiệu năng mạnh mẽ - Màn hình kích thước lớn - iPad 10 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111640.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111640.jpg\" title=\"Hiệu năng mạnh mẽ - Màn hình kích thước lớn - iPad 10 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh việc n&acirc;ng cấp phần cứng so với thế hệ trước th&igrave; lần n&agrave;y nh&agrave; Apple c&ograve;n bổ sung th&ecirc;m cho m&aacute;y hệ điều h&agrave;nh iPadOS 16 ti&ecirc;n tiến. Điều n&agrave;y sẽ gi&uacute;p cho thiết bị c&oacute; thể tối ưu được hiệu suất v&agrave; năng lượng tốt hơn, thực hiện c&aacute;c c&ocirc;ng việc thường ng&agrave;y được nhanh ch&oacute;ng th&ocirc;ng qua nhiều t&iacute;nh năng mới th&uacute; vị v&agrave; tiện &iacute;ch hơn.</p>\r\n\r\n<h3>Hỗ trợ quay - chụp chất lượng cao</h3>\r\n\r\n<p>Bố tr&iacute; ở phần mặt lưng sẽ l&agrave; camera đơn với độ ph&acirc;n giải 12 MP, n&oacute; gi&uacute;p người d&ugrave;ng c&oacute; thể lưu giữ những khung cảnh đẹp được sắc n&eacute;t nhờ độ chi tiết kh&aacute; cao. B&ecirc;n cạnh đ&oacute; th&igrave; m&aacute;y cũng c&oacute; thể thu lại những thước phim chất lượng cao nhờ chuẩn video tối đa được hỗ trợ l&agrave; 4K - tốc độ khung h&igrave;nh 60.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111642.jpg\" onclick=\"return false;\"><img alt=\"Quay video chất lượng cao - iPad 10 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111642.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111642.jpg\" title=\"Quay video chất lượng cao - iPad 10 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>Ở phần m&agrave;n h&igrave;nh sẽ được trang bị camera selfie 12 MP, được hỗ trợ th&ecirc;m t&iacute;nh năng thu ph&oacute;ng khung h&igrave;nh để tự động điều chỉnh sao cho chủ thể c&oacute; thể xuất hiện ở vị tr&iacute; gần trung t&acirc;m nhất, ph&ugrave; hợp cho những bạn đang l&agrave;m c&aacute;c c&ocirc;ng việc thi&ecirc;n về s&aacute;ng tạo nội dung th&ocirc;ng qua video.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111644.jpg\" onclick=\"return false;\"><img alt=\"Camera trước chất lượng -  iPad 10 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111644.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/295453/ipad-10-wifi-cellular-64gb-281022-111644.jpg\" title=\"Camera trước chất lượng -  iPad 10 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>C&oacute; thể thấy iPad 10 l&agrave; sự lựa chọn kh&aacute; tối ưu khi m&aacute;y c&oacute; một mức gi&aacute; b&aacute;n hấp dẫn nhưng lại được trang bị bộ th&ocirc;ng số rất tốt, đ&acirc;y được xem l&agrave; sự lựa chọn l&yacute; tưởng d&agrave;nh cho những bạn đang l&agrave; học sinh - sinh vi&ecirc;n hay người d&ugrave;ng đang l&agrave;m c&aacute;c c&ocirc;ng việc thi&ecirc;n về thiết kế.</p>\r\n', 'Đã đăng', 2, '08-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(47, 'VHL#111', 'iPad 9 WiFi Cellular 64GB', 10390000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'table12.jpg', '<h3>Sau th&agrave;nh c&ocirc;ng của iPad 8&nbsp;th&igrave;&nbsp;<a href=\"https://www.thegioididong.com/apple\" target=\"_blank\" title=\"Tham khảo các sản phẩm Apple tại Thế Giới Di Động\">Apple</a>&nbsp;đ&atilde; tung ra chiếc&nbsp;iPad 9 WiFi Cellular 64GB&nbsp;với những trang bị ng&agrave;y c&agrave;ng vượt trội v&agrave; phong c&aacute;ch thiết kế đậm chất iPad 10.2 Series, đ&acirc;y sẽ l&agrave; mẫu&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo các dòng máy tính bảng đang bán tại TGDĐ\">m&aacute;y t&iacute;nh bảng</a>&nbsp;đ&aacute;ng c&acirc;n nhắc trong ph&acirc;n kh&uacute;c gi&aacute;.</h3>\r\n\r\n<h3>Phong c&aacute;ch thiết kế quen thuộc của d&ograve;ng iPad&nbsp;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng iPad đang kinh doanh tại Thế Giới Di Động \">M&aacute;y t&iacute;nh bảng iPad</a>&nbsp;n&agrave;y c&oacute; khung m&aacute;y h&igrave;nh chữ nhật k&egrave;m với 2 cạnh viền tr&ecirc;n dưới quen thuộc. Ở phi&ecirc;n bản n&agrave;y c&oacute; 2 m&agrave;u sắc l&agrave; m&agrave;u Bạc v&agrave; m&agrave;u X&aacute;m v&ocirc; c&ugrave;ng quen thuộc m&agrave; vẫn v&ocirc; c&ugrave;ng thanh lịch m&agrave; kh&ocirc;ng k&eacute;n người d&ugrave;ng. Bạn c&oacute; thể lựa chọn m&agrave;u sắc cho ph&ugrave; hợp với phong c&aacute;ch của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-1.jpg\" onclick=\"return false;\"><img alt=\"Có 2 phiên bản màu sắc - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-1.jpg\" title=\"Có 2 phiên bản màu sắc - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>Vỏ ngo&agrave;i bằng nh&ocirc;m c&oacute; khả năng tản nhiệt tốt v&agrave; cứng c&aacute;p bảo vệ to&agrave;n diện c&aacute;c linh kiện ở b&ecirc;n trong m&aacute;y. Mặt lưng hơi nh&aacute;m hạn chế b&aacute;m v&acirc;n tay, cho cảm gi&aacute;c cầm nắm tốt. iPad chỉ nhẹ 498 g v&agrave; mỏng 7.5 mm gi&uacute;p cho ta c&oacute; thẻ dễ d&agrave;ng cầm nắm v&agrave; thực hiện c&aacute;c thao t&aacute;c tr&ecirc;n m&agrave;n h&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-3.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế nhỏ gọn - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-3.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-3.jpg\" title=\"Thiết kế nhỏ gọn - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>N&uacute;t Home vẫn được giữ lại đặt ở giữa của cạnh viền dưới của m&agrave;n h&igrave;nh hỗ trợ bạn thao t&aacute;c nhanh ch&oacute;ng khi cần. Touch ID cũng được t&iacute;ch hợp ngay tr&ecirc;n ph&iacute;m Home n&agrave;y gi&uacute;p bạn mở kh&oacute;a m&agrave;n h&igrave;nh nhanh ch&oacute;ng, đăng nhập ứng dụng hay thanh to&aacute;n online đều v&ocirc; c&ugrave;ng tiện lợi.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-2.jpg\" onclick=\"return false;\"><img alt=\"Touch ID - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-2.jpg\" title=\"Touch ID - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<h3>Hiển thị h&igrave;nh ảnh sắc n&eacute;t, m&agrave;u sắc ch&iacute;nh x&aacute;c</h3>\r\n\r\n<p>iPad 9 sở hữu một m&agrave;n h&igrave;nh lớn tới 10.2 inch tạo một kh&ocirc;ng gian rộng r&atilde;i hiển thị được nhiều nội dung hơn, độ ph&acirc;n giải 1620 x 2160 Pixels cho c&aacute;c chi tiết hiện l&ecirc;n r&otilde; n&eacute;t v&agrave; m&agrave;u sắc v&ocirc; c&ugrave;ng sống động sẵn s&agrave;ng truyền tải nội dung đến bạn một c&aacute;ch ấn tượng nhất. M&agrave;n h&igrave;nh lớn gi&uacute;p đ&ocirc;i mắt của bạn dễ chịu hơn khi d&ugrave;ng l&acirc;u, l&agrave;m việc, xem phim hay đọc b&aacute;o điều v&ocirc; c&ugrave;ng tốt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-4.jpg\" onclick=\"return false;\"><img alt=\"Sở hữu màn hình 10.2 inch - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-4.jpg\" title=\"Sở hữu màn hình 10.2 inch - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>Chất lượng m&agrave;n h&igrave;nh Retina với độ chi tiết đ&aacute;ng kinh ngạc, m&agrave;u sắc ch&iacute;nh x&aacute;c v&agrave; c&ocirc;ng nghệ True Tone điều chỉnh m&agrave;n h&igrave;nh theo nhiệt độ m&agrave;u của m&ocirc;i trường gi&uacute;p thể hiện h&igrave;nh ảnh lu&ocirc;n v&ocirc; c&ugrave;ng nổi bật.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-5.jpg\" onclick=\"return false;\"><img alt=\"Công nghệ True Tone - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-5.jpg\" title=\"Công nghệ True Tone - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; bề mặt m&agrave;n h&igrave;nh c&ograve;n được phủ một lớp oleophobic hạn chế tối đa t&igrave;nh trạng lưu dấu v&acirc;n tay, giữ cho m&agrave;n h&igrave;nh của bạn lu&ocirc;n sạch sẽ.</p>\r\n\r\n<p>B&ecirc;n cạnh trải nghiệm h&igrave;nh ảnh tuyệt vời th&igrave; chiếc iPad cho ra những giai điệu v&ocirc; c&ugrave;ng sống động, cho khoảnh khắc giải tr&iacute; của bạn th&ecirc;m phần thoải m&aacute;i.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-6.jpg\" onclick=\"return false;\"><img alt=\"Công nghệ âm thanh Stereo - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-6.jpg\" title=\"Công nghệ âm thanh Stereo - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<h3>Cấu h&igrave;nh ổn định l&agrave;m việc hay giải tr&iacute; đều tốt</h3>\r\n\r\n<p>CPU Apple A13 Bionic 6 nh&acirc;n v&agrave; GPU đều được tăng 20% hiệu suất hoạt động hơn so với thế hệ trước l&agrave; Apple A12 Bionic, cho tốc độ xử l&yacute; cực mạnh, gi&uacute;p tất cả c&aacute;c thao t&aacute;c của bạn tr&ecirc;n m&aacute;y đều diễn ra một c&aacute;ch mượt m&agrave;. Khả năng xử l&yacute; đồ họa ho&agrave;n hảo, mang tới khả năng chỉnh sửa ảnh, video hay chơi game đỉnh cao.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-7.jpg\" onclick=\"return false;\"><img alt=\"Vi xử lý Apple A13 Bionic - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-7.jpg\" title=\"Vi xử lý Apple A13 Bionic - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang-ram-3gb\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng RAM 3 GB đang kinh doanh tại Thế Giới Di Động \">M&aacute;y t&iacute;nh bảng c&oacute;&nbsp;RAM 3 GB</a>&nbsp;cho khả năng xử l&yacute; đa nhiệm mượt m&agrave;, khi c&oacute; nhiều ứng dụng được chạy ngầm th&igrave; m&aacute;y vẫn vận h&agrave;nh v&ocirc; c&ugrave;ng trơn tru. Đi k&egrave;m 64 GB ROM mở rộng kh&ocirc;ng gian lưu trữ cho người d&ugrave;ng lưu lại những nội dung y&ecirc;u th&iacute;ch hay t&agrave;i liệu quan trọng. Ngo&agrave;i ROM th&igrave; bạn c&oacute; thể d&ugrave;ng iCloud để lưu trữ dữ liệu trong những trường hợp cần th&ecirc;m kh&ocirc;ng gian lưu trữ online.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-8.jpg\" onclick=\"return false;\"><img alt=\"Thoải mái lưu trữ - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-8.jpg\" title=\"Thoải mái lưu trữ - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>Hệ điều h&agrave;nh iPadOS 15 cho khả năng tương t&aacute;c, t&igrave;m kiếm v&agrave; sắp xếp th&ocirc;ng tin một c&aacute;ch hiệu quả nhất. Bạn c&oacute; thể dễ d&agrave;ng chia đ&ocirc;i m&agrave;n h&igrave;nh để sử dụng c&ugrave;ng l&uacute;c 2 ứng dụng tối ưu cho c&ocirc;ng việc nhất, n&oacute; c&ograve;n đưa c&aacute;c tiện &iacute;ch ra m&agrave;n h&igrave;nh ch&iacute;nh hay hiển thị ghi ch&uacute; ở bất cứ đ&acirc;u. C&oacute; đủ mọi ứng dụng bạn cần tr&ecirc;n phi&ecirc;n bản iPadOS n&agrave;y, từ chơi game giải tr&iacute; cho đến c&aacute;c phần mềm l&agrave;m việc chuy&ecirc;n dụng bạn cứ y&ecirc;n t&acirc;m.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-9.jpg\" onclick=\"return false;\"><img alt=\"Hệ điều hành iPadOS - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-9.jpg\" title=\"Hệ điều hành iPadOS - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>Hệ điều h&agrave;nh n&agrave;y c&ograve;n cung cấp c&aacute;c ứng dụng Dịch với chế độ tự động dịch, xem trực diện, t&iacute;nh năng Live Text c&oacute; khả năng nhận diện chữ trong h&igrave;nh ảnh, t&iacute;nh năng Focus sẽ tiến h&agrave;nh lọc th&ocirc;ng b&aacute;o dựa nội dung bạn đang l&agrave;m tr&ecirc;n m&aacute;y t&iacute;nh bảng,...</p>\r\n\r\n<p>Bạn c&oacute; thể kết hợp với những phụ kiện hữu &iacute;ch như b&uacute;t cảm ứng&nbsp;Apple Pencil&nbsp;v&agrave; b&agrave;n ph&iacute;m Smart Keyboard. Apple Pencil sẽ cho bạn trải nghiệm viết vẽ tự nhi&ecirc;n như đang thực hiện tr&ecirc;n giấy, để bạn nhanh ch&oacute;ng lưu lại khi bất ngờ c&oacute; &yacute; tưởng hay th&ocirc;ng tin cần ghi ch&uacute; v&agrave; c&aacute;c thao t&aacute;c chỉnh sửa cũng v&ocirc; c&ugrave;ng nhạy.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-10.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ kết nối với bàn phím ngoài - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-10.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-10.jpg\" title=\"Hỗ trợ kết nối với bàn phím ngoài - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>C&ograve;n Smart Keyboard ki&ecirc;m bao da bảo vệ l&agrave; phụ kiện ho&agrave;n hảo để bạn tiến h&agrave;nh nhập liệu, đ&aacute;nh m&aacute;y như tr&ecirc;n một chiếc laptop nhỏ gọn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-16.jpg\" onclick=\"return false;\"><img alt=\"Phụ kiện - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-16.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-16.jpg\" title=\"Phụ kiện - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>Lưu &yacute;: B&uacute;t Apple Pencil v&agrave; b&agrave;n ph&iacute;m Smart Keyboard l&agrave; phụ kiện b&aacute;n rời, kh&ocirc;ng đi k&egrave;m khi mua sản phẩm.</p>\r\n\r\n<p>Về khả năng kết nối iPad 9 hỗ trợ Wi-Fi 802.11 a/b/g/n/ac v&agrave; Bluetooth v4.2 cung cấp đường truyền internet ổn định cho bạn lướt web, chơi game mượt m&agrave;, kết nối c&ugrave;ng c&aacute;c thiết bị kh&aacute;c một c&aacute;ch nhanh ch&oacute;ng v&agrave; dễ d&agrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-11.jpg\" onclick=\"return false;\"><img alt=\"Kết nối nhanh chóng - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-11.jpg\" title=\"Kết nối nhanh chóng - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<h3>Chụp ảnh hay video call đều v&ocirc; c&ugrave;ng sắc n&eacute;t</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/may-tinh-bang/ipad-9-wifi-cellular-64gb\" target=\"_blank\" title=\"Tham khảo một số mẫu máy tính bảng  đang kinh doanh tại Thế Giới Di Động \">iPad 9 WiFi Cellular 64GB</a>&nbsp;trang bị một camera sau c&oacute; độ ph&acirc;n giải 8 MP c&ugrave;ng t&iacute;nh năng zoom kỹ thuật số gi&uacute;p k&eacute;o gần c&aacute;c đối tượng ở xa v&agrave;o khung h&igrave;nh của bạn một c&aacute;ch sắc n&eacute;t. Hỗ trợ chụp ảnh chuẩn HDR cho ra đời những bức ảnh si&ecirc;u ch&acirc;n thật v&agrave; th&iacute;ch mắt kết hợp c&ugrave;ng khả năng chụp to&agrave;n cảnh Panorama cho bức ảnh phong cảnh th&ecirc;m phần tuyệt hảo.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-12.jpg\" onclick=\"return false;\"><img alt=\"Camera sau tích hợp nhiều tính năng - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-12.jpg\" title=\"Camera sau tích hợp nhiều tính năng - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>Camera trước của m&aacute;y c&oacute; độ ph&acirc;n giải l&ecirc;n tới 12 MP v&agrave; g&oacute;c chụp si&ecirc;u rộng đem đến chất lượng h&igrave;nh ảnh r&otilde; n&eacute;t, khả năng chụp ảnh selfie ấn tượng ngay cả khi chụp bạn d&ugrave;ng n&oacute; để chụp nh&oacute;m bạn đ&ocirc;ng người. Camera trước c&oacute; độ ph&acirc;n giải cao c&ograve;n rất hữu &iacute;ch khi học tập hay họp online bởi n&oacute; sẽ h&igrave;nh ảnh của bạn th&ecirc;m phần lung linh gi&uacute;p bạn tự tin hơn, nội dung hiển thị sắc n&eacute;t dễ d&agrave;ng trao đổi.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-13.jpg\" onclick=\"return false;\"><img alt=\"Camera trước 12 MP - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-13.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-13.jpg\" title=\"Camera trước 12 MP - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>Hơn nữa, trong những cuộc gọi video, chế độ Center Stage c&ograve;n c&oacute; thể tự động điều chỉnh, thay đổi g&oacute;c quay, zoom theo từng chuyển động để bạn lu&ocirc;n đứng ở trung t&acirc;m khung h&igrave;nh. Trường hợp c&oacute; đối tượng kh&aacute;c tham gia v&agrave;o khung h&igrave;nh th&igrave; n&oacute; cũng sẽ tự điều chỉnh để c&acirc;n bằng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-14.jpg\" onclick=\"return false;\"><img alt=\"Chế độ Center Stage - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-14.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-14.jpg\" title=\"Chế độ Center Stage - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>Chiếc&nbsp;iPad&nbsp;n&agrave;y c&ograve;n hỗ trợ quay video Full HD tạo n&ecirc;n những thước phim v&ocirc; c&ugrave;ng sống động. B&ecirc;n cạnh đ&oacute; m&aacute;y c&ograve;n hỗ trợ chế độ quay chậm Slow Motion v&agrave; quay nhanh Time‑lapse cho chiếc video của bạn th&ecirc;m phần đặc sắc</p>\r\n\r\n<h3>Sử dụng thoải m&aacute;i suốt cả ng&agrave;y</h3>\r\n\r\n<p>iPad 9 cho dung lượng pin l&agrave; 32.4 Wh (~8600 mAh) với c&aacute;c t&aacute;c vụ th&ocirc;ng thường th&igrave; n&oacute; c&oacute; thể k&eacute;o d&agrave;i đến 10 giờ sử dụng, sẵn s&agrave;ng đ&aacute;p ứng mọi nhu cầu hoạt động giải tr&iacute;, l&agrave;m việc của bạn trong cả ng&agrave;y d&agrave;i.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-15.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng pin - iPad 9 WiFi Cellular 64GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-15.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/522/250731/ipad-9-wifi-cellular-64gb-15.jpg\" title=\"Dung lượng pin - iPad 9 WiFi Cellular 64GB\" /></a></p>\r\n\r\n<p>Với sự gi&uacute;p sức của c&ocirc;ng nghệ&nbsp;sạc nhanh&nbsp;20 W th&igrave; vi&ecirc;n pin lớn của iPad 9 sẽ dễ d&agrave;ng được sạc đầy trong khoảng thời gian ngắn, gi&uacute;p bạn nhanh ch&oacute;ng quay trở lại với c&ocirc;ng việc v&agrave; hạn chế gi&aacute;n đoạn khi giải tr&iacute;.</p>\r\n\r\n<p>iPad 9 WiFi Cellular 64GB sẽ l&agrave; một chiếc m&aacute;y t&iacute;nh bảng mạnh mẽ, thiết kế sang trọng c&ugrave;ng nhiều t&iacute;nh năng tiện dụng hỗ trợ bạn trong nhiều hoạt động. Bạn c&oacute; thể d&ugrave;ng n&oacute; để học online, xem phim, thiết kế đồ họa,...&nbsp;</p>\r\n', 'Đã đăng', 2, '08-09-2023', 0),
(48, 'VHL#201', 'MacBook Air 13 inch M1 2020', 18490000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'macbook1.jpg', '<h3>Laptop Apple MacBook Air M1 2020 thuộc d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=cao-cap-sang-trong\" target=\"_blank\" title=\"Xem thêm các mẫu laptop cao cấp tại Thế Giới Di Động\">laptop cao cấp sang trọng</a>&nbsp;c&oacute; cấu h&igrave;nh mạnh mẽ, chinh phục được c&aacute;c t&iacute;nh năng văn ph&ograve;ng lẫn đồ hoạ m&agrave; bạn mong muốn, thời lượng pin d&agrave;i, thiết kế mỏng nhẹ sẽ đ&aacute;p ứng tốt c&aacute;c nhu cầu l&agrave;m việc của bạn.</h3>\r\n\r\n<h3>Chip Apple M1 tốc độ xử l&yacute; nhanh gấp 3.5 lần thế hệ trước</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.topzone.vn/mac\" target=\"_blank\" title=\"Tham khảo các sản phẩm MacBook đang kinh doanh tại TopZone - thành viên của Thế Giới Di Động\">MacBook</a>&nbsp;n&agrave;y được trang bị con chip&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/apple-m1-la-gi-1305904\" target=\"_blank\" title=\"Tìm hiểu thêm về chip Apple M1\">Apple M1&nbsp;</a>được sản xuất độc quyền bởi Nh&agrave; T&aacute;o&nbsp;tr&ecirc;n tiến tr&igrave;nh&nbsp;<strong>5 nm</strong>,&nbsp;<strong>8 l&otilde;i</strong>&nbsp;bao gồm&nbsp;<strong>4 l&otilde;i</strong>&nbsp;tiết kiệm điện v&agrave;&nbsp;<strong>4 l&otilde;i</strong>&nbsp;hiệu suất cao, mang đến một hiệu năng kinh ngạc, xử l&yacute; mọi t&aacute;c vụ văn ph&ograve;ng một c&aacute;ch mượt m&agrave; như Word, Excel, Powerpoint,... thực hiện tốt c&aacute;c nhiệm vụ chỉnh sửa h&igrave;nh ảnh, kết xuất 2D tr&ecirc;n c&aacute;c phần mềm Photoshop, AI,... m&aacute;y c&ograve;n hỗ trợ tiết kiệm được điện năng cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125154.jpg\" onclick=\"return false;\"><img alt=\"Apple MacBook Air M1 2020 - Làm việc đa nhiệm\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125154.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125154.jpg\" title=\"Apple MacBook Air M1 2020 - Làm việc đa nhiệm\" /></a></p>\r\n\r\n<p>Đi c&ugrave;ng CPU l&agrave; card đồ họa t&iacute;ch hợp&nbsp;<strong>7 nh&acirc;n GPU</strong>&nbsp;c&oacute; hiệu năng vượt trội sau nhiều b&agrave;i thử nghiệm hiệu năng đồ họa của benchmark, bạn c&oacute; thể sử dụng nhiều phần mềm đồ họa chuy&ecirc;n nghiệp, thoả sức s&aacute;ng tạo nội dung, render video ổn định với chất lượng h&igrave;nh ảnh cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125156.jpg\" onclick=\"return false;\"><img alt=\"Apple MacBook Air M1 2020 - Render video\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125156.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125156.jpg\" title=\"Apple MacBook Air M1 2020 - Render video\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-apple-macbook-air\" target=\"_blank\" title=\"Xem thêm các mẫu MacBook Air hiện đang kinh doanh tại thegioididong.com\">MacBook Air</a>&nbsp;được trang bị&nbsp;<strong>RAM 8 GB</strong>&nbsp;cho khả năng đa nhiệm cao, bạn c&oacute; thể mở c&ugrave;ng l&uacute;c nhiều cửa sổ hoặc ứng dụng để phục vụ cho c&ocirc;ng việc, giải tr&iacute; của m&igrave;nh v&iacute; dụ như vừa mở Chrome tra cứu th&ocirc;ng tin vừa mở Word để l&agrave;m việc cực kỳ tiện lợi m&agrave; kh&ocirc;ng cần lo lắng l&agrave; m&aacute;y sẽ bị đơ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Xem thêm một số mẫu Macbook đang kinh doanh tại Thế Giới Di Động\">MacBook&nbsp;</a>sở hữu ổ cứng&nbsp;<strong>SSD 256 GB</strong>&nbsp;cho tốc độ xử l&yacute; nhanh ch&oacute;ng, thao t&aacute;c cuộn trong Safari cực mượt, đ&aacute;nh thức m&aacute;y đang trong chế độ ngủ chỉ mất v&agrave;i gi&acirc;y v&agrave; đem đến kh&ocirc;ng gian lưu trữ rộng r&atilde;i bạn cứ thoải m&aacute;i lưu lại những bộ phim hay m&agrave; kh&ocirc;ng lo n&oacute; sẽ chiếm chỗ của c&aacute;c tệp t&agrave;i liệu c&ocirc;ng việc.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125153.jpg\" onclick=\"return false;\"><img alt=\"Apple MacBook Air M1 2020 - RAM \" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125153.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125153.jpg\" title=\"Apple MacBook Air M1 2020 - RAM \" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh sử dụng con chip mới Apple c&ograve;n ra mắt phi&ecirc;n bản hệ điều h&agrave;nh&nbsp;<strong>macOS Big Sur</strong>&nbsp;với giao diện ho&agrave;n hảo hơn, c&aacute;c chuyển động cuộn, ph&oacute;ng to v&agrave; chuyển tiếp m&agrave;n h&igrave;nh mượt m&agrave;. Ngo&agrave;i đổi mới giao diện&nbsp;<strong>macOS Big Sur</strong>&nbsp;c&ograve;n mang đến khả năng phản hồi nhanh ch&oacute;ng v&agrave; kho ứng dụng khổng lồ.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/game-app/danh-sach-ung-dung-chay-duoc-tren-macbook-chip-apple-m1-1310311\" target=\"_blank\" title=\"Danh sách ứng dụng chạy được trên Macbook chip Apple M1\">Danh s&aacute;ch ứng dụng chạy được tr&ecirc;n Macbook chip Apple M1</a>.</p>\r\n\r\n<h3>Thiết kế sang trọng, tinh tế</h3>\r\n\r\n<p><a href=\"https://www.topzone.vn/mac/apple-macbook-air-2020-mgn63saa\" target=\"_blank\" title=\"MacBook Air 13 inch M1 2020 8GB/256GB hiện đang bán tại Topzone.vn\">MacBook Air&nbsp;M1 256GB 2020</a>&nbsp;kh&ocirc;ng những thừa hưởng thiết kế mỏng nhẹ, hiện đại của c&aacute;c thế hệ trước m&agrave; c&ograve;n học hỏi th&ecirc;m phong c&aacute;ch hiện đại của c&aacute;c d&ograve;ng Macbook Pro. Khối lượng m&aacute;y chỉ vỏn vẹn&nbsp;<strong>1.29 kg</strong>&nbsp;v&agrave; mỏng của th&acirc;n m&aacute;y l&agrave;&nbsp;<strong>16.1 mm</strong>&nbsp;c&oacute; t&iacute;nh cơ động cao, dễ đem theo xử l&yacute; c&ocirc;ng việc.</p>\r\n\r\n<p>Vỏ m&aacute;y được thiết kế nguy&ecirc;n khối từ nguy&ecirc;n liệu nh&ocirc;m t&aacute;i chế 100% vừa g&oacute;p phần bảo vệ m&ocirc;i trường vừa cho độ bền cao, chịu va đập tốt bảo vệ c&aacute;c linh kiện b&ecirc;n trong an to&agrave;n nếu c&oacute; xảy ra va chạm mạnh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125152.jpg\" onclick=\"return false;\"><img alt=\"Apple MacBook Air M1 2020 - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125152.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125152.jpg\" title=\"Apple MacBook Air M1 2020 - Thiết kế\" /></a></p>\r\n\r\n<p><strong>Magic Keyboard</strong>&nbsp;c&oacute; h&agrave;nh tr&igrave;nh ph&iacute;m&nbsp;<strong>1 mm</strong>&nbsp;mang lại trải nghiệm g&otilde; nhạy, thoải m&aacute;i v&agrave; y&ecirc;n tĩnh, b&agrave;n ph&iacute;m c&ograve;n được trang bị th&ecirc;m&nbsp;<a href=\"https://www.thegioididong.com/laptop-den-ban-phim\" target=\"_blank\" title=\"Laptop, máy tính xách tay trang bị đèn bàn phím đang kinh doanh tại Thegioididong.com\">đ&egrave;n nền</a>&nbsp;vừa tạo cảm gi&aacute;c sang trọng vừa hỗ trợ bạn g&otilde; ph&iacute;m ch&iacute;nh x&aacute;c hơn trong m&ocirc;i trường thiếu s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm một số mẫu laptop đang kinh doanh tại Thế Giới Di Động\">Laptop</a>&nbsp;được trang bị 2 cổng kết nối&nbsp;<strong>Thunderbolt 3&nbsp;</strong>với tốc độ truyền tải l&ecirc;n đến&nbsp;<strong>40 Gb/s</strong>&nbsp;cho tốc độ truyền tải nhanh gấp 2 lần so với thế hệ Thunderbolt 2 trước đ&acirc;y. B&ecirc;n cạnh Thunderbolt 3 m&aacute;y c&ograve;n được trang bị th&ecirc;m kết nối kh&ocirc;ng d&acirc;y l&agrave;&nbsp;<strong>Wi-Fi 6&nbsp;</strong>cho tốc độ truyền tải dữ liệu nhanh ch&oacute;ng l&ecirc;n đến&nbsp;<strong>10.000 Mbps</strong>&nbsp;cao hơn Wi-Fi 5 chỉ c&oacute;&nbsp;<strong>7.000 Mbps</strong>&nbsp;v&agrave;&nbsp;<strong>Bluetooth v5.0&nbsp;</strong>c&oacute; tốc độ tối đa l&agrave;&nbsp;<strong>50 Mbps</strong>&nbsp;cho bạn trải nghiệm mượt m&agrave; hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125159.jpg\" onclick=\"return false;\"><img alt=\"Apple MacBook Air M1 2020 - Kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125159.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125159.jpg\" title=\"Apple MacBook Air M1 2020 - Kết nối\" /></a></p>\r\n\r\n<p>Apple trang bị cho chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-m1\" target=\"_blank\" title=\"Xem thêm Macbook M1 tại Thế Giới Di Động\">Macbook M1</a>&nbsp;n&agrave;y&nbsp;<strong>bảo mật&nbsp;</strong><b><strong>v&acirc;n tay</strong>&nbsp;</b>gi&uacute;p bạn mở kh&oacute;a nhanh ch&oacute;ng chỉ với một c&aacute;i chạm nhẹ. B&ecirc;n cạnh đ&oacute;, v&acirc;n tay của bạn sẽ được t&iacute;ch hợp v&agrave;o việc x&aacute;c nhận th&ocirc;ng tin khi mua sắm online hay truy cập v&agrave;o c&aacute;c t&agrave;i liệu c&oacute; bảo mật m&agrave; kh&ocirc;ng cần phải nhập mật khẩu cực kỳ tiện lợi.</p>\r\n\r\n<h3>M&agrave;u sắc ch&acirc;n thực, h&igrave;nh ảnh sắc n&eacute;t&nbsp;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-retina\" target=\"_blank\" title=\"Các sản phẩm laptop sở hữu màn hình Retina hiện đang bán tại thegioididong.com \">Laptop m&agrave;n h&igrave;nh Retina</a><strong>&nbsp;</strong><strong>13.3 inch&nbsp;</strong>cao cấp cho h&igrave;nh ảnh sắc n&eacute;t m&agrave;u sắc tự nhi&ecirc;n. Kết hợp c&ugrave;ng c&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-cong-nghe-man-hinh-true-tone-992705\" target=\"_blank\" title=\"Tìm hiểu công nghệ màn hình True Tone\">True Tone</a><strong>&nbsp;</strong>gi&uacute;p điều chỉnh c&acirc;n bằng sắc trắng cũng như độ s&aacute;ng để m&agrave;u sắc h&igrave;nh ảnh hiển thị ch&iacute;nh x&aacute;c nhất.</p>\r\n\r\n<p>Ngo&agrave;i ra, g&oacute;c nh&igrave;n của m&aacute;y cũng được mở rộng hơn nhờ c&oacute; c&ocirc;ng nghệ&nbsp;<strong>IPS</strong>&nbsp;đem lại trải nghiệm nh&igrave;n đ&atilde; mắt, d&ugrave; bạn nh&igrave;n m&agrave;n h&igrave;nh bằng g&oacute;c nghi&ecirc;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125150.jpg\" onclick=\"return false;\"><img alt=\"Apple MacBook Air M1 2020 - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125150.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/231244/apple-macbook-air-2020-mgn63saa-280323-125150.jpg\" title=\"Apple MacBook Air M1 2020 - Màn hình\" /></a></p>\r\n\r\n<p><a href=\"https://www.topzone.vn/macbook-air\" target=\"_blank\" title=\"Xem thêm các sản phẩm MacBook Air M1 chính hãng Apple đang kinh doanh tại TopZone\">MacBook Air M1</a>&nbsp;được trang bị camera&nbsp;<strong>FaceTime HD</strong>&nbsp;gi&uacute;p bạn gọi video cho người th&acirc;n, bạn b&egrave; với chất lượng hiển thị cao. T&iacute;ch hợp d&atilde;y&nbsp;<strong>3 micro</strong>&nbsp;gi&uacute;p truyền tải giọng n&oacute;i của bạn ch&iacute;nh x&aacute;c hơn.&nbsp;</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop/apple-macbook-air-2020-mgn63saa\" target=\"_blank\" title=\"Laptop Apple MacBook Air M1 2020 hiện đang bán tại thegioididong.com\">Laptop Apple MacBook Air M1 2020</a>&nbsp;với hiệu năng mạnh mẽ đ&aacute;p ứng tốt mọi c&ocirc;ng việc, thiết kế cực mỏng sẽ l&agrave; sự lựa chọn tuyệt vời d&agrave;nh cho bạn.</p>\r\n', 'Đã đăng', 3, '08-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(49, 'VHL#202', 'MacBook Air 13 inch M2 2022', 25990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'macbook2.jpg', '<h3>Sau 14 năm, ba lần sửa đổi v&agrave; hai kiến tr&uacute;c bộ vi xử l&yacute; kh&aacute;c nhau, kiểu d&aacute;ng mỏng dần mang t&iacute;nh biểu tượng của MacBook Air đ&atilde; đi v&agrave;o lịch sử. Thay v&agrave;o đ&oacute; l&agrave; một chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop/apple-macbook-air-m2-2022\" target=\"_blank\" title=\"Laptop Apple MacBook Air M2 2022 đang bán tại thegioididong.com\">MacBook Air M2</a>&nbsp;với thiết kế ho&agrave;n to&agrave;n mới, độ d&agrave;y kh&ocirc;ng thay đổi tương tự như MacBook Pro, đ&aacute;nh bật mọi r&agrave;o cản với con chip Apple M2 đầy mạnh mẽ.</h3>\r\n\r\n<h3>Lột x&aacute;c ngoạn mục với thiết kế thời thượng, sang trọng&nbsp;</h3>\r\n\r\n<p>Sự đẳng cấp đến từ ngoại h&igrave;nh của chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-air\" target=\"_blank\" title=\"Xem thêm các mẫu MacBook Air hiện đang kinh doanh tại thegioididong.com\">MacBook Air</a>&nbsp;nh&agrave; Apple l&agrave; điều kh&ocirc;ng thể phủ nhận v&agrave; kh&oacute; c&oacute; d&ograve;ng sản phẩm c&ugrave;ng ph&acirc;n kh&uacute;c n&agrave;o c&oacute; thể qua mặt được. Vẫn l&agrave; lớp&nbsp;<strong>vỏ kim loại nguy&ecirc;n</strong>&nbsp;<strong>khối</strong>&nbsp;cứng c&aacute;p c&ugrave;ng c&aacute;c cạnh g&oacute;c vu&ocirc;ng vức, sang trọng nhưng chiếc MacBook Air 2022 năm nay đ&atilde; tho&aacute;t ra khỏi ng&ocirc;n ngữ thiết kế nhẹ nh&agrave;ng vốn c&oacute; từ l&acirc;u v&agrave; kho&aacute;c l&ecirc;n diện mạo mới tương tự như &ldquo;đ&agrave;n anh&rdquo; MacBook Pro.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112213.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Thiết kế thời thượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112213.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112213.jpg\" title=\"MacBook Air M2 - Thiết kế thời thượng\" /></a></p>\r\n\r\n<p>Bề d&agrave;y&nbsp;<strong>11.3 mm</strong>&nbsp;c&ugrave;ng c&acirc;n nặng ấn tượng&nbsp;<strong>1.24 kg</strong>&nbsp;sẽ l&agrave; một điểm cộng ho&agrave;n hảo cho khả năng di động, lu&ocirc;n sẵn s&agrave;ng đồng h&agrave;nh c&ugrave;ng một người d&ugrave;ng l&agrave; sinh vi&ecirc;n, d&acirc;n văn ph&ograve;ng như m&igrave;nh đến trường học, c&ocirc;ng ty hay cũng thoải m&aacute;i cho người d&ugrave;ng s&aacute;ng tạo v&agrave; đặc biệt l&agrave; những chuyến c&ocirc;ng t&aacute;c xa của doanh nh&acirc;n.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112217.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Thiết kế sang trọng\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112217.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112217.jpg\" title=\"MacBook Air M2 - Thiết kế sang trọng\" /></a></p>\r\n\r\n<p>Mặc d&ugrave; c&oacute; ngoại h&igrave;nh mỏng v&agrave; khối lượng nhẹ hơn song MacBook Air mới vẫn kh&ocirc;ng k&eacute;m phần chắc chắn, độ ho&agrave;n thiện tốt so với trước đ&acirc;y. Khung m&aacute;y cứng, nắp m&aacute;y gần như kh&ocirc;ng uốn cong khi m&igrave;nh t&aacute;c động lực v&agrave; vẫn c&oacute; thể mở l&ecirc;n bằng một ng&oacute;n tay. Apple lu&ocirc;n đứng đầu khi n&oacute;i đến chất lượng sản xuất v&agrave; chiếc Air mới cũng kh&ocirc;ng ngoại lệ.</p>\r\n\r\n<p>C&aacute; nh&acirc;n m&igrave;nh rất th&iacute;ch m&agrave;u&nbsp;Midnight với lớp ho&agrave;n thiện m&agrave;u xanh đen đậm c&oacute; thể thay đổi t&ugrave;y theo &aacute;nh s&aacute;ng nhưng lại c&oacute; điểm trừ khi dễ b&aacute;m dấu v&acirc;n tay, l&agrave;m mất đi ngoại h&igrave;nh sang chảnh vốn c&oacute; của một chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=cao-cap-sang-trong\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop cao cấp - sang trọng hiện đang bán tại thegioididong.com\">laptop cao cấp - sang trọng</a>, bạn c&oacute; thể chọn mua m&agrave;u x&aacute;m, bạc hoặc v&agrave;ng sẽ kh&ocirc;ng dễ bị b&aacute;m dấu v&acirc;n tay hoặc chịu&nbsp;kh&oacute; lau ch&ugrave;i thường xuy&ecirc;n để giữ cho m&aacute;y lu&ocirc;n s&aacute;ng b&oacute;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112207.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Midnight\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112207.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112207.jpg\" title=\"MacBook Air M2 - Midnight\" /></a></p>\r\n\r\n<p>Một sự thay đổi r&otilde; rệt tr&ecirc;n chiếc b&agrave;n ph&iacute;m&nbsp;<strong>Magic Key</strong>&nbsp;của d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop Apple hiện đang kinh doanh tại thegioididong.com\">laptop Apple</a>&nbsp;M2 năm nay ch&iacute;nh l&agrave; k&iacute;ch thước của h&agrave;ng ph&iacute;m chức năng F được gia tăng bằng với c&aacute;c d&ograve;ng ph&iacute;m kh&aacute;c, cho m&igrave;nh thao t&aacute;c sử dụng chuẩn x&aacute;c, kh&ocirc;ng bị nhầm lẫn. Hơn nữa trải nghiệm g&otilde; ph&iacute;m cũng rất nhẹ nh&agrave;ng, &ecirc;m tay, tiếng ấn cũng nhẹ nh&agrave;ng hơn so với b&agrave;n ph&iacute;m c&aacute;nh bướm cũ. Kết hợp c&ugrave;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop-den-ban-phim\" target=\"_blank\" title=\"Laptop, máy tính xách tay trang bị đèn bàn phím đang kinh doanh tại Thegioididong.com\">đ&egrave;n nền</a>&nbsp;hỗ trợ m&igrave;nh l&agrave;m việc dễ d&agrave;ng hơn trong nhiều điều kiện &aacute;nh s&aacute;ng tối kh&aacute;c nhau.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112231.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Bàn phím\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112231.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112231.jpg\" title=\"MacBook Air M2 - Bàn phím\" /></a></p>\r\n\r\n<p>T&iacute;nh năng&nbsp;<strong>Touch ID</strong>&nbsp;(cảm biến v&acirc;n tay)&nbsp;t&iacute;ch hợp n&uacute;t nguồn thay cho mật khẩu cho ph&eacute;p m&igrave;nh mở kh&oacute;a m&aacute;y, truy cập c&aacute;c ứng dụng v&agrave; thanh to&aacute;n ứng dụng một c&aacute;ch nhanh ch&oacute;ng, kh&ocirc;ng cần tốn nhiều thời gian hay phải nhớ mật khẩu như c&aacute;c kiểu bảo mật truyền thống.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112234.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Touch ID\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112234.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112234.jpg\" title=\"MacBook Air M2 - Touch ID\" /></a></p>\r\n\r\n<p>B&agrave;n di chuột kh&ocirc;ng c&oacute; kh&aacute;c nhiều so với c&aacute;c mẫu cũ mặc d&ugrave; Apple đ&atilde; l&agrave;m rộng hơn một ch&uacute;t, hoạt động giống nhau về mặt chức năng với khả năng di vuốt tuyệt vời, hỗ trợ cử chỉ v&agrave; từ chối lệnh khi m&igrave;nh lỡ đặt l&ograve;ng b&agrave;n tay l&ecirc;n.&nbsp;Một trong những thay đổi th&uacute; vị kh&aacute;c tr&ecirc;n chiếc Air mới ch&iacute;nh l&agrave; lưới loa đ&atilde; được giấu đi, nằm ở bản lề giữa b&agrave;n ph&iacute;m v&agrave; m&agrave;n h&igrave;nh để giữ cho ngoại h&igrave;nh gọn g&agrave;ng hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112209.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - TouchPad\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112209.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112209.jpg\" title=\"MacBook Air M2 - TouchPad\" /></a></p>\r\n\r\n<p>MacBook Air M2 2022 sở hữu&nbsp;<strong>đầu sạc MagSafe</strong>&nbsp;tương tự như MacBook Pro 14 v&agrave; 16, cho ph&eacute;p m&igrave;nh sạc m&agrave; kh&ocirc;ng lo vấp phải d&acirc;y c&aacute;p khiến m&aacute;y t&iacute;nh rơi xuống s&agrave;n.&nbsp;Nhờ c&oacute; cổng MagSafe m&agrave; m&igrave;nh thực sự c&oacute; th&ecirc;m một cổng USB-C so với c&aacute;c kiểu m&aacute;y cũ. Thay v&igrave; phải sử dụng một cổng để sạc m&aacute;y t&iacute;nh v&agrave; cổng c&ograve;n lại cho thiết bị ngoại vi, giờ đ&acirc;y m&igrave;nh c&oacute; thể sử dụng cả hai cổng hỗ trợ Thunderbolt cho c&aacute;c phụ kiện.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112245.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Cổng MagSafe\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112245.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112245.jpg\" title=\"MacBook Air M2 - Cổng MagSafe\" /></a></p>\r\n\r\n<h3>Ph&aacute; vỡ mọi giới hạn nhờ sức mạnh từ bộ vi xử l&yacute; Apple M2</h3>\r\n\r\n<p>Bộ vi xử l&yacute;<a href=\"https://www.thegioididong.com/tin-tuc/chip-apple-m2-chinh-thuc-ra-mat-voi-suc-manh-khung-1437839\" target=\"_blank\" title=\"Tìm hiểu thêm về chip Apple M2 \">&nbsp;Apple M2</a>&nbsp;tuy c&oacute; mặt cấu tạo tương đồng với M1 nhưng hiệu suất hoạt động cao hơn&nbsp;<strong>18%</strong>&nbsp;v&agrave; nhanh hơn&nbsp;<strong>1.9 lần</strong>&nbsp;so với c&aacute;c d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop | Máy tính xách tay đang kinh doanh tại Thegioididong.com\">laptop</a>&nbsp;sở hữu bộ vi xử l&yacute; 10 l&otilde;i kh&aacute;c, dư sức c&acirc;n mọi t&aacute;c vụ từ học tập, l&agrave;m việc đến đồ họa đến kỹ thuật chuy&ecirc;n s&acirc;u. Đồ họa&nbsp;<strong>GPU 8 nh&acirc;n</strong>&nbsp;cho khả năng hoạt động mạnh mẽ hơn&nbsp;<strong>35%</strong>, xử l&yacute; l&ecirc;n đến&nbsp;<strong>15.8 ngh&igrave;n tỷ</strong>&nbsp;t&aacute;c vụ trong&nbsp;<strong>1 gi&acirc;y</strong>&nbsp;n&ecirc;n c&aacute;c c&ocirc;ng việc li&ecirc;n quan đến h&igrave;nh ảnh, đồ họa, video,... đều được giải quyết rất mượt m&agrave;.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112236.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Hiệu năng\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112236.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112236.jpg\" title=\"MacBook Air M2 - Hiệu năng\" /></a></p>\r\n\r\n<p>M&aacute;y đ&aacute;p ứng ho&agrave;n hảo việc chỉnh sửa video v&agrave; ảnh nhẹ, đặc biệt nếu bạn sử dụng ứng dụng Ảnh hoặc iMovie của Apple cho c&aacute;c t&aacute;c vụ đ&oacute;.&nbsp;<a href=\"https://www.thegioididong.com/laptop-8-gb\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop RAM 8 GB hiện đang kinh doanh tại thegioididong.com\">Laptop RAM 8 GB</a>&nbsp;cho ph&eacute;p m&igrave;nh sử dụng c&ugrave;ng l&uacute;c nhiều cửa sổ ứng dụng kh&aacute;c nhau với độ đa nhiệm mượt m&agrave; đ&aacute;ng kể, vừa l&agrave;m việc tr&ecirc;n Photoshop vừa nghe nhạc vẫn rất trơn tru.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112222.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Đa nhiệm\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112222.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112222.jpg\" title=\"MacBook Air M2 - Đa nhiệm\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng gian ổ cứng&nbsp;<strong>SSD 256 GB</strong>&nbsp;vừa đủ cho c&aacute;c t&agrave;i liệu, ứng dụng học tập nhưng do chỉ được trang bị một chip NAND duy nhất thay v&igrave; hai chip như tr&ecirc;n c&aacute;c mẫu M1 hoặc M2 mới c&oacute; ổ cứng 512 GB trở l&ecirc;n n&ecirc;n tốc độ đọc ghi đ&atilde; bị giới hạn hơn đ&aacute;ng kể. M&igrave;nh đ&atilde; thử đo tốc độ đọc bằng c&ocirc;ng cụ Black Magic Speed Test được 1543 MB/s, thấp hơn gần một nửa so với con số 2982 MB/s của MacBook Air M1 256 GB.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112239.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Tốc độ SSD\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112239.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112239.jpg\" title=\"MacBook Air M2 - Tốc độ SSD\" /></a></p>\r\n\r\n<p>M&igrave;nh đ&atilde; kiểm chứng nhiệt độ của m&aacute;y bằng c&aacute;ch chạy c&aacute;c t&aacute;c vụ nặng c&ugrave;ng l&uacute;c như render video, mở nhiều tab Safari v&agrave; chạy th&ecirc;m Photoshop th&igrave; sau thời gian d&agrave;i m&aacute;y đ&atilde; n&oacute;ng l&ecirc;n r&otilde; rệt. Theo m&igrave;nh l&yacute; do một phần đến từ ngoại h&igrave;nh mỏng hơn, th&ecirc;m v&agrave;o đ&oacute; l&agrave; tản nhiệt kh&ocirc;ng c&oacute; quạt n&ecirc;n m&aacute;y n&oacute;ng l&ecirc;n nhanh cũng l&agrave; điều dễ hiểu. Tr&ecirc;n thực tế cũng c&oacute; &iacute;t người sử dụng MacBook Air cho c&aacute;c t&aacute;c vụ nặng như tr&ecirc;n.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112241.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Nhiệt độ\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112241.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112241.jpg\" title=\"MacBook Air M2 - Nhiệt độ\" /></a></p>\r\n\r\n<p>Sở hữu pin&nbsp;<strong>52.6 Wh</strong>&nbsp;nhưng do hiệu năng v&agrave; nhiệt độ cao n&ecirc;n M2 Air c&oacute; thời lượng pin tương tự như mẫu M1, cho ph&eacute;p m&igrave;nh sử dụng từ&nbsp;<strong>8 đến 10 giờ</strong>&nbsp;ở mức độ s&aacute;ng 200 nits v&agrave; nhiều t&aacute;c vụ l&agrave;m việc văn ph&ograve;ng kh&aacute;c nhau. Vẫn thoải m&aacute;i để m&igrave;nh c&oacute; thể x&aacute;ch m&aacute;y ra qu&aacute;n c&agrave; ph&ecirc; l&agrave;m việc trong suốt cả ng&agrave;y d&agrave;i.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112226.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Pin\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112226.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112226.jpg\" title=\"MacBook Air M2 - Pin\" /></a></p>\r\n\r\n<h3>Trải nghiệm h&igrave;nh ảnh, &acirc;m thanh tr&ecirc;n cả tuyệt vời&nbsp;</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh của chiếc Air lớn hơn một ch&uacute;t so với trước đ&acirc;y,&nbsp;<strong>13.6 inch</strong>&nbsp;thay v&igrave; 13.3 inch nhưng phần lớn&nbsp;k&iacute;ch thước được gia tăng theo chiều dọc. C&aacute;c viền xung quanh m&agrave;n h&igrave;nh đ&atilde; được l&agrave;m mỏng hơn v&agrave; c&aacute;c g&oacute;c cũng được bo tr&ograve;n, mang đến vẻ ngo&agrave;i hiện đại hơn trước.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh c&oacute; độ s&aacute;ng cao&nbsp;<strong>500 nits</strong>&nbsp;khi sử dụng trong nh&agrave; m&igrave;nh thực sự kh&ocirc;ng nhận ra sự kh&aacute;c biệt nhưng khi x&aacute;ch m&aacute;y ra qu&aacute;n c&agrave; ph&ecirc; mới thấy r&otilde; được sự kh&aacute;c biệt, nội dung lu&ocirc;n được hiển thị với độ sắc n&eacute;t cao m&agrave; kh&ocirc;ng hề bị hiện tượng hắt s&aacute;ng g&acirc;y kh&oacute; chịu.&nbsp;C&ocirc;ng nghệ&nbsp;<strong>True Tone</strong>&nbsp;sẽ tự động điều chỉnh mức độ &aacute;nh s&aacute;ng ph&ugrave; hợp với m&ocirc;i trường, gi&uacute;p cho nội dung thể hiện tự nhi&ecirc;n hơn v&agrave; giảm thiểu hiện tượng mỏi mắt.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112219.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112219.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112219.jpg\" title=\"MacBook Air M2 - Màn hình\" /></a></p>\r\n\r\n<p>Độ ph&acirc;n giải&nbsp;<strong>Liquid Retina (2560 x 1664)</strong>&nbsp;kết hợp c&ugrave;ng&nbsp;<strong>dải m&agrave;u rộng P3</strong>&nbsp;v&agrave; khả năng hiển thị&nbsp;<strong>1 tỷ m&agrave;u</strong>&nbsp;tạo n&ecirc;n một kh&ocirc;ng gian ho&agrave;n chỉnh cho mọi trải nghiệm h&igrave;nh ảnh với m&agrave;u sắc sinh động v&agrave; chuẩn x&aacute;c tr&ecirc;n từng vật thể nhỏ nhất. Ngo&agrave;i ra cũng đ&aacute;p ứng được nhu cầu l&agrave;m việc với đồ họa, h&igrave;nh ảnh của m&igrave;nh tr&ecirc;n c&aacute;c ứng dụng nh&agrave; Adobe như Photoshop, Lightroom,...</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112221.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112221.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112221.jpg\" title=\"MacBook Air M2 - Màn hình\" /></a></p>\r\n\r\n<p>Notch m&agrave;n h&igrave;nh tr&ecirc;n chiếc MacBook Air M2 tạo điểm mới mẻ v&agrave; cũng l&agrave; điểm nhận dạng thế hệ&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Máy tính xách tay, laptop Macbook chính hãng đang kinh doanh tại Thegioididong.com\">MacBook</a>&nbsp;mới nhưng lại kh&ocirc;ng cho m&igrave;nh trải nghiệm sử dụng tốt, phần notch kh&ocirc;ng xấu hay g&acirc;y mất tập trung m&agrave; chưa được t&ugrave;y biến tốt, sẽ bị ẩn đi khi m&igrave;nh mở những ứng dụng l&agrave;m việc v&agrave; hiển thị trở lại khi sử dụng ở dạng cửa sổ.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112224.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Notch\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112224.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112224.jpg\" title=\"MacBook Air M2 - Notch\" /></a></p>\r\n\r\n<p>B&ugrave; lại&nbsp;<strong>Camera Facetime HD 1080p</strong>&nbsp;b&ecirc;n trong chiếc notch c&oacute; nhiều cải tiến cho h&igrave;nh ảnh sắc n&eacute;t hơn, m&agrave;u sắc v&agrave; độ tương phản cũng tốt hơn. Kết hợp với dải ba micr&ocirc; gi&uacute;p cho qu&aacute; tr&igrave;nh học tập, l&agrave;m việc trực tuyến hay gọi video của m&igrave;nh diễn ra su&ocirc;n.</p>\r\n\r\n<p>Hệ thống bốn loa mới bao gồm&nbsp;<strong>hai loa tweeter</strong>,&nbsp;<strong>hai loa trầm</strong>&nbsp;c&ugrave;ng c&ocirc;ng nghệ&nbsp;<strong>Spatial Audio</strong>&nbsp;v&agrave;&nbsp;<strong>Dolby Atmos</strong>&nbsp;cho m&igrave;nh trải nghiệm &acirc;m thanh to r&otilde;, &iacute;t tiếng vang hơn khi chỉnh ở mức &acirc;m lượng tối đa. Chất lượng vượt xa so với c&aacute;c d&ograve;ng m&aacute;y t&iacute;nh kh&aacute;c, cung cấp &acirc;m thanh r&otilde; r&agrave;ng khi xem video, nghe nhạc, YouTube,...</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112228.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 - Loa\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112228.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282827/apple-macbook-air-m2-2022-161122-112228.jpg\" title=\"MacBook Air M2 - Loa\" /></a></p>\r\n\r\n<p>MacBook Air M2 2022 ch&iacute;nh l&agrave; sự kết hợp ho&agrave;n hảo giữa phong c&aacute;ch thiết kế thời thượng, hiệu năng vượt trội c&ugrave;ng m&agrave;n h&igrave;nh hiển thị sắc n&eacute;t, l&agrave; một c&aacute;nh tay phải đắc lực c&ugrave;ng m&igrave;nh đối đầu mọi th&aacute;ch thức.&nbsp;</p>\r\n', 'Đã đăng', 3, '08-09-2023', 1),
(50, 'VHL#203', 'MacBook Pro 13 inch M2 2022', 28290000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'macbook3.jpg', '<h3>Sở hữu thiết kế thanh lịch, sang trọng c&ugrave;ng hiệu năng vượt trội đến từ bộ vi xử l&yacute; Apple M2&nbsp;t&acirc;n tiến,&nbsp;<a href=\"https://www.thegioididong.com/laptop/apple-macbook-pro-13-inch-m2-2022\" target=\"_blank\" title=\"Laptop Apple Macbook Pro M2 2022 đang bán tại thegioididong.com\">Macbook Pro M2</a>&nbsp;hứa hẹn sẽ đ&aacute;p ứng ho&agrave;n hảo cho người d&ugrave;ng s&aacute;ng tạo, phục vụ tốt cho c&aacute;c nhu cầu thiết kế đồ họa n&acirc;ng cao.</h3>\r\n\r\n<h3>Hiệu năng vượt trội của chip M2 thế hệ mới</h3>\r\n\r\n<p>MacBook Pro 13 inch l&agrave; thiết bị đầu ti&ecirc;n được trang bị bộ vi xử l&yacute; M2 ho&agrave;n to&agrave;n mới của Apple. Mặc d&ugrave; c&ocirc;ng ty cho biết M1 Pro v&agrave; M1 Max vẫn sẽ c&oacute; lợi thế hơn về hiệu suất nhờ c&oacute; th&ecirc;m l&otilde;i nhưng M2 thể hiện một bước tiến kh&aacute; lớn so với M1.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-241122-094728.jpg\" onclick=\"return false;\"><img alt=\"Macbook Pro M2 - Apple M2\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-241122-094728.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-241122-094728.jpg\" title=\"Macbook Pro M2 - Apple M2\" /></a></p>\r\n\r\n<p>M2 c&oacute;&nbsp;<strong>CPU 8 nh&acirc;n</strong>&nbsp;với&nbsp;<strong>4 l&otilde;i tiết kiệm điện</strong>&nbsp;v&agrave;&nbsp;<strong>4 l&otilde;i hiệu năng</strong>,&nbsp;<strong>GPU 10 l&otilde;i</strong>, nhiều hơn&nbsp;<strong>2 l&otilde;i</strong>&nbsp;so với GPU trong M1, c&oacute; thể chạy&nbsp;mượt m&agrave; c&aacute;c t&aacute;c vụ của một chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=do-hoa-ky-thuat\" target=\"_blank\" title=\"Xem thêm laptop đồ hoạ - kỹ thuật đang bán tại thegioididong.com\">laptop đồ họa - kỹ thuật</a>&nbsp;như thiết kế, chỉnh ảnh hay render video tr&ecirc;n phần mềm của&nbsp;Adobe như: Photoshop, Premiere,...&nbsp;Đồng thời bộ vi xử l&yacute; cũng được t&iacute;ch hợp đến&nbsp;<strong>20 triệu b&oacute;ng b&aacute;n dẫn.</strong></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041529.jpg\" onclick=\"return false;\"><img alt=\"Macbook Pro M2 - Xử lý đồ họa\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041529.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041529.jpg\" title=\"Macbook Pro M2 - Xử lý đồ họa\" /></a></p>\r\n\r\n<p>Từ những con số n&oacute;i tr&ecirc;n, Apple hứa hẹn M2 sẽ c&oacute; hiệu suất tổng thể&nbsp;<strong>tăng</strong>&nbsp;<strong>40%</strong>&nbsp;so với M1. M&igrave;nh cũng đ&atilde; thử đo hiệu năng bằng c&ocirc;ng cụ Cinebench R23 v&agrave; Geekbench 5, M2 đạt được hiệu suất tốt hơn về cả CPU v&agrave; GPU, mang lại sự cải thiện r&otilde; rệt về khả năng đa nhiệm khi m&igrave;nh mở nhiều ứng dụng kh&aacute;c nhau.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041527.jpg\" onclick=\"return false;\"><img alt=\"Macbook Pro M2 - Hiệu năng đo bằng Cinebench R23\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041527.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041527.jpg\" title=\"Macbook Pro M2 - Hiệu năng đo bằng Cinebench R23\" /></a></p>\r\n\r\n<p>Khả năng xử l&yacute; video của MacBook Pro M2 rất ấn tượng khi c&oacute; thể dễ d&agrave;ng xử l&yacute; nhiều luồng video 8K c&ugrave;ng một l&uacute;c. M&aacute;y c&oacute; hiệu suất cao, đặc biệt l&agrave; trong c&aacute;c t&aacute;c vụ về đồ họa v&agrave; sử dụng h&agrave;ng ng&agrave;y. Trong suốt thời gian sử dụng m&aacute;y m&igrave;nh rất ấn tượng bởi sự mượt m&agrave; v&agrave; độ m&aacute;t mẻ, quạt hiếm khi hoạt động d&ugrave; m&igrave;nh l&agrave;m việc với nhiều t&aacute;c vụ nặng trong thời gian d&agrave;i.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041522.jpg\" onclick=\"return false;\"><img alt=\"Macbook Pro M2 - Xử lý video\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041522.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041522.jpg\" title=\"Macbook Pro M2 - Xử lý video\" /></a></p>\r\n\r\n<p>Bộ nhớ<strong>&nbsp;RAM 8 GB&nbsp;</strong>một lần nữa g&acirc;y ấn tượng với m&igrave;nh khi c&oacute; thể chạy nhiều ứng dụng c&ugrave;ng l&uacute;c m&agrave; kh&ocirc;ng gặp bất kỳ vấn đề g&igrave;. M&igrave;nh ho&agrave;n to&agrave;n&nbsp;c&oacute; thể đa nhiệm với c&aacute;c t&aacute;c vụ như lướt web, đọc b&aacute;o, chỉnh sửa ảnh Photoshop m&agrave; kh&ocirc;ng bị đứng m&aacute;y hay phản hồi chậm.</p>\r\n\r\n<p>Tr&ecirc;n l&yacute; thuyết M2 cũng c&oacute; nhiều băng th&ocirc;ng bộ nhớ hơn, đạt&nbsp;<strong>100 GB/gi&acirc;y</strong>&nbsp;so với 68 GB/gi&acirc;y của M1, do đ&oacute; sẽ tối ưu được trải nghiệm sử dụng.&nbsp;<strong>Ổ cứng SSD 256 GB</strong>&nbsp;mang đến cho m&igrave;nh kh&ocirc;ng gian lưu trữ đủ d&ugrave;ng cho c&aacute;c t&aacute;c vụ văn ph&ograve;ng, thiết kế,... tuy nhi&ecirc;n do chỉ được trang bị một chip n&ecirc;n tốc độ sẽ bị hạn chế hơn so với c&aacute;c d&ograve;ng m&aacute;y thuộc thế hệ M1.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041515.jpg\" onclick=\"return false;\"><img alt=\"Macbook Pro M2 - Làm việc văn phòng\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041515.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041515.jpg\" title=\"Macbook Pro M2 - Làm việc văn phòng\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh Retina sống động, l&ocirc;i cuốn</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Xem thêm laptop Apple Macbook đang bán tại thegioididong.com\">Macbook</a>&nbsp;sở hữu m&agrave;n h&igrave;nh&nbsp;<strong>13.3 inch</strong>&nbsp;c&ugrave;ng độ ph&acirc;n giải&nbsp;<strong>Retina (2560 x 1600)</strong>&nbsp;sắc n&eacute;t, cho m&agrave;u sắc trung thực. Độ tương phản cao nhờ độ s&aacute;ng&nbsp;<strong>500 nits</strong>, c&ocirc;ng nghệ&nbsp;<strong>True tone</strong>&nbsp;tự động điều chỉnh mức độ s&aacute;ng để lu&ocirc;n đảm bảo hiển thị r&otilde; r&agrave;ng d&ugrave; m&igrave;nh sử dụng ở bất k&igrave; đ&acirc;u.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041520.jpg\" onclick=\"return false;\"><img alt=\"Macbook Pro M2 - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041520.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041520.jpg\" title=\"Macbook Pro M2 - Màn hình\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng gian m&agrave;u rộng&nbsp;<strong>Wide color (P3)</strong>&nbsp;mang đến m&agrave;u sắc hiển thị ch&iacute;nh x&aacute;c đồng thời tối ưu khả năng l&agrave;m việc cho người d&ugrave;ng thiết kế s&aacute;ng tạo. C&aacute;c c&ocirc;ng nghệ đ&oacute; được kết hợp với nhau tạo n&ecirc;n một kh&ocirc;ng gian giải tr&iacute; để xem YouTube, Netflix,... sống động hơn bao giờ hết.</p>\r\n\r\n<p>Bộ vi xử l&yacute; M2 cho ph&eacute;p thế hệ MacBook mới vừa c&oacute; hiệu suất cao vừa mang lại thời lượng pin ấn tượng. Trong trải nghiệm thực tế MacBook Pro M2 cho m&igrave;nh thời gian sử dụng l&ecirc;n đến&nbsp;<strong>2 ng&agrave;y</strong>&nbsp;đối với c&aacute;c t&aacute;c vụ thường d&ugrave;ng, ho&agrave;n to&agrave;n đ&aacute;p ứng tốt cho những chuyến c&ocirc;ng t&aacute;c xa hay x&aacute;ch m&aacute;y ra qu&aacute;n c&agrave; ph&ecirc; l&agrave;m việc cả ng&agrave;y d&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Apple cũng đ&atilde; thực hiện một n&acirc;ng cấp nhỏ tr&ecirc;n củ sạc đi k&egrave;m của chiếc MacBook mới, c&ocirc;ng suất&nbsp;<strong>67 W</strong>&nbsp;so với 61 W của thế hệ cũ, do đ&oacute; pin sẽ được sạc nhanh hơn một ch&uacute;t. M&igrave;nh hơi tiếc khi thế hệ M2 kh&ocirc;ng c&oacute; sạc MagSafe như chiếc Air mới, một sự hy sinh do MacBook Pro M2 giữ nguy&ecirc;n thiết kế cũ.</p>\r\n\r\n<h3>Thiết kế quyến rũ, biểu tượng cho sự ho&agrave;n mỹ thu h&uacute;t mọi &aacute;nh nh&igrave;n</h3>\r\n\r\n<p>Ấn tượng trong thiết kế của Apple lu&ocirc;n l&agrave;&nbsp;<strong>logo t&aacute;o khuyết</strong>&nbsp;được đặt ch&iacute;nh giữa mặt lưng, tạo n&ecirc;n sự nhận diện thương hiệu m&agrave; ai ai cũng phải ngước nh&igrave;n mỗi khi m&igrave;nh cầm tr&ecirc;n tay hay l&agrave;m việc ngo&agrave;i qu&aacute;n c&agrave; ph&ecirc;.&nbsp;Khối lượng m&aacute;y chỉ&nbsp;<strong>1.4 kg</strong>&nbsp;v&agrave; mỏng&nbsp;<strong>15.6 mm</strong>&nbsp;đem đến sự cơ động, dễ d&agrave;ng bỏ v&agrave;o balo để mang theo b&ecirc;n m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041508.jpg\" onclick=\"return false;\"><img alt=\"Macbook Pro M2 - Logo Apple\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041508.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041508.jpg\" title=\"Macbook Pro M2 - Logo Apple\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-apple-macbook-pro\" target=\"_blank\" title=\"Xem thêm laptop Apple  Macbook Pro đang bán tại thegioididong.com 1\">Macbook Pro</a>&nbsp;được chế t&aacute;c từ lớp&nbsp;<strong>vỏ kim loại</strong>&nbsp;nguy&ecirc;n khối, độ&nbsp;ho&agrave;n thiện chắc chắn, m&agrave;n h&igrave;nh kh&ocirc;ng khuyết điểm v&agrave;&nbsp;<strong>Touch ID</strong>&nbsp;cho ph&eacute;p m&igrave;nh đăng nhập v&agrave;o macOS một c&aacute;ch nhanh ch&oacute;ng, dễ d&agrave;ng bằng c&aacute;ch sử dụng dấu v&acirc;n tay. Sự tinh tế v&agrave; chất lượng ho&agrave;n thiện của Apple được thể hiện r&otilde; r&agrave;ng tr&ecirc;n MacBook Pro M2, vẫn l&agrave; một trong những m&aacute;y t&iacute;nh mỏng nhất v&agrave; nhẹ nhất ở thời điểm hiện tại.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041512.jpg\" onclick=\"return false;\"><img alt=\"Macbook Pro M2 - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041512.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041512.jpg\" title=\"Macbook Pro M2 - Thiết kế\" /></a></p>\r\n\r\n<p>Do giữ nguy&ecirc;n thiết kế cũ n&ecirc;n MacBook Pro M2 vẫn được trang bị thanh&nbsp;<strong>Touch Bar</strong>&nbsp;hỗ trợ m&igrave;nh điều chỉnh nhanh những chức năng của m&aacute;y kh&aacute;c nhau. C&aacute; nh&acirc;n m&igrave;nh vẫn muốn loại bỏ lu&ocirc;n thanh Touch Bar v&igrave; kh&ocirc;ng sử dụng nhiều v&agrave; những ứng dụng của b&ecirc;n thứ ba cũng đ&atilde; dần bỏ hỗ trợ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041510.jpg\" onclick=\"return false;\"><img alt=\"Macbook Pro M2 - Thanh Touch Bar\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041510.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041510.jpg\" title=\"Macbook Pro M2 - Thanh Touch Bar\" /></a></p>\r\n\r\n<p>Hai cổng&nbsp;<strong>Thunderbolt 3</strong>&nbsp;được t&iacute;ch hợp sạc pin, truyền dữ liệu nhanh ch&oacute;ng với băng th&ocirc;ng&nbsp;<strong>40 Gbps</strong>&nbsp;v&agrave; Jack tai nghe 3.5 mm, ph&ugrave; hợp với nhiều loại tai nghe phổ biến tr&ecirc;n thị trường. Nếu chỉ c&oacute; nhu cầu sử dụng th&ocirc;ng thường th&igrave; hai cổng vẫn đủ để đ&aacute;p ứng tốt nhu cầu, c&ograve;n nếu muốn kết nối với nhiều thiết bị hơn bạn c&oacute; thể sắm th&ecirc;m một chiếc Hub để n&acirc;ng cao hiệu quả c&ocirc;ng việc.</p>\r\n\r\n<p>B&agrave;n ph&iacute;m&nbsp;<strong>Magic Keyboard</strong>&nbsp;được thiết kế tinh xảo, đẹp mắt, độ nảy ổn định mang đến sự y&ecirc;n tĩnh v&agrave; thoải m&aacute;i mỗi khi soạn thảo văn bản.&nbsp;<strong>Đ&egrave;n nền b&agrave;n ph&iacute;m</strong>&nbsp;được t&iacute;ch hợp tr&ecirc;n m&aacute;y gi&uacute;p cho m&igrave;nh thao t&aacute;c dễ d&agrave;ng trong m&ocirc;i trường thiếu s&aacute;ng, n&acirc;ng cao trải nghiệm.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041514.jpg\" onclick=\"return false;\"><img alt=\"Macbook Pro M2 - Bàn phím\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041514.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041514.jpg\" title=\"Macbook Pro M2 - Bàn phím\" /></a></p>\r\n\r\n<p>TouchPad (b&agrave;n di chuột) th&igrave; khỏi phải b&agrave;n c&atilde;i nữa rồi, k&iacute;ch thước lớn cho m&igrave;nh thao t&aacute;c thoải m&aacute;i, cảm gi&aacute;c di vuốt lu&ocirc;n mượt m&agrave; cho m&igrave;nh trải nghiệm hơn hẳn những chiếc m&aacute;y của c&aacute;c h&atilde;ng kh&aacute;c. Th&ocirc;ng thường m&igrave;nh cũng kh&ocirc;ng cần d&ugrave;ng đến chuột rời khi sử dụng, chỉ những khi cần chỉnh h&igrave;nh ảnh trong Photoshop mới cần d&ugrave;ng đến.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041517.jpg\" onclick=\"return false;\"><img alt=\"Macbook Pro M2 - Bàn di chuột\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041517.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-231122-041517.jpg\" title=\"Macbook Pro M2 - Bàn di chuột\" /></a></p>\r\n\r\n<h3>Một Studio thu nhỏ trong tầm tay</h3>\r\n\r\n<p>Kh&ocirc;ng gian giải tr&iacute; của c&aacute;c sản phẩm nh&agrave; Apple chưa bao giờ l&agrave;m người d&ugrave;ng thất vọng v&agrave; chiếc MacBook Pro M2 cũng kh&ocirc;ng ngoại lệ khi sở hữu&nbsp;<strong>loa k&eacute;p (2 k&ecirc;nh)</strong>&nbsp;c&ugrave;ng c&ocirc;ng nghệ&nbsp;<strong>Wide Stereo</strong>&nbsp;v&agrave;&nbsp;<strong>Dolby Atmos</strong>, mang đến chất lượng &acirc;m thanh tr&ecirc;n cả tuyệt vời, đắm ch&igrave;m m&igrave;nh v&agrave;o những bản nhạc du dương lan tỏa khắp căn ph&ograve;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-07.jpg\" onclick=\"return false;\"><img alt=\"Macbook Pro M2 - Âm thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-07.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/282828/apple-macbook-pro-13-inch-m2-2022-07.jpg\" title=\"Macbook Pro M2 - Âm thanh\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm laptop đang bán tại thegioididong.com\">Laptop</a>&nbsp;được t&iacute;ch hợp&nbsp;<strong>720p FaceTime Camera</strong>&nbsp;cho khả năng hiển thị sắc n&eacute;t gi&uacute;p m&igrave;nh lu&ocirc;n tươi tắn trong c&aacute;c buổi họp hay lớp học trực tuyến.&nbsp;<strong>3 microphones</strong>&nbsp;thu tiếng tốt hơn, kh&ocirc;ng bị r&egrave;, khử tiếng ồn tốt v&agrave; mang lại sự ổn định khi giao tiếp, hỗ trợ&nbsp;cho qu&aacute; tr&igrave;nh học tập trực tuyến hay gọi điện video tiện lợi hơn.</p>\r\n\r\n<p>MacBook Pro M2 l&agrave; sự cộng hưởng ho&agrave;n hảo giữa thiết kế đẳng cấp v&agrave; hiệu năng vượt trội. Tất cả t&iacute;nh năng t&acirc;n tiến được Apple t&iacute;ch hợp tr&ecirc;n chiếc m&aacute;y phục vụ tối cho c&ocirc;ng việc s&aacute;ng tạo, thiết kế đồ họa n&acirc;ng cao của người d&ugrave;ng.</p>\r\n', 'Đã đăng', 3, '08-09-2023', 0),
(51, 'VHL#204', 'MacBook Air 15 inch M2 2023', 32290000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'macbook4.jpg', '<h3><a href=\"https://www.thegioididong.com/laptop/apple-macbook-air-m2-2023\" target=\"_blank\" title=\"Tham khảo các mẫu MacBook Air 15 inch M2 2023 tại thegioididong.com\">MacBook Air 15 inch M2 2023</a>&nbsp;đ&atilde; c&oacute; phi&ecirc;n bản cải tiến vừa được nh&agrave; Apple cho ra mắt,&nbsp;th&ecirc;m kh&ocirc;ng gian cho những điều bạn y&ecirc;u với m&agrave;n h&igrave;nh Liquid Retina 15 inch ấn tượng. Với người d&ugrave;ng y&ecirc;u&nbsp;th&iacute;ch hiệu năng &quot;nhanh như chớp&quot; trong một thiết kế si&ecirc;u gọn nhẹ của d&ograve;ng Air th&igrave; đ&acirc;y chắc chắn l&agrave; một sản phẩm bạn kh&ocirc;ng n&ecirc;n bỏ qua.</h3>\r\n\r\n<h3>M&agrave;n h&igrave;nh&nbsp;Retina 15 inch lớn ấn tượng&nbsp;</h3>\r\n\r\n<p>Với phi&ecirc;n bản 2023,&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-air\" target=\"_blank\" title=\"Tham khảo các mẫu Macbook Air tại thegioididong.com\">Macbook Air</a>&nbsp;được n&acirc;ng cấp m&agrave;n h&igrave;nh l&ecirc;n&nbsp;<strong>15.3 inch</strong>, được trang bị tấm nền&nbsp;<strong>IPS</strong>&nbsp;c&ugrave;ng độ ph&acirc;n giải&nbsp;<strong>Liquid Retina (2880 x 1864)</strong>&nbsp;gi&uacute;p những khung h&igrave;nh được t&aacute;i hiện ho&agrave;n hảo, c&oacute; độ sắc n&eacute;t cao c&ugrave;ng kh&ocirc;ng gian trải nghiệm nội dung rộng lớn. Độ phủ m&agrave;u rộng&nbsp;<strong>P3</strong>&nbsp;c&ugrave;ng mức hiển thị&nbsp;<strong>1 tỷ m&agrave;u</strong>&nbsp;hỗ trợ c&aacute;c t&aacute;c vụ đồ họa với khả năng xuất h&igrave;nh ảnh chuẩn x&aacute;c.</p>\r\n\r\n<p>Hơn nữa, độ s&aacute;ng<strong>&nbsp;500 nits</strong>&nbsp;c&ugrave;ng c&ocirc;ng nghệ&nbsp;<strong>True Tone Technology</strong>&nbsp;gi&uacute;p tăng cường khả năng hiển thị của bạn, dễ d&agrave;ng điều chỉnh linh hoạt, tương th&iacute;ch tự động độ s&aacute;ng với điều kiện m&ocirc;i trường để cho tr&igrave;nh xem ho&agrave;n hảo nhất.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-six.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-six.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-six.jpg\" title=\"MacBook Air 15 inch M2 2023 - Màn hình\" /></a></p>\r\n\r\n<p>Hệ thống &acirc;m thanh v&ograve;m đa chiều<strong>&nbsp;Dolby Atmos</strong>,&nbsp;<strong>Spatial Audio</strong>&nbsp;kết hợp c&ugrave;ng&nbsp;<strong>6 loa</strong>&nbsp;mang đến trọn vẹn những trải nghiệm giải tr&iacute; ch&acirc;n thực với chất &acirc;m sống động, &acirc;m bass to r&otilde;, độ khuếch đại &acirc;m thanh lớn cho ph&eacute;p bạn đắm ch&igrave;m trong những bản nhạc hay bộ phim y&ecirc;u th&iacute;ch.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-044421.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 - Âm thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-044421.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-044421.jpg\" title=\"MacBook Air 15 inch M2 2023 - Âm thanh\" /></a></p>\r\n\r\n<h3>Hiệu năng vượt trội, c&acirc;n mọi t&aacute;c vụ</h3>\r\n\r\n<p>MacBook Air 15 inch sở hữu hiệu năng mạnh mẽ đến từ bộ vi xử l&yacute;&nbsp;<strong>Apple M2</strong>&nbsp;sử dụng kiến ​​tr&uacute;c&nbsp;<strong>Apple ARM</strong>&nbsp;v&agrave; hiện được sản xuất tr&ecirc;n tiến tr&igrave;nh<strong>&nbsp;5 nm&nbsp;</strong>ho&agrave;n chỉnh với&nbsp;<strong>20 tỷ b&oacute;ng b&aacute;n dẫn</strong>&nbsp;mang đến hiệu năng mạnh mẽ v&agrave; ổn định được duy tr&igrave; từ phi&ecirc;n bản 13 inch trước đ&oacute;. Với tốc độ băng th&ocirc;ng bộ nhớ&nbsp;<strong>100 GB/s&nbsp;</strong>được cải tiến vượt trội, m&aacute;y cho khả năng vận h&agrave;nh ho&agrave;n hảo mọi t&aacute;c vụ học tập, l&agrave;m việc hay giải tr&iacute;.</p>\r\n\r\n<p>Khả năng xử l&yacute; đồ họa, chinh phục c&aacute;c t&aacute;c vụ thiết kế nhanh ch&oacute;ng v&agrave; hiệu quả card t&iacute;ch hợp&nbsp;<strong>10 nh&acirc;n GPU</strong>. Chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Tham khảo các mẫu Macbook tại thegioididong.com\">Macbook</a>&nbsp;Apple n&agrave;y&nbsp;cho ph&eacute;p bạn thao t&aacute;c mượt m&agrave; c&aacute;c ứng dụng đồ họa như Adobe Illustrator, Premiere,.... thực hiện thiết kế, s&aacute;ng tạo c&ugrave;ng với tr&igrave;nh ph&aacute;t video n&acirc;ng cao đều kh&ocirc;ng th&agrave;nh vấn đề.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-one.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 - Hiệu năng\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-one.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-one.jpg\" title=\"MacBook Air 15 inch M2 2023 - Hiệu năng\" /></a></p>\r\n\r\n<p>Bộ nhớ&nbsp;<strong>RAM 8 GB</strong>&nbsp;mang đến khả năng đa nhiệm mượt m&agrave;, thực hiện xử l&yacute; c&ocirc;ng việc với nhiều layer đồ họa c&ugrave;ng l&uacute;c ứng dụng kh&aacute;c như Spotify hay Youtube m&agrave; kh&ocirc;ng gặp hiện tượng giật lag. Với ổ cứng&nbsp;<strong>SSD&nbsp;256 GB&nbsp;</strong>cung cấp một kh&ocirc;ng gian lưu trữ vừa đủ cho c&aacute;c tệp tin, ứng dụng v&agrave; tr&ograve; chơi.</p>\r\n\r\n<p>Hệ điều h&agrave;nh&nbsp;<strong>Mac OS</strong>&nbsp;vừa mang đến khi ứng dụng đồ sộ đ&aacute;p ứng đầy đủ mọi nhu cầu l&agrave;m việc v&agrave; giải tr&iacute; của người d&ugrave;ng, vừa n&acirc;ng cao tr&igrave;nh bảo mật v&agrave; t&iacute;nh năng quản l&yacute; của m&aacute;y, bảo vệ tốt cho dữ liệu v&agrave; th&ocirc;ng tin.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-043656.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 - MacOS\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-043656.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-043656.jpg\" title=\"MacBook Air 15 inch M2 2023 - MacOS\" /></a></p>\r\n\r\n<h3>Thiết kế linh hoạt, mỏng kh&ocirc;ng tưởng</h3>\r\n\r\n<p>Macbook lu&ocirc;n l&agrave; điển h&igrave;nh của&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=cao-cap-sang-trong\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop cao cấp - sang trọng hiện đang kinh doanh tại thegioididong.com\">laptop cao cấp- sang trọng</a>&nbsp;với lối thiết kế sang trọng v&agrave; thanh lịch cũng kh&ocirc;ng ngoại lệ với phi&ecirc;n bản&nbsp;MacBook Air 15 inch lần n&agrave;y. Sở hữu thiết kế tai thỏ tương tự phi&ecirc;n bản Mac Air M2 13 inch, tuy nhi&ecirc;n với k&iacute;ch thước lớn hơn n&ecirc;n tạo cảm gi&aacute;c nh&igrave;n th&iacute;ch mắt v&agrave; tinh tế hơn đ&ocirc;i phần.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Tham khảo các mẫu laptop tại thegioididong.com\">Laptop</a>&nbsp;với khối lượng&nbsp;<strong>1.51 kg</strong>&nbsp;c&ugrave;ng độ d&agrave;y chỉ vỏn vẹn&nbsp;<strong>11.5 mm&nbsp;</strong>kết hợp với chất liệu<strong>&nbsp;nh&ocirc;m t&aacute;i chế 100%</strong>, kh&ocirc;ng những th&acirc;n thiện với m&ocirc;i trường m&agrave; c&ograve;n sang trọng, gọn nhẹ&nbsp;thuận tiện cho người d&ugrave;ng mang theo phục vụ c&ocirc;ng việc mọi l&uacute;c mọi nơi.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-043653.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-043653.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-043653.jpg\" title=\"MacBook Air 15 inch M2 2023 - Thiết kế\" /></a></p>\r\n\r\n<p><strong>Magic Keyboard</strong>&nbsp;với h&agrave;ng ph&iacute;m chức năng c&oacute; chiều cao ti&ecirc;u chuẩn, tạo cảm gi&aacute;c &ecirc;m &aacute;i c&oacute; độ tĩnh lặng khi thao t&aacute;c g&otilde;, ph&ugrave; hợp với đối tượng người d&ugrave;ng l&agrave;m văn ph&ograve;ng, y&ecirc;u cầu c&oacute; sự y&ecirc;n lặng để tập trung cao độ khi l&agrave;m việc. B&ecirc;n cạnh đ&oacute;,&nbsp;<strong>đ&egrave;n nền b&agrave;n ph&iacute;m</strong>&nbsp;c&ograve;n gi&uacute;p &iacute;ch rất nhiều khi l&agrave;m việc trong m&ocirc;i trường tối.</p>\r\n\r\n<p><strong>Touch ID</strong>&nbsp;hỗ trợ c&aacute;c thao t&aacute;c truy cập v&agrave;o m&aacute;y hay mở kh&oacute;a c&aacute;c ứng dụng v&agrave; trang web nhanh ch&oacute;ng, dễ d&agrave;ng với một chạm đơn giản.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-043654.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 - Cảm biến vân tay\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-043654.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-043654.jpg\" title=\"MacBook Air 15 inch M2 2023 - Cảm biến vân tay\" /></a></p>\r\n\r\n<p>Cổng giao tiếp&nbsp;<strong>Thunderbolt 3</strong>&nbsp;hỗ trợ kết nối phụ kiện với tốc độ cao, kết nối với m&agrave;n h&igrave;nh độ ph&acirc;n giải l&ecirc;n tới 6K v&agrave; cũng c&oacute; thể sạc m&aacute;y khi bạn qu&ecirc;n đem theo sạc của m&aacute;y.&nbsp;<strong>Sạc nam ch&acirc;m MagSafe 3</strong>&nbsp;dễ d&agrave;ng cắm v&agrave;o, r&uacute;t ra, ph&ograve;ng tr&aacute;nh c&aacute;c sự cố rơi l&agrave;m rớt m&aacute;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-043658.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-043658.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309016/apple-macbook-air-m2-2023-070623-043658.jpg\" title=\"MacBook Air 15 inch M2 2023 - Cổng kết nối\" /></a></p>\r\n\r\n<p>M&aacute;y cũng t&iacute;ch hợp&nbsp;<strong>1080p FaceTime HD camera</strong>&nbsp;c&ugrave;ng&nbsp;<strong>3 microphones</strong>&nbsp;hỗ trợ tối đa cho qu&aacute; tr&igrave;nh học tập v&agrave; l&agrave;m việc của bạn.</p>\r\n\r\n<p>Ghi dấu lần &quot; quay trở lại &quot; v&agrave;o năm 2023, MacBook Air 15 inch M2 2023 với hiệu năng mạnh mẽ c&ugrave;ng khung h&igrave;nh rộng lớn 15 inch được cải tiến, xứng đ&aacute;ng l&agrave; sản phẩm bạn n&ecirc;n sở hữu.</p>\r\n', 'Đã đăng', 3, '08-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(52, 'VHL#205', 'MacBook Pro 14 inch M2 Pro 2023', 47890000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'macbook5.jpg', '<h3>Apple đ&atilde; &acirc;m thầm tung ra thế hệ&nbsp;<a href=\"https://www.thegioididong.com/laptop/macbook-pro-14-inch-m2-pro\" target=\"_blank\" title=\"MacBook Pro 14 inch M2 Pro 2023 mới ra mắt đang kinh doanh tại Thegioididong.com\">MacBook Pro 14 inch M2 Pro 2023</a>&nbsp;ho&agrave;n to&agrave;n mới, hứa hẹn hiệu suất cao hơn so với MacBook Pro đợt trước, c&oacute; khả năng xử l&yacute; mượt m&agrave; c&aacute;c t&aacute;c vụ khắt khe của người d&ugrave;ng chuy&ecirc;n nghiệp.</h3>\r\n\r\n<h3>Hiệu suất v&ocirc; song, thời lượng pin d&agrave;i chưa từng c&oacute;</h3>\r\n\r\n<p>Với bộ vi xử l&yacute;&nbsp;<strong>M2 Pro</strong>,&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-pro-14-inch\" target=\"_blank\" title=\"MacBook Pro 14 inch đang kinh doanh tại Thegioididong.com\">MacBook Pro 14 inch</a>&nbsp;c&oacute; thể đ&aacute;p ứng ho&agrave;n hảo nhu cầu l&agrave;m việc chuy&ecirc;n nghiệp thuộc nhiều lĩnh vực kh&aacute;c nhau từ nghệ thuật, khoa học cho đến ph&aacute;t triển ứng dụng. Người d&ugrave;ng sẽ được trải nghiệm những cải tiến ấn tượng về hiệu suất, thời lượng pin, khả năng kết nối v&agrave; năng suất tổng thể.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-121204.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 14 inch M2 Pro - M2 Pro\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-121204.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-121204.jpg\" title=\"MacBook Pro 14 inch M2 Pro - M2 Pro\" /></a></p>\r\n\r\n<p>Được x&acirc;y dựng bằng c&ocirc;ng nghệ xử l&yacute;&nbsp;<strong>5 nm</strong>&nbsp;thế hệ thứ hai, M2 Pro chứa tổng cộng&nbsp;<strong>40 tỷ b&oacute;ng b&aacute;n dẫn</strong>, nhiều hơn gần&nbsp;<strong>20%</strong>&nbsp;so với M1 Pro v&agrave; gấp đ&ocirc;i số lượng so với M2. Bộ vi xử l&yacute; mới c&oacute; băng th&ocirc;ng bộ nhớ l&ecirc;n đến&nbsp;<strong>200 GB/gi&acirc;y</strong>, con số gấp đ&ocirc;i so với M2.&nbsp;<strong>CPU 10 l&otilde;i</strong>&nbsp;gồm&nbsp;<strong>6 l&otilde;i hiệu năng cao</strong>&nbsp;v&agrave;&nbsp;<strong>4 l&otilde;i tiết kiệm điện</strong>, cho khả năng xử l&yacute; đa luồng nhanh hơn, nhờ đ&oacute; m&agrave; người d&ugrave;ng c&oacute; thể bi&ecirc;n dịch code nhanh hơn&nbsp;<strong>25%</strong>&nbsp;so với M1 Pro.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-2-1.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 14 inch M2 Pro - Bộ vi xử lý M2 Pro\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-2-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-2-1.jpg\" title=\"MacBook Pro 14 inch M2 Pro - Bộ vi xử lý M2 Pro\" /></a></p>\r\n\r\n<p>GPU trong M2 Pro được cấu h&igrave;nh với&nbsp;<strong>16 nh&acirc;n</strong>&nbsp;v&agrave; c&oacute; bộ nhớ đệm L2 lớn hơn. Apple hứa hẹn tốc độ xử l&yacute; đồ họa sẽ nhanh hơn tới&nbsp;<strong>30%</strong>&nbsp;so với M1 Pro, gi&uacute;p tăng đ&aacute;ng kể hiệu suất xử l&yacute; h&igrave;nh ảnh, đồng thời cho ph&eacute;p người d&ugrave;ng chơi game cấu h&igrave;nh cao. MacBook Pro c&oacute; thể xử l&yacute; h&igrave;nh ảnh trong Adobe Photoshop nhanh hơn tới&nbsp;<strong>40%</strong>&nbsp;so với M1 Pro.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-120740.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 14 inch M2 Pro - Biên dịch code nhanh hơn\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-120740.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-120740.jpg\" title=\"MacBook Pro 14 inch M2 Pro - Biên dịch code nhanh hơn\" /></a></p>\r\n\r\n<p>Do M2 Pro sử dụng kiến ​​tr&uacute;c hệ thống tr&ecirc;n chip với bộ nhớ&nbsp;<strong>RAM 16 GB</strong>&nbsp;hợp nhất, băng th&ocirc;ng l&ecirc;n đến&nbsp;<strong>200 GB/gi&acirc;y</strong>&nbsp;khiến cho mọi t&aacute;c vụ đều được phản hồi tức th&igrave;, thao t&aacute;c chuyển qua lại giữa c&aacute;c ứng dụng nhanh đến kh&ocirc;ng tưởng. Ổ cứng&nbsp;<strong>SSD 512 GB</strong>&nbsp;được thiết kế để thực hiện c&aacute;c t&aacute;c vụ h&agrave;ng ng&agrave;y nhanh ch&oacute;ng v&agrave; nhạy b&eacute;n, đ&aacute;p ứng ho&agrave;n hảo c&aacute;c t&aacute;c vụ từ cơ bản đến khắt khe nhất.</p>\r\n\r\n<p>Apple cho biết sản phẩm mới của h&atilde;ng c&oacute; thời lượng pin ấn tượng, con số l&ecirc;n đến&nbsp;<strong>18 giờ ph&aacute;t video</strong>&nbsp;hoặc&nbsp;<strong>12 giờ duyệt web</strong>&nbsp;sử dụng kết nối kh&ocirc;ng d&acirc;y. MacBook Pro cũng duy tr&igrave; hiệu năng kể cả khi người d&ugrave;ng cắm nguồn điện hay d&ugrave;ng pin.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-120845.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 14 inch M2 Pro - Hiệu năng ổn định\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-120845.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-120845.jpg\" title=\"MacBook Pro 14 inch M2 Pro - Hiệu năng ổn định\" /></a></p>\r\n\r\n<h3>Thiết kế tinh tế, đầy đủ cổng kết nối cần thiết</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Máy tính xách tay, laptop Macbook đang được kinh doanh tại Thegioididong.com\">MacBook</a>&nbsp;mới sở hữu thiết kế tương tự như chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop/macbook-pro-14-m1-pro-2021-8-core-cpu\" target=\"_blank\" title=\"MacBook Pro 14 M1 Pro 2021 8-core CPU/16GB/512GB/14-core GPU (MKGR3SA/A) hiện đang kinh doanh tại thegioididong.com\">Macbook Pro 14 inch M1 Pro 2021</a>, với lớp&nbsp;<strong>vỏ kim loại</strong>&nbsp;nguy&ecirc;n khối bằng nh&ocirc;m, kiểu d&aacute;ng phẳng, viền m&agrave;n h&igrave;nh mỏng với sự xuất hiện của chiếc notch tai thỏ quen thuộc, b&agrave;n ph&iacute;m to&agrave;n dải m&agrave;u đen, b&agrave;n di chuột Force Touch lớn v&agrave; c&oacute; hai lưới loa ở hai cạnh b&ecirc;n b&agrave;n ph&iacute;m.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-113705.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 14 inch M2 Pro - Thiết kế không đổi\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-113705.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-113705.jpg\" title=\"MacBook Pro 14 inch M2 Pro - Thiết kế không đổi\" /></a></p>\r\n\r\n<p>Sản phẩm được thiết kế để giảm thiểu t&aacute;c động đối với m&ocirc;i trường, Apple cho biết đ&atilde; sử dụng&nbsp;<strong>100% vật liệu t&aacute;i chế</strong>&nbsp;như: Nh&ocirc;m tạo th&agrave;nh vỏ, c&aacute;c nguy&ecirc;n tố đất hiếm c&oacute; trong nam ch&acirc;m, thiếc trong mối h&agrave;n của bảng mạch ch&iacute;nh v&agrave; v&agrave;ng trong lớp mạ của nhiều bo mạch in.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-120529.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 14 inch M2 Pro - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-120529.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-120529.jpg\" title=\"MacBook Pro 14 inch M2 Pro - Thiết kế\" /></a></p>\r\n\r\n<p>MacBook Pro kh&ocirc;ng chỉ được t&iacute;ch hợp&nbsp;<strong>Wi-Fi 6E</strong>&nbsp;cho kết nối kh&ocirc;ng d&acirc;y nhanh hơn m&agrave; c&ograve;n được trang bị cổng HDMI ti&ecirc;n tiến hơn, hỗ trợ xuất nội dung l&ecirc;n m&agrave;n h&igrave;nh 8K ở tần số qu&eacute;t 60 Hz hoặc m&agrave;n h&igrave;nh 4K với tần số qu&eacute;t 240 Hz.&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, thế hệ MacBook Pro mới cũng sở hữu đến 3 cổng Thunderbolt 4 để kết nối c&aacute;c thiết bị ngoại vi tốc độ cao, khe cắm thẻ SDXC, sạc MagSafe 3 v&agrave; jack cắm 3.5 mm hỗ trợ tai nghe c&oacute; trở kh&aacute;ng cao.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-115225.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 14 inch M2 Pro - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-115225.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-115225.jpg\" title=\"MacBook Pro 14 inch M2 Pro - Cổng kết nối\" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh tai thỏ hiển thị sống động</h3>\r\n\r\n<p>MacBook Pro được trang bị m&agrave;n h&igrave;nh&nbsp;<strong>Liquid Retina XDR</strong>&nbsp;sử dụng c&ocirc;ng nghệ<strong>&nbsp;LED mini</strong>&nbsp;đ&atilde; c&oacute; mặt tr&ecirc;n iPad Pro 12.9 inch, k&iacute;ch thước lớn&nbsp;<strong>14.2 inch</strong>&nbsp;với độ ph&acirc;n giải gốc&nbsp;<strong>3024 x 1964</strong>, tương đương&nbsp;<strong>254 pixel mỗi inch</strong>.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-6-1.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 14 inch M2 Pro - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-6-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-6-1.jpg\" title=\"MacBook Pro 14 inch M2 Pro - Màn hình\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh c&oacute; độ s&aacute;ng tối đa l&ecirc;n đến&nbsp;<strong>1000 nits</strong>&nbsp;v&agrave; tỷ lệ tương phản&nbsp;<strong>1.000.000:1</strong>, đồng thời hỗ trợ dải m&agrave;u&nbsp;<strong>P3 Wide</strong>&nbsp;cho m&agrave;u sắc tươi s&aacute;ng, trung thực. Một c&ocirc;ng nghệ m&agrave;n h&igrave;nh đặc trưng tr&ecirc;n MacBook Pro mới ch&iacute;nh l&agrave;&nbsp;<strong>True Tone -&nbsp;</strong>được thiết kế để c&acirc;n bằng trắng sao cho ph&ugrave; hợp với &aacute;nh s&aacute;ng của m&ocirc;i trường xung quanh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-120636.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 14 inch M2 Pro - Chất lượng hiển thị\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-120636.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-120636.jpg\" title=\"MacBook Pro 14 inch M2 Pro - Chất lượng hiển thị\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, MacBook Pro cũng hỗ trợ c&ocirc;ng nghệ&nbsp;<strong>ProMotion</strong>&nbsp;cho tốc độ l&agrave;m mới th&iacute;ch ứng từ&nbsp;<strong>24 Hz</strong>&nbsp;đến&nbsp;<strong>120 Hz</strong>&nbsp;t&ugrave;y theo nội dung hiển thị. Khi xem trang web tĩnh kh&ocirc;ng y&ecirc;u cầu tốc độ l&agrave;m mới cao, m&agrave;n h&igrave;nh sẽ giảm tốc độ l&agrave;m mới thấp để duy tr&igrave; thời lượng pin, nhưng khi chơi game, cuộn hoặc thực hiện c&aacute;c hoạt động kh&aacute;c cần tốc độ l&agrave;m mới cao hơn, tốc độ khung h&igrave;nh sẽ được đẩy l&ecirc;n cao.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-121518.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 14 inch M2 Pro - Màn hình hiển thị\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-121518.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302146/macbook-pro-14-inch-m2-pro-180123-121518.jpg\" title=\"MacBook Pro 14 inch M2 Pro - Màn hình hiển thị\" /></a></p>\r\n\r\n<p>T&oacute;m lại, MacBook Pro với bộ vi xử l&yacute; M2 Pro đ&atilde; thay đổi cuộc chơi, cho ph&eacute;p c&aacute;c chuy&ecirc;n gia vượt qua giới hạn khi c&oacute; thể thực hiện c&ocirc;ng việc kể cả khi đang di chuyển. Với hiệu suất nhanh hơn, khả năng kết nối n&acirc;ng cao, thời lượng pin d&agrave;i c&ugrave;ng với m&agrave;n h&igrave;nh hiển thị đẹp mắt, đơn giản l&agrave; kh&ocirc;ng c&oacute; chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop | Máy tính xách tay đang kinh doanh chính hãng tại Thegioididong.com\">laptop</a>&nbsp;n&agrave;o kh&aacute;c c&oacute; thể qua mặt được.</p>\r\n', 'Đã đăng', 3, '08-09-2023', 0),
(53, 'VHL#206', 'MacBook Pro 16 inch M2 Pro 2023', 65990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'macbook6.jpg', '<h3>Mở đầu cho một năm 2023 đầy b&ugrave;ng nổ với sự xuất hiện của chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop/macbook-pro-16-inch-m2-pro-1tb\" target=\"_blank\" title=\"Laptop MacBook Pro 16 inch M2 Pro 2023 12-core CPU/16GB/1TB/19-core GPU đang bán tại thegioididong.com\">MacBook Pro 16 inch M2 Pro 2023</a>, vẫn l&agrave; những đường n&eacute;t m&aacute;y thời thượng, tinh xảo nhưng nay đ&atilde; được n&acirc;ng cấp sức mạnh ngo&agrave;i tầm mong đợi với con chip Apple M2 Pro, hứa hẹn xứng tầm doanh nh&acirc;n th&agrave;nh đạt hay nh&agrave; s&aacute;ng tạo nội dung chuy&ecirc;n nghiệp.&nbsp;</h3>\r\n\r\n<h3>Mạnh mẽ hơn với c&aacute;c t&aacute;c vụ chuy&ecirc;n s&acirc;u&nbsp;</h3>\r\n\r\n<p>Tuy vẫn được x&acirc;y dựng tr&ecirc;n tiến tr&igrave;nh&nbsp;<strong>5 nm</strong>&nbsp;nhưng chip&nbsp;<strong>Apple M2 Pro</strong>&nbsp;lại mang hiệu năng mạnh mẽ hơn l&ecirc;n đến&nbsp;<strong>20%</strong>&nbsp;so với d&ograve;ng chip Apple M1 Pro, băng th&ocirc;ng bộ nhớ đạt tốc độ&nbsp;<strong>200 GB/s</strong>&nbsp;cho khả năng xử l&yacute; mọi t&aacute;c vụ chuy&ecirc;n s&acirc;u một c&aacute;ch nhanh ch&oacute;ng v&agrave; mượt m&agrave; hơn hẳn so với c&aacute;c d&ograve;ng sản phẩm c&ugrave;ng ph&acirc;n kh&uacute;c đồng thời tiết kiệm nguồn điện năng đ&aacute;ng kể với hơn&nbsp;<strong>40 tỷ b&oacute;ng b&aacute;n dẫn</strong>.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-061955.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 16 inch M2 Pro 2023 19-core GPU - Chip M2 Pro\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-061955.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-061955.jpg\" title=\"MacBook Pro 16 inch M2 Pro 2023 19-core GPU - Chip M2 Pro\" /></a></p>\r\n\r\n<p>Hiệu suất xử l&yacute; h&igrave;nh ảnh được gia tăng đ&aacute;ng kinh ngạc gi&uacute;p chinh phục c&aacute;c c&ocirc;ng việc đồ họa tr&ecirc;n Adobe Premiere, Illustrator, AutoCAD,... từ chỉnh sửa h&igrave;nh ảnh, render video đến thiết kế 2D, 3D chuy&ecirc;n nghiệp một c&aacute;ch ấn tượng nhờ sự hỗ trợ của&nbsp;<strong>19 nh&acirc;n GPU</strong>&nbsp;mạnh mẽ.&nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p>Bộ nhớ&nbsp;<strong>RAM 16 GB</strong>&nbsp;mang đến khả năng đa nhiệm mượt m&agrave; ngo&agrave;i tầm mong đợi, cho ph&eacute;p người d&ugrave;ng xử l&yacute; c&ocirc;ng việc tr&ecirc;n nhiều tr&igrave;nh duyệt Safari c&ugrave;ng l&uacute;c hay vừa l&agrave;m layer đồ họa vừa nghe nhạc tr&ecirc;n Spotify trơn tru hơn, hạn chế tối đa hiện tượng bị lag hay đơ m&aacute;y.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-062027.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 16 inch M2 Pro 2023 19-core GPU - Hiệu năng\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-062027.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-062027.jpg\" title=\"MacBook Pro 16 inch M2 Pro 2023 19-core GPU - Hiệu năng\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng gian lưu trữ ấn tượng cho c&aacute;c t&aacute;c phẩm c&aacute; nh&acirc;n đặc sắc, những tập phim y&ecirc;u th&iacute;ch hay ứng dụng cần thiết nhờ ổ cứng&nbsp;<strong>SSD 1 TB</strong>&nbsp;được trang bị tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-pro\" target=\"_blank\" title=\"Xem thêm các mẫu MacBook Pro hiện đang kinh doanh tại thegioididong.com\">MacBook Pro</a>, b&ecirc;n cạnh đ&oacute; c&ograve;n gia tăng tốc độ đọc ghi dữ liệu cũng như hiệu suất l&agrave;m việc đồng thời gi&uacute;p bạn khởi động m&aacute;y nhanh ch&oacute;ng hơn chỉ trong v&agrave;i gi&acirc;y.&nbsp;</p>\r\n\r\n<p>Hệ điều h&agrave;nh&nbsp;<strong>MacOS</strong>&nbsp;với thiết kế giao diện tối giản, đẹp mắt nhưng vẫn kh&ocirc;ng k&eacute;m phần sang trọng, c&aacute;c thanh c&ocirc;ng cụ cần thiết được đặt gọn g&agrave;ng ở giữa m&aacute;y gi&uacute;p việc t&igrave;m kiếm nhanh gọn, kho ứng dụng khổng lồ sẵn s&agrave;ng đ&aacute;p ứng mọi nhu cầu sử dụng của người d&ugrave;ng.&nbsp;</p>\r\n\r\n<h3>Tinh xảo v&agrave; qu&yacute; ph&aacute;i tr&ecirc;n từng đường n&eacute;t m&aacute;y&nbsp;</h3>\r\n\r\n<p>Phong c&aacute;ch thiết kế của&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Xem thêm các mẫu MacBook hiện đang kinh doanh tại thegioididong.com\">MacBook</a>&nbsp;vẫn lu&ocirc;n đứng vững ở vị tr&iacute; h&agrave;ng đầu với lớp&nbsp;<strong>vỏ kim loại</strong>&nbsp;cứng c&aacute;p, đường n&eacute;t vu&ocirc;ng vức c&ugrave;ng logo tr&aacute;i t&aacute;o khuyết đặc trưng đ&atilde; mang đến một tổng thể tuy đơn giản nhưng lại v&ocirc; c&ugrave;ng tinh xảo v&agrave; đậm t&iacute;nh thời trang, ghi điểm tuyệt đối trong mắt mọi người xung quanh mỗi khi bạn xuất hiện.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-061712.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 16 inch M2 Pro 2023 19-core GPU - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-061712.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-061712.jpg\" title=\"MacBook Pro 16 inch M2 Pro 2023 19-core GPU - Thiết kế\" /></a>&nbsp;&nbsp;</p>\r\n\r\n<p>Phần b&agrave;n ph&iacute;m vẫn được giữ nguy&ecirc;n thiết kế của d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-pro-16-inch\" target=\"_blank\" title=\"Xem thêm các mẫu MacBook Pro 16 inch hiện đang kinh doanh tại thegioididong.com \">MacBook Pro 16 inch</a>&nbsp;với thanh Touch Bar được loại bỏ, thay v&agrave;o đ&oacute; l&agrave; d&atilde;y ph&iacute;m F chức năng gi&uacute;p thao t&aacute;c sử dụng nhanh ch&oacute;ng hơn, trải nghiệm cảm gi&aacute;c g&otilde; &ecirc;m &aacute;i với h&agrave;nh tr&igrave;nh ph&iacute;m ổn định gi&uacute;p bạn tăng năng suất l&agrave;m việc v&agrave; hạn chế tối đa việc ảnh hưởng tiếng ồn đến mọi người. M&aacute;y cũng c&oacute;&nbsp;<strong>đ&egrave;n nền b&agrave;n ph&iacute;m</strong>&nbsp;cho người d&ugrave;ng l&agrave;m việc trong m&ocirc;i trường tối tốt hơn.</p>\r\n\r\n<p>T&iacute;nh năng&nbsp;<strong>bảo mật v&acirc;n tay</strong>&nbsp;được t&iacute;ch hợp tr&ecirc;n ph&iacute;m nguồn cho ph&eacute;p người d&ugrave;ng đăng nhập v&agrave;o m&aacute;y cũng như khởi động c&aacute;c ứng dụng một c&aacute;ch nhanh ch&oacute;ng so với kiểu nhập mật khẩu truyền thống rườm r&agrave; đồng thời n&acirc;ng cao chất lượng bảo mật c&aacute;c th&ocirc;ng tin c&aacute; nh&acirc;n một c&aacute;ch ho&agrave;n hảo.&nbsp;</p>\r\n\r\n<p>Camera&nbsp;<strong>1080p FaceTime HD</strong>&nbsp;được trang bị tr&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop hiện đang kinh doanh tại thegioididong.com\">laptop</a>&nbsp;n&agrave;y đ&atilde; mang đến chất lượng học tập cũng như l&agrave;m việc trực tuyến qua c&aacute;c ứng dụng Zoom, Google Meet, Ms Team,... được tốt hơn rất nhiều với h&igrave;nh ảnh, video r&otilde; n&eacute;t hơn.&nbsp;</p>\r\n\r\n<p>C&aacute;c cổng kết nối kh&aacute; phổ biến như Jack tai nghe, HDMI, Thunderbolt 4, khe thẻ nhớ SD được t&iacute;ch hợp tr&ecirc;n chiếc MacBook Pro 16 inch hỗ trợ người d&ugrave;ng li&ecirc;n kết cũng như truyền tải dữ liệu đến c&aacute;c thiết bị ngoại vi kh&aacute;c mượt m&agrave;. Sạc&nbsp;<strong>MagSafe 3</strong>&nbsp;gi&uacute;p m&aacute;y sạc pin nhanh hơn với thiết kế gọn g&agrave;ng c&ugrave;ng cơ chế h&uacute;t nam ch&acirc;m hiện đại.&nbsp;&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-061744.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 16 inch M2 Pro 2023 19-core GPU - CKN\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-061744.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-061744.jpg\" title=\"MacBook Pro 16 inch M2 Pro 2023 19-core GPU - CKN\" /></a></p>\r\n\r\n<h3>M&atilde;n nh&atilde;n từ chất lượng h&igrave;nh ảnh đến &acirc;m thanh&nbsp;</h3>\r\n\r\n<p>K&iacute;ch thước m&agrave;n h&igrave;nh&nbsp;<strong>16.2 inch</strong>&nbsp;rộng r&atilde;i c&oacute; độ ph&acirc;n giải&nbsp;<strong>Liquid Retina XDR display (3456 x 2234)</strong>&nbsp;c&ugrave;ng&nbsp;<strong>1 tỷ m&agrave;u</strong>&nbsp;đắm ch&igrave;m người d&ugrave;ng v&agrave;o kh&ocirc;ng gian hiển thị si&ecirc;u ch&acirc;n thực tr&ecirc;n từng chi tiết d&ugrave; l&agrave; tĩnh hay động, kh&ocirc;ng chỉ gi&uacute;p người d&ugrave;ng giải tr&iacute; với những thước phim tuyệt đỉnh m&agrave; c&ograve;n mang đến độ chuẩn x&aacute;c m&agrave;u sắc cao khi xử l&yacute; c&aacute;c t&aacute;c vụ đồ họa.&nbsp;</p>\r\n\r\n<p>Chuyển động h&igrave;nh ảnh mượt m&agrave; hơn bao giờ hết với dải m&agrave;u rộng&nbsp;<strong>Wide color (P3)</strong>&nbsp;c&ugrave;ng tần số qu&eacute;t l&ecirc;n đến&nbsp;<strong>120 Hz</strong>, hạn chế tối đa t&igrave;nh trạng nh&ograve;e hay x&eacute; h&igrave;nh tr&ecirc;n m&aacute;y, mang đến những trải nghiệm ấn tượng đến kh&oacute; qu&ecirc;n. Độ s&aacute;ng&nbsp;<strong>1000 nits</strong>&nbsp;tự tin c&acirc;n tốt mọi điều kiện &aacute;nh s&aacute;ng kh&aacute;c nhau, kết hợp c&ocirc;ng nghệ&nbsp;<strong>True Tone</strong>&nbsp;cho khả năng tự động c&acirc;n bằng c&aacute;c gam m&agrave;u trắng để ph&ugrave; hợp với &aacute;nh s&aacute;ng tự nhi&ecirc;n, bảo vệ tốt cho thị lực người d&ugrave;ng.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-061851.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro 16 inch M2 Pro 2023 19-core GPU - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-061851.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302150/macbook-pro-16-inch-m2-pro-1-tb-190123-061851.jpg\" title=\"MacBook Pro 16 inch M2 Pro 2023 19-core GPU - Màn hình\" /></a></p>\r\n\r\n<p>Sự kết hợp giữa&nbsp;<strong>Wide stereo sound</strong>,&nbsp;<strong>Spatial Audio</strong>&nbsp;c&ugrave;ng&nbsp;<strong>Dolby Atmos</strong>&nbsp;đ&atilde; mang đến một hệ thống &acirc;m thanh ho&agrave;n mỹ với chất lượng &acirc;m thanh sống động, to r&otilde;, hiệu ứng &acirc;m v&ograve;m đ&aacute;ng kinh ngạc khi bạn nghe nhạc hay xem phim thư gi&atilde;n, tr&aacute;nh được c&aacute;c t&igrave;nh trạng bị m&eacute;o hay r&egrave; tiếng, lan tỏa sự tuyệt hảo đến mọi ng&oacute;c ng&aacute;ch căn ph&ograve;ng.</p>\r\n\r\n<p>MacBook Pro 16 inch M2 Pro 2023 một lần nữa đ&atilde; khẳng định vị thế của m&igrave;nh trong ph&acirc;n kh&uacute;c&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=cao-cap-sang-trong\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop cao cấp - sang trọng hiện đang kinh doanh tại thegioididong.com\">laptop cao cấp - sang trọng</a>&nbsp;nhờ phong c&aacute;ch thiết kế đẳng cấp, thời thượng c&ugrave;ng sức mạnh b&ugrave;ng nổ từ bộ vi xử l&yacute; Apple M2 Pro, hứa hẹn kh&ocirc;ng l&agrave;m người d&ugrave;ng thất vọng.</p>\r\n', 'Đã đăng', 3, '08-09-2023', 0),
(54, 'VHL#207', 'MacBook Pro 16 inch M2 Pro 2023', 59690000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'macbook7.jpg', '<h3>Apple vừa giới thiệu đến người d&ugrave;ng chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop/macbook-pro-16-inch-m2-pro\" target=\"_blank\" title=\"Laptop MacBook Pro 16 inch M2 Pro 2023 tại Thế Giới Di Động\">MacBook Pro 16 inch M2 Pro 2023&nbsp;</a>c&oacute; kiểu d&aacute;ng mỏng nhẹ nhưng b&ecirc;n trong l&agrave; một hiệu năng v&ocirc; c&ugrave;ng mạnh mẽ đ&aacute;p ứng được mọi t&aacute;c vụ, hứa hẹn sẽ trở th&agrave;nh một người bạn đồng h&agrave;nh tuyệt vời trong c&ocirc;ng việc, học tập v&agrave; giải tr&iacute;.&nbsp;</h3>\r\n\r\n<h3>Xử l&yacute; trơn tru mọi c&ocirc;ng việc nhờ con chip Apple M2 Pro</h3>\r\n\r\n<p>Vẫn được sản xuất tr&ecirc;n tiến tr&igrave;nh&nbsp;<strong>5 nm&nbsp;</strong>nhưng con&nbsp;<strong>chip Apple M2 Pro</strong>&nbsp;c&oacute; rất nhiều cải tiến so với thế hệ M1 Pro đ&atilde; ra mắt trước đ&oacute;. Với<strong>&nbsp;CPU 12 nh&acirc;n</strong>&nbsp;c&ugrave;ng băng th&ocirc;ng bộ nhớ&nbsp;<strong>200 GB/s</strong>&nbsp;sẽ cung cấp sức mạnh cao hơn&nbsp;<strong>20%&nbsp;</strong>gi&uacute;p vận h&agrave;nh trơn tru mọi t&aacute;c vụ từ cơ bản đến n&acirc;ng cao nhưng vẫn rất tiết kiệm năng lượng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-4-1.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 Pro 2023 - Chip Apple M2 Pro\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-4-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-4-1.jpg\" title=\"MacBook Pro M2 Pro 2023 - Chip Apple M2 Pro\" /></a></p>\r\n\r\n<p>Chiếc&nbsp;Apple MacBook&nbsp;được trang bị card đồ họa t&iacute;ch hợp&nbsp;<strong>19 l&otilde;i GPU</strong>&nbsp;cung cấp hiệu suất đồ họa cao hơn&nbsp;<strong>30%</strong>&nbsp;v&agrave;&nbsp;<strong>Neural Engine nhanh hơn 40%&nbsp;</strong>so với thế hệ tiền nhiệm gi&uacute;p đẩy nhanh tốc độ xử l&yacute; của c&aacute;c t&aacute;c vụ đồ họa. Người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể thực hiện c&aacute;c c&ocirc;ng việc chỉnh sửa h&igrave;nh ảnh, thiết kế, dựng phim hay render video tr&ecirc;n những phần mềm đồ họa một c&aacute;ch nhẹ nh&agrave;ng.</p>\r\n\r\n<p>Được trang bị bộ nhớ&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=16-gb\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop RAM 16 GB hiện đang kinh doanh tại thegioididong.com\">RAM 16 GB</a>&nbsp;n&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Xem thêm Macbook đang bán tại thegioiddidong.com\">MacBook</a>&nbsp;n&agrave;y c&oacute; khả năng xử l&yacute; đa nhiệm v&ocirc; c&ugrave;ng mượt m&agrave;, người d&ugrave;ng c&oacute; thể mở c&ugrave;ng l&uacute;c nhiều cửa sổ l&agrave;m việc, layer đồ họa, file Excel lớn,... m&agrave; kh&ocirc;ng gặp phải t&igrave;nh trạng giật lag. B&ecirc;n cạnh đ&oacute;, thiết bị c&ograve;n c&oacute; tốc độ đọc ghi v&ocirc; c&ugrave;ng nhanh ch&oacute;ng, thời gian khởi động m&aacute;y được r&uacute;t ngắn nhờ ổ cứng&nbsp;<strong>SSD 512 GB</strong>.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-5-1.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 Pro 2023 - Hiệu năng mạnh mẽ\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-5-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-5-1.jpg\" title=\"MacBook Pro M2 Pro 2023 - Hiệu năng mạnh mẽ\" /></a></p>\r\n\r\n<h3>Giữ vững lối thiết kế sang trọng, tỉ mỉ tr&ecirc;n từng chi tiết</h3>\r\n\r\n<p>Vỏ được l&agrave;m từ&nbsp;<strong>kim loại</strong>&nbsp;kh&ocirc;ng chỉ đủ cứng c&aacute;p để bảo vệ c&aacute;c linh kiện b&ecirc;n trong m&agrave; c&ograve;n mang đến vẻ ngo&agrave;i v&ocirc; c&ugrave;ng sang trọng. C&aacute;c g&oacute;c được bo cong một c&aacute;ch tinh tế, hạn chế m&oacute;p m&eacute;o khi va đập, logo &ldquo;quả t&aacute;o cắn dở&rdquo; vẫn được đặt ở trung t&acirc;m mặt lưng thu h&uacute;t mọi &aacute;nh nh&igrave;n. Thiết bị c&oacute; khối lượng<strong>&nbsp;2.15 kg&nbsp;</strong>v&agrave; d&agrave;y<strong>&nbsp;16.8 mm</strong>. Tuy vậy, chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=do-hoa-ky-thuat\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop đồ họa - kỹ thuật hiện đang kinh doanh tại thegioididong.com\">laptop đồ họa - kỹ thuật</a>&nbsp;vẫn cho bạn khả năng di chuyển linh hoạt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-1-1.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 Pro 2023 - Kiểu dáng sang trọng\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-1-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-1-1.jpg\" title=\"MacBook Pro M2 Pro 2023 - Kiểu dáng sang trọng\" /></a></p>\r\n\r\n<p>Bạn c&oacute; thể mở kh&oacute;a chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-pro\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop MacBook Pro hiện đang kinh doanh tại thegioididong.com\">MacBook Pro</a>&nbsp;v&ocirc; c&ugrave;ng nhanh ch&oacute;ng chỉ với một lần chạm th&ocirc;ng qua&nbsp;<strong>cảm biến v&acirc;n tay&nbsp;</strong>(Touch ID)&nbsp;t&iacute;ch hợp tr&ecirc;n n&uacute;t nguồn. Kh&ocirc;ng những r&uacute;t ngắn thời gian thao t&aacute;c đăng nhập m&agrave; c&ograve;n đảm bảo độ an to&agrave;n hơn cho dữ liệu b&ecirc;n trong so với loại mật khẩu truyền thống.</p>\r\n\r\n<p><strong>Magic keyboard&nbsp;</strong>mang đến cảm gi&aacute;c g&otilde; ph&iacute;m &ecirc;m nhẹ khi g&otilde; hạn chế tối đa việc ảnh hưởng tiếng ồn đến những người xung quanh, h&agrave;nh tr&igrave;nh ổn định v&agrave; bố cục ph&iacute;m sắp xếp hợp l&yacute; gi&uacute;p đẩy nhanh tốc độ đ&aacute;nh chữ. B&agrave;n ph&iacute;m c&ograve;n trang bị&nbsp;<a href=\"https://www.thegioididong.com/laptop-den-ban-phim\" target=\"_blank\" title=\"Laptop, máy tính xách tay trang bị đèn bàn phím đang kinh doanh tại Thegioididong.com\">đ&egrave;n nền</a>&nbsp;hỗ trợ người d&ugrave;ng l&agrave;m việc tốt hơn trong m&ocirc;i trường &aacute;nh s&aacute;ng yếu.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-2-1.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 Pro 2023 - Bàn phím\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-2-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-2-1.jpg\" title=\"MacBook Pro M2 Pro 2023 - Bàn phím\" /></a></p>\r\n\r\n<p>Được trang bị<strong>&nbsp;3 cổng Thunderbolt 4</strong>,&nbsp;<strong>HDMI</strong>&nbsp;v&agrave;<strong>&nbsp;Jack tai nghe 3.5 mm</strong>&nbsp;nằm gọn g&agrave;ng dọc cạnh b&ecirc;n của&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop đang kinh doanh chính hãng tại Thegioididong.com\">laptop</a>&nbsp;gi&uacute;p tổng thể tr&ocirc;ng đẹp mắt nhưng vẫn đảm bảo việc kết nối, truyền tải dữ liệu trong qu&aacute; tr&igrave;nh sử dụng. Đảm bảo đường truyền mạng lu&ocirc;n trong trạng th&aacute;i ổn định nhờ&nbsp;<strong>Wi-Fi 6E (802.11ax)</strong>, kết nối kh&ocirc;ng d&acirc;y nhanh ch&oacute;ng th&ocirc;ng qua&nbsp;<strong>Bluetooth 5.3</strong>.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-3-1.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 Pro 2023 - Kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-3-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-3-1.jpg\" title=\"MacBook Pro M2 Pro 2023 - Kết nối\" /></a></p>\r\n\r\n<h3>L&agrave;m việc, giải tr&iacute; trọn vẹn với m&agrave;n h&igrave;nh chất lượng cao</h3>\r\n\r\n<p>Sở hữu m&agrave;n h&igrave;nh&nbsp;<strong>16.2 inch</strong>&nbsp;mang đến kh&ocirc;ng gian hiển thị rộng r&atilde;i, độ ph&acirc;n giải&nbsp;<strong>Liquid Retina XDR display (3456 x 2234)</strong>&nbsp;mang đến những nội dung v&ocirc; c&ugrave;ng sắc n&eacute;t, kết hợp c&ugrave;ng c&ocirc;ng nghệ&nbsp;<strong>Wide color (P3)</strong>&nbsp;cho m&agrave;u sắc v&ocirc; c&ugrave;ng sống động, chuẩn x&aacute;c tr&ecirc;n từng chi tiết, ph&ugrave; hợp với người d&ugrave;ng l&agrave;m trong lĩnh vực thiết kế.</p>\r\n\r\n<p>Khả năng hiển thị nội dung r&otilde; n&eacute;t ở nhiều m&ocirc;i trường s&aacute;ng kh&aacute;c nhau nhờ độ s&aacute;ng&nbsp;<strong>1000 nits</strong>&nbsp;đảm bảo cho người d&ugrave;ng l&agrave;m việc hiệu quả ở bất kỳ đ&acirc;u. C&ocirc;ng nghệ<strong>&nbsp;True Tone</strong>&nbsp;tr&ecirc;n MacBook Pro 2023 c&oacute; khả năng tự động điều chỉnh độ s&aacute;ng m&agrave;n h&igrave;nh t&ugrave;y theo m&ocirc;i trường xung quanh cung cấp cho người d&ugrave;ng những h&igrave;nh ảnh tự nhi&ecirc;n nhất.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-6-1.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 Pro 2023 - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-6-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-6-1.jpg\" title=\"MacBook Pro M2 Pro 2023 - Màn hình\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh c&ograve;n c&oacute;&nbsp;<strong>tấn số qu&eacute;t l&ecirc;n đến 120 Hz&nbsp;</strong>kết hợp c&ocirc;ng nghệ&nbsp;<strong>ProMotion</strong>&nbsp;tự động điều chỉnh để ph&ugrave; hợp với chuyển động của nội dung đ&aacute;p ứng nhu cầu giải tr&iacute; với c&aacute;c tựa game, l&agrave;m đồ họa,... khi chỉnh sửa video bạn c&ograve;n c&oacute; thể chọn tốc độ l&agrave;m mới khung h&igrave;nh cho ph&ugrave; hợp với từng đoạn phim.</p>\r\n\r\n<p>Hệ thống &acirc;m đa chiều&nbsp;<strong>Dolby Atmos</strong>&nbsp;c&ugrave;ng c&ocirc;ng nghệ&nbsp;<strong>Wide stereo sound&nbsp;</strong>v&agrave;&nbsp;<strong>Spatial Audio</strong>&nbsp;gi&uacute;p người d&ugrave;ng đắm ch&igrave;m v&agrave;o kh&ocirc;ng gian &acirc;m thanh sống động, thưởng thức những giai điệu cuốn h&uacute;t hoặc những thước phim h&agrave;nh động kịch t&iacute;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-7-1.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 Pro 2023 - Âm thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-7-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/302149/macbook-pro-16-inch-m2-pro-7-1.jpg\" title=\"MacBook Pro M2 Pro 2023 - Âm thanh\" /></a></p>\r\n\r\n<p>Với thiết kế thời thượng, hiệu năng mạnh mẽ đến từ con chip Apple M2 Pro đ&aacute;p ứng được mọi t&aacute;c vụ chiếc MacBook Pro 16 inch M2 Pro 2023 19-core GPU chắc chắn l&agrave; chiếc m&aacute;y t&iacute;nh x&aacute;ch tay ho&agrave;n hảo để đồng h&agrave;nh c&ugrave;ng bạn trong c&ocirc;ng việc, cuộc sống.&nbsp;</p>\r\n', 'Đã đăng', 3, '08-09-2023', 0),
(55, 'VHL#208', 'MacBook Pro 13 inch M2 2022', 40190000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'macbook8.jpg', '<h3>Sự đẳng cấp của nh&agrave; Apple l&agrave; điều kh&ocirc;ng thể phủ nhận khi n&oacute; lu&ocirc;n c&oacute; những bước cải tiến nổi bật qua từng năm. V&agrave; đến năm 2022, sự xuất hiện của con chip M2 tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/laptop/apple-macbook-pro-m2-2022-16gb\" target=\"_blank\" title=\"Laptop Apple MacBook Pro 13 inch M2 2022 16GB/512GB/10-core GPU đang bán tại thegioididong.com\">MacBook Pro M2 16GB</a>&nbsp;l&agrave; một dấu ấn mạnh mẽ khẳng định vị thế của &quot;nh&agrave; T&aacute;o&quot; trong ph&acirc;n kh&uacute;c&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=do-hoa-ky-thuat\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop đồ họa - kỹ thuật hiện đang kinh doanh tại thegioididong.com\">laptop đồ họa - kỹ thuật</a>.&nbsp;&nbsp;</h3>\r\n\r\n<h3>Hiệu năng vượt bậc với con chip M2 cải tiến</h3>\r\n\r\n<p>D&ugrave; vẫn sở hữu tiến tr&igrave;nh&nbsp;<strong>5 nm</strong>&nbsp;nhưng&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-m2\" target=\"_blank\" title=\"Xem thêm các mẫu MacBook M2 hiện đang kinh doanh tại thegioididong.com\">MacBook M2</a>&nbsp;đ&atilde; c&oacute; những cải tiến đ&aacute;ng kể hơn so với chip M1 khi sở hữu&nbsp;<strong>20 ngh&igrave;n tỷ b&oacute;ng b&aacute;n dẫn</strong>&nbsp;v&agrave; băng th&ocirc;ng bộ nhớ l&ecirc;n đến&nbsp;<strong>100 GB/s,</strong>&nbsp;nhiều hơn&nbsp;<strong>50%</strong>&nbsp;so với thế hệ tiền nhiệm, mang đến khả năng xử l&yacute; mọi t&aacute;c vụ một c&aacute;ch đ&aacute;ng kinh ngạc nhưng vẫn tiết kiệm năng lượng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-10.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 2022 - Chip Apple M2\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-10.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-10.jpg\" title=\"MacBook Pro M2 2022 - Chip Apple M2\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-apple-macbook-pro\" target=\"_blank\" title=\"Xem thêm các mẫu MacBook Pro hiện đang kinh doanh tại thegioididong.com\">MacBook Pro</a>&nbsp;thống trị ph&acirc;n kh&uacute;c đồ họa, kỹ thuật khi giải quyết mượt m&agrave; c&aacute;c c&ocirc;ng việc chỉnh ảnh, thiết kế hay render video tr&ecirc;n những phần mềm đồ họa của nh&agrave; Adobe như Photoshop, Illustrator, Premiere,... nhờ&nbsp;<strong>10 nh&acirc;n GPU</strong>&nbsp;mạnh mẽ, cho hiệu suất hoạt động cao hơn&nbsp;<strong>35%</strong>&nbsp;so với chip M1 v&agrave; khả năng xử l&yacute; l&ecirc;n đến&nbsp;<strong>15.8 ngh&igrave;n tỷ</strong>&nbsp;t&aacute;c vụ trong&nbsp;<strong>1 gi&acirc;y</strong>&nbsp;đ&aacute;ng kinh ngạc.&nbsp;</p>\r\n\r\n<p>Đa nhiệm mượt m&agrave; ngo&agrave;i tầm mong đợi với bộ nhớ&nbsp;<strong>RAM 16 GB</strong>, cho ph&eacute;p bạn l&agrave;m việc tr&ecirc;n nhiều cửa sổ Safari c&ugrave;ng l&uacute;c hay vừa l&agrave;m layer đồ họa vừa nghe nhạc thư gi&atilde;n vẫn rất su&ocirc;n sẻ, kh&ocirc;ng giật lag. B&ecirc;n cạnh đ&oacute; c&ograve;n gia tăng tốc độ đọc ghi cũng như thời gian khởi động m&aacute;y nhanh ch&oacute;ng hơn nhờ ổ cứng&nbsp;<strong>SSD 512 GB</strong>.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-3-2.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 2022 - Cấu hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-3-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-3-2.jpg\" title=\"MacBook Pro M2 2022 - Cấu hình\" /></a></p>\r\n\r\n<h3>Giải tr&iacute; trọn vẹn với m&agrave;n h&igrave;nh Retina sắc n&eacute;t</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-retina\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop độ phân giải Retina hiện đang kinh doanh tại thegioididong.com\">Laptop m&agrave;n h&igrave;nh Retina (2560 x 1600)</a>&nbsp;c&oacute; k&iacute;ch thước&nbsp;<strong>13.3 inch</strong>&nbsp;cho bạn trải nghiệm h&igrave;nh ảnh ch&acirc;n thực tr&ecirc;n từng chi tiết, kết hợp&nbsp;<strong>dải m&agrave;u rộng P3</strong>&nbsp;c&ugrave;ng độ s&aacute;ng&nbsp;<strong>500 nits</strong>&nbsp;gi&uacute;p t&aacute;i tạo m&agrave;u sắc chuẩn x&aacute;c, mang đến kh&ocirc;ng giản giải tr&iacute; tuyệt đỉnh tr&ecirc;n từng tập phim h&agrave;nh động hấp dẫn v&agrave; đặc biệt l&agrave; cực kỳ ph&ugrave; hợp với người d&ugrave;ng s&aacute;ng tạo, thiết kế.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-5-2.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 2022 - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-5-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-5-2.jpg\" title=\"MacBook Pro M2 2022 - Màn hình\" /></a></p>\r\n\r\n<p>C&ocirc;ng nghệ&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-cong-nghe-man-hinh-true-tone-992705\" target=\"_blank\" title=\"Tìm hiểu thêm về công nghệ True Tone\">True Tone</a>&nbsp;tự động điều chỉnh &aacute;nh s&aacute;ng của m&aacute;y tương đồng với m&agrave;u sắc của m&ocirc;i trường xung quanh, mang đến những hiển thị khung h&igrave;nh tự nhi&ecirc;n nhất c&oacute; thể, từ đ&oacute; bảo vệ thị lực của người d&ugrave;ng tốt hơn.&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop hiện đang kinh doanh tại thegioididong.com\">Laptop</a>&nbsp;c&ograve;n hạn chế tối đa hiện tượng biến đổi m&agrave;u sắc khi nh&igrave;n ở nhiều vị tr&iacute; kh&aacute;c nhau nhờ&nbsp;<strong>tấm nền IPS</strong>&nbsp;ti&ecirc;n tiến.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-6-2.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 2022 - Độ tái tạo màu\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-6-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-6-2.jpg\" title=\"MacBook Pro M2 2022 - Độ tái tạo màu\" /></a></p>\r\n\r\n<p>Hệ thống v&ograve;m&nbsp;<strong>Dolby Atmos</strong>,&nbsp;<strong>loa k&eacute;p (2 k&ecirc;nh)</strong>&nbsp;c&ugrave;ng c&ocirc;ng nghệ&nbsp;<strong>Wide stereo sound</strong>&nbsp;được trang bị tr&ecirc;n laptop cho chất &acirc;m sống động, chiều s&acirc;u, chiều rộng cũng như độ cao của &acirc;m được gia tăng đ&aacute;ng kể, đắm ch&igrave;m bạn v&agrave;o những b&agrave;i nhạc remix cực chất hay c&acirc;n lu&ocirc;n c&aacute;c t&aacute;c phẩm Lofi du dương.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-7.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 2022 - Âm thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-7.jpg\" title=\"MacBook Pro M2 2022 - Âm thanh\" /></a></p>\r\n\r\n<h3>Tối giản nhưng kh&ocirc;ng k&eacute;m phần thời trang, đẳng cấp</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop Apple hiện đang kinh doanh tại thegioididong.com\">Laptop Apple</a>&nbsp;vẫn giữ phong th&aacute;i sang trọng với lớp vỏ&nbsp;<strong>kim loại nguy&ecirc;n khối</strong>&nbsp;cứng c&aacute;p, c&aacute;c cạnh g&oacute;c bo tr&ograve;n tinh tế v&agrave; chiếc logo t&aacute;o khuyết huyền thoại nằm giữa m&aacute;y, tạo n&ecirc;n một n&eacute;t đẹp đặc trưng, đẳng cấp v&agrave; thu h&uacute;t mọi &aacute;nh nh&igrave;n mỗi khi n&oacute; xuất hiện. C&acirc;n nặng&nbsp;<strong>1.4 kg</strong>&nbsp;cho độ di động cao, sẵn s&agrave;ng c&ugrave;ng người d&ugrave;ng di chuyển đến mọi chuyến c&ocirc;ng t&aacute;c xa.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-2-2.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 2022 - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-2-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-2-2.jpg\" title=\"MacBook Pro M2 2022 - Thiết kế\" /></a></p>\r\n\r\n<p>Mở kh&oacute;a nhanh ch&oacute;ng chỉ bằng 1 chạm nhờ t&iacute;nh năng&nbsp;<strong>bảo mật v&acirc;n tay</strong>&nbsp;được t&iacute;ch hợp tr&ecirc;n n&uacute;t nguồn của m&aacute;y, cho thao t&aacute;c đăng nhập dễ d&agrave;ng hơn c&aacute;c loại bảo mật truyền thống nhưng vẫn đảm bảo độ an to&agrave;n cho dữ liệu c&aacute; nh&acirc;n của người d&ugrave;ng.</p>\r\n\r\n<p>Trải nghiệm cảm gi&aacute;c g&otilde; ph&iacute;m &ecirc;m tay, y&ecirc;n tĩnh nhờ h&agrave;nh tr&igrave;nh ổn định v&agrave; bố cục ph&iacute;m sắp xếp hợp l&yacute;, hạn chế tối đa việc ảnh hưởng tiếng ồn đến những người xung quanh. Thanh&nbsp;<strong>Touch Bar (thanh cảm ứng)</strong>&nbsp;tr&ecirc;n m&aacute;y cho thao t&aacute;c sử dụng c&aacute;c tiện &iacute;ch như tăng giảm độ s&aacute;ng, tua nhanh video,... được thuận tiện v&agrave; nhanh ch&oacute;ng hơn rất nhiều. M&aacute;y c&ograve;n trang bị&nbsp;<strong>đ&egrave;n nền</strong>&nbsp;b&agrave;n ph&iacute;m hỗ trợ bạn l&agrave;m việc trong m&ocirc;i trường thiếu s&aacute;ng tốt hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-4-2.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 2022 - Bàn phím\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-4-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-4-2.jpg\" title=\"MacBook Pro M2 2022 - Bàn phím\" /></a></p>\r\n\r\n<p>Cổng Thunderbolt 3 v&agrave; Jack tai nghe 3.5 mm đ&atilde; mang đến sự tinh gọn cho m&aacute;y, gi&uacute;p tổng thể tối giản đẹp mắt nhưng vẫn đảm bảo cho qu&aacute; tr&igrave;nh kết nối cũng như truy xuất dữ liệu được thực hiện dễ d&agrave;ng v&agrave; nhanh ch&oacute;ng với băng th&ocirc;ng l&ecirc;n đến&nbsp;<strong>40 Gbps</strong>. Đảm bảo đường truyền mạng lu&ocirc;n trong trạng th&aacute;i ổn định nhờ&nbsp;<strong>Bluetooth 5.0</strong>&nbsp;kết hợp với&nbsp;<strong>Wi-Fi 6 (802.11ax)</strong>.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-9.jpg\" onclick=\"return false;\"><img alt=\"MacBook Pro M2 2022 - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/283464/apple-macbook-pro-m2-2022-16gb-9.jpg\" title=\"MacBook Pro M2 2022 - Cổng kết nối\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/laptop/apple-macbook-pro-13-inch-m2-2022\" target=\"_blank\" title=\"Laptop MacBook Pro M2 2022 hiện đang kinh doanh tại thegioididong.com\">MacBook Pro M2</a>&nbsp;13 inch 2022 chưa bao giờ l&agrave;m c&aacute;c t&iacute;n đồ nh&agrave; T&aacute;o thất vọng khi lu&ocirc;n c&oacute; những sự cải thiện r&otilde; rệt về hiệu năng m&aacute;y. B&ecirc;n cạnh đ&oacute;, lối thiết kế thời thượng, đẳng cấp c&ugrave;ng m&agrave;n h&igrave;nh Retina tuyệt đỉnh cũng l&agrave; một trong những l&iacute; do m&agrave; bạn n&ecirc;n sở hữu ngay em MacBook n&agrave;y.</p>\r\n', 'Đã đăng', 3, '08-09-2023', 0);
INSERT INTO `list_products` (`product_id`, `product_code`, `product_name`, `price`, `product_desc`, `product_thumb`, `product_content`, `status`, `cat_id`, `created_date`, `star`) VALUES
(56, 'VHL#209', 'MacBook Air 13 inch M2 2022', 39190000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'macbook9.jpg', '<h3><a href=\"https://www.thegioididong.com/laptop/apple-macbook-air-m2-2022-16gb\" target=\"_blank\" title=\"Laptop Apple MacBook Air M2 2022 16GB/512GB/10-core GPU đang bán tại thegioididong.com\">MacBook Air M2 2022</a>&nbsp;một lần nữa đ&atilde; khẳng định vị thế h&agrave;ng đầu của Apple trong ph&acirc;n kh&uacute;c&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=cao-cap-sang-trong\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop cao cấp - sang trọng hiện đang kinh doanh tại thegioididong.com\">laptop cao cấp - sang trọng</a>&nbsp;v&agrave;o giữa năm 2022 khi sở hữu phong c&aacute;ch thiết kế thời thượng, đẳng cấp c&ugrave;ng sức mạnh bộc ph&aacute; đến từ bộ vi xử l&yacute; Apple M2 mạnh mẽ.</h3>\r\n\r\n<h3>Kh&aacute;m ph&aacute; nguồn sức mạnh từ d&ograve;ng chip thế hệ mới</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tin-tuc/chip-apple-m2-chinh-thuc-ra-mat-voi-suc-manh-khung-1437839\" target=\"_blank\" title=\"Tìm hiểu thêm về chip Apple M2\">Chip Apple M2</a>&nbsp;vẫn được sản xuất ở tiến tr&igrave;nh&nbsp;<strong>5 nm</strong>&nbsp;với&nbsp;<strong>4 nh&acirc;n hiệu năng cao</strong>&nbsp;v&agrave;&nbsp;<strong>4 nh&acirc;n tiết kiệm kiệm</strong>&nbsp;như d&ograve;ng M1 nhưng tốc độ băng th&ocirc;ng đ&atilde; được cải tiến vượt trội l&ecirc;n đến&nbsp;<strong>100GB/s</strong>, c&ugrave;ng với đ&oacute; l&agrave; sự trợ gi&uacute;p của&nbsp;<strong>20 ngh&igrave;n tỷ b&oacute;ng b&aacute;n dẫn</strong>&nbsp;gi&uacute;p hiệu suất hoạt động được n&acirc;ng cao hơn&nbsp;<strong>18%</strong>&nbsp;so với phi&ecirc;n bản tiền nhiệm, đảm bảo vận h&agrave;nh trơn tru mọi t&aacute;c vụ học tập, l&agrave;m việc từ cơ bản đến n&acirc;ng cao.&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng chỉ giải quyết ho&agrave;n hảo những t&aacute;c vụ th&ocirc;ng thường,&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-apple-m2\" target=\"_blank\" title=\"Xem thêm các mẫu MacBook M2 hiện đang kinh doanh tại thegioididong.com\">MacBook M2</a>&nbsp;c&ograve;n chinh phục người d&ugrave;ng s&aacute;ng tạo khi trang bị&nbsp;<strong>10 nh&acirc;n GPU</strong>&nbsp;với khả năng xử l&yacute; l&ecirc;n đến&nbsp;<strong>15.8 ngh&igrave;n tỷ</strong>&nbsp;t&aacute;c vụ chỉ trong&nbsp;<strong>1 gi&acirc;y</strong>, cho ph&eacute;p bạn thao t&aacute;c mượt m&agrave; c&aacute;c ứng dụng đồ họa như Adobe Illustrator, Premiere, AutoCAD,... cũng như chỉnh sửa h&igrave;nh ảnh, ph&aacute;t video&nbsp;<strong>4K, 8K ProRes 12</strong>&nbsp;ấn tượng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-3.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 2022 10-core GPU - Cấu hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-3.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-3.jpg\" title=\"MacBook Air M2 2022 10-core GPU - Cấu hình\" /></a></p>\r\n\r\n<p>Khả năng đa nhiệm được n&acirc;ng tầm đ&aacute;ng kể nhờ bộ nhớ&nbsp;<strong>RAM 16 GB</strong>, người d&ugrave;ng ho&agrave;n to&agrave;n c&oacute; thể xử l&yacute; nhiều layer đồ họa kh&aacute;c nhau song song với việc nghe nhạc tr&ecirc;n Spotify nhưng vẫn kh&ocirc;ng xảy ra hiện tượng giật lag. Ổ cứng&nbsp;<strong>SSD 512 GB</strong>&nbsp;cho kh&ocirc;ng gian lưu trữ ổn định đồng thời gia tăng tốc độ đọc ghi nhanh ch&oacute;ng hơn.</p>\r\n\r\n<p>Hệ điều h&agrave;nh&nbsp;<strong>MacOS</strong>&nbsp;c&ugrave;ng vi xử l&yacute;&nbsp;<strong>Apple Silicon</strong>&nbsp;kh&ocirc;ng chỉ mang đến một kho ứng dụng l&agrave;m việc, giải tr&iacute; đồ sộ m&agrave; c&ograve;n n&acirc;ng cao hệ thống bảo mật của m&aacute;y, cho ph&eacute;p người d&ugrave;ng kiểm so&aacute;t chặt chẽ hơn về quyền ri&ecirc;ng tư cũng như c&aacute;c th&ocirc;ng tin hay dữ liệu c&aacute; nh&acirc;n.</p>\r\n\r\n<h3>Ngoại h&igrave;nh thời thượng, biểu tượng cho sự ho&agrave;n mỹ</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-air\" target=\"_blank\" title=\"Xem thêm các mẫu MacBook Air hiện đang kinh doanh tại thegioididong.com\">MacBook Air</a>&nbsp;năm nay đ&atilde; kho&aacute;c l&ecirc;n m&igrave;nh một diện mạo ho&agrave;n to&agrave;n mới với xu hướng thiết kế c&oacute; phần tương tự như MacBook Pro, lớp&nbsp;<strong>vỏ kim loại</strong>&nbsp;sang trọng, cứng c&aacute;p với khối lượng chỉ vỏn vẹn&nbsp;<strong>1.24 kg</strong>&nbsp;lu&ocirc;n l&agrave; trợ thủ đắc lực cho mọi cuộc h&agrave;nh tr&igrave;nh của người d&ugrave;ng d&ugrave; l&agrave; trong kh&ocirc;ng gian văn ph&ograve;ng hay những chuyến c&ocirc;ng t&aacute;c xa.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-2.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 2022 10-core GPU - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-2.jpg\" title=\"MacBook Air M2 2022 10-core GPU - Thiết kế\" /></a></p>\r\n\r\n<p>B&agrave;n ph&iacute;m&nbsp;<strong>Magic Key</strong>&nbsp;với d&atilde;y ph&iacute;m F chức năng gi&uacute;p cho thao t&aacute;c soạn thảo hay nhập liệu được dễ d&agrave;ng v&agrave; tiện lợi hơn. MacBook lu&ocirc;n l&agrave; sự lựa chọn h&agrave;ng đầu cho những người l&agrave;m việc văn ph&ograve;ng hay y&ecirc;u th&iacute;ch sự y&ecirc;n tĩnh với trải nghiệm g&otilde; ph&iacute;m cực &ecirc;m tay v&agrave; hạn chế tối đa việc ảnh hưởng tiếng ồn đến những người xung quanh.&nbsp;<a href=\"https://www.thegioididong.com/laptop-den-ban-phim\" target=\"_blank\" title=\"Xem thêm các mẫu Laptop có đèn bàn phím hiện đang kinh doanh tại thegioigidong.com\">Laptop c&oacute; đ&egrave;n b&agrave;n ph&iacute;m</a>&nbsp;hỗ trợ người d&ugrave;ng l&agrave;m việc tốt hơn trong m&ocirc;i trường thiếu s&aacute;ng.</p>\r\n\r\n<p>T&iacute;nh năng&nbsp;<strong>bảo mật v&acirc;n tay</strong>&nbsp;được t&iacute;ch hợp với ph&iacute;m nguồn của&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Tham khảo các mẫu laptop tai Thegioididong.com\">laptop</a>&nbsp;vừa gi&uacute;p bạn mở m&aacute;y nhanh ch&oacute;ng chỉ bằng một chạm vừa củng cố th&ecirc;m hệ thống bảo mật cũng như quyền ri&ecirc;ng tư, hạn chế tối đa việc bị mất th&ocirc;ng tin c&aacute; nh&acirc;n của người d&ugrave;ng.&nbsp;</p>\r\n\r\n<p>2 cổng Thunderbolt 3, Jack tai nghe 3.5 mm c&ugrave;ng MagSafe 3 đ&atilde; mang đến cho m&aacute;y vẻ đẹp tối giản nhưng vẫn đảm bảo truyền tải mọi dữ liệu hay h&igrave;nh ảnh, video,... đến c&aacute;c thiết bị ngoại vi kh&aacute;c một c&aacute;ch nhanh ch&oacute;ng v&agrave; dễ d&agrave;ng.&nbsp;<strong>1080p FaceTime HD camera</strong>&nbsp;c&ugrave;ng&nbsp;<strong>3 microphones</strong>&nbsp;hỗ trợ tối đa cho qu&aacute; tr&igrave;nh học tập v&agrave; l&agrave;m việc của bạn với chất lượng h&igrave;nh ảnh kết xuất sắc n&eacute;t c&ugrave;ng giọng n&oacute;i được thu &acirc;m r&otilde; r&agrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-8.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 2022 10-core GPU - Cổng kết nối \" data-src=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-8.jpg\" title=\"MacBook Air M2 2022 10-core GPU - Cổng kết nối \" /></a></p>\r\n\r\n<h3>M&agrave;n h&igrave;nh Retina sắc n&eacute;t cho mọi trải nghiệm ho&agrave;n hảo</h3>\r\n\r\n<p>K&iacute;ch thước m&agrave;n h&igrave;nh&nbsp;<strong>13.6 inch</strong>&nbsp;c&oacute; độ ph&acirc;n giải&nbsp;<strong>Liquid Retina (2560 x 1664)</strong>&nbsp;c&ugrave;ng&nbsp;<strong>1 tỷ m&agrave;u</strong>&nbsp;mang đến cho chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Xem thêm các mẫu MacBook hiện đang kinh doanh tại thegioididong.com\">MacBook</a>&nbsp;những khung ảnh si&ecirc;u sắc n&eacute;t với những gam m&agrave;u chuẩn x&aacute;c, cực kỳ th&iacute;ch hợp cho người d&ugrave;ng khi thực hiện c&aacute;c t&aacute;c vụ chỉnh sửa h&igrave;nh ảnh, video,... hay đắm ch&igrave;m v&agrave;o những thước phim h&agrave;nh động sống động.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-4.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 2022 10-core GPU - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-4.jpg\" title=\"MacBook Air M2 2022 10-core GPU - Màn hình\" /></a></p>\r\n\r\n<p>Độ s&aacute;ng&nbsp;<strong>500 nits</strong>&nbsp;gi&uacute;p bạn ho&agrave;n to&agrave;n tự tin khi sử dụng m&aacute;y ở những điều kiện m&ocirc;i trường nắng gắt nhưng vẫn nh&igrave;n r&otilde; được nội dung hiển thị tr&ecirc;n m&agrave;n h&igrave;nh. Dải m&agrave;u rộng&nbsp;<strong>P3</strong>&nbsp;c&ugrave;ng c&ocirc;ng nghệ&nbsp;<strong>True Tone</strong>&nbsp;cho c&aacute;c khung ảnh được t&aacute;i tạo chuy&ecirc;n nghiệp v&agrave; ch&acirc;n thực nhất so với điều kiện &aacute;nh s&aacute;ng xung quanh.&nbsp;</p>\r\n\r\n<p>Hệ thống &acirc;m thanh v&ograve;m đa chiều&nbsp;<strong>Dolby Atmos</strong>,&nbsp;<strong>Spatial Audio</strong>&nbsp;kết hợp c&ugrave;ng&nbsp;<strong>4 loa</strong>&nbsp;mang đến trọn vẹn những trải nghiệm giải tr&iacute; ch&acirc;n thực với chất &acirc;m sống động, &acirc;m bass to r&otilde;, độ khuếch đại &acirc;m thanh lớn gi&uacute;p bạn h&ograve;a m&igrave;nh v&agrave;o những bản t&igrave;nh ca du dương hay c&aacute;c tập phim h&agrave;nh động kịch t&iacute;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-6.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air M2 2022 10-core GPU - Âm thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/289441/vi-vn-apple-macbook-air-m2-2022-16gb-ab-6.jpg\" title=\"MacBook Air M2 2022 10-core GPU - Âm thanh\" /></a></p>\r\n\r\n<p>MacBook Air M2 2022 sẽ l&agrave; một đối thủ đ&aacute;ng gờm trong c&ugrave;ng ph&acirc;n kh&uacute;c khi sở hữu một vẻ ngo&agrave;i ki&ecirc;u kỳ, ho&agrave;n mỹ, m&agrave;n h&igrave;nh Retina sắc n&eacute;t c&ugrave;ng hiệu năng ấn tượng đến từ chip Apple M2 t&acirc;n tiến, hứa hẹn kh&ocirc;ng l&agrave;m c&aacute;c t&iacute;n đồ nh&agrave; T&aacute;o thất vọng.</p>\r\n', 'Đã đăng', 3, '08-09-2023', 0),
(57, 'VHL#210', 'MacBook Air 15 inch M2 2023', 37990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'macbook10.jpg', '<h3>Sự gọn nhẹ trong thiết kế c&ugrave;ng t&iacute;nh linh hoạt trong hiệu năng lu&ocirc;n l&agrave; những ti&ecirc;u ch&iacute; ấn định với d&ograve;ng Macbook Air của nh&agrave; Apple.&nbsp;<a href=\"https://www.thegioididong.com/laptop/apple-macbook-air-15-inch-m2-2023\" target=\"_blank\" title=\"Tham khảo MacBook Air 15 inch M2 2023 8CPU/8GB/512GB/10GPU tại thegioididong.com\">MacBook Air 15 inch M2 2023 512 GB</a>&nbsp;đ&atilde; quay trở lại với phi&ecirc;n bản ho&agrave;n to&agrave;n mới v&agrave;o năm nay, mang đến kh&ocirc;ng gian trải nghiệm rộng lớn cho người d&ugrave;ng.</h3>\r\n\r\n<h3>Hiệu năng mạnh mẽ từ d&ograve;ng chip Apple M2 thế hệ mới</h3>\r\n\r\n<p>Hiệu năng mạnh mẽ đến từ d&ograve;ng chip&nbsp;<strong>M2</strong>&nbsp;phi&ecirc;n bản mới của nh&agrave; Apple giống với c&aacute;c mẫu&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-air\" target=\"_blank\" title=\"Tham khảo các mẫu laptop Macbook Air tại thegioididong.com\">Macbook Air</a>&nbsp;2022, được thiết kế bằng c&ocirc;ng nghệ&nbsp;<strong>5 nm</strong>,&nbsp;sở hữu&nbsp;<strong>20 tỷ b&oacute;ng b&aacute;n dẫn</strong>, xử l&yacute; c&ocirc;ng việc nhanh hơn&nbsp;<strong>18% so với M1</strong>&nbsp;cho tốc độ băng th&ocirc;ng l&ecirc;n đến&nbsp;<strong>100 GB/s</strong>.&nbsp;MacBook Air 15 inch M2 2023 cho khả năng xử l&yacute; cực mượt m&agrave; mọi t&aacute;c vụ học tập, l&agrave;m việc hay giải tr&iacute;.</p>\r\n\r\n<p>Hiệu năng của chiếc&nbsp;<strong>MacBook Air 15 inch M2 2023&nbsp;</strong>n&agrave;y c&ograve;n linh hoạt v&agrave; hiệu quả trong xử l&yacute; c&aacute;c t&aacute;c vụ xử l&yacute; đồ họa hay thiết kế với&nbsp;<strong>card t&iacute;ch hợp</strong>&nbsp;v&agrave;<strong>&nbsp;10 nh&acirc;n GPU.</strong>&nbsp;Với hiệu suất hoạt động cao hơn 35% khi so với chip M1 c&aacute;c c&ocirc;ng việc thực hiện hay xử l&yacute; li&ecirc;n quan đến đồ họa như chỉnh ảnh, thiết kế, render video,... đều kh&ocirc;ng th&agrave;nh vấn đề.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-060623-082740.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 - Hiệu năng\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-060623-082740.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-060623-082740.jpg\" title=\"MacBook Air 15 inch M2 2023 - Hiệu năng\" /></a></p>\r\n\r\n<p>Khả năng đa nhiệm trơn tru đến từ bộ nhớ&nbsp;<strong>RAM 8 GB</strong>&nbsp;gi&uacute;p người d&ugrave;ng c&oacute; thể thoải m&aacute;i thao t&aacute;c với c&aacute;c ứng dụng đồ họa, phần mềm nặng m&agrave; kh&ocirc;ng bắt gặp hiện tượng giật lag, gi&aacute;n đoạn. Ổ cứng&nbsp;<strong>SSD 512 GB</strong>&nbsp;mang đến kh&ocirc;ng gian lưu trữ rộng lớn v&agrave; ổn định cho c&aacute;c tệp tin, ứng dụng.</p>\r\n\r\n<p>L&agrave;m việc dễ d&agrave;ng v&agrave; hiệu quả hơn với giao diện được thiết kế tối giản, đẹp mắt tr&ecirc;n hệ điều h&agrave;nh&nbsp;<strong>macOS</strong>&nbsp;c&ugrave;ng th&agrave;nh c&ocirc;ng cụ được nằm gọn g&agrave;ng ở giữa m&agrave;n h&igrave;nh gi&uacute;p bạn t&igrave;m kiếm được ch&iacute;nh x&aacute;c nội dung mong muốn đồng thời lu&ocirc;n cập nhật kho ứng dụng khổng lồ để đ&aacute;p ứng mọi nhu cầu của bạn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-eight.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 512 GB - MacOS\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-eight.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-eight.jpg\" title=\"MacBook Air 15 inch M2 2023 512 GB - MacOS\" /></a></p>\r\n\r\n<h3>Kh&ocirc;ng gian sống 15 inch, bắt trọn từng khoảnh khắc</h3>\r\n\r\n<p>Nếu bạn đ&atilde; y&ecirc;u th&iacute;ch d&ograve;ng Macbook Air m&agrave; vẫn chưa ưng &yacute; về chiếc m&agrave;n h&igrave;nh kh&ocirc;ng qu&aacute; to của n&oacute; th&igrave; đừng lo lắng, với phi&ecirc;n bản mới nhất n&agrave;y -&nbsp;<strong>MacBook Air 15 inch M2 2023</strong>&nbsp;đ&atilde; được trang bị m&agrave;n h&igrave;nh với k&iacute;ch thước&nbsp;<strong>15.3 inch&nbsp;</strong>c&ugrave;ng tấm nền<strong>&nbsp;IPS&nbsp;</strong>c&oacute; độ ph&acirc;n giải&nbsp;<strong>Liquid Retina (2880 x 1864)&nbsp;</strong>mang đến kh&ocirc;ng gian h&igrave;nh ảnh bao trọn c&ugrave;ng g&oacute;c nh&igrave;n rộng lớn, người d&ugrave;ng tha hồ tận hưởng chất lượng nội dung sắc n&eacute;t v&agrave; đắm ch&igrave;m.</p>\r\n\r\n<p>Dải m&agrave;u rộng&nbsp;<strong>P3</strong>&nbsp;c&ugrave;ng độ hiển thị&nbsp;<strong>1 tỷ m&agrave;u&nbsp;</strong>cho ph&eacute;p t&aacute;i tạo c&aacute;c khung h&igrave;nh chuẩn x&aacute;c, độ ch&acirc;n thực cao v&agrave; hỗ trợ rất tốt thi thực hiện c&aacute;c t&aacute;c vụ đồ họa. C&ocirc;ng nghệ<strong>&nbsp;True Tone</strong>&nbsp;hiện đại, cho ph&eacute;p m&aacute;y tự động điều chỉnh &aacute;nh s&aacute;ng sao cho ph&ugrave; hợp với m&ocirc;i trường tự nhi&ecirc;n m&agrave; vẫn đảm bảo cho c&aacute;c nội dung tr&ecirc;n m&agrave;n h&igrave;nh được hiển thị r&otilde; n&eacute;t, chi tiết nhất.&nbsp;&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-060623-082732.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-060623-082732.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-060623-082732.jpg\" title=\"MacBook Air 15 inch M2 2023 - Màn hình\" /></a></p>\r\n\r\n<p>Hệ thống gồm<strong>&nbsp;6 loa</strong>&nbsp;được t&iacute;ch hợp với c&aacute;c c&ocirc;ng nghệ&nbsp;<strong>Spatial Audio</strong>&nbsp;v&agrave;&nbsp;<strong>Dolby Atmos&nbsp;</strong>mang đến cho chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Tham khảo các mẫu Macbook tại thegioididong.com\">MacBook</a>&nbsp;n&agrave;y khả năng khuếch t&aacute;n &acirc;m tuyệt vời, &acirc;m thanh được t&aacute;i tạo đa chiều mang đến sức sống động cho ph&eacute;p người d&ugrave;ng ch&igrave;m đắm s&acirc;u v&agrave;o những bản nhạc hay bộ phim.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-twelve.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 512 GB - Âm thanh \" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-twelve.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-twelve.jpg\" title=\"MacBook Air 15 inch M2 2023 512 GB - Âm thanh \" /></a></p>\r\n\r\n<h3>Thiết kế sang trọng, cấu tr&uacute;c mỏng nhẹ</h3>\r\n\r\n<p>Với bản n&acirc;ng cấp năm 2023,&nbsp;MacBook Air vẫn mang những n&eacute;t thiết kế tai thỏ đặc trưng giống với bản 13 inch 2022, nhưng với sở hữu k&iacute;ch thước lớn hơn c&ugrave;ng&nbsp;<strong>vỏ kim loại nh&ocirc;m t&aacute;i chế 100%</strong>, tạo n&ecirc;n cảm gi&aacute;c sang trọng, bắt mắt hơn khi nh&igrave;n v&agrave;o. N&eacute;t đặc trưng của d&ograve;ng Air xứng đ&aacute;ng l&agrave; một chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=cao-cap-sang-trong\" target=\"_blank\" title=\"Tham khảo các mẫu laptop sang trọng - cao cấp tại thegioididong.com\">laptop cao cấp- sang trọng</a>&nbsp;với độ d&agrave;y chỉ&nbsp;<strong>11.5 mm&nbsp;</strong>v&agrave; khối lượng&nbsp;<strong>1.51 kg</strong>, linh hoạt theo bạn đến bất cứ đ&acirc;u.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-ten.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 512 GB - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-ten.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-ten.jpg\" title=\"MacBook Air 15 inch M2 2023 512 GB - Thiết kế\" /></a></p>\r\n\r\n<p>Phần b&agrave;n ph&iacute;m của d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Tham khảo các mẫu laptop tại Thegioididong.com\">laptop</a>&nbsp;Apple chip M2 với sự tiện &iacute;ch của h&agrave;ng chức năng F, khoảng c&aacute;ch giữa c&aacute;c ph&iacute;m được gia tăng tạo n&ecirc;n t&iacute;nh đồng nhất v&agrave; ph&iacute;m c&oacute; độ nảy tốt c&ugrave;ng t&iacute;nh tĩnh lặng khi g&otilde; gi&uacute;p c&aacute;c trải nghiệm sử dụng của người d&ugrave;ng tăng l&ecirc;n rất nhiều.&nbsp;M&aacute;y c&ograve;n được trang bị&nbsp;<strong>đ&egrave;n nền đơn sắc</strong>&nbsp;gi&uacute;p bạn l&agrave;m việc dễ d&agrave;ng hơn d&ugrave; ở trong m&ocirc;i trường thiếu s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-elevent.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 512 GB - Bàn phím\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-elevent.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-elevent.jpg\" title=\"MacBook Air 15 inch M2 2023 512 GB - Bàn phím\" /></a></p>\r\n\r\n<p>C&aacute;c cổng kết nối bao gồm Jack 3.5 mm, 2 cổng kết nối Thunderbolt 3 v&agrave; sạc nam ch&acirc;m MagSafe 3 cho qu&aacute; tr&igrave;nh truyền tải dữ liệu nhanh hơn với tốc độ l&ecirc;n đến&nbsp;<strong>40 Gbps</strong>, đồng thời hỗ trợ sạc pin nhanh cho m&aacute;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-seven-1.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 512 GB - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-seven-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-seven-1.jpg\" title=\"MacBook Air 15 inch M2 2023 512 GB - Cổng kết nối\" /></a></p>\r\n\r\n<p>Đăng nhập tiện lợi, nhanh ch&oacute;ng với&nbsp;<strong>cảm biến v&acirc;n tay</strong>&nbsp;được t&iacute;ch hợp, th&ocirc;ng tin v&agrave; h&igrave;nh ảnh của người d&ugrave;ng cũng được đảm bảo an to&agrave;n v&agrave; bảo mật.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-nine.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 512 GB - Cảm biến vân tay\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-nine.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309017/apple-macbook-air-15-inch-m2-2023-acv-nine.jpg\" title=\"MacBook Air 15 inch M2 2023 512 GB - Cảm biến vân tay\" /></a></p>\r\n\r\n<p>Apple đ&atilde; th&ocirc;ng tin rằng dung lượng pin của chiếc Macbook n&agrave;y cao đ&aacute;ng kể, hỗ trợ sử dụng &quot;cả ng&agrave;y&quot; với 18 tiếng đồng hồ v&agrave; n&oacute; sẽ thay đổi tuỳ theo nhu cầu sử dụng của bạn.</p>\r\n\r\n<p>Đối với những người y&ecirc;u th&iacute;ch sự gọn nhẹ của d&ograve;ng Macbook Air th&igrave; nay đ&atilde; c&oacute; phi&ecirc;n bản 2023, laptop Apple MacBook Air 15 inch M2 2023 8-core CPU/8GB/512GB/10-core GPU cho kh&ocirc;ng gian trải nghiệm nội dung th&ecirc;m trọn vẹn, hiệu năng mạnh mẽ được giữ vững.&nbsp;</p>\r\n', 'Đã đăng', 3, '08-09-2023', 1),
(58, 'VHL#211', 'MacBook Air 15 inch M2 2023', 37990000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'macbook11.jpg', '<h3>Sự kiện 2023 của nh&agrave; Apple với sự ra mắt c&ugrave;ng diện mạo mới của chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop/apple-macbook-air-m2-2023-16gb\" target=\"_blank\" title=\"Tham khảo MacBook Air 15 inch M2 2023 8CPU/16GB/512GB/10GPU tại thegioididong.com\">MacBook Air 15 inch M2 2023 10-core GPU</a>, c&oacute; kh&ocirc;ng gian trải nghiệm nội dung rộng lớn với m&agrave;n h&igrave;nh 15 inch. Với những người d&ugrave;ng y&ecirc;u th&iacute;ch t&iacute;nh gọn nhẹ cũng như sở hữu hiệu năng mạnh của d&ograve;ng Macbook Air th&igrave; chắc chắn kh&ocirc;ng thể bỏ qua sản phẩm n&agrave;y.</h3>\r\n\r\n<h3>Trải nghiệm kh&ocirc;ng gian sống trọn vẹn với khung h&igrave;nh lớn</h3>\r\n\r\n<p><strong>MacBook Air 15 inch M2 2023</strong>&nbsp;sở hữu m&agrave;n h&igrave;nh c&oacute; k&iacute;ch thước&nbsp;<strong>15.3 inch</strong>&nbsp;được n&acirc;ng cấp đ&aacute;ng kể so với phi&ecirc;n bản cũ, b&ecirc;n cạnh đ&oacute; với tấm nền&nbsp;<strong>IPS&nbsp;</strong>c&oacute; độ ph&acirc;n giải&nbsp;<strong>Liquid Retina (2880 x 1864)</strong>&nbsp;cho ph&eacute;p người d&ugrave;ng c&oacute; thể thoải m&aacute;i tận hưởng những giờ ph&uacute;t giải tr&iacute; hay l&agrave;m việc đầy sống động, h&igrave;nh ảnh v&agrave; nội dung r&otilde; n&eacute;t với kh&ocirc;ng gian hiển thị mở rộng. Ngo&agrave;i ra, c&ocirc;ng nghệ&nbsp;<strong>Wide color (P3)</strong>&nbsp;cho m&agrave;u sắc v&ocirc; c&ugrave;ng chuẩn x&aacute;c, t&aacute;i hiện r&otilde; từng chi tiết, ph&ugrave; hợp với người d&ugrave;ng l&agrave;m trong lĩnh vực thiết kế.</p>\r\n\r\n<p>Khả năng hiển thị nội dung r&otilde; n&eacute;t với nhiều điều kiện s&aacute;ng với độ hiển thị&nbsp;<strong>500 nits</strong>, gi&uacute;p người d&ugrave;ng quan s&aacute;t r&otilde; r&agrave;ng mọi nội dung. C&ocirc;ng nghệ&nbsp;<strong>True Tone</strong>&nbsp;c&oacute; khả năng tự động điều chỉnh độ s&aacute;ng m&agrave;n h&igrave;nh t&ugrave;y theo m&ocirc;i trường xung quanh cung cấp cho người d&ugrave;ng những h&igrave;nh ảnh tự nhi&ecirc;n nhất.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-060623-090604.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-060623-090604.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-060623-090604.jpg\" title=\"MacBook Air 15 inch M2 2023 - Màn hình\" /></a></p>\r\n\r\n<p>Hệ thống &acirc;m gồm<strong>&nbsp;6 loa&nbsp;</strong>mang đến khả năng ph&aacute;t &acirc;m thanh đa chiều c&ugrave;ng tần số &acirc;m cao nhờ t&iacute;ch hợp c&aacute;c c&ocirc;ng nghệ&nbsp;<strong>Spatial Audio</strong>&nbsp;v&agrave;&nbsp;<strong>Dolby Atmos</strong>, cho ph&eacute;p bạn đắm ch&igrave;m v&agrave; tận hưởng s&acirc;u mọi sắc độ &acirc;m thanh được ph&aacute;t ra.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045505.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 10-core GPU - Âm thanh \" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045505.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045505.jpg\" title=\"MacBook Air 15 inch M2 2023 10-core GPU - Âm thanh \" /></a></p>\r\n\r\n<h3>N&eacute;t linh hoạt, thiết kế nhỏ gọn của d&ograve;ng Macbook Air</h3>\r\n\r\n<p>Vỏ được l&agrave;m từ&nbsp;<strong>kim loại nh&ocirc;m t&aacute;i chế 100%&nbsp;</strong>kh&ocirc;ng chỉ th&acirc;n thiện với m&ocirc;i trường, cứng c&aacute;p bảo vệ c&aacute;c linh kiện b&ecirc;n trong m&agrave; c&ograve;n mang đến vẻ ngo&agrave;i v&ocirc; c&ugrave;ng sang trọng. C&aacute;c g&oacute;c được bo cong một c&aacute;ch tinh tế cũng logo &quot;tr&aacute;i t&aacute;o khuyết&quot; được đặt giữa đặc trưng của d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook\" target=\"_blank\" title=\"Tham khảo các mẫu Macbook tại thegioididong.com\">Macbook</a>. Được ho&agrave;n thiện với độ d&agrave;y chỉ&nbsp;<strong>11.5 mm&nbsp;</strong>v&agrave; khối lượng&nbsp;<strong>1.51 kg</strong>, m&aacute;y mang đến độ thuận tiện khi di chuyển, phục vụ tối đa nhu cầu cho người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045509.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 10-core GPU - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045509.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045509.jpg\" title=\"MacBook Air 15 inch M2 2023 10-core GPU - Thiết kế\" /></a></p>\r\n\r\n<p><strong>Magic Keyboard</strong>&nbsp;quen thuộc với h&agrave;ng ph&iacute;m chức năng F quen thuộc, ph&iacute;m c&oacute; độ nảy cao, h&igrave;nh tr&igrave;nh ph&iacute;m ổn định với khoảng c&aacute;ch được bố tr&iacute; hợp l&yacute;, gi&uacute;p ho&agrave;n th&agrave;nh c&ocirc;ng việc nhanh ch&oacute;ng, đem lại trải nghiệm g&otilde; ph&iacute;m &ecirc;m &aacute;i, chuẩn x&aacute;c v&agrave; &quot;tĩnh lặng&quot;. M&aacute;y c&ograve;n trang bị&nbsp;<strong>đ&egrave;n nền</strong>&nbsp;tối ưu phục vụ người d&ugrave;ng d&ugrave; phải l&agrave;m việc trong m&ocirc;i trường thiếu s&aacute;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045507.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 10-core GPU - Bàn phím\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045507.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045507.jpg\" title=\"MacBook Air 15 inch M2 2023 10-core GPU - Bàn phím\" /></a></p>\r\n\r\n<p>Người d&ugrave;ng c&oacute; thể dễ d&agrave;ng truy cập v&agrave;o thiết bị hay mở kh&oacute;a những trang web dễ d&agrave;ng v&agrave; nhanh ch&oacute;ng với chỉ một chạm tr&ecirc;n&nbsp;<strong>cảm biến v&acirc;n tay (Touch ID)</strong>&nbsp;t&iacute;ch hợp sẵn ở n&uacute;t nguồn. T&iacute;nh năng c&ograve;n mang đến độ bảo mật tốt cho c&aacute;c th&ocirc;ng tin v&agrave; h&igrave;nh ảnh của người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045510.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 10-core GPU - Touch ID\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045510.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045510.jpg\" title=\"MacBook Air 15 inch M2 2023 10-core GPU - Touch ID\" /></a></p>\r\n\r\n<p><strong>MacBook Air 15 inch M2 2023</strong>&nbsp;được trang bị đa dạng c&aacute;c cổng kết nối như: Jack tai nghe 3.5 mm, MagSafe 3, Thunderbolt 3 hỗ trợ bạn li&ecirc;n kết với c&aacute;c thiết bị ngoại vi dễ d&agrave;ng v&agrave; nhanh ch&oacute;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045513.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 10-core GPU - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045513.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-070623-045513.jpg\" title=\"MacBook Air 15 inch M2 2023 10-core GPU - Cổng kết nối\" /></a></p>\r\n\r\n<h3>Hiệu năng tuyệt vời, &quot;xử&quot; gọn mọi t&aacute;c vụ</h3>\r\n\r\n<p>Phi&ecirc;n bản 2023 của chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-apple-macbook-air\" target=\"_blank\" title=\"Tham khảo các mẫu Macbook Air tại thegioididong.com\">Macbook Air</a>&nbsp;vẫn được trang bị con chip<strong>&nbsp;M2&nbsp;</strong>mạnh mẽ với thiết kế&nbsp;được sản xuất tr&ecirc;n tiến tr&igrave;nh&nbsp;<strong>5 nm</strong>&nbsp;ho&agrave;n chỉnh với&nbsp;<strong>20 tỷ b&oacute;ng b&aacute;n dẫn</strong>&nbsp;nhiều hơn<strong>&nbsp;25%&nbsp;</strong>so với M1, cho tốc độ băng th&ocirc;ng l&ecirc;n đến&nbsp;<strong>100 GB/s</strong>. M&aacute;y cho khả năng vận h&agrave;nh hiệu quả mọi t&aacute;c vụ từ học tập, l&agrave;m việc đến giải tr&iacute; đa phương tiện.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ thế, chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=cao-cap-sang-trong\" target=\"_blank\" title=\"Tham khảo các mẫu laptop cao cấp - sang trọng tại thegioididong.com\">laptop cao cấp</a>&nbsp;n&agrave;y c&ograve;n cho khả năng s&aacute;ng tạo v&agrave; thiết kế đồ họa đ&aacute;ng kinh ngạc với&nbsp;<strong>card t&iacute;ch hợp 10 nh&acirc;n GPU</strong>. Bạn c&oacute; thể thoải m&aacute;i thực hiện c&aacute;c ấn phẩm h&igrave;nh ảnh, thiết kế 2D hay render video,.... một c&aacute;ch chuy&ecirc;n nghiệp nhất.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-060623-090613.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 - Hiệu năng\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-060623-090613.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-060623-090613.jpg\" title=\"MacBook Air 15 inch M2 2023 - Hiệu năng\" /></a></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, khả năng đa nhiệm tuyệt vời với bộ nhớ&nbsp;<strong>RAM 16 GB</strong>, hỗ trợ người d&ugrave;ng l&agrave;m việc trơn tru ngay cả với c&aacute;c t&aacute;c vụ &quot;nặng k&yacute;&quot; m&agrave; kh&ocirc;ng gặp bất cứ hiện tượng giật lag n&agrave;o. Ổ cứng&nbsp;<strong>SSD 256 GB&nbsp;</strong>cho ph&eacute;p truy xuất dữ liệu nhanh đ&aacute;ng kể c&ugrave;ng kh&ocirc;ng gian lưu trữ rộng r&atilde;i.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-060623-090611.jpg\" onclick=\"return false;\"><img alt=\"MacBook Air 15 inch M2 2023 - Sử dụng\" data-src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-060623-090611.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/44/309018/apple-macbook-air-m2-2023-16gb-060623-090611.jpg\" title=\"MacBook Air 15 inch M2 2023 - Sử dụng\" /></a></p>\r\n\r\n<p>Macbook Air lu&ocirc;n l&agrave; sự lựa chọn của đối tượng người d&ugrave;ng y&ecirc;u th&iacute;ch sự gọn nhẹ c&ugrave;ng hiệu năng mạnh mẽ. Với lần &quot;quay trở lại&quot; n&agrave;y, laptop Apple MacBook Air 15 inch M2 2023 8-core CPU vẫn kh&ocirc;ng l&agrave;m mọi người thất vọng m&agrave; c&ograve;n mang đến những cải tiến tuyệt vời. M&aacute;y xứng đ&aacute;ng l&agrave; sản phẩm được mọi nh&agrave; sở hữu v&agrave;o năm 2023 n&agrave;y.</p>\r\n', 'Đã đăng', 3, '08-09-2023', 0),
(59, 'VHL#400', 'Vivo Y02T', 3290000, '<p>Bộ vi xử l&yacute; :Intel Core i505200U 2.2 GHz (3MB L3)</p>\r\n\r\n<p>Cache upto 2.7 GHz</p>\r\n\r\n<p>Bộ nhớ RAM :4 GB (DDR3 Bus 1600 MHz)</p>\r\n\r\n<p>Đồ họa :Intel HD Graphics</p>\r\n\r\n<p>Ổ đĩa cứng :500 GB (HDD)</p>\r\n', 'vivo1.jpg', '<h3><a href=\"https://www.thegioididong.com/dtdd/vivo-y02t\" target=\"_blank\" title=\"Tham khảo điện thoại vivo Y02T tại Thegioididong.com\">Điện thoại vivo Y02T</a>&nbsp;l&agrave; một trong những sản phẩm mới của vivo, h&atilde;ng smartphone nổi tiếng của Trung Quốc. Tuy thuộc ph&acirc;n kh&uacute;c gi&aacute; rẻ, nhưng vẫn c&oacute; những t&iacute;nh năng nổi bật như pin lớn 5000 mAh, m&agrave;n h&igrave;nh IPS LCD 6.51 inch, bộ xử l&yacute; Helio P35 v&agrave; bộ nhớ lưu trữ 64 GB.</h3>\r\n\r\n<h3>Vẻ ngo&agrave;i đơn giản v&agrave; tinh tế</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd-vivo\" target=\"_blank\" title=\"Tham khảo điện thoại vivo tại Thegioididong.com\">Điện thoại vivo</a>&nbsp;n&agrave;y c&oacute; thiết kế đơn giản nhưng tinh tế, với th&acirc;n m&aacute;y l&agrave;m bằng nhựa v&agrave; c&oacute; 2 m&agrave;u sắc để lựa chọn: Orchid Blue (Xanh hoa lan), Cosmic Grey (x&aacute;m kh&ocirc;ng gian). Mặt trước của m&aacute;y c&oacute;&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-ips-lcd-la-gi-905752\" target=\"_blank\" title=\"Tham khảo màn hình IPS LCD là gì tại Thegioididong.com\">m&agrave;n h&igrave;nh IPS LCD</a>&nbsp;6.51 inch với độ ph&acirc;n giải HD+ (720 x 1600 Pixels), cho h&igrave;nh ảnh sắc n&eacute;t v&agrave; r&otilde; r&agrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309723/vivo-y02t-thiet-ke-1.jpg\" onclick=\"return false;\"><img alt=\"vivo Y02T - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309723/vivo-y02t-thiet-ke-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309723/vivo-y02t-thiet-ke-1.jpg\" title=\"vivo Y02T - Thiết kế\" /></a></p>\r\n\r\n<p>M&agrave;n h&igrave;nh cũng c&oacute; cảm ứng đa điểm điện dung, cho ph&eacute;p thao t&aacute;c dễ d&agrave;ng v&agrave; nhanh ch&oacute;ng. Mặt sau của m&aacute;y c&oacute; camera ch&iacute;nh 8 MP với đ&egrave;n flash LED v&agrave; logo vivo. M&aacute;y cũng c&oacute; camera trước 5 MP để chụp ảnh selfie v&agrave; video call. M&aacute;y c&oacute; khối lượng khoảng 186 g, cho cảm gi&aacute;c cầm nắm vừa vặn v&agrave; thoải m&aacute;i kh&ocirc;ng qu&aacute; mỏi khi sử dụng l&acirc;u.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309723/vivo-y02t-man-hinh-1.jpg\" onclick=\"return false;\"><img alt=\"vivo Y02T - Màn hình\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309723/vivo-y02t-man-hinh-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309723/vivo-y02t-man-hinh-1.jpg\" title=\"vivo Y02T - Màn hình\" /></a></p>\r\n\r\n<h3>Hiệu năng đủ d&ugrave;ng với Helio P35</h3>\r\n\r\n<p>vivo Y02T được trang bị&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-ve-dong-chip-mediatek-helio-p35-1172096\" target=\"_blank\" title=\"Tham khảo CPU Helio P35 là gì tại Thegioididong.com\">bộ xử l&yacute; Helio P35</a>&nbsp;của MediaTek, một chip t&aacute;m nh&acirc;n với tốc độ xung nhịp l&ecirc;n đến 2.3 GHz. Bộ xử l&yacute; n&agrave;y cho hiệu năng ổn định v&agrave; mượt m&agrave; khi chạy c&aacute;c ứng dụng cơ bản v&agrave; chơi game nhẹ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309723/vivo-y02t-cpu.jpg\" onclick=\"return false;\"><img alt=\"vivo Y02T - Hiệu năng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309723/vivo-y02t-cpu.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309723/vivo-y02t-cpu.jpg\" title=\"vivo Y02T - Hiệu năng\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/dtdd-ram-4gb\" target=\"_blank\" title=\"Tham khảo điện thoại RAM 4GB tại Thegioididong.com\">Điện thoại RAM 4 GB</a>&nbsp;hỗ trợ đa nhiệm tốt v&agrave; khả năng mở rộng th&ecirc;m 4 GB RAM ảo cho việc thao t&aacute;c tr&ecirc;n m&aacute;y trở n&ecirc;n mượt m&agrave; hơn. Ngo&agrave;i ra, vivo Y02T chạy hệ điều h&agrave;nh Funtouch OS 13 dựa tr&ecirc;n Android 13 (Go Edition), mang đến giao diện người d&ugrave;ng th&acirc;n thiện v&agrave; nhiều t&iacute;nh năng tiện &iacute;ch.</p>\r\n\r\n<h3>Camera cho chất lượng chụp cơ bản</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-vivo-y\" target=\"_blank\" title=\"Tham khảo điện thoại vivo dòng Y tại Thegioididong.com\">điện thoại vivo d&ograve;ng Y</a>&nbsp;c&oacute; camera sau 8 MP với khẩu độ f/2.0, cho khả năng chụp ảnh ở mức ổn. Camera n&agrave;y cũng c&oacute; đ&egrave;n flash LED để hỗ trợ chụp ảnh trong điều kiện &aacute;nh s&aacute;ng yếu. M&aacute;y cũng c&oacute; camera trước 5 MP với khẩu độ f/2.2, cho khả năng chụp ảnh selfie đẹp v&agrave; tự nhi&ecirc;n. Camera sau c&oacute; chế độ l&agrave;m đẹp khu&ocirc;n mặt, chế độ ch&acirc;n dung, video tua nhanh thời gian để tạo ra c&aacute;c t&aacute;c phẩm s&aacute;ng tạo v&agrave; độc đ&aacute;o.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/42/309723/vivo-y02t-camera-1.jpg\" onclick=\"return false;\"><img alt=\"vivo Y02T - Camera sau\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/309723/vivo-y02t-camera-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/42/309723/vivo-y02t-camera-1.jpg\" title=\"vivo Y02T - Camera sau\" /></a></p>\r\n\r\n<h3>Vi&ecirc;n pin dung lượng lớn 5000 mAh</h3>\r\n\r\n<p>Điểm nổi bật của vivo Y02T l&agrave; pin lớn 5000 mAh cho thời gian sử dụng l&acirc;u d&agrave;i v&agrave; thoải m&aacute;i. Kết hợp với con chip ti&ecirc;u tốn &iacute;t điện năng bạn ho&agrave;n to&agrave;n c&oacute; thể sử dụng trong khoảng 2 ng&agrave;y với c&aacute;c t&aacute;c vụ cơ bản. M&aacute;y cũng c&oacute; c&ocirc;ng suất sạc 10 W v&agrave; cổng sạc Micro USB để nạp pin khi cần thiết, tuy kh&ocirc;ng qu&aacute; cao nhưng với một chiếc điện thoại gi&aacute; rẻ th&igrave; ho&agrave;n to&agrave;n chấp nhận được.</p>\r\n\r\n<p>M&aacute;y c&oacute; hai khe cắm SIM nano để sử dụng hai số&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại kinh doanh tại Thegioididong.com\">điện thoại</a>&nbsp;kh&aacute;c nhau, hỗ trợ c&aacute;c loại mạng từ 2G đến 4G cho tốc độ truyền dữ liệu cao v&agrave; khả năng bắt s&oacute;ng tốt. Ngo&agrave;i ra, m&aacute;y c&ograve;n c&oacute; Wifi hai băng tần (2.4 GHz / 5 GHz), Bluetooth 5.0, GPS, OTG v&agrave; FM để kết nối với c&aacute;c thiết bị kh&aacute;c v&agrave; thưởng thức c&aacute;c nội dung giải tr&iacute;.</p>\r\n\r\n<p>Với mức gi&aacute; hợp l&yacute;, vivo Y02T l&agrave; một sự lựa chọn tuyệt vời cho những ai muốn c&oacute; một chiếc điện thoại th&ocirc;ng minh hiện đại v&agrave; tiện dụng. M&aacute;y c&oacute; nhiều t&iacute;nh năng nổi bật như pin dung lượng cao 5000 mAh, m&agrave;n h&igrave;nh IPS LCD rộng 6.51 inch, thiết kế đơn giản nhưng thanh lịch.</p>\r\n', 'Đã đăng', 1, '11-09-2023', 0),
