--
-- Cấu trúc bảng cho bảng `list_shopping`
--

CREATE TABLE `list_shopping` (
  `id` int(11) NOT NULL,
  `order_code` varchar(1000) NOT NULL,
  `fullname` varchar(1000) NOT NULL,
  `quantity` varchar(1000) NOT NULL,
  `total_price` varchar(1000) NOT NULL,
  `status` enum('Chờ xét duyệt','Đang vận chuyển','Thành công') NOT NULL DEFAULT 'Chờ xét duyệt',
  `address` varchar(1000) NOT NULL,
  `phone` varchar(1000) NOT NULL,
  `time` timestamp NOT NULL DEFAULT current_timestamp(),
  `order_buy` longtext NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_vietnamese_ci;

